INSERT INTO `awards` (`id`, `name`, `slug`, `image`, `created_at`, `updated_at`, `order`, `tooltip`) VALUES
(7, 'Global Banking and Finance Review', 'global-banking-and-finance-review', 'images/award/1520414132_Global&finanace.png', '2018-03-07 21:15:32', '2018-03-16 19:26:00', 0, '<p><strong>&ldquo;C&ocirc;ng ty Bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam năm 2017&rdquo;</strong></p>\r\n<p><span style=\"font-weight: 400;\">Global Banking &amp; Finanace Review (GBAF) trao giải</span></p>'),
(8, 'Top 500', 'top-500', 'images/award/1520414159_logofast.png', '2018-03-07 21:15:59', '2018-03-16 19:26:00', 5, '<p><strong>500 Doanh nghiệp tăng trưởng nhanh nhất Việt Nam năm 2017</strong></p>\r\n<p><span style=\"font-weight: 400;\">FAST500 - Thời B&aacute;o Kinh tế Việt Nam b&igrave;nh chọn</span></p>\r\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>'),
(9, 'CSI Certificate', 'csi-certificate', 'images/award/1520414204_Logo CSI 2017.jpg', '2018-03-07 21:16:44', '2018-03-16 19:26:00', 1, '<p><strong>C&ocirc;ng ty Bảo hiểm phi nh&acirc;n thọ c&oacute; dịch vụ kh&aacute;ch h&agrave;ng tốt nhất Việt Nam 2017</strong></p>\r\n<p><span style=\"font-weight: 400;\">Global Banking and Finance b&igrave;nh chọn v&agrave; đạt&nbsp;chỉ số h&agrave;i l&ograve;ng kh&aacute;ch h&agrave;ng CSI</span></p>\r\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>'),
(10, 'Top 10', 'top-10', 'images/award/1520849678_top10.png', '2018-03-12 21:14:38', '2018-03-16 19:26:00', 2, '<p>&nbsp;C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam năm 2016</p>'),
(11, 'Top 20', 'top-20', 'images/award/1520849708_top20.png', '2018-03-12 21:15:08', '2018-03-16 19:26:00', 3, '<p>&nbsp;Thương hiệu nổi tiếng nhất Việt Nam 2015, 2016, 2017</p>'),
(12, 'Top 100', 'top-100', 'images/award/1520849737_top100.png', '2018-03-12 21:15:37', '2018-03-16 19:26:00', 4, '<p>&nbsp;Thương hiệu Việt Nam xuất sắc năm 2014, 2015</p>'),
(13, 'Top 1000', 'top-1000', 'images/award/1520849773_top1000.png', '2018-03-12 21:16:13', '2018-03-16 19:26:00', 6, '<p>&nbsp;Tập đo&agrave;n nộp thuế thu nhập doanh nghiệp của Việt Nam năm 2012, 2014, 2015</p>');
