INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(31, 2, 'Trao giải chương trình thi đua \"Khởi động nhanh tranh thưởng lớn cùng SME Bancass\"', 'trao-giai-chuong-trinh-thi-dua-khoi-dong-nhanh-tranh-thuong-lon-cung-sme-bancass', '<p style=\"text-align: left;\"><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 02/03/2018, Ph&ograve;ng Kh&aacute;ch h&agrave;ng Doanh nghiệp vừa v&agrave; nhỏ (KHDN VVN) &ndash; Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam (VietinBank) đ&atilde; phối hợp với Tổng C&ocirc;ng ty Cổ phần Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam (VBI) tổ chức lễ trao giải chương tr&igrave;nh thi đua &ldquo;Khởi động nhanh tranh thưởng</span></span></p>', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 02/03/2018, Ph&ograve;ng Kh&aacute;ch h&agrave;ng Doanh nghiệp vừa v&agrave; nhỏ (KHDN VVN) &ndash; Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam (VietinBank) đ&atilde; phối hợp với Tổng C&ocirc;ng ty Cổ phần Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam (VBI) tổ chức lễ trao giải chương tr&igrave;nh thi đua &ldquo;Khởi động nhanh tranh thưởng</span></span></p>\r\n<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\"><img src=\"../../../uploads/img1.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></span></span></p>\r\n<p style=\"text-align: center;\"><em>Đại diện VietinBank &ndash; B&agrave; Nguyễn Thu Hằng Trưởng ph&ograve;ng KHDN VVN v&agrave; Đại diện VBI &ndash; &Ocirc;ng L&ecirc; Tuấn Dũng Tổng Gi&aacute;m đốc trao giải đặc biệt cho c&aacute; nh&acirc;n xuất sắc nhất chương tr&igrave;nh thi đua</em></p>\r\n<div>&nbsp;Tham dự Lễ trao giải c&oacute; b&agrave; Nguyễn Thu Hằng &ndash; Trưởng ph&ograve;ng KHDN VVN VietinBank, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng gi&aacute;m đốc VBI cũng c&aacute;c &ocirc;ng/b&agrave; trong Ban l&atilde;nh đạo của của ph&ograve;ng KHDN VVN &ndash; Trụ sở ch&iacute;nh. Về ph&iacute;a chi nh&aacute;nh, c&oacute; sự tham dự của c&aacute;c &ocirc;ng b&agrave; trong Ban l&atilde;nh đạo Chi nh&aacute;nh : CN3, CN 11, CN TP HCM, CN Bắc S&agrave;i G&ograve;n, CN Cần Thơ, CN KCN Bi&ecirc;n H&ograve;a c&ugrave;ng c&aacute;c c&aacute; nh&acirc;n v&agrave; tập thể xuất sắc đạt giải của chương tr&igrave;nh thi đua.</div>\r\n<div>&nbsp;</div>\r\n<div>&ldquo;<strong>Khởi động nhanh &ndash; Tranh thưởng lớn c&ugrave;ng SME BANCASS</strong>&rdquo; l&agrave; chương tr&igrave;nh được VietinBank phối hợp với VBI triển khai từ ng&agrave;y 01/02/2018 đến 14/02/2017. Đ&acirc;y l&agrave; chương tr&igrave;nh thi đua ngắn ng&agrave;y rất hấp dẫn, khuyến kh&iacute;ch c&aacute; c&aacute; nhận/đơn vị trực thuộc VietinBank tăng cường b&aacute;n ch&eacute;o đối với sản phẩm bảo hiểm VBI. Kết th&uacute;c chương tr&igrave;nh thu đua, to&agrave;n hệ thống VietinBank đ&atilde; ghi nhận 645 đơn bảo hiểm cấp th&agrave;nh c&ocirc;ng cho KHDN VVN tương ứng 13,549 tỷ đồng doanh thu ph&iacute; bảo hiểm, tăng gấp 3 lần so với c&ugrave;ng kỳ năm 2017, g&oacute;p phần tăng tồng doanh thu ph&iacute; bảo hiểm th&aacute;ng 02/2018 tăng 70% với với th&aacute;ng 02/2017.</div>\r\n<div>&nbsp;</div>\r\n<div>Ph&aacute;t biểu tại Lễ trao giải, b&agrave; Nguyễn Thu Hằng- Trưởng ph&ograve;ng KHDN VVN-Trụ sở ch&iacute;nh ch&uacute;c mừng v&agrave; gửi lời c&aacute;m ơn đến c&aacute;c c&aacute; nh&acirc;n/ đơn vị c&oacute; mặt trong buổi lễ trao giải n&oacute;i ri&ecirc;ng v&agrave; tr&ecirc;n to&agrave;n hệ thống VietinBank n&oacute;i chung đ&atilde; c&oacute; những cố gắng, nỗ lực trong c&ocirc;ng t&aacute;c b&aacute;n h&agrave;ng, đạt những kết quả xuất sắc trong thời gian diễn ra chương tr&igrave;nh thi đua đồng thời cũng mong rằng c&aacute;c c&aacute; nh&acirc;n/ đơn vị tại CN sẽ tiếp tục đồng h&agrave;nh, đẩy mạnh c&ocirc;ng t&aacute;c b&aacute;n ch&eacute;o sản phẩm, n&acirc;ng cao hiệu quả của SME Bancass. B&agrave; Hằng cũng cho biết th&ecirc;m trong thời gian tới, VietinBank n&oacute;i chung v&agrave; ph&ograve;ng KHDN VVN n&oacute;i ri&ecirc;ng sẽ tiếp tục phối hợp với VBI triển khai c&aacute;c chương tr&igrave;nh thi đua kinh doanh để tạo động lực cho c&aacute;c c&aacute;n bộ VietinBank mang tới c&aacute;c sản phẩm dịch vụ một cửa ng&acirc;n h&agrave;ng &ndash; bảo hiểm tốt nhất tới kh&aacute;ch h&agrave;ng. B&ecirc;n cạnh đ&oacute;, sẽ nhanh ch&oacute;ng thực hiện xong việc kết nối th&ocirc;ng tin kh&aacute;ch h&agrave;ng doanh nghiệp của VietinBank với phần mềm bancass của VBI để quản trị th&ocirc;ng tin nhanh v&agrave; xuy&ecirc;n suốt. Định hướng x&acirc;y dựng c&aacute;c sản phẩm phức hợp t&iacute;n dụng-bảo hiểm t&iacute;n dụng cũng sẽ được VietinBank nghi&ecirc;n cứu trong thời gian ngắn tới.</div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<div>Tiếp theo những lời ph&aacute;t biểu, chia sẻ của b&agrave; Nguyễn Thu Hằng, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng gi&aacute;m đốc VBI cũng nhiệt liệt ch&uacute;c mừng v&agrave; gửi lời c&aacute;m ơn đến c&aacute;c c&aacute; nh&acirc;n/đơn vị tr&ecirc;n to&agrave;n hệ thống VietinBank&nbsp; đ&atilde; nỗ lực, t&iacute;ch cực trong c&ocirc;ng t&aacute;c đẩy b&aacute;n sản phẩm bảo hiểm VBI trong suốt thời gian qua, đặc biệt đối với ph&acirc;n kh&uacute;c KHDN VVN. &Ocirc;ng Dũng cũng chia sẻ về định hướng ph&aacute;t triển của VBI trong 2018 trong đ&oacute;:</div>\r\n<ul>\r\n<li>Tập trung n&acirc;ng cao chất lượng sản phẩm dịch vụ của VBI</li>\r\n<li>Cam kết cao về chất lượng li&ecirc;n quan đến dịch vụ truy đ&ograve;i bảo hiểm v&agrave; c&ocirc;ng t&aacute;c phối hợp đẩy b&aacute;n sản phẩm VBI</li>\r\n<li>Triển khai s&acirc;u rộng v&agrave; hiệu quả ứng dụng My VBI trong việc khai b&aacute;o tổn thất/bồi thường/hỗ trợ KH tham gia bảo hiểm trực tuyến, t&igrave;m hiểu c&aacute;c sản phẩm dịch vụ</li>\r\n<li>Tiếp tục phối hợp với ph&ograve;ng KHDN VVN triển khai nhiều chương tr&igrave;nh thi đua SME Bancass, bao gồm c&aacute;c chương tr&igrave;nh thi đua ngắn ng&agrave;y để cơ hội chia đều cho c&aacute;c chi nh&aacute;nh v&agrave; chương tr&igrave;nh thi đua kết quả t&iacute;ch lũy 6 th&aacute;ng v&agrave; 12 th&aacute;ng d&agrave;nh cho c&aacute;c chi nh&aacute;nh c&oacute; đ&oacute;ng lớn trong hoạt động b&aacute;n c&aacute;c sản phẩm bảo hiểm VBI qua k&ecirc;nh ng&acirc;n h&agrave;ng.</li>\r\n</ul>\r\n</div>\r\n<div><img src=\"../../../uploads/img2.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></div>\r\n<div style=\"text-align: center;\"><em>To&agrave;n thể c&aacute;c c&aacute; nh&acirc;n đơn vị tham dự Lễ trao giải chương tr&igrave;nh thi đua SME Bancass</em></div>\r\n<div>Tại buổi lễ, &Ocirc;ng L&ecirc; Tấn Vĩnh &ndash; Trưởng ph&ograve;ng KHDN VVN Chi nh&aacute;nh 11, chủ nh&acirc;n của chiếc Iphone X trị gi&aacute; 30,000,000 VNĐ d&agrave;nh cho giải c&aacute; nh&acirc;n xuất sắt nhất trong chương trinh thi đua đ&atilde; b&agrave;y tỏ sự vui mừng khi nhận được giải thưởng v&agrave; cam k&ecirc;t sẽ tiếp tục đồng h&agrave;nh c&ugrave;ng SME Bancass trong thời gian tới. Theo &ocirc;ng Vĩnh, việc đẩy b&aacute;n sản phẩm bảo hiểm VBI l&agrave; rất thiết thực, hỗ trợ giảm thiểu rủi ro cho VietinBank cũng như gia tăng nguồn thu từ ph&iacute;. &Ocirc;ng Vĩnh cũng chia sẻ th&ecirc;m giải thưởng n&agrave;y c&oacute; được l&agrave; sự đ&oacute;ng g&oacute;p, nỗ lực cố gắng của to&agrave;n bộ tập thể ph&ograve;ng KHDN VVN CN 11 n&oacute;i ri&ecirc;ng v&agrave; to&agrave;n bộ CN 11 n&oacute;i chung.</div>\r\n<div style=\"text-align: right;\"><em>Dẫn nguồn: Ph&ograve;ng Kh&aacute;ch h&agrave;ng doanh nghiệp vừa v&agrave; nhỏ - VietinBank</em></div>', '2018-03-21 03:30:30', '2018-03-21 14:30:30', 'images/news/1520305245_Ngay-0203_636559234813230600_HasThumb_Thumb.jpg.png'),
(32, 2, 'Nhận IphoneX khi cài ứng dụng My VBI', 'nhan-iphonex-khi-cai-ung-dung-my-vbi', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Với định vị &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, đi đầu xu thế c&ocirc;ng nghệ 4.0 trong lĩnh vực bảo hiểm, Bảo hiểm VietinBank (VBI) ch&iacute;nh thức ra mắt Ứng dụng My VBI với đầy đủ c&aacute;c t&iacute;nh năng tiện &iacute;ch, hỗ trợ kh&aacute;ch h&agrave;ng một c&aacute;ch hiệu quả v&agrave; nhanh ch&oacute;ng. Đ&acirc;y l&agrave; ứng dụng d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng</span></span></p>', '<p><span style=\"font-family: Arial;\">Với định vị &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, đi đầu xu thế c&ocirc;ng nghệ 4.0 trong lĩnh vực bảo hiểm, Bảo hiểm VietinBank (VBI) ch&iacute;nh thức ra mắt Ứng dụng My VBI với đầy đủ c&aacute;c t&iacute;nh năng tiện &iacute;ch, hỗ trợ kh&aacute;ch h&agrave;ng một c&aacute;ch hiệu quả v&agrave; nhanh ch&oacute;ng. Đ&acirc;y l&agrave; ứng dụng d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tham gia bảo hiểm với c&aacute;c t&iacute;nh năng vượt trội như: <em>Khai b&aacute;o bồi thường, chụp ảnh tổn thất xe cơ giới tại hiện trường, hướng dẫn bồi thường bảo hiểm, truy vấn th&ocirc;ng tin bồi thường bảo hiểm, tra cứu Giấy chứng nhận v&agrave; h&oacute;a đơn điện tử. Hơn thế nữa, ứng dụng My VBI c&ograve;n l&agrave; tiện &iacute;ch d&agrave;nh cho những kh&aacute;ch h&agrave;ng c&oacute; quan t&acirc;m đến bảo hiểm khi hỗ trợ kh&aacute;ch h&agrave;ng tham gia bảo hiểm trực tuyến, tra cứu mạng lưới hệ thống VBI, mạng lưới gara v&agrave; c&aacute;c cơ sở y tế trong trường hợp cần được trợ gi&uacute;p. </em></span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>Từ ng&agrave;y 22/1 &ndash; 1/3/2018, mỗi kh&aacute;ch h&agrave;ng khi c&agrave;i ứng dụng My VBI sẽ c&oacute; cơ hội nhận được rất nhiều ưu đ&atilde;i</strong></span></p>\r\n<p><img src=\"../../../uploads/dr binh-a4.jpg\" alt=\"\" width=\"848\" height=\"1200\" /></p>', '2018-03-21 03:31:30', '2018-03-21 14:31:30', 'images/news/1520305589_pic-New-i_636530959929710088_HasThumb_Thumb.jpg.png'),
(33, 2, 'Trải nghiệm bồi thường bảo hiểm chưa bao giờ tiện lợi đến thế', 'trai-nghiem-boi-thuong-bao-hiem-chua-bao-gio-tien-loi-den-the', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Với định vị &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, đi đầu xu thế c&ocirc;ng nghệ 4.0 trong lĩnh vực bảo hiểm, Bảo hiểm VietinBank (VBI) cho ra đời ứng dụng My VBI nhằm n&acirc;ng cao chất lượng dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng v&agrave; phục vụ cho hoạt động b&aacute;n h&agrave;ng. Ứng dụng My VBI được x&acirc;y dựng tr&ecirc;n nền tảng iOs</span></span></p>', '<p><strong>Với định vị &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, đi đầu xu thế c&ocirc;ng nghệ 4.0 trong lĩnh vực bảo hiểm, Bảo hiểm VietinBank (VBI) cho ra đời ứng dụng My VBI nhằm n&acirc;ng cao chất lượng dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng v&agrave; phục vụ cho hoạt động b&aacute;n h&agrave;ng.</strong></p>\r\n<p><img src=\"../../../uploads/img_201801250847152878.jpg\" alt=\"\" width=\"660\" height=\"360\" /></p>\r\n<p><span style=\"font-family: Arial;\">Ứng dụng My VBI được x&acirc;y dựng tr&ecirc;n nền tảng iOs v&agrave; Android, gi&uacute;p tối ưu h&oacute;a trong quy tr&igrave;nh để kh&aacute;ch h&agrave;ng v&agrave; Bảo hiểm VietinBank dễ d&agrave;ng kết nối, tương t&aacute;c với nhau qua thiết bị điện thoại th&ocirc;ng minh. Để từ đ&oacute;, quy tr&igrave;nh gi&aacute;m định, bồi thường hay mua h&agrave;ng của kh&aacute;ch h&agrave;ng được thực hiện một c&aacute;ch nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng nhất.</span></p>\r\n<p><span style=\"font-family: Arial;\">Sự ra đời của ứng dụng My VBI được Bảo Hiểm VietinBank ph&aacute;t triển tập trung cho ba sản phẩm ch&iacute;nh: Gi&aacute;m định, bồi thường tổn thất xe cơ giới, tra cứu bồi thường bảo hiểm sức khỏe v&agrave; mua h&agrave;ng trực tuyến.</span></p>\r\n<p><span style=\"font-family: Arial;\">T&iacute;nh năng gi&aacute;m định v&agrave; bồi thường bảo hiểm trực tuyến l&agrave; một trong những t&iacute;nh năng ưu việt, vượt trội nhất của ứng dụng My VBI. Thay v&igrave; quy tr&igrave;nh khai b&aacute;o bồi thường trước kia với đầy đủ c&aacute;c bước như: tiếp nhận y&ecirc;u cầu xử l&yacute; bồi thường, gi&aacute;m định tổn thất, lựa chọn phương &aacute;n bồi thường, ho&agrave;n thiện hồ sơ bồi thường. Để to&agrave;n tất thục tục bồi thường, kh&aacute;ch h&agrave;ng cần chờ đợi 15 ng&agrave;y l&agrave;m việc, thậm ch&iacute; h&agrave;ng th&aacute;ng trời.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, khi kh&aacute;ch h&agrave;ng ph&aacute;t sinh nhu cầu bồi thường bảo hiểm về &ocirc; t&ocirc; hay sức khỏe, chỉ cần một v&agrave;i thao t&aacute;c tr&ecirc;n ứng dụng My VBI, chụp hồ sơ gốc ngay tại ứng dụng, My VBI sẽ tiếp nhận trục tiếp v&agrave; chuyển về bộ phận gi&aacute;m định v&agrave; bồi thường của Bảo hiểm VietinBank trong v&ograve;ng 2 gi&acirc;y. Tại đ&acirc;y, c&aacute;c th&ocirc;ng tin của kh&aacute;ch h&agrave;ng được xử l&yacute; ngay lập tức m&agrave; kh&ocirc;ng mất thời gian gửi nhận. Với ứng dụng My VBI thời gian xử l&yacute; được r&uacute;t gọn xuống c&ograve;n 5 ng&agrave;y l&agrave;m việc, chỉ bằng 1/3 tốc độ trung b&igrave;nh hiện tại tr&ecirc;n thị trường bảo hiểm.</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/img_201801241423256587.jpg\" alt=\"\" width=\"286\" height=\"342\" /><br /></span></p>\r\n<p><span style=\"font-family: Arial;\">&Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc Bảo hiểm VietinBank cho biết: &ldquo;Ứng dụng My VBI l&agrave; một trong những minh chứng cho nỗ lực kh&ocirc;ng ngừng của Bảo hiểm VietinBank nhằm n&acirc;ng cao hơn nữa chất lượng sản phẩm, dịch vụ, chất lượng bồi thường tổn thất cho kh&aacute;ch h&agrave;ng. Hiện nay, việc ứng dụng My VBI để khai b&aacute;o tổn thất v&agrave; bồi thường con người được xem như l&agrave; c&ocirc;ng nghệ ti&ecirc;n tiến nhất trong thời điểm hiện nay. Ch&uacute;ng t&ocirc;i hy vọng rằng My VBI sẽ được sử dụng rộng r&atilde;i trong cộng đồng để kh&aacute;ch h&agrave;ng c&oacute; cơ hội trải nghiệm c&aacute;c tiện &iacute;ch gia tăng, chủ động cho kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm trong kỉ nguy&ecirc;n c&ocirc;ng nghệ 4.0.&rdquo;</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/img_201801241423386054.jpg\" alt=\"\" width=\"288\" height=\"232\" /><br /></span></p>\r\n<p><span style=\"font-family: Arial;\">Ứng dụng My VBI l&agrave; sự t&acirc;m huyết, d&agrave;y c&ocirc;ng nghi&ecirc;n cứu của cả tập thể Ban L&atilde;nh đạo v&agrave; C&aacute;n bộ, nh&acirc;n vi&ecirc;n Bảo hiểm VietinBank để đem đến cho kh&aacute;ch h&agrave;ng những trải nghiệm ho&agrave;n to&agrave;n mới về việc sử dụng bảo hiểm, x&oacute;a nh&ograve;a những r&agrave;o cản của kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm. My VBI cũng l&agrave; một h&agrave;nh trang vững v&agrave;ng trong năm 2018 gi&uacute;p Bảo hiểm VietinBank ho&agrave;n to&agrave;n tự tin với mục ti&ecirc;u trở th&agrave;nh C&ocirc;ng ty bảo hiểm top đầu về mảng b&aacute;n lẻ bảo hiểm phi nh&acirc;n thọ.</span></p>\r\n<p><span style=\"font-family: Arial;\">Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; thể tải App theo link sau:</span></p>\r\n<p><span style=\"font-family: Arial;\">Android: https://goo.gl/e2WsZA IOS: <a href=\"https://goo.gl/wBU5aq\">https://goo.gl/wBU5aq</a>.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\">Dẫn nguồn:<br />CafeF.vn</span></p>', '2018-03-21 03:32:44', '2018-03-21 14:32:44', 'images/news/1520305816_pic-New-i_636529896954103584_HasThumb_Thumb.jpg.png'),
(34, 2, 'Bảo hiểm VietinBank thành lập thêm 02 Chi nhánh và trao quyết định Giám đốc Chi nhánh Sài Gòn', 'bao-hiem-vietinbank-thanh-lap-them-02-chi-nhanh-va-trao-quyet-dinh-giam-doc-chi-nhanh-sai-gon', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Nhằm khai th&aacute;c tối đa tiềm năng, nhu cầu bảo hiểm của c&aacute;c kh&aacute;ch h&agrave;ng tại TP. Hồ Ch&iacute; Minh, c&aacute;c tỉnh miền T&acirc;y Nam bộ; tại Tp. Hải Ph&ograve;ng v&agrave; c&aacute;c tỉnh ph&iacute;a Đ&ocirc;ng Bắc bộ cũng như thực hiện tốt c&ocirc;ng t&aacute;c phục vụ, chăm s&oacute;c kh&aacute;ch h&agrave;ng tại c&aacute;c khu vực n&agrave;y. </span></span></p>', '<p><span style=\"font-family: Arial;\">Nhằm khai th&aacute;c tối đa tiềm năng, nhu cầu bảo hiểm của c&aacute;c kh&aacute;ch h&agrave;ng tại TP. Hồ Ch&iacute; Minh, c&aacute;c tỉnh miền T&acirc;y Nam bộ; tại Tp. Hải Ph&ograve;ng v&agrave; c&aacute;c tỉnh ph&iacute;a Đ&ocirc;ng Bắc bộ cũng như thực hiện tốt c&ocirc;ng t&aacute;c phục vụ, chăm s&oacute;c kh&aacute;ch h&agrave;ng tại c&aacute;c khu vực n&agrave;y. Ng&agrave;y 22/5/2013 C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam đ&atilde; được Bộ T&agrave;i ch&iacute;nh cấp Giấy ph&eacute;p th&agrave;nh lập v&agrave; điều chỉnh số 21/GPĐC21/KDBH về việc th&agrave;nh lập th&ecirc;m 02 chi nh&aacute;nh:Nhằm khai th&aacute;c tối đa tiềm năng, nhu cầu bảo hiểm của c&aacute;c kh&aacute;ch h&agrave;ng tại TP. Hồ Ch&iacute; Minh, c&aacute;c tỉnh miền T&acirc;y Nam bộ; tại Tp. Hải Ph&ograve;ng v&agrave; c&aacute;c tỉnh ph&iacute;a Đ&ocirc;ng Bắc bộ cũng như thực hiện tốt c&ocirc;ng t&aacute;c phục vụ, chăm s&oacute;c kh&aacute;ch h&agrave;ng tại c&aacute;c khu vực n&agrave;y. Ng&agrave;y 22/5/2013 C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam đ&atilde; được Bộ T&agrave;i ch&iacute;nh cấp Giấy ph&eacute;p th&agrave;nh lập v&agrave; điều chỉnh số 21/GPĐC21/KDBH về việc th&agrave;nh lập th&ecirc;m 02 chi nh&aacute;nh:</span></p>\r\n<p><span style=\"font-family: Arial;\">1.&nbsp;&nbsp; &nbsp;C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam &ndash; <strong>CN S&agrave;i G&ograve;n</strong><br /> Địa chỉ: Tầng 9, T&ograve;a nh&agrave; PICO, số 20 đường Cộng H&ograve;a, quận T&acirc;n B&igrave;nh, TP HCM<br /> Điện thoại: 08.3948 2266&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Fax: 08.3948 2255 &nbsp;</span></p>\r\n<p><span style=\"font-family: Arial;\">2.&nbsp;&nbsp; &nbsp;C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam &ndash; <strong>CN Hải Ph&ograve;ng</strong><br /> Địa chỉ: Tầng 17, Trung t&acirc;m thương mại CATBI PLAZA, số 1 đường L&ecirc; Hồng Phong, quận Ng&ocirc; Quyền, TP Hải Ph&ograve;ng.<br /> Điện thoại:031.365 1368&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Fax: 031.365 1369</span></p>\r\n<p><span style=\"font-family: Arial;\">Theo đ&oacute;, ng&agrave;y 27/5/2013 đ&atilde; diễn ra buổi lễ c&ocirc;ng bố quyết định về việc tuyển dụng, bổ nhiệm <strong>&ocirc;ng Trần Tiến Dũng</strong> giữ chức vụ <strong>Gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam &ndash; Chi nh&aacute;nh S&agrave;i G&ograve;n</strong> v&agrave; c&aacute;c quyết định tuyển dụng, bổ nhiệm c&aacute;c chức danh Trưởng, Ph&oacute; ph&ograve;ng của Chi nh&aacute;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Ph&oacute; Tổng gi&aacute;m đốc phụ tr&aacute;ch - &Ocirc;ng Phạm Anh Tuấn đ&atilde; tới dự v&agrave; y&ecirc;u cầu c&aacute;c c&aacute;n bộ được bổ nhiệm cần chủ động, s&aacute;ng tạo, nỗ lực ph&aacute;t huy năng lực, kinh nghiệm c&ocirc;ng t&aacute;c để thực hiện quản l&yacute; điều h&agrave;nh Chi nh&aacute;nh S&agrave;i G&ograve;n ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao trong năm 2013 v&agrave; c&aacute;c năm tiếp theo.</span></p>\r\n<p><span style=\"font-family: Arial;\">Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh của Lễ trao Quyết định.</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/635386796214340000.jpg\" alt=\"\" width=\"791\" height=\"471\" /><br /></span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/635386796219380000.jpg\" alt=\"\" width=\"795\" height=\"457\" /><br /></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>Bảo hiểm VietinBank</strong></span></p>', '2018-03-21 03:33:35', '2018-03-21 14:33:35', 'images/news/1520306305_pic-New-i_635386796221940000_HasThumb_Thumb.jpg.png'),
(35, 2, 'Bảo hiểm VietinBank tham gia Hội diễn Nghệ thuật quần chúng Khu vực Hà Nội', 'bao-hiem-vietinbank-tham-gia-hoi-dien-nghe-thuat-quan-chung-khu-vuc-ha-noi', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Chiều ng&agrave;y 8/6, tại Trung t&acirc;m Văn h&oacute;a H&agrave;ng kh&ocirc;ng, H&agrave; Nội, Bảo hiểm VietinBank đ&atilde; đ&oacute;ng g&oacute;p những tiết mục văn nghệ đặc sắc ch&agrave;o mừng kỉ niệm 25 năm VietinBank &ldquo;x&acirc;y dựng v&agrave; ph&aacute;t triển&rdquo;</span></span></p>', '<p><img src=\"../../../uploads/tapthe.jpg\" alt=\"\" width=\"500\" height=\"332\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>L&atilde;nh đạo C&ocirc;ng ty động vi&ecirc;n v&agrave; ch&uacute;c mừng những nỗ lực của tập thể c&aacute;n bộ nh&acirc;n vi&ecirc;n</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Tham gia hội diễn khu vực năm nay, Bảo hiểm VietinBank đ&atilde; c&oacute; cơ hội giao lưu, học hỏi c&ugrave;ng gần 800 diễn vi&ecirc;n kh&ocirc;ng chuy&ecirc;n đến từ 25 đơn vị c&ocirc;ng đo&agrave;n kh&aacute;c tr&ecirc;n địa b&agrave;n H&agrave; Nội. Trong hơn 100 tiết mục, Bảo hiểm VietinBank mang đến ba tiết mục vừa mang đậm n&eacute;t văn h&oacute;a d&acirc;n tộc, vừa mang đậm dấu ấn C&ocirc;ng ty.</span></p>\r\n<p><span style=\"font-family: Arial;\">Mở m&agrave;n l&agrave; tiết mục đơn ca &ldquo;<em>Sa Pa nơi gặp gỡ đất trời</em>&rdquo;, với sự thể hiện của c&aacute;n bộ Phạm Văn Tuấn đến từ Chi nh&aacute;nh Bảo hiểm VietinBank H&agrave; Nội. Với chất giọng t&igrave;nh cảm, s&acirc;u lắng c&ugrave;ng m&agrave;n m&uacute;a minh họa mang đậm hơi thở v&ugrave;ng cao T&acirc;y Bắc, ca kh&uacute;c đ&atilde; để lại ấn tượng kh&oacute; phai trong l&ograve;ng kh&aacute;n giả.</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/donca.jpg\" alt=\"\" width=\"500\" height=\"332\" /><br /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Phạm Văn Tuấn thả hồn c&ugrave;ng ca kh&uacute;c</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Niềm hạnh ph&uacute;c trong c&ocirc;ng việc, t&igrave;nh y&ecirc;u qu&ecirc; hương đất nước h&ograve;a quyện c&ugrave;ng t&igrave;nh y&ecirc;u đ&ocirc;i lứa đ&atilde; n&acirc;ng c&aacute;nh cho hai giọng h&aacute;t Ngọc T&uacute;- Vinh Hoa:</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>&ldquo;Gi&oacute; lộng trời xanh chim h&oacute;t hoa đưa hương,<br /> Với cả t&igrave;nh ta n&acirc;ng bước ta l&ecirc;n đường&hellip;&rdquo;</em></span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/songca.jpg\" alt=\"\" width=\"500\" height=\"332\" /><br /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ngọc T&uacute;, Vinh Hoa đắm say với &ldquo;Tr&ecirc;n c&ocirc;ng trường rộn tiếng ca&rdquo;</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Tr&ecirc;n s&acirc;n khấu Hội diễn, những giai điệu thể hiện l&ograve;ng y&ecirc;u nghề, niềm hăng say cống hiến &ldquo;v&igrave; hạnh ph&uacute;c cộng đồng&rdquo; được tập thể c&aacute;n bộ C&ocirc;ng ty thể hiện đầy ấn tượng qua m&agrave;n h&aacute;t m&uacute;a tập thể &ldquo;<em>Bảo hiểm VietinBank- B&agrave;i ca của bạn</em>&rdquo;.</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/mua.jpg\" alt=\"\" width=\"500\" height=\"290\" /><br /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>M&agrave;n m&uacute;a h&aacute;t thể hiện l&ograve;ng tự h&agrave;o v&agrave; nhiệt huyết của tập thể CBNV C&ocirc;ng ty</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Kết th&uacute;c Hội diễn, Bảo hiểm VietinBank vinh dự được Ban tổ chức trao giải Khuyến kh&iacute;ch to&agrave;n đo&agrave;n. Ba tiết mục C&ocirc;ng ty mang đến Hội diễn đều đạt những th&agrave;nh t&iacute;ch đ&aacute;ng kh&iacute;ch lệ: 02 giải B d&agrave;nh cho tiết mục đơn ca, tốp ca h&aacute;t m&uacute;a v&agrave; giải C d&agrave;nh cho tiết mục song ca. Kết quả n&agrave;y l&agrave; minh chứng cho sự đầu tư v&agrave; tập luyện nghi&ecirc;m t&uacute;c của c&aacute;n bộ nh&acirc;n vi&ecirc;n, đặc biệt l&agrave; sự quan t&acirc;m tận t&igrave;nh, s&aacute;t sao của Ban l&atilde;nh đạo C&ocirc;ng ty.</span></p>\r\n<p><span style=\"font-family: Arial;\">Hội diễn văn nghệ quần ch&uacute;ng khu vực H&agrave; Nội năm 2013 kh&ocirc;ng chỉ đ&aacute;nh dấu sự trưởng th&agrave;nh của Bảo hiểm VietinBank trong việc tham gia phong tr&agrave;o truyền thống của Ng&acirc;n h&agrave;ng, thể hiện h&igrave;nh ảnh của C&ocirc;ng ty trong mắt c&aacute;c đơn vị c&ugrave;ng hệ thống VietinBank m&agrave; c&ograve;n g&oacute;p phần l&agrave;m đẹp th&ecirc;m đời sống văn h&oacute;a tinh thần của c&aacute;n bộ nh&acirc;n vi&ecirc;n C&ocirc;ng ty, từ đ&oacute; hỗ trợ thực hiện những mục ti&ecirc;u kinh tế đề ra.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>B&agrave;i: Hải V&acirc;n</strong></span></p>', '2018-03-21 03:35:00', '2018-03-21 14:35:00', 'images/news/1520306760_pic-New-i_635386899202000000_HasThumb_Thumb.jpg.png'),
(36, 2, 'Lễ trao quyết định Giám đốc Chi nhánh Hải Phòng', 'le-trao-quyet-dinh-giam-doc-chi-nhanh-hai-phong', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Như đ&atilde; đưa tin, ng&agrave;y 22/5/2013 C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam được Bộ T&agrave;i ch&iacute;nh cấp Giấy ph&eacute;p số 21/GPĐC21/KDBH về việc th&agrave;nh lập th&ecirc;m chi nh&aacute;nh Hải Ph&ograve;ng nhằm khai th&aacute;c tối đa tiềm năng, nhu cầu bảo hiểm của c&aacute;c kh&aacute;ch h&agrave;ng tại khu vực Tp. Hải Ph&ograve;ng v&agrave; c&aacute;c </span></span></p>', '<p><span style=\"font-family: Arial;\">Như đ&atilde; đưa tin, ng&agrave;y 22/5/2013 C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam được Bộ T&agrave;i ch&iacute;nh cấp Giấy ph&eacute;p số 21/GPĐC21/KDBH về việc th&agrave;nh lập th&ecirc;m chi nh&aacute;nh Hải Ph&ograve;ng nhằm khai th&aacute;c tối đa tiềm năng, nhu cầu bảo hiểm của c&aacute;c kh&aacute;ch h&agrave;ng tại khu vực Tp. Hải Ph&ograve;ng v&agrave; c&aacute;c tỉnh ph&iacute;a Đ&ocirc;ng Bắc bộ.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ng&agrave;y 17/6/2013, tại Trụ sở Chi nh&aacute;nh Hải Ph&ograve;ng, B&agrave; Nguyễn Hồng V&acirc;n - Ủy vi&ecirc;n HĐQT Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam - Chủ tịch C&ocirc;ng ty v&agrave; &Ocirc;ng Phạm Anh Tuấn - Ph&oacute; Tổng gi&aacute;m đốc phụ tr&aacute;ch đ&atilde; tham dự v&agrave; trao c&aacute;c quyết định tuyển dụng v&agrave; bổ nhiệm c&aacute;c chức danh Gi&aacute;m đốc Chi nh&aacute;nh v&agrave; Trưởng, ph&oacute; ph&ograve;ng của Chi nh&aacute;nh. Theo đ&oacute;, &ocirc;ng Nguyễn Tiến Phong được bổ nhiệm chức vụ <strong>Gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Bảo hiểm NHTMCP C&ocirc;ng Thương Việt Nam &ndash; Chi nh&aacute;nh Hải Ph&ograve;ng</strong>.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Chủ tịch C&ocirc;ng ty ch&uacute;c mừng c&aacute;c c&aacute;n bộ được bổ nhiệm, đồng thời y&ecirc;u cầu Ban l&atilde;nh đạo Chi nh&aacute;nh cần chủ động, s&aacute;ng tạo, nỗ lực ph&aacute;t huy năng lực, kinh nghiệm c&ocirc;ng t&aacute;c để thực hiện quản l&yacute; điều h&agrave;nh Chi nh&aacute;nh Hải Ph&ograve;ng ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao trong năm 2013 v&agrave; c&aacute;c năm tiếp theo.</span></p>\r\n<p><span style=\"font-family: Arial;\">Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh của Lễ trao Quyết định:</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/635386798683680000.jpg\" alt=\"\" width=\"816\" height=\"612\" /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>C&ocirc;ng ty trao Quyết định Gi&aacute;m đốc Chi nh&aacute;nh cho &Ocirc;ng Nguyễn Tiến Phong</em></span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/635386798688450000.jpg\" alt=\"\" width=\"1632\" height=\"1224\" /><br /></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>L&atilde;nh đạo C&ocirc;ng ty chụp ảnh c&ugrave;ng tập thể c&aacute;c c&aacute;n bộ mới được bổ nhiệm tại Chi nh&aacute;nh Hải Ph&ograve;ng</em></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>Bảo hiểm VietinBank</strong></span></p>', '2018-03-21 03:35:57', '2018-03-21 14:35:57', 'images/news/1520306913_pic-New-i_635386798699400000_HasThumb_Thumb.jpg.png'),
(37, 2, 'Lễ công bố bộ nhận diện thương hiệu mới của Công ty', 'le-cong-bo-bo-nhan-dien-thuong-hieu-moi-cua-cong-ty', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 18/06/2013, Bộ T&agrave;i ch&iacute;nh c&oacute; Quyết định số 21/GPĐC12/KDBH về việc chuyển đổi t&ecirc;n giao dịch v&agrave; t&ecirc;n viết tắt C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng Thương mại Cổ phần C&ocirc;ng thương Việt Nam. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 18/06/2013, Bộ T&agrave;i ch&iacute;nh c&oacute; Quyết định số 21/GPĐC12/KDBH về việc chuyển đổi t&ecirc;n giao dịch v&agrave; t&ecirc;n viết tắt C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng Thương mại Cổ phần C&ocirc;ng thương Việt Nam. Được sự đồng &yacute; của Chủ tịch C&ocirc;ng ty, C&ocirc;ng ty sẽ ch&iacute;nh thức &aacute;p dụng hệ thống nhận diện thương hiệu mới kể từ ng&agrave;y 01/07/2013. Chi tiết hệ thống nhận diện thương hiệu mới của C&ocirc;ng ty cụ thể như sau:&nbsp;</span></p>\r\n<p><img src=\"../../../uploads/635386800044380000.jpg\" alt=\"\" width=\"635\" height=\"557\" /></p>\r\n<p><span style=\"font-family: Arial;\">-&nbsp;&nbsp;<strong> &nbsp;Giải t&ecirc;n viết tắt</strong>: Ph&ograve;ng Ph&aacute;t triển kinh doanh &ndash; giải thưởng 2 triệu đồng</span></p>\r\n<p><img src=\"../../../uploads/635386800048360000.jpg\" alt=\"\" width=\"637\" height=\"506\" /></p>\r\n<p><span style=\"font-family: Arial;\">-&nbsp;&nbsp; &nbsp;<strong>Giải Slogan</strong>: 2 c&aacute;n bộ Nguyễn Việt Dũng (ph&ograve;ng TCKT Trụ sở ch&iacute;nh) v&agrave; Nguyễn Đức Dũng (ph&oacute; ph&ograve;ng Bồi thường CN Hồ Ch&iacute; Minh) đ&atilde; nhận đồng giải thưởng 2,5 triệu đồng.</span></p>\r\n<p><img src=\"../../../uploads/635386800048770000.jpg\" alt=\"\" width=\"600\" height=\"450\" /></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Bảo hiểm VietinBank</em></strong></span></p>', '2018-03-21 03:36:49', '2018-03-21 14:36:49', 'images/news/1520307021_pic-New-i_635386800050120000_HasThumb_Thumb.jpg.png'),
(38, 2, 'Bảo hiểm VietinBank chú trọng mở rộng hoạt động kinh doanh qua công tác đào tạo đại lý', 'bao-hiem-vietinbank-chu-trong-mo-rong-hoat-dong-kinh-doanh-qua-cong-tac-dao-tao-dai-ly', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Với mong muốn mỗi đại l&yacute; bảo hiểm l&agrave; một c&aacute;nh tay đắc lực g&oacute;p phần gi&uacute;p C&ocirc;ng ty đạt được mục ti&ecirc;u kinh tế đề ra, trong 6 th&aacute;ng đầu năm 2013, Bảo hiểm VietinBank đ&atilde; ch&uacute; trọng mở rộng hoạt động kinh doanh qua c&ocirc;ng t&aacute;c đ&agrave;o tạo đại l&yacute; cơ bản tr&ecirc;n phạm vi to&agrave;n quốc. </span></span></p>', '<p><span style=\"font-family: Arial;\">Đ&agrave;o tạo đại l&yacute; l&agrave; một trong những kh&acirc;u quan trọng trong c&ocirc;ng t&aacute;c quản l&yacute; đại l&yacute;. Quy tr&igrave;nh n&agrave;y quyết định đến chất lượng, hiệu quả hoạt động của đại l&yacute;, từ đ&oacute; c&oacute; ảnh hưởng đến kết quả kinh doanh của mỗi doanh nghiệp bảo hiểm. Nhận thức r&otilde; tầm quan trọng của c&ocirc;ng t&aacute;c đ&agrave;o tạo đại l&yacute;, trong thời gian qua, Bảo hiểm VietinBank đ&atilde; tiến h&agrave;nh đ&agrave;o tạo tập trung c&aacute;n bộ VietinBank để trở th&agrave;nh đại l&yacute; bảo hiểm của C&ocirc;ng ty. C&aacute;c học vi&ecirc;n đạt đủ điều kiện sẽ được cấp Chứng chỉ c&ocirc;ng nhận ho&agrave;n th&agrave;nh chương tr&igrave;nh Đại l&yacute; bảo hiểm, bao gồm chương tr&igrave;nh bảo hiểm cơ bản v&agrave; chương tr&igrave;nh đ&agrave;o tạo sản phẩm của Bảo hiểm VietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\">Kh&oacute;a đ&agrave;o tạo th&aacute;ng 4 tại H&agrave; Nội l&agrave; kh&oacute;a đ&agrave;o tạo đại l&yacute; đầu ti&ecirc;n của C&ocirc;ng ty sau khi c&oacute; sửa đổi Luật Kinh doanh về bảo hiểm, Th&ocirc;ng tư 124/2012/TT-BTC thay thế th&ocirc;ng tư 155/2007/TT-BTC của Bộ T&agrave;i Ch&iacute;nh. Tiếp thu kinh nghiệm từ kh&oacute;a đ&agrave;o tạo khu vực ph&iacute;a Bắc, trong th&aacute;ng 6 vừa qua, Bảo hiểm VietinBank đ&atilde; tiến h&agrave;nh tổ chức hai kh&oacute;a đ&agrave;o tạo tại th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; Đ&agrave; Nẵng. Nhằm bổ sung kiến thức v&agrave; kĩ năng b&aacute;n bảo hiểm cho c&aacute;n bộ VietinBank, hai kh&oacute;a học n&agrave;y đ&atilde; c&oacute; th&ecirc;m phần kĩ năng b&aacute;n bảo hiểm v&agrave; quy tr&igrave;nh hợp t&aacute;c giữa Ng&acirc;n h&agrave;ng v&agrave; C&ocirc;ng ty bảo hiểm trong m&ocirc; h&igrave;nh Bancassurance. Sau ba kh&oacute;a đ&agrave;o tạo, Bảo hiểm VietinBank đ&atilde; c&oacute; th&ecirc;m gần 200 đại l&yacute;, chủ yếu l&agrave; c&aacute;n bộ ph&ograve;ng ban Trụ sở ch&iacute;nh v&agrave; chi nh&aacute;nh VietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\">Với hi vọng x&acirc;y dựng một đội ngũ đại l&yacute; được trang bị đầy đủ kiến thức chuy&ecirc;n m&ocirc;n, đạo đức nghề nghiệp cũng như kĩ năng phục vụ kh&aacute;ch h&agrave;ng, Bảo hiểm VietinBank vẫn sẽ tiếp tục ch&uacute; trọng v&agrave;o c&ocirc;ng t&aacute;c đ&agrave;o tạo cơ bản cũng như chuy&ecirc;n s&acirc;u trong thời gian tới.</span></p>\r\n<p><img src=\"../../../uploads/635386801384230000.jpg\" alt=\"\" width=\"1026\" height=\"704\" /></p>\r\n<p style=\"text-align: center;\"><em>B</em><em>ảo hiểm VietinBank đ&agrave;o tạo đại l&yacute; tại Hồ Ch&iacute; Minh</em></p>\r\n<p><img src=\"../../../uploads/635386801394830000.jpg\" alt=\"\" width=\"1110\" height=\"590\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Kh&oacute;a đ&agrave;o tạo đại l&yacute; bảo hiểm tại Đ&agrave; Nẵng</em></span></p>\r\n<p><img src=\"../../../uploads/635386801396880000.jpg\" alt=\"\" width=\"686\" height=\"515\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Kh&oacute;a đ&agrave;o tạo đại l&yacute; bảo hiểm tại H&agrave; Nội</em></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em><strong>B&agrave;i: Hải V&acirc;n</strong></em></span></p>', '2018-03-21 03:37:56', '2018-03-21 14:37:56', 'images/news/1520307185_pic-New-i_635386801401110000_HasThumb_Thumb.jpg.png'),
(39, 2, 'Bảo hiểm VietinBank (VBI) tuyển dụng Giám đốc chi nhánh', 'bao-hiem-vietinbank-vbi-tuyen-dung-giam-doc-chi-nhanh', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">C&ocirc;ng ty Bảo hiểm VietinBank (VBI) l&agrave; C&ocirc;ng ty con 100% vốn của VietinBank, hoạt động trong lĩnh vực bảo hiểm phi nh&acirc;n thọ c&oacute; mạng lưới hoạt động rộng khắp c&aacute;c tỉnh v&agrave; th&agrave;nh phố trong cả nước </span></span></p>', '<p><span style=\"font-family: Arial;\">C&ocirc;ng ty Bảo hiểm VietinBank (VBI) l&agrave; C&ocirc;ng ty con 100% vốn của VietinBank, hoạt động trong lĩnh vực bảo hiểm phi nh&acirc;n thọ c&oacute; mạng lưới hoạt động rộng khắp c&aacute;c tỉnh v&agrave; th&agrave;nh phố trong cả nước. C&ocirc;ng ty c&oacute; trụ sở ch&iacute;nh tại H&agrave; Nội v&agrave; 5 Chi nh&aacute;nh tại H&agrave; Nội, TP Hồ Ch&iacute; Minh, Đ&agrave; Nẵng, Hải Ph&ograve;ng. Định hướng của Bảo hiểm VietinBank l&agrave; trở th&agrave;nh một trong những C&ocirc;ng ty bảo hiểm chuy&ecirc;n nghiệp h&agrave;ng đầu trong lĩnh vực bảo hiểm phi nh&acirc;n thọ. Hiện nay C&ocirc;ng ty đang c&oacute; nhu cầu tuyển dụng c&aacute;n bộ như sau:</span></p>\r\n<p><img src=\"../../../uploads/635386806532980000.jpg\" alt=\"\" width=\"841\" height=\"159\" /></p>\r\n<p><span style=\"font-family: Arial;\"><strong>M&Ocirc; TẢ C&Ocirc;NG VIỆC</strong><br /> * X&acirc;y dựng chiến lược, mục ti&ecirc;u, kế hoạch kinh doanh của Chi nh&aacute;nh theo từng thời kỳ, từng năm tr&ecirc;n cơ sở nhiệm vụ kinh doanh do C&ocirc;ng ty giao, ph&ugrave; hợp với chiến lược ph&aacute;t triển, phương hướng nhiệm vụ kinh doanh của C&ocirc;ng ty. Giao kế hoạch c&ocirc;ng t&aacute;c th&aacute;ng, qu&yacute;, năm đến từng đơn vị ph&ograve;ng, tổ nghiệp vụ tại Chi nh&aacute;nh.<br /> * Tổ chức v&agrave; điều h&agrave;nh hoạt động kinh doanh tại Chi nh&aacute;nh theo kế hoạch kinh doanh đ&atilde; được ph&ecirc; duyệt v&agrave; trong phạm vi được Tổng gi&aacute;m đốc ủy quyền. Chịu tr&aacute;ch nhiệm về kết quả hoạt động của Chi nh&aacute;nh.<br /> * Gi&aacute;m s&aacute;t việc thực hiện c&aacute;c quy chế, quy định, quy tr&igrave;nh nghiệp vụ theo quy định của ph&aacute;p luật, của C&ocirc;ng ty để đảm bảo an to&agrave;n vật tư, t&agrave;i sản, tiền vốn, con người.<br /> * B&aacute;o c&aacute;o C&ocirc;ng ty v&agrave; c&aacute;c cơ quan chức năng về kết quả hoạt động kinh doanh của Chi nh&aacute;nh theo th&aacute;ng, qu&yacute;, năm v&agrave; chịu tr&aacute;ch nhiệm về t&iacute;nh ch&iacute;nh x&aacute;c v&agrave; hợp ph&aacute;p của th&ocirc;ng tin v&agrave; số liệu đ&atilde; b&aacute;o c&aacute;o.<br /> * Chịu sự kiểm tra, gi&aacute;m s&aacute;t c&aacute;c cơ quan nh&agrave; nước c&oacute; thẩm quyền v&agrave; C&ocirc;ng ty. X&acirc;y dựng v&agrave; duy tr&igrave; mối quan hệ hợp t&aacute;c, tin cậy v&igrave; lợi &iacute;ch của kh&aacute;ch h&agrave;ng v&agrave; C&ocirc;ng ty. Thực hiện tốt chiến lược kh&aacute;ch h&agrave;ng trong từng thời kỳ của C&ocirc;ng ty.<br /> * Đề xuất với C&ocirc;ng ty c&aacute;c ch&iacute;nh s&aacute;ch về tiền lương, chi ph&iacute; v&agrave; phụ cấp ph&ugrave; hợp với hoạt động của Chi nh&aacute;nh trong từng thời kỳ.<br /> * Tuyển dụng c&aacute;n bộ cho Chi nh&aacute;nh theo quy chế tuyển dụng của C&ocirc;ng ty.</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>Y&Ecirc;U CẦU:</strong><br /> &bull; Tốt nghiệp đại học trở l&ecirc;n, chuy&ecirc;n ng&agrave;nh kinh tế, kỹ thuật.<br /> &bull; C&oacute; tối thiểu 05 năm kinh nghiệm trong lĩnh vực bảo hiểm phi nh&acirc;n thọ, trong đ&oacute; kinh nghiệm l&agrave;m quản l&yacute; điều h&agrave;nh tối thiểu 3 năm.<br /> &bull; C&oacute; kiến thức về luật kinh tế v&agrave; c&aacute;c ng&agrave;nh luật li&ecirc;n quan đến hoạt động của ng&agrave;nh bảo hiểm;<br /> &bull; Anh văn tr&igrave;nh độ C trở l&ecirc;n hoặc tương đương; Tin học văn ph&ograve;ng tr&igrave;nh độ B trở l&ecirc;n;<br /> &bull; Độ tuổi kh&ocirc;ng qu&aacute; 45 tuổi;<br /> &bull; C&oacute; chứng chỉ nghiệp vụ bảo hiểm do c&aacute;c cơ sở đ&agrave;o tạo về bảo hiểm được c&ocirc;ng nhận trong nước hoặc quốc tế cấp.<br /> &bull; Kỹ năng mềm:<br /> + Kỹ năng tổ chức, quản l&yacute; điều h&agrave;nh hiệu quả.<br /> + Kỹ năng đ&agrave;m ph&aacute;n/ thương thuyết tốt<br /> + Chủ động, quyết đo&aacute;n, s&aacute;ng tạo trong c&ocirc;ng việc<br /> + Khả năng l&agrave;m việc độc lập/theo nh&oacute;m tốt</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>CHẾ ĐỘ Đ&Atilde;I NGỘ</strong>:<br /> - Mức thu nhập hấp dẫn theo thỏa thuận<br /> - Cơ hội ph&aacute;t triển sự nghiệp bền vững c&ugrave;ng với sự lớn mạnh của C&ocirc;ng ty Bảo hiểm VietinBank;<br /> - Trang thiết bị l&agrave;m việc hiện đại; M&ocirc;i trường l&agrave;m việc năng động, chuy&ecirc;n nghiệp c&ugrave;ng đội ngũ cộng sự đắc lực;<br /> - Li&ecirc;n tục được đ&agrave;o tạo về kiến thức v&agrave; c&aacute;c kỹ năng tr&ecirc;n mọi lĩnh vực của Bảo hiểm;<br /> - L&agrave;m việc, học hỏi kinh nghiệm từ c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu trong lĩnh vực Bảo hiểm, t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng;<br /> - C&aacute;c đ&atilde;i ngộ kh&aacute;c: thưởng, ph&uacute;c lợi&hellip; theo quy định của C&ocirc;ng ty Bảo hiểm VietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>HỒ SƠ GỒM</strong>: (Đề nghị ứng vi&ecirc;n sắp xếp theo thứ tự như sau):<br /> &bull; Đơn xin việc;<br /> &bull; Phiếu Th&ocirc;ng tin ứng vi&ecirc;n theo mẫu của Bảo hiểm VietinBank. Download mẫu Tại đ&acirc;y<br /> &bull; Bằng tốt nghiệp đại học trở l&ecirc;n v&agrave; bảng điểm (c&ocirc;ng chứng);<br /> &bull; Chứng chỉ tiếng Anh tr&igrave;nh độ C v&agrave; tương đương trở l&ecirc;n (c&ocirc;ng chứng);<br /> &bull; Chứng chỉ tin học văn ph&ograve;ng tr&igrave;nh độ B trở l&ecirc;n (c&ocirc;ng chứng);<br /> &bull; Sơ yếu l&yacute; lịch c&oacute; x&aacute;c nhận (c&oacute; gi&aacute; trị trong v&ograve;ng 6 th&aacute;ng); Giấy khai sinh; Giấy kh&aacute;m sức khoẻ c&oacute; gi&aacute; trị trong v&ograve;ng 3 th&aacute;ng), 02 ảnh 4x6<br /> &bull; Giấy tờ chứng nhận qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c ( Quyết định, HĐLĐ,&hellip;)</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>ĐỊA ĐIỂM NỘP HỒ SƠ</strong>:<br /> Nộp hồ sơ trực tiếp trong giờ h&agrave;nh ch&iacute;nh hoặc gửi qua đường bưu điện về địa chỉ:<br /> * Ph&ograve;ng Tổ chức h&agrave;nh ch&iacute;nh, C&ocirc;ng ty Bảo hiểm VietinBank<br /> Tầng 10,11 T&ograve;a nh&agrave; Vietinbank số 126 Đội Cấn, Ba Đ&igrave;nh, H&agrave; Nội.<br /> * C&ocirc;ng ty Bảo hiểm VietinBank &ndash; CN HCM<br /> Tầng 4, 5 Số 354 &ndash; 356 Nguyễn Thị Minh Khai, Quận 3, TP HCM<br /> * C&ocirc;ng ty Bảo hiểm VietinBank &ndash; CN Đ&agrave; Nẵng<br /> Số 172 Nguyễn Văn Linh, Quận Thanh Kh&ecirc;, Đ&agrave; Nẵng</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>THỜI HẠN NHẬN HỒ SƠ:</strong> Từ&nbsp; ng&agrave;y 24/07/2013 đến 24/08/2013<br /> Lưu &yacute;: Kh&ocirc;ng ho&agrave;n lại hồ sơ đối với hồ sơ kh&ocirc;ng đạt y&ecirc;u cầu, hồ sơ kh&ocirc;ng tr&uacute;ng tuyển </span></p>', '2018-03-21 03:38:48', '2018-03-21 14:38:48', 'images/news/1520307315_img_284_635386806536670000_HasThumb_Thumb.jpg.png'),
(40, 2, 'Bổ nhiệm lãnh đạo Bảo hiểm VietinBank', 'bo-nhiem-lanh-dao-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Theo quyết định của HĐQT VietinBank, &ocirc;ng L&ecirc; Tuấn Dũng được bổ nhiệm giữ chức vụ Tổng Gi&aacute;m đốc, b&agrave; Đ&agrave;o Thị Chinh v&agrave; &ocirc;ng Nguyễn Hồng Phong giữ chức vụ Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty. </span></span></p>', '<p><strong><em>Theo quyết định của HĐQT VietinBank, &ocirc;ng L&ecirc; Tuấn Dũng được bổ nhiệm giữ chức vụ Tổng Gi&aacute;m đốc, b&agrave; Đ&agrave;o Thị Chinh v&agrave; &ocirc;ng Nguyễn Hồng Phong giữ chức vụ Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty.</em></strong></p>\r\n<p><img src=\"../../../uploads/img_2854.jpg\" alt=\"\" width=\"500\" height=\"289\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: L&atilde;nh đạo VietinBank ch&uacute;c mừng c&aacute;c l&atilde;nh đạo Bảo hiểm VietinBank mới được bổ nhiệm</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Chiều ng&agrave;y 31/7, tại H&agrave; Nội đ&atilde; diễn ra lễ c&ocirc;ng bố c&aacute;c quyết định của HĐQT VietinBank về việc bổ nhiệm Tổng gi&aacute;m đốc v&agrave; Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam. Ủy vi&ecirc;n HĐQT VietinBank- b&agrave; Nguyễn Hồng V&acirc;n, Trưởng ph&ograve;ng Tổ chức C&aacute;n bộ VietinBank- b&agrave; Trần Thu Huyền c&ugrave;ng kh&aacute;ch mời từ c&aacute;c Ph&ograve;ng ban Trụ sở ch&iacute;nh đ&atilde; tới dự buổi lễ.</span></p>\r\n<div class=\"content\">\r\n<div class=\"content-left\">\r\n<div class=\"detail-new\">\r\n<h2 class=\"title-new2\"><a href=\"http://vbi.vietinbank.vn/tin-tuc/bo-nhiem-lanh-dao-bao-hiem-vietinbank.htm\"> BỔ NHIỆM L&Atilde;NH ĐẠO BẢO HIỂM VIETINBANK </a></h2>\r\n<p class=\"date-new2\">08/06/2014</p>\r\n<div class=\"detail-new-content\">\r\n<p><strong><em>Theo quyết định của HĐQT VietinBank, &ocirc;ng L&ecirc; Tuấn Dũng được bổ nhiệm giữ chức vụ Tổng Gi&aacute;m đốc, b&agrave; Đ&agrave;o Thị Chinh v&agrave; &ocirc;ng Nguyễn Hồng Phong giữ chức vụ Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty.</em></strong><br /> &nbsp;</p>\r\n<p><img src=\"../../../uploads/img_2854.jpg\" alt=\"\" width=\"500\" height=\"289\" /></p>\r\n<p><em>Ảnh: L&atilde;nh đạo VietinBank ch&uacute;c mừng c&aacute;c l&atilde;nh đạo Bảo hiểm VietinBank mới được bổ nhiệm</em></p>\r\n<p>Chiều ng&agrave;y 31/7, tại H&agrave; Nội đ&atilde; diễn ra lễ c&ocirc;ng bố c&aacute;c quyết định của HĐQT VietinBank về việc bổ nhiệm Tổng gi&aacute;m đốc v&agrave; Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam. Ủy vi&ecirc;n HĐQT VietinBank- b&agrave; Nguyễn Hồng V&acirc;n, Trưởng ph&ograve;ng Tổ chức C&aacute;n bộ VietinBank- b&agrave; Trần Thu Huyền c&ugrave;ng kh&aacute;ch mời từ c&aacute;c Ph&ograve;ng ban Trụ sở ch&iacute;nh đ&atilde; tới dự buổi lễ.</p>\r\n<p>Theo c&aacute;c quyết định bổ nhiệm, &ocirc;ng L&ecirc; Tuấn Dũng- Ph&oacute; Tổng gi&aacute;m đốc giữ chức vụ Tổng gi&aacute;m đốc; b&agrave; Đ&agrave;o Thị Chinh- Gi&aacute;m đốc Chi nh&aacute;nh Bảo hiểm VietinBank TP. Hồ Ch&iacute; Minh giữ chức vụ Ph&oacute; Tổng gi&aacute;m đốc; v&agrave; &ocirc;ng Nguyễn Hồng Phong- Trưởng ph&ograve;ng Quản l&yacute; nghiệp vụ giữ chức vụ Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty. C&aacute;c Quyết định c&oacute; hiệu lực kể từ ng&agrave;y 17/7/2013.</p>\r\n<p>Quy tr&igrave;nh, tr&igrave;nh tự v&agrave; thủ tục bổ nhiệm c&aacute;c chức vụ l&atilde;nh đạo của C&ocirc;ng ty được thực hiện đ&uacute;ng theo quy định c&ocirc;ng t&aacute;c c&aacute;n bộ với sự đồng thuận, nhất tr&iacute; cao của <span id=\"s0511916qhe9_6\" class=\"s0511916qhe9\">ban</span> Thường vụ Đảng ủy v&agrave; HĐQT VietinBank.</p>\r\n<p><img src=\"../../../uploads/img_2844.jpg\" alt=\"\" width=\"500\" height=\"693\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Ủy vi&ecirc;n HĐQT VietinBank trao quyết định cho &ocirc;ng L&ecirc; Tuấn Dũng</em></p>\r\n<p><img src=\"../../../uploads/img_2847.jpg\" alt=\"\" width=\"500\" height=\"392\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Ủy vi&ecirc;n HĐQT VietinBank trao quyết định cho b&agrave; Đ&agrave;o Thị Chinh</em></p>\r\n<p><img src=\"../../../uploads/img_2850.jpg\" alt=\"\" width=\"500\" height=\"665\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Ủy vi&ecirc;n HĐQT VietinBank trao quyết định cho &ocirc;ng Nguyễn Hồng Phong</em></p>\r\n<p>Tại buổi lễ, Chủ tịch C&ocirc;ng ty Nguyễn Hồng V&acirc;n ch&uacute;c mừng ba c&aacute;n bộ được bổ nhiệm, ghi nhận v&agrave; đ&aacute;nh gi&aacute; cao nỗ lực phấn đấu của từng c&aacute; nh&acirc;n trong việc ph&aacute;t <span id=\"s0511916qhe9_5\" class=\"s0511916qhe9\">tri</span>ển hoạt động kinh doanh của C&ocirc;ng ty, đồng thời thể hiện sự động vi&ecirc;n, tin tưởng v&agrave;o bộ m&aacute;y l&atilde;nh đạo mới sẽ gi&uacute;p Bảo hiểm VietinBank vượt qua giai đoạn kinh tế kh&oacute; khăn, nhiều biến động để khẳng định được vị thế tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p>Thay mặt cho c&aacute;c l&atilde;nh đạo được bổ nhiệm, &ocirc;ng L&ecirc; Tuấn Dũng b&agrave;y tỏ l&ograve;ng biết ơn s&acirc;u sắc tới HĐQT v&agrave; c&aacute;c Ph&ograve;ng ban VietinBank đ&atilde; tin tưởng v&agrave; ủng hộ c&aacute;c hoạt động của C&ocirc;ng ty. &Ocirc;ng cam kết sẽ x&acirc;y dựng Bảo hiểm VietinBank th&agrave;nh một C&ocirc;ng ty bảo hiểm lớn v&agrave; c&oacute; thương hiệu tr&ecirc;n thị trường bảo hiểm, để xứng đ&aacute;ng l&agrave; một đơn vị th&agrave;nh vi&ecirc;n đang kho&aacute;c tr&ecirc;n m&igrave;nh \"m&agrave;u cờ sắc &aacute;o\" của VietinBank.</p>\r\n<p><img src=\"../../../uploads/img_2863.jpg\" alt=\"\" width=\"500\" height=\"249\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Đại biểu VietinBank v&agrave; tập thể c&aacute;n bộ Bảo hiểm VietinBank ch&uacute;c mừng Ban l&atilde;nh đạo mới</em></p>\r\n<p style=\"text-align: right;\"><em><strong>Bảo hiểm VietinBank</strong></em></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2018-03-21 03:40:55', '2018-03-21 14:40:55', 'images/news/1520307546_pic-New-i_635386894357620000_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(41, 2, 'Bảo hiểm VietinBank làm việc cùng Khối bán lẻ BTMU', 'bao-hiem-vietinbank-lam-viec-cung-khoi-ban-le-btmu', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 31/7 vừa qua, đo&agrave;n L&atilde;nh đạo cấp cao Khối B&aacute;n lẻ Ng&acirc;n h&agrave;ng Tokyo- Mitsubishi UFJ (BTMU) đ&atilde; c&oacute; chuyến thăm v&agrave; trao đổi c&ugrave;ng Bảo hiểm VietinBank (VBI) tại Trụ sở ch&iacute;nh C&ocirc;ng ty&hellip; </span></span></p>', '<p><img src=\"../../../uploads/img_2832.jpg\" alt=\"\" width=\"500\" height=\"333\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Đo&agrave;n Đại biểu cấp cao BTMU v&agrave; C&ocirc;ng ty T&agrave;i ch&iacute;nh ACOM</em></span></p>\r\n<p><span style=\"font-family: Arial;\">BTMU hiện l&agrave; cổ đ&ocirc;ng chiến lược với 20% vốn chủ sở hữu của VietinBank. Trong khu&ocirc;n khổ hợp t&aacute;c, hỗ trợ kĩ thuật, Khối b&aacute;n lẻ VietinBank v&agrave; Khối b&aacute;n lẻ BTMU đ&atilde; li&ecirc;n tục c&oacute; những chuyến thăm v&agrave; trao đổi kinh nghiệm nhằm n&acirc;ng cao hiệu quả hoạt động kinh doanh của Ng&acirc;n h&agrave;ng n&oacute;i chung v&agrave; Khối bản lẻ n&oacute;i ri&ecirc;ng. Buổi gặp gỡ đ&atilde; diễn ra tốt đẹp với sự tham gia của c&aacute;c th&agrave;nh vi&ecirc;n trong Khối b&aacute;n lẻ BTMU, C&ocirc;ng ty T&agrave;i ch&iacute;nh ACOM (C&ocirc;ng ty con trực thuộc BTMU), Khối b&aacute;n lẻ VietinBank v&agrave; Ban l&atilde;nh đạo Bảo hiểm VietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi trao đổi, L&atilde;nh đạo Bảo hiểm VietinBank đ&atilde; tr&igrave;nh b&agrave;y về to&agrave;n cảnh thị trường bảo hiểm Việt Nam, thực trạng t&igrave;nh h&igrave;nh kinh doanh của VBI, qua đ&oacute; ph&acirc;n t&iacute;<span id=\"kf796w_2\" class=\"kf796w\">ch nh</span>ững điểm mạnh, điểm yếu cũ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>ư cơ hội, th&aacute;ch thức của C&ocirc;ng ty trong bối cảnh hiện nay. L&atilde;nh đạo VBI cũng nhấn mạnh về hoạt động Bancassurance giữa VietinBank v&agrave; VBI, trong đ&oacute; định vị việc ph&aacute;t triển Bảo hiểm VietinBank trở th&agrave;nh C&ocirc;ng ty h&agrave;ng đầu tại Việt Nam về hoạt động Bancassurance, đặc biệt trong lĩnh vực b&aacute;n lẻ- một lĩnh vực v&ocirc; c&ugrave;ng tiềm năng cần khai th&aacute;c. VBI cũng đề xuất BTMU trao đổi kinh nghiệm &aacute;p dụng m&ocirc; h&igrave;nh Bancassurance tại Nhật Bản v&agrave; chia sẻ cơ sở dữ liệu kh&aacute;ch h&agrave;ng Nhật Bản tại Việt Nam để VBI c&oacute; thể tiếp cận v&agrave; cung cấp dịch vụ của m&igrave;nh.</span></p>\r\n<p><img src=\"../../../uploads/download.jpg\" alt=\"\" width=\"500\" height=\"277\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: L&atilde;nh đạo Bảo hiểm VietinBank tr&igrave;nh b&agrave;y về thực tế <span id=\"kf796w_8\" class=\"kf796w\">tri</span>ển khai m&ocirc; h&igrave;nh <span id=\"kf796w_4\" class=\"kf796w\">Bancassurance</span></em></span></p>\r\n<p><span style=\"font-family: Arial;\">Đo&agrave;n Đại biểu BTMU v&agrave; ACOM cũng thể hiện sự quan t&acirc;m s&acirc;u sắc tới c&aacute;c chỉ số t&agrave;i ch&iacute;nh của Bảo hiểm VietinBank (về thị phần, doanh thu từ ph&iacute; bảo hiểm, lợi nhuận trước thuế,&hellip;), những sản phẩm chủ lực cũng như chiến lược ph&aacute;t triển của C&ocirc;ng ty. Được biết hiện nay BTMU chưa th&agrave;nh lập C&ocirc;ng ty th&agrave;nh vi&ecirc;n chuy&ecirc;n về Bảo hiểm, tuy nhi&ecirc;n, nhận thức r&otilde; tầm quan trọng của m&ocirc; h&igrave;nh Bancassurance đối với một Ng&acirc;n h&agrave;ng đang c&oacute; định hướng ph&aacute;t triển th&agrave;nh tập đo&agrave;n như VietinBank, Ban L&atilde;nh đạo BTMU b&agrave;y tỏ hi vọng sẽ c&oacute; th&ecirc;m nhiều cơ hội hợp t&aacute;c với Bảo hiểm VietinBank trong thời gian tới.</span></p>\r\n<p><img src=\"../../../uploads/img_2827.jpg\" alt=\"\" width=\"500\" height=\"333\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: L&atilde;nh đạo BTMU trao đổi kinh nghiệm n&acirc;ng cao hiệu quả kinh doanh</em></span></p>', '2018-03-21 03:45:01', '2018-03-21 14:45:01', 'images/news/1520308571_pic-New-i_635386884303360000_HasThumb_Thumb.jpg.png'),
(42, 2, 'Đào tạo nghiệp vụ bảo hiểm tại VietinBank Tây Tiền Giang', 'dao-tao-nghiep-vu-bao-hiem-tai-vietinbank-tay-tien-giang', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Tất cả c&aacute;n bộ quan hệ kh&aacute;ch h&agrave;ng v&agrave; giao dịch vi&ecirc;n của VietinBank T&acirc;y Tiền Giang vừa được VBI đ&agrave;o tạo nghiệp vụ bảo hiểm&hellip; </span></span></p>', '<p><strong><em>Tất cả c&aacute;n bộ quan hệ kh&aacute;ch h&agrave;ng v&agrave; giao dịch vi&ecirc;n của VietinBank T&acirc;y Tiền Giang vừa được VBI đ&agrave;o tạo nghiệp vụ bảo hiểm&hellip;</em></strong></p>\r\n<p><img src=\"../../../uploads/635386809246560000.jpg\" alt=\"\" width=\"1153\" height=\"515\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Đại diện VBI Chi nh&aacute;nh&nbsp; S&agrave;i G&ograve;n thuyết tr&igrave;nh tại buổi đ&agrave;o tạo</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Chiều 10/8 tại Tiền Giang, Bảo hiểm VietinBank (VBI) -CN S&agrave;i G&ograve;n phối hợp với VietinBank T&acirc;y Tiền Giang tổ chức buổi đ&agrave;o tạo nghiệp vụ, hướng dẫn <span id=\"kf796w_7\" class=\"kf796w\">tri</span>ển khai c&aacute;c sản phẩm bảo hiểm cho tr&ecirc;n 35 c&aacute;n bộ quan hệ kh&aacute;ch h&agrave;ng, giao dịch vi&ecirc;n của CN.</span></p>\r\n<p>Chương tr&igrave;nh đ&agrave;o tạo c&oacute; nội dung đa dạng, ph&ugrave; hợp với hoạt động thực tiễn tại CN với hai phần ch&iacute;nh: M&ocirc; h&igrave;nh Bancassurance v&agrave; c&aacute;c sản phẩm bảo hiểm của VBI như: VietinCar, VietinCare, VietinHome cũng như c&aacute;c kỹ năng cần thiết khi tư vấn, giới thiệu sản phẩm bảo hiểm đến kh&aacute;ch h&agrave;ng; Thực h&agrave;nh c&aacute;c thao t&aacute;c ph&aacute;t h&agrave;nh bảo hiểm cho kh&aacute;ch h&agrave;ng tr&ecirc;n phần mềm của VBI v&agrave; c&ocirc;ng t&aacute;c quản l&yacute;, sử dụng ấn chỉ bảo hiểm.</p>\r\n<p>Buổi đ&agrave;o tạo diễn ra trong kh&ocirc;ng kh&iacute; s&ocirc;i nổi, cầu thị v&agrave; cởi mở, c&aacute;c giảng vi&ecirc;n đưa ra những t&igrave;nh huống thực tế phong ph&uacute;, khuyến kh&iacute;ch c&aacute;c c&aacute;n bộ c&ugrave;ng tham gia trao đổi, n&acirc;ng cao kỹ năng nghiệp vụ bảo hiểm trong qu&aacute; tr&igrave;nh tư vấn, cung cấp sản phẩm dịch vụ cho kh&aacute;ch h&agrave;ng. Tại buổi đ&agrave;o tạo rất nhiều những vướng mắc trong qu&aacute; tr&igrave;nh thực tế khi cung cấp c&aacute;c sản phẩm bảo hiểm v&agrave; những &yacute; kiến đề xuất đến VBI nhằm g&oacute;p phần cải tiến c&aacute;c sản phẩm bảo hiểm ng&agrave;y c&agrave;ng tốt hơn cũng được c&aacute;c c&aacute;n bộ quan hệ kh&aacute;ch h&agrave;ng đ&oacute;ng g&oacute;p.</p>\r\n<p>Những kiến thức, kinh nghiệm thực tế được chia sẻ tại kh&oacute;a học gi&uacute;p &iacute;ch rất nhiều cho c&aacute;n bộ của CN khai th&aacute;c tốt c&aacute;c sản phẩm bảo hiểm do VBI cung cấp cho c&aacute;c kh&aacute;ch h&agrave;ng tại CN, qua đ&oacute; n&acirc;ng cao chất lượng sản phẩm dịch vụ, khai th&aacute;c tốt thị trường b&aacute;n lẻ tại địa phương.</p>\r\n<p style=\"text-align: right;\"><strong><em>Phương Th&ugrave;y</em></strong></p>', '2018-03-21 03:45:50', '2018-03-21 14:45:50', 'images/news/1520308892_pic-New-i_635386809247940000_HasThumb_Thumb.jpg.png'),
(43, 2, 'Bảo hiểm VietinBank (VBI) đẩy mạnh văn hóa doanh nghiệp qua các hoạt động ngoại khóa', 'bao-hiem-vietinbank-vbi-day-manh-van-hoa-doanh-nghiep-qua-cac-hoat-dong-ngoai-khoa', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">B&ecirc;n cạnh văn h&oacute;a l&agrave;m việc lu&ocirc;n được thể hiện trong c&aacute;c hoạt động ch&iacute;nh của C&ocirc;ng ty, văn h&oacute;a trong tổ chức c&aacute;c hoạt động ngoại kh&oacute;a c&oacute; &yacute; nghĩa v&ocirc; c&ugrave;ng quan trọng trong việc gắn kết c&aacute;c th&agrave;nh vi&ecirc;n, thắt chặt mối quan hệ giữa c&aacute;c tổ chức, giải tỏa sức &eacute;p cũng như r&egrave;n luyện sức khỏe ... </span></span></p>', '<p>B&ecirc;n cạnh văn h&oacute;a l&agrave;m việc lu&ocirc;n được thể hiện trong c&aacute;c hoạt động ch&iacute;nh của C&ocirc;ng ty, văn h&oacute;a trong tổ chức c&aacute;c hoạt động ngoại kh&oacute;a c&oacute; &yacute; nghĩa v&ocirc; c&ugrave;ng quan trọng trong việc gắn kết c&aacute;c th&agrave;nh vi&ecirc;n, thắt chặt mối quan hệ giữa c&aacute;c tổ chức, giải tỏa sức &eacute;p cũng như r&egrave;n luyện sức khỏe cả về thể chất lẫn tinh thần cho c&aacute;n bộ nh&acirc;n vi&ecirc;n.</p>\r\n<p>Nắm bắt được tầm quan trọng của hoạt động ngoại kh&oacute;a trong việc tạo n&ecirc;n những gi&aacute; trị v&ocirc; h&igrave;nh cho C&ocirc;ng ty, trong thời gian qua, Ban L&atilde;nh đạo Bảo hiểm VietinBank đ&atilde; lu&ocirc;n ch&uacute; trọng đến việc tổ chức c&aacute;c s&acirc;n chơi bổ &iacute;ch cho c&aacute;n bộ nh&acirc;n vi&ecirc;n qua c&aacute;c hoạt động ngoại kh&oacute;a như: văn h&oacute;a thể thao, nghỉ m&aacute;t, d&atilde; ngoại, giao lưu ngo&agrave;i trời&hellip;</p>\r\n<p>Trong m&ugrave;a h&egrave; vừa qua, Bảo hiểm VietinBank đ&atilde; li&ecirc;n tục tổ chức c&aacute;c trận b&oacute;ng đ&aacute; giao hữu với c&aacute;c ph&ograve;ng ban v&agrave; c&aacute;c đơn vị trực thuộc VietinBank như: Trận b&oacute;ng giữa Bảo hiểm VietinBank- TSC v&agrave; CN H&agrave; Nội với ph&ograve;ng C&ocirc;ng nghệ th&ocirc;ng tin VietinBank, giữa Bảo hiểm VietinBank- CN Thanh H&oacute;a, Hội c&aacute;n bộ hưu tr&iacute; VietinBank với Chi nh&aacute;nh VietinBank Thanh H&oacute;a&hellip;</p>\r\n<p><img src=\"../../../uploads/img_0012.jpg\" alt=\"\" width=\"500\" height=\"205\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Đội b&oacute;ng VBI- TSC, CN H&agrave; Nội v&agrave; Ph&ograve;ng CNTT VietinBank chụp ảnh lưu niệm</em></p>\r\n<p><img src=\"../../../uploads/img_7296.jpg\" alt=\"\" width=\"500\" height=\"417\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Trận cầu s&ocirc;i nổi giữa VBI v&agrave; Ph&ograve;ng CNTT VieitinBank</em></p>\r\n<p>B&ecirc;n cạnh c&aacute;c hoạt động thể dục thể thao, giao lưu với c&aacute;c đơn vị bạn, VBI c&ograve;n tổ chức c&aacute;c buổi giao lưu ngo&agrave;i trời với c&aacute;c hoạt động như team building, d&atilde; ngoại&hellip; nhằm thắt chặt tinh thần đo&agrave;n kết trong nội bộ Bảo hiểm VietinBank cũng như tiếp th&ecirc;m năng lượng cho c&aacute;c c&aacute;n bộ nh&acirc;n vi&ecirc;n để l&agrave;m việc tốt hơn.</p>\r\n<p>Với phương ch&acirc;m &ldquo;l&agrave;m nhiệt t&igrave;nh, chơi hết m&igrave;nh&rdquo;, to&agrave;n thể c&aacute;n bộ nh&acirc;n vi&ecirc;n Bảo hiểm VietinBank- Chi nh&aacute;nh S&agrave;i G&ograve;n đ&atilde; c&oacute; một chuyến nghỉ m&aacute;t đầy bổ &iacute;ch tại biển Long Hải (Vũng T&agrave;u). Kh&eacute;p lại chuyến đi, ai ai cũng phấn khởi v&igrave; c&oacute; những ph&uacute;t gi&acirc;y vui chơi thoải m&aacute;i, nạp đầy đủ năng lượng để c&ugrave;ng nhau tăng tốc thực hiện chỉ ti&ecirc;u kinh doanh của đơn vị, hi vọng sẽ c&oacute; bước nước r&uacute;t thần tốc trong qu&yacute; IV, g&oacute;p một phần v&agrave;o việc thực hiện kế hoạch 2013 của Bảo hiểm Vietinbank.</p>\r\n<p><img src=\"../../../uploads/68.jpg\" alt=\"\" width=\"500\" height=\"339\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Tập thể c&aacute;n bộ nh&acirc;n vi&ecirc;n VBI- CN S&agrave;i G&ograve;n nghỉ m&aacute;t tại biển Long Hải</em></p>\r\n<p><img src=\"../../../uploads/69.jpg\" alt=\"\" width=\"500\" height=\"338\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: Hoạt động team building của c&aacute;n bộ nh&acirc;n vi&ecirc;n VBI- CN S&agrave;i G&ograve;n</em></p>\r\n<p style=\"text-align: right;\"><strong><em>Hải V&acirc;n</em></strong></p>', '2018-03-21 03:50:22', '2018-03-21 14:50:22', 'images/news/1520309113_pic-New-i_635386879365760000_HasThumb_Thumb.jpg.png'),
(44, 2, 'Bảo hiểm VietinBank bổ nhiệm một số vị trí lãnh đạo phòng Trụ sở chính', 'bao-hiem-vietinbank-bo-nhiem-mot-so-vi-tri-lanh-dao-phong-tru-so-chinh', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">S&aacute;ng ng&agrave;y 7/9 tại H&agrave; Nội đ&atilde; diễn ra lễ c&ocirc;ng bố c&aacute;c quyết định về việc bổ nhiệm một số vị tr&iacute; l&atilde;nh đạo ph&ograve;ng Trụ sở ch&iacute;nh Bảo hiểm VietinBank </span></span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 7/9 tại H&agrave; Nội đ&atilde; diễn ra lễ c&ocirc;ng bố c&aacute;c quyết định về việc bổ nhiệm một số vị tr&iacute; l&atilde;nh đạo ph&ograve;ng Trụ sở ch&iacute;nh Bảo hiểm VietinBank. Tham dự lễ trao quyết định c&oacute; Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể l&atilde;nh đạo c&aacute;c ph&ograve;ng ban Trụ sở ch&iacute;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Theo c&aacute;c quyết định bổ nhiệm, b&agrave; L&ecirc; Thị Quỳnh Hoa giữ chức vụ Trưởng ph&ograve;ng PTKD; b&agrave; Đỗ Qu&yacute; Chi giữ chức vụ Ph&oacute; ph&ograve;ng PTKD; &ocirc;ng Đỗ Đức Thắng giữ chức vụ Trưởng ph&ograve;ng CNTT; &ocirc;ng Nguyễn Ngọc Nam giữ chức vụ Ph&oacute; ph&ograve;ng QLNN &amp; ĐGRR. Quy tr&igrave;nh, tr&igrave;nh tự v&agrave; thủ tục bổ nhiệm c&aacute;c chức vụ l&atilde;nh đạo của C&ocirc;ng ty được thực hiện đ&uacute;ng theo quy định c&ocirc;ng t&aacute;c c&aacute;n bộ với sự đồng thuận, nhất tr&iacute; cao của <span id=\"kf796w_7\" class=\"kf796w\">Ban</span> L&atilde;nh đạo C&ocirc;ng ty.</span></p>\r\n<p><img src=\"../../../uploads/791.jpg\" alt=\"\" width=\"500\" height=\"333\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: C&aacute;c c&aacute;n bộ mới được bổ nhiệm</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Tổng gi&aacute;m đốc C&ocirc;ng ty, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm, ch&uacute;c mừng c&aacute;c c&aacute;n bộ nhận nhiệm vụ mới, đồng thời n&ecirc;u r&otilde;: Việc bổ nhiệm c&aacute;c vị tr&iacute; l&atilde;nh đạo mới lần n&agrave;y c&oacute; &yacute; nghĩa chiến lược trong việc kiện to&agrave;n bộ m&aacute;y tổ chức của Bảo hiểm VietinBank, g&oacute;p phần v&agrave;o định hướng ph&aacute;t <span id=\"kf796w_6\" class=\"kf796w\">tri</span>ển mới của C&ocirc;ng ty theo m&ocirc; h&igrave;nh <span id=\"kf796w_2\" class=\"kf796w\">Bancassurance</span>.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu nhận nhiệm vụ, c&aacute;c l&atilde;nh đạo ph&ograve;ng gửi lời cảm ơn tới Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; tin tưởng giao ph&oacute; trọng tr&aacute;ch mới, đồng thời cam kết sẽ tiếp tục phấn đấu để tiếp tục cống hiến, đưa Bảo hiểm VietinBank trở th&agrave;nh C&ocirc;ng ty bảo hiểm đ&aacute;ng tin cậy tr&ecirc;n thị trường, đồng thời g&oacute;p phần v&agrave;o việc thực hiện kế hoạch ph&aacute;t triển chung của VietinBank.</span></p>\r\n<p><img src=\"../../../uploads/792.jpg\" alt=\"\" width=\"500\" height=\"259\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Ban L&atilde;nh đạo C&ocirc;ng ty ch&uacute;c mừng c&aacute;c l&atilde;nh đạo ph&ograve;ng mới</em></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Ph&ograve;ng PTKD</em></strong></span></p>', '2018-03-21 03:51:13', '2018-03-21 14:51:13', 'images/news/1520309881_pic-New-i_635386873540060000_HasThumb_Thumb.jpg.png'),
(45, 2, 'Bảo hiểm VietinBank điều động và bổ nhiệm nhân sự chi nhánh Thanh Hóa', 'bao-hiem-vietinbank-dieu-dong-va-bo-nhiem-nhan-su-chi-nhanh-thanh-hoa', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 11/9/2013, tại Bảo hiểm VietinBank- chi nh&aacute;nh Thanh H&oacute;a đ&atilde; diễn ra buổi lễ trao c&aacute;c quyết định bổ nhiệm v&agrave; điều động nh&acirc;n sự đối với l&atilde;nh đạo Chi nh&aacute;nh v&agrave; c&aacute;c Ph&ograve;ng trực thuộc. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 11/9/2013, tại Bảo hiểm VietinBank- chi nh&aacute;nh Thanh H&oacute;a đ&atilde; diễn ra buổi lễ trao c&aacute;c quyết định bổ nhiệm v&agrave; điều độ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>&acirc;n sự đối với l&atilde;nh đạo Chi nh&aacute;nh v&agrave; c&aacute;c Ph&ograve;ng trực thuộc. Tổng Gi&aacute;m đốc L&ecirc; Tuấn Dũng c&ugrave;ng l&atilde;nh đạo một số ph&ograve;ng ban Trụ sở ch&iacute;nh đ&atilde; tham dự v&agrave; trao c&aacute;c quyết định như sau:<br /> -&nbsp;Điều động v&agrave; bổ nhiệm &ocirc;ng Nguyễn Ngọc T&uacute;, nguy&ecirc;n Ph&oacute; ph&ograve;ng phụ tr&aacute;ch ph&ograve;ng Kinh doanh 2 Trụ sở ch&iacute;nh giữ chức vụ Gi&aacute;m đốc Chi nh&aacute;nh Bảo hiểm VietinBank Thanh H&oacute;a;<br /> -&nbsp;Bổ nhiệm &ocirc;ng Phạm Văn Đức giữ chức vụ Trưởng ph&ograve;ng Kế to&aacute;n- H&agrave;nh ch&iacute;nh;<br /> -&nbsp;Điều động &ocirc;ng Nguyễn Minh Cường, Nh&acirc;n vi&ecirc;n ph&ograve;ng Kinh doanh 2 Trụ sở ch&iacute;nh giữ chức vụ Ph&oacute; ph&ograve;ng Kinh doanh khu vực Thanh H&oacute;a;<br /> -&nbsp;Điều động b&agrave; Đinh Thị Thu, Nh&acirc;n vi&ecirc;n ph&ograve;ng Kinh doanh 2 Trụ sở ch&iacute;nh giữ chức vụ Ph&oacute; ph&ograve;ng Kinh doanh khu vực Nghệ An.</span></p>\r\n<p><img src=\"../../../uploads/635386813924570000.jpg\" alt=\"\" width=\"274\" height=\"240\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: &Ocirc;ng Nguyễn Ngọc T&uacute; nhận quyết định Gi&aacute;m đốc chi nh&aacute;nh Thanh H&oacute;a</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Tổng Gi&aacute;m đốc phụ tr&aacute;ch L&ecirc; Tuấn Dũng đ&atilde; ch&uacute;c mừng c&aacute;c c&aacute;n bộ được bổ nhiệm; ghi nhận v&agrave; đ&aacute;nh gi&aacute; nỗ lực phấn đấu c&aacute;c c&aacute; nh&acirc;n tại Chi nh&aacute;nh trong thời gian vừa qua, đồng thời y&ecirc;u cầu Chi nh&aacute;nh cần chủ động, s&aacute;ng tạo, nỗ lực hơn nữa để khẳng định vị tr&iacute; của một chi nh&aacute;nh mới của Bảo hiểm VietinBank, g&oacute;p phần ho&agrave;n th&agrave;nh kế hoạch kinh doanh năm 2013 của cả C&ocirc;ng ty.</span></p>\r\n<p><img src=\"../../../uploads/635386813930740000.jpg\" alt=\"\" width=\"320\" height=\"159\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: <span id=\"kf796w_6\" class=\"kf796w\">Ban</span> L&atilde;nh đạo C&ocirc;ng ty ch&uacute;c mừng c&aacute;c c&aacute;n bộ được bổ nhiệm</em></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Ph&ograve;ng PTKD</em></strong></span></p>', '2018-03-21 03:53:53', '2018-03-21 14:53:53', 'images/news/1520309979_pic-New-i_635386813932140000_HasThumb_Thumb.JPG.png'),
(46, 2, 'VBI góp phần bảo toàn một phần vốn vay của VietinBank nhờ Bảo hiểm hỏa hoạn', 'vbi-gop-phan-bao-toan-mot-phan-von-vay-cua-vietinbank-nho-bao-hiem-hoa-hoan', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">L&agrave; một c&ocirc;ng ty bảo hiểm trong hệ thống Ng&acirc;n h&agrave;ng Vietinbank, Bảo hiểm Vietinbank- VBI lu&ocirc;n x&aacute;c định mục ti&ecirc;u h&agrave;ng đầu l&agrave; bảo to&agrave;n nguồn vốn vay của Ng&acirc;n h&agrave;ng. </span></span></p>', '<p><span style=\"font-family: Arial;\">L&agrave; một c&ocirc;ng ty bảo hiểm trong hệ thống Ng&acirc;n h&agrave;ng Vietinbank, Bảo hiểm Vietinbank- VBI lu&ocirc;n x&aacute;c định mục ti&ecirc;u h&agrave;ng đầu l&agrave; bảo to&agrave;n nguồn vốn vay của Ng&acirc;n h&agrave;ng.<br />Trong những năm qua, sản xuất kinh doanh ng&agrave;y c&agrave;ng mở rộng gắn liền với những rủi ro tiềm ẩn, trong đ&oacute; rủi ro ch&aacute;y nổ l&agrave; một trong những rủi ro c&oacute; mức độ t&agrave;n ph&aacute; nặng nề nhất đối với doanh nghiệp v&agrave; l&agrave;m ảnh hưởng đ&aacute;ng kể đến nguồn vốn vay của Ng&acirc;n h&agrave;ng. Gần đ&acirc;y nhất l&agrave; vụ ch&aacute;y Trung t&acirc;m thương mại Hải Dương ng&agrave;y 15/09/2013 với ước t&iacute;nh sơ bộ tổn thất 500 tỷ đồng hay vụ ch&aacute;y nh&agrave; m&aacute;y giầy Pou- Yuen Việt Nam ng&agrave;y 21/08/2013 với ước t&iacute;nh tổn thất h&agrave;ng chục tỷ đồng&hellip;</span></p>\r\n<p><img src=\"../../../uploads/635386814453410000.jpg\" alt=\"\" width=\"500\" height=\"397\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Vụ ch&aacute;y TTTM Hải Dương với thiệt hại ước t&iacute;nh 500 tỷ đồng</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Với việc tham gia bảo hiểm cho t&agrave;i sản đảm bảo khi vay vốn v&agrave; chuyển quyền thụ hưởng lại cho Ng&acirc;n h&agrave;ng, VBI đ&atilde; gi&uacute;p đảm bảo an to&agrave;n về nguồn vốn vay cũ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>ư t&agrave;i sản cho c&aacute;c c&aacute; nh&acirc;n, doanh nghiệp l&agrave; kh&aacute;ch h&agrave;ng của VietinBank trong suốt thời gian vay vốn của của c&aacute;c đối tượng n&agrave;y. Thực tế trong thời gian vừa qua, VBI đ&atilde; tiến h&agrave;nh chi trả một số vụ tổn thất lớn li&ecirc;n quan đến hỏa hoạn, điển h&igrave;nh l&agrave; vụ ch&aacute;y C&ocirc;ng ty bao b&igrave; Nhơn Trạch &ndash; một kh&aacute;ch h&agrave;ng t&iacute;n dụng lớn của VietinBank Nhơn Trạch với số tiền bồi thường l&ecirc;n tới 62 tỷ đồng.<br /> Hiện nay, Bảo hiểm Vietinbank đ&atilde; cung cấp đầy đủ những sản phẩm bảo hiểm t&agrave;i sản để đ&aacute;p ứng nhu cầu của thị trường như: bảo hiểm ch&aacute;y nổ bắt buộc theo quy định của Ch&iacute;nh phủ, bảo hiểm ch&aacute;y v&agrave; c&aacute;c rủi ro đặc biệt, bảo hiểm mọi rủi ro t&agrave;i sản. Đặc biệt, VBI c&ograve;n phối hợp với Vietinbank để x&acirc;y dựng chương tr&igrave;nh bảo hiểm t&agrave;i sản d&agrave;nh ri&ecirc;ng cho c&aacute;c Hộ tiểu thương c&oacute; tham gia vay vốn tại Ng&acirc;n h&agrave;ng.<br /> Trong sự ph&aacute;t <span id=\"kf796w_6\" class=\"kf796w\">tri</span>ển của thị trường bảo hiểm n&oacute;i chung v&agrave; bảo hiểm hỏa hoạn n&oacute;i ri&ecirc;ng tại Việt Nam, Bảo hiểm Vietinbank với tư c&aacute;ch l&agrave; một đơn vị cung cấp bảo hiểm uy t&iacute;n đ&atilde; đang v&agrave; sẽ l&agrave; chỗ dựa t&acirc;m l&yacute; vững chắc, đảm bảo an to&agrave;n t&agrave;i ch&iacute;nh cho mọi đối tượng tham gia bảo hiểm, g&oacute;p phần đảm bảo an to&agrave;n cho nguồn vốn vay của VietinBank.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em><strong>Ngọc Nam</strong></em></span></p>', '2018-03-21 03:54:33', '2018-03-21 14:54:33', 'images/news/1520310125_pic-New-i_635386814454200000_HasThumb_Thumb.jpg.png'),
(47, 2, 'Bảo hiểm tích lũy cho con', 'bao-hiem-tich-luy-cho-con', '<p>&nbsp;<span style=\"font-family: Arial;\">Con c&aacute;i lu&ocirc;n l&agrave; vốn qu&yacute; của mỗi bậc l&agrave;m cha l&agrave;m mẹ, l&agrave; khởi nguồn niềm <span id=\"kf796w_6\" class=\"kf796w\">tin</span>, niềm hạnh ph&uacute;c của mỗi gia đ&igrave;nh.Tuy nhi&ecirc;n, b&ecirc;n cạnh những điều tốt đẹp đ&oacute; l&agrave; những rủi ro kh&ocirc;ng mong muốn c&oacute; thể xảy ra bất cứ khi n&agrave;o đối với ch&iacute;nh bạn v&agrave; những đứa con th&acirc;n y&ecirc;u.</span></p>', '<p><span style=\"font-family: Arial;\">Con c&aacute;i lu&ocirc;n l&agrave; vốn qu&yacute; của mỗi bậc l&agrave;m cha l&agrave;m mẹ, l&agrave; khởi nguồn niềm <span id=\"kf796w_6\" class=\"kf796w\">tin</span>, niềm hạnh ph&uacute;c của mỗi gia đ&igrave;nh.Tuy nhi&ecirc;n, b&ecirc;n cạnh những điều tốt đẹp đ&oacute; l&agrave; những rủi ro kh&ocirc;ng mong muốn c&oacute; thể xảy ra bất cứ khi n&agrave;o đối với ch&iacute;nh bạn v&agrave; những đứa con th&acirc;n y&ecirc;u.</span></p>\r\n<p><img src=\"../../../uploads/635386815132830000.jpg\" alt=\"\" width=\"285\" height=\"262\" /></p>\r\n<p><span style=\"font-family: Arial;\">Trước thực tế đ&oacute;, Bảo hiểm VietinBank đ&atilde; kết hợp với Ng&acirc;n h&agrave;ng C&ocirc;ng thương thiết kế chương tr&igrave;nh d&agrave;nh ri&ecirc;ng cho đối tượng l&agrave; bố/mẹ c&oacute; kế hoạch t&iacute;ch lũy một khoản tiền trong tương lai để d&agrave;nh tặng cho con.Với những ưu điểm vượt trội, sản phẩm đ&atilde; mang đến giải ph&aacute;p mới để bảo vệ kh&aacute;ch h&agrave;ng gửi tiền v&agrave; những đứa con th&acirc;n y&ecirc;u của họ.</span></p>\r\n<p>&bull;&nbsp;&nbsp;&nbsp; Cung cấp giải ph&aacute;p t&agrave;i ch&iacute;nh tối ưu&nbsp;&nbsp;&nbsp;</p>\r\n<p>L&agrave; sản phẩm ưu việt kết hợp giữa tiết kiệm,t&iacute;ch lũy v&agrave; bảo hiểm<br /> &bull;&nbsp;&nbsp; &nbsp;Tối đa h&oacute;a lợi &iacute;ch cho kh&aacute;ch h&agrave;ng<br /> Quyền lợi bảo hiểm phong ph&uacute; cho cả bố/mẹ v&agrave; con (ngo&agrave;i quyền lợi ch&iacute;nh, kh&aacute;ch h&agrave;ng được hỗ trợ về lương đối với bố mẹ v&agrave; chi ph&iacute; y tế đối với người con)<br /> &bull;&nbsp;&nbsp; &nbsp;Bảo hiểm với mức ph&iacute; thấp, quyền lợi cao<br /> Chỉ với tỷ lệ ph&iacute; bằng 0,5% số tiền dự định t&iacute;ch lũy,kh&aacute;ch h&agrave;ng sẽ nhận được mức chi trả l&ecirc;n đến 2 tỷ đồng<br /> &bull;&nbsp;&nbsp; &nbsp;Thủ tục bồi thường nhanh ch&oacute;ng, thuận tiện v&agrave; dịch vụ trợ gi&uacute;p 24/24</p>\r\n<p><span style=\"font-family: Arial;\">Kh&aacute;ch h&agrave;ng chỉ cần gọi đến số <span id=\"kf796w_2\" class=\"kf796w\">Hotline</span> của VBI để được tư vấn miễn ph&iacute; v&agrave; bồi thường nhanh ch&oacute;ng<br /> Bảo hiểm &ldquo;T&iacute;ch Lũy Cho Con&rdquo; l&agrave; giải ph&aacute;p bảo vệ tối ưu gi&uacute;p bạn giảm nỗi lo về rủi ro trong&nbsp; cuộc sống kết hợp tiết kiệm t&agrave;i ch&iacute;nh để hướng tới một tương lai tốt đẹp hơn. Chương tr&igrave;nh &aacute;p dụng từ ng&agrave;y 01/9/2013.</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>Mọi chi tiết xin vui l&ograve;ng li&ecirc;n hệ :</strong><br /> <strong>Địa chỉ</strong>: Tầng 10- 11, t&ograve;a nh&agrave; 126 Đội Cấn, Ba Đ&igrave;nh, H&agrave; Nội<br /> <strong>Điện thoại</strong>: 04 3942 5650 (m&aacute;y lẻ 602, 603)<br /> <strong><span id=\"kf796w_1\" class=\"kf796w\">Email</span></strong>: bancas.vbi@vietinbank.vn</span></p>', '2018-03-21 03:54:59', '2018-03-21 14:54:59', 'images/news/1520390369_pic-New-i_635386815135140000_HasThumb_Thumb.jpg.png'),
(48, 2, 'VBI hỗ trợ Vietinbank Thanh Hóa thu hồi vốn vay gần 1 tỷ đồng', 'vbi-ho-tro-vietinbank-thanh-hoa-thu-hoi-von-vay-gan-1-ty-dong', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Rất nhiều trường hợp Ng&acirc;n h&agrave;ng cho vay mua xe &ocirc; t&ocirc; m&agrave; kh&ocirc;ng thể thu đ&ograve;i nợ do chiếc xe l&agrave; t&agrave;i sản đảm bảo ch&iacute;nh bị thiệt hại nghi&ecirc;m trọng do tai nạn, ch&aacute;y nổ&hellip; </span></span></p>', '<p><span style=\"font-family: Arial;\">Rất nhiều trường hợp Ng&acirc;n h&agrave;ng cho vay mua xe &ocirc; t&ocirc; m&agrave; kh&ocirc;ng thể thu đ&ograve;i nợ do chiếc xe l&agrave; t&agrave;i sản đảm bảo ch&iacute;nh bị thiệt hại nghi&ecirc;m trọng do tai nạn, ch&aacute;y nổ&hellip; Tuy nhi&ecirc;n nếu kh&aacute;ch h&agrave;ng lựa chọn một doanh nghiệp bảo hiểm c&oacute; uy t&iacute;n để tham gia bảo hiểm cho chiếc xe th&igrave; sẽ cả Ng&acirc;n h&agrave;ng v&agrave; kh&aacute;ch h&agrave;ng đều kh&ocirc;ng phải lo lắng trước khả năng trả nợ v&igrave; đ&atilde; c&oacute; sự tham gia kịp thời của C&ocirc;ng ty bảo hiểm.<br /> Ng&agrave;y 28/05/2013, chiếc xe &ocirc; t&ocirc; Camry của Doanh nghiệp tư nh&acirc;n Tuấn H&ugrave;ng c&oacute; gi&aacute; trị gần 1 tỷ đồng (thế chấp để vay t&iacute;n dụng của Vietinbank CN Thanh H&oacute;a với mục đ&iacute;ch sản xuất kinh doanh) bị ch&aacute;y khi đang lưu th&ocirc;ng tại Thiệu Giao, Thiệu H&oacute;a, Thanh H&oacute;a. Chiếc xe c&oacute; tham gia bảo hiểm &ocirc; t&ocirc; của Bảo hiểm VietinBank (VBI) với quyền thụ hưởng bảo hiểm được chuyển giao cho Ng&acirc;n h&agrave;ng. Ngay khi nhận được th&ocirc;ng tin, VBI đ&atilde; nhanh ch&oacute;ng hướng dẫn kh&aacute;ch h&agrave;ng c&aacute;ch thức khắc phục sự cố nhanh ch&oacute;ng v&agrave; kịp thời qua <span id=\"kf796w_2\" class=\"kf796w\">hotline</span> 24/24, đồng thời cử nh&acirc;n vi&ecirc;n trực tiếp đến hiện trường phối hợp với VietinBank để c&ugrave;ng với kh&aacute;ch h&agrave;ng giải quyết v&agrave; ho&agrave;n thiện c&aacute;c thủ tục bồi thường</span></p>\r\n<p><img src=\"../../../uploads/635386817323060000.jpg\" alt=\"\" width=\"640\" height=\"480\" /></p>\r\n<p><span style=\"font-family: Arial;\">Theo Hợp đồng bảo hiể<span id=\"kf796w_6\" class=\"kf796w\">m</span>, khi vay vốn, kh&aacute;ch h&agrave;ng đ&atilde; chuyển quyền thụ hưởng bảo hiểm cho Ng&acirc;n h&agrave;ng, do đ&oacute;, ngay khi c&oacute; kết luận điều tra cuối c&ugrave;ng, VBI đ&atilde; tiến h&agrave;nh ho&agrave;n tất việc đền b&ugrave; to&agrave;n bộ gi&aacute; trị của chiếc xe cho VietinBank Thanh H&oacute;a. Doanh nghiệp Tuấn H&ugrave;ng cảm thấy h&agrave;i l&ograve;ng v&igrave; giải quyết được m&oacute;n nợ lớn trong thời gian ngắn khi kh&ocirc;ng may sự cố xảy ra. VietinBank Thanh H&oacute;a cũng thu hồi được vốn vay v&agrave; tr&aacute;nh được khoản nợ kh&oacute; đ&ograve;i, th&ecirc;m v&agrave;o đ&oacute; c&ugrave;ng với VBI thể hiện được sự quan t&acirc;m gi&uacute;p đỡ đối với những kh&aacute;ch h&agrave;ng trong l&uacute;c kh&oacute; khăn.<br /> Ngo&agrave;i trường hợp tr&ecirc;n, trong năm 2013, VBI đ&atilde; giải quyết rất nhiều vụ tổn thất đối với t&agrave;i sản đảm bảo của c&aacute;c kh&aacute;ch h&agrave;ng vay vốn v&agrave; thế chấp tại Ng&acirc;n h&agrave;ng, gi&uacute;p cho VietinBank thu hồi số tiền vay l&ecirc;n tới h&agrave;ng chục tỷ đồng, điển h&igrave;nh l&agrave; vụ ch&aacute;y C&ocirc;ng ty bao b&igrave; Nhơn Trạch- kh&aacute;ch h&agrave;ng t&iacute;n dụng của VietinBank Nhơn Trạch với số tiền bồi thường l&ecirc;n tới 37 tỷ đồng hay vụ tổn thất xe &ocirc; t&ocirc; đối với kh&aacute;ch h&agrave;ng vay vốn tại VietinBank Ba Đ&igrave;nh với số tiền bồi thường l&agrave; 282 <span id=\"kf796w_5\" class=\"kf796w\">tri</span>ệu đồng&hellip;</span></p>\r\n<p><span style=\"font-family: Arial;\">C&oacute; thể n&oacute;i, với quy m&ocirc; dư nợ lớn nhất Việt Nam hiện nay như VietinBank, quản trị rủi ro lu&ocirc;n l&agrave; ti&ecirc;u ch&iacute; h&agrave;ng đầu cho sự ph&aacute;t triển bền vững của Ng&acirc;n h&agrave;ng. Bảo hiểm VietinBank trong thời gian qua đ&atilde; phối hợp với VietinBank l&agrave;m tốt bổn phận của m&igrave;nh, g&oacute;p phần v&agrave;o việc tăng cường quản trị rủi ro hoạt động cho to&agrave;n hệ thống Ng&acirc;n h&agrave;ng.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Phương Anh</em></span></p>', '2018-03-21 03:55:30', '2018-03-21 14:55:30', 'images/news/1520390461_pic-New-i_635386817324720000_HasThumb_Thumb.jpg.png'),
(49, 2, 'VBI và VietinBank Hà Tĩnh trao tặng 134 phần quà cho bà con vùng lũ', 'vbi-va-vietinbank-ha-tinh-trao-tang-134-phan-qua-cho-ba-con-vung-lu', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">S&aacute;ng 26/10, Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank- VBI v&agrave; VietinBank Chi nh&aacute;nh H&agrave; Tĩnh đ&atilde; phối hợp c&ugrave;ng Đảng ủy Khối doanh nghiệp H&agrave; Tĩnh đến thăm hỏi v&agrave; trao tặng 134 suất qu&agrave; cho b&agrave; con nh&acirc;n d&acirc;n x&atilde; H&agrave; Linh, huyện Hương Kh&ecirc;, tỉnh H&agrave; Tĩnh </span></span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng 26/10, Đo&agrave;n thanh ni&ecirc;n Bảo hiể<span id=\"kf796w_7\" class=\"kf796w\">m</span> VietinBank- VBI v&agrave; VietinBank Chi nh&aacute;nh H&agrave; Tĩnh đ&atilde; phối hợp c&ugrave;ng Đảng ủy Khối doanh nghiệp H&agrave; Tĩnh đến thăm hỏi v&agrave; trao tặng 134 suất qu&agrave; cho b&agrave; con nh&acirc;n d&acirc;n x&atilde; H&agrave; Linh, huyện Hương Kh&ecirc;, tỉnh H&agrave; Tĩnh</span></p>\r\n<p><img src=\"../../../uploads/635386818540400000.jpg\" alt=\"\" width=\"500\" height=\"333\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Đại diện Đo&agrave;n thanh ni&ecirc;n hai đơn vị VietinBank v&agrave; Đảng ủy Khối DN H&agrave; Tĩnh trao qu&agrave; cho b&agrave; con ngh&egrave;o x&atilde; H&agrave; Linh</em></span></p>\r\n<p><span style=\"font-family: Arial;\">X&atilde; H&agrave; Linh l&agrave; một trong số những x&atilde; ngh&egrave;o tại H&agrave; Tĩnh với tr&ecirc;n 25% hộ ngh&egrave;o, đồng thời cũng l&agrave; x&atilde; phải g&aacute;nh chịu những tổn thất nặng nề sau đợt b&atilde;o lụt th&aacute;ng 10 vừa qua với tổng thiệt hại l&ecirc;n tới hơn 3,5 tỷ đồng.<br />Trong những ng&agrave;y qua, ph&aacute;t huy truyền thống &ldquo;tương th&acirc;n tương &aacute;i&rdquo; c&ugrave;a Ng&acirc;n h&agrave;ng, với khẩu hiệu &ldquo;Đo&agrave;n kết, s&aacute;ng tạo, chung sức v&igrave; cộng đồng&rdquo;, Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank v&agrave; VietinBank H&agrave; Tĩnh đ&atilde; ph&aacute;t động to&agrave;n thể c&aacute;n bộ nh&acirc;n vi&ecirc;n hai đơn vị quy&ecirc;n g&oacute;p ủng hộ nhằm g&oacute;p phần chia sẻ những thiệt hại do mưa lũ g&acirc;y ra đối với b&agrave; con nh&acirc;n d&acirc;n tr&ecirc;n địa b&agrave;n x&atilde; H&agrave; Linh. Tại UBND x&atilde;, 134 hộ gia đ&igrave;nh đ&atilde; trực tiếp nhận những phần qu&agrave; nh&acirc;n &aacute;i từ hai đơn vị VietinBank, mỗi phần qu&agrave; trị gi&aacute; gần 400 ngh&igrave;n đồng, bao gồm tiền mặt, m&igrave; t&ocirc;m, gạo, s&aacute;ch vở, quần &aacute;o v&agrave; nhiều vật dụng cần thiết kh&aacute;c.</span></p>\r\n<p><img src=\"../../../uploads/635386818546480000.jpg\" alt=\"\" width=\"500\" height=\"334\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: B&agrave; con đ&oacute;n nhận những m&oacute;n qu&agrave; hảo t&acirc;m từ Đo&agrave;n vi&ecirc;n VBI v&agrave; VietinBank</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Theo thống k&ecirc; của <span id=\"kf796w_6\" class=\"kf796w\">Ban</span> tiếp nhận v&agrave; ph&acirc;n phối h&agrave;ng cứu trợ lũ lụt tỉnh H&agrave; Tĩnh, t&iacute;nh đến s&aacute;ng 26/10, đ&atilde; c&oacute; 4 đo&agrave;n đến ủng hộ b&agrave; con x&atilde; H&agrave; Linh với tổng số tiền hơn 120 <span id=\"kf796w_5\" class=\"kf796w\">tri</span>ệu đồng. Con số n&agrave;y c&ograve;n rất nhỏ so với những g&igrave; m&agrave; b&agrave; con phải hứng chịu trong đợt thi&ecirc;n tai vừa qua, v&igrave; thế, nụ cười của b&agrave; con khi nhận những phần qu&agrave; vẫn hằn l&ecirc;n nỗi lo về những kh&oacute; khăn trước mắt. Hi vọng rằng ng&agrave;y c&agrave;ng c&oacute; th&ecirc;m nhiều tấm l&ograve;ng hảo t&acirc;m hướng về x&atilde; H&agrave; Linh n&oacute;i ri&ecirc;ng, c&aacute;c tỉnh miền Trung lũ lụt n&oacute;i chung để tiếp th&ecirc;m động lực gi&uacute;p b&agrave; con ngh&egrave;o tiếp tục cuộc sống v&agrave; y&ecirc;n t&acirc;m sản xuất.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Hải V&acirc;n</em></span></p>', '2018-03-21 03:56:11', '2018-03-21 14:56:11', 'images/news/1520390593_pic-New-i_635386818552630000_HasThumb_Thumb.jpg.png'),
(50, 2, 'VBI tạm ứng bồi thường thiệt hại 7 tỷ đồng cho Xi măng Công Thanh trong cơn bão số 10', 'vbi-tam-ung-boi-thuong-thiet-hai-7-ty-dong-cho-xi-mang-cong-thanh-trong-con-bao-so-10', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Cơn b&atilde;o số 10 với những trận mưa lũ k&eacute;o d&agrave;i từ ng&agrave;y 30/9/2013 đến 02/10/2013 vừa qua đ&atilde; g&acirc;y ra những thiệt hại nặng nề, kh&ocirc;ng chỉ với c&aacute;c c&aacute; nh&acirc;n, hộ gia đ&igrave;nh m&agrave; với cả những doanh nghiệp đang hoạt động tr&ecirc;n địa b&agrave;n v&ugrave;ng lũ. </span></span></p>', '<p><span style=\"font-family: Arial;\">Cơn b&atilde;o số 10 với những trận mưa lũ k&eacute;o d&agrave;i từ ng&agrave;y 30/9/2013 đến 02/10/2013 vừa qua đ&atilde; g&acirc;y ra những thiệt hại nặng nề, kh&ocirc;ng chỉ với c&aacute;c c&aacute; nh&acirc;n, hộ gia đ&igrave;nh m&agrave; với cả những doanh nghiệp đang hoạt động tr&ecirc;n địa b&agrave;n v&ugrave;ng lũ.</span></p>\r\n<p><span style=\"font-family: Arial;\">L&agrave; một trong những kh&aacute;ch h&agrave;ng vay vốn l&acirc;u năm của VietinBank, đồng thời tham gia bảo hiểm tại Bảo hiểm Vietinbank (VBI), Nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh, Thanh H&oacute;a cũng l&agrave; một trong số những doanh nghiệp trực tiếp chịu ảnh hưởng từ cơn b&atilde;o. Trước đ&oacute;, Xi măng C&ocirc;ng Thanh được biết đến l&agrave; một trong những nh&agrave; m&aacute;y xi măng c&oacute; quy m&ocirc; lớn nhất to&agrave;n quốc, đang trong qu&aacute; tr&igrave;nh x&acirc;y dựng với c&ocirc;ng suất dự &aacute;n đạt 12.000 tấn clinker/ng&agrave;y, 3,6 <span id=\"kf796w_6\" class=\"kf796w\">tri</span>ệu tấn clinker/năm với tổng số vốn đạt 420 triệu USD (khoảng 7.600 tỷ Việt Nam đồng).</span></p>\r\n<p><span style=\"font-family: Arial;\">Ng&agrave;y 01/10/2013, nhận được th&ocirc;ng b&aacute;o thiệt hại từ nh&agrave; m&aacute;y, VBI đ&atilde; nhanh ch&oacute;ng chỉ định C&ocirc;ng ty gi&aacute;m định <span id=\"kf796w_2\" class=\"kf796w\">Crawford</span> v&agrave; c&aacute;c chuy&ecirc;n gia nước ngo&agrave;i l&agrave; Belfor đến từ Th&aacute;i Lan v&agrave; Forensic đến từ Malaysia, phối hợp với Vietinbank để c&oacute; những kết luận nhanh ch&oacute;ng, kh&aacute;ch quan v&agrave; ch&iacute;nh x&aacute;c nhất về mức độ tổn thất để kịp thời hỗ trợ kh&aacute;ch h&agrave;ng.</span></p>\r\n<p><img src=\"../../../uploads/635386819221130000.png\" alt=\"\" width=\"600\" height=\"450\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: VBI phối hợp cơ quan gi&aacute;m định nhanh ch&oacute;ng c&oacute; mặt tại hiện trường</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Sau khi c&oacute; kết quả gi&aacute;m định, thiệt hại m&agrave; Xi măng C&ocirc;ng Thanh phải hứng chịu sau cơn b&atilde;o số 10 l&ecirc;n đến tr&ecirc;n 65 tỷ đồng, c&aacute;c tổn thất chủ yếu bao gồm tổn thất c&aacute;c phần cố định được x&acirc;y dựng ki&ecirc;n cố như phần k&egrave; taluy đ&aacute; hộc x&acirc;y, mương B&ecirc; t&ocirc;ng, đường R24, nền đường vận chuyển đ&atilde; v&ocirc;i, tường r&agrave;o, đường mỏ ch&iacute;nh đ&aacute; v&ocirc;i,&hellip; bị sạt lở hoặc nứt vỡ. Ngo&agrave;i ra, mưa lũ đ&atilde; l&agrave;m ngập 282 kiện h&agrave;ng, cuộn bảo &ocirc;n bị ẩm ướt khoảng 23.000 cuộn ~ 137 tấn v&agrave; vật liệu chịu lửa (bao gồm gạch v&agrave; xi măng chịu lửa).</span></p>\r\n<p><img src=\"../../../uploads/635386819228790000.png\" alt=\"\" width=\"623\" height=\"511\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Tổn thất do lũ tại Nh&agrave; m&aacute;y Xi măng C&ocirc;ng Thanh</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Trong chưa đầy một th&aacute;ng, với sự phối hợp chặt chẽ v&agrave; chuy&ecirc;n nghiệp giữa VBI v&agrave; hai nh&agrave; gi&aacute;m định nước ngo&agrave;i, Xi măng C&ocirc;ng Thanh đ&atilde; nhận được khoản chi trả bồi thường đ&aacute;ng kể. Ng&agrave;y 26/10, sau khi ho&agrave;n th&agrave;nh thủ tục đ&aacute;nh gi&aacute; bồi thường, VBI đ&atilde; trực tiếp chuyển 7 tỷ đồng tạm ứng cho doanh nghiệp để khắc phục hậu quả v&agrave; x&acirc;y dựng lại hệ thống cho kịp tiến độ sản xuất. C&oacute; thể n&oacute;i, việc chi trả bồi thường kịp thời kh&ocirc;ng chỉ gi&uacute;p doanh nghiệp tiếp tục an t&acirc;m sản xuất, đảm bảo nguồn vốn vay cho VietinBank m&agrave; c&ograve;n thể hiện sự nỗ lực của VBI trong việc cải thiện chất lượng dịch vụ bồi thường.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Nguồn <span id=\"kf796w_8\" class=\"kf796w\">tin</span>: Ph&ograve;ng Bồi thường VB</em></strong></span></p>', '2018-03-21 03:56:50', '2018-03-21 14:56:50', 'images/news/1520390708_congthanh_635405241499540000_HasThumb_Thumb.jpg.png'),
(51, 2, 'Bổ nhiệm Giám đốc VBI chi nhánh Tp.HCM', 'bo-nhiem-giam-doc-vbi-chi-nhanh-tp-hcm', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 2/11 tại Tp.HCM, VBI tổ chức lễ trao quyết định bổ nhiệm Gi&aacute;m đốc chi nh&aacute;nh Tp.HCM cho &ocirc;ng Trần Tiến Sỹ, nguy&ecirc;n Ph&oacute; Gi&aacute;m đốc C&ocirc;ng ty Bảo hiểm PTI Bến Th&agrave;nh. </span></span></p>', '<p><img src=\"../../../uploads/635386820219210000.jpg\" alt=\"\" width=\"500\" height=\"460\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Tổng gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng trao quyết định bổ nhiệm Gi&aacute;m đốc VBI HCM cho &ocirc;ng Trấn Tiến Sỹ</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Tổng gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm v&agrave; ch&uacute;c mừng &ocirc;ng Trần Tiến Sỹ được <span id=\"kf796w_6\" class=\"kf796w\">Ban</span> l&atilde;nh đạo C&ocirc;ng ty tin tưởng, bổ nhiệm v&agrave;o vị tr&iacute; Gi&aacute;m đốc VBI &ndash; chi nh&aacute;nh Tp.HCM (VBI HCM). &Ocirc;ng mong rằng với kinh nghiệm phong ph&uacute; của gần 20 năm hoạt động trong lĩnh vực bảo hiểm, &ocirc;ng Trần Tiến Sỹ sẽ điều h&agrave;nh VBI HCM vượt qua giai đoạn kh&oacute; khăn thử th&aacute;ch hiện nay của thị trường bảo hiểm, ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh của VBI HCM.</span></p>\r\n<p><span style=\"font-family: Arial;\">Đ&aacute;p lại sự <span id=\"kf796w_8\" class=\"kf796w\">tin</span> tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng Trần Tiến Sỹ thể hiện quyết t&acirc;m phấn đấu để VBI HCM ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đưa VBI HCM trở th&agrave;nh đơn vị ti&ecirc;u biểu, dẫn đầu cả nước về hoạt động kinh doanh bảo hiểm.</span></p>\r\n<p><img src=\"../../../uploads/635386820223300000.jpg\" alt=\"\" width=\"500\" height=\"359\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Ban L&atilde;nh đạo v&agrave; c&aacute;n bộ nh&acirc;n vi&ecirc;n ch&uacute;c mừng c&aacute;c l&atilde;nh đạo được bổ nhiệm</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Cũng tại buổi lễ, VBI c&ograve;n trao quyết định th&agrave;nh lập v&agrave; bổ nhiệm &ocirc;ng Ng&ocirc; Quang Trung, nguy&ecirc;n Ph&oacute; gi&aacute;m đốc C&ocirc;ng ty Bảo hiểm PTI Đồng Nai giữ chức vụ Trưởng ph&ograve;ng Bảo hiểm khu vực Đồng Nai trực thuộc VBI HCM. Việc th&agrave;nh lập v&agrave; bổ nhiệm l&atilde;nh đạo Ph&ograve;ng bảo hiểm khu vực Đồng Nai, VBI sẽ tăng cường sự hiện diện của Bảo hiểm VietinBank tại địa phương, tiếp cận trực tiếp v&agrave; <span id=\"kf796w_5\" class=\"kf796w\">tri</span>ển khai nhanh c&aacute;c sản phẩm bảo hiểm đến tận tay kh&aacute;ch h&agrave;ng, cung cấp tốt dịch vụ chăm s&oacute;c sau b&aacute;n h&agrave;ng, tạo sự y&ecirc;n t&acirc;m v&agrave; tin tưởng của kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm tại Bảo hiểm VietinBank ở một thị trường tiềm năng v&agrave; lớn nhất khu vực Đ&ocirc;ng Nam Bộ.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em><strong>Nguyễn Đức Dũng</strong></em></span></p>', '2018-03-21 03:57:30', '2018-03-21 14:57:30', 'images/news/1520390814_pic-New-i_635386820228730000_HasThumb_Thumb.JPG.png'),
(52, 2, 'VBI Hà Nội tìm được “Người thuyết trình hay nhất” chương trình “Muôn kết nối- Trọn tin yêu”', 'vbi-ha-noi-tim-duoc-nguoi-thuyet-trinh-hay-nhat-chuong-trinh-muon-ket-noi-tron-tin-yeu', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">H&ograve;a chung kh&ocirc;ng kh&iacute; thi đua ho&agrave;n th&agrave;nh xuất sắc kế hoạch trong năm, đồng thời thực hiện chủ trương đẩy mạnh m&ocirc; h&igrave;nh b&aacute;n lẻ, vừa qua, VBI &ndash; Chi nh&aacute;nh H&agrave; nội đ&atilde; tiến h&agrave;nh tổ chức cuộc thi &ldquo;Người thuyết tr&igrave;nh hay nhất của Chương tr&igrave;nh Mu&ocirc;n kết nối &ndash; Trọn tin y&ecirc;u&rdquo;. </span></span></p>', '<p><span style=\"font-family: Arial;\">H&ograve;a chung kh&ocirc;ng kh&iacute; thi đ<span id=\"kf796w_7\" class=\"kf796w\">ua</span> ho&agrave;n th&agrave;nh xuất sắc kế hoạch trong năm, đồng thời thực hiện chủ trương đẩy mạnh m&ocirc; h&igrave;nh b&aacute;n lẻ, vừa qua, VBI &ndash; Chi nh&aacute;nh H&agrave; nội đ&atilde; tiến h&agrave;nh tổ chức cuộc thi &ldquo;Người thuyết tr&igrave;nh hay nhất của Chương tr&igrave;nh Mu&ocirc;n kết nối &ndash; Trọn tin y&ecirc;u&rdquo;.</span></p>\r\n<p><span style=\"font-family: Arial;\">Cuộc thi đ&atilde; nhận được sự tham gia nhiệt t&igrave;nh của to&agrave;n thể c&aacute;n bộ kinh doanh Chi nh&aacute;nh bảo hiểm VietinBank H&agrave; Nội. Đ&acirc;y cũng ch&iacute;nh l&agrave; 1 buổi giao lưu, trau dồi kiến thức cho tất cả c&aacute;c c&aacute;n bộ kinh doanh của Chi nh&aacute;nh, nhằm năng cao khả năng tr&igrave;nh b&agrave;y tr&igrave;nh b&agrave;y trước đ&aacute;m đ&ocirc;ng, n&acirc;ng cao khả năng tự tin khi giao tiếp cũ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>ư tư vấn cho kh&aacute;ch h&agrave;ng.</span></p>\r\n<p><img src=\"../../../uploads/635386820933160000.jpg\" alt=\"\" width=\"720\" height=\"540\" /></p>\r\n<p><span style=\"font-family: Arial;\">Với sự đầu tư kỹ lưỡng cho cuộc thi, kết quả cuối c&ugrave;ng chị Ho&agrave;ng Thị Nhung &ndash; C&aacute;n bộ Ph&ograve;ng kinh doanh Thăng Long đ&atilde; gi&agrave;nh được giải thưởng &ldquo;Người thuyết tr&igrave;nh hay nhất&rdquo; của cuộc thi.</span></p>\r\n<p><span style=\"font-family: Arial;\">Kết th&uacute;c cuộc thi, đồng ch&iacute; Gi&aacute;m đốc VBI &ndash; Chi nh&aacute;nh H&agrave; nội đ&atilde; tuy&ecirc;n dương người gi&agrave;nh chiến thắng đồng thời cũng nhấn mạnh Chi nh&aacute;nh VBI H&agrave; nội sẽ tiếp tục tổ chức c&aacute;c cuộc thi tương tự để tạo những cơ sở học tập cũng như kh&iacute;ch lệ tinh thần của c&aacute;n bộ nh&acirc;n vi&ecirc;n, n&acirc;ng cao kiến thức nghiệp vụ nhằm ng&agrave;y c&agrave;ng đưa Chi nh&aacute;nh H&agrave; nội ph&aacute;t <span id=\"kf796w_6\" class=\"kf796w\">tri</span>ển vững mạnh.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>B&agrave;i: Đỗ B&iacute;ch Ch&acirc;m</em></span></p>', '2018-03-21 03:57:59', '2018-03-21 14:57:59', 'images/news/1520390900_pic-New-i_635386820934280000_HasThumb_Thumb.JPG.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(53, 2, 'Truyền thông về Bảo hiểm sức khỏe tại Trụ sở chính VietinBank', 'truyen-thong-ve-bao-hiem-suc-khoe-tai-tru-so-chinh-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Vừa qua, đại diện Ban l&atilde;nh đạo v&agrave; c&aacute;n bộ Bảo hiểm VietinBank (VBI) đ&atilde; c&oacute; buổi tuy&ecirc;n truyền &yacute; nghĩa về Bảo hiểm sức khỏe cho c&aacute;n bộ VietinBank v&agrave; người nh&agrave; tại Trụ sở ch&iacute;nh 108 Trần Hưng Đạo. </span></span></p>', '<p><span style=\"font-family: Arial;\">Vừa qua, đại diện <span id=\"kf796w_7\" class=\"kf796w\">Ban</span> l&atilde;nh đạo v&agrave; c&aacute;n bộ Bảo hiểm VietinBank (VBI) đ&atilde; c&oacute; buổi tuy&ecirc;n truyền &yacute; nghĩa về Bảo hiểm sức khỏe cho c&aacute;n bộ VietinBank v&agrave; người nh&agrave; tại Trụ sở ch&iacute;nh 108 Trần Hưng Đạo.<br /> Đến dự buổi truyền th&ocirc;ng gồm c&oacute; đại diện C&ocirc;ng đo&agrave;n VietinBank, l&atilde;nh đạo nh&acirc;n vi&ecirc;n Trụ sở ch&iacute;nh VietinBank v&agrave; VBI c&ugrave;ng c&aacute;c b&aacute;c sĩ tư vấn sức khỏe đến từ C&ocirc;ng ty Insmart- đối t&aacute;c tư vấn, hỗ trợ dịch vụ bảo hiểm sức khỏe của VBI.</span></p>\r\n<p><img src=\"../../../uploads/635386821552010000.jpg\" alt=\"\" width=\"995\" height=\"664\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Tổng Gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng chia sẻ những lợi &iacute;ch của BHSK với c&aacute;n bộ, nh&acirc;n vi&ecirc;n VietinBank</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Nhận thức được việc quan t&acirc;m tới sức khỏe của c&aacute;n bộ nh&acirc;n vi&ecirc;n cũng ch&iacute;nh l&agrave; một ch&iacute;nh s&aacute;ch thu h&uacute;t nh&acirc;n t&agrave;i, đ&atilde;i ngộ người lao động, đồng thời g&oacute;p phần tăng năng suất lao động, VBI đ&atilde; thiết kế g&oacute;i sản phẩm ph&ugrave; hợp nhất để giới thiệu tới c&aacute;c c&aacute;n bộ Trụ sở ch&iacute;nh VietinBank.<br />Bảo hiểm sức khỏe (VBI Care) l&agrave; một trong những sản phẩm được thiết kế ri&ecirc;ng d&agrave;nh cho c&aacute;n bộ, nh&acirc;n vi&ecirc;n VietinBank v&agrave; người nh&agrave; với những t&iacute;nh năng vượt trội như: bảo hiểm sức khỏe to&agrave;n diện, quyền lợi cao với điều trị nội ngoại tr&uacute;, bảo l&atilde;nh tại c&aacute;c bệnh viện uy t&iacute;n trong v&agrave; ngo&agrave;i nước, đặc biệt hơn dịch vụ bồi thường qua Insmart với những t&iacute;nh năng th&ocirc;ng minh (hồ sơ kiểm so&aacute;t online, dịch vụ nhắc t&aacute;i kh&aacute;m, đường d&acirc;y n&oacute;ng 24/24 d&agrave;nh ri&ecirc;ng cho c&aacute;n bộ VietinBank v&agrave; người nh&agrave; để hỗ trợ dịch vụ kh&aacute;m v&agrave; l&agrave;m thủ tục tại c&aacute;c bệnh viện)&hellip; Ngo&agrave;i ra, VBI c&ograve;n hỗ trợ giảm tới 40% ph&iacute; nhằm đ&aacute;p ứng nhu cầu kh&aacute;m chữa bệnh ng&agrave;y c&agrave;ng gia tăng của kh&aacute;ch h&agrave;ng.</span></p>\r\n<p><img src=\"../../../uploads/635386821555640000.jpg\" alt=\"\" width=\"1037\" height=\"691\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Đại diện Insmart hướng dẫn c&aacute;n bộ, nh&acirc;n vi&ecirc;n VietinBank sử dụng BHSK một c&aacute;ch tiện &iacute;<span id=\"kf796w_2\" class=\"kf796w\">ch nh</span>ất</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Kh&ocirc;ng chỉ giới thiệu sản phẩm Bảo hiểm sức khỏe của VBI, buổi truyền th&ocirc;ng c&ograve;n l&agrave; nơi trao đổi c&aacute;c kinh nghiệm kh&aacute;m chữa bệnh ở c&aacute;c bệnh viện, trung t&acirc;m y tế tại Việt Nam v&agrave; nước ngo&agrave;i. Qua buổi tuy&ecirc;n truyền, VBI nhận được rất nhiều phản ứng t&iacute;ch cực về sản phẩm cũ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>ư những lời g&oacute;p &yacute; để ho&agrave;n thiện sản phẩm VBI Care, nhằm đ&aacute;p ứng được nhu cầu chăm s&oacute;c sức khỏe ng&agrave;y c&agrave;ng&nbsp; cao của c&aacute;n bộ Trụ sở ch&iacute;nh n&oacute;i ri&ecirc;ng v&agrave; to&agrave;n hệ thống n&oacute;i chung.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Ph&ograve;ng PTKD</em></span></p>', '2018-03-21 03:59:01', '2018-03-21 14:59:01', 'images/news/1520391016_pic-New-i_635386821567830000_HasThumb_Thumb.jpg.png'),
(54, 2, 'Các nhà tái cố định của VBI cải thiện bảng xếp hạng tín nhiệm', 'cac-nha-tai-co-dinh-cua-vbi-cai-thien-bang-xep-hang-tin-nhiem', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Th&aacute;ng 11 vừa qua, Tổng c&ocirc;ng ty cổ phẩn T&aacute;i bảo hiểm quốc gia Việt Nam (VINARE) đ&atilde; tổ chức Lễ C&ocirc;ng bố kết quả xếp hạng t&iacute;n nhiệm lần đầu do Tổ chức xếp hạng quốc tế A.M. Best tiến h&agrave;nh. </span></span></p>', '<p><em>Th&aacute;ng 11 vừa qua, Tổng c&ocirc;ng ty cổ phẩn T&aacute;i bảo hiểm quốc gia Việt Nam (VINARE) đ&atilde; tổ chức Lễ C&ocirc;ng bố kết quả xếp hạng t&iacute;n nhiệm lần đầu do Tổ chức xếp hạng quốc tế A.M. Best tiến h&agrave;nh.</em></p>\r\n<p><img src=\"../../../uploads/635386822350780000.jpg\" alt=\"\" width=\"497\" height=\"370\" /></p>\r\n<p>VINARE được xếp hạng năng lực t&agrave;i ch&iacute;nh mức B++ (Tốt) v&agrave; năng lực t&iacute;n dụng của tổ chức ph&aacute;t h&agrave;nh mức &ldquo;bbb&rdquo; (<span id=\"kf796w_6\" class=\"kf796w\">Tri</span>ển vọng ổn định). Theo đ&aacute;nh gi&aacute; của A.M. Best, mức xếp hạng n&agrave;y thể hiện VINARE l&agrave; nh&agrave; t&aacute;i bảo hiểm &ldquo;c&oacute; năng lực t&agrave;i ch&iacute;nh (vốn) vững chắc, kết quả kinh doanh ổn định, thanh khoản tốt v&agrave; c&oacute; vị thế mạnh tr&ecirc;n thị trường t&aacute;i bảo hiểm phi nh&acirc;n thọ Việt Nam.&rdquo;</p>\r\n<p>Vinare l&agrave; nh&agrave; t&aacute;i bảo hiểm lớn thứ hai trong c&aacute;c hợp đồng t&aacute;i bảo hiểm cố định của Bảo hiểm VietinBank- VBI. Việc được xếp hạng năng lực t&agrave;i ch&iacute;nh mức B++ (Tốt) v&agrave; năng lực t&iacute;n dụng của tổ chức ph&aacute;t h&agrave;nh mức &ldquo;bbb&rdquo; của Vinare đ&atilde; l&agrave;m tăng th&ecirc;m sự vững chắc về năng lực bảo hiểm v&agrave; t&agrave;i ch&iacute;nh của VBI. Như vậy t&iacute;nh đến th&aacute;ng 11/2013, tất cả c&aacute;c nh&agrave; t&aacute;i bảo hiểm cố định của VBI đ&atilde; đều được xếp hạng t&iacute;n nhiệm với năng lực t&agrave;i ch&iacute;nh tốt (<span id=\"kf796w_2\" class=\"kf796w\">Swiss</span> Re: A+; Vinare: B++; PVI Re: B+).</p>\r\n<p style=\"text-align: right;\"><em><span id=\"kf796w_8\" class=\"kf796w\">Tin</span> b&agrave;i: Quỳnh Chi</em></p>', '2018-03-21 03:59:35', '2018-03-21 14:59:35', 'images/news/1520391075_pic-New-i_635386822352680000_HasThumb_Thumb.jpg.png'),
(55, 2, 'VBI tham dự Giải Quần vợt thường niên các Doanh nghiệp bảo hiểm năm 2013', 'vbi-tham-du-giai-quan-vot-thuong-nien-cac-doanh-nghiep-bao-hiem-nam-2013', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 23 th&aacute;ng 11 năm 2013, tại cụm s&acirc;n quần vợt KDL Văn Th&aacute;nh, TP. HCM đ&atilde; diễn ra giải quần vợt thường ni&ecirc;n c&aacute;c Doanh nghiệp bảo hiểm ch&agrave;o mừng ng&agrave;y truyền thống ng&agrave;nh Bảo hiểm Việt Nam do Hiệp hội bảo hiểm v&agrave; Bảo hiểm Bảo Long đăng cai tổ chức. </span></span></p>', '<p>Ng&agrave;y 23 th&aacute;ng 11 nă<span id=\"kf796w_6\" class=\"kf796w\">m</span> 2013, tại cụm s&acirc;n quần vợt KDL Văn Th&aacute;nh, TP. HCM đ&atilde; diễn ra giải quần vợt thường ni&ecirc;n c&aacute;c Doanh nghiệp bảo hiểm ch&agrave;o mừng ng&agrave;y truyền thống ng&agrave;nh Bảo hiểm Việt Nam do Hiệp hội bảo hiểm v&agrave; Bảo hiểm Bảo Long đăng cai tổ chức. Tham dự giải lần n&agrave;y, đại diện cho Bảo hiểm VietinBank-VBI l&agrave; c&aacute;c VĐV được tuyển chọn từ VBI Chi nh&aacute;nh HCM v&agrave; VBI Chi nh&aacute;nh S&agrave;i G&ograve;n</p>\r\n<p><img src=\"../../../uploads/img_3390-1.jpg\" alt=\"\" width=\"500\" height=\"375\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: &Ocirc;ng Ph&ugrave;ng Đắc Lộc &ndash; Tổng Thư k&yacute; HHBH Việt Nam ph&aacute;t biểu tại buổi lễ khai mạc</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Lần đầu ti&ecirc;n tham dự giải, c&aacute;c VĐV của VBI đ&atilde; thi đấu tự <span id=\"kf796w_7\" class=\"kf796w\">tin</span> với tinh thần <span id=\"kf796w_5\" class=\"kf796w\">fairplay</span> v&agrave; đạt được th&agrave;nh t&iacute;ch lọt đến v&ograve;ng tứ kết sau khi to&agrave;n thắng 3 trận v&ograve;ng bảng v&agrave; trận đấu loại trực tiếp của v&ograve;ng 1/16. Tại trận tứ kết, cặp VĐV của VBI đ&atilde; khởi đầu rất tốt, dẫn trước cặp VĐV của Bảo hiểm Qu&acirc;n đội với tỷ số 4-1. Tuy nhi&ecirc;n, do phải thi đấu li&ecirc;n tục nhiều trận trước đ&oacute; đ&atilde; l&agrave;m c&aacute;c VĐV của VBI mất sức n&ecirc;n đội bạn khai th&aacute;c điểm yếu về thể lực v&agrave; thắng ngược với tỷ số 6-4 trong sự tiếc nuối của c&aacute;c cổ động vi&ecirc;n. Cặp VĐV của MIC sau đ&oacute; đ&atilde; trở th&agrave;nh đội v&ocirc; địch của giải năm nay.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tuy phải dừng ch&acirc;n tại tứ kết nhưng đ&acirc;y cũng l&agrave; một th&agrave;nh t&iacute;ch rất đ&aacute;ng kh&iacute;ch lệ của cặp VĐV VBI khi lần đầu tham dự giải, đồng thời để lại nhiều ấn tượng tốt đẹp với BTC v&agrave; đội bạn. </span></p>\r\n<p><span style=\"font-family: Arial;\">Một số h&igrave;nh ảnh về giải đấu:</span></p>\r\n<p><img src=\"../../../uploads/0a.jpg\" alt=\"\" width=\"1366\" height=\"515\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: C&aacute;c đội tham dự lễ khai mạc</em></span></p>\r\n<p><img src=\"../../../uploads/0b.jpg\" alt=\"\" width=\"986\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: VĐV VBI nhận cờ lưu niệm của BTC</em></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Nguyễn Đức Dũng</em></strong></span></p>', '2018-03-21 04:00:48', '2018-03-21 15:00:48', 'images/news/1520391305_pic-New-i_635386851024900000_HasThumb_Thumb.jpg.png'),
(56, 2, '“Người thuyết trình hay nhất” Bảo hiểm sức khỏe VBI Care', 'nguoi-thuyet-trinh-hay-nhat-bao-hiem-suc-khoe-vbi-care', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Thời gian qua, cuộc thi t&igrave;m kiếm người thuyết tr&igrave;nh hay nhất về c&aacute;c sản phẩm v&agrave; chương tr&igrave;nh bảo hiểm của VBI đ&atilde; nhận được sự hưởng ứng nhiệt t&igrave;nh v&agrave; những phản hồi t&iacute;ch cực từ hầu hết c&aacute;n bộ kinh doanh của VBI Trụ sở ch&iacute;nh v&agrave; Chi nh&aacute;nh VBI H&agrave; Nội. </span></span></p>', '<p><span style=\"font-family: Arial;\">Thời gian qua, cuộc thi t&igrave;m kiếm người thuyết tr&igrave;nh hay nhất về c&aacute;c sản phẩm v&agrave; chương tr&igrave;nh bảo hiểm của VBI đ&atilde; nhận được sự hưởng ứng nhiệt t&igrave;nh v&agrave; những phản hồi t&iacute;ch cực từ hầu hết c&aacute;n bộ kinh doanh của VBI Trụ sở ch&iacute;nh v&agrave; Chi nh&aacute;nh VBI H&agrave; Nội. Ngay sau khi <span id=\"kf796w_6\" class=\"kf796w\">tri</span>ển khai chương tr&igrave;nh Bảo hiểm sức khỏe (BHSK) d&agrave;nh ri&ecirc;ng cho c&aacute;n bộ VietinBank v&agrave; người nh&agrave; (VBI Care), dưới sự chỉ đạo của Ban L&atilde;nh đạo C&ocirc;ng ty, cuộc thi t&igrave;m kiếm người thuyết tr&igrave;nh hay nhất về chương tr&igrave;nh đ&atilde; diễn ra th&agrave;nh c&ocirc;ng tốt đẹp.</span></p>\r\n<p><span style=\"font-family: Arial;\">Đến với cuộc thi t&igrave;m kiếm người thuyết tr&igrave;nh hay nhất về sản phẩm VBI Care, đ&atilde; c&oacute; gần 20 b&agrave;i thuyết tr&igrave;nh được c&aacute;c c&aacute;n bộ kinh doanh VBI chuẩn bị chu đ&aacute;o v&agrave; thể hiện tự tin nhằm truyền đạt những th&ocirc;ng tin hiệu quả nhất tới kh&aacute;ch h&agrave;ng. Sau khi kết th&uacute;c phần thuyết tr&igrave;nh, c&aacute;c c&aacute;n bộ tham gia sẽ nhận được từ 2-5 c&acirc;u hỏi từ <span id=\"kf796w_7\" class=\"kf796w\">Ban</span> l&atilde;nh đạo cũ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>ư c&aacute;c c&aacute;n bộ kinh doanh kh&aacute;c nhằm thử th&aacute;ch sự linh hoạt, nhạy b&eacute;n c&ugrave;ng với tr&igrave;nh độ chuy&ecirc;n m&ocirc;n của c&aacute;c c&aacute;n bộ VBI trong qu&aacute; tr&igrave;nh giao tiếp với kh&aacute;ch h&agrave;ng. Đ&acirc;y c&oacute; thể được coi l&agrave; một trong những ti&ecirc;u ch&iacute; rất quan trọng bởi lẽ tất cả những h&agrave;nh động trong qu&aacute; tr&igrave;nh phục vụ kh&aacute;ch h&agrave;ng d&ugrave; l&agrave; nhỏ nhất cũng c&oacute; thể g&oacute;p phần x&acirc;y dựng h&igrave;nh ảnh chuy&ecirc;n nghiệp v&agrave; n&acirc;ng cao uy t&iacute;n của VBI.</span></p>\r\n<p><img src=\"../../../uploads/bhsk2.jpg\" alt=\"\" width=\"500\" height=\"378\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: C&aacute;n bộ VBI đang tr&igrave;nh b&agrave;y về sản phẩm</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Cuộc thi kh&ocirc;ng chỉ l&agrave; một s&acirc;n chơi bổ &iacute;ch m&agrave; c&ograve;n diễn ra rất c&ocirc;ng bằng bởi ch&iacute;nh kh&aacute;n giả cũng như người tham gia cuộc thi l&agrave; đối tượng trực tiếp tham gia b&igrave;nh chọn cho b&agrave;i thuyết tr&igrave;nh hay nhất. Chung cuộc, c&aacute;n bộ kinh doanh Phạm Thị Hải Ho&agrave; thuộc ph&ograve;ng kinh doanh Trụ sở ch&iacute;nh VBI đ&atilde; gi&agrave;nh chiến thắng thuyết phục với b&agrave;i thuyết tr&igrave;nh c&ocirc;ng phu v&agrave; đầy t&acirc;m huyết của m&igrave;nh</span></p>\r\n<p><img src=\"../../../uploads/bhsk.jpg\" alt=\"\" width=\"500\" height=\"375\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ảnh: Ph&oacute; Tổng Gi&aacute;m Đốc VBI Đ&agrave;o Thị Chinh l&ecirc;n trao thưởng v&agrave; ch&uacute;c mừng c&aacute;n bộ Phạm Thị Hải H&ograve;a</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Nhận được phần thưởng từ ph&iacute;a Ban L&atilde;nh đạo C&ocirc;ng ty, c&aacute;n bộ Phạm Thị Hải H&ograve;a kh&ocirc;ng qu&ecirc;n gửi lời cảm ơn Ban l&atilde;nh đạo đ&atilde; tạo ra một s&acirc;n chơi th&uacute; vị, tạo điều kiện cho c&aacute;c c&aacute;n bộ kinh doanh học hỏi kinh nghiệm cũng như n&acirc;ng cao kĩ năng giới thiệu sản phẩm v&agrave; phục vụ kh&aacute;ch h&agrave;ng. Đại diện Ban L&atilde;nh đạo VBI, Ph&oacute; Tổng gi&aacute;m đốc Đ&agrave;o Thi Chinh cũng ch&uacute;c mừng c&aacute;n bộ thuyết tr&igrave;nh hay nhất v&agrave; c&oacute; những lời động vi&ecirc;n, kh&iacute;ch lệ tới tất cả c&aacute;n bộ kinh doanh nỗ lực hơn trong những hoạt động thi đ<span id=\"kf796w_8\" class=\"kf796w\">ua</span> sắp tới.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Hồng Hạnh</em></strong></span></p>', '2018-03-21 04:01:32', '2018-03-21 15:01:32', 'images/news/1520391491_pic-New-i_635386868374560000_HasThumb_Thumb.jpg.png'),
(57, 2, 'VBI được cấp giấy chứng nhận ISO 9001: 2008', 'vbi-duoc-cap-giay-chung-nhan-iso-9001-2008', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 29/11/2013, Bảo hiểm VietinBank- VBI đ&atilde; được Tổng cục ti&ecirc;u chuẩn đo lường chất lượng- Trung t&acirc;m chứng nhận ph&ugrave; hợp (Quacert) cấp giấy chứng nhận Hệ thống quản l&yacute; chất lượng ISO 9001: 2008. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/11/2013, Bảo hiể<span id=\"kf796w_8\" class=\"kf796w\">m</span> VietinBank- VBI đ&atilde; được Tổng cục ti&ecirc;u chuẩn đo lường chất lượng- Trung t&acirc;m chứng nhận ph&ugrave; hợp (Quacert) cấp giấy chứng nhận Hệ thống quản l&yacute; chất lượng <span id=\"kf796w_6\" class=\"kf796w\">ISO</span> 9001: 2008.</span></p>\r\n<p><img src=\"../../../uploads/635386824296870000.png\" alt=\"\" width=\"537\" height=\"708\" /></p>\r\n<p><span style=\"font-family: Arial;\">Phạm vi được chứng nhận l&agrave; ph&aacute;t <span id=\"kf796w_7\" class=\"kf796w\">tri</span>ển v&agrave; cung cấp Dịch vụ bảo hiểm phi nh&acirc;n thọ bao gồm: Bảo hiểm sức khỏe v&agrave; bảo hiểm tai nạn con người; Bảo hiểm t&agrave;i sản v&agrave; bảo hiểm thiệt hại; Bảo hiểm h&agrave;ng h&oacute;a vận chuyển đường bộ, đường sắt, đường biển, đường s&ocirc;ng v&agrave; đường h&agrave;ng kh&ocirc;ng;&nbsp; Bảo hiểm xe cơ giới; Bảo hiểm ch&aacute;y, nổ; Bảo hiểm th&acirc;n t&agrave;u v&agrave; tr&aacute;ch nhiệm d&acirc;n sự chủ t&agrave;u; Bảo hiểm tr&aacute;ch nhiệm chung; Bảo hiểm thiệt hại kinh doanh; Kinh doanh nhận v&agrave; nhượng t&aacute;i bảo hiểm v&agrave; đầu tư vốn.</span></p>\r\n<p><span style=\"font-family: Arial;\">ISO 9001:2008 l&agrave; một ti&ecirc;u chuẩn quy định chuẩn mực cho một hệ thống quản l&yacute; khoa học, chặt chẽ đ&atilde; được quốc tế c&ocirc;ng nhận. Việc &aacute;p dụng ISO 9001 v&agrave;o doanh nghiệp đ&atilde; tạo được c&aacute;ch l&agrave;m việc khoa học, tạo ra sự nhất qu&aacute;n trong c&ocirc;ng việc, chuẩn h&oacute;a c&aacute;c quy tr&igrave;nh hoạt động, loại bỏ được nhiều thủ tục kh&ocirc;ng cần thiết, r&uacute;t ngắn thời gian v&agrave; giảm chi ph&iacute; ph&aacute;t sinh do xảy ra những sai lỗi hoặc sai s&oacute;t trong c&ocirc;ng việc, đồng thời l&agrave;m cho năng lực tr&aacute;ch nhiệm cũng như &yacute; thức của c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n n&acirc;ng l&ecirc;n r&otilde; rệt.</span></p>\r\n<p><span style=\"font-family: Arial;\">Với mục đ&iacute;ch cải tổ bộ m&aacute;y, n&acirc;ng cao năng lực cạnh tranh, VBI đ&atilde; chọn &aacute;p dụng ISO 9001:2008 như một giải ph&aacute;p căn bản v&agrave; nền tảng để n&acirc;ng cao năng lực bộ m&aacute;y quản l&yacute; doanh nghiệp của m&igrave;nh.<br />Sau khi nhận được giấy chứng nhận ISO 9001:2008, VBI sẽ cố gắng thường xuy&ecirc;n cải tiến chất lượng c&aacute;c quy tr&igrave;nh v&agrave; sản phầm, từ đ&oacute; tạo một m&ocirc;i trường l&agrave;m việc chuy&ecirc;n nghiệp, hiệu quả, g&oacute;p phần n&acirc;ng cao uy t&iacute;n v&agrave; h&igrave;nh ảnh của C&ocirc;ng ty.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Ph&ograve;ng PTKD</em></span></p>', '2018-03-21 04:02:07', '2018-03-21 15:02:07', 'images/news/1520391648_pic-New-i_635386824310970000_HasThumb_Thumb.PNG'),
(58, 2, 'Ký hợp đồng bảo hiểm Dự án hầm Đường bộ Đèo Cả', 'ky-hop-dong-bao-hiem-du-an-ham-duong-bo-deo-ca', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 6/12, tại H&agrave; Nội đ&atilde; diễn ra lễ k&yacute; kết hợp đồng về mặt nguy&ecirc;n tắc giữa C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả với li&ecirc;n danh bảo hiểm do Bảo hiểm VietinBank- VBI đứng đầu. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 6/12, tại H&agrave; Nội đ&atilde; diễn ra lễ k&yacute; kết hợp đồng về mặt nguy&ecirc;n tắc giữa C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả với li&ecirc;n danh bảo hiểm do Bảo hiểm VietinBank- VBI đứng đầu.</span></p>\r\n<p><img src=\"../../../uploads/635386824897990000.jpg\" alt=\"\" width=\"923\" height=\"758\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>&Aacute;nh: L&atilde;nh đạo VBI v&agrave; C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả k&yacute; kết hợp đồng</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Dự lễ c&oacute; &ocirc;ng Hồ Minh Ho&agrave;ng &ndash; Tổng gi&aacute;m đốc C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc Bảo hiểm VietinBank c&ugrave;ng c&aacute;c l&atilde;nh đạo hai b&ecirc;n.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong dự &aacute;n, C&ocirc;ng ty Cổ phần Đầu tư Đ&egrave;o Cả đồng &yacute; về mặt nguy&ecirc;n tắc cho VBI l&agrave; nh&agrave; bảo hiểm ch&iacute;nh của phần BOT dự &aacute;n Đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh hầm Đường bộ qua Đ&egrave;o Cả QL1A tỉnh Ph&uacute; Y&ecirc;n v&agrave; tỉnh Kh&aacute;nh H&ograve;a. Tổng gi&aacute; trị dự &aacute;n ước t&iacute;nh tr&ecirc;n 9 ngh&igrave;n tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Dự &aacute;n hầm Đường bộ Đ&egrave;o Cả l&agrave; dự &aacute;n giao th&ocirc;ng đường bộ trọng điểm, c&oacute; &yacute; nghĩa quan trọng về ch&iacute;nh trị, kinh tế - x&atilde; hội v&agrave; quốc ph&ograve;ng - an ninh v&agrave; cũng l&agrave; một trong những ph&acirc;n đoạn then chốt của dự &aacute;n mở rộng Quốc lộ 1 đoạn H&agrave; Nội - Cần Thơ. Khi ho&agrave;n th&agrave;nh dự &aacute;n sẽ l&agrave; động lực ph&aacute;t <span id=\"kf796w_5\" class=\"kf796w\">tri</span>ển kinh tế cho tỉnh Kh&aacute;nh H&ograve;a n&oacute;i ri&ecirc;ng v&agrave; cả nước n&oacute;i chung.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>VBI</em></span></p>', '2018-03-21 04:02:32', '2018-03-21 15:02:32', 'images/news/1520391811_pic-New-i_635386824902410000_HasThumb_Thumb.jpg.png'),
(59, 2, 'Bảo hiểm VietinBank chi trả số tiền bảo hiểm 900 triệu đồng cho khách hàng tín dụng tại VietinBank', 'bao-hiem-vietinbank-chi-tra-so-tien-bao-hiem-900-trieu-dong-cho-khach-hang-tin-dung-tai-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 19/12/2013, C&ocirc;ng ty Bảo hiểm VietinBank c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank chi nh&aacute;nh Ng&ocirc; Quyền tới gặp gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Thị Thanh Tỉnh để chia sẻ, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 900 triệu đồng cho gia đ&igrave;nh. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 19/12/2013, C&ocirc;ng ty Bảo hiểm VietinBank c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank chi nh&aacute;nh Ng&ocirc; Quyền tới gặp gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Thị Thanh Tỉnh để chia sẻ, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 900 triệu đồng cho gia đ&igrave;nh.</span></p>\r\n<p><img src=\"../../../uploads/635386825608720000.jpg\" alt=\"\" width=\"1280\" height=\"978\" /></p>\r\n<p><span style=\"font-family: Arial;\">Kh&aacute;ch h&agrave;ng Nguyễn Thị Thanh Tỉnh đầu th&aacute;ng 10/2013 c&oacute; vay 900 triệu tại VietinBank CN Ng&ocirc; Quyền để sửa nh&agrave; v&agrave; được c&aacute;n bộ t&iacute;n dụng tư vấn tham gia sản phẩm bảo hiểm VietinCare để đảm bảo an to&agrave;n cho người đi vay. V&agrave;o ng&agrave;y 30 th&aacute;ng 10 năm 2013, trong qu&aacute; tr&igrave;nh đi tập thể dục kh&aacute;ch h&agrave;ng kh&ocirc;ng may ng&atilde; xuống hồ dẫn đến tử vong.<br /> Sau khi nhận được th&ocirc;ng <span id=\"kf796w_8\" class=\"kf796w\">tin</span> từ gia đ&igrave;nh cũ<span id=\"kf796w_1\" class=\"kf796w\">ng nh</span>ư ng&acirc;n h&agrave;ng, Bảo hiểm VietinBank chi nh&aacute;nh Hải Ph&ograve;ng đ&atilde; phối hợp với Ng&acirc;n h&agrave;ng VietinBank chi nh&aacute;nh Ng&ocirc; Quyền c&ugrave;ng với gia đ&igrave;nh thu thập hồ sơ để l&agrave;m thủ tục bồi thường cho kh&aacute;ch h&agrave;ng. Tới ng&agrave;y 19/12/2013, gia đ&igrave;nh kh&aacute;ch h&agrave;ng Nguyễn Thị Thanh Tỉnh đ&atilde; nhận được 900 triệu tiền bồi thường từ C&ocirc;ng ty bảo hiểm VietinBank.</span></p>\r\n<p><img src=\"../../../uploads/635386825616410000.jpg\" alt=\"\" width=\"530\" height=\"476\" /></p>\r\n<p><span style=\"font-family: Arial;\">Về ph&iacute;a gia đ&igrave;nh kh&aacute;ch h&agrave;ng, con g&aacute;i của chị Tỉnh c&oacute; chia sẻ: &ldquo;Mẹ t&ocirc;i c&oacute; tham gia g&oacute;i bảo hiểm của Bảo hiểm VietinBank nhưng kh&ocirc;ng may mẹ t&ocirc;i đ&atilde; qua đời đột ngột. Gia đ&igrave;nh ch&uacute;ng t&ocirc;i đ&atilde; nhận được số tiền bồi thường 900 triệu đồng từ C&ocirc;ng ty Bảo hiểm VietinBank. Số tiền n&agrave;y đ&atilde; gi&uacute;p ch&uacute;ng t&ocirc;i trả hết nợ cho Ng&acirc;n h&agrave;ng. Ch&uacute;ng t&ocirc;i nhận thấy g&oacute;i bảo hiểm đ&atilde; đem lại rất nhiều lợi &iacute;ch cho kh&aacute;ch h&agrave;ng. T&ocirc;i xin đại diện gia đ&igrave;nh cảm ơn Bảo hiểm VietinBank.&rdquo;</span></p>\r\n<p><span style=\"font-family: Arial;\"><br /> Về ph&iacute;a Ng&acirc;n h&agrave;ng VietinBank, &ocirc;ng Nguyễn L&ecirc; Sơn &ndash; Ph&oacute; Gi&aacute;m đốc chi nh&aacute;nh Ng&ocirc; Quyền cho biết: &ldquo;Bảo hiểm con người VietinCare l&agrave; một sản phẩm rất ưu việt. Sản phẩm đ&atilde; gi&uacute;p giảm thiểu tổn thất cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng, gi&uacute;p gia đ&igrave;nh trang trải nợ vay. Về ph&iacute;a ng&acirc;n h&agrave;ng, ch&uacute;ng t&ocirc;i cũng thu được nợ v&agrave; l&atilde;i tiền vay. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i kh&ocirc;ng phải xử l&yacute; nợ xấu hay l&agrave;m c&aacute;c biện ph&aacute;p để thu hồi nợ. Sản phẩm bảo hiểm con người VietinCare đ&atilde; mang lại lợi &iacute;ch cho rất nhiều b&ecirc;n. T&ocirc;i mong muốn tiếp tục được phối hợp với Bảo hiểm VietinBank trong việc kết hợp hoạt động t&iacute;n dụng ng&acirc;n h&agrave;ng với hoạt động bảo hiểm trong thời gian tới.&rdquo;</span></p>\r\n<p><span style=\"font-family: Arial;\">C&oacute; thể n&oacute;i sản phẩm bảo hiểm VietinCare rất c&oacute; &yacute; nghĩa với người vay vốn tại ng&acirc;n h&agrave;ng VietinBank. Sản phẩm đảm bảo ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải lo trả nợ khi kh&aacute;ch h&agrave;ng gặp rủi ro. Qua vụ việc n&agrave;y, Bảo hiểm VietinBank đ&atilde; chứng tỏ được sự ưu việt của sản phẩm cũng như sự tận t&acirc;m của c&aacute;c c&aacute;n bộ d&agrave;nh cho kh&aacute;ch h&agrave;ng.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>VBI</em></span></p>', '2018-03-21 04:03:24', '2018-03-21 15:03:24', 'images/news/1520392420_New-Imag_635429167274613400_HasThumb_Thumb.JPG.png'),
(60, 2, 'Lễ kỷ niệm 5 năm thành lập Công ty Bảo hiểm VietinBank', 'le-ky-niem-5-nam-thanh-lap-cong-ty-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 9/1/2013, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) tổ chức Lễ kỷ niệm 5 năm th&agrave;nh lập trong kh&ocirc;ng kh&iacute; trang trọng tại kh&aacute;ch sạn Melia H&agrave; Nội </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 9/1/2013, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) tổ chức Lễ kỷ niệm 5 năm th&agrave;nh lập trong kh&ocirc;ng kh&iacute; trang trọng tại kh&aacute;ch sạn Melia H&agrave; Nội. Buổi lễ c&oacute; sự hiện diện của &ocirc;ng Ph&ugrave;ng Ngọc Kh&aacute;nh &ndash; Ph&oacute; Cục trưởng Cục quản l&yacute; v&agrave; gi&aacute;m s&aacute;t bảo hiểm, Bộ T&agrave;i ch&iacute;nh; &ocirc;ng Trịnh Quang Tuyến &ndash; Chủ tịch Hiệp hội Bảo hiểm; &ocirc;ng Phạm Huy H&ugrave;ng &ndash; B&iacute; thư Đảng uỷ, Chủ tịch HĐQT VietinBank; b&agrave; Nguyễn Hồng V&acirc;n &ndash; Uỷ vi&ecirc;n HĐQT VietinBank ki&ecirc;m Chủ tịch C&ocirc;ng ty Bảo hiểm VietinBank. B&ecirc;n cạnh đ&oacute; l&agrave; c&aacute;c kh&aacute;ch h&agrave;ng, đối t&aacute;c th&acirc;n thiết v&agrave; c&aacute;c cơ quan truyền th&ocirc;ng b&aacute;o ch&iacute;.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại lễ kỷ niệm, &ocirc;ng L&ecirc; Tuấn Dũng, Tổng gi&aacute;m đốc VBI, nh&igrave;n nhận, năm 2013 thị trường bảo hiểm Việt Nam trải qua nhiều kh&oacute; khăn, kh&ocirc;ng chỉ ở bối cảnh chung của nền kinh tế m&agrave; c&ograve;n từ hậu quả của thi&ecirc;n tai xảy ra tại nhiều địa b&agrave;n tr&ecirc;n cả nước.</span></p>\r\n<p><span style=\"font-family: Arial;\">Số liệu ước t&iacute;nh mới đ&acirc;y của Tổng cục Thống k&ecirc; cũng cho thấy, thị trường bảo hiểm Việt Nam kh&ocirc;ng thể giữ được tốc độ tăng trưởng ở mức hai con số trong năm qua. Ước t&iacute;nh tổng doanh thu ph&iacute; bảo hiểm to&agrave;n thị trường năm 2013 đạt 44,4 ngh&igrave;n tỷ đồng, chỉ tăng khoảng 7,6% so với năm 2012.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong bối cảnh đ&oacute;, VBI tiếp tục đạt kết quả khả quan, với tổng t&agrave;i sản đến cuối năm 2013 ước t&iacute;nh 814 tỷ đồng, tổng doanh thu 180 tỷ đồng, lợi nhuận trước thuế 48 tỷ đồng, tổng dự ph&ograve;ng nghiệp vụ 81,77 tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Từ năm 2008 đến nay, VBI bồi thường tr&ecirc;n 276 tỷ đồng, gi&uacute;p kh&aacute;ch h&agrave;ng kh&ocirc;i phục lại t&agrave;i sản đ&atilde; bị tổn thất. Đứng đằng sau đảm bảo cho VBI về năng lực bảo hiểm v&agrave; t&agrave;i ch&iacute;nh l&agrave; c&aacute;c nh&agrave; t&aacute;i bảo hiểm h&agrave;ng đầu thế giới như Swiss Re, Novae Re, Lloyd&rsquo;s Syndicate,...</span></p>\r\n<table style=\"width: 609px;\">\r\n<tbody>\r\n<tr>\r\n<td style=\"width: 333px;\">\r\n<p><img src=\"../../../uploads/635380799719130000.jpg\" alt=\"\" width=\"300\" height=\"180\" /></p>\r\n<p><em>Đại diện Bộ T&agrave;i ch&iacute;nh, Ph&oacute; Cục trưởng Cục quản l&yacute; v&agrave; gi&aacute;m s&aacute;t bảo hiểm &ndash; &ocirc;ng Ph&ugrave;ng Ngọc Kh&aacute;nh đ&atilde; biểu dương những th&agrave;nh t&iacute;ch m&agrave; VBI đạt được 5 năm qua v&agrave; ch&uacute;c C&ocirc;ng ty ho&agrave;n th&agrave;nh kế hoạch đề ra trong thời gian tới</em></p>\r\n</td>\r\n<td style=\"width: 274px;\">\r\n<p><img src=\"../../../uploads/635380799725000000.jpg\" alt=\"\" width=\"315\" height=\"223\" /></p>\r\n<p><em>Chủ tịch HĐQT VietinBank &ndash; &ocirc;ng Phạm Huy H&ugrave;ng mong muốn VBI sẽ vượt qua mọi kh&oacute; khăn, th&aacute;ch thức để hiện đại ho&aacute;, ứng dụng c&aacute;c giải ph&aacute;p c&ocirc;ng nghệ hiện đại, đưa tầm v&oacute;c VBI ngang với c&aacute;c C&ocirc;ng ty bảo hiểm quốc tế lớn</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span style=\"font-family: Arial;\">5 năm qua, VBI đ&atilde; c&oacute; những bước ph&aacute;t triển mạnh mẽ, tăng trưởng li&ecirc;n tục về quy m&ocirc; hoạt động, số lượng kh&aacute;ch h&agrave;ng; c&aacute;c hệ số t&agrave;i ch&iacute;nh lu&ocirc;n ở mức độ an to&agrave;n cao. Từ chỗ chỉ c&oacute; 1 Trụ sở ch&iacute;nh v&agrave; 2 Chi nh&aacute;nh (CN), đến nay, VBI đ&atilde; c&oacute; hệ thống mạng lưới gồm 1 Trụ sở ch&iacute;nh tại H&agrave; Nội, 7 CN tại Tp.HCM, Đ&agrave; Nẵng, H&agrave; Nội, Hải Ph&ograve;ng, Thanh H&oacute;a, T&acirc;y Nguy&ecirc;n v&agrave; 21 ph&ograve;ng kinh doanh khu vực tại c&aacute;c tỉnh, th&agrave;nh tr&ecirc;n cả nước. Đặc biệt, VBI c&ograve;n c&oacute; hệ thống khai th&aacute;c bảo hiểm rộng lớn tại hơn 150 CN VietinBank tr&ecirc;n to&agrave;n quốc; cung cấp cho kh&aacute;ch h&agrave;ng dịch vụ t&agrave;i ch&iacute;nh trọn g&oacute;i một cửa, ng&acirc;n h&agrave;ng - bảo hiểm.</span></p>\r\n<p><span style=\"font-family: Arial;\">C&ocirc;ng ty đ&atilde; cung cấp ra thị trường gần 100 sản phẩm bảo hiểm đa dạng. X&aacute;c định cung cấp c&aacute;c sản phẩm qua VietinBank l&agrave; mũi nhọn n&ecirc;n VBI đ&atilde; thiết kế t&iacute;ch hợp c&aacute;c sản phẩm bảo hiểm, triển khai b&aacute;n ch&eacute;o qua hệ thống VietinBank. C&aacute;c k&ecirc;nh ph&acirc;n phối cũng được VBI đa dạng ho&aacute;, ngo&agrave;i c&aacute;c k&ecirc;nh truyền thống qua đại l&yacute;, m&ocirc;i giới, VBI c&ograve;n triển khai b&aacute;n h&agrave;ng trực tuyến qua iPay, qua Contact Center,&hellip;</span></p>\r\n<p><span style=\"font-family: Arial;\">Doanh thu trong 5 năm qua của VBI b&igrave;nh qu&acirc;n tăng trưởng gần 20%/năm; quỹ dự ph&ograve;ng nghiệp vụ tăng trưởng đều theo doanh thu v&agrave; sử dụng hiệu quả cho c&ocirc;ng t&aacute;c đầu tư; hoạt động đầu tư an to&agrave;n; c&ocirc;ng t&aacute;c quản trị doanh nghiệp được n&acirc;ng cao qua việc &aacute;p dụng hệ thống c&ocirc;ng nghệ th&ocirc;ng tin hiện đại; ch&uacute; trọng v&agrave; đẩy mạnh c&ocirc;ng t&aacute;c kiểm tra kiểm so&aacute;t nội bộ. Th&aacute;ng 4/2013, C&ocirc;ng ty đ&atilde; được FAST500 vinh danh trong Top 500 doanh nghiệp tăng trưởng nhanh nhất Việt Nam.</span></p>\r\n<p><span style=\"font-family: Arial;\">Kh&ocirc;ng chỉ kinh doanh an to&agrave;n, hiệu quả, C&ocirc;ng ty cũng lu&ocirc;n l&agrave; đơn vị thực hiện đầy đủ v&agrave; kịp thời nghĩa vụ nộp ng&acirc;n s&aacute;ch. Lần lượt trong c&aacute;c năm 2011 v&agrave; 2012, VBI được Vietnam Report xếp thứ 841 v&agrave; 699 trong TOP 1000 đơn vị nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam.</span></p>\r\n<p><span style=\"font-family: Arial;\">Với c&aacute;ch thức quản l&yacute; hiện đại, quy tr&igrave;nh l&agrave;m việc chuy&ecirc;n nghiệp, đảm bảo an to&agrave;n, VBI l&agrave; c&ocirc;ng ty con đầu ti&ecirc;n của VietinBank được Tổng cục ti&ecirc;u chuẩn đo lường chất lượng &ndash; Trung t&acirc;m chứng nhận ph&ugrave; hợp Quacert cấp Giấy chứng nhận Hệ thống quản l&yacute; chất lượng theo ti&ecirc;u chuẩn ISO 9001:2008. C&ocirc;ng ty cũng vinh dự được nhận giấy khen do Chủ tịch HĐQT VietinBank trao tặng về những th&agrave;nh t&iacute;ch trong 5 năm x&acirc;y dựng v&agrave; ph&aacute;t triển.</span></p>\r\n<p><img src=\"../../../uploads/635380799726060000.jpg\" alt=\"\" width=\"346\" height=\"270\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Đại diện Tổng cục ti&ecirc;u chuẩn đo lường chất lượng &ndash; Trung t&acirc;m chứng nhận ph&ugrave; hợp Quacert cấp Giấy chứng nhận Hệ thống quản l&yacute; chất lượng theo ti&ecirc;u chuẩn ISO 9001:2008 cho Ban điều h&agrave;nh VBI</em></span></p>\r\n<p><img src=\"../../../uploads/635380799727460000.jpg\" alt=\"\" width=\"504\" height=\"272\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Ban điều h&agrave;nh VBI nhận bằng khen từ Chủ tịch HĐQT VietinBank Phạm Huy H&ugrave;ng</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Trong 5 năm tới (2014-2018) C&ocirc;ng ty tiếp tục đẩy mạnh hoạt động kinh doanh nhằm tăng doanh thu, lợi nhuận v&agrave; thị phần; chuy&ecirc;n nghiệp ho&aacute; c&aacute;c hoạt động nghiệp vụ, b&aacute;n h&agrave;ng, chăm s&oacute;c kh&aacute;ch h&agrave;ng; n&acirc;ng cao năng lực t&agrave;i ch&iacute;nh, hiệu quả quản l&yacute;. C&aacute;c chỉ ti&ecirc;u ch&iacute;nh tăng b&igrave;nh qu&acirc;n: Doanh thu 30%, quỹ dự ph&ograve;ng nghiệp vụ 30%; lợi nhuận 20%; vốn chủ sở hữu 10%; ROE 10-12%...</span></p>\r\n<p><span style=\"font-family: Arial;\">Chủ tịch C&ocirc;ng ty Bảo hiểm VietinBank, b&agrave; Nguyễn Hồng V&acirc;n cam kết to&agrave;n bộ Ban điều h&agrave;nh v&agrave; c&aacute;c c&aacute;n bộ nh&acirc;n vi&ecirc;n sẽ c&ugrave;ng nhau x&acirc;y dựng một VBI lớn mạnh kh&ocirc;ng ngừng, khẳng định uy t&iacute;n của một C&ocirc;ng ty bảo hiểm trực thuộc Tập đo&agrave;n t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng số 1 Việt Nam - VietinBank.</span></p>', '2018-03-21 04:04:32', '2018-03-21 15:04:32', 'images/news/1520392683_pic-New-i_635380799735060000_HasThumb_Thumb.jpg.png'),
(61, 2, 'VBI tổ chức Hội nghị triển khai nhiệm vụ kinh doanh năm 2014', 'vbi-to-chuc-hoi-nghi-trien-khai-nhiem-vu-kinh-doanh-nam-2014', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 9/1/2014, C&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam (VBI) đ&atilde; tổ chức Hội nghị triển khai nhiệm vụ kinh doanh năm 2014 </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 9/1/2014, C&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam (VBI) đ&atilde; tổ chức Hội nghị triển khai nhiệm vụ kinh doanh năm 2014.<br />Đến dự đại hội c&oacute; sự tham dự của b&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty bảo hiểm VBI, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc C&ocirc;ng ty bảo hiểm VBI, b&agrave; Trần Thị Kim Yến &ndash; Ph&oacute; Tổng Gi&aacute;m Đốc VBI, &ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m Đốc VBI, tập thể c&aacute;n bộ c&oacute; th&agrave;nh t&iacute;ch xuất sắc của chi nh&aacute;nh H&agrave; Nội, Hải Ph&ograve;ng, Đ&agrave; Nẵng, Thanh H&oacute;a, S&agrave;i G&ograve;n, Hồ Ch&iacute; Minh c&ugrave;ng to&agrave;n thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n VBI Trụ sở ch&iacute;nh.<br />Tại hội nghị, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc VBI đ&atilde; b&aacute;o c&aacute;o về những th&agrave;nh tựu đạt được cũng như những mặt hạn chế của c&ocirc;ng ty trong năm 2013 để từ đ&oacute; c&oacute; những giải ph&aacute;p kịp thời nhằm n&acirc;ng cao chất lượng, dịch vụ cũng như uy t&iacute;n của c&ocirc;ng ty tr&ecirc;n thị trường bảo hiểm trong năm 2014. Trong năm qua, C&ocirc;ng ty đ&atilde; tập trung ho&agrave;n thiện v&agrave; ph&aacute;t triển danh mục sản phẩm bảo hiểm, n&acirc;ng cao chất lượng dịch vụ kh&aacute;ch h&agrave;ng, c&ocirc;ng t&aacute;c quản l&yacute; t&agrave;i ch&iacute;nh &ndash; kế to&aacute;n, chất lượng nguồn nh&acirc;n lực v&agrave; hệ thống c&ocirc;ng nghệ th&ocirc;ng tin,&hellip;<br />Kết th&uacute;c năm 2013, với những kết quả cụ thể đ&atilde; đạt được l&agrave;: Tổng doanh thu ph&iacute; bảo hiểm 180 tỷ đồng, lợi nhuận trước thuế 48 tỷ đồng, quỹ dự ph&ograve;ng nghiệp vụ 81,700 tỷ đồng, c&ocirc;ng ty đ&atilde; ho&agrave;n th&agrave;nh tốt nhiệm vụ được HĐQT VietinBank đặt ra.</span></p>\r\n<p><img src=\"../../../uploads/635380800204170000.jpg\" alt=\"\" width=\"623\" height=\"408\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\">&Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc VBI đang b&aacute;o c&aacute;o cụ thể về th&agrave;nh tựu VBI đạt được trong năm 2013</span></p>\r\n<p><span style=\"font-family: Arial;\">B&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty bảo hiểm VBI đồng thời l&agrave; ủy vi&ecirc;n HĐQT VietinBank đưa ra nhiều lời động vi&ecirc;n, kh&iacute;ch lệ, tuy&ecirc;n dương VBI về những th&agrave;nh tựu đ&atilde; đạt được trong năm 2013 nhưng cũng đặt ra mục ti&ecirc;u cao hơn trong năm 2014 cho VBI, để từ đ&oacute; VBI c&oacute; thể ph&aacute;t huy hơn nữa những th&agrave;nh tựu đ&atilde; đạt được.</span></p>\r\n<p><img src=\"../../../uploads/635380800206510000.jpg\" alt=\"\" width=\"604\" height=\"361\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\">B&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch VBI ph&aacute;t biểu tại hội nghị</span></p>\r\n<p><span style=\"font-family: Arial;\">Bước sang năm 2014, tr&ecirc;n cơ sở ph&acirc;n t&iacute;ch t&igrave;nh h&igrave;nh thị trường v&agrave; nguồn tiềm năng c&ograve;n rất lớn của hệ thống VietinBank, C&ocirc;ng ty hướng đến mục ti&ecirc;u tăng trưởng mạnh mẽ, c&ugrave;ng với đ&oacute; l&agrave; c&aacute;c nh&oacute;m giải ph&aacute;p về đ&agrave;o tạo nh&acirc;n lực, mở rộng mạng lưới, phương ph&aacute;p b&aacute;n h&agrave;ng, cơ chế kế hoạch, kiểm tra kiểm so&aacute;t v&agrave; ứng dụng C&ocirc;ng nghệ th&ocirc;ng tin.<br />VBI lu&ocirc;n tin tưởng rằng, với sự dẫn dắt của Ban điều h&agrave;nh, sự quyết t&acirc;m, đo&agrave;n kết của to&agrave;n bộ c&aacute;n bộ, nh&acirc;n vi&ecirc;n c&ocirc;ng ty, VBI sẽ ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ của HĐQT VietinBank đề ra trong năm 2014.</span></p>', '2018-03-21 04:05:23', '2018-03-21 15:05:23', 'images/news/1520392825_pic-New-i_635380800209260000_HasThumb_Thumb.jpg.png'),
(62, 2, 'VBI tổ chức Đại hội đại biểu Công nhân viên chức năm 2014', 'vbi-to-chuc-dai-hoi-dai-bieu-cong-nhan-vien-chuc-nam-2014', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Chiều ng&agrave;y 09/01/2013, Đại hội đại biểu C&ocirc;ng nh&acirc;n vi&ecirc;n chức C&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương (VBI) năm 2014 đ&atilde; diễn ra trong kh&ocirc;ng kh&iacute; vui tươi, phấn khởi của to&agrave;n thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n của VBI </span></span></p>', '<p><span style=\"font-family: Arial;\">Chiều ng&agrave;y 09/01/2013, Đại hội đại biểu C&ocirc;ng nh&acirc;n vi&ecirc;n chức C&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương (VBI) năm 2014 đ&atilde; diễn ra trong kh&ocirc;ng kh&iacute; vui tươi, phấn khởi của to&agrave;n thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n của VBI.</span></p>\r\n<p><img src=\"../../../uploads/635380800670830000.jpg\" alt=\"\" width=\"613\" height=\"407\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>B&agrave; Nguyễn Thị Thanh Xu&acirc;n &ndash; Ph&oacute; chủ tịch Thường trực c&ocirc;ng đo&agrave;n VietinBank chụp ảnh lưu niệm c&ugrave;ng Ban L&atilde;nh Đạo C&ocirc;ng ty</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu tại Hội nghị, Ph&oacute; Chủ tịch Thường trực C&ocirc;ng đoàn VietinBank b&agrave; Nguyễn Thị Thanh Xu&acirc;n đ&atilde; ghi nh&acirc;̣n những thành tích C&ocirc;ng ty đã đạt được trong năm 2013, đ&ecirc;̀ nghị Ban chấp h&agrave;nh c&ocirc;ng đo&agrave;n và t&acirc;̣p th&ecirc;̉ CBNV C&ocirc;ng ty tri&ecirc;̉n khai thực hi&ecirc;̣n nhi&ecirc;̣m vụ kinh doanh 2014 ngay từ những ngày đ&acirc;̀u, tháng đ&acirc;̀u của năm mới. Bà cũng đ&ecirc;̀ nghị C&ocirc;ng đoàn C&ocirc;ng ty phải đổi mới phương pháp đ&ecirc;̉ phát đ&ocirc;̣ng các phong trào thi đua thi&ecirc;́t thực hơn nữa, l&acirc;́y thi đua làm đ&ocirc;̣ng lực, làm t&ocirc;́t c&ocirc;ng tác quy hoạch cán b&ocirc;̣ ngu&ocirc;̀n đ&ecirc;̉ có đ&ocirc;̣i ngũ k&ecirc;́ c&acirc;̣n t&ocirc;́t, chú trọng c&ocirc;ng tác đào tạo,&hellip;Đ&ocirc;̀ng thời, phải thực hi&ecirc;̣n t&ocirc;́t quy ch&ecirc;́ d&acirc;n chủ cơ sở, tổ chức học t&acirc;̣p và nắm vững n&ocirc;̣i quy lao đ&ocirc;̣ng, s&ocirc;̉ tay văn hóa doanh nghi&ecirc;̣p, x&acirc;y dựng phong cách văn hóa ri&ecirc;ng bi&ecirc;̣t của VietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\">Cũng tại hội nghị, VBI đ&atilde; khen thưởng 16 đơn vị v&agrave; 6 c&aacute; nh&acirc;n đ&atilde; c&oacute; th&agrave;nh t&iacute;ch trong hoạt động kinh doanh năm 2013..</span></p>\r\n<p><img src=\"../../../uploads/635380800674590000.jpg\" alt=\"\" width=\"610\" height=\"396\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Đại diện c&aacute;c đơn vị l&ecirc;n nhận bằng khen của Ban L&atilde;nh Đạo C&ocirc;ng ty</em></span></p>\r\n<p><img src=\"../../../uploads/635380800677410000.jpg\" alt=\"\" width=\"612\" height=\"410\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>C&aacute;c c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch xuất sắc năm 2013</em></span></p>', '2018-03-21 04:06:32', '2018-03-21 15:06:32', 'images/news/1520395735_pic-New-i_635380800682450000_HasThumb_Thumb.jpg.png'),
(63, 2, 'Kết nối đồng nghiệp xuân 2014', 'ket-noi-dong-nghiep-xuan-2014', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 10/01/2014, trong kh&ocirc;ng kh&iacute; đ&oacute;n ch&agrave;o xu&acirc;n 2014, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Chương tr&igrave;nh Kết nối đồng nghiệp 2014 tại khu nghỉ dưỡng Đại Lải Flamingo. Chương tr&igrave;nh c&oacute; sự tham gia của Ban điều h&agrave;nh c&ugrave;ng c&aacute;c c&aacute;n bộ, nh&acirc;n vi&ecirc;n C&ocirc;ng ty Bảo hiểm VietinBank </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 10/01/2014, trong kh&ocirc;ng kh&iacute; đ&oacute;n ch&agrave;o xu&acirc;n 2014, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Chương tr&igrave;nh Kết nối đồng nghiệp 2014 tại khu nghỉ dưỡng Đại Lải Flamingo. Chương tr&igrave;nh c&oacute; sự tham gia của Ban điều h&agrave;nh c&ugrave;ng c&aacute;c c&aacute;n bộ, nh&acirc;n vi&ecirc;n C&ocirc;ng ty Bảo hiểm VietinBank.<br />Đ&acirc;y l&agrave; một hoạt động vui chơi, giải tr&iacute; nằm trong chuỗi hoạt động kỷ niệm 5 năm th&agrave;nh lập c&ocirc;ng ty. Tất cả c&aacute;c th&agrave;nh vi&ecirc;n trong c&ocirc;ng ty đ&atilde; &ldquo;ch&aacute;y&rdquo; hết m&igrave;nh trong suốt 1 ng&agrave;y vui chơi. Chương tr&igrave;nh đ&atilde; gi&uacute;p c&aacute;c th&agrave;nh vi&ecirc;n trong c&ocirc;ng ty x&iacute;ch lại gần nhau hơn, hiểu biết v&agrave; gần gũi nhau hơn.<br />Chương tr&igrave;nh kết nối đồng nghiệp 2014 đ&atilde; để lại những kỷ niệm đẹp, ấn tượng, khắc s&acirc;u trong tr&aacute;i tim của mỗi th&agrave;nh vi&ecirc;n. Chương tr&igrave;nh đ&atilde; tạo động lực cho c&aacute;c th&agrave;nh vi&ecirc;n VBI c&ugrave;ng thi đua, phấn đấu đảm bảo ho&agrave;n th&agrave;nh kế hoạch kinh doanh 2014 với những quyết t&acirc;m mạnh mẽ hơn bao giờ hết.<br />Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh của chương tr&igrave;nh Team Building:</span></p>\r\n<p><img src=\"../../../uploads/635380800886160000.jpg\" alt=\"\" width=\"574\" height=\"341\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Chương tr&igrave;nh mang tới những trải nghiệm mới th&uacute; vị với những tiết mục h&agrave;i kịch, nhảy, tr&igrave;nh diễn thời trang,..từ c&aacute;c ph&ograve;ng ban trong c&ocirc;ng ty. C&aacute;c tiết mục thể hiện sự phối hợp ăn &yacute; giữa c&aacute;c th&agrave;nh vi&ecirc;n, tinh thần đồng đội v&agrave; kỹ năng l&agrave;m việc nh&oacute;m</em></span></p>\r\n<p><img src=\"../../../uploads/635380800889870000.jpg\" alt=\"\" width=\"554\" height=\"368\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Chương tr&igrave;nh Team Building gồm c&aacute;c tr&ograve; chơi mang t&iacute;nh đồng đội như: k&eacute;o co, chung sức,&hellip; mang lại những gi&acirc;y ph&uacute;t vui chơi thoải m&aacute;i v&agrave; n&acirc;ng cao tinh thần đo&agrave;n kết.</em></span></p>\r\n<p><img src=\"../../../uploads/635380800892700000.jpg\" alt=\"\" width=\"596\" height=\"285\" /></p>\r\n<p><img src=\"../../../uploads/635380800895080000.jpg\" alt=\"\" width=\"564\" height=\"224\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Chương tr&igrave;nh l&agrave; cơ hội để c&aacute;c th&agrave;nh vi&ecirc;n VBI c&ugrave;ng lắng nghe, chia sẻ v&agrave; hiểu nhau hơn</em></span></p>', '2018-03-21 04:07:36', '2018-03-21 15:07:36', 'images/news/1520395844_pic-New-i_635380800900870000_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(64, 2, 'Bảo hiểm VietinBank bồi thường gần 9 tỷ đồng cho Công ty PNP', 'bao-hiem-vietinbank-boi-thuong-gan-9-ty-dong-cho-cong-ty-pnp', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Phattanaphan Chemitech (gọi tắt l&agrave; PNP) l&agrave; tập đo&agrave;n của Th&aacute;i Lan, chuy&ecirc;n sản xuất, cung ứng keo sữa v&agrave; chất kết d&iacute;nh, bao gồm cả hệ nước v&agrave; hệ dung m&ocirc;i. PNP c&oacute; nh&agrave; m&aacute;y tại tỉnh Long An, Việt Nam với c&ocirc;ng suất mỗi th&aacute;ng l&agrave; 2.400 tấn </span></span></p>', '<p><span style=\"font-family: Arial;\">Phattanaphan Chemitech (gọi tắt l&agrave; PNP) l&agrave; tập đo&agrave;n của Th&aacute;i Lan, chuy&ecirc;n sản xuất, cung ứng keo sữa v&agrave; chất kết d&iacute;nh, bao gồm cả hệ nước v&agrave; hệ dung m&ocirc;i. PNP c&oacute; nh&agrave; m&aacute;y tại tỉnh Long An, Việt Nam với c&ocirc;ng suất mỗi th&aacute;ng l&agrave; 2.400 tấn. Sản phẩm của PNP đ&aacute;p ứng chất lượng theo ti&ecirc;u chuẩn DIN của Ch&acirc;u &Acirc;u v&agrave; ti&ecirc;u chuẩn JAS của Nhật Bản.</span></p>\r\n<p><span style=\"font-family: Arial;\">C&ocirc;ng ty PNP đ&atilde; tham gia Bảo hiểm mọi rủi ro t&agrave;i sản của Bảo hiểm VietinBank (VBI) từ ng&agrave;y 12/04/2012. V&agrave;o ng&agrave;y 27/10/2012, một sự cố hỏa hoạn đ&atilde; xảy ra tại C&ocirc;ng ty PNP g&acirc;y ra tổn thất nghi&ecirc;m trọng cho PNP. Mặc d&ugrave; cơ quan PCCC v&agrave; người được bảo hiểm đ&atilde; nỗ lực hạn chế tổn thất v&agrave; chữa ch&aacute;y, song do t&iacute;nh chất qu&aacute; lớn của vụ ch&aacute;y, trong v&ograve;ng v&agrave;i tiếng đồng hồ, ngọn lửa đ&atilde; thi&ecirc;u rụi 08 bồn chứa nguy&ecirc;n liệu lỏng, một phần nh&agrave; xưởng, một phần hệ thống điện v&agrave; nhiều t&agrave;i sản gi&aacute; trị kh&aacute;c.</span></p>\r\n<p><img src=\"../../../uploads/635380801241260000.jpg\" alt=\"\" width=\"800\" height=\"600\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>H&igrave;nh ảnh vụ ch&aacute;y tại C&ocirc;ng ty PNP</em></span></p>\r\n<p><span style=\"font-family: Arial;\">C&aacute;n bộ C&ocirc;ng ty PNP tường thuật lại: &ldquo;Khoảng 10h30&rsquo; s&aacute;ng ng&agrave;y 27/10/2012, t&ocirc;i nghe thấy tiếng nổ nhỏ như tiếng th&ugrave;ng phuy để tr&ecirc;n cao bị rơi, sau đ&oacute; khoảng 1 ph&uacute;t th&igrave; c&oacute; tiếng nổ dữ dội rung chuyển mạnh như động đất, rồi khu vực chứa 08 bồn nguy&ecirc;n liệu ch&aacute;y rất to. To&agrave;n bộ nh&acirc;n vi&ecirc;n c&ocirc;ng ty đ&atilde; d&ugrave;ng b&igrave;nh chữa ch&aacute;y v&agrave; v&ograve;i phun nước để dập lửa đồng thời gọi điện th&ocirc;ng b&aacute;o cho C&ocirc;ng an PCCC đến hỗ trợ. Tới khoảng 13h30&rsquo; c&ugrave;ng ng&agrave;y đ&aacute;m ch&aacute;y mới được khống chế v&agrave; dập tắt ho&agrave;n to&agrave;n.&rdquo;</span></p>\r\n<p><img src=\"../../../uploads/635380801242800000.jpg\" alt=\"\" width=\"800\" height=\"600\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>H&igrave;nh ảnh vụ ch&aacute;y tại C&ocirc;ng ty PNP</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Theo th&ocirc;ng b&aacute;o kết quả điều tra số 127/TB ng&agrave;y 21/11/2012 của Cơ quan CSĐT C&ocirc;ng an tỉnh Long An, nguy&ecirc;n nh&acirc;n vụ ch&aacute;y được x&aacute;c định l&agrave; do chập điện.</span></p>\r\n<p><span style=\"font-family: Arial;\">B&ecirc;n cạnh đ&oacute;, VBI cũng chỉ định C&ocirc;ng ty gi&aacute;m định Crawford để kịp thời hỗ trợ giải quyết bồi thường cho C&ocirc;ng ty PNP theo đ&uacute;ng c&aacute;c điều khoản đ&atilde; cam kết trong hợp đồng. VBI đ&atilde; tạm ứng 3 tỷ đồng cho C&ocirc;ng ty PNP nhằm hỗ trợ kh&aacute;ch h&agrave;ng trong việc khắc phục tổn thất do hậu quả của vụ ch&aacute;y, sớm kh&ocirc;i phục v&agrave; ổn định lại sản xuất kinh doanh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ngay sau khi c&oacute; kết quả gi&aacute;m định, VBI thanh to&aacute;n khoản bồi thường tổng trị gi&aacute; 8.907.291.668 VNĐ cho C&ocirc;ng ty PNP. Qua vụ việc n&agrave;y, Bảo hiểm VietinBank ng&agrave;y c&agrave;ng khẳng định uy t&iacute;n tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</span></p>', '2018-03-21 04:08:28', '2018-03-21 15:08:28', 'images/news/1520395958_pic-New-i_635380801245360000_HasThumb_Thumb.jpg.png'),
(65, 2, 'Bảo hiểm VietinBank đồng hành cùng Chương trình giáo dục tài chánh và xã hội cho trẻ em (Aflatoun)', 'bao-hiem-vietinbank-dong-hanh-cung-chuong-trinh-giao-duc-tai-chanh-va-xa-hoi-cho-tre-em-aflatoun', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Chiều ng&agrave;y 10/01/2014, C&ocirc;ng ty Bảo hiểm VietinBank &ndash; Chi nh&aacute;nh Hồ Ch&iacute; Minh (VBI HCM) c&ugrave;ng với đại diện của Ng&acirc;n H&agrave;ng VietinBank &ndash; Chi nh&aacute;nh Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; triển khai Chương tr&igrave;nh tiết kiệm t&iacute;ch lũy cho con v&agrave; Bảo hiểm t&iacute;ch lũy cho con tại Hội nghị Chương tr&igrave;nh gi&aacute;o dục ... </span></span></p>', '<p><span style=\"font-family: Arial;\">V&agrave;o chiều ng&agrave;y 10/01/2014, C&ocirc;ng ty Bảo hiểm VietinBank &ndash; Chi nh&aacute;nh Hồ Ch&iacute; Minh (VBI HCM) c&ugrave;ng với đại diện của Ng&acirc;n H&agrave;ng VietinBank &ndash; Chi nh&aacute;nh Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; triển khai Chương tr&igrave;nh tiết kiệm t&iacute;ch lũy cho con v&agrave; Bảo hiểm t&iacute;ch lũy cho con tại Hội nghị Chương tr&igrave;nh gi&aacute;o dục t&agrave;i ch&aacute;nh v&agrave; x&atilde; hội cho trẻ em (Aflatoun) diễn ra tại trường tiểu học H&ograve;a B&igrave;nh, số 01 C&ocirc;ng x&atilde; Paris, Th&agrave;nh phố Hồ Ch&iacute; Minh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Aflatoun l&agrave; chương tr&igrave;nh gi&aacute;o dục gi&uacute;p trẻ em v&agrave; gia đ&igrave;nh cải thiện cuộc sống bằng c&aacute;ch tiết kiệm, đồng thời gi&aacute;o dục cho c&aacute;c em &yacute; thức về x&atilde; hội, sự chia sẻ với bạn b&egrave; của m&igrave;nh. Chương tr&igrave;nh Aflatoun gi&uacute;p trẻ em hiểu về bản th&acirc;n, quyền v&agrave; tr&aacute;ch nhiệm trong cộng đồng x&atilde; hội, tiết kiệm v&agrave; chi ti&ecirc;u, lập kế hoạch v&agrave; l&ecirc;n ng&acirc;n s&aacute;ch nhằm n&acirc;ng cao &yacute; thức v&agrave; c&oacute; tr&aacute;ch nhiệm với tiền tiết kiệm của m&igrave;nh, h&igrave;nh th&agrave;nh doanh nghiệp x&atilde; hội v&agrave; t&agrave;i ch&iacute;nh của trẻ em để tăng thu nhập, cải thiện t&igrave;nh trạng ngh&egrave;o kh&oacute; v&agrave; hướng nghiệp.</span></p>\r\n<p><span style=\"font-family: Arial;\">Sản phẩm t&iacute;ch lũy cho con của VBI ho&agrave;n to&agrave;n ph&ugrave; hợp với chương tr&igrave;nh n&agrave;y. C&aacute;c em học sinh d&ugrave; chưa đủ tuổi nhưng vẫn c&oacute; thể đứng t&ecirc;n tr&ecirc;n t&agrave;i khoản c&ugrave;ng với số CMND của Cha/Mẹ v&agrave; ng&agrave;y sinh của c&aacute;c em. C&aacute;c em rất h&agrave;o hứng v&igrave; cảm thấy m&igrave;nh được l&agrave;m chủ t&agrave;i khoản tiết kiệm của ch&iacute;nh m&igrave;nh, b&ecirc;n cạnh đ&oacute; bản th&acirc;n c&aacute;c em v&agrave; Cha/mẹ c&ograve;n được hưởng th&ecirc;m những quyền lợi bảo hiểm ưu việt của Bảo hiểmVietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\">Hội nghị đ&atilde; diễn ra th&agrave;nh c&ocirc;ng tốt đẹp v&agrave; đầy &yacute; nghĩa với sự g&oacute;p mặt của Sở Gi&aacute;o Dục v&agrave; Đ&agrave;o Tạo Th&agrave;nh Phố Hồ Ch&iacute; Minh, Hội bảo vệ quyền trẻ em thường trực ph&iacute;a Nam, Ban gi&aacute;m hiệu một số trường tiểu học tr&ecirc;n địa b&agrave;n TP.HCM, Qu&iacute; phụ huynh học sinh, c&aacute;c vị Mạnh thường qu&acirc;n của dự &aacute;n v&agrave; c&aacute;c em học sinh tham gia dự &aacute;n n&agrave;y.</span></p>\r\n<p><img src=\"../../../uploads/635380801586500000.jpg\" alt=\"\" width=\"348\" height=\"215\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>B&agrave; Nguyễn Thị V&acirc;n - Ph&oacute; Phụ tr&aacute;ch Ph&ograve;ng Kinh doanh 02 VBI HCM tr&igrave;nh b&agrave;y về quyền lợi của Bảo hiểm t&iacute;ch lũy cho con tại hội nghị.</em></span></p>\r\n<p><img src=\"../../../uploads/635380801587220000.jpg\" alt=\"\" width=\"409\" height=\"293\" /></p>\r\n<p style=\"text-align: center;\"><em>B&agrave; Nguyễn Thị Hồng - Ph&oacute; Ph&ograve;ng kế to&aacute;n Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Hồ Ch&iacute; Minh<br /> giới thiệu sản phẩm t&iacute;ch lũy cho con tại hội nghị.</em></p>\r\n<p align=\"right\"><strong><em>Nguyễn Thị V&acirc;n- VBI HCM</em></strong>&nbsp;</p>\r\n<p>&nbsp;</p>', '2018-03-21 04:09:14', '2018-03-21 15:09:14', 'images/news/1520396068_pic-New-i_635380801589160000_HasThumb_Thumb.jpg.png'),
(66, 2, 'Đoàn thanh niên VBI tham dự chương trình truyền hình giao lưu đối thoại đầu xuân', 'doan-thanh-nien-vbi-tham-du-chuong-trinh-truyen-hinh-giao-luu-doi-thoai-dau-xuan', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Đại diện Chi Đo&agrave;n thanh ni&ecirc;n C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; tham dự chương tr&igrave;nh truyền h&igrave;nh &ldquo;Dấu Son&rdquo; nhằm t&ocirc;n vinh đ&oacute;ng g&oacute;p của c&aacute;c tập đo&agrave;n, tổng c&ocirc;ng ty, ng&acirc;n h&agrave;ng do Đảng Ủy Khối Doanh nghiệp Trung Ương tổ chức tại trường quay S4 &ndash; Đ&agrave;i truyền h&igrave;nh VTC v&agrave;o ng&agrave;y 19/01/20... </span></span></p>', '<p><span style=\"font-family: Arial;\">Đại diện Chi Đo&agrave;n thanh ni&ecirc;n C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; tham dự chương tr&igrave;nh truyền h&igrave;nh &ldquo;Dấu Son&rdquo; nhằm t&ocirc;n vinh đ&oacute;ng g&oacute;p của c&aacute;c tập đo&agrave;n, tổng c&ocirc;ng ty, ng&acirc;n h&agrave;ng do Đảng Ủy Khối Doanh nghiệp Trung Ương tổ chức tại trường quay S4 &ndash; Đ&agrave;i truyền h&igrave;nh VTC v&agrave;o ng&agrave;y 19/01/2014.</span></p>\r\n<p><span style=\"font-family: Arial;\">Dự chương tr&igrave;nh c&oacute; &ocirc;ng B&ugrave;i Đăng Dũng - Ph&oacute; Chủ nhiệm Ủy ban T&agrave;i ch&iacute;nh Ng&acirc;n s&aacute;ch Quốc hội, &ocirc;ng B&ugrave;i Văn Thạch - Ph&oacute; Trưởng ban Kinh tế Trung Ương, &ocirc;ng Trần Đức Lay - Thứ trưởng Bộ Th&ocirc;ng tin truyền th&ocirc;ng, &ocirc;ng B&ugrave;i Văn Cường- Ủy vi&ecirc;n dự khuyết TW Đảng, B&iacute; thư đảng ủy Khối Doanh nghiệp Trung Ương,&hellip;</span></p>\r\n<p><img src=\"../../../uploads/635380801991570000.jpg\" alt=\"\" width=\"348\" height=\"250\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Chương tr&igrave;nh t&ocirc;n vinh c&aacute;c tập đo&agrave;n, tổng c&ocirc;ng ty, ng&acirc;n h&agrave;ng ti&ecirc;u biểu trong năm 2013</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Năm 2013, sự kiện x&acirc;y dựng văn h&oacute;a VietinBank ti&ecirc;n tiến, hiện đại, theo chuẩn quốc tế được b&igrave;nh chọn l&agrave; một trong 10 sự kiện nổi bật của Đảng ủy Khối Doanh nghiệp Trung Ương. Những gi&aacute; trị văn h&oacute;a của VietinBank l&agrave;: hướng đến kh&aacute;ch h&agrave;ng; hướng đến sự b&agrave;i bản; năng động s&aacute;ng tạo, chuy&ecirc;n nghiệp v&agrave; hiện đại; trung thực, ch&iacute;nh trực, minh bạch, đạo đức nghề nghiệp; sự t&ocirc;n trọng; bảo vệ v&agrave; ph&aacute;t triển thương hiệu; ph&aacute;t triển bền vững v&agrave; thực hiện tốt tr&aacute;ch nhiệm với cộng đồng, x&atilde; hội.</span></p>\r\n<p><span style=\"font-family: Arial;\">Chi đo&agrave;n thanh ni&ecirc;n VBI cũng nhiệt t&igrave;nh tham gia c&aacute;c c&ocirc;ng t&aacute;c an sinh x&atilde; hội, t&iacute;ch cực đ&oacute;ng g&oacute;p v&agrave;o ng&acirc;n s&aacute;ch nh&agrave; nước, x&acirc;y dựng văn h&oacute;a c&ocirc;ng ty theo văn h&oacute;a VietinBank. Với việc tham dự chương tr&igrave;nh, Chi đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; học hỏi được c&aacute;c kinh nghiệm qu&yacute; b&aacute;u từ VietinBank cũng như c&aacute;c tập đo&agrave;n, tổng c&ocirc;ng ty thuộc Đảng Ủy Khối Doanh nghiệp Trung Ương để x&acirc;y dựng C&ocirc;ng ty Bảo hiểm VietinBank ng&agrave;y c&agrave;ng ph&aacute;t triển vững mạnh.</span></p>\r\n<p><img src=\"../../../uploads/635380801993640000.jpg\" alt=\"\" width=\"859\" height=\"1097\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Đại diện Chi đo&agrave;n C&ocirc;ng ty Bảo hiểm VietinBank tham dự chương tr&igrave;nh</em></span></p>', '2018-03-21 04:10:04', '2018-03-21 15:10:04', 'images/news/1520396161_pic-New-i_635380802000670000_HasThumb_Thumb.jpg.png'),
(67, 2, 'Bảo hiểm VietinBank trao giải chương trình thi đua bán bảo hiểm “25 năm gắn kết”', 'bao-hiem-vietinbank-trao-giai-chuong-trinh-thi-dua-ban-bao-hiem-25-nam-gan-ket', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Chương tr&igrave;nh thi đua b&aacute;n bảo hiểm &ldquo;25 năm gắn kết&rdquo; của C&ocirc;ng ty Bảo hiểm VietinBank (VBI) triển khai từ ng&agrave;y 08/07/2013 đến 05/10/2013 đ&atilde; nhận được sự hưởng ứng nhiệt t&igrave;nh v&agrave; những phản hồi t&iacute;ch cực từ c&aacute;c c&aacute;n bộ kinh doanh của VietinBank. Sau 3 th&aacute;ng triển khai, chương tr&igrave;nh đ&atilde; ... </span></span></p>', '<p style=\"text-align: justify;\">Chương tr&igrave;nh thi đua b&aacute;n bảo hiểm &ldquo;25 năm gắn kết&rdquo; của C&ocirc;ng ty Bảo hiểm VietinBank (VBI) triển khai từ ng&agrave;y 08/07/2013 đến 05/10/2013 đ&atilde; nhận được sự hưởng ứng nhiệt t&igrave;nh v&agrave; những phản hồi t&iacute;ch cực từ c&aacute;c c&aacute;n bộ kinh doanh của VietinBank.<br /> Sau 3 th&aacute;ng triển khai, chương tr&igrave;nh đ&atilde; t&igrave;m ra c&aacute;c c&aacute; nh&acirc;n xuất sắc đạt giải thưởng như sau:</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<table style=\"width: 90%; height: 67px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: center;\"><strong>STT</strong></td>\r\n<td style=\"text-align: center;\"><strong>Nội dung</strong></td>\r\n<td style=\"text-align: center;\"><strong>Giải thưởng</strong></td>\r\n<td style=\"text-align: center;\"><strong>C&aacute;n bộ</strong></td>\r\n<td style=\"text-align: center;\"><strong>Đơn vị</strong></td>\r\n</tr>\r\n<tr>\r\n<td>1</td>\r\n<td>Giải Nhất</td>\r\n<td>15 triệu đồng</td>\r\n<td>Nguyễn L&ecirc; Mai Tr&acirc;m</td>\r\n<td>VietinBank C&agrave; Mau</td>\r\n</tr>\r\n<tr>\r\n<td>2</td>\r\n<td>Giải Nh&igrave;</td>\r\n<td>10 triệu đồng</td>\r\n<td>Ho&agrave;ng Trung Ki&ecirc;n</td>\r\n<td>VietinBank Lạng Sơn</td>\r\n</tr>\r\n<tr>\r\n<td>3</td>\r\n<td>Giải Ba</td>\r\n<td>5 triệu đồng</td>\r\n<td>L&ecirc; Thị Th&ugrave;y Dương</td>\r\n<td><span style=\"font-size: 12pt; line-height: 115%; font-family: \'\'Times New Roman\'\'; color: #222222;\">VietinBank T&acirc;y Tiền Giang</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Giải tập thể xuất sắc:</p>\r\n<table style=\"width: 90%; height: 67px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: center;\"><strong>STT</strong></td>\r\n<td style=\"text-align: center;\"><strong>Nội dung</strong></td>\r\n<td style=\"text-align: center;\"><strong>Giải thưởng</strong></td>\r\n<td style=\"text-align: center;\"><strong>Đơn vị</strong></td>\r\n</tr>\r\n<tr>\r\n<td>1</td>\r\n<td>Giải tập thể xuất sắc</td>\r\n<td>01 TV Sony trị gi&aacute; 20 triệu đồng</td>\r\n<td>VietinBank C&agrave; Mau</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>V&agrave;o ng&agrave;y 22 th&aacute;ng 01 năm 2014, đại diện VBI, &ocirc;ng Trần Tiến Dũng &ndash; Gi&aacute;m đốc VBI CN S&agrave;i G&ograve;n đ&atilde; trao tặng thưởng cho c&aacute;c c&aacute; nh&acirc;n v&agrave; tập thể xuất sắc trong chương tr&igrave;nh thi đua b&aacute;n bảo hiểm 25 năm gắn kết. B&ecirc;n cạnh đ&oacute; &ocirc;ng Trần Tiến Dũng đ&atilde; gửi lời ch&uacute;c mừng v&agrave; cảm ơn đến tập thể v&agrave; c&aacute; nh&acirc;n Vietinbank CN C&agrave; Mau v&agrave; T&acirc;y Tiền Giang trong việc phối hợp triển khai b&aacute;n bảo hiểm trong thời gian vừa qua.</p>\r\n<table border=\"0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img src=\"../../../uploads/DSC_0421(1).jpg\" alt=\"\" width=\"500\" height=\"417\" /></p>\r\n</td>\r\n<td>\r\n<p style=\"margin-bottom: 0.0001pt; text-align: center; line-height: normal;\" align=\"center\"><img src=\"../../../uploads/DSC_0423.jpg\" alt=\"\" width=\"500\" height=\"536\" /></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\">Chị Nguyễn L&ecirc; Mai Tr&acirc;m - Vietinbank CN C&agrave; Mau nhận giải nhất c&aacute; nh&acirc;n xuất sắc</td>\r\n<td style=\"text-align: center;\">Chị Nguyễn Hằng Nga &ndash; Ph&oacute; gi&aacute;m đốc Vietinbank CN C&agrave; Mau nhận giải thưởng tập thể xuất sắc</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Trong chương tr&igrave;nh, đại diện Vietinbank CN C&agrave; Mau v&agrave; T&acirc;y Tiền Giang đ&atilde; gửi lời cảm ơn tới VBI đ&atilde; triển khai những chương tr&igrave;nh thi đua b&aacute;n bảo hiểm hấp dẫn, tăng t&iacute;nh phấn đấu cho c&aacute;n bộ ng&acirc;n h&agrave;ng khi tham gia khai th&aacute;c bảo hiểm. B&ecirc;n cạnh đ&oacute;, đại diện c&aacute;c chi nh&aacute;nh Vietinbank chia sẻ mong muốn VBI tiếp tục triển khai c&aacute;c chương tr&igrave;nh thi đua b&aacute;n bảo hiểm kh&aacute;c với mức ph&iacute; ưu đ&atilde;i d&agrave;nh cho kh&aacute;ch h&agrave;ng.<br /> Trước đ&oacute;, v&agrave;o ng&agrave;y 27/12/2013 tại Trụ sở VBI, Đại diện Ban L&atilde;nh đạo VBI, Ph&oacute; Tổng gi&aacute;m đốc Trần Thị Kim Yến đ&atilde; trao Giải Nh&igrave; v&agrave; bằng khen cho anh Ho&agrave;ng Trung Ki&ecirc;n.</p>\r\n<p><img src=\"../../../uploads/635380805643490000.jpg\" alt=\"\" width=\"400\" height=\"172\" /></p>\r\n<p style=\"text-align: center;\"><em>Ph&oacute; Tổng Gi&aacute;m Đốc VBI Trần Thị Kim Yến l&ecirc;n trao thưởng v&agrave; ch&uacute;c mừng c&aacute;n bộ Ho&agrave;ng Trung Ki&ecirc;n</em></p>\r\n<p style=\"text-align: justify;\"><br /> Nhận được giải thưởng từ VBI, anh Ho&agrave;ng Trung Ki&ecirc;n chia sẻ: &ldquo;T&ocirc;i rất vinh dự được nhận giải thưởng n&agrave;y. Trong thời gian tới, chi nh&aacute;nh ch&uacute;ng t&ocirc;i sẽ tiếp tục truyền th&ocirc;ng c&aacute;c sản phẩm bảo hiểm của VBI tới c&aacute;c kh&aacute;ch h&agrave;ng tại Chi nh&aacute;nh.&rdquo;</p>', '2018-03-21 04:15:06', '2018-03-21 15:15:06', 'images/news/1520396311_DSC_042_635380811805390000_HasThumb_Thumb.jpg.png'),
(68, 2, 'Bảo hiểm VietinBank bổ nhiệm vị trí lãnh đạo phòng Trụ sở chính', 'bao-hiem-vietinbank-bo-nhiem-vi-tri-lanh-dao-phong-tru-so-chinh', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">S&aacute;ng ng&agrave;y 25/01/2014 tại H&agrave; Nội đ&atilde; diễn ra lễ c&ocirc;ng bố c&aacute;c quyết định về việc bổ nhiệm vị tr&iacute; l&atilde;nh đạo ph&ograve;ng Trụ sở ch&iacute;nh Bảo hiểm VietinBank. Tham dự lễ trao quyết định c&oacute; Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể l&atilde;nh đạo c&aacute;c ph&ograve;ng ban Trụ sở ch&iacute;nh. Quy tr&igrave;nh, tr&igrave;nh tự v&agrave; thủ tục bổ nh... </span></span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 25/01/2014 tại H&agrave; Nội đ&atilde; diễn ra lễ c&ocirc;ng bố c&aacute;c quyết định về việc bổ nhiệm vị tr&iacute; l&atilde;nh đạo ph&ograve;ng Trụ sở ch&iacute;nh Bảo hiểm VietinBank. Tham dự lễ trao quyết định c&oacute; Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể l&atilde;nh đạo c&aacute;c ph&ograve;ng ban Trụ sở ch&iacute;nh. Quy tr&igrave;nh, tr&igrave;nh tự v&agrave; thủ tục bổ nhiệm c&aacute;c chức vụ l&atilde;nh đạo của C&ocirc;ng ty được thực hiện đ&uacute;ng theo quy định c&ocirc;ng t&aacute;c c&aacute;n bộ với sự đồng thuận, nhất tr&iacute; cao của Ban L&atilde;nh đạo C&ocirc;ng ty.<br />Theo quyết định bổ nhiệm, &ocirc;ng Đỗ Anh Vũ giữ chức vụ Trưởng ph&ograve;ng QLNN &amp; ĐGRR. &Ocirc;ng Đỗ Anh Vũ đ&atilde; c&oacute; hơn 11 năm kinh nghiệm trong ng&agrave;nh bảo hiểm v&agrave; đảm nhiệm nhiều vị tr&iacute; l&atilde;nh đạo quan trọng tại c&aacute;c c&ocirc;ng ty bảo hiểm.<br />Tại buổi lễ, Tổng gi&aacute;m đốc C&ocirc;ng ty, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm, ch&uacute;c mừng &ocirc;ng Đỗ Anh Vũ nhận nhiệm vụ mới, đồng thời n&ecirc;u r&otilde;: Việc bổ nhiệm vị tr&iacute; l&atilde;nh đạo lần n&agrave;y c&oacute; &yacute; nghĩa chiến lược trong việc kiện to&agrave;n bộ m&aacute;y tổ chức của Bảo hiểm VietinBank, g&oacute;p phần v&agrave;o định hướng ph&aacute;t triển mới của C&ocirc;ng ty theo m&ocirc; h&igrave;nh Bancassurance.<br />Ph&aacute;t biểu nhận nhiệm vụ, &ocirc;ng Đỗ Anh Vũ gửi lời cảm ơn tới Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; tin tưởng giao ph&oacute; trọng tr&aacute;ch mới, đồng thời cam kết sẽ tiếp tục phấn đấu để tiếp tục cống hiến, đưa Bảo hiểm VietinBank trở th&agrave;nh C&ocirc;ng ty bảo hiểm đ&aacute;ng tin cậy tr&ecirc;n thị trường, đồng thời g&oacute;p phần v&agrave;o việc thực hiện kế hoạch ph&aacute;t triển chung của VietinBank.</span></p>\r\n<p><img src=\"../../../uploads/635380810094290000.jpg\" alt=\"\" width=\"400\" height=\"267\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\">Tổng gi&aacute;m đốc L&ecirc; Tuấn Dũng trao quyết định bổ nhiệm</span></p>\r\n<p><img src=\"../../../uploads/635380810101360000.jpg\" alt=\"\" width=\"400\" height=\"267\" /></p>', '2018-03-21 04:16:00', '2018-03-21 15:16:00', 'images/news/1520396401_pic-New-i_635380810103710000_HasThumb_Thumb.JPG.png'),
(69, 2, 'Bảo hiểm VietinBank bổ nhiệm vị trí lãnh đạo Chi nhánh Đà Nẵng', 'bao-hiem-vietinbank-bo-nhiem-vi-tri-lanh-dao-chi-nhanh-da-nang', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 16/01/2014 tại Đ&agrave; Nẵng, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ trao quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh Đ&agrave; Nẵng cho &ocirc;ng L&ecirc; Đ&igrave;nh Quốc. Tham dự lễ trao quyết định c&oacute; đại diện Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh Đ&agrave; Nẵng </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 16/01/2014 tại Đ&agrave; Nẵng, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ trao quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh Đ&agrave; Nẵng cho &ocirc;ng L&ecirc; Đ&igrave;nh Quốc. Tham dự lễ trao quyết định c&oacute; đại diện Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh Đ&agrave; Nẵng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Tổng gi&aacute;m đốc VBI, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm v&agrave; ch&uacute;c mừng &ocirc;ng L&ecirc; Đ&igrave;nh Quốc nhận nhiệm vụ mới. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ mong muốn với 10 năm kinh nghiệm trong lĩnh vực bảo hiểm, &ocirc;ng L&ecirc; Đ&igrave;nh Quốc sẽ điều h&agrave;nh VBI Đ&agrave; Nẵng ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh.<br /></span></p>\r\n<p><span style=\"font-family: Arial;\">Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng L&ecirc; Đ&igrave;nh Quốc thể hiện quyết t&acirc;m phấn đấu để VBI Đ&agrave; Nẵng ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đồng thời cam kết sẽ tiếp tục cống hiến, đưa VBI Đ&agrave; Nẵng ph&aacute;t triển mạnh mẽ.</span></p>\r\n<p><img src=\"../../../uploads/Bo nhiem DN.jpg\" alt=\"\" width=\"1366\" height=\"1032\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Tổng gi&aacute;m đốc L&ecirc; Tuấn Dũng trao quyết định bổ nhiệm cho &ocirc;ng L&ecirc; Đ&igrave;nh Quốc</em></span></p>', '2018-03-21 04:16:27', '2018-03-21 15:16:27', 'images/news/1520396471_Bo-nhiem-_635429177674995667_HasThumb_Thumb.jpg.png'),
(70, 2, 'Nhật ký tình nguyện Chương trình “Nồi cháo tình thương”', 'nhat-ky-tinh-nguyen-chuong-trinh-noi-chao-tinh-thuong', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Từ 5h30 s&aacute;ng, Đo&agrave;n thanh ni&ecirc;n C&ocirc;ng ty Bảo hiểm VietinBank đ&atilde; tập hợp c&ugrave;ng nhau để chuẩn bị những nồi ch&aacute;o ấm n&oacute;ng tới Bệnh viện nhi Trung Ương </span></span></p>', '<p><span style=\"font-family: Arial;\">H&ocirc;m nay, ch&uacute;ng t&ocirc;i bắt đầu một ng&agrave;y mới đầy &yacute; nghĩa bằng chuyến h&agrave;nh tr&igrave;nh đến thăm c&aacute;c bệnh nh&acirc;n ở Bệnh viện Nhi Trung Ương. Từ rạng s&aacute;ng, ch&uacute;ng t&ocirc;i bắt đầu c&ocirc;ng việc &ndash; một c&ocirc;ng việc kh&aacute;c thường ng&agrave;y nhưng đem lại niềm hạnh ph&uacute;c đến kỳ lạ.</span></p>\r\n<p><span style=\"font-family: Arial;\">Từ 5h30 s&aacute;ng, Đo&agrave;n thanh ni&ecirc;n C&ocirc;ng ty Bảo hiểm VietinBank đ&atilde; tập hợp c&ugrave;ng nhau để chuẩn bị những nồi ch&aacute;o ấm n&oacute;ng tới Bệnh viện nhi Trung Ương. Ch&uacute;ng t&ocirc;i đ&atilde; chia 200 suất ch&aacute;o cho những bệnh nhi nơi đ&acirc;y, những người ch&uacute;ng t&ocirc;i chưa một lần biết mặt. Ấy vậy m&agrave;, ngay từ những suất ch&aacute;o đầu ti&ecirc;n, cảm gi&aacute;c th&acirc;n thiết đ&atilde; bao tr&ugrave;m tất cả mọi người tham gia.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tận mắt chứng kiến, ch&uacute;ng t&ocirc;i mới thấy v&agrave; biết được ở nơi đ&acirc;y c&oacute; biết bao mảnh đời cơ cực đang cần sự quan t&acirc;m của cộng đồng, x&atilde; hội. Ch&uacute;ng t&ocirc;i quyết định chia nhau ra, người ph&aacute;t ch&aacute;o, người hỏi han, động vi&ecirc;n để c&ugrave;ng một thời gian c&oacute; thể l&agrave;m được nhiều việc v&agrave; l&agrave;m cho chuyến đi t&igrave;nh nguyện n&agrave;y trở n&ecirc;n &yacute; nghĩa hơn. Những người tới nhận ch&aacute;o trong chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; đều l&agrave; người nh&agrave; của những bệnh nhi đang điều trị tại nơi đ&acirc;y, với những số phận, ho&agrave;n cảnh kh&aacute;c nhau nhưng ở họ ch&uacute;ng t&ocirc;i cảm nhận được sự ch&acirc;n th&agrave;nh, mộc mạc, giản dị v&agrave; hết sức gần gũi.</span></p>\r\n<p><span style=\"font-family: Arial;\">7h hơn, khi dọn dẹp xong, ch&uacute;ng t&ocirc;i quay lại với những c&ocirc;ng việc thường nhật, c&oacute; một ch&uacute;t hụt hẫng, một ch&uacute;t nuối tiếc bởi những h&igrave;nh ảnh tại Viện nhi Trung Ương đ&atilde; để lại cho ch&uacute;ng t&ocirc;i một ấn tượng c&ugrave;ng cảm x&uacute;c kh&ocirc;ng thể diễn tả bằng lời. Tận trong đ&aacute;y l&ograve;ng, ch&uacute;ng t&ocirc;i muốn quay trở lại nơi đ&acirc;y thường xuy&ecirc;n hơn để chia sẻ, để trao niềm tin, để c&ugrave;ng c&aacute;c bệnh nhi vượt qua những ng&agrave;y th&aacute;ng kh&oacute; khăn, vất vả.</span></p>\r\n<p><span style=\"font-family: Arial;\">Một v&agrave;i h&igrave;nh ảnh trong chuyến đi &yacute; nghĩa của ch&uacute;ng t&ocirc;i:</span></p>\r\n<p><img src=\"../../../uploads/635380811313320000.jpg\" alt=\"\" width=\"1000\" height=\"765\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>H&igrave;nh ảnh gia đ&igrave;nh bệnh nhi qu&acirc;y quanh nồi ch&aacute;o n&oacute;ng</em></span></p>\r\n<p><img src=\"../../../uploads/635380811326540000.jpg\" alt=\"\" width=\"1000\" height=\"640\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Niềm hạnh ph&uacute;c của ch&uacute;ng t&ocirc;i khi được đ&oacute;p g&oacute;p sức trẻ v&agrave;o c&ocirc;ng việc thiện nguyện</em></span></p>\r\n<p style=\"text-align: center;\"><img src=\"../../../uploads/2.jpg\" alt=\"\" width=\"1000\" height=\"952\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Nụ cười lấp l&aacute;nh niềm vui của người nh&agrave; bệnh nhi khi nhận ch&aacute;o</em></span></p>\r\n<p style=\"text-align: center;\"><img src=\"../../../uploads/1.jpg\" alt=\"\" width=\"1000\" height=\"946\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank mong muốn mang một ch&uacute;t ấm &aacute;p cho c&aacute;c bệnh nhi trong những ng&agrave;y đ&ocirc;ng gi&aacute; lạnh</em></span></p>\r\n<p>&nbsp;</p>', '2018-03-21 04:17:50', '2018-03-21 15:17:50', 'images/news/1520396645__635429169172668734_HasThumb_Thumb.JPG.png'),
(71, 2, 'Bảo hiểm VietinBank tổ chức chương trình kỷ niệm ngày Quốc tế Phụ nữ 8-3', 'bao-hiem-vietinbank-to-chuc-chuong-trinh-ky-niem-ngay-quoc-te-phu-nu-8-3', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Nh&acirc;n dịp kỷ niệm ng&agrave;y Quốc tế Phụ nữ 8-3 , C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh &ldquo;T&ocirc;n vinh ph&aacute;i đẹp VBI&rdquo; d&agrave;nh ri&ecirc;ng cho c&aacute;c nữ c&aacute;n bộ trong c&ocirc;ng ty. Chương tr&igrave;nh diễn ra tươi vui, s&ocirc;i động, qua đ&oacute; thể hiện được quan t&acirc;m của c&ocirc;ng ty đối với c&aacute;c nữ c&aacute;n bộ </span></span></p>', '<p><span style=\"font-family: Arial;\">Nh&acirc;n dịp kỷ niệm ng&agrave;y Quốc tế Phụ nữ 8-3 , C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh &ldquo;T&ocirc;n vinh ph&aacute;i đẹp VBI&rdquo; d&agrave;nh ri&ecirc;ng cho c&aacute;c nữ c&aacute;n bộ trong c&ocirc;ng ty. Chương tr&igrave;nh diễn ra tươi vui, s&ocirc;i động, qua đ&oacute; thể hiện được quan t&acirc;m của c&ocirc;ng ty đối với c&aacute;c nữ c&aacute;n bộ.</span></p>\r\n<p><img src=\"../../../uploads/IMG_1585.jpg\" alt=\"\" width=\"1366\" height=\"884\" /></p>\r\n<p><span style=\"font-family: Arial;\">Thay mặt Ban L&atilde;nh đạo VBI, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc đ&atilde; tặng hoa v&agrave; ph&aacute;t biểu về những đ&oacute;ng g&oacute;p của nữ c&aacute;n bộ VBI trong năm qua. B&ecirc;n cạnh đ&oacute;, &ocirc;ng đ&atilde; d&agrave;nh những lời ch&uacute;c tốt đẹp cho chị em phụ nữ n&oacute;i chung v&agrave; nữ c&aacute;n bộ VBI n&oacute;i ri&ecirc;ng trong ng&agrave;y 8/3: &ldquo;Năm 2013, VBI đạt được những th&agrave;nh t&iacute;ch đ&aacute;ng tự h&agrave;o như: Xếp thứ 135 trong 500 doanh nghiệp tăng trưởng nhanh nhất Việt Nam, tự h&agrave;o l&agrave; C&ocirc;ng ty con đầu ti&ecirc;n của VietinBank nhận được giấy chứng nhận ISO 9001:2008,&hellip;.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tất cả th&agrave;nh c&ocirc;ng đ&oacute; thuộc về to&agrave;n thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n VBI v&agrave; đặc biệt phải kể đến sự đ&oacute;ng g&oacute;p của 118 nữ c&aacute;n bộ VBI - những người mang trong m&igrave;nh trọng tr&aacute;ch của người vợ, người mẹ nhưng vẫn ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ m&agrave; c&ocirc;ng ty giao ph&oacute;. H&ocirc;m nay, nh&acirc;n ng&agrave;y Phụ nữ Việt Nam - một ng&agrave;y đặc biệt d&agrave;nh ri&ecirc;ng cho ph&aacute;i đẹp, t&ocirc;i xin thay mặt Ban l&atilde;nh đạo c&ocirc;ng ty, ch&uacute;c c&aacute;c chị em phụ nữ VBI lu&ocirc;n xinh đẹp, tươi trẻ, th&agrave;nh đạt v&agrave; ngập tr&agrave;n hạnh ph&uacute;c.&rdquo;</span></p>\r\n<p><span style=\"font-family: Arial;\">Sau lời ph&aacute;t biểu, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; gửi tặng những b&oacute; hoa tươi thắm thể hiện sự &acirc;n cần, chu đ&aacute;o d&agrave;nh tặng c&aacute;c chị em VBI.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Nguyễn Hồng Hạnh</em></span></p>', '2018-03-21 04:18:17', '2018-03-21 15:18:17', 'images/news/1520396724_IMG_158_635429169382177102_HasThumb_Thumb.jpg.png'),
(72, 2, 'Bảo hiểm VietinBank ưu đãi 35% phí Bảo hiểm xe ô tô cho cán bộ VietinBank', 'bao-hiem-vietinbank-uu-dai-35-phi-bao-hiem-xe-o-to-cho-can-bo-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Với mong muốn mang lại quyền lợi ưu đ&atilde;i nhất về ph&iacute; bảo hiểm cho c&aacute;n bộ trong hệ thống VietinBank, VBI triển khai chương tr&igrave;nh khuyến mại giảm 35% ph&iacute; bảo hiểm &ocirc; t&ocirc; v&agrave; tặng qu&agrave; cho c&aacute;c c&aacute;n bộ trong hệ thống VietinBank. </span></span></p>', '<p><img src=\"../../../uploads/635380812348540000.png\" alt=\"\" width=\"2018\" height=\"567\" /></p>\r\n<p>Với mong muốn mang lại quyền lợi ưu đ&atilde;i nhất về ph&iacute; bảo hiểm cho c&aacute;n bộ trong hệ thống VietinBank, VBI triển khai chương tr&igrave;nh khuyến mại giảm 35% ph&iacute; bảo hiểm &ocirc; t&ocirc; v&agrave; tặng qu&agrave; cho c&aacute;c c&aacute;n bộ trong hệ thống VietinBank.</p>\r\n<p><strong>1. Thời gian khuyến mại:</strong> Từ 20/03/2014 đến 18/06/2014.</p>\r\n<p><strong>2. Sản phẩm &aacute;p dụng:</strong> Bảo hiểm &Ocirc; t&ocirc; VietinCar.</p>\r\n<p><strong>3. Nội dung khuyến mại : </strong></p>\r\n<div>\r\n<div><strong>&nbsp;&nbsp;&nbsp; - Ưu đ&atilde;i giảm 35% ph&iacute; bảo hiểm</strong></div>\r\n&nbsp;d&agrave;nh ri&ecirc;ng cho CBNV hoặc vợ/chồng CBNV thuộc hệ thống VietinBank</div>\r\n<p>&nbsp;<strong>&nbsp;&nbsp;&nbsp; - Nhận ngay tấm chắn nắng cao cấp từ VBI</strong>&nbsp;</p>\r\n<p>Vui l&ograve;ng li&ecirc;n hệ với c&aacute;c chi nh&aacute;nh Bảo hiểm VietinBank tr&ecirc;n to&agrave;n quốc hoặc li&ecirc;n hệ Email: <a href=\"mailto:info.vbi@vietinbank.vn\">info.vbi@vietinbank.vn</a> để được tư vấn về chương tr&igrave;nh.</p>', '2018-03-21 04:19:36', '2018-03-21 15:19:36', 'images/news/1520397796_pic-New-i_635380812356200000_HasThumb_Thumb.png'),
(73, 2, 'VBI Care – Giải pháp tài chính cho sức khỏe vàng', 'vbi-care-giai-phap-tai-chinh-cho-suc-khoe-vang', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">M&ocirc;i trường &ocirc; nhiễm, thức ăn độc hại, th&oacute;i quen sinh hoạt hằng ng&agrave;y như h&uacute;t thuốc, rượu bia, căng thẳng, &aacute;p lực đến từ c&ocirc;ng việc ảnh hưởng kh&ocirc;ng &iacute;t tới chất lượng cuộc sống v&agrave; đặc biệt l&agrave; sức khỏe của ch&uacute;ng ta </span></span></p>', '<p><span style=\"font-family: Arial;\">M&ocirc;i trường &ocirc; nhiễm, thức ăn độc hại, th&oacute;i quen sinh hoạt hằng ng&agrave;y như h&uacute;t thuốc, rượu bia, căng thẳng, &aacute;p lực đến từ c&ocirc;ng việc ảnh hưởng kh&ocirc;ng &iacute;t tới chất lượng cuộc sống v&agrave; đặc biệt l&agrave; sức khỏe của ch&uacute;ng ta. Ng&agrave;y nay, khi c&aacute;c loại bệnh ng&agrave;y c&agrave;ng gia tăng, ch&uacute;ng ta c&agrave;ng phải ch&uacute; &yacute; nhiều hơn tới việc bảo vệ sức khỏe của m&igrave;nh.</span></p>\r\n<p><img src=\"../../../uploads/635380812676770000.jpg\" alt=\"\" width=\"1280\" height=\"793\" /></p>\r\n<p><span style=\"font-family: Arial;\"><strong>Trước những nguy cơ đ&oacute;, ch&uacute;ng ta phải l&agrave;m g&igrave; để bảo vệ m&igrave;nh?</strong></span></p>\r\n<p><span style=\"font-family: Arial;\">Hiện nay, t&igrave;nh trạng kh&aacute;m, chữa bệnh tại nước ta đang c&oacute; qu&aacute; nhiều bất cập khi c&aacute;c vấn nạn về ng&agrave;nh y tr&agrave;n lan, bệnh viện c&ocirc;ng qu&aacute; tải, trang thiết bị c&ograve;n ngh&egrave;o n&agrave;n, thủ tục kh&aacute;m, chữa bệnh qu&aacute; rắc rối,&hellip; thẻ BHYT kh&ocirc;ng đủ khả năng cung cấp những dịch vụ tốt nhất, tiện nghi nhất cho tất cả mọi người để c&oacute; thể an t&acirc;m kh&aacute;m v&agrave; chữa bệnh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Để đ&aacute;p ứng nhu cầu chăm s&oacute;c sức khỏe ng&agrave;y c&agrave;ng gia tăng, C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; thiết kế sản phẩm bảo hiểm với ưu đ&atilde;i l&ecirc;n tới 40% ph&iacute; d&agrave;nh ri&ecirc;ng cho c&aacute;n bộ VietinBank v&agrave; gia đ&igrave;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>Bảo hiểm VBI Care kh&aacute;c g&igrave; so với những sản phẩm hiện c&oacute; ngo&agrave;i thị trường?</strong></span></p>\r\n<p><span style=\"font-family: Arial;\">Khi tham gia sản phẩm n&agrave;y, c&aacute;n bộ VietinBank sẽ c&oacute; được c&aacute;c quyền lợi như: Được lựa chọn cơ sở kh&aacute;m bệnh uy t&iacute;n, c&aacute;c bệnh viện tại nước ngo&agrave;i; Được hưởng mức ph&iacute; ưu đ&atilde;i giảm 40% d&agrave;nh cho c&aacute;n bộ VietinBank; Được bảo l&atilde;nh thanh to&aacute;n với một số bệnh viện bằng thẻ BHSK VBI Care; Nhiều dịch vụ th&ocirc;ng minh đi k&egrave;m: nhắc t&aacute;i kh&aacute;m, lưu hồ sơ kh&aacute;m bệnh, tư vấn sức khỏe 24/24 qua đường d&acirc;y n&oacute;ng,...</span></p>\r\n<p><span style=\"font-family: Arial;\">Bảo hiểm VBI Care đang nhận được nhiều phản hồi t&iacute;ch cực từ kh&aacute;ch h&agrave;ng l&agrave; ch&iacute;nh c&aacute;c c&aacute;n bộ VietinBank. Một minh chứng cho chất lượng vượt trội của sản phẩm VBI Care l&agrave; trường hợp của chị Hằng &ndash; c&aacute;n bộ VietinBank tại H&agrave; Nội. Sau khi được c&aacute;n bộ VBI tư vấn về sản phẩm, chị Hằng đ&atilde; quyết định mua bảo hiểm VBI Care cho bản th&acirc;n v&agrave; con g&aacute;i. Ng&agrave;y 03/06/2013, con g&aacute;i của chị bị sốt k&egrave;m theo đau bụng, chị đ&atilde; đưa con kh&aacute;m v&agrave; điều trị tại bệnh viện Việt Ph&aacute;p. Ngay sau đ&oacute;, chị Hằng đ&atilde; nhận được hỗ trợ t&agrave;i ch&iacute;nh 100% từ VBI cho to&agrave;n bộ viện ph&iacute; kh&aacute;m v&agrave; điều trị.</span></p>\r\n<p><span style=\"font-family: Arial;\">Với thủ tục nhanh gọn, bảo l&atilde;nh thanh to&aacute;n, mức ph&iacute; bảo hiểm thấp, quyền lợi bảo hiểm cao, VBI Care l&agrave; sản phẩm chất lượng d&agrave;nh cho c&aacute;c tổ chức, doanh nghiệp muốn đảm bảo quyền lợi cho c&aacute;n bộ, nh&acirc;n vi&ecirc;n của m&igrave;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Để tham gia sản phẩm bạn vui l&ograve;ng li&ecirc;n hệc&aacute;c chi nh&aacute;nh Bảo hiểm VietinBank tr&ecirc;n to&agrave;n quốc hoặc li&ecirc;n hệ số điện thoại 04 3942 5650 (số m&aacute;y lẻ 212) hoặc Email: <a href=\"mailto:info.vbi@vietinbank.vn.\">info.vbi@vietinbank.vn.</a></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>Hồng Hạnh</strong></span></p>', '2018-03-21 04:20:03', '2018-03-21 15:20:03', 'images/news/1520397917_pic-New-i_635380812681680000_HasThumb_Thumb.jpg.png'),
(74, 2, 'VBI ưu đãi giảm 40% phí bảo hiểm sức khỏe cho cán bộ VietinBank', 'vbi-uu-dai-giam-40-phi-bao-hiem-suc-khoe-cho-can-bo-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">L&agrave; một c&ocirc;ng ty th&agrave;nh vi&ecirc;n của tập đo&agrave;n t&agrave;i ch&iacute;nh VietinBank, VBI hiểu được sự kh&oacute; khăn của c&aacute;c c&aacute;n bộ quản l&yacute; trong việc c&acirc;n đối chi ph&iacute; của chi nh&aacute;nh với việc mang lại chế độ đ&atilde;i ngộ tốt nhất cho nh&acirc;n vi&ecirc;n của m&igrave;nh để thu h&uacute;t v&agrave; giữ ch&acirc;n c&aacute;c nh&acirc;n t&agrave;i </span></span></p>', '<p><img src=\"../../../uploads/635380812983290000.jpg\" alt=\"\" width=\"765\" height=\"490\" /></p>\r\n<p><span style=\"font-family: Arial;\">L&agrave; một c&ocirc;ng ty th&agrave;nh vi&ecirc;n của tập đo&agrave;n t&agrave;i ch&iacute;nh VietinBank, VBI hiểu được sự kh&oacute; khăn của c&aacute;c c&aacute;n bộ quản l&yacute; trong việc c&acirc;n đối chi ph&iacute; của chi nh&aacute;nh với việc mang lại chế độ đ&atilde;i ngộ tốt nhất cho nh&acirc;n vi&ecirc;n của m&igrave;nh để thu h&uacute;t v&agrave; giữ ch&acirc;n c&aacute;c nh&acirc;n t&agrave;i.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ch&iacute;nh v&igrave; sự thấu hiểu đ&oacute; n&ecirc;n ch&uacute;ng t&ocirc;i đ&atilde; thiết kế sản phẩm bảo hiểm sức khỏe cao cấp VBI Care vớ<strong>i ưu đ&atilde;i đặc biệt giảm tới 40% ph&iacute; d&agrave;nh ri&ecirc;ng cho c&aacute;n bộ nh&acirc;n vi&ecirc;n VietinBank</strong>. Bảo hiểm VBI Care &nbsp;gi&uacute;p Gi&aacute;m đốc chi nh&aacute;nh mang lại ch&iacute;nh s&aacute;ch đ&atilde;i ngộ tốt nhất cho nh&acirc;n vi&ecirc;n, đảm bảo t&agrave;i ch&iacute;nh cho c&aacute;c c&aacute;n bộ nh&acirc;n vi&ecirc;n VietinBank v&agrave; gia đ&igrave;nh trước những vấn đề về sức khỏe như ốm đau, bệnh tật, tai nạn, thai sản,..</span></p>\r\n<p><span style=\"font-family: Arial;\">Đ&acirc;y l&agrave; c&aacute;ch để Gi&aacute;m đốc chi nh&aacute;nh củng cố sự trung th&agrave;nh v&agrave; cam kết của nh&acirc;n vi&ecirc;n cũng như một h&igrave;nh thức thưởng cho những đ&oacute;ng g&oacute;p của nh&acirc;n vi&ecirc;n với chi nh&aacute;nh. Ngo&agrave;i ra, Gi&aacute;m đốc Chi nh&aacute;nh cũng c&oacute; thể to&agrave;n t&acirc;m to&agrave;n &yacute; v&agrave;o việc tăng trưởng lợi nhuận, m&agrave; kh&ocirc;ng phải lo lắng đến những rủi ro t&agrave;i ch&iacute;nh cũng như quản l&yacute; những chi ph&iacute; y tế đắt đỏ, hay sự vắng mặt của nh&acirc;n vi&ecirc;n trong những trường hợp tai nạn, bệnh tật v&igrave; đ&atilde; c&oacute; VBI hỗ trợ về t&agrave;i ch&iacute;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>C&aacute;c quyền lợi khi sử dụng Bảo hiểm sức khỏe VBI Care:</strong></span></p>\r\n<ul>\r\n<li>Được hưởng số tiền bảo hiểm cao trong trường hợp tử vong, thương tật vĩnh viễn do ốm đau, bệnh tật v&agrave; tai nạn</li>\r\n<li>Được hỗ trợ thanh to&aacute;n c&aacute;c chi ph&iacute; điều trị nội tr&uacute; v&agrave; ngoại tr&uacute; với hạn mức cao.</li>\r\n<li>Được chi trả c&aacute;c chi ph&iacute; y tế li&ecirc;n quan tới điều trị răng lợi, thai sản v&agrave; chăm s&oacute;c trẻ sơ sinh.</li>\r\n<li>Được hưởng trợ cấp lương nghỉ do tai nạn.</li>\r\n<li>Được lựa chọn kh&aacute;m chữa bệnh tại c&aacute;c bệnh vi&ecirc;n uy t&iacute;n tr&ecirc;n cả nước như: Bệnh viện Việt Đức, Bệnh viện Bưu Điện, Bệnh viện Vinmec, Bệnh viện Hồng Ngọc, Bệnh viện Việt Ph&aacute;p,&hellip;</li>\r\n<li>Được hưởng dịch vụ bảo l&atilde;nh m&agrave; kh&ocirc;ng cần thanh to&aacute;n bằng tiền mặt tại c&aacute;c bệnh viện trong hệ thống bảo l&atilde;nh của VBI.</li>\r\n</ul>\r\n<p><span style=\"font-family: Arial;\">B&ecirc;n cạnh đ&oacute;, c&aacute;n bộ nh&acirc;n vi&ecirc;n VietinBank sẽ được hưởng dịch vụ tư vấn miễn ph&iacute; c&aacute;c vấn đề li&ecirc;n quan tới sức khỏe v&agrave; bồi thường từ c&aacute;c b&aacute;c sĩ gi&agrave;u kinh nghiệm, chuy&ecirc;n m&ocirc;n cao của ch&uacute;ng t&ocirc;i qua đường d&acirc;y n&oacute;ng <strong>0963.683.553</strong>.</span></p>\r\n<p><span style=\"font-family: Arial;\">Bảo hiểm sức khỏe VBI &nbsp;Care đem lại những ch&iacute;nh s&aacute;ch đ&atilde;i ngộ tốt nhất cho c&aacute;n bộ nh&acirc;n vi&ecirc;n VietinBank. H&atilde;y thể hiện sự quan t&acirc;m chăm s&oacute;c của bạn tới c&aacute;c nh&acirc;n vi&ecirc;n bằng <strong>bảo hiểm sức khỏe VBI Care - Giải ph&aacute;p Bảo hiểm Y tế v&agrave; Tai nạn to&agrave;n diện</strong>.</span></p>', '2018-03-21 04:20:37', '2018-03-21 15:20:37', 'images/news/1520398309_pic-New-i_635380812985670000_HasThumb_Thumb.jpg.png'),
(75, 2, 'Nhận ngay 1 triệu đồng mỗi tuần cùng “Ngôi sao Bancassurance”', 'nhan-ngay-1-trieu-dong-moi-tuan-cung-ngoi-sao-bancassurance', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Từ ng&agrave;y 24/03/2014 đến 31/05/2014, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh thi đua d&agrave;nh cho c&aacute;c c&aacute;n bộ VietinBank tư vấn v&agrave; cấp đơn bảo hiểm qua phần mềm Bancassurance </span></span></p>', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y <strong>24/03/2014 đến 31/05/2014</strong>, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh thi đua d&agrave;nh cho c&aacute;c c&aacute;n bộ VietinBank tư vấn v&agrave; cấp đơn bảo hiểm qua phần mềm Bancassurance.</span></p>\r\n<p><img src=\"../../../uploads/635380813201800000.jpg\" alt=\"\" width=\"686\" height=\"1122\" /></p>\r\n<p><span style=\"font-family: Arial;\">Li&ecirc;n tục trong 10 tuần, c&aacute;n bộ VietinBank cấp đơn xuất sắc nhất mỗi tuần sẽ được nhận ngay danh hiệu &ldquo;Ng&ocirc;i sao Bancassurance&rdquo; của tuần k&egrave;m theo giải thưởng tiền mặt 1.000.000 đồng. Đặc biệt hơn, thứ 5 h&agrave;ng tuần l&agrave; ng&agrave;y c&aacute;c VietinBankers c&oacute; thể tăng tốc với doanh số bảo hiểm được nh&acirc;n đ&ocirc;i để ghi nhận v&agrave;o kết quả thi đua chung của cả tuần. Top 10 c&aacute;n bộ c&oacute; doanh thu cao nhất sẽ li&ecirc;n tục được cập nhật tr&ecirc;n phần mềm.</span></p>\r\n<p><span style=\"font-family: Arial;\">Để tham gia chương tr&igrave;nh, c&aacute;n bộ VietinBank chỉ cần đăng nhập v&agrave;o phần mềm Bancass theo địa chỉ: <a href=\"http://baohiem-vbi\">http://baohiem-vbi</a>, chọn cấp đơn cho c&aacute;c sản phẩm bảo hiểm đ&atilde; được thiết kế t&iacute;ch hợp d&agrave;nh ri&ecirc;ng cho kh&aacute;ch h&agrave;ng VietinBank như: Bảo hiểm con người VietinCare; Bảo hiểm nh&agrave; tư nh&acirc;n VietinHome; Bảo hiểm &ocirc; t&ocirc; VietinCar; Bảo hiểm du học sinh v&agrave; chọn những chương tr&igrave;nh khuyến mại ưu đ&atilde;i giảm ph&iacute; đầy hấp dẫn.</span></p>\r\n<p><span style=\"font-family: Arial;\">Mọi chi tiết về chương tr&igrave;nh, xin vui l&ograve;ng li&ecirc;n hệ đường d&acirc;y n&oacute;ng: 04 3942 5650, số lẻ: 341 hoặc Email: <a href=\"mailto:info.vbi@vietinbank.vn\">info.vbi@vietinbank.vn</a> để được tư vấn về chương tr&igrave;nh.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\">Cao Hải V&acirc;n </span></p>', '2018-03-21 04:21:22', '2018-03-21 15:21:22', 'images/news/1520398413_pic-New-i_635380813203440000_HasThumb_Thumb.jpg.png'),
(76, 2, 'Chi bộ Bảo hiểm VietinBank kết nạp đảng viên', 'chi-bo-bao-hiem-vietinbank-ket-nap-dang-vien', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 26/03/2014, tại H&agrave; Nội, Chi bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức lễ kết nạp đảng cho 6 quần ch&uacute;ng ưu t&uacute;. Tham dự buổi lễ c&oacute; đồng ch&iacute; Chu Văn H&agrave;o - Ủy vi&ecirc;n Ban chấp h&agrave;nh Đảng Bộ VietinBank, đồng ch&iacute; Trương B&iacute;ch V&acirc;n &ndash; Ban tổ chức Đảng ủy VietinBank </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 26/03/2014, tại H&agrave; Nội, Chi bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức lễ kết nạp đảng cho 6 quần ch&uacute;ng ưu t&uacute;. Tham dự buổi lễ c&oacute; đồng ch&iacute; Chu Văn H&agrave;o - Ủy vi&ecirc;n Ban chấp h&agrave;nh Đảng Bộ VietinBank, đồng ch&iacute; Trương B&iacute;ch V&acirc;n &ndash; Ban tổ chức Đảng ủy VietinBank, đồng ch&iacute; Nguyễn Trung Hậu &ndash; Ph&oacute; b&iacute; thư thường trực Đo&agrave;n thanh ni&ecirc;n VietinBank c&ugrave;ng to&agrave;n thể Chi bộ Bảo hiểm VietinBank. Trải qua qu&aacute; tr&igrave;nh tu dưỡng v&agrave; r&egrave;n luyện, 6 quần ch&uacute;ng ưu t&uacute; Nguyễn Ngọc T&uacute;, Nguyễn Thị Quỳnh Hoa, Phạm Trung Th&agrave;nh, Nguyễn Thị Vinh Hoa, Nguyễn Như Th&agrave;nh, Đỗ Thị B&iacute;ch Ch&acirc;m đ&atilde; được k&ecirc;́t nạp Đảng. Đ&acirc;y l&agrave; những c&aacute;n bộ trẻ, ưu t&uacute;, c&oacute; phẩm chất đạo đức tốt, c&oacute; năng lực tr&igrave;nh độ chuy&ecirc;n m&ocirc;n, đ&atilde; trải qua qu&aacute; tr&igrave;nh phấn đấu v&agrave; r&egrave;n luyện tốt.&nbsp;</span></p>\r\n<p><img src=\"../../../uploads/ketnapdang.jpg\" alt=\"\" width=\"1366\" height=\"507\" /></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu chỉ đạo tại buổi lễ, đồng ch&iacute; Chu Văn H&agrave;o - Ủy vi&ecirc;n Ban chấp h&agrave;nh Đảng Bộ VietinBank ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n vừa được kết nạp v&agrave; nhấn mạnh: Phấn đấu trở th&agrave;nh đảng vi&ecirc;n l&agrave; nguyện vọng ch&iacute;nh đ&aacute;ng của tuổi trẻ, l&agrave; vinh dự, niềm tự h&agrave;o v&agrave; cũng l&agrave; đ&ograve;i hỏi, trọng tr&aacute;ch với c&aacute;c đảng vi&ecirc;n mới. Đồng ch&iacute; y&ecirc;u cầu c&aacute;c Đảng vi&ecirc;n mới kết nạp cần học tập nắm vững chủ trương ch&iacute;nh s&aacute;ch của Đảng v&agrave; Nh&agrave; nước, tu&acirc;n thủ ph&aacute;p luật, quy chế nội quy lao động,&hellip;ra sức học tập n&acirc;ng cao nghiệp vụ, t&iacute;ch cực tham gia c&aacute;c hoạt động của đơn vị, n&ecirc;u gương bằng việc l&agrave;m cụ thể, d&aacute;m nghĩ, d&aacute;m l&agrave;m, d&aacute;m chịu tr&aacute;ch nhiệm&hellip; Mỗi đồng ch&iacute; ở mỗi cương vị của m&igrave;nh cần t&iacute;ch cực phấn đấu với tinh thần tr&aacute;ch nhiệm cao, kh&ocirc;ng chỉ ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc hiện tại m&agrave; c&ograve;n g&aacute;nh v&aacute;ch nhiệm vụ lớn hơn nữa trong giai đoạn mới.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu chỉ đạo tại buổi lễ, đồng ch&iacute; Chu Văn H&agrave;o - Ủy vi&ecirc;n Ban chấp h&agrave;nh Đảng Bộ VietinBank ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n vừa được kết nạp v&agrave; nhấn mạnh: Phấn đấu trở th&agrave;nh đảng vi&ecirc;n l&agrave; nguyện vọng ch&iacute;nh đ&aacute;ng của tuổi trẻ, l&agrave; vinh dự, niềm tự h&agrave;o v&agrave; cũng l&agrave; đ&ograve;i hỏi, trọng tr&aacute;ch với c&aacute;c đảng vi&ecirc;n mới. Đồng ch&iacute; y&ecirc;u cầu c&aacute;c Đảng vi&ecirc;n mới kết nạp cần học tập nắm vững chủ trương ch&iacute;nh s&aacute;ch của Đảng v&agrave; Nh&agrave; nước, tu&acirc;n thủ ph&aacute;p luật, quy chế nội quy lao động,&hellip;ra sức học tập n&acirc;ng cao nghiệp vụ, t&iacute;ch cực tham gia c&aacute;c hoạt động của đơn vị, n&ecirc;u gương bằng việc l&agrave;m cụ thể, d&aacute;m nghĩ, d&aacute;m l&agrave;m, d&aacute;m chịu tr&aacute;ch nhiệm&hellip; Mỗi đồng ch&iacute; ở mỗi cương vị của m&igrave;nh cần t&iacute;ch cực phấn đấu với tinh thần tr&aacute;ch nhiệm cao, kh&ocirc;ng chỉ ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc hiện tại m&agrave; c&ograve;n g&aacute;nh v&aacute;ch nhiệm vụ lớn hơn nữa trong giai đoạn mới.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>VBI</strong></span></p>', '2018-03-21 04:21:59', '2018-03-21 15:21:59', 'images/news/1520398602_ketnapdan_635429169911330031_HasThumb_Thumb.JPG.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(77, 2, 'VBI tổ chức Hội thảo tư vấn sức khoẻ', 'vbi-to-chuc-hoi-thao-tu-van-suc-khoe', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Chiều qua (28/3) tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) phối hợp với VietinBank v&agrave; C&ocirc;ng ty Dịch vụ bảo hiểm &amp; sức khoẻ Insmart tổ chức Hội thảo tư vấn sức khoẻ cho CBNV</span></span></p>', '<p><span style=\"font-family: Arial;\">Chiều qua (28/3) tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) phối hợp với VietinBank v&agrave; C&ocirc;ng ty Dịch vụ bảo hiểm &amp; sức khoẻ Insmart tổ chức Hội thảo tư vấn sức khoẻ cho CBNV.</span></p>\r\n<p><img src=\"../../../uploads/635380813826060000.jpg\" alt=\"\" width=\"340\" height=\"219\" /></p>\r\n<p><span style=\"font-family: Arial;\">Với chủ đề &ldquo;Sự hiểu biết v&agrave; phương ph&aacute;p điều trị ung thư trong thời đại mới, xo&aacute; tan định kiến về ung thư&rdquo;, hội thảo đ&atilde; thu h&uacute;t sự tham gia của hơn 100 c&aacute;n bộ nh&acirc;n vi&ecirc;n (CBNV) thuộc Trụ sở ch&iacute;nh, c&aacute;c đơn vị, c&ocirc;ng ty trực thuộc tr&ecirc;n địa b&agrave;n H&agrave; Nội.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu tại hội thảo, &ocirc;ng Trần Ngọc Anh - Ph&oacute; ph&ograve;ng Nghi&ecirc;n cứu thị trường v&agrave; ph&aacute;t triển sản phẩm Khối B&aacute;n lẻ nhấn mạnh: Trong qu&aacute; tr&igrave;nh triển khai hoạt động kinh doanh, b&ecirc;n cạnh việc cung cấp c&aacute;c sản phẩm dịch vụ t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng, VietinBank lu&ocirc;n hướng tới mục ti&ecirc;u tạo c&aacute;c gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng. Hội thảo được tổ chức với mong muốn cung cấp c&aacute;c th&ocirc;ng tin bổ &iacute;ch, hữu hiệu nhằm gi&uacute;p CBNV VietinBank n&acirc;ng cao sức khoẻ v&agrave; cải thiện chất lượng cuộc sống.</span></p>\r\n<p><span style=\"font-family: Arial;\">Thay mặt l&atilde;nh đạo VBI, Ph&oacute; Tổng gi&aacute;m đốc Nguyễn Hồng Phong b&agrave;y tỏ hy vọng sự tham gia của c&aacute;c gi&aacute;o sư đầu ng&agrave;nh đến từ bệnh viện lớn của Malaysia sẽ đem đến những th&ocirc;ng tin thực sự c&oacute; &yacute; nghĩa cho c&aacute;c CBNV VietinBank th&ocirc;ng qua loại h&igrave;nh hội thảo sức khoẻ thiết thực n&agrave;y. &Ocirc;ng Cheam Teck Eng &ndash; Tổng Gi&aacute;m đốc Insmart khẳng định, hội thảo l&agrave; một trong những gi&aacute; trị gia tăng m&agrave; Insmart phối hợp với VBI v&agrave; VietinBank mang đến cho CBNV VietinBank c&aacute;c th&ocirc;ng tin bổ &iacute;ch để c&oacute; thể biết được l&agrave;m sao c&oacute; thể ngăn chặn được bệnh ung thư, dấu hiệu ph&aacute;t hiện bệnh v&agrave; c&aacute;ch chữa bệnh ra sao&hellip;</span></p>\r\n<p><span style=\"font-family: Arial;\">B&aacute;c sỹ Dato Mohamed Ibarhim đến từ Bệnh viện Beacon International Specialist Center (Malaysia) l&agrave; một b&aacute;c sỹ chuy&ecirc;n ng&agrave;nh ung thư, đ&atilde; giới thiệu về những th&agrave;nh tựu mới li&ecirc;n quan đến điều trị ung thư, l&agrave;m giảm nỗi lo, nỗi sợ h&atilde;i của mọi người về căn bệnh ung thư. &Ocirc;ng cũng chỉ ra một số nguy&ecirc;n nh&acirc;n thường g&acirc;y ra căn bệnh ung thư như do h&uacute;t thuốc, chế độ ăn uống, ngo&agrave;i ra c&oacute; thể do tuổi t&aacute;c, uống rượu hoặc mắc c&aacute;c bệnh nhiễm tr&ugrave;ng&hellip;</span></p>\r\n<p><span style=\"font-family: Arial;\">Cũng tại hội thảo, người tham dự v&agrave; b&aacute;c sỹ đ&atilde; c&oacute; phần hỏi đ&aacute;p giao lưu, chia sẻ rất hữu &iacute;ch về c&aacute;c nội dung li&ecirc;n quan đến ph&ograve;ng v&agrave; điều trị căn bệnh n&agrave;y, g&oacute;p phần cải thiện v&agrave; n&acirc;ng cao sức khỏe cho mỗi CBNV.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>VietinBank</em></span></p>', '2018-03-21 04:22:30', '2018-03-21 15:22:30', 'images/news/1520398689_pic-New-i_635380813827170000_HasThumb_Thumb.jpg.png'),
(78, 2, 'Khám phá thế giới cùng VBI', 'kham-pha-the-gioi-cung-vbi', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Bảo hiểm du lịch quốc tế l&agrave; người bạn đồng h&agrave;nh cần thiết trong mọi chuyến đi của bạn: du lịch, hội thảo, du học&hellip; v&agrave; d&ugrave; ở bất cứ đ&acirc;u, bảo hiểm du lịch quốc tế của C&ocirc;ng ty Bảo hiểm VietinBank (VBI) mang lại cho bạn sự y&ecirc;n t&acirc;m tuyệt đối với mạng lưới hỗ trợ y tế v&agrave; th&ocirc;ng tin du lịc... </span></span></p>', '<p><span style=\"font-family: Arial;\">Bảo hiểm du lịch quốc tế l&agrave; người bạn đồng h&agrave;nh cần thiết trong mọi chuyến đi của bạn: du lịch, hội thảo, du học&hellip; v&agrave; d&ugrave; ở bất cứ đ&acirc;u, bảo hiểm du lịch quốc tế của C&ocirc;ng ty Bảo hiểm VietinBank (VBI) mang lại cho bạn sự y&ecirc;n t&acirc;m tuyệt đối với mạng lưới hỗ trợ y tế v&agrave; th&ocirc;ng tin du lịch của SOS tr&ecirc;n phạm vi to&agrave;n cầu. Bảo hiểm du lịch quốc tế l&agrave; sự lựa chọn s&aacute;ng suốt để bạn tự bảo vệ m&igrave;nh khỏi c&aacute;c rủi ro trong suốt h&agrave;nh tr&igrave;nh.</span></p>\r\n<p><img src=\"../../../uploads/635380815023560000.jpg\" alt=\"\" width=\"340\" height=\"313\" /></p>\r\n<p><span style=\"font-family: Arial;\">Diễn ra từ ng&agrave;y 31/3 đến 14/5 (đợt I) v&agrave; từ ng&agrave;y 16/5 đến 30/6 (đợt II), chương tr&igrave;nh khuyến mại &ldquo;Kh&aacute;m ph&aacute; thế giới c&ugrave;ng Bảo hiểm VBI&rdquo; giảm 30% ph&iacute; bảo hiểm du lịch quốc tế cho kh&aacute;ch h&agrave;ng mua bảo hiểm du lịch qua dịch vụ iPay tr&ecirc;n website <a href=\"http://www.vietinbank.vn.\">www.vietinbank.vn.&nbsp;</a></span></p>\r\n<p><span style=\"font-family: Arial;\">Đặc biệt, theo chương tr&igrave;nh, kh&aacute;ch h&agrave;ng đi theo nh&oacute;m gia đ&igrave;nh được giảm tới 2 lần ph&iacute;. Lần thứ nhất giảm theo g&oacute;i gia đ&igrave;nh, lần thứ hai giảm 30%/ph&iacute; của g&oacute;i gia đ&igrave;nh. Kh&aacute;ch h&agrave;ng c&oacute; thể mua bảo hiểm du lịch quốc tế trước cho chuyến đi sau ng&agrave;y 30/6/2014.</span></p>\r\n<p><span style=\"font-family: Arial;\">Đ&acirc;y l&agrave; một trong những gi&aacute; trị gia tăng v&agrave; ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng sử dụng dịch vụ bảo hiểm của VBI cũng như dịch vụ iPay, g&oacute;p phần th&uacute;c đẩy thanh to&aacute;n kh&ocirc;ng d&ugrave;ng tiền mặt ở Việt Nam.</span></p>\r\n<p><span style=\"font-family: Arial;\">Vui l&ograve;ng li&ecirc;n hệ với c&aacute;c chi nh&aacute;nh Bảo hiểm VietinBank tr&ecirc;n to&agrave;n quốc hoặc li&ecirc;n hệ 372228300-601 hoặc&nbsp;Email:<a href=\"mailto:info.vbi@vietinbank.vn\">info.vbi@vietinbank.vn</a> để được tư vấn về chương tr&igrave;nh.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\">Lan Oanh</span></p>', '2018-03-21 04:23:29', '2018-03-21 15:23:29', 'images/news/1520398823_pic-New-i_635380815027500000_HasThumb_Thumb.jpg.png'),
(79, 2, 'VBI bảo hiểm cho Dự án hầm Đường bộ Đèo Cả', 'vbi-bao-hiem-cho-du-an-ham-duong-bo-deo-ca', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 01/03/2014, tại H&agrave; Nội đ&atilde; diễn ra lễ k&yacute; kết hợp đồng giữa C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả với Bảo hiểm VietinBank (VBI). Tham dự lễ k&yacute; kết c&oacute; &ocirc;ng Hồ Minh Ho&agrave;ng &ndash; Tổng gi&aacute;m đốc C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc Bảo hiểm VietinBank c&ugrave;ng c&aacute;c l&atilde;nh đạo hai b&ecirc;n </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 01/03/2014, tại H&agrave; Nội đ&atilde; diễn ra lễ k&yacute; kết hợp đồng giữa C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả với Bảo hiểm VietinBank (VBI). Tham dự lễ k&yacute; kết c&oacute; &ocirc;ng Hồ Minh Ho&agrave;ng &ndash; Tổng gi&aacute;m đốc C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc Bảo hiểm VietinBank c&ugrave;ng c&aacute;c l&atilde;nh đạo hai b&ecirc;n.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong dự &aacute;n, C&ocirc;ng ty Cổ phần Đầu tư Đ&egrave;o Cả đồng &yacute; cho VBI l&agrave; nh&agrave; bảo hiểm ch&iacute;nh của phần BOT dự &aacute;n Đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh hầm Đường bộ qua Đ&egrave;o Cả QL1A tỉnh Ph&uacute; Y&ecirc;n v&agrave; tỉnh Kh&aacute;nh H&ograve;a. Tổng gi&aacute; trị dự &aacute;n ước t&iacute;nh tr&ecirc;n 9 ngh&igrave;n tỷ đồng cho hợp đồng mọi rủi ro x&acirc;y dựng v&agrave; lắp đặt với thời gian bảo hiểm l&ecirc;n đến 48 th&aacute;ng.</span></p>\r\n<p><img src=\"../../../uploads/635380815364050000.jpg\" alt=\"\" width=\"923\" height=\"758\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>&Aacute;nh: L&atilde;nh đạo VBI v&agrave; C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả k&yacute; kết hợp đồng</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Hầm đường bộ qua Đ&egrave;o Cả l&agrave; dự &aacute;n mang t&iacute;nh li&ecirc;n kết quan trọng trong chương tr&igrave;nh ph&aacute;t triển đường cao tốc c&oacute; t&iacute;nh l&acirc;u d&agrave;i của Ch&iacute;nh phủ. Dự &aacute;n c&oacute; tổng mức đầu tư hơn 15.000 tỷ đồng, tổng chiều d&agrave;i to&agrave;n tuyến l&agrave; 13,4km, bề rộng mặt cắt ngang l&agrave; 24m, được thiết kế theo ti&ecirc;u chuẩn đường cao tốc với vận tốc thiết kế l&agrave; 80km/h, đi qua hai tỉnh Ph&uacute; Y&ecirc;n v&agrave; Kh&aacute;nh H&ograve;a. Dự &aacute;n g&oacute;p phần ho&agrave;n thiện mạng lưới giao th&ocirc;ng cả nước, trong đ&oacute; c&oacute; đường cao tốc Bắc &ndash; Nam đang h&igrave;nh th&agrave;nh, th&uacute;c đẩy ph&aacute;t triển kinh tế - x&atilde; hội, củng cố an ninh quốc ph&ograve;ng khu vực miền Trung v&agrave; T&acirc;y Nguy&ecirc;n.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, đại diện l&atilde;nh đạo hai b&ecirc;n đ&atilde; ch&uacute;c mừng sự ph&aacute;t triển tốt đẹp của mối quan hệ giữa VBI v&agrave; C&ocirc;ng ty CP Đầu tư Đ&egrave;o Cả qua hợp đồng vừa được k&yacute; kết v&agrave; tin tưởng v&agrave;o sự th&agrave;nh c&ocirc;ng của dự &aacute;n Hầm đường bộ Đ&egrave;o Cả.</span></p>\r\n<p><span style=\"font-family: Arial;\">Đứng đằng sau đảm bảo vững chắc cho VBI về năng lực bảo hiểm v&agrave; t&agrave;i ch&iacute;nh l&agrave; nh&agrave; T&aacute;i bảo hiểm h&agrave;ng đầu thế giới Swiss Re. Sự tin tưởng của kh&aacute;ch h&agrave;ng v&agrave;o VBI kh&ocirc;ng chỉ bởi sức mạnh t&agrave;i ch&iacute;nh m&agrave; bởi khả năng đ&aacute;p ứng c&aacute;c nhu cầu bảo hiểm v&agrave; kinh nghiệm giải quyết bồi thường.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>VBI</strong></span></p>', '2018-03-21 04:24:09', '2018-03-21 15:24:09', 'images/news/1520398921_pic-New-i_635380815368330000_HasThumb_Thumb.jpg.png'),
(80, 2, 'VBI đào tạo cấp chứng chỉ đại lý bảo hiểm', 'vbi-dao-tao-cap-chung-chi-dai-ly-bao-hiem', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Tuần đầu th&aacute;ng 4/2014 tại H&agrave; Nội, VBI v&agrave; Trường Đ&agrave;o tạo &amp;PTNNL VietinBank tổ chức đ&agrave;o tạo cấp chứng chỉ đại l&yacute; bảo hiểm cho c&aacute;n bộ chi nh&aacute;nh VietinBank khu vực Miền Bắc </span></span></p>', '<p><span style=\"font-family: Arial;\">Tuần đầu th&aacute;ng 4/2014 tại H&agrave; Nội, VBI v&agrave; Trường Đ&agrave;o tạo &amp;PTNNL VietinBank tổ chức đ&agrave;o tạo cấp chứng chỉ đại l&yacute; bảo hiểm cho c&aacute;n bộ chi nh&aacute;nh VietinBank khu vực Miền Bắc.</span></p>\r\n<p><img src=\"../../../uploads/635380818881040000.jpg\" alt=\"\" width=\"460\" height=\"193\" /></p>\r\n<p><span style=\"font-family: Arial;\">Kh&oacute;a đ&agrave;o đạo cấp chứng chỉ đại l&yacute; bảo hiểm phi nh&acirc;n thọ cho c&aacute;n bộ của c&aacute;c chi nh&aacute;nh VietinBank nhằm trang bị kiến thức cơ bản về bảo hiểm, luật kinh doanh bảo hiểm, c&aacute;c văn bản dưới luật về lĩnh vực bảo hiểm; c&aacute;c sản phẩm bảo hiểm phi nh&acirc;n thọ của Bảo hiểm VietinBank (VBI); kỹ năng b&aacute;n bảo hiểm;&hellip;</span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu khai mạc kh&oacute;a học, Ph&oacute; Tổng gi&aacute;m đốc VBI Trần Thị Kim Yến nhấn mạnh: Hướng tới một ng&acirc;n h&agrave;ng hiện đại, VietinBank cung cấp đa dạng c&aacute;c sản phẩm dịch vụ t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng cho kh&aacute;ch h&agrave;ng. Kh&aacute;ch h&agrave;ng ngo&agrave;i việc được cung cấp sản phẩm truyền thống như tiền gửi, tiền vay v&agrave; dịch vụ thanh to&aacute;n c&ograve;n được bảo vệ khỏi c&aacute;c rủi ro bất ngờ trong cuộc sống khi tham gia c&aacute;c hợp đồng bảo hiểm với mức ph&iacute; hợp l&yacute; do Ng&acirc;n h&agrave;ng v&agrave; doanh nghiệp bảo hiểm li&ecirc;n kết ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng&hellip; B&agrave; tin tưởng rằng, với kiến thức, kỹ năng của kh&oacute;a đ&agrave;o tạo, c&aacute;c học vi&ecirc;n sẽ vững tin tư vấn cho kh&aacute;ch h&agrave;ng khi trở về với c&ocirc;ng việc h&agrave;ng ng&agrave;y của m&igrave;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Sau kh&oacute;a đ&agrave;o tạo tại H&agrave; Nội, VBI tiếp tục kết hợp với Trường Đ&agrave;o tạo &amp;PTNNL tổ chức đ&agrave;o tạo đại l&yacute; bảo hiểm cho c&aacute;c chi nh&aacute;nh VietinBank miền Trung v&agrave; miền Nam.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Lan Oanh</em></span></p>', '2018-03-21 04:24:41', '2018-03-21 15:24:41', 'images/news/1520399000_pic-New-i_635380818887240000_HasThumb_Thumb.jpg.png'),
(81, 2, 'Đội bóng VBI tham dự Giải bóng đá VietinBank 2014', 'doi-bong-vbi-tham-du-giai-bong-da-vietinbank-2014', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">S&aacute;ng ng&agrave;y 06/04/2014, tại s&acirc;n b&oacute;ng Đại học Y H&agrave; Nội, đ&atilde; diễn ra trận cầu của v&ograve;ng loại Giải b&oacute;ng đ&aacute; VietinBank khu vực H&agrave; Nội năm 2014 giữa C&ocirc;ng ty Bảo hiểm VietinBank (VBI) v&agrave; C&ocirc;ng ty Quản l&yacute; nợ v&agrave; khai th&aacute;c t&agrave;i sản Ng&acirc;n h&agrave;ng VietinBank (AMC) </span></span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 06/04/2014, tại s&acirc;n b&oacute;ng Đại học Y H&agrave; Nội, đ&atilde; diễn ra trận cầu của v&ograve;ng loại Giải b&oacute;ng đ&aacute; VietinBank khu vực H&agrave; Nội năm 2014 giữa C&ocirc;ng ty Bảo hiểm VietinBank (VBI) v&agrave; C&ocirc;ng ty Quản l&yacute; nợ v&agrave; khai th&aacute;c t&agrave;i sản Ng&acirc;n h&agrave;ng VietinBank (AMC).</span></p>\r\n<p><img src=\"../../../uploads/635380819472520000.jpg\" alt=\"\" width=\"1280\" height=\"960\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\">(Đội h&igrave;nh ra qu&acirc;n của VBI trong trận cầu s&aacute;ng ng&agrave;y 06/04/2014)</span></p>\r\n<p><span style=\"font-family: Arial;\">Trận b&oacute;ng đ&atilde; cống hiến cho kh&aacute;n giả những gi&acirc;y ph&uacute;t s&ocirc;i động, gay cấn với những pha b&oacute;ng đẹp mắt, hấp dẫn bởi tinh thần fair-play của c&aacute;c cầu thủ cả hai đội.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&uacute;t thứ 15 hiệp 2, cầu thủ Huy Ho&agrave;ng (VBI) đ&atilde; thực hiện đường chuyền quyết định để cầu thủ Đức Thắng (VBI) ghi b&agrave;n thắng từ g&oacute;c xa khung th&agrave;nh ấn định tỷ số 1-0 cho VBI.</span></p>\r\n<p><span style=\"font-family: Arial;\">Việc tham gia v&agrave;o Giải b&oacute;ng đ&aacute; VietinBank 2014 kh&ocirc;ng chỉ g&oacute;p phần n&acirc;ng cao tinh thần thể thao, cải thiện sức khỏe cho c&aacute;n bộ VBI m&agrave; c&ograve;n l&agrave; hoạt động thiết thực g&oacute;p phần tăng cường tinh thần đo&agrave;n kết của VBI với c&aacute;c c&ocirc;ng ty/ ph&ograve;ng ban trong hệ thống VietinBank.</span></p>', '2018-03-21 04:25:12', '2018-03-21 15:25:12', 'images/news/1520399074_pic-New-i_635380819483200000_HasThumb_Thumb.JPG.png'),
(82, 2, 'VietinBankers giật giải “Ngôi sao Bancassurance”', 'vietinbankers-giat-giai-ngoi-sao-bancassurance', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Từ ng&agrave;y 24/3 đến 31/5/2014, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh &ldquo;Ng&ocirc;i sao Bancassurance&rdquo;. Li&ecirc;n tục trong 10 tuần, 10 c&aacute;n bộ c&oacute; doanh thu cao nhất sẽ nhận được giải thưởng tiền mặt 1.000.000 đồng </span></span></p>', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 24/3 đến 31/5/2014, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh <strong>&ldquo;Ng&ocirc;i sao Bancassurance&rdquo;</strong>. Li&ecirc;n tục trong 10 tuần, 10 c&aacute;n bộ c&oacute; doanh thu cao nhất sẽ nhận được<strong> giải thưởng tiền mặt 1.000.000 đồng</strong>. Đặc biệt hơn, thứ 5 h&agrave;ng tuần l&agrave; ng&agrave;y c&aacute;c VietinBankers c&oacute; thể tăng tốc với doanh số bảo hiểm được nh&acirc;n đ&ocirc;i để ghi nhận v&agrave;o kết quả thi đua chung của cả tuần.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ngay sau khi triển khai chương tr&igrave;nh, c&aacute;c VietinBankers tỏ ra rất h&agrave;o hứng với chương tr&igrave;nh v&agrave; doanh thu ph&iacute; bảo hiểm tăng l&ecirc;n theo từng ng&agrave;y. Sau 2 tuần triển khai, chương tr&igrave;nh đ&atilde; t&igrave;m ra được 2 c&aacute;n bộ xuất sắc l&agrave; <strong>anh Nguyễn Trọng Hiếu</strong> - PGD An Hữu VietinBank T&acirc;y Tiền Giang v&agrave; <strong>chị Nguyễn Thị Mộng Nhi&ecirc;n</strong> &ndash; Ph&ograve;ng Kh&aacute;ch h&agrave;ng doanh nghiệp VietinBank Nam S&agrave;i G&ograve;n nhận giải thưởng tuần trị gi&aacute; 1 triệu đồng.</span></p>\r\n<p><img src=\"../../../uploads/635380819784600000.jpg\" alt=\"\" width=\"700\" height=\"766\" /></p>\r\n<p><img src=\"../../../uploads/635380819786290000.png\" alt=\"\" width=\"576\" height=\"623\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>(H&igrave;nh ảnh 2 c&aacute;n bộ VietinBank xuất sắc nhận giải thưởng chương tr&igrave;nh)&nbsp;</em></span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>VBI l&agrave; C&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ đầu ti&ecirc;n tr&ecirc;n thị trường triển khai ứng dụng phần mềm b&aacute;n h&agrave;ng Bancassurance</strong>.Phần mềm Bancassurance l&agrave; một bước đột ph&aacute; của VBI trong việc n&acirc;ng cao h&igrave;nh ảnh, chất lượng dịch vụ tới kh&aacute;ch h&agrave;ng. Với c&aacute;c thao t&aacute;c đơn giản, quy tr&igrave;nh b&aacute;n h&agrave;ng th&ocirc;ng minh, phần mềm đ&atilde; nhận được nhiều phản hồi t&iacute;ch cực.</span></p>\r\n<p><span style=\"font-family: Arial;\">T&iacute;nh đến ng&agrave;y 10/04/2014, sau hơn 1 th&aacute;ng triển khai, doanh thu ph&iacute; bảo hiểm qua <strong>phần mềm đ&atilde; c&aacute;n mốc 1 tỷ đồng với 1033 đơn bảo hiểm</strong>. Để đạt được con số n&agrave;y, trong thời gian qua, VBI đ&atilde; tổ chức c&aacute;c lớp đ&agrave;o tạo phần mềm Bancassurance cho c&aacute;c c&aacute;n bộ VietinBank. Bước đột ph&aacute; của sản phẩm n&agrave;y ch&iacute;nh l&agrave; chỉ khoảng 2 ph&uacute;t, c&aacute;n bộ VietinBank sẽ ho&agrave;n tất thủ tục cấp đơn bảo hiểm cho kh&aacute;ch h&agrave;ng.</span></p>\r\n<p><span style=\"font-family: Arial;\">C&oacute; thể n&oacute;i VBI đ&atilde; x&acirc;y dựng th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh Bancassurance b&aacute;n bảo hiểm qua Ng&acirc;n h&agrave;ng với mạng lưới hơn 150 Chi nh&aacute;nh v&agrave; hơn 6000 c&aacute;n bộ b&aacute;n h&agrave;ng của VietinBank; cung cấp cho kh&aacute;ch h&agrave;ng dịch vụ t&agrave;i ch&iacute;nh trọn g&oacute;i một cửa: ng&acirc;n h&agrave;ng &ndash; bảo hiểm. </span></p>', '2018-03-21 04:25:47', '2018-03-21 15:25:47', 'images/news/1520410489_pic-New-i_635380819795540000_HasThumb_Thumb.jpg.png'),
(83, 2, 'VBI và VietinBank Đông Anh tỏa sáng từng đường bóng', 'vbi-va-vietinbank-dong-anh-toa-sang-tung-duong-bong', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Nằm trong khu&ocirc;n khổ Giải b&oacute;ng đ&aacute; VietinBank 2014, trận đầu thứ 2 v&ograve;ng loại của đội tuyển VBI đ&atilde; diễn ra s&aacute;ng ng&agrave;y 13/04/2014 </span></span></p>', '<p><span style=\"font-family: Arial;\">Nằm trong khu&ocirc;n khổ Giải b&oacute;ng đ&aacute; VietinBank 2014, trận đầu thứ 2 v&ograve;ng loại của đội tuyển VBI đ&atilde; diễn ra s&aacute;ng ng&agrave;y 13/04/2014. Đối thủ của VBI tại trận n&agrave;y l&agrave; đội tuyển VietinBank Đ&ocirc;ng Anh. Sau chiến thắng tại trận đầu ra qu&acirc;n, đội tuyển VBI ra s&acirc;n với tinh thần thoải m&aacute;i, tự tin để tiếp tục cống hiến cho kh&aacute;n giả những pha b&oacute;ng đẹp mắt.</span></p>\r\n<p><img src=\"../../../uploads/635380820013300000.jpg\" alt=\"\" width=\"1000\" height=\"788\" /></p>\r\n<p><span style=\"font-family: Arial;\">Ngay từ đầu trận, đội tuyển VBI đ&atilde; cống hiến những đường chuyền linh hoạt nhưng kh&ocirc;ng k&eacute;m phần kh&eacute;o l&eacute;o, k&egrave;m theo đ&oacute; l&agrave; những c&uacute; s&uacute;t căng về khung th&agrave;nh đối phương. Sau những ph&uacute;t đầu nhập cuộc đầy căng thẳng, đội b&oacute;ng VietinBank Đ&ocirc;ng Anh dần lấy lại thăng bằng, tấn c&ocirc;ng b&igrave;nh tĩnh, chắc chắn. Ch&iacute;nh v&igrave; vậy, kh&ocirc;ng kh&iacute; của hiệp 1 trận đấu diễn ra kh&aacute; căng thẳng khi cả hai đội đều ngang t&agrave;i, ngang sức.</span></p>\r\n<p><span style=\"font-family: Arial;\">Nhập cuộc hiệp 2, VBI dồn l&ecirc;n tấn c&ocirc;ng mạnh hơn v&agrave; đ&atilde; c&oacute; những pha b&oacute;ng nguy hiểm đối với khung th&agrave;nh VietinBank Đ&ocirc;ng Anh, VBI đ&atilde; &aacute;p thế trận hơn hẳn hiệp 1. Với lối chơi cống hiến, tấn c&ocirc;ng mạnh mẽ, VBI đ&atilde; c&oacute; nhiều cơ hội ghi b&agrave;n nhưng dường như may mắn đ&atilde; kh&ocirc;ng mỉm cười. Trận đấu kết th&uacute;c với tỷ số h&ograve;a 0 &ndash; 0.</span></p>\r\n<p><span style=\"font-family: Arial;\">Với lối chơi mạnh mẽ, đầy kỹ thuật, đội tuyển VBI cũng như VietinBank Đ&ocirc;ng Anh đang chứng tỏ m&igrave;nh l&agrave; những đối thủ đ&aacute;ng gờm với c&aacute;c đội b&oacute;ng trong giải đấu năm nay.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>Hồng Hạnh</strong></span></p>', '2018-03-21 04:26:13', '2018-03-21 15:26:13', 'images/news/1520410632_pic-New-i_635380820024400000_HasThumb_Thumb.JPG.png'),
(84, 2, 'VBI tạm ứng bồi thường lên tới 12 tỷ đồng cho Công ty xi măng Công Thanh', 'vbi-tam-ung-boi-thuong-len-toi-12-ty-dong-cho-cong-ty-xi-mang-cong-thanh', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 26/10/2013, VBI đ&atilde; chuyển 7 tỷ đồng tạm ứng cho doanh nghiệp để khắc phục hậu quả v&agrave; x&acirc;y dựng lại hệ thống cho kịp tiến độ sản xuất. Tiếp đ&oacute;, ng&agrave;y 10/04/2014, VBI tiếp tục chi trả số tiền bồi thường l&ecirc;n tới 5 tỷ đồng. </span></span></p>', '<p><span style=\"font-family: Arial;\">L&agrave; một trong những kh&aacute;ch h&agrave;ng tham gia bảo hiểm tại Bảo hiểm VietinBank (VBI), Nh&agrave; m&aacute;y Xi măng C&ocirc;ng Thanh được biết đến l&agrave; một trong những nh&agrave; m&aacute;y xi măng c&oacute; quy m&ocirc; lớn nhất Việt Nam. Nh&agrave; m&aacute;y gồm hai d&acirc;y chuyền sản xuất xi măng v&agrave; clinker hiện đại với diện t&iacute;ch hơn 100 h&eacute;cta. D&acirc;y chuyền số 2 đang trong qu&aacute; tr&igrave;nh thi c&ocirc;ng lắp đặt với c&ocirc;ng suất dự kiến đạt 3,6 triệu tấn clinker/năm với tổng số vốn 420 triệu USD (khoảng 7.600 tỷ Việt Nam đồng). Dự kiến sau khi đưa d&acirc;y chuyền thứ 2 v&agrave;o hoạt động, Nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh sẽ l&agrave; một trong những nh&agrave; m&aacute;y sản xuất xi măng lớn nhất Ch&acirc;u &Aacute;.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong khoảng thời gian từ ng&agrave;y 28/09 - 01/10/2013, do ảnh hưởng của cơn b&atilde;o số 10, d&acirc;y chuyền số 2 của nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh đ&atilde; bị ngập s&acirc;u với mực nước l&ecirc;n đến 1m khiến c&aacute;c hạng mục thi c&ocirc;ng bị thiệt hại nặng: c&ocirc;ng tr&igrave;nh bảo vệ m&aacute;i dốc bị sạt lở, taluy đ&agrave;o của đường mỏ s&eacute;t bị sạt trượt, to&agrave;n bộ mặt đường v&agrave; mương tho&aacute;t nước bằng đ&aacute; hộc đ&atilde; bị lũ cuốn tr&ocirc;i, c&aacute;c hạng mục thiết bị đ&atilde; lắp đặt bị ngập trong nước v&agrave; b&ugrave;n đất,&hellip;</span></p>\r\n<p><img src=\"../../../uploads/635380820302040000.jpg\" alt=\"\" width=\"627\" height=\"443\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>(H&igrave;nh ảnh ngập lụt tại Nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh)</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Ng&agrave;y 26/10/2013, chưa đầy 1 th&aacute;ng sau khi nhận được th&ocirc;ng b&aacute;o thiệt hại từ nh&agrave; m&aacute;y, VBI đ&atilde; chuyển 7 tỷ đồng tạm ứng cho doanh nghiệp để khắc phục hậu quả v&agrave; x&acirc;y dựng lại hệ thống cho kịp tiến độ sản xuất.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tiếp đ&oacute;, ng&agrave;y 10/04/2014, ngay khi nhận được đ&aacute;nh gi&aacute; từ C&ocirc;ng ty gi&aacute;m định độc lập Crawford, VBI tiếp tục chi trả số tiền bồi thường l&ecirc;n tới 5 tỷ đồng. Như vậy, tổng số tiền bồi thường C&ocirc;ng ty xi măng C&ocirc;ng Thanh đ&atilde; được tạm ứng đến thời điểm n&agrave;y đ&atilde; l&ecirc;n tới 12 tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">C&oacute; thể n&oacute;i, việc chi trả bồi thường kịp thời kh&ocirc;ng chỉ gi&uacute;p doanh nghiệp tiếp tục an t&acirc;m sản xuất m&agrave; c&ograve;n khẳng định uy t&iacute;n của VBI tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</span></p>', '2018-03-21 04:26:51', '2018-03-21 15:26:51', 'images/news/1520410713_congthanh_635392864993200000_HasThumb_Thumb.jpg.png'),
(85, 2, 'VBI tổ chức đào tạo kỹ năng bán bảo hiểm tại VietinBank Ba Đình', 'vbi-to-chuc-dao-tao-ky-nang-ban-bao-hiem-tai-vietinbank-ba-dinh', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 25/04/2014 tại H&agrave; Nội, VBI phối hợp với VietinBank Chi nh&aacute;nh Ba Đ&igrave;nh tổ chức kh&oacute;a &ldquo;Đ&agrave;o tạo sản phẩm bảo hiểm v&agrave; kỹ năng b&aacute;n h&agrave;ng&rdquo; cho 50 c&aacute;n bộ l&agrave; l&atilde;nh đạo c&aacute;c ph&ograve;ng kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n của Chi nh&aacute;nh </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 25/04/2014 tại H&agrave; Nội, VBI phối hợp với VietinBank Chi nh&aacute;nh Ba Đ&igrave;nh tổ chức kh&oacute;a &ldquo;Đ&agrave;o tạo sản phẩm bảo hiểm v&agrave; kỹ năng b&aacute;n h&agrave;ng&rdquo; cho 50 c&aacute;n bộ l&agrave; l&atilde;nh đạo c&aacute;c ph&ograve;ng kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n của Chi nh&aacute;nh. Tham dự lớp đ&agrave;o tại c&oacute; đại diện Ban Gi&aacute;m đốc VietinBank Ba Đ&igrave;nh.</span></p>\r\n<p><span style=\"font-family: Arial;\">Với mục ti&ecirc;u tăng cường kiến thức, v&agrave; kỹ năng b&aacute;n h&agrave;ng trong hệ thống VietinBank, VBI đ&atilde; đưa ra chương tr&igrave;nh đ&agrave;o tạo với nội dung phong ph&uacute; bao gồm: Kiến thức bảo hiểm cơ bản; Bảo hiểm &ocirc; t&ocirc; VietinCar; Bảo hiểm con người VietinCare; Bảo hiểm nh&agrave; tư nh&acirc;n VietinHome; Bảo hiểm Xe m&aacute;y, Quy tr&igrave;nh cấp bảo hiểm tr&ecirc;n phần mềm Bancassurance v&agrave; Kỹ năng b&aacute;n bảo hiểm hiệu quả.</span></p>\r\n<p><img src=\"../../../uploads/635380820508210000.jpg\" alt=\"\" width=\"1207\" height=\"574\" /></p>\r\n<p><span style=\"font-family: Arial;\">Th&ocirc;ng qua buổi đ&agrave;o tạo n&agrave;y, b&ecirc;n cạnh việc được hiểu r&otilde; hơn về c&aacute;c sản phẩm li&ecirc;n kết ng&acirc;n h&agrave;ng &ndash; bảo hiểm, c&aacute;c c&aacute;n bộ VietinBank Chi nh&aacute;nh Ba Đ&igrave;nh c&ograve;n được c&aacute;c giảng vi&ecirc;n VBI chia sẻ những kiến thức rất bổ &iacute;ch về kỹ năng b&aacute;n bảo hiểm, c&aacute;c bước trong quy tr&igrave;nh b&aacute;n bảo hiểm qua phần mềm Bancassurance v&agrave; kỹ năng x&acirc;y dựng quan hệ với kh&aacute;ch h&agrave;ng th&ocirc;ng qua c&aacute;c t&igrave;nh huống thực tế.</span></p>\r\n<p><span style=\"font-family: Arial;\">Buổi đ&agrave;o tạo đ&atilde; nhận được sự tham gia s&ocirc;i nổi v&agrave; h&agrave;o hứng của c&aacute;c c&aacute;n bộ VietinBank Ba Đ&igrave;nh. C&aacute;c c&aacute;n bộ cũng chia sẻ: đ&acirc;y l&agrave; những sản phẩm bảo hiểm đơn giản, thiết thực v&agrave; dễ tiếp cận với c&aacute;c kh&aacute;ch h&agrave;ng tới giao dịch tại Ng&acirc;n h&agrave;ng. Với những kỹ năng được học ng&agrave;y h&ocirc;m nay, ch&uacute;ng t&ocirc;i sẽ đẩy mạnh việc ch&agrave;o b&aacute;n sản phẩm n&agrave;y cho c&aacute;c kh&aacute;ch h&agrave;ng trong thời gian tới.</span></p>\r\n<p><span style=\"font-family: Arial;\">Từ đầu năm tới nay, VBI đ&atilde; t&iacute;ch cực tổ chức c&aacute;c kh&oacute;a đ&agrave;o tạo về kiến thức v&agrave; kỹ năng b&aacute;n bảo hiểm cho hơn 20 Chi nh&aacute;nh Ng&acirc;n h&agrave;ng VietinBank, g&oacute;p phần thực hiện chủ trương chung của Ng&acirc;n h&agrave;ng về việc đẩy mạnh hoạt động b&aacute;n lẻ.</span></p>', '2018-03-21 04:27:24', '2018-03-21 15:27:24', 'images/news/1520410790_pic-New-i_635380820512730000_HasThumb_Thumb.JPG.png'),
(86, 2, 'Giảm nỗi lo cháy nổ với Bảo hiểm VietinBank', 'giam-noi-lo-chay-no-voi-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Từ đầu năm 2014 đến nay đ&atilde; xảy ra h&agrave;ng loạt vụ ch&aacute;y lớn, c&oacute; thể kể tới vụ ch&aacute;y tại C&ocirc;ng ty h&oacute;a chất ở Khu c&ocirc;ng nghiệp L&ecirc; Minh Xu&acirc;n (TP HCM) ng&agrave;y 17/04, ch&aacute;y Khu c&ocirc;ng nghiệp Vĩnh Tuy (H&agrave; Nội) ng&agrave;y 14/04, ch&aacute;y kho vật tư Nh&agrave; m&aacute;y nước H&agrave; Tĩnh ng&agrave;y 10/04, ch&aacute;y chợ Phố Hiến (Hưng Y&ecirc;n)... </span></span></p>', '<p><span style=\"font-family: Arial;\">Từ đầu năm 2014 đến nay đ&atilde; xảy ra h&agrave;ng loạt vụ ch&aacute;y lớn, c&oacute; thể kể tới vụ ch&aacute;y tại C&ocirc;ng ty h&oacute;a chất ở Khu c&ocirc;ng nghiệp L&ecirc; Minh Xu&acirc;n (TP HCM) ng&agrave;y 17/04, ch&aacute;y Khu c&ocirc;ng nghiệp Vĩnh Tuy (H&agrave; Nội) ng&agrave;y 14/04, ch&aacute;y kho vật tư Nh&agrave; m&aacute;y nước H&agrave; Tĩnh ng&agrave;y 10/04, ch&aacute;y chợ Phố Hiến (Hưng Y&ecirc;n) ng&agrave;y 19/03,&hellip;Những vụ hỏa hoạn tr&ecirc;n đ&atilde; g&acirc;y thiệt hại lớn về người v&agrave; của cho c&aacute;c doanh nghiệp. B&ecirc;n cạnh việc quan t&acirc;m hơn tới c&ocirc;ng t&aacute;c ph&ograve;ng chống ch&aacute;y nổ, một giải ph&aacute;p nữa cho doanh nghiệp để giảm thiểu rủi ro về t&agrave;i ch&iacute;nh l&agrave; tham gia bảo hiểm ch&aacute;y nổ. Khi xảy ra c&aacute;c vụ hỏa hoạn, c&ocirc;ng ty bảo hiểm sẽ hỗ trợ doanh nghiệp b&ugrave; đắp c&aacute;c tổn thất về t&agrave;i sản.</span></p>\r\n<p><img src=\"../../../uploads/635380820842330000.jpg\" alt=\"\" width=\"430\" height=\"293\" /></p>\r\n<p><span style=\"font-family: Arial;\">Tr&ecirc;n thị trường bảo hiểm hiện nay, Bảo hiểm Vietinbank (VBI) đ&atilde; cung cấp đầy đủ những sản phẩm bảo hiểm t&agrave;i sản để đ&aacute;p ứng nhu cầu của thị trường như: bảo hiểm ch&aacute;y nổ bắt buộc theo quy định của Ch&iacute;nh phủ, bảo hiểm ch&aacute;y v&agrave; c&aacute;c rủi ro đặc biệt, bảo hiểm mọi rủi ro t&agrave;i sản. Đặc biệt, VBI c&ograve;n phối hợp với Vietinbank để x&acirc;y dựng chương tr&igrave;nh bảo hiểm t&agrave;i sản d&agrave;nh ri&ecirc;ng cho c&aacute;c Hộ tiểu thương c&oacute; tham gia vay vốn tại Ng&acirc;n h&agrave;ng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Năm 2013, doanh thu ph&iacute; bảo hiểm ch&aacute;y nổ v&agrave; mọi rủi ro t&agrave;i sản to&agrave;n thị trường l&agrave; 1,768 tỷ, bồi thường 894 tỷ, tỷ lệ bồi thường l&agrave; 50%. Tại VBI, doanh thu ph&iacute; bảo hiểm t&agrave;i sản l&agrave; 36 tỷ, tổng số tiền bồi thường l&agrave; 17 tỷ, tỷ lệ bồi thường l&agrave; 47%. Trong suốt thời gian qua, VBI đ&atilde; tiến h&agrave;nh chi trả một số vụ tổn thất lớnli&ecirc;n quan đến hỏa hoạn cho c&aacute;c kh&aacute;ch h&agrave;ng vay vốn tại Vietinbank, điển h&igrave;nh l&agrave; vụ ch&aacute;y C&ocirc;ng ty bao b&igrave; Nhơn Trạch với số tiền bồi thường l&ecirc;n tới 62 tỷ đồng, tạm ứng vụ ch&aacute;y băng tải tại C&ocirc;ng ty xi măng C&ocirc;ng Thanh l&ecirc;n tới 12 tỷ đồng (dự kiến tổng tổn thất l&ecirc;n tới 14 tỷ), vụ ch&aacute;y tại C&ocirc;ng ty PNP với số tiền bồi thường gần 9 tỷ.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong sự ph&aacute;t triển của thị trường bảo hiểm n&oacute;i chung v&agrave; bảo hiểm hỏa hoạn n&oacute;i ri&ecirc;ng tại Việt Nam, Bảo hiểm Vietinbank với tư c&aacute;ch l&agrave; một đơn vị cung cấp bảo hiểm uy t&iacute;n đ&atilde; đang v&agrave; sẽ l&agrave; chỗ dựa t&acirc;m l&yacute; vững chắc, đảm bảo an to&agrave;n t&agrave;i ch&iacute;nh cho kh&aacute;ch h&agrave;ng.</span></p>', '2018-03-21 04:27:53', '2018-03-21 15:27:53', 'images/news/1520410857_VietinBan_635392909042920000_HasThumb_Thumb.jpg.png'),
(87, 2, 'VBI bồi thường hơn 4 tỷ đồng cho Công ty Cổ phần thức ăn thủy sản Hùng Cá', 'vbi-boi-thuong-hon-4-ty-dong-cho-cong-ty-co-phan-thuc-an-thuy-san-hung-ca', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Nhằm hỗ trợ kh&aacute;ch h&agrave;ng nhanh ch&oacute;ng khắc phục tổn thất v&agrave; t&aacute;i hoạt động sản xuất, VBI đ&atilde; tạm ứng bồi thường cho C&ocirc;ng ty H&ugrave;ng C&aacute; 1,7 tỷ đồng, tiếp sau đ&oacute;, ng&agrave;y 12/05/2014 VBI bồi thường số tiền c&ograve;n lại l&agrave; 2,6 tỷ đồng. Như vậy, tổng số tiền bồi thường C&ocirc;ng ty H&ugrave;ng C&aacute; nhận được l&ecirc;n t... </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 18/04/2013, C&ocirc;ng ty Cổ phần thức ăn thủy sản H&ugrave;ng C&aacute; (viết tắt l&agrave; C&ocirc;ng ty H&ugrave;ng C&aacute;) &ndash; kh&aacute;ch h&agrave;ng th&acirc;n thiết của Ng&acirc;n h&agrave;ng C&ocirc;ng thương đ&atilde; tham gia Bảo hiểm mọi rủi ro t&agrave;i sản của Bảo hiểm VietinBank (VBI). Tới ng&agrave;y 29/05/2013, tại khu vực cụm c&ocirc;ng nghiệp Thanh B&igrave;nh nơi C&ocirc;ng ty H&ugrave;ng C&aacute; đặt nh&agrave; xưởng, mưa gi&ocirc;ng k&egrave;m theo gi&oacute; lốc, sấm s&eacute;t đ&atilde; xuất hiện g&acirc;y hư hỏng m&aacute;y m&oacute;c v&agrave; nh&agrave; xưởng c&ocirc;ng ty.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ngay sau khi nhận được th&ocirc;ng b&aacute;o tổn thất từ C&ocirc;ng ty H&ugrave;ng C&aacute;, VBI đ&atilde; nhanh ch&oacute;ng tới ngay hiện trường v&agrave; mời C&ocirc;ng ty gi&aacute;m định v&agrave; tư vấn kỹ thuật Raco x&aacute;c định thiệt hại của C&ocirc;ng ty H&ugrave;ng C&aacute;. Kết quả gi&aacute;m định thiệt hại như sau: Nh&agrave; xưởng chế biến bột c&aacute; v&agrave; nh&agrave; l&ograve; hơi bị sập đổ ho&agrave;n to&agrave;n, m&aacute;i tole bị cong v&ecirc;nh, khung k&egrave;o th&eacute;p bị vặn xoắn; d&acirc;y chuyền chế biến bột c&aacute; v&agrave; l&ograve; hơi bị ảnh hưởng do nước mưa; d&acirc;y chuyền chế biến thức ăn thủy sản, bao gồm: hệ thống sấy, hệ thống lọc bụi, hệ thống &eacute;p đ&ugrave;n,v.v&hellip;kh&ocirc;ng hoạt động.</span></p>\r\n<p><img src=\"../../../uploads/635380821614990000.jpg\" alt=\"\" width=\"1024\" height=\"576\" /></p>\r\n<p><img src=\"../../../uploads/635380821618650000.jpg\" alt=\"\" width=\"1024\" height=\"576\" /></p>\r\n<p><img src=\"../../../uploads/635380821627810000.jpg\" alt=\"\" width=\"1024\" height=\"576\" /></p>\r\n<p><img src=\"../../../uploads/635380821632900000.jpg\" alt=\"\" width=\"1024\" height=\"576\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>(H&igrave;nh ảnh thiệt hại tại C&ocirc;ng ty Cổ phần thức ăn thủy sản H&ugrave;ng C&aacute;)</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Nhằm hỗ trợ kh&aacute;ch h&agrave;ng nhanh ch&oacute;ng khắc phục tổn thất v&agrave; t&aacute;i hoạt động sản xuất, VBI đ&atilde; tạm ứng bồi thường cho C&ocirc;ng ty H&ugrave;ng C&aacute; 1,7 tỷ đồng, tiếp sau đ&oacute;, ng&agrave;y 12/05/2014 VBI bồi thường số tiền c&ograve;n lại l&agrave; 2,6 tỷ đồng. Như vậy, tổng số tiền bồi thường C&ocirc;ng ty H&ugrave;ng C&aacute; nhận được l&ecirc;n tới 4,3 tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong thời gian qua, VBI đ&atilde; nỗ lực bảo to&agrave;n t&agrave;i sản cho C&ocirc;ng ty H&ugrave;ng C&aacute; n&oacute;i ri&ecirc;ng v&agrave; tất cả c&aacute;c kh&aacute;ch h&agrave;ng của VBI n&oacute;i chung để mang lại sự an t&acirc;m, tin tưởng cho kh&aacute;ch h&agrave;ng khi xảy ra rủi ro.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong><em>Nguyễn Hồng Hạnh</em></strong></span></p>', '2018-03-21 04:29:10', '2018-03-21 15:29:10', 'images/news/1520410983_hung-c_635405240089350000_HasThumb_Thumb.jpg.png'),
(88, 2, 'Ưu đãi bảo hiểm VBI khi gửi tiền tại VietinBank', 'uu-dai-bao-hiem-vbi-khi-gui-tien-tai-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Từ ng&agrave;y 02/06/2014 đến hết ng&agrave;y 14/06/2014, khi tham gia chương tr&igrave;nh khuyến mại tiền gửi &ldquo;Rạng rỡ ng&agrave;y h&egrave; &ndash; Gửi tiền tr&uacute;ng lớn&rdquo;, kh&aacute;ch h&agrave;ng c&oacute; cơ hội được nhận Bảo hiểm An to&agrave;n C&aacute; nh&acirc;n của Bảo hiểm VietinBank (VBI). </span></span></p>', '<p><img src=\"../../../uploads/rang ro ngay he-02.jpg\" alt=\"\" width=\"1366\" height=\"429\" /></p>\r\n<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 02/06/2014 đến hết ng&agrave;y 14/06/2014, khi tham gia chương tr&igrave;nh khuyến mại tiền gửi &ldquo;Rạng rỡ ng&agrave;y h&egrave; &ndash; Gửi tiền tr&uacute;ng lớn&rdquo;, kh&aacute;ch h&agrave;ng c&oacute; cơ hội được nhận Bảo hiểm An to&agrave;n C&aacute; nh&acirc;n của Bảo hiểm VietinBank (VBI). V&agrave; <strong>ưu đ&atilde;i giảm 25% ph&iacute; bảo hiểm</strong> khi mua một trong c&aacute;c sản phẩm bảo hiểm &Ocirc;t&ocirc; VietinCar, Bảo hiểm Nh&agrave; tư nh&acirc;n VietinHome, Bảo hiểm con người VietinCare v&agrave; Bảo hiểm Du học. B&ecirc;n cạnh đ&oacute;, kh&aacute;ch h&agrave;ng mua bảo hiểm &ocirc;t&ocirc; VietinCar c&ograve;n được nhận ngay 01 đ&ocirc;i tấm chắn nắng cho &ocirc;t&ocirc;.</span></p>\r\n<p><span style=\"font-family: Arial;\">Đối với c&aacute;c ưu đ&atilde;i tiền gửi, kh&aacute;ch h&agrave;ng sẽ được hưởng 03 cơ hội tr&uacute;ng thưởng hấp dẫn gồm: Quay số tr&uacute;ng thưởng cuối chương tr&igrave;nh với 1.081 giải thưởng, trong đ&oacute; giải đặc biệt l&agrave; 01 &ocirc;t&ocirc; Honda City 1.5L 5AT v&agrave; nhiều sản phẩm c&oacute; gi&aacute; trị kh&aacute;c với tổng trị gi&aacute; l&ecirc;n đến tr&ecirc;n 1 tỷ đồng; 110.000 thẻ c&agrave;o 100% tr&uacute;ng thưởng với mệnh gi&aacute; thẻ c&agrave;o tối đa l&agrave; 500.000 đồng; V&agrave; 15.000 qu&agrave; tặng trao ngay l&agrave; c&aacute;c bộ sản phẩm đồ nhựa cao cấp mang thương hiệu Song Long từ ng&agrave;y 02/06/2014 đến hết ng&agrave;y 02/07/2014.</span></p>\r\n<p><span style=\"font-family: Arial;\">Cơ cấu quay số tr&uacute;ng thưởng cuối chương tr&igrave;nh như sau:</span></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"115\">\r\n<p>iải</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p>Nội dung</p>\r\n</td>\r\n<td valign=\"top\" width=\"114\">\r\n<p>Số lượng</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"115\">\r\n<p>Đặc biệt</p>\r\n</td>\r\n<td width=\"390\">\r\n<p>&Ocirc; t&ocirc; Honda City 1.5L 5AT</p>\r\n</td>\r\n<td valign=\"top\" width=\"114\">\r\n<p>1</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"115\">\r\n<p>Nhất</p>\r\n</td>\r\n<td width=\"390\">\r\n<p>Tủ lạnh LG GR-C362MG</p>\r\n</td>\r\n<td valign=\"top\" width=\"114\">\r\n<p>10</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"115\">\r\n<p>Nh&igrave;</p>\r\n</td>\r\n<td width=\"390\">\r\n<p>M&aacute;y giặt lồng đứng LG MAGILG0073 (WF-S8419FS)</p>\r\n</td>\r\n<td valign=\"top\" width=\"114\">\r\n<p>20</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"115\">\r\n<p>Ba</p>\r\n</td>\r\n<td width=\"390\">\r\n<p>L&ograve; vi s&oacute;ng LG MH6042D</p>\r\n</td>\r\n<td valign=\"top\" width=\"114\">\r\n<p>50</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"115\">\r\n<p>Khuyến kh&iacute;ch</p>\r\n</td>\r\n<td width=\"390\">\r\n<p>Tiền mặt</p>\r\n</td>\r\n<td valign=\"top\" width=\"114\">\r\n<p>1.000</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><span style=\"font-family: Arial;\">Điều kiện để tham gia chương tr&igrave;nh hết sức đơn giản. Kh&aacute;ch h&agrave;ng chỉ cần đ&aacute;p ứng điều kiện l&agrave; c&aacute; nh&acirc;n người Việt Nam v&agrave; nước ngo&agrave;i đang sinh sống v&agrave; hoạt động hợp ph&aacute;p tại Việt Nam, gửi tiền tiết kiệm bằng VND hoặc USD kỳ hạn 01, 02, 03, 06, 09 v&agrave; 12 th&aacute;ng l&atilde;i suất cố định.</span></p>\r\n<p><span style=\"font-family: Arial;\">Để biết th&ecirc;m th&ocirc;ng tin chi tiết, vui l&ograve;ng li&ecirc;n hệ Chi nh&aacute;nh/Ph&ograve;ng giao dịch VietinBank gần nhất hoặc tổng đ&agrave;i 1900558868.</span></p>\r\n<p><span style=\"font-family: Arial;\">H&atilde;y tham gia chương tr&igrave;nh ngay từ ng&agrave;y h&ocirc;m nay để được nhận ưu đ&atilde;i hấp dẫn nhất từ chương tr&igrave;nh.</span></p>', '2018-03-21 04:29:38', '2018-03-21 15:29:38', 'images/news/1520411266_rang-ro-n_635405238676950000_HasThumb_Thumb.jpg.png'),
(89, 2, 'Bảo hiểm VietinBank chi trả gần 1 tỷ đồng cho khách hàng tham gia bảo hiểm cây cao su', 'bao-hiem-vietinbank-chi-tra-gan-1-ty-dong-cho-khach-hang-tham-gia-bao-hiem-cay-cao-su', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 10/06/2014 vừa qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả tiền bảo hiểm cho kh&aacute;ch h&agrave;ng Đồng Minh To&agrave;n (kh&aacute;ch h&agrave;ng vay vốn tại Vietinbank &ndash; CN Đ&ocirc;ng S&agrave;i G&ograve;n), sau 2 vụ tổn thất c&acirc;y cao su li&ecirc;n tiếp xảy ra với số tiền l&ecirc;n tới gần 1 tỷ đồng </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 10/06/2014 vừa qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả tiền bảo hiểm cho kh&aacute;ch h&agrave;ng Đồng Minh To&agrave;n (kh&aacute;ch h&agrave;ng vay vốn tại Vietinbank &ndash; CN Đ&ocirc;ng S&agrave;i G&ograve;n), sau 2 vụ tổn thất c&acirc;y cao su li&ecirc;n tiếp xảy ra với số tiền l&ecirc;n tới gần 1 tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Cụ thể, vụ tổn thất thứ nhất xảy ra v&agrave;o 17h30 ph&uacute;t ng&agrave;y 01/04/2014, tại B&igrave;nh Phước, mưa gi&ocirc;ng k&egrave;m theo gi&ocirc;ng lốc k&eacute;o d&agrave;i gần 2 tiếng đ&atilde; l&agrave;m vườn c&acirc;y cao su của anh To&agrave;n bị g&atilde;y đổ. Tiếp theo đ&oacute;, vụ tổn thất thứ hai với nguy&ecirc;n nh&acirc;n tương tự xảy ra v&agrave;o khoảng thời gian từ 17h30 đến 18h00 ng&agrave;y 10/04/2014. Do đặc điểm c&acirc;y cao su rất d&ograve;n, rễ n&ocirc;ng, khả năng chịu gi&oacute; k&eacute;m n&ecirc;n khi gặp gi&ocirc;ng lốc sẽ bị g&atilde;y c&agrave;nh, g&atilde;y ngang th&acirc;n c&acirc;y hoặc bật gốc.</span></p>\r\n<p><img src=\"../../../uploads/635400693668810000.jpg\" alt=\"\" width=\"800\" height=\"600\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>(H&igrave;nh ảnh hiện trường tổn thất tại vườn c&acirc;y cao su)</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Ngay khi nhận được th&ocirc;ng tin tổn thất từ anh To&agrave;n, c&aacute;n bộ VBI Hồ Ch&iacute; Minh đ&atilde; nhanh ch&oacute;ng tới hiện trường tổn thất để gi&aacute;m định thiệt hại v&agrave; kiểm đếm to&agrave;n bộ số lượng c&acirc;y cao su bị g&atilde;y đổ. Sau khi c&oacute; kết quả gi&aacute;m định, anh To&agrave;n đ&atilde; nhận được số tiển 436.064.000 đồng cho tổn thất lần 1 v&agrave; 453.977.000 đồng cho tổn thất lần 2. Như vậy, tổng số tiền anh To&agrave;n nhận được từ Bảo hiểm VietinBank (VBI) sau hai vụ tổn thất l&ecirc;n tới gần 1 tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Việc chi trả bồi thường nhanh ch&oacute;ng đ&atilde; gi&uacute;p anh To&agrave;n nhanh ch&oacute;ng khắc phục tổn thất v&agrave; t&aacute;i hoạt động kinh doanh. Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; kh&ocirc;ng ngừng cải tiến dịch vụ v&agrave; n&acirc;ng cao chất lượng bồi thường để mang lại sự an t&acirc;m, tin tưởng tuyệt đối cho kh&aacute;ch h&agrave;ng khi tham gia c&aacute;c sản phẩm bảo hiểm của VBI.</span></p>', '2018-03-21 04:30:27', '2018-03-21 15:30:27', 'images/news/1520411391_c_635405233222590000_HasThumb_Thumb.jpg.png'),
(90, 2, 'VBI được tôn vinh là doanh nghiệp nộp thuế tiêu biểu 2013', 'vbi-duoc-ton-vinh-la-doanh-nghiep-nop-thue-tieu-bieu-2013', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Theo đ&aacute;nh gi&aacute; của Cục thuế TP H&agrave; Nội, năm 2013 l&agrave; một năm c&oacute; nhiều kh&oacute; khăn, th&aacute;ch thức. Vượt l&ecirc;n những kh&oacute; khăn đ&oacute;, Bảo hiểm VietinBank (VBI) đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ đ&oacute;ng g&oacute;p cho Ng&acirc;n s&aacute;ch Nh&agrave; nước </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 17/06, Bảo hiểm VietinBank (VBI) được Cục Thuế TP H&agrave; Nội khen thưởng trong Hội nghị tuy&ecirc;n dương tổ chức, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch kinh doanh hiệu quả v&agrave; thực hiện tốt nghĩa vụ thuế. Năm 2013, Bảo hiểm VietinBank (VBI) đạt doanh thu hơn 174 tỷ đồng, lợi nhuận trước thuế 48 tỷ đồng, đ&oacute;ng g&oacute;p v&agrave;o ng&acirc;n s&aacute;ch Nh&agrave; nước 25 tỷ đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Theo đ&aacute;nh gi&aacute; của Cục thuế TP H&agrave; Nội, năm 2013 l&agrave; một năm c&oacute; nhiều kh&oacute; khăn, th&aacute;ch thức. Vượt l&ecirc;n những kh&oacute; khăn đ&oacute;, Bảo hiểm VietinBank (VBI) đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ đ&oacute;ng g&oacute;p cho Ng&acirc;n s&aacute;ch Nh&agrave; nước.</span></p>\r\n<p><img src=\"../../../uploads/635400694054310000.jpg\" alt=\"\" width=\"695\" height=\"467\" /></p>\r\n<p><span style=\"font-family: Arial;\">Cục trưởng Cục Thuế TP H&agrave; Nội &ndash; Phi V&acirc;n Tuấn nhấn mạnh: Trong bối cảnh kh&oacute; khăn, nhiều doanh nghiệp đ&atilde; kh&ocirc;ng ngừng t&igrave;m t&ograve;i hướng đi đ&uacute;ng đắn, vượt qua kh&oacute; khăn, duy tr&igrave; hoạt động sản xuất kinh doanh, v&agrave; trở th&agrave;nh những \"điểm s&aacute;ng\" trong c&aacute;c phong tr&agrave;o thi đua y&ecirc;u nước, trong đ&oacute; c&oacute; phong tr&agrave;o \"Thực hiện tốt ch&iacute;nh s&aacute;ch ph&aacute;p luật thuế năm 2013\". Ng&agrave;y nay, uy t&iacute;n của doanh nghiệp được bảo đảm kh&ocirc;ng chỉ từ việc kinh doanh hiệu quả, m&agrave; c&ograve;n từ việc ho&agrave;n th&agrave;nh c&aacute;c nghĩa vụ đối với Nh&agrave; nước, trong đ&oacute; c&oacute; nghĩa vụ thuế.</span></p>', '2018-03-21 04:31:00', '2018-03-21 15:31:00', 'images/news/1520411448_pic-New-i_635400694057190000_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(91, 2, 'Nhận ngay giải thưởng hấp dẫn từ chương trình \"Ngôi sao Bancassurance\"', 'nhan-ngay-giai-thuong-hap-dan-tu-chuong-trinh-ngoi-sao-bancassurance', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Từ ng&agrave;y 30/05 đến ng&agrave;y 28/12/2014, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh thi đua d&agrave;nh cho c&aacute;c c&aacute;n bộ VietinBank tư vấn v&agrave; cấp đơn bảo hiểm qua phần mềm Bancassurance. </span></span></p>', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y <strong>30/05 đến ng&agrave;y 28/12/2014</strong>, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh thi đua d&agrave;nh cho c&aacute;c c&aacute;n bộ VietinBank tư vấn v&agrave; cấp đơn bảo hiểm qua phần mềm Bancassurance.Trong 26 tuần diễn ra chương tr&igrave;nh, c&aacute;c c&aacute;n bộ VieitnBank c&oacute; cơ hội nhận được nhiều phần qu&agrave; hấp dẫn với <strong>tổng gi&aacute; trị giải thưởng l&ecirc;n tới hơn 200 triệu đồng.</strong></span></p>\r\n<p><span style=\"font-family: Arial;\">Tổng trị gi&aacute; giải thưởng tuần của chương tr&igrave;nh l&agrave; 104 triệu đồng. Mỗi tuần, giải thưởng 1 triệu đồng sẽ được trao choc&aacute;c c&aacute;n bộ VietinBank cấp đơn xuất sắc nhất đạt danh hiệu <strong>&ldquo;Ng&ocirc;i sao Bancassurance&rdquo;,&ldquo;Ng&ocirc;i sao VietinHome&rdquo;, &ldquo;Ng&ocirc;i sao VietinCare&rdquo;, &ldquo;Ng&ocirc;i sao VietinCar&rdquo;.</strong></span></p>\r\n<p><img src=\"../../../uploads/Poster Bancassurance.jpg\" alt=\"\" width=\"1024\" height=\"1200\" /></p>\r\n<p><span style=\"font-family: Arial;\">Đặc biệt, c&aacute;c c&aacute;n bộ xếp thứ nh&igrave; v&agrave; ba trong bảng xếp hạng &ldquo;Ng&ocirc;i sao Bancassurance&rdquo;, &ldquo;Ng&ocirc;i sao VietinCare&rdquo;, &ldquo;Ng&ocirc;i sao VietinCar&rdquo;, &ldquo;Ng&ocirc;i sao VietinHome&rdquo; của mỗi tuần sẽ được<strong> tham gia bốc thăm may mắn</strong> nhiều phần qu&agrave; hấp dẫn với tổng gi&aacute; trị giải thưởng l&ecirc;n tới 15 triệu đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">B&ecirc;n cạnh đ&oacute;, chương tr&igrave;nh &ldquo;Ng&ocirc;i sao Bancassurance&rdquo; sẽ l&agrave; cuộc đua đầy cam go m&agrave; c&aacute;c c&aacute;n bộ VietinBank phải vượt qua để đạt giải thưởng&ldquo;N<strong>g&ocirc;i sao Bancassurance của th&aacute;ng&rdquo; với tổng trị gi&aacute; giải thưởng l&ecirc;n tới 12 triệu đồng</strong>.</span></p>\r\n<p><span style=\"font-family: Arial;\">Điểm nổi bật của chương tr&igrave;nh l&agrave; Top 3 Chi nh&aacute;nh VietinBank đứng đầu về doanh thu ph&iacute; bảo hiểm sẽ nhận được giải thưởng tiền mặt với tổng trị gi&aacute; l&ecirc;n tới 90 triệu đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\"><strong>Ng&ocirc;i sao Bancass hứa hẹn sẽ l&agrave; cuộc đua hấp dẫn với &ldquo;cơn mưa&rdquo; giải thưởng d&agrave;nh cho c&aacute;n bộ VietinBank.</strong></span></p>\r\n<p><span style=\"font-family: Arial;\">Mọi chi tiết về chương tr&igrave;nh, xin vui l&ograve;ng li&ecirc;n hệ đường d&acirc;y n&oacute;ng: 04 3942 5650, số lẻ: 341 hoặc Email: <a href=\"mailto:info.vbi@vietinbank.vn\">info.vbi@vietinbank.vn</a> để được tư vấn về chương tr&igrave;nh.</span></p>', '2018-03-21 04:32:14', '2018-03-21 15:32:14', 'images/news/1520411544_Poster-Ba_635405232869260000_HasThumb_Thumb.jpg.png'),
(92, 2, 'VBI tổ chức Hội nghị triển khai nhiệm vụ kinh doanh 6 tháng cuối năm 2014', 'vbi-to-chuc-hoi-nghi-trien-khai-nhiem-vu-kinh-doanh-6-thang-cuoi-nam-2014', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Trong 6 th&aacute;ng đầu năm 2014, mặc d&ugrave; t&igrave;nh h&igrave;nh kinh tế c&ograve;n gặp nhiều kh&oacute; khăn, Bảo hiểm VietinBank tiếp tục ki&ecirc;n tr&igrave;, nỗ lực đạt được những kết quả tăng trưởng khả quan </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 08/07 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) tổ chức Hội nghị triển khai nhiệm vụ kinh doanh 6 th&aacute;ng cuối năm 2014. Tham dự hội nghị c&oacute; sự tham dự của b&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty bảo hiểm VBI, Ban điều h&agrave;nh c&ugrave;ng to&agrave;n thể c&aacute;n bộ VBI.</span></p>\r\n<p><span style=\"font-family: Arial;\">Trong 6 th&aacute;ng đầu năm 2014, mặc d&ugrave; t&igrave;nh h&igrave;nh kinh tế c&ograve;n gặp nhiều kh&oacute; khăn, Bảo hiểm VietinBank tiếp tục ki&ecirc;n tr&igrave;, nỗ lực đạt được những kết quả tăng trưởng khả quan. Tổng doanh thu ph&iacute; bảo hiểm 6 th&aacute;ng đầu năm 2</span></p>\r\n<p><span style=\"font-family: Arial;\">014 tăng trưởng 88,7% so với năm 2013. Trong đ&oacute;, doanh thu bảo hiểm gốc l&agrave; 96,7 tỷ đồng, tăng trưởng 100% so với c&ugrave;ng kỳ năm 2013. Doanh thu hoạt động nhận t&aacute;i đạt 16,7 tỷ đồng, tăng trưởng 41,8% so với c&ugrave;ng kỳ năm 2013.&nbsp;</span></p>\r\n<p><img src=\"../../../uploads/635405027683910000.jpg\" alt=\"\" width=\"524\" height=\"480\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>Tổng Gi&aacute;m đốc b&aacute;o c&aacute;o kết quả hoạt động kinh doanh 6 th&aacute;ng đầu năm 2014</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Tại hội nghị, &Ocirc;ng L&ecirc; Tuấn Dũng - Tổng gi&aacute;m đốc VBI đ&atilde; b&aacute;o c&aacute;o kết quả hoạt động kinh doanh 6 th&aacute;ng đầu năm 2014, đồng thời thẳng thắn đưa ra những tồn tại v&agrave; th&aacute;ch thức m&agrave; to&agrave;n c&ocirc;ng ty phải đối mặt, b&ecirc;n cạnh đ&oacute; đưa ra giải ph&aacute;p hiệu quả, t&iacute;ch cực. Tổng gi&aacute;m đốc nhấn mạnh: 6 th&aacute;ng cuối năm 2014, b&ecirc;n cạnh việc duy tr&igrave; thị trường kh&aacute;ch h&agrave;ng doanh nghiệp, kh&aacute;ch h&agrave;ng dự &aacute;n, C&ocirc;ng ty tiếp tục ph&aacute;t triển kinh doanh theo hướng mở rộng thị trường b&aacute;n lẻ, ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n, ho&agrave;n thiện m&ocirc; h&igrave;nh Bancassurance, tăng cường c&ocirc;ng t&aacute;c đ&agrave;o tạo c&aacute;n bộ, đại l&yacute; v&agrave; n&acirc;ng cao chất lượng bồi thường.</span></p>\r\n<p><span style=\"font-family: Arial;\">Chủ tịch C&ocirc;ng ty &ndash; B&agrave; Nguyễn Hồng V&acirc;n ghi nhận v&agrave; đ&aacute;nh gi&aacute; cao nỗ lực của VBI với những chuyển biến hết sức t&iacute;ch cực về c&ocirc;ng t&aacute;c bồi thường, kinh doanh cũng như ph&aacute;t triển thương hiệu đồng thời đưa ra những chỉ đạo cụ thể về định hướng hoạt động kinh doanh v&agrave; ph&aacute;t triển thương hiệu trong thời gian tới. Chủ tịch VBI tin tưởng rằng với sự quyết t&acirc;m cao v&agrave; đồng l&ograve;ng của to&agrave;n c&ocirc;ng ty, VBI sẽ ho&agrave;n th&agrave;nh v&agrave; vượt c&aacute;c chỉ ti&ecirc;u kế hoạch đ&atilde; đề ra.</span></p>', '2018-03-21 04:32:40', '2018-03-21 15:32:40', 'images/news/1520411623_TGD-phat-_635405200936340000_HasThumb_Thumb.jpg.png'),
(93, 2, 'VBI ký kết hợp đồng hợp tác với Trung tâm thẻ VietinBank', 'vbi-ky-ket-hop-dong-hop-tac-voi-trung-tam-the-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 14/7, tại H&agrave; Nội đ&atilde; diễn ra lễ k&yacute; kết hợp đồng hợp t&aacute;c giữa Bảo hiểm VietinBank (VBI) v&agrave; Trung t&acirc;m thẻ VietinBank. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 14/7, tại H&agrave; Nội đ&atilde; diễn ra lễ k&yacute; kết hợp đồng hợp t&aacute;c giữa Bảo hiểm VietinBank (VBI) v&agrave; Trung t&acirc;m thẻ VietinBank. Dự lễ c&oacute; &ocirc;ng Phạm Huy Th&ocirc;ng - Ủy vi&ecirc;n HĐQT ki&ecirc;m Ph&oacute; Tổng Gi&aacute;m đốc VietinBank; &ocirc;ng L&ecirc; Tuấn Dũng -Tổng Gi&aacute;m đốc VBI, &ocirc;ng Phạm Văn Khoa - Gi&aacute;m đốc Trung t&acirc;m Thẻ,...</span></p>\r\n<p><span style=\"font-family: Arial;\">Theo hợp đồng k&yacute; kết, VBI v&agrave; VietinBank v&agrave; cam kết hợp t&aacute;c triển khai một số nội dung như: hỗ trợ b&aacute;n ch&eacute;o c&aacute;c sản phẩm bảo hiểm v&agrave; sản phẩm thẻ; cung cấp v&agrave; đẩy mạnh c&aacute;c sản phẩm thẻ, bảo hiểm cho kh&aacute;ch h&agrave;ng của cả 2 b&ecirc;n; thống nhất triển khai dịch vụ bảo hiểm mất thẻ cho to&agrave;n bộ chủ thẻ VietinBank, mang lại sự thuận tiện tối đa cho kh&aacute;ch h&agrave;ng trong qu&aacute; tr&igrave;nh sử dụng thẻ.</span></p>\r\n<p><img src=\"../../../uploads/Le ky ket VBI_TTT.jpg\" alt=\"\" width=\"640\" height=\"336\" /></p>\r\n<p><span style=\"font-family: Arial;\">Tại lễ k&yacute; kết, Ph&oacute; Tổng gi&aacute;m đốc Phạm Huy Th&ocirc;ng cho biết: VietinBank lu&ocirc;n ti&ecirc;n phong cung cấp đa dạng c&aacute;c sản phẩm dịch vụ ng&acirc;n h&agrave;ng theo chuẩn mực quốc tế. Đặc biệt, c&aacute;c sản phẩm dịch vụ thẻ của VietinBank đ&atilde; nhận được sự t&iacute;n nhiệm của đ&ocirc;ng đảo kh&aacute;ch h&agrave;ng tr&ecirc;n to&agrave;n quốc. T&iacute;nh đến nay, thị phần thẻ VietinBank đang dẫn đầu thị trường với hơn 15 triệu thẻ ghi nợ nội địa, thẻ quốc tế; tr&ecirc;n 30% thị phần thanh to&aacute;n POS tại Việt Nam. </span></p>\r\n<p><span style=\"font-family: Arial;\">Kh&ocirc;ng chỉ cung cấp c&aacute;c sản phẩm thẻ truyền thống, VietinBank c&ograve;n ti&ecirc;n phong trong việc cung cấp c&aacute;c sản phẩm dịch vụ c&ocirc;ng nghệ t&iacute;ch hợp như: thẻ đồng thương hiệu, thẻ li&ecirc;n kết, dịch vụ thu học ph&iacute;, thanh to&aacute;n thẻ trực tuyến v&agrave; gần đ&acirc;y nhất l&agrave; cung cấp 5 sản phẩm dịch vụ c&ocirc;ng nghệ cao gồm: MPOS, Merchant online, r&uacute;t tiền kh&ocirc;ng cần thẻ, gửi tiền/chuyển đổi ngoại tệ tại ATM, thẻ E-fast On&hellip; gi&uacute;p kh&aacute;ch h&agrave;ng tiếp cận với c&aacute;c phương thức thanh to&aacute;n hiện đại, gia tăng độ an to&agrave;n do kh&ocirc;ng phải mang theo tiền mặt.</span></p>\r\n<p><span style=\"font-family: Arial;\">Việc k&yacute; kết hợp đồng hợp t&aacute;c to&agrave;n diện giữa VBI v&agrave; VietinBank g&oacute;p phần gia tăng dịch vụ tiện &iacute;ch cho kh&aacute;ch h&agrave;ng cũng l&agrave; dịp để hai b&ecirc;n trao đổi c&aacute;c cơ hội đầu tư v&agrave; mang tới cho kh&aacute;ch h&agrave;ng những tiện &iacute;ch v&agrave; gi&aacute; trị gia tăng kh&aacute;c.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tổng Gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng tin tưởng rằng Lễ k&yacute; kết mở ra cơ hội để hai b&ecirc;n cung cấp nhiều sản phẩm dịch vụ hiện đại, tiện &iacute;ch cho kh&aacute;ch h&agrave;ng cũng như c&ugrave;ng hỗ trợ v&agrave; ph&aacute;t triển hoạt động kinh doanh; tiếp tục đẩy mạnh tăng trưởng, khai th&aacute;c một c&aacute;ch tốt nhất thế mạnh v&agrave; tiềm năng của VBI v&agrave; Trung t&acirc;m Thẻ.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em><strong>Nguồn: VietinBank</strong></em></span></p>', '2018-03-21 04:33:06', '2018-03-21 15:33:06', 'images/news/1520411711_Hop-tac-V_635410185990900000_HasThumb_Thumb.jpg.png'),
(94, 2, 'VBI CN Sài Gòn đạt chức vô địch bóng đá nam tại Hội thao VietinBank Chi nhánh 11', 'vbi-cn-sai-gon-dat-chuc-vo-dich-bong-da-nam-tai-hoi-thao-vietinbank-chi-nhanh-11', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 20/07/2014, Bảo hiểm VietinBank Chi nh&aacute;nh S&agrave;i G&ograve;n (VBI S&agrave;i G&ograve;n) đ&atilde; tham dự Hội thao do VietinBank chi nh&aacute;nh (CN) 11 tổ chức tại cụm s&acirc;n Khang An, Q.G&ograve; Vấp, Tp.HCM. </span></span></p>', '<p><strong><span style=\"font-family: arial, helvetica, sans-serif;\">Ng&agrave;y 20/07/2014, Bảo hiểm VietinBank Chi nh&aacute;nh S&agrave;i G&ograve;n (VBI S&agrave;i G&ograve;n) đ&atilde; tham dự Hội thao do VietinBank chi nh&aacute;nh (CN) 11 tổ chức tại cụm s&acirc;n Khang An, Q.G&ograve; Vấp, Tp.HCM. Đ&acirc;y l&agrave; sự kiện thể thao lớn của CN nhằm thắt chặt v&agrave; th&uacute;c đẩy mối quan hệ hợp t&aacute;c với kh&aacute;ch h&agrave;ng tr&ecirc;n tinh thần giao lưu thể thao, tăng t&igrave;nh đo&agrave;n kết.</span></strong></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Dự hội thao c&oacute; hơn 220 vận động vi&ecirc;n thuộc 22 đội đến từ 24 đơn vị gồm: Ng&acirc;n h&agrave;ng Nh&agrave; nước Tp.HCM; một số chi nh&aacute;nh, đơn vị VietinBank tr&ecirc;n địa b&agrave;n Tp.HCM; c&aacute;c kh&aacute;ch h&agrave;ng đối t&aacute;c th&acirc;n thiết như: CII, Vietjet Air, C&ocirc;ng ty CP Xăng dầu, Dịch vụ h&agrave;ng hải STS,&hellip;</span></p>\r\n<p><img src=\"../../../uploads/635421339776535046.jpg\" alt=\"\" width=\"640\" height=\"360\" /></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Tại hội thao, 16 đội b&oacute;ng đ&aacute; đ&atilde; tham dự v&agrave; cống hiến cho c&aacute;c cổ động vi&ecirc;n 32 trận cầu hấp dẫn, quyết liệt. Vượt qua 15 đội b&oacute;ng xuất sắc tham dự Hội thao, VBI S&agrave;i G&ograve;n đ&atilde; đạt chức v&ocirc; địch b&oacute;ng đ&aacute; nam một c&aacute;c thuyết phục.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">B&ecirc;n cạnh đ&oacute;, với 21 b&agrave;n thắng ghi được trong giải, danh hiệu vua ph&aacute; lưới đ&atilde; được trao cho tiền đạo Trịnh C&ocirc;ng Danh - VBI S&agrave;i G&ograve;n.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">C&oacute; thể n&oacute;i việc tham gia c&aacute;c hoạt động giao lưu thể thao đ&atilde; g&oacute;p phần tăng cường mối quan hệ hợp t&aacute;c giữa c&aacute;c đơn vị trong VietinBank cũng như với c&aacute;c đối t&aacute;c, kh&aacute;ch h&agrave;ng th&acirc;n thiết.</span></p>', '2018-03-21 04:33:36', '2018-03-21 15:33:36', 'images/news/1520411856_vodichbon_635421339786207063_HasThumb_Thumb.jpg.png'),
(95, 2, 'VBI tặng quà Trung tâm điều dưỡng thương binh Thuận Thành', 'vbi-tang-qua-trung-tam-dieu-duong-thuong-binh-thuan-thanh', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Nh&acirc;n dịp ng&agrave;y 27/7, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; đến thăm v&agrave; tặng qu&agrave; cho c&aacute;c thương bệnh binh đang được chăm s&oacute;c v&agrave; điều trị tại Trung t&acirc;m điều dưỡng Thương binh Thuận Th&agrave;nh &ndash; Bắc Ninh. </span></span></p>', '<p><span style=\"font-family: Arial;\">Nh&acirc;n dịp ng&agrave;y 27/7, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; đến thăm v&agrave; tặng qu&agrave; cho c&aacute;c thương bệnh binh đang được chăm s&oacute;c v&agrave; điều trị tại Trung t&acirc;m điều dưỡng Thương binh Thuận Th&agrave;nh &ndash; Bắc Ninh. Đ&acirc;y l&agrave; hoạt động đền ơn đ&aacute;p nghĩa thường ni&ecirc;n, thể hiện tinh thần &ldquo;uống nước nhớ nguồn&rdquo; v&agrave; tr&aacute;ch nhiệm chia sẻ kh&oacute; khăn với c&aacute;c thương bệnh binh nh&acirc;n dịp kỷ niệm Ng&agrave;y Thương binh Liệt sỹ của VBI.</span></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi thăm hỏi, Ph&oacute; Tổng gi&aacute;m đốc VBI - b&agrave; Trần Thị Kim Yến đ&atilde; thay mặt Ban l&atilde;nh đạo, ph&aacute;t biểu thể hiện t&igrave;nh cảm, lời tri &acirc;n, động vi&ecirc;n tinh thần tới c&aacute;c đồng ch&iacute; thương binh, bệnh binh - những người đ&atilde; hy sinh xương m&aacute;u v&agrave; một phần cơ thể m&igrave;nh cho độc lập d&acirc;n tộc, đồng thời chia sẻ những mất m&aacute;t, đau thương cũng như kh&iacute;ch lệ c&aacute;c đồng ch&iacute; vượt quan những đau đớn v&agrave; mất m&aacute;t, tiếp tục lu&ocirc;n l&agrave; tấm gương mẫu mực cho c&aacute;c thế hệ trẻ noi theo.</span></p>\r\n<p><img src=\"../../../uploads/635421349972244954.jpg\" alt=\"\" width=\"640\" height=\"429\" /></p>\r\n<p><span style=\"font-family: Arial;\">Đại diện cho Ban l&atilde;nh đạo v&agrave; c&aacute;c đo&agrave;n thể VBI, B&agrave; Trần Thị Kim Yến đ&atilde; trao tặng hơn 20 triệu đồng cho c&aacute;c thương bệnh binh tại Trung t&acirc;m. Phần qu&agrave; tuy nhỏ nhưng chứa đựng tấm l&ograve;ng biết ơn s&acirc;u sắc của tập thể VBI với c&aacute;c đồng ch&iacute; thương binh, bệnh binh giờ đ&acirc;y vẫn đ&ecirc;m ng&agrave;y chiến đấu, vật lộn với thương tật, n&ecirc;u cao tấm gương dũng cảm, &yacute; ch&iacute; v&agrave; bản lĩnh sắt son của Bộ đội Cụ Hồ, xứng đ&aacute;ng với lời dạy của B&aacute;c: &ldquo;Thương binh t&agrave;n nhưng kh&ocirc;ng phế&rdquo;.</span></p>\r\n<p><img src=\"../../../uploads/635421349985348977.jpg\" alt=\"\" width=\"610\" height=\"480\" /></p>\r\n<p><span style=\"font-family: Arial;\">&Ocirc;ng Nguyễn Khắc Dư - Gi&aacute;m đốc Trung t&acirc;m điều dưỡng thương bệnh binh Thuận Th&agrave;nh b&agrave;y tỏ l&ograve;ng cảm ơn s&acirc;u sắc trước sự quan t&acirc;m, chia sẻ của VBI. &Ocirc;ng cho biết: &ldquo;B&ecirc;n cạnh sự chăm lo của Nh&agrave; nước, sự hỗ trợ từ VBI cũng như c&aacute;c tổ chức, cơ quan kh&aacute;c đ&atilde; gi&uacute;p đời sống vật chất tinh thần của anh em thương bệnh binh được cải thiện đ&aacute;ng kể&rdquo;.</span></p>\r\n<p><span style=\"font-family: Arial;\">C&oacute; thể n&oacute;i những c&aacute;i bắt tay ấm &aacute;p v&agrave; những sẻ chia giữa đo&agrave;n c&aacute;n bộ VBI với c&aacute;c đồng ch&iacute; thương bệnh binh nặng sẽ m&atilde;i l&agrave; những kỉ niệm đẹp.</span></p>', '2018-03-21 04:34:29', '2018-03-21 15:34:29', 'images/news/1520411934_2707_0_635421350006253013_HasThumb_Thumb.jpg.png'),
(96, 2, 'Đoàn thanh niên VBI thắp nến tri ân các Anh hùng Liệt sỹ', 'doan-thanh-nien-vbi-thap-nen-tri-an-cac-anh-hung-liet-sy', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Kỷ niệm ng&agrave;y 27/7, Đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; tham dự Lễ thắp nến tri &acirc;n kỷ niệm 67 năm ng&agrave;y Thương binh Liệt sỹ tại nghĩa trang Mai Dịch. </span></span></p>', '<p><span style=\"font-family: Arial;\">Kỷ niệm ng&agrave;y 27/7, Đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; tham dự Lễ thắp nến tri &acirc;n kỷ niệm 67 năm ng&agrave;y Thương binh Liệt sỹ tại nghĩa trang Mai Dịch. Tại buổi lễ, Ban tổ chức tiến h&agrave;nh d&acirc;ng hương tại Đ&agrave;i tưởng niệm c&aacute;c Anh h&ugrave;ng Liệt sỹ, trao tặng 67 xuất qu&agrave; cho c&aacute;c gia đ&igrave;nh thương binh, liệt sỹ c&oacute; ho&agrave;n cảnh kh&oacute; khăn tr&ecirc;n địa b&agrave;n th&agrave;nh phố; tổ chức thắp nến tri &acirc;n, d&acirc;ng hương, hoa tại c&aacute;c phần mộ của c&aacute;c đồng ch&iacute; L&atilde;nh đạo Đảng, Nh&agrave; nước, c&aacute;c bậc l&atilde;o th&agrave;nh c&aacute;ch mạng, c&aacute;c anh h&ugrave;ng liệt sỹ đang y&ecirc;n nghỉ tại Nghĩa trang Mai Dịch.</span></p>\r\n<p><img src=\"../../../uploads/635421350974858715.jpg\" alt=\"\" width=\"420\" height=\"256\" /></p>\r\n<p><span style=\"font-family: Arial;\">G&oacute;p sức c&ugrave;ng Ban tổ chức thực hiện chương tr&igrave;nh, c&aacute;c đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI c&ugrave;ng với c&aacute;c đo&agrave;n vi&ecirc;n VietinBank tại H&agrave; Nội đ&atilde; đại diện tuổi trẻ VietinBank tham dự v&agrave; thắp nến d&acirc;ng hương tri &acirc;n c&aacute;c Anh h&ugrave;ng Liệt sỹ. Đ&acirc;y l&agrave; hoạt động hoạt động thường ni&ecirc;n của ĐTN VietinBank cũng như Đo&agrave;n khối DNTW thiết thực kỷ niệm ng&agrave;y Thương binh Liệt sỹ. Hoạt động n&agrave;y c&oacute; &yacute; nghĩa lớn trong việc gi&aacute;o dục, bồi dưỡng truyền thống, đạo đức, t&igrave;nh y&ecirc;u qu&ecirc; hương đất nước đối với tuổi trẻ VietinBank đồng thời ph&aacute;t huy vai tr&ograve; xunh k&iacute;ch, t&igrave;nh nguyện tham gia thực hiện nhiệm vụ ch&iacute;nh trị v&agrave; c&aacute;c hoạt động an sinh x&atilde; hội của tuổi trẻ VietinBank.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><em>Nguồn: VietinBank</em></span></p>', '2018-03-21 04:34:57', '2018-03-21 15:34:57', 'images/news/1520411994_Thap-nen-_635421350982502728_HasThumb_Thumb.jpg.png'),
(97, 2, 'VBI Bổ nhiệm Phó Tổng giám đốc', 'vbi-bo-nhiem-pho-tong-giam-doc', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Theo Quyết định số 1008/QĐ-HĐQT-NHCT1 ng&agrave;y 11/7/2014, &ocirc;ng Chu Văn H&agrave;o được điều động, bổ nhiệm chức vụ Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Bảo hiểm VietinBank kể từ ng&agrave;y 15/7/2014. </span></span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng 28/7 tại H&agrave; Nội, đ&atilde; diễn ra lễ c&ocirc;ng bố quyết bổ nhiệm Ph&oacute; Tổng gi&aacute;m đốc tại Bảo hiểm VietinBank (VBI). Tham dự buổi lễ c&oacute; b&agrave; Nguyễn Hồng V&acirc;n - Ủy vi&ecirc;n HĐQT VietinBank ki&ecirc;m Chủ tịch HĐQT VBI; &ocirc;ng Trần Ki&ecirc;n Cường- Ph&oacute; B&iacute; thư thường trực Đảng ủy VietinBank; &ocirc;ng Hạ Quang Vũ- Trưởng ph&ograve;ng Tổ chức c&aacute;n bộ v&agrave; Đ&agrave;o tạo VietinBank, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng gi&aacute;m đốc VBI c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n trong Ban l&atilde;nh đạo VBI.</span></p>\r\n<p><span style=\"font-family: Arial;\">Theo Quyết định số 1008/QĐ-HĐQT-NHCT1 ng&agrave;y 11/7/2014, &ocirc;ng Chu Văn H&agrave;o được điều động, bổ nhiệm chức vụ Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Bảo hiểm VietinBank kể từ ng&agrave;y 15/7/2014. &Ocirc;ng Chu Văn H&agrave;o l&agrave; c&aacute;n bộ quản l&yacute; c&oacute; phẩm chất đạo đức tốt, c&oacute; tr&igrave;nh độ, năng lực quản trị điều h&agrave;nh hoạt động kinh doanh; lu&ocirc;n năng động, s&aacute;ng tạo, tr&aacute;ch nhiệm cao với c&ocirc;ng việc được giao. </span></p>\r\n<p><img src=\"../../../uploads/DSC_0280.jpg\" alt=\"\" width=\"1366\" height=\"851\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>(L&atilde;nh đạo VietinBank ch&uacute;c mừng t&acirc;n Ph&oacute; TGĐ VBI)</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Với 15 năm c&ocirc;ng t&aacute;c li&ecirc;n tục trong hệ thống VietinBank, &ocirc;ng Chu Văn H&agrave;o đ&atilde; trải qua nhiều nghiệp vụ v&agrave; đảm nhiệm c&aacute;c vị tr&iacute; như: C&aacute;n bộ ph&ograve;ng Kh&aacute;ch h&agrave;ng Doanh nghiệp lớn (TSC); Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh Bắc Giang; Ph&oacute; ph&ograve;ng Kiểm so&aacute;t giải ng&acirc;n (TSC); Ph&oacute; Chủ nhiệm Ủy ban Kiểm tra Đảng ủy VietinBank v&agrave; nay l&agrave; Ph&oacute; Tổng gi&aacute;m đốc Bảo hiểm VietinBank. Tr&ecirc;n mọi cương vị c&ocirc;ng t&aacute;c, &ocirc;ng H&agrave;o lu&ocirc;n được đ&aacute;nh gi&aacute; l&agrave; người năng động, nhiệt huyết, tận t&acirc;m, tận tụy với c&ocirc;ng việc, ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ được giao.</span></p>\r\n<p><img src=\"../../../uploads/1_-_chon.jpg\" alt=\"\" width=\"1366\" height=\"943\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>(Ban điều h&agrave;nh VBI với sự hiện diện của t&acirc;n Ph&oacute; Tổng gi&aacute;m đốc)</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu tại buổi lễ, Ủy vi&ecirc;n HĐQT Nguyễn Hồng V&acirc;n đ&atilde; ch&uacute;c mừng t&acirc;n Ph&oacute; Tổng gi&aacute;m đốc mới. B&agrave; mong rằng tr&ecirc;n cương vị v&agrave; trọng tr&aacute;ch mới &ocirc;ng H&agrave;o sẽ tiếp tục ph&aacute;t huy hơn nữa năng lực, tr&iacute; tuệ trong c&ocirc;ng t&aacute;c, đồng sức đồng l&ograve;ng, đo&agrave;n kết để ph&aacute;t huy sức mạnh tập thể, vững v&agrave;ng bản lĩnh ch&iacute;nh trị v&agrave; phẩm chất đạo đức c&aacute;ch mạng, nỗ lực vượt qua những kh&oacute; khăn, th&aacute;ch thức c&ugrave;ng Ban Gi&aacute;m đốc VBI chỉ đạo, điều h&agrave;nh VBI ng&agrave;y c&agrave;ng ph&aacute;t triển bền vững, hiệu quả, ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ.</span></p>', '2018-03-21 04:35:40', '2018-03-21 15:35:40', 'images/news/1520412100_PTG_635421659271345086_HasThumb_Thumb.jpg.png'),
(98, 2, 'VBI trao giải thưởng trị giá 10 triệu đồng cho cán bộ VietinBank', 'vbi-trao-giai-thuong-tri-gia-10-trieu-dong-cho-can-bo-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">V&agrave;o ng&agrave;y 18/08/2014, VBI đ&atilde; tổ chức lễ trao giải thưởng 10 triệu đồng c&ugrave;ng bằng khen của Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam cho chị L&ecirc; Trần Phương Nhị - c&aacute;n bộ VietinBank Chi nh&aacute;nh Quảng B&igrave;nh </span></span></p>', '<p><span style=\"font-family: Arial;\">Trong thời gian qua, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; triển khai nhiều chương tr&igrave;nh thi đua hấp dẫn d&agrave;nh ri&ecirc;ng cho c&aacute;c c&aacute;n bộ VietinBank b&aacute;n bảo hiểm trong c&aacute;c chương tr&igrave;nh khuyến mại như: 1000 tỷ ưu đ&atilde;i kh&aacute;ch h&agrave;ng b&aacute;n lẻ, Rạng rỡ ng&agrave;y h&egrave; &ndash; Gửi tiền tr&uacute;ng lớn, Mu&ocirc;n kết nối trọn tin y&ecirc;u,...</span></p>\r\n<p><span style=\"font-family: Arial;\">V&agrave;o ng&agrave;y 18/08/2014, VBI đ&atilde; tổ chức lễ trao giải thưởng 10 triệu đồng c&ugrave;ng bằng khen của Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam cho chị L&ecirc; Trần Phương Nhị - c&aacute;n bộ VietinBank Chi nh&aacute;nh Quảng B&igrave;nh đạt doanh thu b&aacute;n ch&eacute;o sản phẩm bảo hiểm cao nhất chương tr&igrave;nh &ldquo;1000 tỷ ưu đ&atilde;i kh&aacute;ch h&agrave;ng b&aacute;n lẻ&rdquo;. Lễ trao giải c&oacute; sự tham dự của &ocirc;ng Lương Hải Lưu &ndash; Gi&aacute;m đốc VietinBank Chi nh&aacute;nh Quảng B&igrave;nh, &ocirc;ng L&ecirc; Đ&igrave;nh Quốc &ndash; Gi&aacute;m đốc VBI Chi nh&aacute;nh Đ&agrave; Nẵng c&ugrave;ng to&agrave;n thể Ban L&atilde;nh đạo, c&aacute;n bộ nh&acirc;n vi&ecirc;n VietinBank Chi nh&aacute;nh Quảng B&igrave;nh.</span></p>\r\n<p><img src=\"../../../uploads/trao giai 1000 ty KH ban le.jpg\" alt=\"\" width=\"933\" height=\"594\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em>H&igrave;nh ảnh tại buổi lễ trao giải chương tr&igrave;nh thi đua &ldquo;1000 tỷ ưu đ&atilde;i kh&aacute;ch h&agrave;ng lẻ&rdquo;</em></span></p>\r\n<p><span style=\"font-family: Arial;\">Trong thời gian tới, VBI sẽ tiếp tục phối hợp triển khai c&aacute;c chương tr&igrave;nh thi đua kinh doanh để tạo động lực cho c&aacute;c c&aacute;n bộ VietinBank mang tới c&aacute;c sản phẩm, dịch vụ một cửa ng&acirc;n h&agrave;ng &ndash; bảo hiểm tốt nhất tới kh&aacute;ch h&agrave;ng. </span></p>', '2018-03-21 04:36:10', '2018-03-21 15:36:10', 'images/news/1520412184_trao-giai_635441489450608625_HasThumb_Thumb.jpg.png'),
(99, 2, 'Sôi động cùng Hội thi Nét đẹp Văn hóa VietinBank', 'soi-dong-cung-hoi-thi-net-dep-van-hoa-vietinbank', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">Ng&agrave;y 22/08 tại H&agrave; Nội, VBI đ&atilde; tổ chức th&agrave;nh c&ocirc;ng Hội thi N&eacute;t đẹp Văn h&oacute;a VietinBank 2014. </span></span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 22/08 tại H&agrave; Nội, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức th&agrave;nh c&ocirc;ng Hội thi N&eacute;t đẹp Văn h&oacute;a VietinBank 2014. Ph&aacute;t biểu khai mạc hội thi, &Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc VBI đ&atilde; nhấn mạnh: &ldquo;Nhằm t&ocirc;n vinh c&aacute;c n&eacute;t đẹp văn h&oacute;a đ&atilde; v&agrave; đang được hệ thống VietinBank giữ g&igrave;n, ph&aacute;t huy c&ugrave;ng với đ&oacute; tạo ra s&acirc;n chơi bổ &iacute;ch cho c&aacute;c c&aacute;n bộ, VBI đ&atilde; tổ chức Hội Thi n&eacute;t đẹp văn h&oacute;a VietinBank để c&aacute;c c&aacute;n bộ c&oacute; thể biết th&ecirc;m nhiều n&eacute;t đẹp văn h&oacute;a của VietinBank v&agrave; tự h&agrave;o về c&aacute;c gi&aacute; trị đ&oacute;.&rdquo;</span></p>\r\n<p><img src=\"../../../uploads/IMG_9443.jpg\" alt=\"\" width=\"1366\" height=\"655\" /></p>\r\n<p><span style=\"font-family: Arial;\">Tất cả c&aacute;c ph&ograve;ng tại Trụ sở ch&iacute;nh của C&ocirc;ng ty v&agrave; 2 Chi nh&aacute;nh VBI (Chi nh&aacute;nh Thăng Long v&agrave; H&agrave; Nội) được chia th&agrave;nh 3 đội: Bảo văn h&oacute;a, Đại sứ VBI v&agrave; CQPT. C&aacute;c đội đ&atilde; trải qua 3 phần thi, bao gồm: ch&agrave;o hỏi, kiến thức v&agrave; xử l&yacute; t&igrave;nh huống. Tại phần thi ch&agrave;o hỏi, c&aacute;c đội c&oacute; tối đa 10 ph&uacute;t để giới thiệu t&ecirc;n, đặc điểm nổi bật của đội m&igrave;nh với những tiểu phẩm, clip ngắn được đầu tư kỹ lưỡng về mặt nội dung để thể hiện được tr&igrave;nh độ chuy&ecirc;n m&ocirc;n nghiệp vụ của c&aacute;c đội, bao gồm: kỹ năng giao tiếp khi gặp kh&aacute;ch h&agrave;ng, kỹ năng giải quyết bồi thường cho kh&aacute;ch h&agrave;ng,... c&ugrave;ng với đ&oacute; truyền tải đầy đủ dấu ấn văn h&oacute;a VietinBank.</span></p>\r\n<p><span style=\"font-family: Arial;\">Sau phần ch&agrave;o hỏi, c&aacute;c đội thi cử ra 5 th&agrave;nh vi&ecirc;n để tham gia phần thi kiến thức v&agrave; xử l&yacute; t&igrave;nh huống đầy kịch t&iacute;nh. Bằng việc trả lời rất chuẩn x&aacute;c c&aacute;c c&acirc;u hỏi c&ugrave;ng với đ&oacute; l&agrave; sự nhanh nhạy trong kh&acirc;u giải quyết t&igrave;nh huống được đưa ra bởi Ban tổ chức, c&aacute;c đội đ&atilde; mang đến kh&ocirc;ng kh&iacute; s&ocirc;i nổi, h&agrave;o hứng tới hội thi.Đội Bảo Văn H&oacute;a với phần thi xuất sắc đ&atilde; nhận được số điểm cao nhất từ Ban Gi&aacute;m khảo v&agrave; d&agrave;nh giải Nhất Hội thi.</span></p>\r\n<p><span style=\"font-family: Arial;\">Hội thi đ&atilde; kết th&uacute;c trong kh&ocirc;ng kh&iacute; vui tươi, phấn khởi v&agrave; đo&agrave;n kết, tạo kh&iacute; thế cho tập thể CBNV VBI quyết t&acirc;m triển khai v&agrave; vận dụng văn h&oacute;a VietinBank trong c&ocirc;ng việc h&agrave;ng ng&agrave;y, mang chất lượng sản phẩm, dịch vụ tốt nhất tới kh&aacute;ch h&agrave;ng.</span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\"><strong>VBI</strong></span></p>', '2018-03-21 04:36:36', '2018-03-21 15:36:36', 'images/news/1520412258_IMG_944_635448231959337573_HasThumb_Thumb.JPG.png'),
(100, 2, 'VBI ra mắt website mới - thân thiện và tiện dụng hơn', 'vbi-ra-mat-website-moi-than-thien-va-tien-dung-hon', '<p><span style=\"font-family: Arial;\"><span class=\"teaser-new\">VBI vừa ch&iacute;nh thức ra mắt website mới http://vbi.vietinbank.vn v&agrave;o ng&agrave;y 28/08/2014.VBI mong muốn sẽ mang đến cho kh&aacute;ch h&agrave;ng, nh&agrave; đầu tư v&agrave; đối t&aacute;c một website giao diện mới, hiện đại, gần gũi v&agrave; nhiều tiện &iacute;ch hơn với người sử dụng. </span></span></p>', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Bảo hiểm VietinBank (VBI) vừa ch&iacute;nh thức ra mắt website mới </strong><a href=\"http://www.baovietbank.vn/\"><strong>http://vbi.vietinbank.vn</strong></a><strong> v&agrave;o ng&agrave;y 28/08/2014</strong>. <strong>VBI mong muốn mang đến cho kh&aacute;ch h&agrave;ng, nh&agrave; đầu tư v&agrave; đối t&aacute;c một website giao diện mới, hiện đại, gần gũi v&agrave; nhiều tiện &iacute;ch hơn với người sử dụng.</strong></span></p>\r\n<p>Với giao diện được thiết kế hiện đại, th&acirc;n thiện, c&ugrave;ng c&aacute;c t&iacute;nh năng ưu việt dễ d&agrave;ng cho việc truy cập, website mới l&agrave; k&ecirc;nh truyền th&ocirc;ng ch&iacute;nh thức của VBI về c&aacute;c hoạt động kinh doanh, gi&uacute;p kh&aacute;ch h&agrave;ng dễ d&agrave;ng tra cứu, t&igrave;m hiểu th&ocirc;ng tin, sử dụng sản phẩm, dịch vụ v&agrave; c&aacute;c chương tr&igrave;nh khuyến m&atilde;i của VBI.</p>\r\n<p>Điểm ưu việt của website mới l&agrave; sự đơn giản v&agrave; tiện dụng đối với từng đối tượng kh&aacute;ch h&agrave;ng. Đồng thời t&iacute;nh năng hiển thị danh s&aacute;ch Chi nh&aacute;nh VBI, Bệnh viện v&agrave; Gara bảo l&atilde;nh gi&uacute;p kh&aacute;ch h&agrave;ng nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng t&igrave;m kiếm được c&aacute;c điạ điểm gần nhất. Website mới của VBI cũng được đầu tư kỹ lưỡng về giao diện v&agrave; thống nhất với c&aacute;c yếu tố nhận diện của Bộ nhận diện thương hiệu VBI 2014. Trong thời gian tới, VBI sẽ tiếp tục đầu tư v&agrave;o c&ocirc;ng nghệ để tăng cường khả năng tương t&aacute;c giữa VBI v&agrave; kh&aacute;ch h&agrave;ng như ch&agrave;o ph&iacute; bảo hiểm hay mua bảo hiểm trực tuyến,...</p>\r\n<p><img src=\"../../../uploads/Website VBI.png\" alt=\"\" width=\"966\" height=\"633\" /></p>\r\n<p style=\"text-align: center;\"><em>Giao diện trang chủ website VBI mới</em></p>\r\n<p>Việc ra mắt website mới v&agrave; thường xuy&ecirc;n cập nhật c&ocirc;ng nghệ ti&ecirc;n tiến v&agrave;o hoạt động kinh doanh một lần nữa khẳng định quyết t&acirc;m của VBI trong việc n&acirc;ng cao chất lượng dịch vụ nhằm đem đến cho kh&aacute;ch h&agrave;ng sự h&agrave;i l&ograve;ng trong từng giao dịch.</p>\r\n<p>Hiện nay, VBI đ&atilde; đ&atilde; x&acirc;y dựng th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh Bancassurance b&aacute;n bảo hiểm qua Ng&acirc;n h&agrave;ng với mạng lưới hơn 150 Chi nh&aacute;nh v&agrave; hơn 6000 c&aacute;n bộ b&aacute;n h&agrave;ng của VietinBank. Từ đ&oacute;, cung cấp cho kh&aacute;ch h&agrave;ng dịch vụ t&agrave;i ch&iacute;nh trọn g&oacute;i một cửa: ng&acirc;n h&agrave;ng &ndash; bảo hiểm. Tổng doanh thu ph&iacute; bảo hiểm 6 th&aacute;ng đầu năm 2014 của VBI tăng trưởng 88,7% so với năm 2013. Trong đ&oacute;, doanh thu bảo hiểm gốc đạt 96,7 tỷ đồng, tăng trưởng 100% so với c&ugrave;ng kỳ năm 2013; doanh thu hoạt động nhận t&aacute;i bảo hiểm đạt 16,7 tỷ đồng, tăng trưởng 41,8% so với c&ugrave;ng kỳ năm 2013. Th&aacute;ng 6/2014 vừa qua, VBI được Cục Thuế TP H&agrave; Nội khen thưởng v&igrave; đ&atilde; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ đ&oacute;ng g&oacute;p cho Ng&acirc;n s&aacute;ch Nh&agrave; nước.</p>\r\n<p>Với định hướng phấn đấu trở th&agrave;nh C&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ chuy&ecirc;n nghiệp h&agrave;ng đầu tại Việt Nam, Bảo hiểm VietinBank quyết t&acirc;m nỗ lực, s&aacute;ng tạo, g&oacute;p phần đưa thương hiệu VietinBank n&oacute;i chung v&agrave; Bảo hiểm VietinBank n&oacute;i ri&ecirc;ng vươn cao v&agrave; xa hơn nữa tr&ecirc;n thị trường trong nước v&agrave; quốc tế.</p>', '2018-03-21 04:37:05', '2018-03-21 15:37:05', 'images/news/1520412360_Website-V_635448330054345961_HasThumb_Thumb.png'),
(101, 2, 'Tổng Giám đốc VBI nhận Giải thưởng Top 100 doanh nhân trẻ tiêu biểu', 'tong-giam-doc-vbi-nhan-giai-thuong-top-100-doanh-nhan-tre-tieu-bieu', '<p><span style=\"font-family: Arial;\">Giải thưởng Top 100 doanh nh&acirc;n trẻ ti&ecirc;u biểu l&agrave; giải thưởng cao qu&yacute; của TW Hội Li&ecirc;n hiệp Thanh ni&ecirc;n Việt Nam, TW Hội Doanh nh&acirc;n Trẻ Việt Nam, x&eacute;t trao tặng những doanh nh&acirc;n trẻ xuất sắc, c&oacute; nhiều đ&oacute;ng g&oacute;p trong sự nghiệp ph&aacute;t triển kinh tế x&atilde; hội đất nước.&nbsp;</span></p>', '<p>&nbsp;</p>\r\n<p>Tổng Gi&aacute;m đốc Bảo hiểm VietinBank (VBI) &ndash; L&ecirc; Tuấn Dũng vinh dự nhận Giải thưởng Top 100 doanh nh&acirc;n trẻ ti&ecirc;u biểu năm 2014.<br /> <br />Ng&agrave;y 30/8, tại H&agrave; Nội đ&atilde; diễn ra lễ trao Giải thưởng Sao đỏ v&agrave; Top 100 doanh nh&acirc;n trẻ Việt Nam ti&ecirc;u biểu năm 2014. Dự lễ c&oacute; &ocirc;ng Vũ Văn Ninh - Ph&oacute; Thủ tướng Ch&iacute;nh phủ, &ocirc;ng Đinh La Thăng - Bộ trưởng Bộ Giao th&ocirc;ng vận tải, &ocirc;ng Nguyễn Qu&acirc;n - Bộ trưởng Bộ Khoa học C&ocirc;ng nghệ, &ocirc;ng Ho&agrave;ng Đ&igrave;nh Qu&acirc;n -Trưởng Ban đối ngoại TW Đảng; &ocirc;ng B&ugrave;i Văn Cường - B&iacute; thư Đảng ủy Khối DNTW,&hellip;</p>\r\n<p>Giải thưởng Top 100 doanh nh&acirc;n trẻ ti&ecirc;u biểu l&agrave; giải thưởng cao qu&yacute; của TW Hội Li&ecirc;n hiệp Thanh ni&ecirc;n Việt Nam, TW Hội Doanh nh&acirc;n Trẻ Việt Nam, x&eacute;t trao tặng những doanh nh&acirc;n trẻ xuất sắc, c&oacute; nhiều đ&oacute;ng g&oacute;p trong sự nghiệp ph&aacute;t triển kinh tế x&atilde; hội đất nước. Giải thưởng được tổ chức 3 năm một lần, c&ugrave;ng với nhiệm kỳ Đại hội của Hội Doanh nh&acirc;n trẻ Việt Nam.</p>\r\n<p>Năm nay, 100 doanh nh&acirc;n được lựa chọn từ 138 hồ sơ, trải qua 3 v&ograve;ng b&igrave;nh chọn khoa học, chặt chẽ. Đối tượng tham gia b&igrave;nh chọn Giải thưởng l&agrave; c&aacute;c doanh nh&acirc;n c&oacute; độ tuổi kh&ocirc;ng qu&aacute; 45, đang giữ vị tr&iacute; l&atilde;nh đạo chủ chốt trong c&aacute;c doanh nghiệp. Giải thưởng đề cao c&aacute;c ti&ecirc;u ch&iacute; về ph&aacute;t triển bền vững, tr&aacute;ch nhiệm x&atilde; hội, đạo đức kinh doanh, đặc biệt ưu ti&ecirc;n những doanh nh&acirc;n trẻ c&oacute; nhiều s&aacute;ng kiến, s&aacute;ng tạo, ứng dụng đem lại th&agrave;nh c&ocirc;ng.</p>\r\n<p>C&aacute;c doanh nh&acirc;n trẻ được trao thưởng l&agrave; những tấm gương thanh ni&ecirc;n ti&ecirc;u biểu, đại diện cho thế hệ trẻ Việt Nam tr&iacute; tuệ, năng động, khao kh&aacute;t vươn l&ecirc;n l&agrave;m gi&agrave;u cho ch&iacute;nh m&igrave;nh v&agrave; đất nước, l&agrave; điển h&igrave;nh ti&ecirc;n tiến g&oacute;p phần khơi dậy tinh thần lập th&acirc;n, lập nghiệp kiến quốc của tuổi trẻ.</p>\r\n<p>Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI đ&atilde; vinh dự nhận Giải thưởng 100 doanh nh&acirc;n trẻ Việt Nam ti&ecirc;u biểu 2014. &Ocirc;ng L&ecirc; Tuấn Dũng được đ&aacute;nh gi&aacute; l&agrave; nh&agrave; l&atilde;nh đạo trẻ với năng lực quản trị điều h&agrave;nh đ&atilde; đưa VBI đạt nhiều th&agrave;nh t&iacute;ch đ&aacute;ng ghi nhận.</p>\r\n<p><img src=\"../../../uploads/Sao-do-VBI.jpg\" alt=\"\" width=\"340\" height=\"242\" /></p>\r\n<p style=\"text-align: center;\">Tổng Gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng nhận giải thưởng 100 doanh nh&acirc;n trẻ ti&ecirc;u biểu.</p>\r\n<p><img src=\"../../../uploads/Award.jpg\" alt=\"\" width=\"1366\" height=\"1012\" /></p>\r\n<p>Ph&aacute;t biểu tại Lễ trao thưởng, Ph&oacute; Thủ tướng Vũ Văn Ninh biểu dương v&agrave; đ&aacute;nh gi&aacute; cao những tấm gương ti&ecirc;u biểu của thế hệ doanh nh&acirc;n mới. Ph&oacute; Thủ tướng nhấn mạnh: &ldquo;Ch&iacute;nh phủ sẽ tiếp tục hỗ trợ mạnh mẽ hơn nữa trong sản xuất kinh doanh, gi&uacute;p doanh nghiệp th&aacute;o gỡ kh&oacute; khăn, doanh nh&acirc;n l&agrave;m gi&agrave;u ch&iacute;nh đ&aacute;ng, ph&aacute;t triển doanh nghiệp lớn mạnh, bền vững&rdquo;.</p>\r\n<p>Qua 15 năm (1999 - 2014), sau 9 lần tổ chức, Giải thưởng đ&atilde; t&ocirc;n vinh gần 300 doanh nh&acirc;n trẻ. &Ocirc;ng Mai Hữu T&iacute;n, Chủ tịch Hội Doanh nh&acirc;n trẻ Việt Nam cho biết: Danh s&aacute;ch năm nay thực sự ấn tượng với sự c&oacute; mặt của những doanh nh&acirc;n trẻ, s&aacute;ng tạo, c&oacute; năng lực cạnh tranh, c&oacute; tinh thần vượt kh&oacute;, tinh thần v&igrave; cộng đồng, tr&aacute;ch nhiệm x&atilde; hội, khẳng định vai tr&ograve; quan trọng của doanh nh&acirc;n trẻ Việt Nam trong sự nghiệp c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a đất nước.</p>', '2018-03-21 04:38:33', '2018-03-21 15:38:33', 'images/news/1520412506_Sao-do-VB_635452938865111154_HasThumb_Thumb.jpg.png'),
(102, 2, 'VBI chi trả bồi thường hơn 1,5 tỷ đồng cho Công ty Tôn Phương Nam', 'vbi-chi-tra-boi-thuong-hon-1-5-ty-dong-cho-cong-ty-ton-phuong-nam', '<p><span style=\"font-family: Arial;\">Nhằm ph&ograve;ng ngừa c&aacute;c rủi ro c&oacute; thể mang lại để an t&acirc;m sản xuất, C&ocirc;ng ty T&ocirc;n Phương Nam đ&atilde; tin tưởng v&agrave; tham gia sử dụng Bảo hiểm Mọi rủi ro t&agrave;i sản của VBI trong nhiều năm liền.&nbsp;</span></p>', '<p>C&ocirc;ng ty T&ocirc;n Phương Nam &ndash; một trong những kh&aacute;ch h&agrave;ng tham gia bảo hiểm tại Bảo hiểm VietinBank (VBI) được biết đến l&agrave; C&ocirc;ng ty đ&atilde; v&agrave; đang l&agrave; đơn vị dẫn đầu trong lĩnh vực SXKD t&ocirc;n mạ kẽm v&agrave; t&ocirc;n mạ m&agrave;u ở Việt Nam với nhiều d&acirc;y chuyền m&aacute;y m&oacute;c thiết bị hiện đại, bao gồm: D&acirc;y chuyền mạ kẽm - c&ocirc;ng suất: 100.000 tấn/năm; D&acirc;y chuyền mạ m&agrave;u - c&ocirc;ng suất: 70.000 tấn/năm.</p>\r\n<p>Nhằm ph&ograve;ng ngừa c&aacute;c rủi ro c&oacute; thể mang lại để an t&acirc;m sản xuất, C&ocirc;ng ty T&ocirc;n Phương Nam đ&atilde; tin tưởng v&agrave; tham gia sử dụng Bảo hiểm Mọi rủi ro t&agrave;i sản của VBI trong nhiều năm liền.</p>\r\n<p><img src=\"../../../uploads/khu vuc san xuat Ton Phuong Nam.jpg\" alt=\"\" width=\"489\" height=\"273\" /></p>\r\n<p style=\"text-align: center;\"><em>Ảnh: khu vực sản xuất của C&ocirc;ng ty T&ocirc;n Phương Nam</em></p>\r\n<p>Ng&agrave;y 24/03/2014, trong qu&aacute; tr&igrave;nh sản xuất hơi dung m&ocirc;i t&iacute;ch tụ b&ecirc;n trong buồng sấy t&ocirc;n th&agrave;nh phẩm vượt ngưỡng cho ph&eacute;p gặp ngọn lửa trần từ b&eacute;c đốt g&acirc;y ch&aacute;y b&ecirc;n trong buồng sấy tạo &aacute;p suất cao sau đ&oacute; ph&aacute;t nổ. Nghe thấy c&oacute; tiếng nổ lớn tại l&ograve; sấy, anh L&ecirc; Văn L&ecirc;n (c&ocirc;ng nh&acirc;n nh&agrave; m&aacute;y) v&agrave; anh Trịnh Xu&acirc;n An (Trưởng ca sản xuất) đ&atilde; lập tức tiến h&agrave;nh kiểm tra v&agrave; huy động c&aacute;c c&ocirc;ng nh&acirc;n đang l&agrave;m việc tại d&acirc;y chuyền mạ v&agrave; sơn đến để tham gia dập lửa. Khoảng 01h35 c&ugrave;ng ng&agrave;y, c&aacute;c c&ocirc;ng nh&acirc;n đ&atilde; kiểm so&aacute;t v&agrave; dập tắt đ&aacute;m ch&aacute;y.</p>\r\n<p>Nhận được th&ocirc;ng tin, VBI đ&atilde; kết hợp c&ugrave;ng C&ocirc;ng ty gi&aacute;m định VietAdjuster thực hiện gi&aacute;m định cho tổn thất tr&ecirc;n, mức độ tổn thất của vụ nổ được x&aacute;c định đ&atilde; l&agrave;m hư hỏng một phần d&acirc;y chuyền mạ m&agrave;u, bao gồm: Phần vỏ l&ograve; sấy bị vỡ tung, c&aacute;c ống dẫn kh&iacute; bị nổ bung, biến dạng; Ph&ograve;ng sơn th&agrave;nh phẩm bị ch&aacute;y; M&aacute;y sơn đặt trong ph&ograve;ng sơn th&agrave;nh phẩm bị ch&aacute;y, c&ugrave;ng với c&aacute;c m&aacute;y m&oacute;c, thiết bị kh&aacute;c thuộc d&acirc;y chuyền mạ m&agrave;u bị hư hỏng, ảnh hưởng.</p>\r\n<p>Sau khi c&oacute; kết quả gi&aacute;m định về thiệt hại, VBI đ&atilde; nhanh ch&oacute;ng ho&agrave;n tất thủ tục để bồi thường cho C&ocirc;ng ty T&ocirc;n Phương Nam với số tiền l&ecirc;n tới 1.517.974.173 đồng. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng, VBI đ&atilde; v&agrave; đang khẳng định được uy t&iacute;n của tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</p>\r\n<p style=\"text-align: right;\">VBI</p>', '2018-03-21 04:39:04', '2018-03-21 15:39:04', 'images/news/1520413069_khu-vuc-s_635456202615587110_HasThumb_Thumb.jpg.png'),
(103, 2, 'VBI Sài Gòn giao lưu bóng đá chào mừng quốc khánh', 'vbi-sai-gon-giao-luu-bong-da-chao-mung-quoc-khanh', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank CN S&agrave;i G&ograve;n đ&atilde; phối hợp c&ugrave;ng VietinBank CN T&acirc;y Tiền Giang tổ chức giao lưu b&oacute;ng đ&aacute; giải tứ h&ugrave;ng v&agrave;o ng&agrave;y 30/08/2014, tại s&acirc;n b&oacute;ng đ&aacute; Kim Phượng (Cai Lậy - Tiền Giang).&nbsp;</span></p>', '<p>Nh&acirc;n kỷ niệm 69 năm ng&agrave;y Quốc kh&aacute;nh (02/09/1945 &ndash; 02/09/2014); nhằm tạo s&acirc;n chơi thể thao l&agrave;nh mạnh, r&egrave;n luyện sức khỏe cho CBNV chi nh&aacute;nh cũng như tăng cường giao lưu tạo mối quan hệ bền vững với c&aacute;c đơn vị trong hệ thống VietinBank; c&aacute;c kh&aacute;ch h&agrave;ng doanh nghiệp, c&aacute; nh&acirc;n tr&ecirc;n địa b&agrave;n Cai Lậy, Bảo hiểm VietinBank CN S&agrave;i G&ograve;n (VBI CN SG) đ&atilde; phối hợp c&ugrave;ng VietinBank CN T&acirc;y Tiền Giang tổ chức giao lưu b&oacute;ng đ&aacute; giải tứ h&ugrave;ng v&agrave;o ng&agrave;y 30/08/2014, tại s&acirc;n b&oacute;ng đ&aacute; Kim Phượng (Cai Lậy - Tiền Giang).</p>\r\n<p>Tới tham dự giao lưu gồm 4 đội: VietinBank T&acirc;y Tiền Giang, VietinBank Vĩnh Long, VBI &ndash; CN S&agrave;i G&ograve;n v&agrave; Li&ecirc;n qu&acirc;n kh&aacute;ch h&agrave;ng tr&ecirc;n địa b&agrave;n Cai Lậy; cổ vũ cho c&aacute;c đội chơi c&oacute; c&aacute;c đồng ch&iacute; Ban L&atilde;nh đạo v&agrave; c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n của c&aacute;c đơn vị. C&aacute;c trận đấu đ&atilde; diễn ra trong kh&ocirc;ng kh&iacute; s&ocirc;i nổi, fairplay với sự cổ vũ nhiệt t&igrave;nh của c&aacute;c cổ động vi&ecirc;n.</p>\r\n<p>Kết quả chung cuộc: đội VietinBank Vĩnh Long đạt chức v&ocirc; địch, đội Li&ecirc;n qu&acirc;n kh&aacute;ch h&agrave;ng tr&ecirc;n địa b&agrave;n Cai Lậy đạt giải nh&igrave;, đội VietinBank T&acirc;y Tiền Giang v&agrave; đội VBI &ndash; CN S&agrave;i G&ograve;n đồng giải ba.</p>\r\n<p><img src=\"../../../uploads/giao luu da bong.jpg\" alt=\"\" width=\"623\" height=\"465\" /></p>\r\n<p>Buổi thi đấu giao hữu b&oacute;ng đ&aacute; thực sự l&agrave; s&acirc;n chơi bổ &iacute;ch, v&agrave; l&agrave; m&ocirc;i trường văn h&oacute;a l&agrave;nh mạnh, tạo điều kiện cho CBNV của c&aacute;c đơn vị c&oacute; cơ hội giao lưu, học hỏi c&aacute;c kỹ năng v&agrave; kinh nghiệm trong m&ocirc;n thể thao vua, đồng thời qua đ&oacute; tăng cường mối quan hệ với kh&aacute;ch h&agrave;ng, tinh thần đo&agrave;n kết tương trợ lẫn nhau trong c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n giữa c&aacute;c đơn vị.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI SG</strong></em></p>', '2018-03-21 04:39:28', '2018-03-21 15:39:28', 'images/news/1520413160_giao-luu-_635457925167269276_HasThumb_Thumb.jpg.png'),
(104, 2, 'Công bố quyết định công tác cán bộ tại Bảo hiểm VietinBank', 'cong-bo-quyet-dinh-cong-tac-can-bo-tai-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Với hơn 30 năm c&ocirc;ng t&aacute;c tại VietinBank, trong đ&oacute; c&oacute; 10 năm tr&ecirc;n cương vị Ph&oacute; Tổng Gi&aacute;m đốc, Kế to&aacute;n Trưởng C&ocirc;ng ty Bảo hiểm VietinBank, b&agrave; Trần Thị Kim Yến lu&ocirc;n t&acirc;m huyết v&agrave; ho&agrave;n th&agrave;nh xuất sắc mọi nhiệm vụ.&nbsp;</span></p>', '<p>Chiều 04/09, tại H&agrave; Nội đ&atilde; diễn ra Lễ c&ocirc;ng bố quyết định của HĐQT VietinBank về c&ocirc;ng t&aacute;c c&aacute;n bộ tại Bảo hiểm VietinBank (VBI). Dự lễ c&oacute; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI c&ugrave;ng to&agrave;n thể ban l&atilde;nh đạo v&agrave; c&aacute;n bộ VBI trụ sở ch&iacute;nh. Theo quyết định của HĐQT, b&agrave; Trần Thị Kim Yến &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VietinBank được hưởng chế độ nghỉ hưu theo quy định của Nh&agrave; nước.</p>\r\n<p>B&agrave; Trần Thị Kim Yến đ&atilde; c&oacute; qu&aacute; tr&igrave;nh 30 năm c&ocirc;ng t&aacute;c v&agrave; cống hiến tại VietinBank từ năm 1984 đến nay. Với 10 năm tr&ecirc;n cương vị Ph&oacute; Tổng Gi&aacute;m đốc v&agrave; Kế to&aacute;n Trưởng VBI, b&agrave; Trần Thị Kim Yến được c&aacute;c thế hệ l&atilde;nh đạo VietinBank đ&aacute;nh gi&aacute; l&agrave; c&aacute;n bộ quản l&yacute; c&oacute; phẩm chất đạo đức tốt, gương mẫu v&agrave; tận tụy. Tại cương vị n&agrave;o b&agrave; Yến cũng lu&ocirc;n t&acirc;m huyết v&agrave; ho&agrave;n th&agrave;nh xuất sắc mọi nhiệm vụ được giao.</p>\r\n<p><img src=\"../../../uploads/chi yen.jpg\" alt=\"\" width=\"1047\" height=\"1039\" /></p>\r\n<p style=\"text-align: center;\"><em>Tổng Gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng trao quyết định nghỉ hưu v&agrave; tặng hoa cho b&agrave; Trần Thị Kim Yến</em></p>\r\n<p>Tổng gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng ch&uacute;c mừng b&agrave; Trần Thị Kim Yến đ&atilde; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ trong qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c; gửi lời cảm ơn, l&ograve;ng cảm phục v&agrave; niềm k&iacute;nh trọng v&igrave; những đ&oacute;ng g&oacute;p, cống hiến t&iacute;ch cực, hiệu quả của b&agrave; đối với qu&aacute; tr&igrave;nh x&acirc;y dựng v&agrave; ph&aacute;t triển của VBI n&oacute;i ri&ecirc;ng v&agrave; sự nghiệp x&acirc;y dựng, đổi mới v&agrave; ph&aacute;t triển của VBI n&oacute;i ri&ecirc;ng v&agrave; VietinBank n&oacute;i chung.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:39:51', '2018-03-21 15:39:51', 'images/news/1520413276_chi-ye_635461298209107222_HasThumb_Thumb.jpg.png'),
(105, 2, 'Tuổi trẻ VBI tham gia Ngày hội hiến máu tình nguyện', 'tuoi-tre-vbi-tham-gia-ngay-hoi-hien-mau-tinh-nguyen', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 12/09/2014, Bảo hiểm VietinBank đ&atilde; tham gia Ng&agrave;y hội hiến m&aacute;u t&igrave;nh nguyện mang t&ecirc;n &ldquo;H&agrave;nh tr&igrave;nh Đỏ - Kết nối d&ograve;ng m&aacute;u Việt&rdquo;.&nbsp;</span></p>', '<p>Ng&agrave;y 12/09/2014, Bảo hiểm VietinBank (VBI) đ&atilde; tham gia Ng&agrave;y hội hiến m&aacute;u t&igrave;nh nguyện mang t&ecirc;n &ldquo;H&agrave;nh tr&igrave;nh Đỏ - Kết nối d&ograve;ng m&aacute;u Việt&rdquo; do Đo&agrave;n thanh ni&ecirc;n Ng&acirc;n h&agrave;ng Trung ương tổ chức. Đ&acirc;y l&agrave; một hoạt động thiết thực hướng ứng năm Thanh ni&ecirc;n t&igrave;nh nguyện 2014 của của Đo&agrave;n thanh ni&ecirc;n VBI.</p>\r\n<p><img src=\"../../../uploads/hien mau.jpg\" alt=\"\" width=\"1366\" height=\"1106\" /></p>\r\n<p style=\"text-align: center;\"><em>H&igrave;nh ảnh tại Ng&agrave;y hội hiến m&aacute;u t&igrave;nh nguyện</em></p>\r\n<p>Trong những năm qua b&ecirc;n cạnh việc chăm lo ph&aacute;t triển chuy&ecirc;n m&ocirc;n của m&igrave;nh, th&igrave; VBI lu&ocirc;n quan t&acirc;m tới c&aacute;c c&ocirc;ng t&aacute;c x&atilde; hội, c&aacute;c hoạt động từ thiện trong đ&oacute; đặc biệt ch&uacute; trọng tới việc hiến m&aacute;u nh&acirc;n đạo, v&agrave; việc l&agrave;m n&agrave;y đ&atilde; được Ban l&atilde;nh đạo VBI tiến h&agrave;nh thường ni&ecirc;n. Với t&igrave;nh cảm nh&acirc;n &aacute;i cao đẹp v&agrave; sự chia sẻ đ&oacute;, đ&atilde; c&oacute; biết bao để người bệnh c&oacute; th&ecirc;m cơ hội được cứu sống. <br /><br /></p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:40:15', '2018-03-21 15:40:15', 'images/news/1520413383_hien-ma_635461299886326924_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(106, 2, 'Đại hội Đoàn thanh niên Bảo hiểm VietinBank nhiệm kỳ 2014 -2017', 'dai-hoi-doan-thanh-nien-bao-hiem-vietinbank-nhiem-ky-2014-2017', '<p><span style=\"font-family: Arial;\">Đại hội Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank, nhiệm kỳ 2014-2017 diễn ra v&agrave;o l&uacute;c tuổi trẻ Vietinbank v&agrave; tuổi trẻ cả nước đang s&ocirc;i nổi thực hiện năm thanh ni&ecirc;n t&igrave;nh nguyện 2014.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Chiều 18/09 tại H&agrave; Nội, Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank tổ chức Đại hội Đo&agrave;n lần thứ II, nhiệm kỳ 2014 - 2017. Tới dự v&agrave; chỉ đạo Đại hội c&oacute; Ph&oacute; B&iacute; thư Đo&agrave;n VietinBank Nguyễn Trung Hậu, Ban Gi&aacute;m đốc VBI, BCH C&ocirc;ng đo&agrave;n VBI v&agrave; 45 đại biểu, đại diện cho 68 đo&agrave;n vi&ecirc;n thanh ni&ecirc;n (ĐVTN) đang sinh hoạt tại Chi đo&agrave;n Bảo hiểm.<br /> <br />Đại hội Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank, nhiệm kỳ 2014-2017 diễn ra v&agrave;o l&uacute;c tuổi trẻ Vietinbank v&agrave; tuổi trẻ cả nước đang s&ocirc;i nổi thực hiện năm thanh ni&ecirc;n t&igrave;nh nguyện 2014. Đại hội Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank lần n&agrave;y c&oacute; nhiệm vụ tổng kết thực hiện Nghị quyết Đại hội nhiệm kỳ 2012 - 2014, đề ra phương hướng, nhiệm vụ nhiệm kỳ 2014-2017; kiểm điểm sự l&atilde;nh đạo của Ban Chấp h&agrave;nh Chi đo&agrave;n nhiệm kỳ trước v&agrave; bầu Ban Chấp h&agrave;nh Chi đo&agrave;n nhiệm kỳ 2014-2017.<br /> <br />Với số ĐVTN đ&ocirc;ng đảo, chiếm 52,7% lao động to&agrave;n C&ocirc;ng ty, nhiệm kỳ vừa qua ĐVTN thực sự l&agrave; lực lượng trẻ, nhiệt t&igrave;nh, xung k&iacute;ch trong c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n, đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o kết quả hoạt động kinh doanh. B&ecirc;n cạnh đ&oacute;, Chi đo&agrave;n thường xuy&ecirc;n, chủ động tổ chức gi&aacute;o dục truyền thống th&ocirc;ng qua c&aacute;c hoạt động x&atilde; hội, đền ơn, đ&aacute;p nghĩa, uống nước nhớ nguồn như: thăm v&agrave; tặng qu&agrave; cho c&aacute;c gia đ&igrave;nh ch&iacute;nh s&aacute;ch; c&aacute;c ch&aacute;u c&oacute; ho&agrave;n cảnh đặc biệt; c&aacute;c thương binh nặng tại TT Thuận Th&agrave;nh; tặng qu&agrave; cho b&agrave; con v&ugrave;ng lũ tại H&agrave; Tĩnh; tham gia c&ugrave;ng Đo&agrave;n thanh ni&ecirc;n VietinBank tổ chức ph&aacute;t ch&aacute;o miễn ph&iacute; tại Viện Nhi Trung Ương.</span></p>\r\n<p><img src=\"../../../uploads/IMG_0267.jpg\" alt=\"\" width=\"1366\" height=\"838\" /></p>\r\n<p><span style=\"font-family: Arial;\">Đo&agrave;n vi&ecirc;n thanh ni&ecirc;n tại VBI l&agrave; đội ngũ được đ&agrave;o tạo cơ bản, c&oacute; tr&igrave;nh độ, v&agrave; l&ograve;ng nhiệt huyết với ng&agrave;nh, với nghề c&oacute; tinh thần tr&aacute;ch nhiệm trong việc thực hiện nhiệm vụ chuy&ecirc;n m&ocirc;n; thanh ni&ecirc;n c&oacute; mặt ở hầu hết c&aacute;c mảng c&ocirc;ng việc, đặc biệt l&agrave; c&aacute;c lĩnh vực then chốt v&agrave; mũi nhọn như: Quản l&yacute; nghiệp vụ, T&aacute;i bảo hiểm, Bồi thường, Kinh doanh, &hellip;<br /> <br />Đại hội đ&atilde; bầu BCH Chi Đo&agrave;n nhiệm kỳ 2014-2017 gồm 7 đồng ch&iacute;: Nguyễn Thế Anh, Trần Tr&iacute; Cương, Ho&agrave;ng Thị Nhung, B&ugrave;i Thanh Tuyền, Nguyễn Thị T&uacute; Uy&ecirc;n, Cao Hải V&acirc;n v&agrave; L&ecirc; Quang Vinh. Đại hội Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank nhiệm kỳ 2014 - 2017 k&ecirc;u gọi to&agrave;n thể đo&agrave;n vi&ecirc;n thanh ni&ecirc;n quyết t&acirc;m khắc phục mọi kh&oacute; khăn, xung phong t&igrave;nh nguyện thực hiện thắng lợi nhiệm vụ ch&iacute;nh trị cũng như nhiệm vụ chuy&ecirc;n m&ocirc;n, g&oacute;p phần xứng đ&aacute;ng v&agrave;o sự ph&aacute;t triển chung của Vietinbank.<br /></span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 04:40:43', '2018-03-21 15:40:43', 'images/news/1520413555_IMG_026_635467388452413975_HasThumb_Thumb.JPG.png'),
(107, 2, 'Bảo hiểm VietinBank chi trả 100 triệu đồng tiền bảo hiểm cho khách hàng tại Hải Phòng', 'bao-hiem-vietinbank-chi-tra-100-trieu-dong-tien-bao-hiem-cho-khach-hang-tai-hai-phong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 23/09/2014, C&ocirc;ng ty Bảo hiểm VietinBank c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh L&ecirc; Ch&acirc;n tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 100 triệu đồng cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Thị Hiền.&nbsp;</span></p>', '<p>Ng&agrave;y 23/09/2014, C&ocirc;ng ty Bảo hiểm VietinBank c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh L&ecirc; Ch&acirc;n tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 100 triệu đồng cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Thị Hiền.</p>\r\n<p>Đầu th&aacute;ng 6 năm 2014, kh&aacute;ch h&agrave;ng Nguyễn Thị Hiền c&oacute; vay 100 triệu tại VietinBank CN L&ecirc; Ch&acirc;n để sửa nh&agrave;, đồng thời tham gia sản phẩm bảo hiểm VietinCare để đảm bảo an to&agrave;n cho người đi vay. V&agrave;o ng&agrave;y 25/07/2014, tr&ecirc;n đường đi c&ocirc;ng t&aacute;c, kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp tai nạn dẫn đến tử vong.<br /> <br />Sau khi nhận được th&ocirc;ng tin từ gia đ&igrave;nh cũng như ng&acirc;n h&agrave;ng, Bảo hiểm VietinBank chi nh&aacute;nh Hải Ph&ograve;ng đ&atilde; phối hợp với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh L&ecirc; Ch&acirc;n c&ugrave;ng với gia đ&igrave;nh thu thập hồ sơ để l&agrave;m thủ tục bồi thường cho kh&aacute;ch h&agrave;ng. Tới ng&agrave;y 23/09/2014, gia đ&igrave;nh kh&aacute;ch h&agrave;ng Nguyễn Thị Hiền đ&atilde; nhận được 100 triệu tiền bồi thường từ C&ocirc;ng ty bảo hiểm VietinBank.</p>\r\n<p><img src=\"../../../uploads/Boi thuong chi HIen.jpg\" alt=\"\" width=\"1366\" height=\"1047\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh đại diện VBI v&agrave; VietinBank trao tiền bảo hiểm cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng Nguyễn Thị Hiền)</em></p>\r\n<p>Về ph&iacute;a gia đ&igrave;nh kh&aacute;ch h&agrave;ng, mẹ của chị Hiền c&oacute; chia sẻ: &ldquo;Con g&aacute;i t&ocirc;i c&oacute; tham gia g&oacute;i bảo hiểm của Bảo hiểm VietinBank nhưng kh&ocirc;ng may con t&ocirc;i gặp tai nạn qua đời đột ngột. Ngay sau khi nhận được tin buồn từ ph&iacute;a gia đ&igrave;nh, C&ocirc;ng ty Bảo hiểm VietinBank Chi nh&aacute;nh Hải Ph&ograve;ng đ&atilde; cử người qua thăm viếng v&agrave; chi trả số tiền bồi thường 100 triệu cho gia đ&igrave;nh. Đ&acirc;y l&agrave; một nghĩa cử rất đẹp của c&aacute;c anh chị b&ecirc;n Bảo hiểm VietinBank. T&ocirc;i xin đại diện gia đ&igrave;nh cảm ơn Bảo hiểm VietinBank.&rdquo;<br /> <br />Về ph&iacute;a Ng&acirc;n h&agrave;ng VietinBank, &ocirc;ng Trần Việt H&ugrave;ng &ndash; Ph&oacute; Gi&aacute;m đốc chi nh&aacute;nh L&ecirc; Ch&acirc;n cho biết: &ldquo;Bảo hiểm con người VietinCare l&agrave; một sản phẩm rất ưu việt. Sản phẩm đ&atilde; gi&uacute;p giảm thiểu tổn thất cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng, gi&uacute;p gia đ&igrave;nh trang trải nợ vay. Về ph&iacute;a ng&acirc;n h&agrave;ng, ch&uacute;ng t&ocirc;i cũng thu được nợ v&agrave; l&atilde;i tiền vay. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i kh&ocirc;ng phải xử l&yacute; nợ xấu hay l&agrave;m c&aacute;c biện ph&aacute;p để thu hồi nợ. Sản phẩm bảo hiểm con người VietinCare đ&atilde; mang lại lợi &iacute;ch cho rất nhiều b&ecirc;n. T&ocirc;i mong muốn tiếp tục được phối hợp với Bảo hiểm VietinBank trong việc kết hợp hoạt động t&iacute;n dụng ng&acirc;n h&agrave;ng với hoạt động bảo hiểm trong thời gian tới.&rdquo;<br /> <br />C&oacute; thể n&oacute;i sản phẩm bảo hiểm VietinCare rất c&oacute; &yacute; nghĩa với người vay vốn tại ng&acirc;n h&agrave;ng VietinBank. Sản phẩm đảm bảo ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng gặp rủi ro. Qua vụ việc n&agrave;y, Bảo hiểm VietinBank đ&atilde; chứng tỏ được sự ưu việt của sản phẩm cũng như sự tận t&acirc;m của c&aacute;c c&aacute;n bộ d&agrave;nh cho kh&aacute;ch h&agrave;ng.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:41:19', '2018-03-21 15:41:19', 'images/news/1520413645_Boi-thuon_635472519965564546_HasThumb_Thumb.jpg.png'),
(108, 2, 'VBI bổ nhiệm Phó Phòng Phát triển kinh doanh', 'vbi-bo-nhiem-pho-phong-phat-trien-kinh-doanh', '<p><span style=\"font-family: Arial;\">Theo quyết định bổ nhiệm, &ocirc;ng Phạm Việt H&ugrave;ng giữ chức vụ Ph&oacute; Ph&ograve;ng Ph&aacute;t triển kinh doanh. &Ocirc;ng Phạm Việt H&ugrave;ng đ&atilde; c&oacute; hơn 14 năm kinh nghiệm trong ng&agrave;nh t&agrave;i ch&iacute;nh v&agrave; được đ&aacute;nh gi&aacute; l&agrave; một c&aacute;n bộ đầy nhiệt huyết v&agrave; mẫn c&aacute;n trong c&ocirc;ng việc.&nbsp;</span></p>', '<p>VBI Bổ nhiệm Ph&oacute; Ph&ograve;ng Ph&aacute;t triển kinh doanh<br /> <br />S&aacute;ng 03/10 tại H&agrave; Nội, đ&atilde; diễn ra lễ c&ocirc;ng bố quyết định bổ nhiệm Ph&oacute; ph&ograve;ng Ph&aacute;t triển kinh doanh tại Bảo hiểm VietinBank (VBI). Tham dự buổi lễ c&oacute; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng gi&aacute;m đốc VBI c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n trong Ban l&atilde;nh đạo VBI.</p>\r\n<p>Theo quyết định bổ nhiệm, &ocirc;ng Phạm Việt H&ugrave;ng giữ chức vụ Ph&oacute; Ph&ograve;ng Ph&aacute;t triển kinh doanh. &Ocirc;ng Phạm Việt H&ugrave;ng đ&atilde; c&oacute; hơn 14 năm kinh nghiệm trong ng&agrave;nh t&agrave;i ch&iacute;nh v&agrave; được đ&aacute;nh gi&aacute; l&agrave; một c&aacute;n bộ đầy nhiệt huyết v&agrave; mẫn c&aacute;n trong c&ocirc;ng việc.</p>\r\n<p><img src=\"../../../uploads/bo nhiem anh hung.jpg\" alt=\"\" width=\"1366\" height=\"903\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ban l&atilde;nh đạo VBI trao quyết định bổ nhiệm Ph&oacute; Ph&ograve;ng Ph&aacute;t triển kinh doanh)</em></p>\r\n<p>Tại buổi lễ, Tổng gi&aacute;m đốc C&ocirc;ng ty, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm, ch&uacute;c mừng &ocirc;ng Phạm Việt H&ugrave;ng nhận nhiệm vụ mới, đồng thời n&ecirc;u r&otilde;: Việc bổ nhiệm vị tr&iacute; l&atilde;nh đạo lần n&agrave;y c&oacute; &yacute; nghĩa chiến lược trong việc kiện to&agrave;n bộ m&aacute;y tổ chức của Bảo hiểm VietinBank, g&oacute;p phần v&agrave;o định hướng ph&aacute;t triển mới của C&ocirc;ng ty.</p>\r\n<p>Ph&aacute;t biểu nhận nhiệm vụ, &ocirc;ng Phạm Việt H&ugrave;ng gửi lời cảm ơn tới Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; tin tưởng giao ph&oacute; trọng tr&aacute;ch mới, đồng thời cam kết sẽ tiếp tục phấn đấu để tiếp tục cống hiến, đưa Bảo hiểm VietinBank trở th&agrave;nh C&ocirc;ng ty bảo hiểm đ&aacute;ng tin cậy tr&ecirc;n thị trường, đồng thời g&oacute;p phần v&agrave;o việc thực hiện kế hoạch ph&aacute;t triển chung của VietinBank.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:41:47', '2018-03-21 15:41:47', 'images/news/1520415415_bo-nhiem-_635479503448174668_HasThumb_Thumb.jpg.png'),
(109, 2, 'Bảo hiểm VietinBank khai trương Chi nhánh Thăng Long', 'bao-hiem-vietinbank-khai-truong-chi-nhanh-thang-long', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 17/10/2014, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ khai trương Chi nh&aacute;nh Thăng Long tại tầng 5, số 46 Phố Vọng, Phường Phương Mai, Quận Đống Đa, Th&agrave;nh phố H&agrave; Nội.&nbsp;</span></p>', '<p>Ng&agrave;y 17/10/2014, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ khai trương Chi nh&aacute;nh Thăng Long tại tầng 5, số 46 Phố Vọng, Phường Phương Mai, Quận Đống Đa, Th&agrave;nh phố H&agrave; Nội. Tham dự buổi lễ c&oacute; đại diện Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh Thăng Long.</p>\r\n<p>Tại buổi lễ, Tổng gi&aacute;m đốc VBI, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm Gi&aacute;m đốc Chi nh&aacute;nh Thăng Long cho &ocirc;ng Nguyễn Ph&uacute; B&igrave;nh v&agrave; bổ nhiệm 9 vị tr&iacute; l&atilde;nh đạo cấp ph&ograve;ng cho &ocirc;ng Vũ Văn V&otilde;, &ocirc;ng L&ecirc; Thanh T&ugrave;ng, &ocirc;ng Nguyễn Văn Trường, &ocirc;ng Phạm Trung Th&agrave;nh, b&agrave; Phạm Thị Hải H&ograve;a, b&agrave; Vũ Thị Ng&agrave;, &ocirc;ng Vũ Viết Dĩnh, &ocirc;ng Ng&ocirc; Hữu Ph&uacute;c v&agrave; &ocirc;ng H&agrave; Th&aacute;i. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ mong muốn &ocirc;ng Nguyễn Ph&uacute; B&igrave;nh c&ugrave;ng to&agrave;n thể c&aacute;n bộ được bổ nhiệm sẽ điều h&agrave;nh VBI Thăng Long ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh.</p>\r\n<p>Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng Nguyễn Ph&uacute; B&igrave;nh c&ugrave;ng c&aacute;c c&aacute;n bộ đ&atilde; thể hiện quyết t&acirc;m phấn đấu để VBI Thăng Long ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đồng thời cam kết sẽ tiếp tục cống hiến, đưa VBI Thăng Long ph&aacute;t triển mạnh mẽ.</p>\r\n<p><img src=\"../../../uploads/IMG_0379.jpg\" alt=\"\" width=\"1366\" height=\"1187\" /></p>\r\n<p style=\"text-align: center;\"><em>Tổng gi&aacute;m đốc L&ecirc; Tuấn Dũng trao quyết định bổ nhiệm cho &ocirc;ng Nguyễn Ph&uacute; B&igrave;nh</em></p>\r\n<p><img src=\"../../../uploads/IMG_0414.jpg\" alt=\"\" width=\"1366\" height=\"869\" /></p>\r\n<p style=\"text-align: center;\"><em>Ban l&atilde;nh đạo VBI chụp ảnh c&ugrave;ng c&aacute;c c&aacute;n bộ mới được bổ nhiệm tại Chi nh&aacute;nh Thăng Long</em></p>', '2018-03-21 04:42:38', '2018-03-21 15:42:38', 'images/news/1520415535_IMG_041_635494150556997574_HasThumb_Thumb.JPG.png'),
(110, 2, 'VBI Tây Nguyên kỷ niệm 1 năm thành lập chi nhánh', 'vbi-tay-nguyen-ky-niem-1-nam-thanh-lap-chi-nhanh', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 14/11/2014, tại T&acirc;y Nguy&ecirc;n, C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; tổ chức &ldquo;Lễ kỷ niệm 1 năm th&agrave;nh lập chi nh&aacute;nh T&acirc;y Nguy&ecirc;n&rdquo;. Buổi lễ c&oacute; sự tham dự của đại diện Ng&acirc;n h&agrave;ng nh&agrave; nước Chi nh&aacute;nh Đắc Lắc, Ban l&atilde;nh đạo VBI,...&nbsp;</span></p>', '<p>Ng&agrave;y<span style=\"font-family: Arial;\"> 14/11/2014, tại T&acirc;y Nguy&ecirc;n, C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; tổ chức &ldquo;Lễ kỷ niệm 1 năm th&agrave;nh lập chi nh&aacute;nh T&acirc;y Nguy&ecirc;n&rdquo;. Buổi lễ c&oacute; sự tham dự của đại diện Ng&acirc;n h&agrave;ng nh&agrave; nước Chi nh&aacute;nh Đắc Lắc, Ban l&atilde;nh đạo VBI, to&agrave;n thể c&aacute;n bộ nh&acirc;n vi&ecirc;n chi nh&aacute;nh T&acirc;y Nguy&ecirc;n, c&ugrave;ng c&aacute;c đối t&aacute;c kh&aacute;ch h&agrave;ng của chi nh&aacute;nh. <br /> <br />Chi nh&aacute;nh VBI T&acirc;y Nguy&ecirc;n được th&agrave;nh lập ng&agrave;y 14/11/2014. Trong 1 năm qua, VBI T&acirc;y Nguy&ecirc;n đ&atilde; khẳng định được thương hiệu tr&ecirc;n địa b&agrave;n, kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng sử dụng sản phẩm do VBI cung cấp. B&ecirc;n cạnh đ&oacute;, VBI T&acirc;y Nguy&ecirc;n cũng tổ chức nhiều lớp huấn luyện nghiệp vụ để triển khai b&aacute;n ch&eacute;o c&aacute;c sản phẩm bảo hiểm qua hệ thống quản l&yacute; Bancassurance; cung cấp tới kh&aacute;ch h&agrave;ng dịch vụ t&agrave;i ch&iacute;nh trọn g&oacute;i một cửa: Ng&acirc;n h&agrave;ng &ndash; Bảo hiểm. Trong thời gian tới, VBI T&acirc;y Nguy&ecirc;n sẽ tiếp tục ph&aacute;t huy những kết quả đ&atilde; đạt được, cung cấp những dịch vụ tốt nhất cho kh&aacute;ch h&agrave;ng.&nbsp;</span></p>\r\n<p><img src=\"../../../uploads/tayduyen.jpg\" alt=\"\" width=\"1239\" height=\"1157\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Gi&aacute;m đốc VBI T&acirc;y Nguy&ecirc;n - &ocirc;ng Nguyễn Cao Bằng ph&aacute;t biểu tại Lễ kỷ niệm</span></em></p>\r\n<p><img src=\"../../../uploads/PHAT BIEU TGD.jpg\" alt=\"\" width=\"1366\" height=\"842\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Tổng Gi&aacute;m đốc VBI - &ocirc;ng L&ecirc; Tuấn Dũng ph&aacute;t biểu tại Lễ kỷ niệm</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Cũng tại buổi lễ, Tổng gi&aacute;m đốc VBI &ndash; &Ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; chia sẻ một v&agrave;i th&ocirc;ng tin về kết quả kinh doanh 9 th&aacute;ng năm 2014 của VBI. D&ugrave; t&igrave;nh h&igrave;nh kinh tế năm 2014 c&ograve;n gặp nhiều kh&oacute; khăn nhưng Bảo hiểm VietinBank vẫn c&oacute; kết quả kinh doanh tăng trưởng khả quan. Cụ thể, tổng doanh thu ph&iacute; bảo hiểm 9 th&aacute;ng đầu năm 2014 của VBI tăng trưởng 182% so với năm 2013. Trong đ&oacute;, doanh thu bảo hiểm gốc đạt 159 tỷ đồng; doanh thu hoạt động nhận t&aacute;i bảo hiểm đạt 33,1 tỷ đồng, tăng trưởng 189% so với c&ugrave;ng kỳ năm 2013.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>\r\n<p>&nbsp;</p>', '2018-03-21 04:43:40', '2018-03-21 15:43:40', 'images/news/1520415657_PHAT BIEU GĐ CN TAY NGUYEN.jpg'),
(111, 2, 'Chi bộ Bảo hiểm VietinBank tổ chức lễ kết nạp Đảng viên mới', 'chi-bo-bao-hiem-vietinbank-to-chuc-le-ket-nap-dang-vien-moi', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank đ&atilde; tổ chức lễ kết nạp Đảng vi&ecirc;n mới v&agrave; chương tr&igrave;nh hướng về nguồn cội cho c&aacute;c Đảng vi&ecirc;n về thăm Th&agrave;nh cổ Quảng Trị, nghĩa trang Trường Sơn, nghĩa trang Đường 9, ng&atilde; ba Đồng Lộc v&agrave; viếng mộ Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p.&nbsp;</span></p>', '<p>Được<span style=\"font-family: Arial;\"> sự thống nhất của Đảng bộ Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam, Chi bộ C&ocirc;ng ty Bảo hiểm, v&agrave;o l&uacute;c 10h00&rsquo; ng&agrave;y 22 th&aacute;ng 11 năm 2014 tại Th&agrave;nh Cổ Quảng Trị, Chi bộ C&ocirc;ng ty đ&atilde; tiến h&agrave;nh tổ chức &ldquo;Lễ kết nạp đảng vi&ecirc;n mới&rdquo; cho 4 quần ch&uacute;ng xuất sắc v&agrave;o Đảng Cộng sản Việt Nam. Tham dự buổi lễ c&oacute; sự tham dự của đồng ch&iacute; Trần Ki&ecirc;n Cường, Ph&oacute; b&iacute; thư Đảng ủy NHCT Việt Nam, đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Chi bộ, Tổng Gi&aacute;m Đốc C&ocirc;ng ty c&ugrave;ng to&agrave;n thể c&aacute;c Đảng vi&ecirc;n của C&ocirc;ng ty Bảo hiểm VietinBank.<br /> <br />C&aacute;c quần ch&uacute;ng ưu t&uacute; được kết nạp v&agrave;o Đảng tại chi bộ C&ocirc;ng ty Bảo hiểm VietinBank đợt n&agrave;y bao gồm: đ/c L&ecirc; Thị Quỳnh Hoa &ndash; Trưởng ph&ograve;ng Ph&aacute;t triển kinh doanh, đồng ch&iacute; Nguyễn Thế Anh &ndash; Ph&oacute; Ph&ograve;ng QLNV v&agrave; ĐGRR, Nguyễn Ngọc Nam &ndash; Ph&oacute; Ph&ograve;ng QLNV v&agrave; ĐGRR, Nguyễn Việt Dũng &ndash; c&aacute;n bộ Ph&ograve;ng Kế to&aacute;n h&agrave;nh ch&iacute;nh, đều được r&egrave;n luyện v&agrave; trưởng th&agrave;nh trong m&ocirc;i trường c&ocirc;ng t&aacute;c nhiều thử th&aacute;ch, hiện đang đảm nhiệm vị tr&iacute; l&atilde;nh đạo v&agrave; l&agrave; đầu tầu trong c&aacute;c tổ chức C&ocirc;ng đo&agrave;n, Đo&agrave;n thanh ni&ecirc;n tại C&ocirc;ng ty Bảo hiểm VietinBank.</span></p>\r\n<p><img src=\"../../../uploads/anh ket nap dang.jpg\" alt=\"\" width=\"1366\" height=\"910\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(C&aacute;c quần ch&uacute;ng ưu t&uacute; được kết nạp v&agrave;o Đảng đợt n&agrave;y chụp ảnh kỷ niệm với đ/c Trần Ki&ecirc;n Cường v&agrave; đ/c L&ecirc; Tuấn Dũng)</span></em></p>\r\n<p>Ph&aacute;t biểu tại buổi lễ, Ph&oacute; B&iacute; thư Đảng ủy Trần Ki&ecirc;n Cường y&ecirc;u cầu c&aacute;c đảng vi&ecirc;n mới kết nạp lu&ocirc;n giữ g&igrave;n phẩm chất đạo đức, nhiệt t&igrave;nh trong c&ocirc;ng t&aacute;c, ph&aacute;t huy năng lực của mỗi c&aacute; nh&acirc;n để c&ugrave;ng chi bộ ho&agrave;n th&agrave;nh xuất sắc c&aacute;c nhiệm vụ được giao.<br /> <br />C&aacute;c đảng vi&ecirc;n mới đều x&uacute;c động, tự h&agrave;o khi được đứng trong h&agrave;ng ngũ của Đảng v&agrave; hứa quyết t&acirc;m tiếp tục phấn đấu hơn nữa, ph&aacute;t huy tốt vai tr&ograve; tiền phong gương mẫu tr&ecirc;n mọi lĩnh vực c&ocirc;ng t&aacute;c, g&oacute;p phần t&iacute;ch cực c&ugrave;ng Đảng bộ ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ ch&iacute;nh trị.</p>\r\n<p>Sau khi kết nạp bốn đồng ch&iacute; tr&ecirc;n, tổng số đảng vi&ecirc;n Chi bộ C&ocirc;ng ty Bảo hiểm VietinBank được n&acirc;ng l&ecirc;n th&agrave;nh 35 đảng vi&ecirc;n, trong đ&oacute; đảng vi&ecirc;n ch&iacute;nh thức l&agrave; 24 đồng ch&iacute;, đảng vi&ecirc;n dự bị 11 đồng ch&iacute;.</p>\r\n<p><img src=\"../../../uploads/anh ket nap dang 2.jpg\" alt=\"\" width=\"1366\" height=\"910\" /></p>\r\n<p style=\"text-align: center;\"><em>(To&agrave;n thể c&aacute;c Đảng vi&ecirc;n của Chi bộ C&ocirc;ng ty Bảo hiểm tham gia chương tr&igrave;nh hướng về nguồn cội)</em></p>\r\n<p>Cũng nh&acirc;n dịp n&agrave;y, C&ocirc;ng ty bảo hiểm VietinBank đ&atilde; tổ chức chương tr&igrave;nh hướng về nguồn cội cho c&aacute;c Đảng vi&ecirc;n trong C&ocirc;ng ty về thăm Th&agrave;nh cổ Quảng Trị, nghĩa trang Trường Sơn, nghĩa trang Đường 9 v&agrave; Ng&atilde; ba Đồng Lộc, đồng thời, viếng mộ Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p để tỏ l&ograve;ng biết ơn tới c&aacute;c vong linh anh h&ugrave;ng liệt sỹ đ&atilde; hy sinh v&igrave; tổ quốc.</p>', '2018-03-21 04:44:33', '2018-03-21 15:44:33', 'images/news/1520415778_anh ket nap dang.jpg'),
(112, 2, 'VBI chi trả hơn 600 triệu tiền bảo hiểm cho Công ty liên doanh Bông sen', 'vbi-chi-tra-hon-600-trieu-tien-bao-hiem-cho-cong-ty-lien-doanh-bong-sen', '<p><span style=\"font-family: Arial;\">Ngay sau khi nhận được th&ocirc;ng b&aacute;o từ kh&aacute;ch h&agrave;ng, VBI đ&atilde; nhanh ch&oacute;ng tới hiện trường v&agrave; phối hợp c&ugrave;ng C&ocirc;ng ty Gi&aacute;m định VietAdjusters tiến h&agrave;nh c&ocirc;ng t&aacute;c gi&aacute;m định bồi thường. Số tiền bồi thường 671.300.000 đồng đ&atilde; gi&uacute;p c&ocirc;ng ty khắc phục được tối đa c&aacute;c tổn thất v&agrave; kh&ocirc;i phục sản xuấ...&nbsp;</span></p>', '<p>C&ocirc;ng ty li&ecirc;n doanh B&ocirc;ng sen l&agrave; đơn vị li&ecirc;n doanh đầu ti&ecirc;n tại Việt Nam trong lĩnh vực khai th&aacute;c cảng biển Quốc tế giữa C&ocirc;ng ty VIETRANS, VOSA v&agrave; đối t&aacute;c nước ngo&agrave;i BLASCO (Ukraine). Nhằm đảm bảo an to&agrave;n sản xuất kinh doanh, c&ocirc;ng ty đ&atilde; mua Bảo hiểm Mọi rủi ro t&agrave;i sản của Bảo hiểm VietinBank (VBI).</p>\r\n<p>V&agrave;o khoảng 23h10 ng&agrave;y 20 th&aacute;ng 02 năm 2014, sau khi xuất h&agrave;ng cho t&agrave;u Vinashin Liner 1, nh&acirc;n vi&ecirc;n l&aacute;i xe n&acirc;ng điều kiển xe n&acirc;ng Kalmar v&agrave;o b&atilde;i container để nghỉ giữa ca. Sau đ&oacute;, đến khoảng 23h30 c&ugrave;ng ng&agrave;y, bảo vệ c&ocirc;ng ty ph&aacute;t hiện c&oacute; lửa ch&aacute;y dưới gầm m&aacute;y xe n&acirc;ng n&agrave;y. Khi ph&aacute;t hiện ch&aacute;y, nh&acirc;n vi&ecirc;n c&ocirc;ng ty đ&atilde; d&ugrave;ng b&igrave;nh chữa ch&aacute;y v&agrave; nước để dập lửa. Đến khoảng 00h15 ng&agrave;y 21/02/2014, đ&aacute;m ch&aacute;y đ&atilde; được dập tắt. Xe n&acirc;ng bị ch&aacute;y to&agrave;n bộ phần b&ecirc;n ngo&agrave;i động cơ v&agrave; c&aacute;c bộ phận, chi tiết nằm gần động cơ, hệ thống đường ống d&acirc;y (d&acirc;y điện, đường ống dầu), một số bộ phận kh&aacute;c bị n&aacute;m kh&oacute;i.</p>\r\n<p><img src=\"../../../uploads/xa.png\" alt=\"\" width=\"488\" height=\"511\" /></p>\r\n<p style=\"text-align: center;\"><em>Một v&agrave;i h&igrave;nh ảnh tổn thất của xe n&acirc;ng Kalmar</em></p>\r\n<p>Ngay sau khi nhận được th&ocirc;ng b&aacute;o từ kh&aacute;ch h&agrave;ng, VBI đ&atilde; nhanh ch&oacute;ng tới hiện trường v&agrave; phối hợp c&ugrave;ng C&ocirc;ng ty Gi&aacute;m định VietAdjusters tiến h&agrave;nh c&ocirc;ng t&aacute;c gi&aacute;m định bồi thường. Số tiền bồi thường 671.300.000 đồng đ&atilde; gi&uacute;p c&ocirc;ng ty khắc phục được tối đa c&aacute;c tổn thất v&agrave; kh&ocirc;i phục sản xuất, kinh doanh.</p>\r\n<p>Qua sự việc tr&ecirc;n, VBI ng&agrave;y c&agrave;ng khẳng định dịch vụ kh&aacute;ch h&agrave;ng vượt trội v&agrave; uy t&iacute;n trong lĩnh vực bảo hiểm phi nh&acirc;n thọ.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:45:16', '2018-03-21 15:45:16', 'images/news/1520415924_cty-bong-_635544299859926935_HasThumb_Thumb.png'),
(113, 2, 'Bảo hiểm VietinBank chi trả bồi thường lên tới 18 tỷ đồng cho Công ty xi măng Công Thanh', 'bao-hiem-vietinbank-chi-tra-boi-thuong-len-toi-18-ty-dong-cho-cong-ty-xi-mang-cong-thanh', '<p><span style=\"font-family: Arial;\">T&iacute;nh đến nay, VBI đ&atilde; v&agrave; đang giải quyết bồi thường cho C&ocirc;ng ty xi măng C&ocirc;ng Thanh 5 vụ tổn thất. Tổng số tiền bồi thường C&ocirc;ng ty xi măng C&ocirc;ng Thanh nhận được từ VBI l&agrave; gần 18 tỷ đồng.</span></p>', '<p>L&agrave; một trong những kh&aacute;ch h&agrave;ng tham gia bảo hiểm tại Bảo hiểm VietinBank (VBI), Nh&agrave; m&aacute;y Xi măng C&ocirc;ng Thanh được biết đến l&agrave; một trong những nh&agrave; m&aacute;y xi măng c&oacute; quy m&ocirc; lớn nhất Việt Nam. Nh&agrave; m&aacute;y gồm hai d&acirc;y chuyền sản xuất xi măng v&agrave; clinker hiện đại với diện t&iacute;ch hơn 100 h&eacute;cta. D&acirc;y chuyền số 2 đang trong qu&aacute; tr&igrave;nh thi c&ocirc;ng lắp đặt với c&ocirc;ng suất dự kiến đạt 3,6 triệu tấn clinker/năm với tổng số vốn 420 triệu USD (khoảng 7.600 tỷ Việt Nam đồng). Dự kiến sau khi đưa d&acirc;y chuyền thứ 2 v&agrave;o hoạt động, Nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh sẽ l&agrave; một trong những nh&agrave; m&aacute;y sản xuất xi măng lớn nhất Ch&acirc;u &Aacute;.</p>\r\n<p>Khoảng 07h ng&agrave;y 04 th&aacute;ng 10 năm 2013, c&ocirc;ng nh&acirc;n tại Nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh ph&aacute;t hiện c&oacute; ch&aacute;y tại vị tr&iacute; đặt băng tải 5J1.BG30, đoạn nối từ nh&agrave; nghiền đến l&ocirc; xi măng th&agrave;nh phẩm nhanh ch&oacute;ng lan ra. C&ocirc;ng ty đ&atilde; nỗ lực dập lửa v&agrave; lập tức gọi điện cho cảnh s&aacute;t PCCC đến hỗ trợ. Tuy nhi&ecirc;n, do t&iacute;nh chất nghi&ecirc;m trọng của vụ ch&aacute;y, mọi nỗ lực đều v&ocirc; hiệu. Nguy&ecirc;n nh&acirc;n g&acirc;y ra vụ ch&aacute;y l&agrave; do chập điện tại khu vực băng tải nghiền xi, thiết bị điện bị nước v&agrave;o do ảnh hưởng của cơn b&atilde;o Wutip.</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img src=\"../../../uploads/ket cau theo bi thiet hai.png\" alt=\"\" width=\"629\" height=\"466\" /></p>\r\n<p style=\"text-align: center;\"><em>Kết cấu th&eacute;p bị thiệt hại</em></p>\r\n</td>\r\n<td>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"../../../uploads/he thong bang tai thiet hai_CT.png\" alt=\"\" width=\"609\" height=\"454\" /></p>\r\n<p style=\"text-align: center;\"><em>Hệ thống băng tải bị thiệt hại</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh thiệt hại tại Nh&agrave; m&aacute;y xi măng C&ocirc;ng Thanh)</em></p>\r\n<p>Ng&agrave;y 26/10/2013, chưa đầy 1 th&aacute;ng sau khi nhận được th&ocirc;ng b&aacute;o thiệt hại từ nh&agrave; m&aacute;y, VBI đ&atilde; chuyển 7 tỷ đồng tạm ứng cho doanh nghiệp để khắc phục hậu quả v&agrave; x&acirc;y dựng lại hệ thống cho kịp tiến độ sản xuất. Gần đ&acirc;y nhất, ng&agrave;y 05/12/2014, VBI đ&atilde; bồi thường 5.997.429.807 VND. Như vậy tổng số tiền bồi thường C&ocirc;ng ty xi măng C&ocirc;ng Thanh nhận được l&agrave; gần 18 tỷ đồng.</p>\r\n<p>T&iacute;nh đến nay, VBI đ&atilde; v&agrave; đang giải quyết bồi thường cho C&ocirc;ng ty xi măng C&ocirc;ng Thanh 5 vụ tổn thất. Hiện tại, VBI đang tiếp tục thực hiện c&aacute;c c&ocirc;ng t&aacute;c gi&aacute;m định cho vụ Ch&aacute;y thiết bị t&aacute;ch từ, Vụ tổn thất nứt silo thuộc DC2 v&agrave; vụ Tổn thất đối với d&acirc;y chuyền 1 với ước t&iacute;nh bồi thường l&ecirc;n tới 17 tỷ đồng.</p>\r\n<p>C&oacute; thể n&oacute;i, việc chi trả bồi thường kịp thời kh&ocirc;ng chỉ gi&uacute;p doanh nghiệp tiếp tục an t&acirc;m sản xuất m&agrave; c&ograve;n khẳng định uy t&iacute;n của VBI tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:46:22', '2018-03-21 15:46:22', 'images/news/1520416252_cong-than_635548640179180773_HasThumb_Thumb.jpg.png'),
(114, 2, 'Thông báo thời gian nghỉ Tết Dương Lịch của các Cơ sở y tế', 'thong-bao-thoi-gian-nghi-tet-duong-lich-cua-cac-co-so-y-te', '<p><span style=\"font-family: Arial;\">Thời gian nghỉ Tết Dương Lịch của c&aacute;c Cơ sở y tế sẽ bắt đầu từ ng&agrave;y 01 th&aacute;ng 01 năm 2015 đến hết ng&agrave;y 04 th&aacute;ng 01 năm 2015, trong thời gian n&agrave;y, đa số Cơ sở y tế sẽ tạm ngừng dịch vụ bảo l&atilde;nh viện ph&iacute; ngoại tr&uacute;, nội tr&uacute;, tai nạn, cấp cứu. Ch&uacute;ng t&ocirc;i xin tr&acirc;n trọng th&ocirc;ng b&aacute;o đến Qu...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">K&iacute;nh gửi Qu&yacute; đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng,<br /> <br />C&ocirc;ng ty bảo hiểm VietinBank xin tr&acirc;n trọng th&ocirc;ng b&aacute;o:<br /> <br />Thời gian nghỉ Tết Dương Lịch của c&aacute;c Cơ sở y tế sẽ bắt đầu từ ng&agrave;y 01 th&aacute;ng 01 năm 2015 đến hết ng&agrave;y 04 th&aacute;ng 01 năm 2015, trong thời gian n&agrave;y, đa số Cơ sở y tế sẽ tạm ngừng dịch vụ bảo l&atilde;nh viện ph&iacute; ngoại tr&uacute;, nội tr&uacute;, tai nạn, cấp cứu.<br /> <br />Ch&uacute;ng t&ocirc;i xin tr&acirc;n trọng th&ocirc;ng b&aacute;o đến Qu&yacute; đối t&aacute;c, kh&aacute;ch h&agrave;ng v&agrave; c&aacute;c tổ chức c&aacute; nh&acirc;n c&oacute; li&ecirc;n quan về lịch nghỉ lễ v&agrave; việc tạm ngưng hoạt động dịch vụ n&agrave;y của một số Cơ sở y tế.<br /> <br />Ch&acirc;n th&agrave;nh cảm ơn sự hợp t&aacute;c của Qu&yacute; đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng trong thời gian qua, hy vọng sẽ nhận được sự hỗ trợ, hợp t&aacute;c chặt chẽ hơn trong thời gian tới.<br /> <br />K&iacute;nh ch&uacute;c Qu&yacute; đối t&aacute;c v&agrave; kh&aacute;ch h&agrave;ng một năm mới th&agrave;nh c&ocirc;ng, thịnh vượng!</span></p>', '2018-03-07 21:51:42', '2018-03-07 21:51:42', 'images/news/1520416302_10-xu-huo_635556339905084420_HasThumb_Thumb.jpg.png'),
(115, 2, 'Bảo hiểm VietinBank bổ nhiệm Phó Giám đốc Chi nhánh Sài Gòn', 'bao-hiem-vietinbank-bo-nhiem-pho-giam-doc-chi-nhanh-sai-gon', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 09/01/2015 tại Tp.HCM, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ trao quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh S&agrave;i G&ograve;n cho &ocirc;ng Qu&aacute;ch Thanh Phong. Tham dự lễ trao quyết định c&oacute; đại diện Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh S&agrave;i G&ograve;n.&nbsp;</span></p>', '<p>Ng&agrave;y 09/01/2015 tại Tp.HCM, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ trao quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh S&agrave;i G&ograve;n cho &ocirc;ng Qu&aacute;ch Thanh Phong. Tham dự lễ trao quyết định c&oacute; đại diện Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh S&agrave;i G&ograve;n.</p>\r\n<p>Tại buổi lễ, Tổng gi&aacute;m đốc VBI - &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm v&agrave; ch&uacute;c mừng &ocirc;ng Qu&aacute;ch Thanh Phong nhận nhiệm vụ mới. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ mong muốn với hơn 10 năm kinh nghiệm trong lĩnh vực bảo hiểm, &ocirc;ng Qu&aacute;ch Thanh Phong sẽ c&ugrave;ng với VBI S&agrave;i G&ograve;n ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh.</p>\r\n<p><img src=\"../../../uploads/IMG_415034608038493.jpg\" alt=\"\" width=\"679\" height=\"381\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh tại Lễ bổ nhiệm)</em></p>\r\n<p>Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng Qu&aacute;ch Thanh Phong thể hiện quyết t&acirc;m phấn đấu để VBI S&agrave;i G&ograve;n ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đồng thời cam kết sẽ tiếp tục cống hiến, đưa VBI S&agrave;i G&ograve;n ph&aacute;t triển mạnh mẽ.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 04:47:00', '2018-03-21 15:47:00', 'images/news/1520416477_IMG_415034608038493.jpeg'),
(116, 2, 'Bảo hiểm VietinBank triển khai nhiệm vụ kinh doanh năm 2015', 'bao-hiem-vietinbank-trien-khai-nhiem-vu-kinh-doanh-nam-2015', '<p><span style=\"font-family: Arial;\">Trong bối cảnh to&agrave;n thị trường gặp kh&oacute; khăn, VBI vẫn đạt những con số ấn tượng: doanh thu đạt 318 tỷ tăng trưởng 86%; lợi nhuận đạt 63,27 tỷ đồng tăng trưởng 30%; tỷ lệ ROE đạt 8,74% tăng trưởng 35% so với năm 2013; tỷ lệ ROA 7,12% tăng trưởng 18% so với năm 2013, l&atilde;i nghiệp vụ đ...&nbsp;</span></p>', '<p>Ng&agrave;y 30/1/2015, C&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam (VBI) đ&atilde; tổ chức Hội nghị triển khai nhiệm vụ kinh doanh năm 2015.</p>\r\n<p>Đến dự đại hội c&oacute; sự tham dự của b&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty bảo hiểm VBI, Ban điều h&agrave;nh VBI c&ugrave;ng tập thể c&aacute;n bộ c&oacute; th&agrave;nh t&iacute;ch xuất sắc của Trụ sở ch&iacute;nh v&agrave; 8 chi nh&aacute;nh VBI tr&ecirc;n cả nước.</p>\r\n<p>Năm 2014 l&agrave; năm đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của VBI với sự chuyển biến tăng trưởng mạnh mẽ so với năm 2013 cả về doanh thu, lợi nhuận lẫn cơ cấu bộ m&aacute;y tổ chức. Trong bối cảnh to&agrave;n thị trường gặp kh&oacute; khăn, VBI vẫn đạt những con số ấn tượng: doanh thu đạt 318 tỷ tăng trưởng 86%; lợi nhuận đạt 63,27 tỷ đồng tăng trưởng 30%; tỷ lệ ROE đạt 8,74% tăng trưởng 35% so với năm 2013; tỷ lệ ROA 7,12% tăng trưởng 18% so với năm 2013, l&atilde;i nghiệp vụ đạt 6,1 tỷ đồng; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 26,13%. Cũng trong năm, C&ocirc;ng ty đ&atilde; mở th&ecirc;m 1 Chi nh&aacute;nh mới, 15 ph&ograve;ng khu vực, tuyển th&ecirc;m 81 lao động v&agrave; tăng th&ecirc;m 1087 đại l&yacute;.</p>\r\n<p><img src=\"../../../uploads/tong ket 2015.jpg\" alt=\"\" width=\"986\" height=\"772\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh tại Hội nghị triển khai nhiệm vụ kinh doanh 2015)</em></p>\r\n<p>Với th&agrave;nh t&iacute;ch vượt trội trong năm 2014, VBI đ&atilde; vinh dự nhận được c&aacute;c giải thưởng uy t&iacute;n như: &ldquo;Giải Thương hiệu mạnh 2014&rdquo; do Thời b&aacute;o kinh tế Kinh tế Việt Nam phối hợp c&ugrave;ng Cục X&uacute;c tiến thương mại tổ chức; &ldquo;Top 1000 đơn vị đ&oacute;ng thuế thu nhập doanh nghiệp lớn nhất VN&rdquo; do VietNam Report c&ocirc;ng bố.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, L&atilde;nh đạo C&ocirc;ng ty cũng được nhận những giải thưởng vinh dự như: Được Hội doanh nh&acirc;n trẻ Việt Nam chứng nhận l&agrave; doanh nh&acirc;n trẻ xếp thứ 25 trong 100 doanh nh&acirc;n trẻ Việt Nam ti&ecirc;u biểu tại Giải thưởng sao đỏ năm 2014. Được chứng nhận Top 100 nh&agrave; quản l&yacute; ti&ecirc;u biểu thời đại mới năm 2014 do Viện Ch&iacute;nh s&aacute;ch Ph&aacute;p Luật v&agrave; Quản l&yacute; Nh&agrave; Nước tổ chức.<br />Tại hội nghị, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc VBI đ&atilde; b&aacute;o c&aacute;o về những th&agrave;nh tựu đạt được cũng như những mặt hạn chế của c&ocirc;ng ty trong năm 2014 để từ đ&oacute; c&oacute; những giải ph&aacute;p kịp thời nhằm n&acirc;ng cao chất lượng, dịch vụ cũng như uy t&iacute;n của c&ocirc;ng ty tr&ecirc;n thị trường bảo hiểm trong năm 2015.</p>\r\n<p>Ph&aacute;t biểu tại Hội nghị, b&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty bảo hiểm VBI đồng thời l&agrave; ủy vi&ecirc;n HĐQT VietinBank đưa ra nhiều lời động vi&ecirc;n, kh&iacute;ch lệ, tuy&ecirc;n dương VBI về những th&agrave;nh tựu đ&atilde; đạt được trong năm 2014 nhưng cũng đặt ra mục ti&ecirc;u cao hơn trong năm 2015 cho VBI, để từ đ&oacute; VBI c&oacute; thể ph&aacute;t huy hơn nữa những th&agrave;nh tựu đ&atilde; đạt được. Bà cũng đ&ecirc;̀ nghị C&ocirc;ng ty phải đổi mới phương pháp đ&ecirc;̉ phát đ&ocirc;̣ng các phong trào thi đua thi&ecirc;́t thực hơn nữa, l&acirc;́y thi đua làm đ&ocirc;̣ng lực, làm t&ocirc;́t c&ocirc;ng tác quy hoạch cán b&ocirc;̣ ngu&ocirc;̀n đ&ecirc;̉ có đ&ocirc;̣i ngũ k&ecirc;́ c&acirc;̣n t&ocirc;́t, chú trọng c&ocirc;ng tác đào tạo,&hellip;Đ&ocirc;̀ng thời, phải thực hi&ecirc;̣n t&ocirc;́t quy ch&ecirc;́ d&acirc;n chủ cơ sở, tổ chức học t&acirc;̣p và nắm vững n&ocirc;̣i quy lao đ&ocirc;̣ng, s&ocirc;̉ tay văn hóa doanh nghi&ecirc;̣p, x&acirc;y dựng phong cách văn hóa ri&ecirc;ng bi&ecirc;̣t của VietinBank.</p>\r\n<p>Trong năm 2015, VBI sẽ tiếp tục mở rộng mạng lưới hoạt động; t&aacute;i cơ cấu theo m&ocirc; h&igrave;nh quản l&yacute; tập trung, ph&aacute;t triển mạnh k&ecirc;nh bancassurance v&agrave; tập trung v&agrave;o c&aacute;c sản phẩm b&aacute;n lẻ đ&oacute;ng g&oacute;i với c&aacute;c sản phẩm Ng&acirc;n h&agrave;ng như bảo hiểm con người, xe cơ giới; &aacute;p dụng c&ocirc;ng nghệ hiện đại v&agrave;o việc quản l&yacute;; tăng cường h&igrave;nh ảnh, thương hiệu của c&ocirc;ng ty.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:47:26', '2018-03-21 15:47:26', 'images/news/1520416673_tong ket 2015.jpg'),
(117, 2, 'VBI tổ chức Chương trình kết nối đồng nghiệp và Gala Dinner năm 2015', 'vbi-to-chuc-chuong-trinh-ket-noi-dong-nghiep-va-gala-dinner-nam-2015', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/01/2015, C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh Team building v&agrave; Gala Dinner cho c&aacute;n bộ nh&acirc;n vi&ecirc;n tại Đ&agrave; Nẵng.&nbsp;</span></p>', '<p>Ng&agrave;y 29/01/2015, C&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh Team building v&agrave; Gala Dinner cho c&aacute;n bộ nh&acirc;n vi&ecirc;n tại Đ&agrave; Nẵng. Chương tr&igrave;nh với c&aacute;c hoạt động ngoại kh&oacute;a x&acirc;y dựng tinh thần đồng đội trong to&agrave;n tập thể c&ocirc;ng ty, truyền nhiệt huyết l&agrave;m việc trong mỗi th&agrave;nh vi&ecirc;n. Tất cả c&aacute;c th&agrave;nh vi&ecirc;n trong c&ocirc;ng ty đ&atilde; &ldquo;ch&aacute;y&rdquo; hết m&igrave;nh trong suốt 1 ng&agrave;y vui chơi. Chương tr&igrave;nh đ&atilde; gi&uacute;p c&aacute;c th&agrave;nh vi&ecirc;n trong c&ocirc;ng ty x&iacute;ch lại gần nhau hơn, hiểu biết v&agrave; gần gũi nhau hơn.</p>\r\n<p>Chương tr&igrave;nh kết nối đồng nghiệp v&agrave; Gala Dinner năm 2015 đ&atilde; để lại những kỷ niệm đẹp, ấn tượng, khắc s&acirc;u trong tr&aacute;i tim của mỗi th&agrave;nh vi&ecirc;n v&agrave; tạo động lực cho c&aacute;c th&agrave;nh vi&ecirc;n VBI c&ugrave;ng thi đua, phấn đấu đảm bảo ho&agrave;n th&agrave;nh kế hoạch kinh doanh 2015 với những quyết t&acirc;m mạnh mẽ hơn bao giờ hết.</p>\r\n<p>Sau đ&acirc;y l&agrave; một v&agrave;i h&igrave;nh ảnh về chương tr&igrave;nh:</p>\r\n<p><img src=\"../../../uploads/3.jpg\" alt=\"\" width=\"640\" height=\"365\" /></p>\r\n<p><img src=\"../../../uploads/test.jpg\" alt=\"\" width=\"640\" height=\"370\" /></p>\r\n<p><img src=\"../../../uploads/5dinner.jpg\" alt=\"\" width=\"640\" height=\"459\" /></p>\r\n<p><img src=\"../../../uploads/1dinner.jpg\" alt=\"\" width=\"640\" height=\"412\" /></p>\r\n<p><img src=\"../../../uploads/7.jpg\" alt=\"\" width=\"640\" height=\"444\" /></p>\r\n<p><img src=\"../../../uploads/6.jpg\" alt=\"\" width=\"640\" height=\"368\" /></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 04:52:00', '2018-03-21 15:52:00', 'images/news/1520416881__635585596559771214_HasThumb_Thumb.jpg.png'),
(118, 2, 'Thông báo lịch nghỉ Tết Nguyên đán của Bảo hiểm VietinBank', 'thong-bao-lich-nghi-tet-nguyen-dan-cua-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Nh&acirc;n dịp năm mới Ất M&ugrave;i, VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng một năm mới nhiều sức khỏe, an khang thịnh vượng, vạn sự c&aacute;t tường.&nbsp;</span></p>', '<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch h&agrave;ng,</strong><br /> <br />Lời đầu ti&ecirc;n Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.<br /> <br />Nh&acirc;n dịp năm mới Ất M&ugrave;i, VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng một năm mới nhiều sức khỏe, an khang thịnh vượng, vạn sự c&aacute;t tường.<br /> <br />Bảo hiểm VietinBank sẽ nghỉ Tết từ ng&agrave;y 15 th&aacute;ng 02 năm 2015 đến hết ng&agrave;y 23 th&aacute;ng 02 năm 2015. Ng&agrave;y 24 th&aacute;ng 02 năm 2015, VBI sẽ trở lại hoạt động b&igrave;nh thường.<br /> <br /><strong>Tr&acirc;n trọng cảm ơn!</strong></p>\r\n<p><img src=\"../../../uploads/banner website CMNM.jpg\" alt=\"\" width=\"964\" height=\"305\" /></p>', '2018-03-21 04:52:34', '2018-03-21 15:52:34', 'images/news/1520417157_banner-to_635592704187481434_HasThumb_Thumb.jpg.png'),
(119, 2, 'Bảo hiểm VietinBank được vinh danh “Thương hiệu mạnh Việt Nam 2014”', 'bao-hiem-vietinbank-duoc-vinh-danh-thuong-hieu-manh-viet-nam-2014', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 21/3/2015, tại H&agrave; Nội đ&atilde; diễn ra Li&ecirc;n hoan Doanh nghiệp Rồng v&agrave;ng v&agrave; c&aacute;c Thương hiệu mạnh Việt Nam lần thứ XI do Thời b&aacute;o Kinh tế Việt Nam tổ chức. Đ&acirc;y l&agrave; một trong những giải thưởng d&agrave;nh cho doanh nghiệp uy t&iacute;n tại Việt Nam, được tổ chức thường ni&ecirc;n nhằm vinh danh những doa...&nbsp;</span></p>', '<p>Ng&agrave;y 21/3/2015, tại H&agrave; Nội đ&atilde; diễn ra Li&ecirc;n hoan Doanh nghiệp Rồng v&agrave;ng v&agrave; c&aacute;c Thương hiệu mạnh Việt Nam lần thứ XI do Thời b&aacute;o Kinh tế Việt Nam tổ chức. Với chủ đề &ldquo;Tạo dựng m&ocirc;i trường kinh doanh b&igrave;nh đẳng v&agrave; l&agrave;nh mạnh&rdquo;, li&ecirc;n hoan năm nay t&ocirc;n vinh 71 doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i (FDI) đạt danh hiệu Rồng V&agrave;ng v&agrave; 95 doanh nghiệp Việt đạt danh hiệu Thương hiệu mạnh Việt Nam. Đ&acirc;y l&agrave; một trong những giải thưởng d&agrave;nh cho doanh nghiệp uy t&iacute;n tại Việt Nam, được tổ chức thường ni&ecirc;n nhằm vinh danh những doanh nghiệp c&oacute; th&agrave;nh t&iacute;ch trong sản xuất kinh doanh, cung ứng dịch vụ, thực hiện đầy đủ nghĩa vụ nộp thuế, đảm bảo lợi &iacute;ch người lao động, t&iacute;ch cực tham gia c&aacute;c hoạt động x&atilde; hội v&agrave; đ&oacute;ng g&oacute;p cho cộng đồng.</p>\r\n<p>L&agrave; một trong 95 doanh nghiệp được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo; &ndash; C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; khẳng định được t&ecirc;n tuổi v&agrave; tạo được vị thế tr&ecirc;n thị trường bảo hiểm Việt Nam. Năm 2014 vừa qua l&agrave; năm đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của VBI với sự chuyển biến tăng trưởng mạnh mẽ cả về doanh thu, lợi nhuận lẫn cơ cấu bộ m&aacute;y tổ chức. Trong bối cảnh to&agrave;n thị trường gặp kh&oacute; khăn, VBI vẫn đạt những con số ấn tượng: doanh thu đạt 318 tỷ tăng trưởng 86% so với năm 2013; lợi nhuận đạt 63,27 tỷ đồng tăng trưởng 30%; tỷ lệ ROE đạt 8,74% tăng trưởng 35% so với năm 2013; tỷ lệ ROA 7,12% tăng trưởng 18% so với năm 2013; l&atilde;i nghiệp vụ đạt 6,1 tỷ đồng; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 26,13%. Cũng trong năm, VBI đ&atilde; mở th&ecirc;m 1 Chi nh&aacute;nh mới, 15 ph&ograve;ng kinh doanh khu vực, tuyển th&ecirc;m 81 lao động v&agrave; tăng th&ecirc;m 1896 đại l&yacute;.</p>\r\n<p><img src=\"../../../uploads/anh dang web.jpg\" alt=\"\" width=\"640\" height=\"462\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VBI nhận C&uacute;p Thương hiệu mạnh Việt Nam 2014.)</em></p>\r\n<p>Theo Ban tổ chức, những doanh nghiệp được t&ocirc;n vinh năm nay đều c&oacute; kết quả tăng trưởng v&agrave; ph&aacute;t triển tốt, nỗ lực vượt qua kh&oacute; khăn trong bối cảnh khủng hoảng kinh tế khu vực v&agrave; thế giới k&eacute;o d&agrave;i; c&oacute; kế hoạch chiến lược n&acirc;ng cao năng lực cạnh tranh cả về nh&acirc;n lực, c&ocirc;ng nghệ, chất lượng, dịch vụ để c&oacute; thể chủ động đ&oacute;n những cơ hội v&agrave; th&aacute;ch thức từ hội nhập kinh tế quốc tế ng&agrave;y c&agrave;ng s&acirc;u rộng.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 04:52:55', '2018-03-21 15:52:55', 'images/news/1520417249_anh-dang-_635627074594053965_HasThumb_Thumb.jpg.png'),
(120, 2, 'Đoàn thanh niên VBI tổ chức chương trình kỷ niệm ngày thành lập Đoàn 26/03', 'doan-thanh-nien-vbi-to-chuc-chuong-trinh-ky-niem-ngay-thanh-lap-doan-26-03', '<p><span style=\"font-family: Arial;\">H&ocirc;m nay, hướng tới ch&agrave;o mừng kỷ niệm ng&agrave;y th&agrave;nh lập Đo&agrave;n Thanh ni&ecirc;n Cộng sản Hồ Ch&iacute; Minh (26/3/1931- 26/3/2015), Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank (VBI) bắt đầu một ng&agrave;y mới đầy &yacute; nghĩa bằng chuyến thăm nh&agrave; Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p, Lăng B&aacute;c v&agrave; di t&iacute;ch Phủ chủ tịch&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">H&ocirc;m nay, hướng tới ch&agrave;o mừng kỷ niệm ng&agrave;y th&agrave;nh lập Đo&agrave;n Thanh ni&ecirc;n Cộng sản Hồ Ch&iacute; Minh (26/3/1931- 26/3/2015), Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank (VBI) bắt đầu một ng&agrave;y mới đầy &yacute; nghĩa bằng chuyến thăm nh&agrave; Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p. Từ 8h s&aacute;ng, c&aacute;c đo&agrave;n vi&ecirc;n VBI tập hợp c&ugrave;ng nhau để thắp hương v&agrave; viếng Đại tướng. Đo&agrave;n ch&uacute;ng t&ocirc;i tới đ&acirc;y đều c&oacute; những cảm x&uacute;c, những kỷ niệm rất đặc biệt. Tại đ&acirc;y, ch&uacute;ng t&ocirc;i rất vinh dự v&agrave; x&uacute;c động khi được đến thăm, thắp hương v&agrave; nghe ch&uacute; V&otilde; Điện Bi&ecirc;n - con trai b&aacute;c Gi&aacute;p chia sẻ về cuộc đời cũng như sự nghiệp C&aacute;ch mạng của b&aacute;c.&nbsp;</span></p>\r\n<p><img src=\"../../../uploads/doan (1).jpg\" alt=\"\" width=\"1366\" height=\"759\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh đo&agrave;n thanh ni&ecirc;n VBI tại nh&agrave; Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Sau khi rời nh&agrave; b&aacute;c Gi&aacute;p, đo&agrave;n ch&uacute;ng t&ocirc;i di chuyển tới Lăng Chủ tịch v&agrave; tham quan Khu Di t&iacute;ch Chủ tịch Hồ Ch&iacute; Minh. Trong số ch&uacute;ng t&ocirc;i, c&oacute; những người đ&atilde; v&agrave;o Lăng viếng B&aacute;c một đ&ocirc;i lần, nhưng cũng c&oacute; những người chỉ mới đến lần đầu. Song, tất cả đều c&oacute; một điểm chung l&agrave; ai cũng th&agrave;nh k&iacute;nh v&agrave; x&uacute;c động khi được v&agrave;o Lăng viếng B&aacute;c. Mỗi địa điểm như nh&agrave; s&agrave;n, ao c&aacute;, nh&agrave; 54&hellip;đều gắn với những kỷ niệm x&uacute;c động về 15 năm sống v&agrave; hoạt động C&aacute;ch mạng cuối c&ugrave;ng của B&aacute;c. Được v&agrave;o lăng viếng B&aacute;c, chứng kiến nơi ở, nơi l&agrave;m việc đơn sơ, giản dị của Người, trong l&ograve;ng mỗi ch&uacute;ng t&ocirc;i kh&ocirc;ng khỏi bồi hồi x&uacute;c động. Qua chuyến thăm n&agrave;y ch&uacute;ng t&ocirc;i hiểu r&otilde; hơn về nh&acirc;n c&aacute;ch cao đẹp v&agrave; sự hy sinh lớn lao B&aacute;c d&agrave;nh cho d&acirc;n tộc, n&acirc;ng cao l&ograve;ng tự h&agrave;o d&acirc;n tộc, quyết t&acirc;m học tập v&agrave; lao động để g&oacute;p phần cống hiến đưa Việt Nam \"s&aacute;nh vai c&ugrave;ng c&aacute;c cường quốc năm ch&acirc;u\" như lời B&aacute;c Hồ đ&atilde; từng căn dặn.</span></p>\r\n<p><img src=\"../../../uploads/IMG_6133.jpg\" alt=\"\" width=\"892\" height=\"609\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh đo&agrave;n thanh ni&ecirc;n VBI tại Khu di t&iacute;ch Chủ tịch Hồ Ch&iacute; Minh)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong ng&agrave;y, Chi đo&agrave;n VBI cũng tổ chức Chương tr&igrave;nh giao lưu b&oacute;ng đ&aacute; gồm 04 đội: VBI TSC, VBI H&agrave; Nội, VBI Thăng Long, Li&ecirc;n qu&acirc;n VBI Hải Ph&ograve;ng v&agrave; VBI Thanh H&oacute;a. Dưới sự cổ vũ nhiệt t&igrave;nh của c&aacute;c cổ động vi&ecirc;n, c&aacute;c cầu thủ của bốn đội b&oacute;ng đ&atilde; mang đến cho kh&aacute;n giả một trận đấu thực sự s&ocirc;i nổi v&agrave; hấp dẫn với nhiều pha b&oacute;ng đẹp mắt, ấn tượng. Hoạt động n&agrave;y đ&atilde; gi&uacute;p cho đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI r&egrave;n luyện sức khỏe theo gương B&aacute;c Hồ vĩ đại để thực hiện tốt c&aacute;c hoạt động Đo&agrave;n cũng như c&aacute;c c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n.</span></p>\r\n<p><img src=\"../../../uploads/doan3.jpg\" alt=\"\" width=\"640\" height=\"371\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh c&aacute;c đo&agrave;n vi&ecirc;n thanh ni&ecirc;n tham dự trận giao lưu b&oacute;ng đ&aacute;)</span></em></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 04:54:14', '2018-03-21 15:54:14', 'images/news/1520417398_IMG_613_635627846176910201_HasThumb_Thumb.JPG.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(121, 2, 'Chương trình tình nguyện “Nồi cháo tình thương” 2015', 'chuong-trinh-tinh-nguyen-noi-chao-tinh-thuong-2015', '<p><span style=\"font-family: Arial;\">Với tinh thần chia sẻ kh&oacute; khăn c&ugrave;ng cộng đồng, mang đến cho c&aacute;c bệnh nh&acirc;n ngh&egrave;o đang điều trị tại Bệnh nhi h&agrave;ng trăm suất ch&aacute;o đủ dinh dưỡng, ấm &aacute;p t&igrave;nh người, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; l&agrave; một hoạt động mang &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Trong suốt một tuần qua, c&aacute;c đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI lu&ocirc;n bắt đầu một ng&agrave;y mới đầy &yacute; nghĩa bằng chuyến h&agrave;nh tr&igrave;nh đến thăm c&aacute;c bệnh nh&acirc;n ở Bệnh viện Nhi Trung Ương. Với tinh thần chia sẻ kh&oacute; khăn c&ugrave;ng cộng đồng, mang đến cho c&aacute;c bệnh nh&acirc;n ngh&egrave;o đang điều trị tại Bệnh nhi h&agrave;ng trăm suất ch&aacute;o đủ dinh dưỡng, ấm &aacute;p t&igrave;nh người, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; l&agrave; một hoạt động mang &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc. B&ecirc;n cạnh việc ph&aacute;t ch&aacute;o, đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI c&ograve;n thăm hỏi động vi&ecirc;n bệnh nh&acirc;n v&agrave; người nh&agrave; vượt qua kh&oacute; khăn, y&ecirc;n t&acirc;m điều trị.</span></p>\r\n<p><img src=\"../../../uploads/noi chao tinh thuong2.jpg\" alt=\"\" width=\"1164\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Niềm hạnh ph&uacute;c của c&aacute;c đo&agrave;n vi&ecirc;n VBI khi được đ&oacute;p g&oacute;p sức trẻ v&agrave;o c&ocirc;ng việc thiện nguyện)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">B&ecirc;n cạnh đ&oacute;, ngay từ c&aacute;c th&aacute;ng đầu năm 2015, Đo&agrave;n thanh ni&ecirc;n VBI li&ecirc;n tục triển khai nhiều hoạt động đo&agrave;n thể trong đ&oacute; tập trung v&agrave;o c&aacute;c chương tr&igrave;nh h&agrave;nh động t&igrave;nh nguyện v&igrave; cuộc sống cộng đồng, an sinh x&atilde; hội,&hellip;Đ&acirc;y l&agrave; tiền đề quan trọng để tăng cường c&ocirc;ng t&aacute;c Đo&agrave;n v&agrave; phong tr&agrave;o thanh ni&ecirc;n tại VBI, g&oacute;p phần v&agrave;o sự ph&aacute;t triển ng&agrave;y c&agrave;ng lớn mạnh của Bảo hiểm VietinBank.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 04:54:54', '2018-03-21 15:54:54', 'images/news/1520417534_noi-chao-_635628962999782012_HasThumb_Thumb.jpg.png'),
(122, 2, 'VBI chi trả 100 triệu đồng tiền bảo hiểm cho khách hàng tại Tuyên Quang', 'vbi-chi-tra-100-trieu-dong-tien-bao-hiem-cho-khach-hang-tai-tuyen-quang', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 24/03/2015, đại diện C&ocirc;ng ty Bảo hiểm VietinBank (VBI) c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Tuy&ecirc;n Quang đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 100 triệu đồng cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Tạ Thị Thiệu.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 24/03/2015, đại diện C&ocirc;ng ty Bảo hiểm VietinBank (VBI) c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Tuy&ecirc;n Quang đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 100 triệu đồng cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Tạ Thị Thiệu.<br /> <br />Kh&aacute;ch h&agrave;ng Tạ Thị Thiệu l&agrave; kh&aacute;ch h&agrave;ng vay vốn của VietinBank Tuy&ecirc;n Quang v&agrave; c&oacute; tham gia bảo hiểm con người Vietincare với số tiền bảo hiểm 100 triệu đồng. Ng&agrave;y 14/01/2015, khi đang lưu th&ocirc;ng tr&ecirc;n đường QL2 theo hướng H&agrave; Giang - Tuy&ecirc;n Quang. B&agrave; Thiệu kh&ocirc;ng may gặp tai nạn thương t&acirc;m dẫn đến tử vong. Ngay sau khi nhận được th&ocirc;ng tin từ gia đ&igrave;nh cũng như Ng&acirc;n h&agrave;ng, c&aacute;c c&aacute;n bộ VBI khu vực T&acirc;y Bắc đ&atilde; phối hợp với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Tuy&ecirc;n Quang hướng dẫn gia đ&igrave;nh thu thập hồ sơ để l&agrave;m thủ tục nhận bảo hiểm.</span></p>\r\n<p><img src=\"../../../uploads/vbi-chi-tra.jpg\" alt=\"\" width=\"1366\" height=\"792\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Đại diện VBI trao 100 triệu tiền bảo hiểm cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Về ph&iacute;a gia đ&igrave;nh kh&aacute;ch h&agrave;ng, b&agrave; Tạ Thị Mai - chị g&aacute;i b&agrave; Thiệu c&oacute; chia sẻ: &ldquo;Em g&aacute;i t&ocirc;i c&oacute; tham gia g&oacute;i bảo hiểm VietinCare của VBI nhưng kh&ocirc;ng may gặp tai nạn qua đời đột ngột. Ngay sau khi nhận được tin buồn từ ph&iacute;a gia đ&igrave;nh, VBI đ&atilde; cử người qua thăm viếng v&agrave; thực hiện đ&uacute;ng tr&aacute;ch nhiệm theo hợp đồng bảo hiểm v&agrave; nhanh ch&oacute;ng chi trả số tiền bồi thường 100 triệu cho gia đ&igrave;nh. Đ&acirc;y l&agrave; một nghĩa cử rất đẹp của c&aacute;c anh chị b&ecirc;n Bảo hiểm VietinBank. T&ocirc;i xin đại diện gia đ&igrave;nh cảm ơn Bảo hiểm VietinBank.&rdquo;<br /> <br />Về ph&iacute;a Ng&acirc;n h&agrave;ng VietinBank, b&agrave; Nguyễn Thị Việt Hằng - Gi&aacute;m đốc chi nh&aacute;nh Tuy&ecirc;n Quang cho biết: &ldquo;Bảo hiểm con người VietinCare l&agrave; một sản phẩm rất ưu việt. Sản phẩm đ&atilde; gi&uacute;p giảm thiểu tổn thất cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng, gi&uacute;p gia đ&igrave;nh trang trải nợ vay. Về ph&iacute;a ng&acirc;n h&agrave;ng, ch&uacute;ng t&ocirc;i cũng thu được nợ v&agrave; l&atilde;i tiền vay. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i kh&ocirc;ng phải xử l&yacute; nợ xấu hay l&agrave;m c&aacute;c biện ph&aacute;p để thu hồi nợ. Chi nh&aacute;nh t&ocirc;i sẽ tăng cường phối hợp với Bảo hiểm VietinBank trong việc kết hợp hoạt động t&iacute;n dụng ng&acirc;n h&agrave;ng với hoạt động bảo hiểm trong thời gian tới.&rdquo;<br /> <br />C&oacute; thể n&oacute;i sản phẩm bảo hiểm VietinCare rất c&oacute; &yacute; nghĩa với người vay vốn tại ng&acirc;n h&agrave;ng VietinBank. Sản phẩm đảm bảo ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng gặp rủi ro. Qua vụ việc n&agrave;y, Bảo hiểm VietinBank đ&atilde; chứng tỏ được sự ưu việt của sản phẩm cũng như sự tận t&acirc;m của c&aacute;c c&aacute;n bộ d&agrave;nh cho kh&aacute;ch h&agrave;ng.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 04:55:31', '2018-03-21 15:55:31', 'images/news/1520417633__635628968125192918_HasThumb_Thumb.jpg.png'),
(123, 2, 'VBI chi trả 50 triệu đồng tiền bảo hiểm cho khách hàng tại Điện Biên', 'vbi-chi-tra-50-trieu-dong-tien-bao-hiem-cho-khach-hang-tai-dien-bien', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 07/04/2014, đại diện C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 50 triệu đồng cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Đỗ Hải Hưng.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 07/04/2014, đại diện C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 50 triệu đồng cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Đỗ Hải Hưng.<br /> <br />Kh&aacute;ch h&agrave;ng Đỗ Hải Hưng l&agrave; kh&aacute;ch h&agrave;ng vay vốn của VietinBank Điện Bi&ecirc;n v&agrave; c&oacute; tham gia bảo hiểm con người VietinCare với số tiền bảo hiểm 50 triệu đồng. Ng&agrave;y 27/12/2014, &ocirc;ng Hưng bị xuất huyết ti&ecirc;u h&oacute;a do gi&atilde;n vỡ tĩnh mạch thực quản dẫn đến tử vong.<br /> <br />Ngay sau khi nhận được th&ocirc;ng tin từ gia đ&igrave;nh cũng như Ng&acirc;n h&agrave;ng, c&aacute;c c&aacute;n bộ VBI H&agrave; Nội đ&atilde; phối hợp với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Điện Bi&ecirc;n hướng dẫn gia đ&igrave;nh thu thập hồ sơ để l&agrave;m thủ tục nhận bảo hiểm.</span></p>\r\n<p><img src=\"../../../uploads/vietincare.png\" alt=\"\" width=\"858\" height=\"526\" /></p>\r\n<p><span style=\"font-family: Arial;\">Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.<br /> <br />Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 1 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng, VBI đ&atilde; v&agrave; đang khẳng định được uy t&iacute;n của tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 04:56:04', '2018-03-21 15:56:04', 'images/news/1520417700_bao-hiem-_635640253851610064_HasThumb_Thumb.png'),
(124, 2, 'VBI tổ chức đào tạo kỹ năng cho cán bộ quản lý cấp trung', 'vbi-to-chuc-dao-tao-ky-nang-cho-can-bo-quan-ly-cap-trung', '<p><span style=\"font-family: Arial;\">Trong 5 ng&agrave;y từ 08/04-12/04/2015, VBI phối hợp với trường Đại học Kinh tế Quốc d&acirc;n tổ chức kh&oacute;a học kỹ năng quản l&yacute; cho c&aacute;n bộ cấp trung nhằm n&acirc;ng cao kiến thức, kỹ năng quản l&yacute; cho c&aacute;c c&aacute;n bộ.&nbsp;</span></p>', '<p>Trong 5 ng&agrave;y từ 08/04-12/04/2015, VBI phối hợp với trường Đại học Kinh tế Quốc d&acirc;n tổ chức kh&oacute;a học kỹ năng quản l&yacute; cho c&aacute;n bộ cấp trung nhằm n&acirc;ng cao kiến thức, kỹ năng quản l&yacute; cho c&aacute;c c&aacute;n bộ. Tới dự lễ khai giảng, về ph&iacute;a trường Đại học Kinh tế Quốc d&acirc;n c&oacute; b&agrave; Trần Thị V&acirc;n Hoa &ndash; Ph&oacute; Hiệu trưởng nh&agrave; trường c&ugrave;ng c&aacute;c giảng vi&ecirc;n đầy kinh nghiệm của trường. Về ph&iacute;a VBI c&oacute; &ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc VBI v&agrave; đ&ocirc;ng đủ học vi&ecirc;n l&agrave; c&aacute;n bộ cấp trung của VBI.</p>\r\n<p>Chương tr&igrave;nh đ&agrave;o tạo sẽ gi&uacute;p c&aacute;c c&aacute;n bộ VBI hiểu r&otilde; vai tr&ograve; v&agrave; vị tr&iacute;, h&igrave;nh ảnh của một c&aacute;n bộ quản l&yacute; cấp trung chuy&ecirc;n nghiệp trong doanh nghiệp bảo hiểm, nắm vững c&aacute;c kiến thức v&agrave; kỹ năng cần thiết để n&acirc;ng cao năng lực quản trị, x&acirc;y dựng kế hoạch v&agrave; quản l&yacute; thực hiện mục ti&ecirc;u của đơn vị hiệu quả để trở th&agrave;nh một quản l&yacute; cấp trung chuy&ecirc;n nghiệp.</p>\r\n<p>Trong khu&ocirc;n khổ kh&oacute;a học, học vi&ecirc;n sẽ c&ugrave;ng nhau trao đổi, chia sẻ v&agrave; thảo luận dựa tr&ecirc;n cơ sở l&yacute; thuyết v&agrave; c&aacute;c t&igrave;nh huống cụ thể về nhiều vấn đề nhằm n&acirc;ng cao năng lực quản l&yacute; của c&aacute;c học vi&ecirc;n. B&ecirc;n cạnh đ&oacute;, học vi&ecirc;n c&ograve;n được chia sẻ những kinh nghiệm thực tế từ c&aacute;c doanh nghiệp lớn của Việt Nam.</p>\r\n<p><img src=\"../../../uploads/photo dao tao KTQD.jpg\" alt=\"\" width=\"969\" height=\"439\" /></p>\r\n<p style=\"text-align: center;\"><em>(C&aacute;c c&aacute;n bộ VBI chụp ảnh c&ugrave;ng c&aacute;c giảng vi&ecirc;n trường Đại học Kinh tế quốc d&acirc;n)</em></p>\r\n<p>Định hướng ph&aacute;t triển nguồn nh&acirc;n lực đi đ&ocirc;i với tuyển dụng v&agrave; đ&agrave;o tạo, VBI l&ecirc;n kế hoạch đ&agrave;o tạo h&agrave;ng năm v&agrave; thực hiện nghi&ecirc;m t&uacute;c v&agrave; kỹ lưỡng c&ocirc;ng t&aacute;c tuyển dụng, c&aacute;c kh&oacute;a đ&agrave;o tạo được ph&acirc;n loại cho c&aacute;c đối tượng c&aacute;n bộ ph&ugrave; hợp với chiến lược kinh doanh từng giai đoạn nhằm ph&aacute;t triển nguồn nh&acirc;n lực c&oacute; t&iacute;nh chiến lược.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 06:34:52', '2018-03-21 17:34:52', 'images/news/1520417785_photo-dao_635641124458723149_HasThumb_Thumb.jpg.png'),
(125, 2, 'VBI tăng trưởng doanh thu phí bảo hiểm 141% trong Quý I 2015', 'vbi-tang-truong-doanh-thu-phi-bao-hiem-141-trong-quy-i-2015', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) đạt kết quả kinh doanh Qu&yacute; I/2015 với doanh thu ph&iacute; bảo hiểm tăng 141% so với c&ugrave;ng kỳ năm trước. Năm 2015, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI l&agrave; 500 tỷ đồng, tăng trưởng 50% so với 2014.&nbsp;</span></p>', '<p>Bảo hiểm VietinBank (VBI) đạt kết quả kinh doanh Qu&yacute; I/2015 với doanh thu ph&iacute; bảo hiểm tăng 141% so với c&ugrave;ng kỳ năm trước. Cụ thể, doanh thu từ ph&iacute; bảo hiểm trong 3 th&aacute;ng đầu năm l&agrave; 98,6 tỷ đồng, đạt 20% kế hoạch của năm. Năm 2015, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI l&agrave; 500 tỷ đồng, tăng trưởng 50% so với 2014.</p>\r\n<p>Trong 3 th&aacute;ng đầu năm 2015, VBI đ&atilde; th&agrave;nh lập th&ecirc;m 2 Chi nh&aacute;nh, 8 Ph&ograve;ng kinh doanh khu vực. Năm 2015, VBI sẽ tiếp tục đẩy mạnh việc mở rộng mạng lưới hoạt động để đ&aacute;p ứng nhu cầu hoạt động b&aacute;n lẻ, tăng cường c&ocirc;ng t&aacute;c b&aacute;n h&agrave;ng v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng. Dự kiến, VBI sẽ mở th&ecirc;m 3 Chi nh&aacute;nh v&agrave; 15 Ph&ograve;ng kinh doanh khu vực, n&acirc;ng tổng số Chi nh&aacute;nh l&ecirc;n th&agrave;nh 13 Chi nh&aacute;nh v&agrave; 50 Ph&ograve;ng kinh doanh khu vực.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, để tri &acirc;n kh&aacute;ch h&agrave;ng cũng như thu h&uacute;t kh&aacute;ch h&agrave;ng mới, ngay từ những th&aacute;ng đầu năm 2015, VBI đ&atilde; triển khai h&agrave;ng loạt c&aacute;c chương tr&igrave;nh ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng như: &ldquo;Ưu đ&atilde;i bảo hiểm &ocirc; t&ocirc; Ng&agrave;n xe sang vui Tết&rdquo;, &ldquo;Giao dịch nhận qu&agrave; ngay&rdquo;, &ldquo;Ưu đ&atilde;i bảo hiểm l&atilde;i h&egrave; giảm nhiệt&rdquo;, v.v...</p>\r\n<p>Ng&agrave;y 21/03/2015, Bảo hiểm VietinBank (VBI) được vinh danh &ldquo;Thương hiệu mạnh Việt Nam 2014&rdquo;. Đ&acirc;y l&agrave; danh hiệu uy t&iacute;n do Thời b&aacute;o Kinh tế Việt Nam phối hợp với Cục X&uacute;c tiến thương mại (Bộ C&ocirc;ng Thương) tổ chức thường ni&ecirc;n nhằm vinh danh những doanh nghiệp c&oacute; th&agrave;nh t&iacute;ch xuất sắc trong sản xuất kinh doanh, cung ứng dịch vụ, thực hiện đầy đủ nghĩa vụ nộp thuế, đảm bảo lợi &iacute;ch người lao động, t&iacute;ch cực tham gia c&aacute;c hoạt động x&atilde; hội v&agrave; đ&oacute;ng g&oacute;p cho cộng đồng. L&agrave; một trong 95 doanh nghiệp được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo; &ndash; VBI đ&atilde; khẳng định được t&ecirc;n tuổi v&agrave; tạo được vị thế tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p>Nhằm đổi mới to&agrave;n diện cơ cấu tổ chức, n&acirc;ng cao năng lực t&agrave;i ch&iacute;nh, hiệu quả sử dụng vốn v&agrave; t&agrave;i sản, trong năm 2015, VBI sẽ t&iacute;ch cực chuyển đổi m&ocirc; h&igrave;nh tổ chức, mở rộng mạng lưới hoạt động, &aacute;p dụng c&ocirc;ng nghệ hiện đại v&agrave;o việc quản l&yacute;; tăng cường h&igrave;nh ảnh, thương hiệu của c&ocirc;ng ty, đẩy nhanh tiến tr&igrave;nh cổ phần h&oacute;a. Việc cổ phần h&oacute;a sẽ gi&uacute;p VBI t&igrave;m kiếm, lựa chọn được những cổ đ&ocirc;ng chiến lược l&agrave; c&aacute;c doanh nghiệp bảo hiểm, t&aacute;i bảo hiểm lớn tr&ecirc;n thế giới, đổi mới c&ocirc;ng nghệ, học hỏi phương thức kinh doanh v&agrave; quản l&yacute; ti&ecirc;n tiến, cải thiện v&agrave; mở rộng mạng lưới, tăng khả năng cạnh tranh, g&oacute;p phần thực hiện được định hướng x&acirc;y dựng VietinBank trở th&agrave;nh Tập đo&agrave;n t&agrave;i ch&iacute;nh h&ugrave;ng mạnh.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-07 22:17:56', '2018-03-07 22:17:56', 'images/news/1520417876_anh-dang-_635663266230123585_HasThumb_Thumb.jpg.png'),
(126, 2, 'VBI chi trả 250 triệu đồng bảo hiểm cho khách hàng tại Gia Lai', 'vbi-chi-tra-250-trieu-dong-bao-hiem-cho-khach-hang-tai-gia-lai', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 18/05/2015, đại diện Bảo Hiểm VietinBank (VBI) c&ugrave;ng Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền 250 triệu đồng cho gia đ&igrave;nh &ocirc;ng Phạm Văn Sơn.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 18/05/2015, đại diện Bảo Hiểm VietinBank (VBI) c&ugrave;ng Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền 250 triệu đồng cho gia đ&igrave;nh &ocirc;ng Phạm Văn Sơn.<br /> <br />&Ocirc;ng Phạm Văn Sơn mua sản phẩm Bảo Hiểm Con Người VietinCare của VBI &ndash; Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n v&agrave;o th&aacute;ng 8 năm 2014. Tới ng&agrave;y 11/02/2015, &ocirc;ng Phạm Văn Sơn kh&ocirc;ng may tử vong tại nh&agrave; ri&ecirc;ng TDP 2, Thị Trấn Chư Pr&ocirc;ng, huyện Chư Pr&ocirc;ng, tỉnh Gia Lai. Ngay khi nhận được tin b&aacute;o từ gia đ&igrave;nh, c&aacute;n bộ VBI CN T&acirc;y Nguy&ecirc;n đ&atilde; nhanh ch&oacute;ng tới thăm viếng v&agrave; hướng dẫn gia đ&igrave;nh thu thập hồ sơ để l&agrave;m thủ tục nhận bảo hiểm.</span></p>\r\n<p><img src=\"../../../uploads/IMG_1591.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: Đại diện Bảo Hiểm VietinBank (VBI) c&ugrave;ng Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Gia Lai<br />trao số tiền 250 triệu đồng cho gia đ&igrave;nh &ocirc;ng Phạm Văn Sơn)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">B&agrave; Nguyễn Thị Nga - vợ của &ocirc;ng Sơn x&uacute;c động chia sẻ: &ldquo;T&ocirc;i rất cảm động khi nhận được 250 triệu đồng tiền bảo hiểm từ VBI. H&ocirc;m hay tin &ocirc;ng nh&agrave; t&ocirc;i mất, c&aacute;c anh chị VBI CN T&acirc;y Nguy&ecirc;n đ&atilde; gọi điện v&agrave; tới chia buồn c&ugrave;ng gia đ&igrave;nh. Hơn nữa c&ograve;n hướng dẫn c&aacute;c thủ tục bảo hiểm chu đ&aacute;o v&agrave; tận t&igrave;nh. T&ocirc;i rất tin tưởng ở Bảo hiểm Vietinbank.&rdquo;</span></p>\r\n<p><img src=\"../../../uploads/IMG_1596.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: B&agrave; Nguyễn Thị Nga - vợ của &ocirc;ng Sơn x&uacute;c động chia sẻ)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Ph&iacute;a ng&acirc;n h&agrave;ng VietinBank, &ocirc;ng Nguyễn Cư - ph&oacute; gi&aacute;m đốc phụ tr&aacute;ch b&aacute;n lẻ của CN VTB Gia Lai cho biết : &ldquo;Bảo hiểm VietinCare l&agrave; sản phẩm ưu việt m&agrave; c&aacute;n bộ VietinBank CN Gia Lai lu&ocirc;n tư vấn kh&aacute;ch h&agrave;ng vay vốn tham gia. Với sản phẩm n&agrave;y, gia đ&igrave;nh kh&aacute;ch h&agrave;ng sẽ giảm nỗi lo chi trả nợ nợ ng&acirc;n h&agrave;ng khi kh&aacute;ch h&agrave;ng gặp rủi ro. Về ph&iacute;a ng&acirc;n h&agrave;ng, ch&uacute;ng t&ocirc;i cũng kh&ocirc;ng phải xử l&yacute; nợ xấu hay l&agrave;m c&aacute;c biện ph&aacute;p thu hồi nợ. Qua sự việc của &ocirc;ng Sơn đ&atilde; cho thấy điểm ưu việt lớn nhất của g&oacute;i bảo hiểm VietinCare m&agrave; kh&aacute;ch h&agrave;ng ch&uacute;ng t&ocirc;i lựa chọn. Trong thời gian tới, để đảm bảo lợi &iacute;ch tốt nhất của kh&aacute;ch h&agrave;ng, ph&iacute;a ng&acirc;n h&agrave;ng ch&uacute;ng t&ocirc;i sẽ tăng cường phối hợp nhiều hơn nữa với VBI trong việc kết hợp hoạt động t&iacute;n dụng của ng&acirc;n h&agrave;ng với hoạt động bảo hiểm của VBI.&rdquo;</span></p>\r\n<p><img src=\"../../../uploads/IMG_1584.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: &ocirc;ng Nguyễn Cư - ph&oacute; gi&aacute;m đốc phụ tr&aacute;ch b&aacute;n lẻ của CN VTB Gia Lai)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Với sản phẩm VietinCare v&agrave; c&ocirc;ng t&aacute;c bồi thường nhanh ch&oacute;ng, Bảo hiểm VietinBank một lần nữa khẳng định được sản phẩm v&agrave; dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 06:36:08', '2018-03-21 17:36:08', 'images/news/1520418056_IMG_159_635676302596799580_HasThumb_Thumb.JPG.png'),
(127, 2, 'Đại hội Đảng bộ Công ty Bảo hiểm VietinBank (VBI)', 'dai-hoi-dang-bo-cong-ty-bao-hiem-vietinbank-vbi', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 15/5, Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức đại hội Đảng bộ lần I nhiệm kỳ 2015- 2020 tại trụ sở ch&iacute;nh của VBI - H&agrave; Nội. Đảng bộ Bảo hiểm l&agrave; Đảng bộ cơ sở trực thuộc Đảng bộ Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam được n&acirc;ng cấp từ chi bộ cơ sở l&ecirc;n đảng bộ cơ sở từ ng&agrave;y 10/...&nbsp;</span></p>', '<p>Ng&agrave;y<span style=\"font-family: Arial;\"> 15/5, Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức đại hội Đảng bộ lần I nhiệm kỳ 2015- 2020 tại trụ sở ch&iacute;nh của VBI - H&agrave; Nội. Đảng bộ Bảo hiểm l&agrave; Đảng bộ cơ sở trực thuộc Đảng bộ Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam được n&acirc;ng cấp từ chi bộ cơ sở l&ecirc;n đảng bộ cơ sở từ ng&agrave;y 10/4/2015 tại Quyết định số 627- QĐ/ĐUNHCT.<br /> <br />Đến dự đại hội Đảng bộ Bảo hiểm Vietinbank c&oacute;:<br />- Đồng ch&iacute; Nguyễn Hồng V&acirc;n - Ủy vi&ecirc;n BCH Đảng bộ Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam, Ủy vi&ecirc;n HĐQT NHCTVN, Chủ tịch C&ocirc;ng ty Bảo hiểm.<br />- Đồng ch&iacute; Vũ Thị Thục Anh &ndash; C&aacute;n bộ Ban tổ chức NHCTVN<br />- Đồng ch&iacute; Mai Thu Hương &ndash; C&aacute;n bộ Văn ph&ograve;ng đảng ủy NHCTVN<br />C&ugrave;ng với 33 đảng vi&ecirc;n l&agrave; những đồng ch&iacute; gương mẫu, đầu tầu trong c&aacute;c tổ chức C&ocirc;ng đo&agrave;n, v&agrave; những quần ch&uacute;ng ưu t&uacute; tại c&ocirc;ng ty Bảo Hiểm VietinBank.<br /> <br />B&aacute;o c&aacute;o ch&iacute;nh trị tại Đại hội, đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Đảng ủy -Tổng Gi&aacute;m Đốc VBI cho biết: C&aacute;n bộ, đảng vi&ecirc;n v&agrave; người lao động t&iacute;ch cực hưởng ứng &ldquo;Học tập v&agrave; l&agrave;m theo tấm gương đạo đức Hồ Ch&iacute; Minh&rdquo;. X&acirc;y dựng đạo đức, lối sống, nhận thức tư tưởng đ&uacute;ng đắn dưới sự l&atilde;nh đạo của Đảng; Dưới sự chỉ đạo của Đảng ủy, Ban l&atilde;nh đạo C&ocirc;ng ty, nhiệm kỳ 2013 &ndash; 2015, Chi bộ Bảo hiểm l&atilde;nh đạo c&aacute;n bộ, đảng vi&ecirc;n v&agrave; người lao động phấn đấu ho&agrave;n th&agrave;nh xuất sắc c&aacute;c chỉ ti&ecirc;u nhiệm vụ được giao. Ph&aacute;t huy vai tr&ograve;, sức mạnh tập thể thường duy tr&igrave; chế độ sinh hoạt theo qui định của Đảng; Phố biến kịp thời c&aacute;c chủ trương đường lối của Đảng, ch&iacute;nh s&aacute;ch của Nh&agrave; nước.</span></p>\r\n<p><img src=\"../../../uploads/IMG_2894.jpg\" alt=\"\" width=\"1366\" height=\"854\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đồng Ch&iacute; L&ecirc; Tuấn Dũng ph&aacute;t biểu b&aacute;o c&aacute;o ch&iacute;nh trị tại đại hội)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong b&agrave;i ph&aacute;t biểu tại đại hội, đồng ch&iacute; Nguyễn Hồng V&acirc;n vui mừng trước những th&agrave;nh quả Đảng bộ C&ocirc;ng ty Bảo hiểm Vietinbank đ&atilde; đạt được trong năm vừa qua, mong muốn Đảng bộ tiếp tục ph&aacute;t huy thế mạnh v&agrave; kh&ocirc;ng ngừng ph&aacute;t triển hơn nữa trong nhiệm kỳ mới 2015-2020. Song song với đ&oacute; l&agrave; nhiệm vụ, c&ocirc;ng t&aacute;c ph&aacute;t triển Đảng bộ cơ sở trong sạch vững mạnh, thực hiện tốt chủ trương, đường lối, chỉ thị, nghị quyết của Đảng, ch&iacute;nh s&aacute;ch, ph&aacute;p luật của Nh&agrave; nước.</span></p>\r\n<p><img src=\"../../../uploads/IMG_2931.jpg\" alt=\"\" width=\"1366\" height=\"1036\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: Đồng ch&iacute; Nguyễn Hồng V&acirc;n ph&aacute;t biểu tại đại hội)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Tại Đại hội, với tinh thần d&acirc;n chủ, c&ocirc;ng khai v&agrave; theo quy chế bầu cử trong đảng đ&atilde; bầu Ban Chấp h&agrave;nh Đảng bộ Bảo hiểm Vietinbank nhiệm kỳ 2015-2020 gồm 5 đồng ch&iacute;. Bầu đại biểu đi dự Đại hội đại biểu Đảng bộ NHCT lần thứ IX, nhiệm kỳ 2015 &ndash; 2020 gồm 5 đồng ch&iacute;.</span></p>\r\n<p><img src=\"../../../uploads/IMG_2975.jpg\" alt=\"\" width=\"1366\" height=\"433\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đồng ch&iacute; Nguyễn Hồng V&acirc;n v&agrave; Ban chấp h&agrave;nh Đảng Bộ nhiệm kỳ 2015-2020)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Đại hội đ&atilde; biểu quyết th&ocirc;ng qua Nghị quyết Đại hội với mục ti&ecirc;u phấn đấu từ nay đến năm 2020 li&ecirc;n tục ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ ch&iacute;nh trị được giao; phấn đấu 100% tổ chức đảng trực thuộc đạt trong sạch vững mạnh; 100% đảng vi&ecirc;n xếp loại từ ho&agrave;n th&agrave;nh tốt nhiệm vụ trở l&ecirc;n; l&atilde;nh đạo c&aacute;c tổ chức đo&agrave;n thể vững mạnh to&agrave;n diện. Tiếp tục ph&aacute;t huy những kết quả đạt được, coi trọng nhiệm vụ x&acirc;y dựng Đảng gắn với n&acirc;ng cao chất lượng hoạt động của c&aacute;c đo&agrave;n thể, x&acirc;y dựng chi bộ, đảng bộ trong sạch, vững mạnh; tiếp tục chủ động nghi&ecirc;n cứu, đề xuất, tham mưu gi&uacute;p Ban L&atilde;nh đạo chỉ đạo c&aacute;c nội dung li&ecirc;n quan đến nghiệp vụ quản trị; chủ động đề xuất những m&ocirc; h&igrave;nh c&ocirc;ng việc mới, c&oacute; t&iacute;nh đột ph&aacute;, c&oacute; t&iacute;nh ứng dụng cao li&ecirc;n quan đến c&ocirc;ng t&aacute;c h&agrave;nh ch&iacute;nh - quản trị &aacute;p dụng trong Bảo Hiểm Vietinbank; tăng cường c&ocirc;ng t&aacute;c gi&aacute;o dục ch&iacute;nh trị, tư tưởng cho c&aacute;n bộ, đảng vi&ecirc;n; l&agrave;m tốt c&ocirc;ng t&aacute;c ph&aacute;t triển đảng vi&ecirc;n mới; quan t&acirc;m, đ&agrave;o tạo v&agrave; bồi dưỡng c&aacute;n bộ, nh&acirc;n vi&ecirc;n trẻ c&oacute; năng lực, tr&igrave;nh độ; thực hiện tốt c&ocirc;ng t&aacute;c bảo vệ ch&iacute;nh trị nội bộ, bảo vệ Đảng.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 06:37:13', '2018-03-21 17:37:13', 'images/news/1520418749_IMG_297_635676308814262703_HasThumb_Thumb.jpg.png'),
(128, 2, 'VBI phối hợp VietinBank chính thức triển khai bán lẻ BHSK VBICare', 'vbi-phoi-hop-vietinbank-chinh-thuc-trien-khai-ban-le-bhsk-vbicare', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 25/5/2015, Bảo hiểm VietinBank (VBI) phối hợp c&ugrave;ng VietinBank ch&iacute;nh thức triển khai sản phẩm Bảo hiểm sức khỏe VBI Care qua k&ecirc;nh ph&acirc;n phối Bancassurance. Theo đ&oacute;, c&aacute;n bộ VietinBank sẽ trực tiếp tư vấn v&agrave; cấp đơn bảo hiểm cho kh&aacute;ch h&agrave;ng. Với sản phẩm mới n&agrave;y, VBI sẽ cung cấp ...&nbsp;</span></p>', '<p>Ng&agrave;y 25/5/2015, Bảo hiểm VietinBank (VBI) phối hợp c&ugrave;ng VietinBank ch&iacute;nh thức triển khai sản phẩm Bảo hiểm sức khỏe VBI Care qua k&ecirc;nh ph&acirc;n phối Bancassurance. Theo đ&oacute;, c&aacute;n bộ VietinBank sẽ trực tiếp tư vấn v&agrave; cấp đơn bảo hiểm cho kh&aacute;ch h&agrave;ng. Với sản phẩm mới n&agrave;y, VBI sẽ cung cấp cho kh&aacute;ch h&agrave;ng của VietinBank một giải ph&aacute;p t&agrave;i ch&iacute;nh to&agrave;n diện hơn trước những rủi ro trong cuộc sống&hellip;</p>\r\n<p>Được đ&aacute;nh gi&aacute; l&agrave; sản phẩm tiềm năng, mang lại nguồn lợi lớn v&agrave; d&agrave;i hạn cho Ng&acirc;n h&agrave;ng, VBI đ&atilde; thiết kế sản phẩm với nhiều t&iacute;nh năng ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng như: Quyền lợi bảo hiểm phong ph&uacute; với 04 chương tr&igrave;nh đ&ecirc; kh&aacute;ch h&agrave;ng lựa chọn t&ugrave;y theo nhu cầu v&agrave; khả năng t&agrave;i ch&iacute;nh của m&igrave;nh; Ap dụng tr&ecirc;n phạm vi to&agrave;n cầu đối với chương tr&igrave;nh Đặc biệt; Quyền lợi bảo hiểm cho tai nạn l&ecirc;n đến 2 tỷ đồng; Quyền lợi nội tr&uacute; l&ecirc;n đến 400 triệu, thai sản 100 triệu, ngoại tr&uacute; 40 triệu; Bảo hiểm cho bệnh hiểm ngh&egrave;o l&ecirc;n đến 200 triệu; Trẻ em tr&ecirc;n 12 th&aacute;ng tuổi c&oacute; thể tham gia bảo hiểm độc lập; Ưu đ&atilde;i giảm ph&iacute; cho Gia đ&igrave;nh v&agrave; kh&aacute;ch h&agrave;ng t&aacute;i tục kh&ocirc;ng c&oacute; khiếu nại bồi thường trong 1 năm trở l&ecirc;n; Kh&ocirc;ng y&ecirc;u cầu kh&aacute;m sức khỏe trước khi đăng k&yacute;; Bảo l&atilde;nh viện ph&iacute; tại tr&ecirc;n 100 bệnh viện, ph&ograve;ng kh&aacute;m li&ecirc;n kết tr&ecirc;n to&agrave;n quốc; Bồi thường nhanh, ch&iacute;nh x&aacute;c qua Insmart,&hellip;</p>\r\n<p>Đặc biệt, đến hết ng&agrave;y 31/08/2015, c&aacute;c kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care sẽ c&oacute; cơ hội nhận được h&agrave;ng trăm phần qu&agrave; chăm s&oacute;c sức khỏe hấp dẫn từ VBI như: Nhiệt kế điện tử Omron, M&aacute;y đo huyết &aacute;p Omron.<br /> <br />Nhằm triển khai sản phẩm th&agrave;nh c&ocirc;ng, VBI đ&atilde; tổ chức c&aacute;c buổi đ&agrave;o tạo sản phẩm tập trung tại H&agrave; Nội v&agrave; Hồ Ch&iacute; Minh cho gần 200 c&aacute;n bộ đầu mối b&aacute;n lẻ tại VietinBank, gi&uacute;p c&aacute;n bộ b&aacute;n nắm r&otilde; về sản phẩm, n&acirc;ng cao nghiệp vụ chuy&ecirc;n m&ocirc;n để từ đ&oacute; c&oacute; thể tư vấn v&agrave; b&aacute;n sản phẩm cho kh&aacute;ch h&agrave;ng.</p>\r\n<p><img src=\"../../../uploads/photo 2.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>(ảnh: Đ&agrave;o tạo BHSK tại H&agrave; Nội)</em></p>\r\n<p><img src=\"../../../uploads/20150523_141042.jpg\" alt=\"\" width=\"1366\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\">(ảnh: Đ&agrave;o tạo BHSK tại TP.Hồ Ch&iacute; Minh)</p>\r\n<p>Sản phẩm Bảo hiểm sức khỏe VBI Care ra đời để b&ugrave; đắp những hạn chế của Bảo hiểm y tế, bảo vệ to&agrave;n diện cho kh&aacute;ch h&agrave;ng với hầu hết c&aacute;c rủi ro về bệnh, tai nạn c&oacute; thể xảy ra trong cuộc sống thường ng&agrave;y. Hi vọng rằng, th&ocirc;ng qua sản phẩm n&agrave;y, VBI n&oacute;i ri&ecirc;ng v&agrave; VietinBank n&oacute;i chung sẽ tiếp tục khẳng định chất lượng sản phẩm v&agrave; dich vụ v&igrave; sự an t&acirc;m t&agrave;i ch&iacute;nh cho kh&aacute;ch h&agrave;ng.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 06:39:20', '2018-03-21 17:39:20', 'images/news/1521614360_photo 2.JPG'),
(129, 2, 'VBI bổ nhiệm 4 cán bộ vào các vị trí chủ chốt tại Trụ Sở Chính', 'vbi-bo-nhiem-4-can-bo-vao-cac-vi-tri-chu-chot-tai-tru-so-chinh', '<p><span style=\"font-family: Arial;\">Chiều ng&agrave;y 08/06/2015, tại Trụ sở ch&iacute;nh (H&agrave; Nội), Bảo Hiểm Vietinbank (VBI) tổ chức lễ trao quyết định bổ nhiệm 4 c&aacute;n bộ v&agrave;o vị tr&iacute; mới. Tham dự lễ trao quyết định c&oacute; b&agrave; Nguyễn Hồng V&acirc;n - Chủ tịch v&agrave; Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng c&aacute;c đồng ch&iacute; trưởng ph&ograve;ng &ndash; ph&oacute; ph&ograve;ng tại Trụ sở ch&iacute;nh...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Chiều ng&agrave;y 08/06/2015, tại Trụ sở ch&iacute;nh (H&agrave; Nội), Bảo Hiểm Vietinbank (VBI) tổ chức lễ trao quyết định bổ nhiệm 4 c&aacute;n bộ v&agrave;o vị tr&iacute; mới; bổ nhiệm b&agrave; Phạm Thị Thanh Thủy giữ chức vụ Kế to&aacute;n trưởng C&ocirc;ng ty; bổ nhiệm &ocirc;ng Mai Thanh Hải giữ chức vụ Trưởng ph&ograve;ng Kiểm so&aacute;t nội bộ v&agrave; Ph&aacute;p chế TSC; bổ nhiệm &ocirc;ng Trần Th&aacute;i H&ograve;a giữ chức vụ Ph&oacute; ph&ograve;ng C&ocirc;ng nghệ th&ocirc;ng tin TSC; bổ nhiệm b&agrave; Nguyễn Thị Thanh T&acirc;m giữ chức vụ Ph&oacute; ph&ograve;ng Bồi thường TSC. Trong buổi lễ, Ban L&atilde;nh đạo C&ocirc;ng ty cũng quyết định th&agrave;nh lập ph&ograve;ng Kiểm so&aacute;t nội bộ v&agrave; Ph&aacute;p chế thuộc Trụ sở ch&iacute;nh tr&ecirc;n cơ sở n&acirc;ng cấp Tổ Kiểm tra kiểm so&aacute;t nội bộ. Tham dự lễ trao quyết định c&oacute; b&agrave; Nguyễn Hồng V&acirc;n - Chủ tịch v&agrave; Ban L&atilde;nh đạo C&ocirc;ng ty c&ugrave;ng c&aacute;c đồng ch&iacute; trưởng ph&ograve;ng &ndash; ph&oacute; ph&ograve;ng tại Trụ sở ch&iacute;nh.<br /> <br />Tại buổi lễ, Chủ tịch VBI &ndash; b&agrave; Nguyễn Hồng V&acirc;n v&agrave; Tổng Gi&aacute;m Đốc VBI -&ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm v&agrave; ch&uacute;c mừng 4 c&aacute;n bộ nhận nhiệm vụ mới. B&ecirc;n cạnh đ&oacute;, b&agrave; Nguyễn Hồng V&acirc;n cũng chia sẻ mong muốn với những kinh nghiệm qu&yacute; b&aacute;u trong lĩnh vực bảo hiểm, 4 c&aacute;n bộ được bổ nhiệm mới sẽ ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ v&agrave; trọng tr&aacute;ch được Ban L&atilde;nh đạo VBI giao ph&oacute;.</span></p>\r\n<p><img src=\"../../../uploads/IMG_3011-1.jpg\" alt=\"\" width=\"1366\" height=\"889\" /></p>\r\n<p><span style=\"font-family: Arial;\">Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, thay mặt cho c&aacute;c c&aacute;n bộ mới, b&agrave; Phạm Thị Thanh Thủy - Kế to&aacute;n trưởng thể hiện quyết t&acirc;m phấn đấu v&agrave; nỗ lực hết m&igrave;nh để ho&agrave;n th&agrave;nh c&aacute;c nhiệm vụ được giao, g&oacute;p phần đưa VBI ph&aacute;t triển m&shy;&shy;&shy;&shy;ạnh mẽ.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 06:40:02', '2018-03-21 17:40:02', 'images/news/1520419270_IMG_3011_635694376020644419_HasThumb_Thumb.jpg.png'),
(130, 2, 'Bảo hiểm Vietinbank ra mắt chi nhánh Nghệ An', 'bao-hiem-vietinbank-ra-mat-chi-nhanh-nghe-an', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 17/06/2015, Bảo hiểm Vietinbank (VBI) vui mừng tổ chức lễ ra mắt Chi nh&aacute;nh Nghệ An, tại số 7 đường Nguyễn Sỹ S&aacute;ch, th&agrave;nh phố Vinh, tỉnh Nghệ An. Tham dự buổi lễ c&oacute; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; tổng gi&aacute;m đốc VBI; c&aacute;c c&aacute;n bộ l&atilde;nh đạo ph&ograve;ng tại Trụ sở ch&iacute;nh; đại diện c&aacute;c chi nh&aacute;nh ...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 17/06/2015, Bảo hiểm Vietinbank (VBI) vui mừng tổ chức lễ ra mắt Chi nh&aacute;nh Nghệ An, tại số 7 đường Nguyễn Sỹ S&aacute;ch, th&agrave;nh phố Vinh, tỉnh Nghệ An. Tham dự buổi lễ c&oacute; &ocirc;ng L&ecirc; Tuấn Dũng &ndash; tổng gi&aacute;m đốc VBI; c&aacute;c c&aacute;n bộ l&atilde;nh đạo ph&ograve;ng tại Trụ sở ch&iacute;nh; đại diện c&aacute;c chi nh&aacute;nh VBI; đại diện c&aacute;c ph&ograve;ng giao dịch Vietinbank tại Nghệ An.<br /> <br />Tại lễ ra mắt, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng gi&aacute;m đốc VBI, trao quyết định bổ nhiệm gi&aacute;m đốc chi nh&aacute;nh Nghệ An cho b&agrave; Đinh Thị Thu, v&agrave; bổ nhiệm 4 l&atilde;nh đạo cấp ph&ograve;ng cho b&agrave; Phạm Thị Minh Hằng; b&agrave; Nguyễn Thu Ho&agrave;i; &ocirc;ng Ho&agrave;ng Khắc Đức; b&agrave; Trần Thị C&uacute;c.</span></p>\r\n<p><img src=\"../../../uploads/20150617_094850.jpg\" alt=\"\" width=\"1366\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: Lễ c&ocirc;ng bố c&aacute;c quyết định bổ nhiệm c&aacute;n bộ tại Nghệ An)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong b&agrave;i ph&aacute;t biểu trước l&atilde;nh đạo, c&aacute;n bộ nh&acirc;n vi&ecirc;n thuộc chi nh&aacute;nh mới, &ocirc;ng L&ecirc; Tuấn Dũng tin tưởng v&agrave; mong muốn b&agrave; Đinh Thị Thu c&ugrave;ng tất cả c&aacute;n bộ được bổ nhiệm sẽ điều h&agrave;nh VBI Nghệ An ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh trong thời gian tới, đẩy mạnh hơn nữa nghiệp vụ v&agrave; mạng lưới kh&aacute;ch h&agrave;ng trong tương lai.<br /> <br />Đ&aacute;p lại sự tin tưởng từ Ban l&atilde;nh đạo VBI, b&agrave; Đinh Thị Thu thay mặt tập thể c&aacute;n bộ của chi nh&aacute;nh thể hiện quyết t&acirc;m, t&iacute;ch cực phấn đấu để ho&agrave;n th&agrave;nh vượt chỉ ti&ecirc;u được giao, c&ugrave;ng với đ&oacute;, cam kết sẽ cấu hiến hết m&igrave;nh để đưa VBI Nghệ An ph&aacute;t triển mạnh mẽ</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 06:40:34', '2018-03-21 17:40:34', 'images/news/1520419406_20150617__635705577288564189_HasThumb_Thumb.jpg.png'),
(131, 2, 'VBI đồng hành cùng chương trình Màu Hoa Đỏ', 'vbi-dong-hanh-cung-chuong-trinh-mau-hoa-do', '<p><span style=\"font-family: Arial;\">Tối ng&agrave;y 23/07/2015, trong kh&ocirc;ng kh&iacute; trang nghi&ecirc;m m&agrave; ấm &aacute;p tại hội trường Bộ Quốc Ph&ograve;ng số 7 Nguyễn Tri Phương Q. Ba Đ&igrave;nh, H&agrave; Nội, Ban tuy&ecirc;n gi&aacute;o TW phối hợp với B&aacute;o điện tử Đảng cộng sản Việt Nam tổ chức chương tr&igrave;nh nghệ thuật mang t&ecirc;n M&agrave;u hoa đỏ kỉ niệm 68 năm ng&agrave;y thương binh...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Tối ng&agrave;y 23/07/2015, trong kh&ocirc;ng kh&iacute; trang nghi&ecirc;m m&agrave; ấm &aacute;p tại hội trường Bộ Quốc Ph&ograve;ng số 7 Nguyễn Tri Phương Q. Ba Đ&igrave;nh, H&agrave; Nội, Ban tuy&ecirc;n gi&aacute;o TW phối hợp với B&aacute;o điện tử Đảng cộng sản Việt Nam tổ chức chương tr&igrave;nh nghệ thuật mang t&ecirc;n M&agrave;u hoa đỏ kỉ niệm 68 năm ng&agrave;y thương binh liệt sỹ (27/07/1947-27/07/2015). Chương tr&igrave;nh c&oacute; sự tham dự của nhiều l&atilde;nh đạo Đảng, Nh&agrave; nước, c&aacute;c Doanh nghiệp, c&aacute;c tổ chức x&atilde; hội c&ugrave;ng sự theo d&otilde;i của h&agrave;ng triệu người d&acirc;n Việt Nam qua cầu truyền h&igrave;nh vtv2.</span></p>\r\n<p><img src=\"../../../uploads/1 (1)(1).jpg\" alt=\"\" width=\"1366\" height=\"767\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: Ban tổ chức trao qu&agrave; tặng tri &acirc;n c&aacute;c thương binh liệt sỹ tại tỉnh Bắc Ninh)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Với sự quan t&acirc;m v&agrave; tấm l&ograve;ng tri &acirc;n tới c&aacute;c thương binh liệt sỹ, Bảo hiểm Vietinbank (VBI) c&ugrave;ng c&aacute;c Đo&agrave;n thể, Doanh nghiệp kh&aacute;c tr&ecirc;n cả nước h&acirc;n hạnh t&agrave;i trợ cho chương tr&igrave;nh. Theo đ&oacute;, Ban tổ chức đ&atilde; trực tiếp trao tặng gần 1000 phần qu&agrave; gồm sổ tiết kiệm, nh&agrave; t&igrave;nh nghĩa, b&ograve; giống v&agrave; nhiều suất học bổng với tổng gi&aacute; trị 1,8 tỷ đồng đến c&aacute;c mẹ Việt Nam anh h&ugrave;ng, gia đ&igrave;nh c&aacute;c thương binh, bệnh binh c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại nhiều địa phương như Tuy&ecirc;n Quang, Th&aacute;i B&igrave;nh, Bắc Ninh&hellip;</span></p>\r\n<p><img src=\"../../../uploads/1 (3).jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: Đ/c L&ecirc; Thị Quỳnh Hoa nhận bảng v&agrave;ng tri &acirc;n doanh nghiệp t&agrave;i trợ chương tr&igrave;nh)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Đại diện c&ocirc;ng ty, b&agrave; L&ecirc; Thị Quỳnh Hoa- trưởng ph&ograve;ng Ph&aacute;t triển kinh doanh l&ecirc;n nhận bảng v&agrave;ng tri &acirc;n doanh nghiệp hỗ trợ. Trong lời chia sẻ của m&igrave;nh sau chương tr&igrave;nh, đại diện VBI gửi lời cảm ơn đến Ban tổ chức đ&atilde; l&agrave; cầu nối gi&uacute;p VBI chuyển những phần qu&agrave; cũng như b&agrave;y tỏ l&ograve;ng biết ơn của m&igrave;nh đến những người đ&atilde; d&agrave;nh trọn tuổi trẻ v&agrave; cuộc đời m&igrave;nh cho non s&ocirc;ng Tổ quốc h&ocirc;m nay.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:05:16', '2018-03-21 18:05:16', 'images/news/1520419578_1-2_635733739890037881_HasThumb_Thumb.JPG.png'),
(132, 2, 'Bảo hiểm VietinBank - Nhãn hiệu nổi tiếng- nhãn hiệu cạnh tranh Việt Nam 2015', 'bao-hiem-vietinbank-nhan-hieu-noi-tieng-nhan-hieu-canh-tranh-viet-nam-2015', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 25/07/2015, Bảo hiểm VietinBank (VBI) đ&atilde; vinh dự nhận giải thưởng Top 20 nh&atilde;n hiệu nổi tiếng- nh&atilde;n hiệu cạnh tranh tại Lễ t&ocirc;n vinh trao giải Nh&atilde;n hiệu nổi tiếng &ndash; Nh&atilde;n hiệu cạnh tranh Việt Nam năm 2015 do Viện Sở hữu tr&iacute; tuệ, Bộ Khoa học v&agrave; C&ocirc;ng nghệ tổ chức ở Nh&agrave; h&aacute;t ca m&uacute;a...&nbsp;</span></p>', '<p>Ng&agrave;y 25/07/2015, Bảo hiểm VietinBank (VBI) đ&atilde; vinh dự nhận giải thưởng Top 20 nh&atilde;n hiệu nổi tiếng- nh&atilde;n hiệu cạnh tranh tại Lễ t&ocirc;n vinh trao giải Nh&atilde;n hiệu nổi tiếng &ndash; Nh&atilde;n hiệu cạnh tranh Việt Nam năm 2015 do Viện Sở hữu tr&iacute; tuệ, Bộ Khoa học v&agrave; C&ocirc;ng nghệ tổ chức ở Nh&agrave; h&aacute;t ca m&uacute;a nhạc Việt Nam, số 8 Huỳnh Th&uacute;c Kh&aacute;ng, Q. Ba Đ&igrave;nh, H&agrave; Nội.</p>\r\n<p>Chương tr&igrave;nh tư vấn v&agrave; b&igrave;nh chọn nh&atilde;n hiệu nổi tiếng &ndash; nh&atilde;n hiệu cạnh tranh Việt Nam được tổ chức thường ni&ecirc;n từ năm 2006 nhằm t&ocirc;n vinh những thương hiệu mạnh, nổi tiếng, g&oacute;p phần ph&aacute;t triển v&agrave; bảo hộ thương hiệu Việt tr&ecirc;n trường quốc tế. Qua những đ&aacute;nh gi&aacute; kh&aacute;ch quan, ch&iacute;nh x&aacute;c từ c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu của Hội đồng tư vấn sở hữu tr&iacute; tuệ, chương tr&igrave;nh đ&atilde; tạo được tầm uy t&iacute;n ảnh hưởng lớn đến thị trường v&agrave; nhận được sự quan t&acirc;m của nhiều tổ chức, doanh nghiệp tr&ecirc;n cả nước.</p>\r\n<p>Với niềm tự h&agrave;o sau những cố gắng suốt 7 năm x&acirc;y dựng v&agrave; ph&aacute;t triển thương hiệu c&ugrave;ng phương ch&acirc;m &ldquo;Bảo to&agrave;n gi&aacute; trị cuộc sống&rdquo;, thay mặt ban l&atilde;nh đạo c&ocirc;ng ty, B&agrave; Phạm Thị Thanh Thủy- trường ph&ograve;ng T&agrave;i ch&iacute;nh- kế to&aacute;n đ&atilde; l&ecirc;n nhận giải tại hội trường Nh&agrave; h&aacute;t.</p>\r\n<p><img src=\"../../../uploads/20150725_102819.jpg\" alt=\"\" width=\"1366\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\"><em>(ảnh: B&agrave; Phạm Thị Thanh Thủy l&ecirc;n nhận giải thưởng Top 20 nh&atilde;n hiệu nổi tiếng- nh&atilde;n hiệu cạnh tranh 2015)</em></p>\r\n<p>L&agrave; một c&ocirc;ng ty đ&oacute;n đầu c&ocirc;ng nghệ trong việc tập trung x&acirc;y dựng hệ thống kinh doanh bảo hiểm qua k&ecirc;nh Bancassurance chuẩn quốc tế, VBI đ&atilde; v&agrave; đang chăm s&oacute;c, bảo vệ kh&aacute;ch h&agrave;ng với c&aacute;c sản phẩm bảo hiểm đa dạng. Trong tương lai, c&ocirc;ng ty sẽ khai th&aacute;c tối đa hiệu quả của hệ thống k&ecirc;nh ph&acirc;n phối n&agrave;y nhằm ph&aacute;t triển một VBI năng động, tận t&acirc;m v&igrave; cộng đồng v&agrave; hội nhập thị trường quốc tế.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:05:51', '2018-03-21 18:05:51', 'images/news/1520419704_20150725__635736131242475433_HasThumb_Thumb.jpg.png'),
(133, 2, 'Bảo hiểm VietinBank chi trả 280 triệu đồng tiền bảo hiểm cho khách hàng tại Phú Thọ', 'bao-hiem-vietinbank-chi-tra-280-trieu-dong-tien-bao-hiem-cho-khach-hang-tai-phu-tho', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 01/08/2015, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Ph&uacute; Thọ đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 280 triệu đồng cho kh&aacute;ch h&agrave;ng Lương Quang Tảo.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 01/08/2015, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) c&ugrave;ng với Ng&acirc;n h&agrave;ng VietinBank Chi nh&aacute;nh Ph&uacute; Thọ đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao số tiền bảo hiểm trị gi&aacute; 280 triệu đồng cho kh&aacute;ch h&agrave;ng Lương Quang Tảo.<br />Kh&aacute;ch h&agrave;ng Lương Quang Tảo tham gia Bảo hiểm con người Vietincare với số tiền bảo hiểm 500 triệu đồng. Ng&agrave;y 07/06/2015, khi đang l&agrave;m việc tại c&ocirc;ng ty &ocirc;ng Tảo bị m&aacute;y cuốn v&agrave;o &eacute;p n&aacute;t cẳng tay tr&aacute;i, hậu quả bị cắt cụt 1/3 cẳng tay tr&aacute;i. Ngay sau khi nhận được th&ocirc;ng tin từ gia đ&igrave;nh cũng như ng&acirc;n h&agrave;ng, VBI T&acirc;y Bắc đ&atilde; phối hợp với VietinBank Chi nh&aacute;nh Ph&uacute; Thọ c&ugrave;ng với gia đ&igrave;nh thu thập hồ sơ để khẩn trương l&agrave;m thủ tục bồi thường cho kh&aacute;ch h&agrave;ng. Ng&agrave;y 01/08/2015, VBI T&acirc;y Bắc đ&atilde; chi trả 280 triệu đồng tiền bồi thường cho &ocirc;ng Lương Quang Tảo v&agrave; chuyền số tiền n&agrave;y cho đơn vị thụ hưởng l&agrave; VietinBank Ph&uacute; Thọ gi&uacute;p gia đ&igrave;nh &ocirc;ng Tảo thanh to&aacute;n khoản vay trước đ&oacute; theo Hợp đồng t&iacute;n dụng v&agrave; bảo hiểm.</span></p>\r\n<p><img src=\"../../../uploads/IMG_2088.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Đại diện VBI trao số tiền bảo hiểm 280 triệu đồng cho kh&aacute;ch h&agrave;ng</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong lời t&acirc;m sự của m&igrave;nh, &ocirc;ng Tảo c&oacute; chia sẻ: &ldquo;T&ocirc;i c&oacute; tham gia g&oacute;i bảo hiểm của Bảo hiểm VietinBank nhưng kh&ocirc;ng may gặp tai nạn. Ngay sau khi nhận được tin từ ph&iacute;a gia đ&igrave;nh, C&ocirc;ng ty Bảo hiểm VietinBank đ&atilde; cử người qua thăm hỏi v&agrave; hướng dẫn thủ tục chi trả số tiền bồi thường 280 triệu cho t&ocirc;i trừ v&agrave;o dư nợ tại ng&acirc;n h&agrave;ng. Đ&acirc;y l&agrave; một nghĩa cử rất đẹp của c&aacute;c anh chị. T&ocirc;i xin cảm ơn Bảo hiểm VietinBank.&rdquo;<br />Về ph&iacute;a Ng&acirc;n h&agrave;ng VietinBank, &ocirc;ng Đỗ Tiến Dũng - Trưởng ph&ograve;ng giao dịch N&ocirc;ng Trang - Chi nh&aacute;nh Ph&uacute; Thọ cho biết: &ldquo;Bảo hiểm con người VietinCare l&agrave; một sản phẩm rất ưu việt. Sản phẩm đ&atilde; gi&uacute;p giảm thiểu tổn thất cho kh&aacute;ch h&agrave;ng, gi&uacute;p kh&aacute;ch h&agrave;ng trang trải nợ vay. Về ph&iacute;a ng&acirc;n h&agrave;ng, ch&uacute;ng t&ocirc;i cũng thu được nợ v&agrave; kh&ocirc;ng phải lo lắng đến vấn đề xử l&yacute; nợ xấu. Kh&ocirc;ng chỉ Vietincare, t&ocirc;i mong muốn tiếp tục được phối hợp với Bảo hiểm VietinBank trong c&aacute;c g&oacute;i sản phẩm li&ecirc;n kết ng&acirc;n h&agrave;ng-bảo hiểm v&agrave; nhiều hoạt động kh&aacute;c trong thời gian tới.<br />Vietincare tuy l&agrave; một sản phẩm đơn giản những lại rất c&oacute; &yacute; nghĩa với người vay vốn tại ng&acirc;n h&agrave;ng VietinBank. Sản phẩm đem lại lợi &iacute;ch nhiều b&ecirc;n khi vừa đảm bảo ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ vừa gi&uacute;p kh&aacute;ch h&agrave;ng y&ecirc;n t&acirc;m c&ocirc;ng t&aacute;c v&agrave; lao động. Xuất ph&aacute;t từ những &yacute; nghĩa nh&acirc;n văn đ&oacute;, BH con người Vietincare đang dần trở th&agrave;nh một người bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu đối với kh&aacute;ch h&agrave;ng vay vốn tại VietinBank.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:06:19', '2018-03-21 18:06:19', 'images/news/1520419901_IMG_208_635745544813808841_HasThumb_Thumb.JPG.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(134, 2, 'VBI và OceanBank ký kết thỏa thuận hợp tác toàn diện', 'vbi-va-oceanbank-ky-ket-thoa-thuan-hop-tac-toan-dien', '<p><span style=\"font-family: Arial;\">Chiều ng&agrave;y 12/08/2015, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) v&agrave; Ng&acirc;n h&agrave;ng TM TNHH MTV Đại dương (OceanBank) đ&atilde; k&yacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện nhằm tăng cường mối quan hệ hợp t&aacute;c Ng&acirc;n h&agrave;ng- Bảo hiểm trong cung ứng c&aacute;c dịch vụ đồng thời th&uacute;c đẩy mọi hoạt động t&agrave;i ch&iacute;nh song ph...&nbsp;</span></p>', '<p>Chiều ng&agrave;y 12/08/2015, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) v&agrave; Ng&acirc;n h&agrave;ng TM TNHH MTV Đại dương (OceanBank) đ&atilde; k&yacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện nhằm tăng cường mối quan hệ hợp t&aacute;c Ng&acirc;n h&agrave;ng- Bảo hiểm trong cung ứng c&aacute;c dịch vụ đồng thời th&uacute;c đẩy mọi hoạt động t&agrave;i ch&iacute;nh song phương.</p>\r\n<p>Đến tham dự buổi lễ, Đại diện ph&iacute;a VBI c&oacute; b&agrave; Nguyễn Hồng V&acirc;n- Chủ tịch C&ocirc;ng ty, &ocirc;ng L&ecirc; Tuấn Dũng- Tổng gi&aacute;m đốc c&ugrave;ng c&aacute;c l&atilde;nh đạo ph&ograve;ng ban. Về ph&iacute;a OceanBank c&oacute; &ocirc;ng Đỗ Thanh Sơn - Chủ tịch Hội đồng th&agrave;nh vi&ecirc;n, &ocirc;ng Ng&ocirc; Anh Tuấn-Tổng gi&aacute;m đốc c&ugrave;ng l&atilde;nh đạo c&aacute;c ph&ograve;ng ban ph&iacute;a ng&acirc;n h&agrave;ng cũng đến tham gia lễ k&yacute; kết thỏa thuận.</p>\r\n<p>Từ th&aacute;ng 05/2015, Ng&acirc;n h&agrave;ng OceanBank c&oacute; sự chuyển đổi m&ocirc; h&igrave;nh c&ocirc;ng ty từ Ng&acirc;n h&agrave;ng TMCP th&agrave;nh Ng&acirc;n h&agrave;ng 100% Vốn điều lệ Nh&agrave; nước c&oacute; sự quản trị của Ng&acirc;n h&agrave;ng VietinBank. Điều n&agrave;y đ&atilde; mở ra một bước ph&aacute;t triển mới trong tiến tr&igrave;nh hoạt động của c&ocirc;ng ty v&agrave; cũng l&agrave; cơ duy&ecirc;n gắn kết OceanBank với Bảo hiểm VietinBank.</p>\r\n<p><img src=\"../../../uploads/D.jpg\" alt=\"\" width=\"1366\" height=\"885\" /></p>\r\n<p style=\"text-align: center;\"><em>Lễ k&yacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện giữa VBI v&agrave; OceanBank</em></p>\r\n<p>Tr&ecirc;n cơ sở thỏa thuận hợp t&aacute;c to&agrave;n diện, OceanBank sẽ hợp t&aacute;c với Bảo hiểm VietinBank trong việc kinh doanh c&aacute;c sản phẩm bảo hiểm c&ugrave;ng c&aacute;c dịch vụ t&agrave;i ch&iacute;nh ưu đ&atilde;i trọn g&oacute;i hướng tới phục vụ kh&aacute;ch h&agrave;ng hai b&ecirc;n tr&ecirc;n cơ sở t&ocirc;n trọng bảo mật th&ocirc;ng tin. VBI cam kết sử dụng c&aacute;c dịch vụ Ng&acirc;n h&agrave;ng tại OceanBank v&agrave; thực hiện c&aacute;c giao dịch với kh&aacute;ch h&agrave;ng qua OceanBank nhằm n&acirc;ng cao chất lượng dịch vụ, mở rộng thị trường đồng thời tăng nguồn lợi đầu tư cho mỗi đơn vị.</p>\r\n<p><img src=\"../../../uploads/B.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em>B&agrave; Nguyễn Hồng V&acirc;n Chủ tịch C&ocirc;ng ty VBI ph&aacute;t biểu tại lễ k&iacute; kết</em></p>\r\n<p>Tại lễ k&yacute; kết thỏa thuận, b&agrave; Nguyễn Hồng V&acirc;n- Chủ tịch c&ocirc;ng ty ph&aacute;t biểu dự định &ldquo;Sau buổi lễ k&iacute; kết VBI sẽ phối hợp với OceanBank triển khai hệ thống kinh doanh bảo hiểm qua ng&acirc;n h&agrave;ng theo m&ocirc; h&igrave;nh Bancassurance một c&aacute;ch chuy&ecirc;n nghiệp, b&agrave;i bản nhằm cung cấp c&aacute;c sản phẩm dịch vụ đa dạng th&ocirc;ng qua c&aacute;c sản phẩm t&iacute;ch hợp d&agrave;nh cho cả kh&aacute;ch h&agrave;ng vay vốn v&agrave; kh&aacute;ch h&agrave;ng tiền gửi tại OceanBank. Đặc biệt, c&aacute;n bộ ng&acirc;n h&agrave;ng sẽ được đ&agrave;o tạo chuy&ecirc;n s&acirc;u nhiều kĩ năng, được hỗ trợ cấp đơn qua phần mềm t&iacute;nh ph&iacute; tự động v&agrave; tham gia c&aacute;c chương tr&igrave;nh marketing, thi đua khen thưởng hấp dẫn như chương tr&igrave;nh Ng&ocirc;i sao Bancassurance do VBI tổ chức.&rdquo;</p>\r\n<p><img src=\"../../../uploads/A.jpg\" alt=\"\" width=\"1366\" height=\"888\" /></p>\r\n<p style=\"text-align: center;\"><em>&Ocirc;ng Đỗ Thanh Sơn- Chủ tịch HĐTV OceanBank ph&aacute;t biểu tại lễ k&iacute; kết</em></p>\r\n<p>Đ&aacute;p lại thiện ch&iacute; từ ph&iacute;a VBI, &ocirc;ng Đỗ Thanh Sơn- Chủ tịch HĐTV Ng&acirc;n h&agrave;ng OceanBank đ&atilde; b&agrave;y tỏ niềm vinh hạnh khi được hợp t&aacute;c c&ugrave;ng Bảo hiểm VietinBank. &Ocirc;ng cũng chia sẻ &ldquo;Ph&iacute;a Ng&acirc;n h&agrave;ng rất mong muốn được tham gia nhiều chương tr&igrave;nh v&agrave; hoạt động do VBI cũng như Ng&acirc;n h&agrave;ng C&ocirc;ng thương tổ chức\". &Ocirc;ng hi vọng hai b&ecirc;n sẽ c&ugrave;ng chia sẻ cơ sở dữ liệu kh&aacute;ch h&agrave;ng, gắn kết chặt chẽ hơn nữa để vượt qua mọi kh&oacute; khăn tiến tới th&agrave;nh c&ocirc;ng trong tương lai.<br /> <br />Thỏa thuận hợp t&aacute;c to&agrave;n diện kh&ocirc;ng chỉ đ&aacute;nh dấu bước ngoặt mới trong mối quan hệ gắn b&oacute; l&acirc;u d&agrave;i giữa VBI v&agrave; OceanBank v&igrave; lợi &iacute;ch chung của hai đơn vị m&agrave; c&ograve;n khẳng định phương hướng kinh doanh năng động v&agrave; hội nhập của Bảo hiểm VietinBank hiện nay.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:07:16', '2018-03-21 18:07:16', 'images/news/1520420057__635750714866591269_HasThumb_Thumb.jpg.png'),
(135, 2, 'VBI thành lập chi nhánh Khánh Hòa', 'vbi-thanh-lap-chi-nhanh-khanh-hoa', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 14/08/2015, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ c&ocirc;ng bố th&agrave;nh lập v&agrave; bổ nhiệm nh&acirc;n sự chủ chốt của Chi nh&aacute;nh Kh&aacute;nh H&ograve;a tại TP Nha Trang, tỉnh Kh&aacute;nh H&ograve;a&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 14/08/2015, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ c&ocirc;ng bố th&agrave;nh lập v&agrave; bổ nhiệm nh&acirc;n sự chủ chốt của Chi nh&aacute;nh Kh&aacute;nh H&ograve;a tại TP Nha Trang, tỉnh Kh&aacute;nh H&ograve;a. Tham dự buổi lễ c&oacute; đại diện Ban L&atilde;nh đạo VBI, Ban L&atilde;nh đạo Vietinbank Chi nh&aacute;nh Kh&aacute;nh H&ograve;a v&agrave; Ph&uacute; Y&ecirc;n c&ugrave;ng đại diện c&aacute;c h&atilde;ng gara, c&aacute;c c&ocirc;ng ty bảo hiểm, c&aacute;c kh&aacute;ch h&agrave;ng lớn tr&ecirc;n địa b&agrave;n.</span></p>\r\n<p><img src=\"../../../uploads/IMG_0812.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Đại diện ban l&atilde;nh đạo VBI trao quyết định bổ nhiệm nh&acirc;n sự chi nh&aacute;nh Kh&aacute;nh H&ograve;a</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Tại buổi lễ, Tổng gi&aacute;m đốc VBI, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm Gi&aacute;m đốc Chi nh&aacute;nh Kh&aacute;nh H&ograve;a cho &ocirc;ng Đo&agrave;n Trung Dũng v&agrave; bổ nhiệm 3 vị tr&iacute; l&atilde;nh đạo cấp ph&ograve;ng cho b&agrave; L&ecirc; Thị Hồng Thọ- Trưởng ph&ograve;ng Kế to&aacute;n- H&agrave;nh ch&iacute;nh, &ocirc;ng Nguyễn Quốc &Acirc;n, &ocirc;ng Cao Quang H&acirc;n, b&agrave; Nguyễn Thị Th&uacute;y Uy&ecirc;n- Trưởng ph&ograve;ng Kinh doanh. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ mong muốn &ocirc;ng Đo&agrave;n Trung Dũng c&ugrave;ng to&agrave;n thể c&aacute;n bộ được bổ nhiệm sẽ điều h&agrave;nh VBI Kh&aacute;nh H&ograve;a ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh.</span></p>\r\n<p><img src=\"../../../uploads/IMG_0830.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">&Ocirc;ng L&ecirc; Tuấn Dũng- Tổng gi&aacute;m đốc VBI chia sẻ tại buổi lễ</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng Đo&agrave;n Trung Dũng c&ugrave;ng c&aacute;c c&aacute;n bộ đ&atilde; thể hiện quyết t&acirc;m phấn đấu để VBI Kh&aacute;nh H&ograve;a ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đồng thời cam kết sẽ tiếp tục cống hiến, đưa VBI Kh&aacute;nh H&ograve;a ph&aacute;t triển mạnh mẽ, khai th&aacute;c tối đa hiệu quả lợi thế về địa l&yacute; trong hoạt động kinh doanh thời gian tới.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:08:07', '2018-03-21 18:08:07', 'images/news/1520420173_IMG_081_635754043900113741_HasThumb_Thumb.JPG.png'),
(136, 2, 'VBI hân hạnh tài trợ và đồng hành cùng Hội thao VietinBank lần thứ IX', 'vbi-han-hanh-tai-tro-va-dong-hanh-cung-hoi-thao-vietinbank-lan-thu-ix', '<p><span style=\"font-family: Arial;\">Hội thao to&agrave;n hệ thống ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam lần thứ IX năm 2015 đ&atilde; diễn ra trong năm ng&agrave;y 11-15/08 tại Tp. Bu&ocirc;n Ma Thuột - tỉnh Đăk Lăk v&agrave; kết th&uacute;c th&agrave;nh c&ocirc;ng tốt đẹp. Hơn 400 VĐV đến từ c&aacute;c chi nh&aacute;nh VietinBank tr&ecirc;n địa b&agrave;n cả nước đ&atilde; thi đấu hết m&igrave;nh qua c&aacute;c m&ocirc;n...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Hội thao to&agrave;n hệ thống ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam lần thứ IX năm 2015 đ&atilde; diễn ra trong năm ng&agrave;y 11-15/08 tại Tp. Bu&ocirc;n Ma Thuột - tỉnh Đăk Lăk v&agrave; kết th&uacute;c th&agrave;nh c&ocirc;ng tốt đẹp. Hơn 400 VĐV đến từ c&aacute;c chi nh&aacute;nh VietinBank tr&ecirc;n địa b&agrave;n cả nước đ&atilde; thi đấu hết m&igrave;nh qua c&aacute;c m&ocirc;n thi: Golf, B&oacute;ng đ&aacute;, Cầu l&ocirc;ng, Cờ tướng, Tennis, B&oacute;ng b&agrave;n&hellip; VBI h&acirc;n hạnh t&agrave;i trợ v&agrave; đồng h&agrave;nh c&ugrave;ng c&aacute;c VĐV trong suốt qu&aacute; tr&igrave;nh thi đấu.</span></p>\r\n<p><img src=\"../../../uploads/20150814_081426.jpg\" alt=\"\" width=\"1366\" height=\"574\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">VBI đồng h&agrave;nh c&ugrave;ng Hội thao với BH sức khỏe VBIcare</span></em></p>\r\n<p>Điểm đặc biệt của Hội thao VietinBank l&agrave; c&aacute;c VĐV đều l&agrave; những người kh&ocirc;ng chuy&ecirc;n, những c&aacute;n bộ đang l&agrave;m việc tại VietinBank với tinh thần thi đấu, cống hiến hết m&igrave;nh để gi&agrave;nh huy chương cho c&aacute; nh&acirc;n, đồng đội, cho chi nh&aacute;nh. Đồng h&agrave;nh c&ugrave;ng c&aacute;c VĐV, với sản phẩm BH sức khỏe VBIcare, Bảo hiểm Vietinbank đ&atilde; g&oacute;p phần bảo vệ cho cho t&iacute;nh mạng, sức khỏe cho hơn 400 VĐV tham gia hội thao gi&uacute;p c&aacute;c VĐV của ch&uacute;ng ta ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m trong suốt qu&aacute; tr&igrave;nh thi đấu v&agrave; gi&agrave;nh nhiều th&agrave;nh t&iacute;ch đ&aacute;ng mừng.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, VBI cũng tham gia hỗ trợ cho Hội thao ở kh&acirc;u tổ chức trong suốt qu&aacute; tr&igrave;nh diễn ra diễn ra sự kiện từ khi khai mạc cho đến khi kết th&uacute;c th&ocirc;ng qua việc t&agrave;i trợ đồ uống v&agrave; c&ocirc;ng t&aacute;c hậu cần phục vụ c&aacute;c VĐV tham gia thi đấu.</p>\r\n<p><img src=\"../../../uploads/DSC_0087.jpg\" alt=\"\" width=\"1366\" height=\"514\" /></p>\r\n<p style=\"text-align: center;\"><em>C&aacute;c VĐV tham gia thi đấu b&oacute;ng b&agrave;n</em></p>\r\n<p>L&agrave; th&agrave;nh vi&ecirc;n t&iacute;ch cực trong hệ thống NHCT, VBI cũng tham gia thi đấu tại Hội thao (với 2 m&ocirc;n thi l&agrave; quần vợt, cầu l&ocirc;ng) v&agrave; gi&agrave;nh th&agrave;nh t&iacute;ch huy chương đồng đội nam chung cuộc.</p>\r\n<p>Th&ocirc;ng qua Hội thao, VBI c&oacute; cơ hội được giao lưu c&ugrave;ng c&aacute;c chi nh&aacute;nh VietinBank trong hệ thống, được cống hiến sức trẻ cũng như sự năng động của m&igrave;nh cho c&aacute;c hoạt động đo&agrave;n thể, đặc biệt l&agrave; được mang đến những dịch vụ bảo hiểm tiện &iacute;ch cho c&aacute;c c&aacute;n bộ VietinBank trong ch&iacute;nh những hoạt động văn h&oacute;a, thể thao.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:08:50', '2018-03-21 18:08:50', 'images/news/1520420298_20150814__635754984220913265_HasThumb_Thumb.jpg.png'),
(137, 2, '“Trung thu yêu thương” của đại gia đình VBI', 'trung-thu-yeu-thuong-cua-dai-gia-dinh-vbi', '<p><span style=\"font-family: Arial;\">Chỉ c&ograve;n &iacute;t ng&agrave;y nữa l&agrave; rằm Trung Thu lại về với c&aacute;c em thiếu nhi, kh&ocirc;ng kh&iacute; đ&oacute;n tết đang ngập tr&agrave;n tr&ecirc;n cả nước. Trung thu l&agrave; dịp trẻ em khắp nơi được ph&aacute; cỗ đ&oacute;n trăng, c&ugrave;ng vui chơi với bạn b&egrave; b&ecirc;n cạnh những chiếc đ&egrave;n lồng xinh xắn đủ sắc m&agrave;u.&nbsp;</span></p>', '<p>Chỉ c&ograve;n &iacute;t ng&agrave;y nữa l&agrave; rằm Trung Thu lại về với c&aacute;c em thiếu nhi, kh&ocirc;ng kh&iacute; đ&oacute;n tết đang ngập tr&agrave;n tr&ecirc;n cả nước. Trung thu l&agrave; dịp trẻ em khắp nơi được ph&aacute; cỗ đ&oacute;n trăng, c&ugrave;ng vui chơi với bạn b&egrave; b&ecirc;n cạnh những chiếc đ&egrave;n lồng xinh xắn đủ sắc m&agrave;u. Nhằm mang đến cho c&aacute;c b&eacute; l&agrave; con em C&aacute;n bộ nh&acirc;n vi&ecirc;n (CBNV) của VBI một ng&agrave;y tết trung thu tr&agrave;n ngập tiếng cười , VBI trụ sở ch&iacute;nh đ&atilde; phối hợp c&ugrave;ng với VBI Thăng Long v&agrave; VBI H&agrave; Nội tổ chức chương tr&igrave;nh &ldquo;Trung thu y&ecirc;u thương&rdquo; v&agrave;o tối ng&agrave;y 24/09/2015 tại nh&agrave; h&agrave;ng Th&uacute;y Nga Plaza.</p>\r\n<p>Gần 80 ch&aacute;u nhỏ đủ mọi lứa tuổi, h&aacute;o hức c&ugrave;ng bố mẹ tới tham dự l&agrave;m đ&ecirc;m hội trung thu của VBI th&ecirc;m s&ocirc;i động. Khung cảnh đ&ecirc;m tổ chức được trang ho&agrave;ng lộng lẫy với rất nhiều đ&egrave;n m&agrave;u, b&oacute;ng bay ngũ sắc c&ugrave;ng những tiếng cười, tiếng trống rộn r&atilde; trong sự h&aacute;o hức, phấn kh&iacute;ch của c&aacute;c em nhỏ. Những phần qu&agrave;, m&acirc;m cỗ, phần trang tr&iacute; s&acirc;n khấu, đ&egrave;n &ocirc;ng sao &ldquo;khổng lồ&rdquo; v&agrave; nhiều c&aacute;c tiết mục kh&aacute;c đều do CBNV của VBI tranh thủ thời gian sau giờ l&agrave;m việc, tận tay l&agrave;m với hy vọng mang lại niềm vui cho c&aacute;c ch&aacute;u.</p>\r\n<p><img src=\"../../../uploads/photo5.jpg\" alt=\"\" width=\"1366\" height=\"810\" /></p>\r\n<p style=\"text-align: center;\"><em>Chị Thỏ Ngọc, Gấu b&eacute;o c&ugrave;ng c&aacute;c b&eacute; nh&agrave; VBI</em></p>\r\n<p>Chương tr&igrave;nh diễn ra trong kh&ocirc;ng kh&iacute; vui tươi, rộn r&agrave;ng với c&aacute;c tiết mục văn nghệ &ldquo;c&acirc;y nh&agrave; l&aacute; vườn&rdquo; đặc sắc, vui nhộn do ch&iacute;nh những CBNV của VBI biểu diễn. Mở đầu l&agrave; tiết mục văn nghệ &ldquo; Li&ecirc;n kh&uacute;c thiếu nhi&rdquo; v&ocirc; c&ugrave;ng nh&iacute; nhảnh do c&aacute;c bạn Ph&ograve;ng Ph&aacute;t triển kinh doanh - VBI trụ sở ch&iacute;nh biểu diễn. S&ocirc;i động nhất vẫn l&agrave; m&agrave;n biểu diễn m&uacute;a l&acirc;n đi&ecirc;u luyện của c&aacute;c bạn đến từ VBI Thăng Long. Kh&ocirc;ng kh&iacute; rộn r&agrave;ng của tiếng trống, chi&ecirc;ng, những điệu m&uacute;a của những ch&uacute; l&acirc;n, ch&uacute; Tễu v&agrave; tr&agrave;ng ph&aacute;o tay gi&ograve;n gi&atilde; của c&aacute;c bạn nhỏ đ&atilde; khuấy động cả hội trường.</p>\r\n<p><img src=\"../../../uploads/photo2.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>Tiết mục văn nghệ \"Li&ecirc;n kh&uacute;c thiếu nhi\"</em></p>\r\n<p>Hai MC Gấu, Thỏ c&ugrave;ng Chị Hằng Nga v&agrave; ch&uacute; Cuội đ&atilde; mang đến rất nhiều tr&ograve; chơi cho c&aacute;c bạn nhỏ. C&aacute;c b&eacute; được trổ t&agrave;i kh&eacute;o tay qua cuộc thi sắp m&acirc;m ngũ quả, thử tr&iacute; th&ocirc;ng minh qua tr&ograve; chơi đuổi h&igrave;nh bắt chữ c&ugrave;ng c&acirc;u hỏi th&ocirc;ng minh để gi&agrave;nh nhiều gấu b&ocirc;ng, đồ chơi th&uacute; vị. Đặc biệt, cả hội trường kh&ocirc;ng thể đứng im trước m&agrave;n biểu diễn t&agrave;i năng đ&aacute;nh đ&agrave;n, h&aacute;t, nhảy cực kỳ đi&ecirc;u luyện của c&aacute;c b&eacute;, phi&ecirc;n bản VBI Got talent ra đời lu&ocirc;n g&acirc;y sự ch&uacute; &yacute; của tất cả mọi người.</p>\r\n<p><img src=\"../../../uploads/photo1.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>M&acirc;m ngũ quả VBI Thăng Long</em></p>\r\n<p><img src=\"../../../uploads/photo4.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>C&aacute;c b&eacute; tham gia tr&ograve; chơi Đuổi h&igrave;nh bắt chữ</em></p>\r\n<p>Hi vọng rằng đại gia đ&igrave;nh VBI đ&atilde; đem đến cho c&aacute;c b&eacute; một đ&ecirc;m Trung thu vui tươi, tr&agrave;n ngập y&ecirc;u thương v&agrave; ghi dấu một kỉ niệm kh&ocirc;ng thể n&agrave;o qu&ecirc;n nơi tuổi thơ của c&aacute;c em.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:10:51', '2018-03-21 18:10:51', 'images/news/1520475868_photo5.JPG'),
(138, 2, 'VBI tổ chức khóa đào tạo OceanBank', 'vbi-to-chuc-khoa-dao-tao-oceanbank', '<p><span style=\"font-family: Arial;\">Sau lễ k&iacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện ng&agrave;y 12/08/2015, VBI phối hợp với OceanBank chuẩn bị mọi c&ocirc;ng việc cho hoạt động hợp t&aacute;c kinh doanh sắp tới trong đ&oacute; hai b&ecirc;n ch&uacute; trọng l&agrave; việc đ&agrave;o t&agrave;o kĩ năng, nghiệp vụ bảo hiểm cho c&aacute;c c&aacute;n bộ ng&acirc;n h&agrave;ng. Từ giữa th&aacute;ng 9 đến hết th&aacute;ng 10...&nbsp;</span></p>', '<p>Sau lễ k&iacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện ng&agrave;y 12/08/2015, VBI phối hợp với OceanBank chuẩn bị mọi c&ocirc;ng việc cho hoạt động hợp t&aacute;c kinh doanh sắp tới trong đ&oacute; hai b&ecirc;n ch&uacute; trọng l&agrave; việc đ&agrave;o t&agrave;o kĩ năng, nghiệp vụ bảo hiểm cho c&aacute;c c&aacute;n bộ ng&acirc;n h&agrave;ng. Từ giữa th&aacute;ng 9 đến hết th&aacute;ng 10, VBI dự kiến tổ chức c&aacute;c lớp &ldquo; Đ&agrave;o tạo chuy&ecirc;n s&acirc;u về nghiệp vụ bảo hiểm&rdquo; tại c&aacute;c chi nh&aacute;nh OceanBank tr&ecirc;n cả nước.</p>\r\n<p>Buổi đ&agrave;o tạo đầu ti&ecirc;n tại H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh ng&agrave;y 19/09/2015 đ&atilde; diễn ra th&agrave;nh c&ocirc;ng tốt đẹp. Tham dự lớp đ&agrave;o tạo, đại diện của b&ecirc;n OceanBank c&oacute; &ocirc;ng Trần Thế Vĩnh &ndash; Ph&oacute; Gi&aacute;m đốc Khối Marketing, b&agrave; L&ecirc; Thị Cẩm T&uacute; &ndash; Ph&oacute; gi&aacute;m đốc trung t&acirc;m đ&agrave;o tạo nh&acirc;n sự c&ugrave;ng hơn 50 học vi&ecirc;n l&agrave; gi&aacute;m đốc, ph&oacute; gi&aacute;m đốc, trưởng ph&ograve;ng c&aacute;c chi nh&aacute;nh của OceanBank. Về ph&iacute;a c&ocirc;ng ty bảo hiểm VietinBank, giảng vi&ecirc;n đ&agrave;o tạo c&oacute; b&agrave; L&ecirc; Thị Quỳnh Hoa &ndash; trưởng ph&ograve;ng Ph&aacute;t triển Kinh doanh v&agrave; &ocirc;ng Đỗ Anh Vũ &ndash; Trưởng ph&ograve;ng Quản l&yacute; nghiệp vụ v&agrave; Đ&aacute;nh gi&aacute; rủi ro.</p>\r\n<p><img src=\"../../../uploads/image.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p>Chương tr&igrave;nh đ&agrave;o tạo chuy&ecirc;n s&acirc;u gồm c&oacute; hai phần ch&iacute;nh: Nghiệp vụ bảo hiểm v&agrave; kĩ năng kinh doanh bảo hiểm. Th&ocirc;ng qua buổi đ&agrave;o tạo n&agrave;y, c&aacute;c c&aacute;n bộ b&ecirc;n OceanBank được giảng vi&ecirc;n ph&acirc;n t&iacute;ch cụ thể chi tiết về quyền lợi, ph&iacute; của c&aacute;c sản phẩm VBI thiết kế như Bảo hiểm sức khỏe VBIcare, c&aacute;c sản phẩm t&iacute;ch hợp g&oacute;i vay t&iacute;n dụng mang t&ecirc;n Oceancar, OceanHome. Kh&ocirc;ng chỉ vậy, c&aacute;n bộ Oceanbank c&ograve;n được tham gia giao lưu c&aacute;c t&igrave;nh huống b&aacute;n h&agrave;ng thực tế, thực h&agrave;nh cấp đơn bảo hiểm trực tiếp qua phần mềm v&agrave; tổng kết c&aacute;c kĩ năng kinh doanh bảo hiểm.<br /> <br />Buổi đ&agrave;o tạo đ&atilde; nhận được sự tham gia s&ocirc;i nổi v&agrave; h&agrave;o hứng của c&aacute;c c&aacute;n bộ OceanBank. Một số c&aacute;n bộ cũng chia sẻ: &ldquo;Đ&acirc;y l&agrave; những sản phẩm bảo hiểm đơn giản, thiết thực v&agrave; dễ tiếp cận với c&aacute;c kh&aacute;ch h&agrave;ng tới giao dịch tại Ng&acirc;n h&agrave;ng. Với những kỹ năng được học ng&agrave;y h&ocirc;m nay, ch&uacute;ng t&ocirc;i sẽ đẩy mạnh việc ch&agrave;o b&aacute;n sản phẩm n&agrave;y cho c&aacute;c kh&aacute;ch h&agrave;ng trong thời gian tới. Ch&uacute;ng t&ocirc;i rất mong sẽ c&oacute; nhiều buổi đ&agrave;o tạo như thế n&agrave;y nữa&rdquo;. Đứng ở vị tr&iacute; của đơn vị tổ chức, VBI thật sự rất vui mừng khi nhận được sự ủng hộ từ ph&iacute;a OceanBank đặc biệt l&agrave; kết quả 100% học vi&ecirc;n đạt y&ecirc;u cầu khi kiểm tra b&agrave;i test cuối kh&oacute;a đ&atilde; phần n&agrave;o thể hiện được t&iacute;nh hiệu quả của buổi đ&agrave;o tạo.<br /> <br />Dự kiến, c&aacute;c lớp đ&agrave;o tạo đối với Oceanbank sẽ được triển khai li&ecirc;n tục trong th&aacute;ng 10. Chắc chắn Oceanbank sẽ l&agrave; một trong những ng&acirc;n h&agrave;ng ph&aacute;t triển mạnh mẽ m&ocirc; h&igrave;nh Bancassurance c&ugrave;ng VBI thời gian tới.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:11:31', '2018-03-21 18:11:31', 'images/news/1520475967_image.jpg'),
(139, 2, 'VBI chi trả 180 triệu đồng tiền bồi thường bảo hiểm Con người VietinCare tại Đắk Song', 'vbi-chi-tra-180-trieu-dong-tien-boi-thuong-bao-hiem-con-nguoi-vietincare-tai-dak-song', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 28/9, Chi nh&aacute;nh C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương (VBI) T&acirc;y Nguy&ecirc;n phối hợp với Chi nh&aacute;nh Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương (VTB) T&acirc;y Nguy&ecirc;n đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; tiến h&agrave;nh chi trả 180 triệu đồng tiền bồi thường bảo hiểm con người VietinCare cho gia đ&igrave;nh ...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 28/9, Chi nh&aacute;nh C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương (VBI) T&acirc;y Nguy&ecirc;n phối hợp với Chi nh&aacute;nh Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Đăk N&ocirc;ng đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; tiến h&agrave;nh chi trả 180 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh &ocirc;ng Phạm Đức Sơn, ở th&ocirc;n 11, x&atilde; Nam B&igrave;nh, huyện Đăk Song, tỉnh Đăk N&ocirc;ng.</span></p>\r\n<p><img src=\"../../../uploads/2087-TT-5.jpg\" alt=\"\" width=\"530\" height=\"359\" /></p>\r\n<p><span style=\"font-family: Arial;\">Được biết đầu năm 2014, &ocirc;ng Phạm Đức Sơn được Chi nh&aacute;nh VBI T&acirc;y Nguy&ecirc;n giải quyết cho vay 180 triệu đồng v&agrave; c&oacute; tham gia bảo hiểm con người VietinCare của VBI. Đến giữa năm 2015, &ocirc;ng Sơn kh&ocirc;ng may gặp rủi ro qua đời.<br />Ngay khi nhận được tin b&aacute;o từ gia đ&igrave;nh, c&aacute;n bộ Chi nh&aacute;nh VBI T&acirc;y Nguy&ecirc;n c&ugrave;ng với Chi nh&aacute;nh VTB tỉnh đ&atilde; nhanh ch&oacute;ng tới thăm viếng v&agrave; hướng dẫn gia đ&igrave;nh thu thập hồ sơ để l&agrave;m thủ tục nhận bảo hiểm. Với mong muốn san sẻ rủi ro v&agrave; g&aacute;nh nặng t&agrave;i ch&iacute;nh với gia đ&igrave;nh, c&ocirc;ng ty VBI đ&atilde; cố gắng xử l&yacute; nhanh ch&oacute;ng, kịp thời việc chi trả quyền lợi bảo hiểm cho gia đ&igrave;nh &ocirc;ng Sơn.<br />Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm được cung cấp bởi c&ocirc;ng ty VBI v&agrave; ph&acirc;n phối tại tất cả c&aacute;c chi nh&aacute;nh của VietinBank tr&ecirc;n to&agrave;n quốc. Người vay vốn tham gia Bảo hiểm con người VietinCare sẽ nhận được quyền lợi bảo vệ ưu việt với mức ph&iacute; thấp.<br />Từ khi triển khai sản phẩm Bảo hiểm con người Vietincare đến nay đ&atilde; c&oacute; nhiều trường hợp người vay vốn kh&ocirc;ng may qua đời hoặc bị thương tật to&agrave;n bộ vĩnh viễn được c&ocirc;ng ty VBI chi trả quyền lợi bảo hiểm với những số tiền bồi thường l&ecirc;n tới h&agrave;ng tỉ đồng.<br />Với sản phẩm VietinCare v&agrave; c&ocirc;ng t&aacute;c bồi thường nhanh ch&oacute;ng, Bảo hiểm VietinBank một lần nữa khẳng định được sản phẩm v&agrave; dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:12:10', '2018-03-21 18:12:10', 'images/news/1520476034_2087-TT-5.jpg'),
(140, 2, 'VBI chi trả 40 triệu đồng bảo hiểm con người VietinCare cho khách hàng tại Lâm Đồng', 'vbi-chi-tra-40-trieu-dong-bao-hiem-con-nguoi-vietincare-cho-khach-hang-tai-lam-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 27/10/2015, Bảo hiểm VBI - chi nh&aacute;nh T&acirc;y Nguy&ecirc;n c&ugrave;ng VietinBank &ndash; chi nh&aacute;nh L&acirc;m Đồng đ&atilde; tổ chức bồi thường v&agrave; trao số tiền 40 triệu đồng cho gia đ&igrave;nh anh Phạm Nguy&ecirc;n Khang, tr&uacute; tại thị trấn Dran, huyện Đơn Dương, tỉnh L&acirc;m Đồng.</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 27/10/2015, Bảo hiểm VBI - chi nh&aacute;nh T&acirc;y Nguy&ecirc;n c&ugrave;ng VietinBank &ndash; chi nh&aacute;nh L&acirc;m Đồng đ&atilde; tổ chức bồi thường v&agrave; trao số tiền 40 triệu đồng cho gia đ&igrave;nh anh Phạm Nguy&ecirc;n Khang, tr&uacute; tại thị trấn Dran, huyện Đơn Dương, tỉnh L&acirc;m Đồng.</span></p>\r\n<p><img src=\"../../../uploads/20151027_110146.jpg\" alt=\"\" width=\"1366\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">h&igrave;nh ảnh: Đại diện Bảo hiểm VBI v&agrave; VietinBank trao bồi thường cho &ocirc;ng Phi&ecirc;n, cha của kh&aacute;ch h&agrave;ng Phạm Nguy&ecirc;n Khang</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Anh Khang vay vốn tại ng&acirc;n h&agrave;ng VietinBank v&agrave; tham gia bảo hiểm con người VietinCare từ năm 2014. Năm 2015, trong qu&aacute; tr&igrave;nh tham gia giao th&ocirc;ng tại địa phương, anh Khang kh&ocirc;ng may gặp tai nạn dẫn tới tử vong. Ngay khi nhận được tin b&aacute;o từ gia đ&igrave;nh, Bảo hiểm VBI &ndash; chi nh&aacute;nh T&acirc;y Nguy&ecirc;n nhanh ch&oacute;ng ho&agrave;n tất hồ sơ bồi thường v&agrave; c&ugrave;ng VietinBank &ndash; chi nh&aacute;nh L&acirc;m Đồng phối hợp tổ chức trao số tiền 40 triệu đồng cho &ocirc;ng Phạm Như Phi&ecirc;n, cha của anh Khang.</span></p>\r\n<p><img src=\"../../../uploads/20151027_110116.jpg\" alt=\"\" width=\"1366\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">h&igrave;nh ảnh: &ocirc;ng Phi&ecirc;n, cha anh Khang, người được thụ hưởng bảo hiểm ph&aacute;t biểu</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong buổi chi trả bồi thường, &ocirc;ng Phi&ecirc;n chia sẻ: &ldquo;Khang, con trai t&ocirc;i vay vốn của VietinBank để ph&aacute;t triển sản xuất tại gia đ&igrave;nh được một năm th&igrave; kh&ocirc;ng may ch&aacute;u mất. Gia đ&igrave;nh vừa b&agrave;ng ho&agrave;ng v&igrave; sự ra đi của Khang, phần cũng lo lắng c&ocirc;ng việc trước mắt. Trong l&uacute;c gia đ&igrave;nh đang rất kh&oacute; khăn, c&aacute;c anh chị VietinBank cũng như Bảo hiểm VBI đ&atilde; nhiệt t&igrave;nh hỗ trợ thủ tục v&agrave; nhanh ch&oacute;ng chi trả số tiền c&ograve;n nợ ng&acirc;n h&agrave;ng gi&uacute;p ch&uacute;ng t&ocirc;i cũng bớt đi phần n&agrave;o g&aacute;nh nặng trước mắt.&rdquo;<br /> <br />Qua trao đổi, đại diện VietinBank &ndash; chi nh&aacute;nh L&acirc;m Đồng cho biết: &ldquo;Sản phẩm bảo hiểm con người VietinCare của VBI kh&ocirc;ng chỉ hữu dụng v&agrave; an to&agrave;n cho người vay vốn m&agrave; c&ograve;n đảm bảo việc trả nợ cho ng&acirc;n h&agrave;ng khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro trong cuộc sống gi&uacute;p cho Ng&acirc;n h&agrave;ng kh&ocirc;ng phải lo lắng vấn đề xử l&yacute; nợ xấu. Hiểu được &yacute; nghĩa VietinCare mang lại, c&aacute;c c&aacute;n bộ của ch&uacute;ng t&ocirc;i cũng thường xuy&ecirc;n tư vấn cho kh&aacute;ch h&agrave;ng tham gia sản phẩm n&agrave;y nhằm đảm bảo sự an to&agrave;n v&agrave; ổn định t&agrave;i ch&iacute;nh cho kh&aacute;ch h&agrave;ng.&rdquo;<br /> <br />Một lần nữa, sản phẩm bảo hiểm con người VietinCare khẳng định được sự quan trọng, tiện &iacute;ch vượt trội v&agrave; kh&ocirc;ng thể thiếu của m&igrave;nh đối với người vay vốn tại ng&acirc;n h&agrave;ng VietinBank.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:13:33', '2018-03-21 18:13:33', 'images/news/1520476160_20151027__635817905606278533_HasThumb_Thumb.jpg.png'),
(141, 2, 'VBI chi trả 100 triệu đồng bảo hiểm sức khỏe VBI CARE cho khách hàng tại Vĩnh Phúc', 'vbi-chi-tra-100-trieu-dong-bao-hiem-suc-khoe-vbi-care-cho-khach-hang-tai-vinh-phuc', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 03/11/2015, VBI H&agrave; Nội c&ugrave;ng chi nh&aacute;nh VietinBank - B&igrave;nh Xuy&ecirc;n đ&atilde; đến thăm hỏi, động vi&ecirc;n v&agrave; trao tận tay số tiền 100 triệu đồng cho gia đ&igrave;nh anh Trần H&ugrave;ng Mạnh, tr&uacute; tại x&atilde; T&acirc;n Lập, huyện S&ocirc;ng L&ocirc;, tỉnh Vĩnh Ph&uacute;c.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 03/11/2015, VBI H&agrave; Nội c&ugrave;ng chi nh&aacute;nh VietinBank - B&igrave;nh Xuy&ecirc;n đ&atilde; đến thăm hỏi, động vi&ecirc;n v&agrave; trao tận tay số tiền 100 triệu đồng cho gia đ&igrave;nh anh Trần H&ugrave;ng Mạnh, tr&uacute; tại x&atilde; T&acirc;n Lập, huyện S&ocirc;ng L&ocirc;, tỉnh Vĩnh Ph&uacute;c.<br />Anh Mạnh l&agrave; nh&acirc;n vi&ecirc;n l&aacute;i xe của chi nh&aacute;nh VietinBank B&igrave;nh Xuy&ecirc;n đồng thời cũng l&agrave; kh&aacute;ch h&agrave;ng tham gia bảo hiểm sức khỏe VBI Care, do VBI cung cấp, theo chương tr&igrave;nh CBNV VietinBank. Anh vốn l&agrave; trụ cột gia đ&igrave;nh của vợ v&agrave; ba con nhỏ, cũng l&agrave; chỗ dựa ch&iacute;nh của cha mẹ đ&atilde; gi&agrave; yếu. Ng&agrave;y 20/09/2015, trong qu&aacute; tr&igrave;nh sinh hoạt thường nhật tại gia đ&igrave;nh, anh Mạnh kh&ocirc;ng may bị điện giật dẫn tới tử vong. Ngay khi biết tin, Bảo hiểm VietinBank nhanh ho&agrave;n tất hồ sơ bồi thường, c&ugrave;ng chi nh&aacute;nh VietinBank &ndash; B&igrave;nh Xuy&ecirc;n tới thăm hỏi động vi&ecirc;n v&agrave; trao số tiền 100 triệu đồng cho gia đ&igrave;nh anh Mạnh.</span></p>\r\n<p><img src=\"../../../uploads/DSC_1143.jpg\" alt=\"\" width=\"1366\" height=\"908\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">H&igrave;nh ảnh: Đại diện VBI v&agrave; VietinBank đến thăm hỏi gia đ&igrave;nh</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Sau khi c&aacute;n bộ VBI v&agrave; VietinBank thắp n&eacute;n nhang v&agrave; chia buồn c&ugrave;ng gia quyến, chị Phạm Thị Thanh, vợ anh Mạnh x&uacute;c động: &ldquo;T&ocirc;i rất cảm ơn c&aacute;c anh chị đồng nghiệp của chồng t&ocirc;i b&ecirc;n VietinBank v&agrave; c&aacute;c c&aacute;n bộ VBI đ&atilde; tới chia sẻ nỗi mất m&aacute;t n&agrave;y với gia đ&igrave;nh. Anh Mạnh qua đời đột ngột khiến gia đ&igrave;nh hết sức ngỡ ng&agrave;ng v&agrave; cũng kh&ocirc;ng biết tr&ocirc;ng cậy v&agrave;o đ&acirc;u nhưng c&ocirc;ng ty Bảo hiểm đ&atilde; nhanh ch&oacute;ng l&agrave;m hồ sơ v&agrave; sớm bồi thường cũng khiến gia đ&igrave;nh đỡ đi phần n&agrave;o nỗi lo về kinh tế trong tương lai.&rdquo;<br />Tiếp lời chị Thanh, đại diện chi nh&aacute;nh VietinBank - B&igrave;nh Xuy&ecirc;n chia sẻ: &ldquo;Với 100 triệu đồng c&oacute; lẽ l&agrave; số tiền kh&ocirc;ng đủ để b&ugrave; đắp sự mất m&aacute;t của gia đ&igrave;nh nhưng VietinBank cũng như VBI hi vọng n&oacute; cũng phần n&agrave;o hỗ trợ cho chị Thanh, đặc biệt l&agrave; c&aacute;c ch&aacute;u nhỏ, vượt qua khoảng thời gian kh&oacute; khăn n&agrave;y\"<br />VBI Care kh&ocirc;ng chỉ l&agrave; sản phẩm bảo hiểm sức khỏe b&aacute;n lẻ bảo vệ cho c&aacute; nh&acirc;n m&agrave; c&ograve;n được thiết kế ưu việt theo c&aacute;c chương tr&igrave;nh d&agrave;nh cho doanh nghiệp như chương tr&igrave;nh CBNV của VietinBank. Kể từ đầu năm 2015 đến nay, VBI đ&atilde; chi trả 9,9 tỷ đồng cho gần 7.500 trường hợp li&ecirc;n quan đến tử vong, thai sản c&ugrave;ng nhiều bệnh nh&acirc;n kh&aacute;c l&agrave; kh&aacute;ch h&agrave;ng tham gia bảo hiểm sức khỏe VBI Care.<br />Qua sự việc n&agrave;y, sản phẩm VBI Care một lần nữa thể hiện vai tr&ograve; lớn đối với sự nghiệp chăm s&oacute;c sức khỏe c&ocirc;ng đồng v&agrave; khẳng định chất lượng dịch vụ vượt trội của bảo hiểm VietinBank.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:41:04', '2018-03-21 18:41:04', 'images/news/1520476245_DSC_1143.jpg'),
(142, 2, 'VBI thành lập Chi nhánh Cần Thơ', 'vbi-thanh-lap-chi-nhanh-can-tho', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 23/11/2015, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ c&ocirc;ng bố th&agrave;nh lập v&agrave; bổ nhiệm nh&acirc;n sự chủ chốt của Chi nh&aacute;nh Cần Thơ tại địa chỉ Tầng 10, số 45A-47 Đường 30/4, Phường An Lạc, Quận Ninh Kiều, Th&agrave;nh phố Cần Thơ.&nbsp;</span></p>', '<p>Ng&agrave;y 23/11/2015, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ c&ocirc;ng bố th&agrave;nh lập v&agrave; bổ nhiệm nh&acirc;n sự chủ chốt của Chi nh&aacute;nh Cần Thơ tại địa chỉ Tầng 10, số 45A-47 Đường 30/4, Phường An Lạc, Quận Ninh Kiều, Th&agrave;nh phố Cần Thơ. Tham dự buổi lễ c&oacute; đại diện Ban L&atilde;nh đạo VBI, Ban L&atilde;nh đạo Vietinbank Chi nh&aacute;nh Cần Thơ c&ugrave;ng c&aacute;c kh&aacute;ch h&agrave;ng lớn tr&ecirc;n địa b&agrave;n.</p>\r\n<p>Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng - Tổng gi&aacute;m đốc VBI đ&atilde; trao quyết định bổ nhiệm Gi&aacute;m đốc Chi nh&aacute;nh Cần Thơ cho &ocirc;ng L&ecirc; C&ocirc;ng Nguyễn Ho&agrave;ng, c&aacute;c vị tr&iacute; Trưởng ph&ograve;ng kinh doanh v&agrave; Ph&oacute; ph&ograve;ng kế to&aacute;n h&agrave;nh ch&iacute;nh.</p>\r\n<p><img src=\"../../../uploads/thanh lap chi nhanh Can Tho_web.jpg\" alt=\"\" width=\"448\" height=\"299\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: TGĐ VBI trao quyết định bổ nhiệm nh&acirc;n sự Chi nh&aacute;nh Cần Thơ)</em></p>\r\n<p>&Ocirc;ng L&ecirc; C&ocirc;ng Nguyễn Ho&agrave;ng - Gi&aacute;m đốc VBI Cần Thơ ph&aacute;t biểu: &ldquo;VBI Cần Thơ l&agrave; chi nh&aacute;nh thứ 13 trong hệ thống chi nh&aacute;nh của VBI. Trong thời gian tới, VBI Cần Thơ sẽ phấn đấu ho&agrave;n th&agrave;nh c&aacute;c mục ti&ecirc;u kinh doanh đề ra, x&acirc;y dựng ph&aacute;t triển VBI Cần Thơ ng&agrave;y c&agrave;ng vững mạnh. VBI Cần Thơ mong muốn nhận được sự hợp t&aacute;c từ c&aacute;c đơn vị trong hệ thống VietinBank cũng như sự ủng hộ kh&aacute;ch h&agrave;ng v&agrave; c&aacute;c đối t&aacute;c tr&ecirc;n địa b&agrave;n.&rdquo;</p>\r\n<p>Việc th&agrave;nh lập Chi nh&aacute;nh Cần Thơ thể hiện cam kết ph&aacute;t triển mạnh mẽ của VBI, mang đến cho c&aacute;c doanh nghiệp, tổ chức, c&aacute; nh&acirc;n tại địa b&agrave;n Cần Thơ những sản phẩm, dịch vụ bảo hiểm chất lượng tốt nhất, g&oacute;p phần v&agrave;o mục ti&ecirc;u phấn đấu của VBI, trở th&agrave;nh một trong những c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ chuy&ecirc;n nghiệp h&agrave;ng đầu tại Việt Nam,</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:41:34', '2018-03-21 18:41:34', 'images/news/1520476344_thanh-lap_635839577121469550_HasThumb_Thumb.jpg.png'),
(143, 2, 'VBI chi trả 150 triệu đồng bảo hiểm Con người VietinCare cho khách hàng tại Đắk Đoa', 'vbi-chi-tra-150-trieu-dong-bao-hiem-con-nguoi-vietincare-cho-khach-hang-tai-dak-doa', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 24/11/2015, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai tới thăm hỏi, động vi&ecirc;n v&agrave; trao 150 triệu đồng tiền bồi thường bảo hiểm con người VietinCare cho gia đ&igrave;nh &ocirc;ng Phạm Trọng Thủy tr&uacute; tại Th&ocirc;n Tam Hiệp, X&atilde; H&rsquo;Neng, huyện Đăk Đoa, tỉnh Gia Lai...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 24/11/2015, Bảo hiểm VietinBank (VBI) Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai tới thăm hỏi, động vi&ecirc;n v&agrave; trao 150 triệu đồng tiền bồi thường bảo hiểm con người VietinCare cho gia đ&igrave;nh &ocirc;ng Phạm Trọng Thủy tr&uacute; tại Th&ocirc;n Tam Hiệp, X&atilde; H&rsquo;Neng, huyện Đăk Đoa, tỉnh Gia Lai.</span></p>\r\n<p><img src=\"../../../uploads/boi thuong dakdoa.png\" alt=\"\" width=\"542\" height=\"535\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p>Đầu năm 2015, &ocirc;ng Phạm Trọng Thủy được VietinBank CN Gia Lai cho vay 150 triệu đồng, đồng thời tham gia bảo hiểm con người VietinCare của VBI. V&agrave;o giữa năm 2015, &ocirc;ng Thủy kh&ocirc;ng may gặp rủi ro tai nạn giao th&ocirc;ng dẫn tới tử vong.<br />Ngay sau khi nhận được th&ocirc;ng tin từ gia đ&igrave;nh, VBI CN T&acirc;y Nguy&ecirc;n đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Gia Lai hướng dẫn gia đ&igrave;nh l&agrave;m hồ sơ nhận tiền bảo hiểm theo quy định. VBI đ&atilde; ho&agrave;n thiện thủ tục v&agrave; thanh to&aacute;n 150 triệu anh Thủy c&ograve;n nợ cho Ng&acirc;n h&agrave;ng VietinBank Gia Lai, gi&uacute;p gia đ&igrave;nh anh loại bỏ g&aacute;nh nặng trả nợ.<br /> <br />Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả gần 2 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng, VBI đ&atilde; v&agrave; đang khẳng định được uy t&iacute;n của tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:42:05', '2018-03-21 18:42:05', 'images/news/1520476436_boi-thuon_635840584286665154_HasThumb_Thumb.PNG'),
(144, 2, 'VBI ký kết hợp đồng hợp tác toàn diện với GPBank', 'vbi-ky-ket-hop-dong-hop-tac-toan-dien-voi-gpbank', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 30/11/2015, tại H&agrave; Nội, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam (VBI) v&agrave; Ng&acirc;n h&agrave;ng TM TNHH MTV Dầu Kh&iacute; To&agrave;n Cầu (GPBank) đ&atilde; ch&iacute;nh thức k&yacute; kết hợp đồng hợp t&aacute;c to&agrave;n diện.&nbsp;</span></p>', '<p>&nbsp;Ng&agrave;y 30/11/2015, tại H&agrave; Nội, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam (VBI) v&agrave; Ng&acirc;n h&agrave;ng TM TNHH MTV Dầu Kh&iacute; To&agrave;n Cầu (GPBank) đ&atilde; ch&iacute;nh thức k&yacute; kết hợp đồng hợp t&aacute;c to&agrave;n diện nhằm tăng cường mối quan hệ hợp t&aacute;c Ng&acirc;n h&agrave;ng &ndash; Bảo hiểm trong c&aacute;c dịch vụ cung ứng đồng thời th&uacute;c đẩy mọi hoạt động t&agrave;i ch&iacute;nh song phương.</p>\r\n<p>Đến tham dự buổi lễ, Đại diện ph&iacute;a VBI c&oacute; b&agrave; Nguyễn Hồng V&acirc;n - Chủ tịch C&ocirc;ng ty, &ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc c&ugrave;ng c&aacute;c l&atilde;nh đạo ph&ograve;ng ban. Về ph&iacute;a GPBank c&oacute; b&agrave; Trần Thị Lệ Nga &ndash; Chủ tịch HĐTV, &Ocirc;ng Phạm Huy Th&ocirc;ng - Ủy vi&ecirc;n HĐTV ki&ecirc;m Tổng gi&aacute;m đốc, c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n trong Ban L&atilde;nh đạo.</p>\r\n<p>Hợp t&aacute;c to&agrave;n diện giữa VBI v&agrave; GPBank sẽ g&oacute;p phần n&acirc;ng cao chất lượng dịch vụ, mở rộng thị trường đồng thời tăng nguồn lợi đầu tư cho c&aacute;c b&ecirc;n. Tr&ecirc;n cơ sở hợp t&aacute;c n&agrave;y, VBI cam kết mang lại cho GPBank c&aacute;c sản phẩm, dịch vụ bảo hiểm ưu việt nhất. Ngo&agrave;i ra, VBI sẽ mở t&agrave;i khoản thanh to&aacute;n tại GPBank v&agrave; thực hiện c&aacute;c giao dịch với kh&aacute;ch h&agrave;ng qua t&agrave;i khoản mở tại GPBank để thuận tiện cho việc thanh to&aacute;n khi triển khai c&aacute;c dịch vụ hợp t&aacute;c với GPBank.</p>\r\n<p>B&ecirc;n cạnh thỏa thuận Hợp t&aacute;c to&agrave;n diện, GPBank v&agrave; VBI c&ograve;n k&yacute; kết Hợp đồng đại l&yacute; bảo hiểm, tr&ecirc;n cơ sở đ&oacute;, GPBank l&agrave; đại l&yacute; ch&iacute;nh thức cung cấp sản phẩm bảo hiểm của VBI tới c&aacute;c kh&aacute;ch h&agrave;ng của m&igrave;nh. Đồng thời, trong buổi lễ, VBI v&agrave; GPBank cũng k&yacute; kết hợp đồng nguy&ecirc;n tắc bảo hiểm xe cơ giới. Trong đ&oacute;, VBI sẽ l&agrave; nh&agrave; bảo hiểm ch&iacute;nh thức cho hệ thống xe &ocirc; t&ocirc; của GPBank.</p>\r\n<p><img src=\"../../../uploads/le ky ket(1).jpg\" alt=\"\" width=\"640\" height=\"313\" /></p>\r\n<p style=\"text-align: center;\"><em>(Đại diện VBI v&agrave; GPBank k&yacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện)</em></p>\r\n<p>Tại buổi lễ, b&agrave; Nguyễn Hồng V&acirc;n - Chủ tịch VBI ph&aacute;t biểu: &ldquo;Sau buổi lễ k&yacute; kết hợp t&aacute;c to&agrave;n diện, VBI sẽ phối hợp với GPBank triển khai hệ thống kinh doanh bảo hiểm qua ng&acirc;n h&agrave;ng theo m&ocirc; h&igrave;nh Bancassurance một c&aacute;ch chuy&ecirc;n nghiệp, nhằm cung cấp c&aacute;c dịch vụ bảo hiểm đa dạng tới kh&aacute;ch h&agrave;ng. C&aacute;c c&aacute;n bộ GPBank sẽ được đ&agrave;o tạo kĩ năng chuy&ecirc;n s&acirc;u về sản phẩm bảo hiểm, về kỹ năng b&aacute;n h&agrave;ng v&agrave; được hỗ trợ phần mềm cấp đơn tự động. Đặc biệt, c&aacute;c c&aacute;n bộ GPBank sẽ được tham gia c&aacute;c chương tr&igrave;nh marketing, thi đua khen thưởng hấp dẫn do VBI tổ chức.&rdquo;</p>\r\n<p><img src=\"../../../uploads/Chu tich VBI phat bieu.jpg\" alt=\"\" width=\"640\" height=\"385\" /></p>\r\n<p style=\"text-align: center;\"><em>(B&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch VBI ph&aacute;t biểu tại buổi lễ)</em></p>\r\n<p>&nbsp;Để ghi nhận sự hợp t&aacute;c của hai b&ecirc;n, B&agrave; Trần Thị Lệ Nga &ndash; Chủ tịch HĐTV GPBank chia sẻ: &ldquo;Sự hợp t&aacute;c giữa GPBank v&agrave; VBI kh&ocirc;ng chỉ mang lại những cơ hội kinh doanh mới, m&agrave; c&ograve;n mang &yacute; nghĩ hỗ trợ, cộng lực c&ugrave;ng nhau ph&aacute;t triển. Nhằm gia tăng tiện &iacute;ch cho kh&aacute;ch h&agrave;ng, GPBank lu&ocirc;n lấy việc ph&aacute;t triển c&aacute;c sản phẩm dịch vụ uy t&iacute;n, chất lượng đ&aacute;p ứng nhu cầu của mọi đối tượng kh&aacute;ch h&agrave;ng l&agrave;m nhiệm vụ cốt l&otilde;i h&agrave;ng đầu. Cam kết thực hiện tốt qu&aacute; tr&igrave;nh t&aacute;i cơ cấu, GPBank đang tiếp tục nỗ lực thực hiện tốt chỉ đạo của NHNN, ph&aacute;t huy kinh nghiệm v&agrave; tiềm lực hiện c&oacute; để ho&agrave;n thiện hơn tr&ecirc;n chặng đường sắp tới&rdquo;.</p>\r\n<p>Thỏa thuận hợp t&aacute;c với GPBank đ&aacute;nh dấu bước ph&aacute;t triển của VBI trong c&aacute;c hoạt động hợp t&aacute;c song phương v&agrave; đa phương, n&acirc;ng cao hiệu quả hoạt động kinh doanh, g&oacute;p phần khẳng định gi&aacute; trị thương hiệu của VBI.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:42:55', '2018-03-21 18:42:55', 'images/news/1520476601_le-ky-ke_635844966725320242_HasThumb_Thumb.jpg.png'),
(145, 2, 'Đoàn thanh niên VBI tổ chức Chương trình về nguồn', 'doan-thanh-nien-vbi-to-chuc-chuong-trinh-ve-nguon', '<p><span style=\"font-family: Arial;\">Vừa qua, Đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI đ&atilde; tổ chức Chương tr&igrave;nh H&agrave;nh về nguồn kết hợp từ thiện tại tỉnh Nghệ An&nbsp;</span></p>', '<p>5h s&aacute;ng ng&agrave;y 04/12/2015, hơn 30 đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI trong m&agrave;u &aacute;o xanh t&igrave;nh nguyện đ&atilde; chuẩn bị sẵn s&agrave;ng cho H&agrave;nh tr&igrave;nh về nguồn. Thời tiết mưa gi&oacute; v&agrave; r&eacute;t buốt tại H&agrave; Nội kh&ocirc;ng l&agrave;m lạnh được tinh thần t&igrave;nh nguyện đang ch&aacute;y rực trong mỗi th&agrave;nh vi&ecirc;n trong đo&agrave;n.</p>\r\n<p>Xe ch&uacute;ng t&ocirc;i đi qua đường m&ograve;n Hồ Ch&iacute; Minh lịch sử, vượt qua 400 km từ H&agrave; Nội tới Ninh B&igrave;nh, qua Thanh H&oacute;a để đến với Nghệ An. Tr&ecirc;n đường đi, ch&uacute;ng t&ocirc;i ấn tượng mạnh bởi h&igrave;nh ảnh những những ruộng l&uacute;a, những c&aacute;nh đồng ng&ocirc;, nhưng ng&ocirc;i l&agrave;ng nhỏ nằm giữa những ngọn n&uacute;i ẩn m&igrave;nh trong sương mờ. Vẻ đẹp của Nghệ An khiến ch&uacute;ng t&ocirc;i kh&ocirc;ng khỏi ngỡ ng&agrave;ng.</p>\r\n<p>S&aacute;ng ng&agrave;y 05/12, đo&agrave;n ch&uacute;ng t&ocirc;i di chuyển đến Nghĩa trang Việt L&agrave;o. Với tấm l&ograve;ng th&agrave;nh k&iacute;nh, biết ơn s&acirc;u sắc, ch&uacute;ng t&ocirc;i thắp hương tưởng nhớ hơn mười ngh&igrave;n liệt sỹ đ&atilde; anh dũng hy sinh cho sự nghiệp giải ph&oacute;ng, x&acirc;y dựng bảo vệ Tổ quốc.</p>\r\n<p><img src=\"../../../uploads/IMG_4716.jpg\" alt=\"\" width=\"640\" height=\"427\" /></p>\r\n<p style=\"text-align: center;\"><em>(Đo&agrave;n th&agrave;nh ni&ecirc;n VBI d&acirc;ng hương tại nghĩa trang Việt L&agrave;o)</em></p>\r\n<p>D&ugrave; trời gi&aacute; lạnh, mưa nặng hạt, nhưng ai nấy trong đo&agrave;n vẫn kh&ocirc;ng quản ngại kh&oacute; khăn, lội b&ugrave;n, leo n&uacute;i tới trường tiểu học ở x&atilde; Tường Sơn v&agrave; Bản Ồ Ồ, Gia H&oacute;p thuộc Huyện Anh Sơn để mang những t&uacute;i &aacute;o ấm, đồ d&ugrave;ng học tập cho c&aacute;c em học sinh. Thật may mắn khi ch&uacute;ng t&ocirc;i đến trường th&igrave; mưa ngớt v&agrave; c&aacute;c em nhỏ c&oacute; thể ra ngo&agrave;i vui chơi v&agrave; giao lưu c&ugrave;ng cả đo&agrave;n. C&aacute;c em học sinh rất th&iacute;ch th&uacute; khi được mặc những chiếc &aacute;o ấm, được cầm tr&ecirc;n tay b&uacute;t vở, đ&egrave;n học v&agrave; những đ&ocirc;i d&eacute;p xinh xắn. &Aacute;nh mắt ng&acirc;y thơ, tiếng cười n&oacute;i, nụ cười của c&aacute;c em, lời cảm ơn của thầy c&ocirc; v&agrave; ba mẹ c&aacute;c em đ&atilde; mang đến niềm hạnh ph&uacute;c cho ch&uacute;ng t&ocirc;i. Với c&aacute;c th&agrave;nh vi&ecirc;n trong Đo&agrave;n thanh ni&ecirc;n VBI, chưa bao giờ t&ocirc;i thấy m&ugrave;a đ&ocirc;ng ấm &aacute;p đến vậy!</p>\r\n<p><img src=\"../../../uploads/IMG_4928.jpg\" alt=\"\" width=\"632\" height=\"480\" /></p>\r\n<p style=\"text-align: center;\"><em>(Đại diện Đo&agrave;n thanh ni&ecirc;n VBI trao qu&agrave; từ thiện cho c&aacute;c em nhỏ tại Huyện Anh Sơn)</em></p>\r\n<p style=\"text-align: left;\"><img src=\"../../../uploads/IMG_4916.jpg\" alt=\"\" width=\"606\" height=\"480\" /></p>\r\n<p style=\"text-align: left;\"><img src=\"../../../uploads/received_918435664942141.jpg\" alt=\"\" width=\"505\" height=\"480\" /></p>\r\n<p style=\"text-align: left;\">Chuyến đi từ thiện về nguồn đọng lại trong ch&uacute;ng t&ocirc;i biết bao kỉ niệm. Đối với c&aacute;c đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI, tr&aacute;ch nhiệm v&agrave; nghĩa vụ với cộng đồng lu&ocirc;n được ưu ti&ecirc;n h&agrave;ng đầu. H&igrave;nh ảnh nụ cười của c&aacute;c em nhỏ v&agrave; c&aacute;nh đồng hoa hướng dương vươn m&igrave;nh mạnh mẽ trong mưa gi&oacute;, vẫn c&ograve;n nguy&ecirc;n trong t&acirc;m tr&iacute; ch&uacute;ng t&ocirc;i. Những chuyến đi từ thiện về nguồn gi&uacute;p ch&uacute;ng t&ocirc;i c&oacute; th&ecirc;m động lực để sống, l&agrave;m việc v&agrave; cống hiến hết m&igrave;nh.</p>\r\n<p style=\"text-align: left;\"><img src=\"../../../uploads/IMG_5132(1).jpg\" alt=\"\" width=\"598\" height=\"397\" /></p>\r\n<p style=\"text-align: center;\"><em>(C&aacute;nh đồng hoa hướng dương vươn m&igrave;nh mạnh mẽ trong mưa gi&oacute; tại Nghệ An)</em></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:44:17', '2018-03-21 18:44:17', 'images/news/1520477601_IMG_492_635851026465494004_HasThumb_Thumb.JPG.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(146, 2, 'VBI chi trả 130 triệu đồng tiền bồi thường bảo hiểm Con người VietinCare tại Gia Lai', 'vbi-chi-tra-130-trieu-dong-tien-boi-thuong-bao-hiem-con-nguoi-vietincare-tai-gia-lai', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 22/12/2015, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 130 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh anh Tạ Văn Khoa, tr&uacute; tại L&agrave;ng O r&ecirc; II, Ia G răng, Ia Grai, Gia Lai.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 22/12/2015, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 130 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh anh Tạ Văn Khoa, tr&uacute; tại L&agrave;ng O r&ecirc; II, Ia G răng, Ia Grai, Gia Lai.</span></p>\r\n<p><img src=\"../../../uploads/IMG_6941.png\" alt=\"\" width=\"640\" height=\"480\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p>Th&aacute;ng 6 năm 2015, anh Tạ Văn Khoa được VietinBank CN Gia Lai cho vay 130 triệu đồng, đồng thời tham gia bảo hiểm con người VietinCare của VBI. Th&aacute;ng 8 năm 2015, anh Khoa bất ngờ mắc trọng bệnh rồi đột ngột qua đời.</p>\r\n<p>Sau khi nhận được th&ocirc;ng b&aacute;o từ gia đ&igrave;nh, VBI CN T&acirc;y Nguy&ecirc;n đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Gia Lai hướng dẫn gia đ&igrave;nh anh Khoa ho&agrave;n thiện hồ sơ nhận tiền bảo hiểm theo quy định. Ngay khi nhận được hồ sơ ho&agrave;n thiện, VBI đ&atilde; thanh to&aacute;n 130 triệu anh Khoa c&ograve;n nợ cho Ng&acirc;n h&agrave;ng VietinBank Gia Lai, gi&uacute;p gia đ&igrave;nh anh giảm bớt g&aacute;nh nặng trả nợ.</p>\r\n<p>Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 2 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng, VBI đ&atilde; v&agrave; đang khẳng định được uy t&iacute;n của tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:44:42', '2018-03-21 18:44:42', 'images/news/1520477774_IMG_694_635863960216166933_HasThumb_Thumb.PNG'),
(147, 2, 'Bảo hiểm VietinBank tổ chức họp báo công bố chương trình hợp tác toàn diện với Nệm Kim Cương', 'bao-hiem-vietinbank-to-chuc-hop-bao-cong-bo-chuong-trinh-hop-tac-toan-dien-voi-nem-kim-cuong', '<p><span style=\"font-family: Arial;\">Th&aacute;ng 12/2015, tại Th&agrave;nh phố Hồ Ch&iacute; Minh, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam (VBI) tổ chức họp b&aacute;o c&ocirc;ng bố về chương tr&igrave;nh hợp t&aacute;c to&agrave;n diện với C&ocirc;ng ty TNHH TM-SX Nệm Kim Cương (NKC).&nbsp;</span></p>', '<p>Th&aacute;ng<span style=\"font-family: Arial;\"> 12/2015, tại Th&agrave;nh phố Hồ Ch&iacute; Minh, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam (VBI) tổ chức họp b&aacute;o c&ocirc;ng bố về chương tr&igrave;nh hợp t&aacute;c to&agrave;n diện với C&ocirc;ng ty TNHH TM-SX Nệm Kim Cương (NKC). Tham dự buổi họp b&aacute;o, đại diện ph&iacute;a VBI c&oacute; &ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc, về ph&iacute;a NKC c&oacute; &ocirc;ng Nguyễn Ph&uacute;c Quang &ndash; Tổng Gi&aacute;m đốc, c&ugrave;ng c&aacute;c kh&aacute;ch h&agrave;ng th&acirc;n thiết v&agrave; ph&oacute;ng vi&ecirc;n tới từ c&aacute;c cơ quan th&ocirc;ng tấn b&aacute;o ch&iacute;.</span></p>\r\n<p><img src=\"../../../uploads/Hinh anh le ky ket copy.png\" alt=\"\" width=\"478\" height=\"398\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh Tổng Gi&aacute;m đốc VBI v&agrave; NKC tại buổi họp b&aacute;o)</span></em></p>\r\n<p>Tại buổi họp b&aacute;o, VBI v&agrave; NKC đ&atilde; k&yacute; kết chương tr&igrave;nh hợp t&aacute;c to&agrave;n diện. Tr&ecirc;n cơ sở chương tr&igrave;nh k&yacute; kết, VBI v&agrave; NKC sẽ triển khai Chương tr&igrave;nh khuyến m&atilde;i &ldquo;Triệu lời tri &acirc;n&rdquo;, tặng c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm hơn 20 tỷ đồng cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>B&ecirc;n cạnh việc triển khai chương tr&igrave;nh tri &acirc;n kh&aacute;ch h&agrave;ng, với tinh thần quan t&acirc;m tới sức khỏe v&agrave; chia sẻ rủi ro với cộng đồng, VBI v&agrave; NKC c&ugrave;ng nhau phối hợp thực hiện Chương tr&igrave;nh từ thiện trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>Thỏa thuận hợp t&aacute;c với NKC một lần nữa khẳng định h&igrave;nh ảnh VBI l&agrave; doanh nghiệp lu&ocirc;n hướng tới kh&aacute;ch h&agrave;ng, c&oacute; tr&aacute;ch nhiệm với x&atilde; hội v&agrave; c&oacute; tinh thần nh&acirc;n đạo, tương th&acirc;n, tương &aacute;i.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:45:07', '2018-03-21 18:45:07', 'images/news/1520477899_Hinh-anh-_635865513475697718_HasThumb_Thumb.png'),
(148, 2, 'VBI thông báo lịch nghỉ Tết dương lịch 2016', 'vbi-thong-bao-lich-nghi-tet-duong-lich-2016', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) sẽ nghỉ Tết ng&agrave;y 01 th&aacute;ng 01 năm 2016. Ng&agrave;y 04 th&aacute;ng 01 năm 2016, VBI sẽ trở lại hoạt động b&igrave;nh thường.&nbsp;</span></p>', '<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch h&agrave;ng,</strong><br /> <br />Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.<br /> <br />Nh&acirc;n dịp năm mới 2016, VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng một năm mới nhiều sức khỏe, an khang thịnh vượng, vạn sự như &yacute;!<br /> <br />Bảo hiểm VietinBank (VBI) sẽ nghỉ Tết dương lịch ng&agrave;y 01 th&aacute;ng 01 năm 2016. Ng&agrave;y 04 th&aacute;ng 01 năm 2016, VBI sẽ trở lại hoạt động b&igrave;nh thường.</p>\r\n<p><strong>Tr&acirc;n trọng cảm ơn!</strong></p>\r\n<p><img src=\"../../../uploads/VBI ecard 2016.jpg\" alt=\"\" width=\"1142\" height=\"1142\" /></p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:45:32', '2018-03-21 18:45:32', 'images/news/1520477967_VBI-ecard_635871782870041772_HasThumb_Thumb.jpg.png'),
(149, 2, 'VBI mang yêu thương trao khắp mọi miền', 'vbi-mang-yeu-thuong-trao-khap-moi-mien', '<p><span style=\"font-family: Arial;\">Trong th&aacute;ng 01/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng Nệm Kim Cương (NKC) tổ chức Chương tr&igrave;nh từ thiện trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc.</span></p>', '<p>Trong th&aacute;ng 01/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng Nệm Kim Cương (NKC) tổ chức Chương tr&igrave;nh từ thiện trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc. VBI v&agrave; NKC dự kiến trao 630 g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn như: người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;,&hellip; Bảo hiểm sức khỏe VBICare hỗ trợ họ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p>Đặc biệt, tại 9 tỉnh: H&agrave; Nội, TP.Hồ Ch&iacute; Minh, L&agrave;o Cai, Hải Ph&ograve;ng, Vinh, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức chương tr&igrave;nh &ldquo;Thăm kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại 9 tỉnh tr&ecirc;n, b&ecirc;n cạnh việc trao cho qu&agrave; tặng l&agrave; bảo hiểm VBICare, VBI c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o với mong muốn sẻ chia ho&agrave;n cảnh kh&oacute; khăn.</p>\r\n<p><img src=\"../../../uploads/VBI trao tu thien cho nguoi ngheo.jpg\" alt=\"\" width=\"1366\" height=\"864\" /></p>\r\n<p style=\"text-align: center;\"><em> (VBI v&agrave; NKC tổ chức chương tr&igrave;nh từ thiện tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc)</em></p>\r\n<p>Tổng Gi&aacute;m đốc VBI &ndash; &ocirc;ng L&ecirc; Tuấn Dũng chia sẻ: &ldquo;Phương ch&acirc;m hoạt động của VBI l&agrave; &ldquo;Bảo to&agrave;n gi&aacute; trị cuộc sống&rdquo;. C&ugrave;ng với những cam kết về chất lượng sản phẩm dịch vụ, hiệu quả hoạt động, ch&uacute;ng t&ocirc;i lu&ocirc;n ch&uacute; trọng v&agrave; sẵn s&agrave;ng trong c&ocirc;ng t&aacute;c chia sẻ, chung tay đ&oacute;ng g&oacute;p v&igrave; cộng đồng. Đ&acirc;y cũng ch&iacute;nh l&agrave; một trong những gi&aacute; trị cốt l&otilde;i m&agrave; VBI theo đuổi, v&agrave; cũng l&agrave; niềm tự h&agrave;o của thương hiệu VBI&rdquo;.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương c&ugrave;ng VietinBank, thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh, ủng hộ đồng b&agrave;o lũ lụt H&agrave; tĩnh, thăm v&agrave; tặng qu&agrave; cho b&agrave; con tỉnh H&agrave; Giang, Ủng hộ Trường sa th&acirc;n y&ecirc;u, v.v...</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>C&ocirc;ng ty Nệm Kim Cương sản xuất v&agrave; cung cấp c&aacute;c loại nệm cao su, gối cao su, nệm l&ograve; xo t&uacute;i cao cấp. Sản phẩm của C&ocirc;ng ty Nệm Kim Cương được sản xuất với 100% cao su thi&ecirc;n nhi&ecirc;n tr&ecirc;n d&acirc;y chuyền hiện đại CHLB Đức, được kiểm so&aacute;t bởi hệ thống quản l&yacute; chất lượng đạt ti&ecirc;u chuẩn ISO 9001:2008.<br />Website: www.nemkimcuong.vn<br />Hotline: 1900555510</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:46:05', '2018-03-21 18:46:05', 'images/news/1520478217_VBI-trao-_635890517684384956_HasThumb_Thumb.JPG.png'),
(150, 2, 'Năm 2015: Bảo hiểm VietinBank về đích xuất sắc', 'nam-2015-bao-hiem-vietinbank-ve-dich-xuat-sac', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 06/01/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; nhận giải thưởng đơn vị c&oacute; th&agrave;nh t&iacute;ch xuất sắc tại Hội nghị tổng kết hoạt động kinh doanh năm 2015 v&agrave; triển khai nhiệm vụ kinh doanh năm 2016 do Ng&acirc;n H&agrave;ng TMCP C&ocirc;ng Thương Việt Nam tổ chức.</span></p>', '<p>Ng&agrave;y 06/01/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; nhận giải thưởng đơn vị c&oacute; th&agrave;nh t&iacute;ch xuất sắc tại Hội nghị tổng kết hoạt động kinh doanh năm 2015 v&agrave; triển khai nhiệm vụ kinh doanh năm 2016 do Ng&acirc;n H&agrave;ng TMCP C&ocirc;ng Thương Việt Nam tổ chức.</p>\r\n<p>Năm 2015, Ban L&atilde;nh đạo c&ugrave;ng c&aacute;c c&aacute;n bộ VBI đ&atilde; đồng sức, đồng l&ograve;ng đưa hoạt động kinh doanh tăng trưởng, ph&aacute;t triển nhanh, mạnh, hướng tới tầm nh&igrave;n dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p>VBI đ&atilde; ho&agrave;n th&agrave;nh thắng lợi, xuất sắc v&agrave; to&agrave;n diện c&aacute;c chỉ ti&ecirc;u kinh doanh được giao. Năm 2015, doanh thu VBI dự kiến đạt 550 tỷ đồng, tăng trưởng 77% so với năm 2014, lợi nhuận dự kiến đạt 68,7 tỷ đồng, tăng 8,7% so với năm 2014.</p>\r\n<p>Đặc biệt, VBI đ&atilde; vinh dự nhận nhiều danh hiệu uy t&iacute;n như &ldquo;Thương hiệu mạnh Việt Nam 2014&rdquo;, &ldquo;Top 20 nh&atilde;n hiệu nổi tiếng&rdquo;, được tổ chức VietNam Report vinh danh trong Top 1000 Doanh nghiệp nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam. Gần đ&acirc;y nhất, th&aacute;ng 10/2015, VBI đ&atilde; vinh dự nhận danh hiệu &ldquo;Sao v&agrave;ng Đất Việt&rdquo;.</p>\r\n<p><img src=\"../../../uploads/hoi nghi vietinbank 2016_web.jpg\" alt=\"\" width=\"800\" height=\"548\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Tổng Gi&aacute;m đốc VBI đại diện nhận Giải thưởng \"Đơn vị c&oacute; th&agrave;nh t&iacute;ch xuất sắc\")</em></p>\r\n<p style=\"text-align: left;\">&nbsp;B&ecirc;n cạnh đ&oacute;, VBI đ&atilde; đổi mới c&ocirc;ng t&aacute;c quản trị theo chiến lược để triển khai chuyển đổi m&ocirc; h&igrave;nh theo hướng cổ phần h&oacute;a nhằm năng lực cạnh tranh v&agrave; năng lực điều h&agrave;nh. VBI ph&aacute;t triển c&aacute;c hoạt động dịch vụ v&agrave; hệ thống sản phẩm mới đa dạng, cạnh tranh th&ocirc;ng qua cải tiến mạnh mẽ hệ thống c&ocirc;ng nghệ hiện đại; hệ thống dịch vụ ph&aacute;t triển mạnh mẽ.</p>\r\n<p>Ngo&agrave;i ra, VBI tiếp tục củng cố v&agrave; ph&aacute;t triển hệ thống mạng lưới hoạt động tr&ecirc;n to&agrave;n quốc. Th&aacute;ng 1/2015, VBI th&agrave;nh lập Chi nh&aacute;nh T&acirc;y Bắc, Đồng Nai. Th&aacute;ng 5/2015, VBI th&agrave;nh lập Chi nh&aacute;nh Nghệ An. Th&aacute;ng 7/2015, VBI th&agrave;nh lập Chi nh&aacute;nh Kh&aacute;nh H&ograve;a, Cần Thơ.</p>\r\n<p>Năm 2015, VBI tăng cường hợp t&aacute;c to&agrave;n diện với c&aacute;c ng&acirc;n h&agrave;ng: OceanBank v&agrave; GP. Bank. Thỏa thuận hợp t&aacute;c với OceanBank v&agrave; GPBank đ&aacute;nh dấu bước ph&aacute;t triển của VBI trong c&aacute;c hoạt động hợp t&aacute;c song phương v&agrave; đa phương, n&acirc;ng cao hiệu quả hoạt động kinh doanh, g&oacute;p phần khẳng định gi&aacute; trị thương hiệu của VBI.</p>\r\n<p>B&ecirc;n cạnh kết quả kinh doanh ấn tượng, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: tặng qu&agrave; v&agrave; tổ chức thăm kh&aacute;m bệnh cho người ngh&egrave;o tại 63 tỉnh tr&ecirc;n to&agrave;n quốc, tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương c&ugrave;ng VietinBank, tổ chức chương tr&igrave;nh từ thiện &ldquo;&Aacute;o ấm trao em&rdquo; tặng quần &aacute;o v&agrave; đồ d&ugrave;ng học tập cho c&aacute;c em nhỏ tại x&atilde; Tường Sơn v&agrave; Bản Ồ Ồ, Gi&agrave; H&oacute;p thuộc Huyện Anh Sơn, Tỉnh Nghệ An,v.v,&hellip;</p>\r\n<p>Với định hướng ph&aacute;t triển bền vững, chiến lược kinh doanh d&agrave;i hạn, ch&uacute; trọng n&acirc;ng cao chất lượng dịch vụ kh&aacute;ch h&agrave;ng, trong năm 2016, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u chiến lược chuyển đổi m&ocirc; h&igrave;nh nhằm n&acirc;ng cao hiệu quả kinh doanh, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:46:43', '2018-03-21 18:46:43', 'images/news/1520478310_hoi-nghi-_635877821718917981_HasThumb_Thumb.jpg.png'),
(151, 2, 'Đảng bộ Bảo hiểm VietinBank kết nạp Đảng viên', 'dang-bo-bao-hiem-vietinbank-ket-nap-dang-vien', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 12/01/2016, tại H&agrave; Nội, Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức lễ kết nạp đảng cho 10 quần ch&uacute;ng ưu t&uacute;: Nguyễn Hồng Phong, Đỗ Anh Vũ, Vũ Mạnh H&agrave;, Đ&agrave;o Hải Anh, L&ecirc; Thanh T&ugrave;ng, Vũ Văn V&otilde;, Phạm Thị Hải H&ograve;a, V&otilde; Thị Th&uacute;y Hằng, Trần Thị Thu Hảo, Nguyễn B&iacute;ch Hạnh&nbsp;</span></p>', '<p>Ng&agrave;y 12/01/2016, tại H&agrave; Nội, Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức lễ kết nạp đảng cho 10 quần ch&uacute;ng ưu t&uacute;. Tham dự buổi lễ c&oacute; đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Đảng bộ Bảo hiểm VietinBank, đồng ch&iacute; Trần Thị Hương Thủy &ndash; Ph&oacute; B&iacute; thư Đảng bộ Bảo hiểm VietinBank c&ugrave;ng to&agrave;n thể Đảng bộ Bảo hiểm VietinBank.</p>\r\n<p>Trải qua qu&aacute; tr&igrave;nh tu dưỡng v&agrave; r&egrave;n luyện, 10 quần ch&uacute;ng ưu t&uacute; Nguyễn Hồng Phong, Đỗ Anh Vũ, Vũ Mạnh H&agrave;, Đ&agrave;o Hải Anh, L&ecirc; Thanh T&ugrave;ng, Vũ Văn V&otilde;, Phạm Thị Hải H&ograve;a, V&otilde; Thị Th&uacute;y Hằng, Trần Thị Thu Hảo, Nguyễn B&iacute;ch Hạnh đ&atilde; được k&ecirc;́t nạp Đảng. Đ&acirc;y l&agrave; những c&aacute;n bộ ưu t&uacute;, c&oacute; phẩm chất đạo đức tốt, c&oacute; năng lực tr&igrave;nh độ chuy&ecirc;n m&ocirc;n, đ&atilde; trải qua qu&aacute; tr&igrave;nh phấn đấu v&agrave; r&egrave;n luyện tốt.</p>\r\n<p><img src=\"../../../uploads/IMG_5500.jpg\" alt=\"\" width=\"1366\" height=\"555\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh buổi lễ kết nạp 10 Đảng vi&ecirc;n mới)</em></p>\r\n<p>Ph&aacute;t biểu chỉ đạo tại buổi lễ, đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Đảng bộ Bảo hiểm VietinBank ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n vừa được kết nạp v&agrave; nhấn mạnh: Phấn đấu trở th&agrave;nh đảng vi&ecirc;n l&agrave; niềm vinh dự, niềm tự h&agrave;o v&agrave; cũng l&agrave; đ&ograve;i hỏi, trọng tr&aacute;ch với c&aacute;c Đảng vi&ecirc;n mới. C&aacute;c Đảng vi&ecirc;n mới kết nạp cần học tập nắm vững chủ trương ch&iacute;nh s&aacute;ch của Đảng v&agrave; Nh&agrave; nước, tu&acirc;n thủ ph&aacute;p luật, quy chế nội quy lao động,&hellip;ra sức học tập n&acirc;ng cao nghiệp vụ, t&iacute;ch cực tham gia c&aacute;c hoạt động của đơn vị. Mỗi đồng ch&iacute; cần gương mẫu trong c&ocirc;ng việc, t&iacute;ch cực phấn đấu với tinh thần tr&aacute;ch nhiệm cao. Trong thời gian tới Đảng bộ Bảo hiểm VietinBank sẽ đẩy mạnh ph&aacute;t triển lực lượng Đảng vi&ecirc;n v&agrave; quần ch&uacute;ng ưu t&uacute; đứng trong h&agrave;ng ngũ Đảng.<br /> <br />Thay mặt cho c&aacute;c đảng vi&ecirc;n mới được kết nạp, đồng ch&iacute; Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VBI ph&aacute;t biểu b&agrave;y tỏ niềm vinh dự được đứng trong h&agrave;ng ngũ của Đảng, cảm ơn sự quan t&acirc;m gi&aacute;o dục, bồi dưỡng của Đảng ủy Bảo hiểm VietinBank, Ban l&atilde;nh đạo, c&aacute;c tổ chức Đảng cơ sở đ&atilde; tạo điều kiện cho c&aacute;c quần ch&uacute;ng ưu t&uacute; r&egrave;n luyện v&agrave; phấn đấu trở th&agrave;nh đảng vi&ecirc;n. C&aacute;c đảng vi&ecirc;n mới sẽ quyết t&acirc;m, nỗ lực nhiều hơn nữa để xứng đ&aacute;ng l&agrave; đảng vi&ecirc;n, thực hiện đầy đủ nhiệm vụ Đảng bộ giao, g&oacute;p phần x&acirc;y dựng Đảng bộ Bảo hiểm VietinBank ng&agrave;y một vững mạnh.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:47:19', '2018-03-21 18:47:19', 'images/news/1520478406_IMG_550_635882165054738137_HasThumb_Thumb.jpg.png'),
(152, 2, 'VBI chi trả 1,3 tỷ đồng bảo hiểm cho khách hàng tại Phú Yên', 'vbi-chi-tra-1-3-ty-dong-bao-hiem-cho-khach-hang-tai-phu-yen', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 21/01/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Kh&aacute;nh H&ograve;a phối hợp với VietinBank CN Ph&uacute; Y&ecirc;n đ&atilde; tổ chức trao 1,3 tỷ đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh chị Nguyễn Thị Hồng Ngọc tại khu phố Long Hải Nam, phường Xu&acirc;n Ph&uacute;, thị x&atilde; S&ocirc;ng Cầu, tỉnh Ph&uacute; Y&ecirc;n&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 21/01/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Kh&aacute;nh H&ograve;a phối hợp với VietinBank CN Ph&uacute; Y&ecirc;n đ&atilde; tổ chức trao 1,3 tỷ đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh chị Nguyễn Thị Hồng Ngọc tại khu phố Long Hải Nam, phường Xu&acirc;n Ph&uacute;, thị x&atilde; S&ocirc;ng Cầu, tỉnh Ph&uacute; Y&ecirc;n.</span></p>\r\n<p><img src=\"../../../uploads/boi thuong phu yen_web.jpg\" alt=\"\" width=\"640\" height=\"452\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p>Th&aacute;ng 9/2015, chị Nguyễn Thị Hồng Ngọc được VietinBank CN Ph&uacute; Y&ecirc;n cho vay 1,3 tỷ đồng, đồng thời tham gia bảo hiểm con người VietinCare của VBI. V&agrave;o ng&agrave;y 08/10/2015, chị Ngọc kh&ocirc;ng may gặp rủi ro tai nạn giao th&ocirc;ng tại Km 1294+600m, Quốc lộ 1A, Phường Xu&acirc;n Đ&agrave;i, thị x&atilde; S&ocirc;ng Cầu, tỉnh Ph&uacute; Y&ecirc;n dẫn tới tử vong.</p>\r\n<p>Ngay sau khi nhận được th&ocirc;ng tin từ gia đ&igrave;nh, VBI CN Kh&aacute;nh H&ograve;a đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Ph&uacute; Y&ecirc;n hướng dẫn gia đ&igrave;nh l&agrave;m hồ sơ nhận tiền bảo hiểm theo quy định. Tới 21/01/2016, VBI đ&atilde; ho&agrave;n thiện thủ tục v&agrave; thanh to&aacute;n 1,3 tỷ đồng chị Ngọc c&ograve;n nợ cho Ng&acirc;n h&agrave;ng VietinBank Ph&uacute; Y&ecirc;n, gi&uacute;p gia đ&igrave;nh chị loại bỏ g&aacute;nh nặng trả nợ.<br /> <br />Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 3 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng, VBI đ&atilde; v&agrave; đang khẳng định được uy t&iacute;n của tr&ecirc;n thị trường bảo hiểm với chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:48:04', '2018-03-21 18:48:04', 'images/news/1520478519_boi-thuon_635890508436069541_HasThumb_Thumb.jpg.png'),
(153, 2, 'VBI triển khai nhiệm vụ kinh doanh năm 2016', 'vbi-trien-khai-nhiem-vu-kinh-doanh-nam-2016', '<p><span style=\"font-family: Arial;\">S&aacute;ng nay (22/01/2016), tại Nha Trang, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Hội nghị tổng kết hoạt động kinh doanh năm 2015 v&agrave; triển khai nhiệm vụ kinh doanh năm 2016. Hội nghị nhằm đ&aacute;nh gi&aacute; lại t&igrave;nh h&igrave;nh hoạt động kinh doanh năm 2015 v&agrave; đề ra c&aacute;c phương hướng, nhiệm vụ của năm 20...&nbsp;</span></p>', '<p>S&aacute;ng nay (22/01/2016), tại Nha Trang, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Hội nghị tổng kết hoạt động kinh doanh năm 2015 v&agrave; triển khai nhiệm vụ kinh doanh năm 2016. Hội nghị nhằm đ&aacute;nh gi&aacute; lại t&igrave;nh h&igrave;nh hoạt động kinh doanh năm 2015 v&agrave; đề ra c&aacute;c phương hướng, nhiệm vụ của năm 2016, đưa VBI hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p>Năm 2015, VBI đ&atilde; đạt được kết quả kinh doanh vượt bậc, c&aacute;c chỉ ti&ecirc;u đều tăng trưởng mạnh mẽ. Tổng t&agrave;i sản đạt 1.115 tỷ đồng, tăng 23,5% so với năm 2014; doanh thu đạt 550 tỷ đồng, tăng trưởng 77% so với năm 2014; trong đ&oacute;, doanh thu bảo hiểm gốc đạt 488 tỷ đồng, tăng trưởng 85% so với năm 2014; doanh thu nhận t&aacute;i bảo hiểm đạt 62 tỷ đồng, tăng trưởng 34% so với năm 2014; lợi nhuận đạt 68,7 tỷ đồng, tăng 8,7% so với năm 2014, tổng quỹ dự ph&ograve;ng nghiệp vụ đạt 237 tỷ đồng tăng 73% so với năm 2014.</p>\r\n<p>Đặc biệt, VBI đ&atilde; vinh dự nhận nhiều danh hiệu uy t&iacute;n như &ldquo;Thương hiệu mạnh Việt Nam 2014&rdquo;, &ldquo;Top 20 nh&atilde;n hiệu nổi tiếng&rdquo;, được tổ chức Vietnam Report vinh danh trong &ldquo;Top 1000 Doanh nghiệp nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam&rdquo;. Gần đ&acirc;y nhất, th&aacute;ng 10/2015, VBI đ&atilde; vinh dự nhận danh hiệu &ldquo;Sao v&agrave;ng Đất Việt&rdquo;. Những danh hiệu tr&ecirc;n đ&atilde; khẳng định sự cố gắng ph&aacute;t triển kh&ocirc;ng ngừng, sự vươn m&igrave;nh mạnh mẽ của VBI trong năm qua.</p>\r\n<p>Cũng trong năm 2015, VBI đ&atilde; k&yacute; kết hợp t&aacute;c to&agrave;n diện với c&aacute;c ng&acirc;n h&agrave;ng OceanBank v&agrave; GPBank, việc k&yacute; kết thỏa thuận hợp t&aacute;c n&agrave;y đ&aacute;nh dấu bước ph&aacute;t triển của VBI trong c&aacute;c hoạt động hợp t&aacute;c song phương v&agrave; đa phương, n&acirc;ng cao hiệu quả hoạt động kinh doanh, g&oacute;p phần khẳng định gi&aacute; trị thương hiệu của VBI tr&ecirc;n thị trường Việt Nam.</p>\r\n<p><img src=\"../../../uploads/web.jpg\" alt=\"\" width=\"800\" height=\"438\" /></p>\r\n<p style=\"text-align: center;\"><em>(C&aacute;c đơn vị đạt th&agrave;nh t&iacute;ch xuất sắc nhận bằng khen tại Hội nghị triển khai nhiệm vụ kinh doanh 2016)</em></p>\r\n<p>Tại Hội nghị, b&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty bảo hiểm VBI đồng thời l&agrave; Ủy vi&ecirc;n HĐQT VietinBank ph&aacute;t biểu: &ldquo;Để tiếp tục n&acirc;ng cao năng lực cạnh tranh v&agrave; năng lực điều h&agrave;nh, VBI sẽ tiếp tục đổi mới c&ocirc;ng t&aacute;c quản trị, ph&aacute;t triển c&aacute;c hoạt động dịch vụ v&agrave; hệ thống sản phẩm mới đa dạng, cải tiến mạnh mẽ hệ thống c&ocirc;ng nghệ hiện đại, hệ thống dịch vụ sau b&aacute;n h&agrave;ng. Đồng thời, VBI sẽ nghi&ecirc;n cứu, chuẩn bị triển khai chuyển đổi m&ocirc; h&igrave;nh theo hướng cổ phần h&oacute;a trong năm 2016 nhằm đ&aacute;p ứng nhu cầu ng&agrave;y một ph&aacute;t triển của doanh nghiệp.&rdquo;</p>\r\n<p>Kế hoạch kinh doanh năm 2016, VBI đề ra một số chỉ ti&ecirc;u kế hoạch t&agrave;i ch&iacute;nh: Tổng t&agrave;i sản tăng 10%, tổng doanh thu tăng trưởng 50%, lợi nhuận tăng trưởng 10% so với năm 2015, mở rộng mạng lưới kinh doanh, th&agrave;nh lập th&ecirc;m từ 3-5 chi nh&aacute;nh mới.</p>\r\n<p>Với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững, trong năm 2016, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u chiến lược chuyển đổi m&ocirc; h&igrave;nh nhằm n&acirc;ng cao hiệu quả kinh doanh, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 07:48:37', '2018-03-21 18:48:37', 'images/news/1520478621_we_635890698769892981_HasThumb_Thumb.jpg.png'),
(154, 2, 'VBI ký kết hợp tác toàn diện với Saigontourist', 'vbi-ky-ket-hop-tac-toan-dien-voi-saigontourist', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 25/1/2016 tại Tp.HCM, VBI tổ chức lễ k&yacute; kết hợp t&aacute;c to&agrave;n diện với Saigontourist. Theo hợp đồng k&yacute; kết, VBI sẽ tư vấn to&agrave;n bộ chương tr&igrave;nh quản l&yacute; rủi ro v&agrave; cung cấp dịch vụ bảo hiểm thống nhất cho to&agrave;n bộ hoạt động của Saigontourist bao gồm: bảo hiểm t&agrave;i sản, bảo hiểm tr&aacute;ch ...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 25/1/2016 tại Tp.HCM, Bảo hiểm VietinBank (VBI) tổ chức lễ k&yacute; kết hợp t&aacute;c to&agrave;n diện với Tổng c&ocirc;ng ty Du lịch S&agrave;i G&ograve;n (Saigon Tourist). &Ocirc;ng Nguyễn Anh Tuấn - Chủ tịch HĐTV Saigontourist, &ocirc;ng Trần H&ugrave;ng Việt &ndash; Tổng gi&aacute;m đốc Saigontourist, b&agrave; B&ugrave;i Như &Yacute; &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VietinBank, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI c&ugrave;ng nhiều l&atilde;nh đạo của hai đơn vị đ&atilde; dự lễ k&yacute; kết quan trọng n&agrave;y.<br /> <br />Trước đ&oacute;, v&agrave;o th&aacute;ng 12/2014, VietinBank đ&atilde; k&yacute; kết hợp t&aacute;c to&agrave;n diện với Saigontourist. Đ&acirc;y l&agrave; tiền đề của hợp t&aacute;c giữa VBI v&agrave; Saigontourist. Theo hợp đồng k&yacute; kết, VBI sẽ tư vấn to&agrave;n bộ chương tr&igrave;nh quản l&yacute; rủi ro v&agrave; cung cấp dịch vụ bảo hiểm thống nhất cho to&agrave;n bộ hoạt động của Saigontourist bao gồm: bảo hiểm t&agrave;i sản, bảo hiểm tr&aacute;ch nhiệm, bảo hiểm xe cơ giới, bảo hiểm sức khoẻ, bảo hiểm du lịch,&hellip;</span></p>\r\n<p><img src=\"../../../uploads/IMG_8661.jpg\" alt=\"\" width=\"788\" height=\"451\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh tại buổi lễ k&yacute; kết hợp t&aacute;c to&agrave;n diện VBI &ndash; Saigontourist)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Chia sẻ tại lễ k&yacute; kết, TGĐ Saigontourist &ocirc;ng Trần H&ugrave;ng Việt cho biết: th&agrave;nh lập v&agrave;o năm 1975, Saigontourist đ&atilde; li&ecirc;n tục ph&aacute;t triển, hiện đang sở hữu v&agrave; quản l&yacute; nhiều c&ocirc;ng ty du lịch lữ h&agrave;nh, nh&agrave; h&agrave;ng, kh&aacute;ch sạn, khu du lịch đẳng cấp quốc tế. Trong lĩnh vực li&ecirc;n doanh, Saigontourist đ&atilde; đầu tư v&agrave;o hơn 50 c&ocirc;ng ty cổ phần, c&ocirc;ng ty TNHH trong nước v&agrave; 9 c&ocirc;ng ty li&ecirc;n doanh c&oacute; vốn nước ngo&agrave;i, hoạt động tại c&aacute;c th&agrave;nh phố lớn tr&ecirc;n khắp cả nước.<br /> <br />B&agrave; B&ugrave;i Như &Yacute; &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VietinBank tin tưởng rằng việc k&yacute; kết hợp t&aacute;c to&agrave;n diện giữa Saigontourist v&agrave; VBI sẽ mở ra tiềm năng lớn trong việc ph&aacute;t huy thế mạnh của mỗi b&ecirc;n. VBI cũng cam kết cung cấp cho Saigontourist c&aacute;c sản phẩm bảo hiểm đa dạng với chất lượng dịch vụ tốt nhất.<br /> <br />Th&ocirc;ng qua việc k&yacute; kết n&agrave;y, hai b&ecirc;n sẽ triển khai c&aacute;c nội dung hợp t&aacute;c s&acirc;u rộng tới c&aacute;c đơn vị th&agrave;nh vi&ecirc;n của Saigontourist. Trong thời gian tới, VBI v&agrave; Saigontourist sẽ tiếp tục những hoạt động thiết thực nhằm tối ưu ho&aacute; lợi &iacute;ch của c&aacute;c b&ecirc;n v&agrave; c&ugrave;ng nhau ph&aacute;t triển mạnh mẽ.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:49:06', '2018-03-21 18:49:06', 'images/news/1520478717_IMG_866_635894253181479843_HasThumb_Thumb.JPG.png'),
(155, 2, 'VBI thông báo lịch nghỉ Tết Bính Thân', 'vbi-thong-bao-lich-nghi-tet-binh-than', '<p><span style=\"font-family: Arial;\">VBI sẽ nghỉ Tết từ ng&agrave;y 06 th&aacute;ng 02 năm 2016 tới ng&agrave;y 14 th&aacute;ng 02 năm 2016. Ng&agrave;y 15 th&aacute;ng 02 năm 2016, VBI sẽ trở lại hoạt động b&igrave;nh thường.&nbsp;</span></p>', '<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch h&agrave;ng,</strong></p>\r\n<p>Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.</p>\r\n<p>Nh&acirc;n dịp năm mới B&iacute;nh Th&acirc;n, VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng một năm mới nhiều sức khỏe, an khang thịnh vượng, vạn sự như &yacute;.</p>\r\n<p>Bảo hiểm VietinBank sẽ nghỉ Tết từ ng&agrave;y 06 th&aacute;ng 02 năm 2016 tới ng&agrave;y 14 th&aacute;ng 02 năm 2016. Ng&agrave;y 15 th&aacute;ng 02 năm 2016, VBI sẽ trở lại hoạt động b&igrave;nh thường.</p>\r\n<p><strong>Tr&acirc;n trọng cảm ơn!</strong></p>\r\n<p><img src=\"../../../uploads/banner ngang tet binh than.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>&nbsp;</p>', '2018-03-21 07:49:31', '2018-03-21 18:49:31', 'images/news/1520478785_banner-to_635899343993709969_HasThumb_Thumb.jpg.png'),
(156, 2, 'VBI tổ chức gặp mặt cán bộ hưu trí nhân dịp Tết Bính Thân', 'vbi-to-chuc-gap-mat-can-bo-huu-tri-nhan-dip-tet-binh-than', '<p><span style=\"font-family: Arial;\">Trong kh&ocirc;ng kh&iacute; chuẩn bị đ&oacute;n ch&agrave;o năm mới Xu&acirc;n B&iacute;nh Th&acirc;n 2016, ng&agrave;y 2/2/2016 tại H&agrave; Nội, VBI trang trọng tổ chức buổi gặp mặt c&aacute;c c&aacute;n bộ hưu tr&iacute;. Đ&acirc;y l&agrave; hoạt động gi&agrave;u &yacute; nghĩa v&agrave; l&agrave; một n&eacute;t đẹp Văn h&oacute;a doanh nghiệp VBI.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Trong kh&ocirc;ng kh&iacute; chuẩn bị đ&oacute;n ch&agrave;o năm mới Xu&acirc;n B&iacute;nh Th&acirc;n 2016, ng&agrave;y 2/2/2016 tại H&agrave; Nội, VBI trang trọng tổ chức buổi gặp mặt c&aacute;c c&aacute;n bộ hưu tr&iacute;. Đ&acirc;y l&agrave; hoạt động gi&agrave;u &yacute; nghĩa v&agrave; l&agrave; một n&eacute;t đẹp Văn h&oacute;a doanh nghiệp VBI. Tham dự buổi gặp mặt c&oacute; đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; Thư Đảng ủy, Tổng Gi&aacute;m đốc VBI; đồng ch&iacute; Trần Thị Hương Thủy - Ph&oacute; B&iacute; thư Đảng ủy, Trưởng Ph&ograve;ng Tổ chức h&agrave;nh ch&iacute;nh c&ugrave;ng c&ugrave;ng to&agrave;n thể c&aacute;n bộ hưu tr&iacute; VBI.<br /> <br />Trong kh&ocirc;ng kh&iacute; ấm c&uacute;ng, th&acirc;n mật trước thềm xu&acirc;n mới, đồng ch&iacute; L&ecirc; Tuấn Dũng thay mặt Ban L&atilde;nh đạo VBI gửi đến tất cả c&aacute;c c&aacute;n bộ hưu tr&iacute; những lời ch&uacute;c sức khoẻ, thăm hỏi ch&acirc;n th&agrave;nh. Đồng ch&iacute; Dũng đ&atilde; vui mừng th&ocirc;ng b&aacute;o những kết quả ấn tượng trong hoạt động kinh doanh của VBI trong năm 2015 v&agrave; kế hoạch chuyển đổi m&ocirc; h&igrave;nh theo hướng cổ phần h&oacute;a năm 2016.<br /> <br />Năm 2015, VBI đ&atilde; tăng trưởng, ph&aacute;t triển nhanh, mạnh mẽ, hướng tới tầm nh&igrave;n dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam. Minh chứng cho những th&agrave;nh c&ocirc;ng của VBI, năm 2015, doanh thu VBI đạt 550 tỷ đồng, tăng trưởng 77% so với năm 2014, lợi nhuận đạt 68,7 tỷ đồng, tăng 8,7% so với năm 2014. Đặc biệt, VBI đ&atilde; vinh dự nhận nhiều danh hiệu uy t&iacute;n như &ldquo;Thương hiệu mạnh Việt Nam 2014&rdquo;, &ldquo;Top 20 nh&atilde;n hiệu nổi tiếng&rdquo;, được tổ chức VietNam Report vinh danh trong Top 1000 Doanh nghiệp nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam. Gần đ&acirc;y nhất, th&aacute;ng 10/2015, VBI đ&atilde; vinh dự nhận danh hiệu &ldquo;Sao v&agrave;ng Đất Việt&rdquo; đ&atilde; khẳng định sự cố gắng ph&aacute;t triển kh&ocirc;ng ngừng, sự vươn m&igrave;nh mạnh mẽ của VBI.</span></p>\r\n<p><img src=\"../../../uploads/gap mat can bo huu tet binh than.jpg\" alt=\"\" width=\"1366\" height=\"777\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Tổng Gi&aacute;m đốc VBI tặng qu&agrave; Tết cho c&aacute;c c&aacute;n bộ hưu tr&iacute;)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Để c&oacute; được những th&agrave;nh t&iacute;ch đ&aacute;ng tự h&agrave;o tr&ecirc;n, ngo&agrave;i sự cố gắng, nỗ lực của Ban L&atilde;nh đạo v&agrave; tập thể VBI, phải kể đến sự t&iacute;ch lũy, kế thừa những kinh nghiệm qu&yacute; b&aacute;u của đội ngũ l&atilde;nh đạo v&agrave; c&aacute;n bộ đi trước, những người đ&atilde; đặt nền m&oacute;ng cho sự ra đời v&agrave; ph&aacute;t triển của VBI. VBI ghi nhận v&agrave; tri &acirc;n c&ocirc;ng lao đ&oacute;ng g&oacute;p của c&aacute;c thế hệ c&aacute;n bộ hưu tr&iacute;, những người đ&atilde; từng tận t&acirc;m, tận lực cống hiến g&oacute;p phần đưa VBI ph&aacute;t triển lớn mạnh.<br /> <br />Đại diện c&aacute;c c&aacute;n bộ hưu tr&iacute;, b&agrave; Trần Thị B&iacute;ch Li&ecirc;n &ndash; Nguy&ecirc;n Tổng Gi&aacute;m đốc C&ocirc;ng ty b&agrave;y bỏ niềm vui trước kết quả kinh doanh t&iacute;ch cực của VBI thời gian qua. B&agrave; Li&ecirc;n tin tưởng với sự t&acirc;m huyết, s&aacute;ng tạo v&agrave; tầm nh&igrave;n xa của c&aacute;c đồng ch&iacute; trong Ban L&atilde;nh đạo c&ugrave;ng lớp c&aacute;n bộ trẻ đầy nhiệt huyết, VBI sẽ chuyển đổi th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh cổ phần h&oacute;a trong năm 2016.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 07:50:28', '2018-03-21 18:50:28', 'images/news/1520480693_gap-mat-c_635900910767457876_HasThumb_Thumb.jpg.png'),
(157, 2, 'VBI tổ chức khám bệnh và tặng quà cho 10 hộ nghèo tại Đà Nẵng', 'vbi-to-chuc-kham-benh-va-tang-qua-cho-10-ho-ngheo-tai-da-nang', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 19/2/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại Đ&agrave; Nẵng. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VB...&nbsp;</span></p>', '<p>Ng&agrave;y<span style=\"font-family: Arial;\"> 19/2/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại Đ&agrave; Nẵng. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.<br /> <br />Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Đ&agrave; Nẵng như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</span></p>\r\n<p><img src=\"../../../uploads/DN.png\" alt=\"\" width=\"397\" height=\"360\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại Đ&agrave; Nẵng)</span></em></p>\r\n<p><span style=\"font-family: Arial;\"> Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.<br /> <br />H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 07:50:57', '2018-03-21 18:50:57', 'images/news/1520480824_pic-New-i_636177418680048705_HasThumb_Thumb.png'),
(158, 2, 'VBI tổ chức tặng quà cho 10 hộ nghèo tại Bạc Liêu', 'vbi-to-chuc-tang-qua-cho-10-ho-ngheo-tai-bac-lieu', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 26/2/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại Bạc Li&ecirc;u. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe V...&nbsp;</span></p>', '<p>Ng&agrave;y 26/2/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại Bạc Li&ecirc;u. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Bạc Li&ecirc;u như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/Bac Lieu.jpg\" alt=\"\" width=\"905\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại Bạc Li&ecirc;u)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:51:25', '2018-03-21 18:51:25', 'images/news/1520480920_pic-New-i_636177411442093258_HasThumb_Thumb.jpg.png'),
(159, 2, 'VBI tặng quà cho 10 hộ nghèo tại Quảng Nam', 'vbi-tang-qua-cho-10-ho-ngheo-tai-quang-nam', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 03/03/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức trao từ thiện cho 10 hộ ngh&egrave;o tại Quảng Nam. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi...</span></p>', '<p>Ng&agrave;y 03/03/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức trao từ thiện cho 10 hộ ngh&egrave;o tại Quảng Nam. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Quảng Nam như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/IMG_9365.jpg\" alt=\"\" width=\"1366\" height=\"920\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh đại diện VBI trao từ thiện cho người ngh&egrave;o tại Quảng Nam)</em></p>\r\n<p><img src=\"../../../uploads/IMG_9359.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:54:01', '2018-03-21 18:54:01', 'images/news/1520481026_IMG_936_635926979378318126_HasThumb_Thumb.JPG.png'),
(160, 2, 'VBI tưng bừng kỷ niệm Ngày Quốc tế Phụ nữ 8/3', 'vbi-tung-bung-ky-niem-ngay-quoc-te-phu-nu-8-3', '<p><span style=\"font-family: Arial;\">Ch&agrave;o mừng ng&agrave;y Quốc tế Phụ nữ 8/3, trụ sở ch&iacute;nh v&agrave; c&aacute;c chi nh&aacute;nh VBI đ&atilde; tổ chức nhiều hoạt động thiết thực, &yacute; nghĩa d&agrave;nh tặng chị em phụ nữ.&nbsp;</span></p>', '<p>Ch&agrave;o mừng ng&agrave;y Quốc tế Phụ nữ 8/3, trụ sở ch&iacute;nh v&agrave; c&aacute;c chi nh&aacute;nh VBI đ&atilde; tổ chức nhiều hoạt động thiết thực, &yacute; nghĩa d&agrave;nh tặng chị em phụ nữ.</p>\r\n<p>Đại diện Ban L&atilde;nh đạo, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI đ&atilde; tặng hoa, d&agrave;nh những lời ch&uacute;c tốt đẹp v&agrave; ghi nhận sự đ&oacute;ng g&oacute;p t&iacute;ch cực của các nữ c&aacute;n bộ đối với sự ph&aacute;t triển, th&agrave;nh c&ocirc;ng của VBI trong suốt những năm qua.</p>\r\n<p><img src=\"../../../uploads/IMG_5564A.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><img src=\"../../../uploads/IMG_5558A.jpg\" alt=\"\" width=\"1366\" height=\"747\" /></p>\r\n<p>Cũng trong ng&agrave;y, VBI Chi nh&aacute;nh S&agrave;i G&ograve;n đ&atilde; tổ chức cuộc thi cắm hoa. C&aacute;c ph&ograve;ng ban chia ra l&agrave;m 5 đội thi. Sau 90 ph&uacute;t hăng say lao động, 5 đội đ&atilde; ho&agrave;n th&agrave;nh c&aacute;c t&aacute;c phẩm nghệ thuật rất đặc biệt. Giải thưởng cao nhất đ&atilde; thuộc về đội số 2 với chủ đề T&igrave;nh mẹ.</p>\r\n<p><img src=\"../../../uploads/IMG_8312.jpg\" alt=\"\" width=\"1024\" height=\"339\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8187.jpg\" alt=\"\" width=\"800\" height=\"533\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8207.jpg\" alt=\"\" width=\"1024\" height=\"682\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8230.jpg\" alt=\"\" width=\"912\" height=\"565\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8240.jpg\" alt=\"\" width=\"1024\" height=\"683\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8274.jpg\" alt=\"\" width=\"1024\" height=\"587\" /></p>\r\n<p>C&aacute;c hoạt động nh&acirc;n kỷ niệm Ng&agrave;y Quốc tế Phụ nữ 8/3 l&agrave; m&oacute;n qu&agrave; tinh thần v&ocirc; gi&aacute; đối với chị em phụ nữ VBI, gi&uacute;p cho c&aacute;n bộ VBI th&ecirc;m đo&agrave;n kết v&agrave; gắn b&oacute; với nhau hơn, tạo n&ecirc;n nền tảng văn h&oacute;a doanh nghiệp vững chắc.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:56:33', '2018-03-21 18:56:33', 'images/news/1520481217_TSC_635930489264415537_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(161, 2, 'VBI tổ chức đào tạo mô hình Bancassurance tại Hàn Quốc', 'vbi-to-chuc-dao-tao-mo-hinh-bancassurance-tai-han-quoc', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 18/03 - 25/03/2016, VBI đ&atilde; tổ chức đ&agrave;o tạo v&agrave; học tập kinh nghiệm triển khai m&ocirc; h&igrave;nh Bancassurance cho hơn 30 c&aacute;n bộ VBI v&agrave; VietinBank tại H&agrave;n Quốc.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 18/03 - 25/03/2016, VBI đ&atilde; tổ chức đ&agrave;o tạo v&agrave; học tập kinh nghiệm triển khai m&ocirc; h&igrave;nh Bancassurance cho hơn 30 c&aacute;n bộ VBI v&agrave; VietinBank tại H&agrave;n Quốc. B&ecirc;n cạnh c&aacute;c buổi đ&agrave;o tạo, Đo&agrave;n đ&atilde; được đi thăm quan c&aacute;c danh lam thắng cảnh nổi tiếng của H&agrave;n Quốc như Cung điện ho&agrave;ng gia, Nh&agrave; xanh Phủ Tổng thống, th&aacute;p truyền h&igrave;nh Namsan Hanok. Cũng tại chuyến đi, VBI đ&atilde; tổ chức trao thưởng cho c&aacute;c c&aacute;n bộ c&oacute; th&agrave;nh t&iacute;ch trong c&ocirc;ng t&aacute;c triển khai Bancassurance.<br /> <br />Chuyến đ&agrave;o tạo tại H&agrave;n Quốc đ&atilde; đạt được nhiều kết quả tốt đẹp v&agrave; để lại những ấn tượng kh&oacute; phai. Chuyến đi n&agrave;y đ&atilde; mở ra một tầm nh&igrave;n mới cho c&aacute;c th&agrave;nh vi&ecirc;n trong đo&agrave;n, đặc biệt l&agrave; trong c&ocirc;ng t&aacute;c ứng dụng v&agrave; triển khai th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh Bancassurance, hiện thực h&oacute;a mục ti&ecirc;u &ldquo;dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ tại Việt Nam&rdquo; của VBI.<br /> <br />Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh của đo&agrave;n tại H&agrave;n Quốc</span></p>\r\n<p><img src=\"../../../uploads/IMG_2570 (2).jpg\" alt=\"\" width=\"800\" height=\"569\" /></p>\r\n<p><img src=\"../../../uploads/IMG_2529 (2).jpg\" alt=\"\" width=\"726\" height=\"600\" /></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 07:58:36', '2018-03-21 18:58:36', 'images/news/1520481304_IMG_2570-_635950127997810230_HasThumb_Thumb.jpg.png'),
(162, 2, 'VBI nhận danh hiệu “Thương hiệu mạnh Việt Nam 2015”', 'vbi-nhan-danh-hieu-thuong-hieu-manh-viet-nam-2015', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 2/4/2016 tại H&agrave; Nội, Thời b&aacute;o Kinh tế Việt Nam đ&atilde; tổ chức &ldquo;Li&ecirc;n hoan vinh danh c&aacute;c Doanh nghiệp Rồng V&agrave;ng v&agrave; Thương hiệu mạnh Việt Nam&rdquo;. Năm nay l&agrave; năm thứ 2 li&ecirc;n tiếp VBI được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo;.&nbsp;</span></p>', '<p>S&aacute;ng ng&agrave;y 2/4/2016 tại H&agrave; Nội, Thời b&aacute;o Kinh tế Việt Nam đ&atilde; tổ chức &ldquo;Li&ecirc;n hoan vinh danh c&aacute;c Doanh nghiệp Rồng V&agrave;ng v&agrave; Thương hiệu mạnh Việt Nam&rdquo;. Năm nay l&agrave; năm thứ 2 li&ecirc;n tiếp VBI được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo;.</p>\r\n<p>&ldquo;Li&ecirc;n hoan vinh danh c&aacute;c Doanh nghiệp Rồng V&agrave;ng v&agrave; Thương hiệu mạnh Việt Nam&rdquo; l&agrave; chương tr&igrave;nh do Thời b&aacute;o Kinh tế Việt Nam khởi xướng từ năm 2001. Chương tr&igrave;nh được tổ chức để ghi nhận, động vi&ecirc;n v&agrave; cổ vũ những doanh nghiệp c&oacute; th&agrave;nh t&iacute;ch xuất sắc trong hoạt động sản xuất, kinh doanh, cung ứng dịch vụ; &aacute;p dụng c&aacute;c giải ph&aacute;p, c&ocirc;ng nghệ bảo vệ m&ocirc;i trường; thực hiện đầy đủ c&aacute;c nghĩa vụ nộp thuế cũng như đảm bảo quyền lợi cho người lao động; t&iacute;ch cực tham gia c&aacute;c hoạt động x&atilde; hội, cộng đồng.</p>\r\n<p>VBI vinh dự nằm trong Top 100 doanh nghiệp c&oacute; thương hiệu mạnh nhất Việt Nam do Ban Bi&ecirc;n tập v&agrave; độc giả của Thời B&aacute;o Kinh tế Việt Nam b&igrave;nh chọn.</p>\r\n<p>Năm 2015, VBI đ&atilde; ph&aacute;t triển đột ph&aacute; với doanh thu đạt 549 tỷ đồng, tăng trưởng 77% so với năm 2014; lợi nhuận đạt 68,4 tỷ đồng, tăng trưởng 8% so với năm 2014; tỷ lệ ROA đạt 6,3%, tỷ lệ ROE đạt 9%, tổng quỹ dự ph&ograve;ng nghiệp vụ đạt 234 tỷ đồng, tăng 70% so với năm 2014. Hiện VBI c&oacute; 13 chi nh&aacute;nh v&agrave; 50 ph&ograve;ng giao dịch khu vực tại c&aacute;c địa b&agrave;n trọng yếu tr&ecirc;n to&agrave;n quốc.<br /> <br />Đặc biệt, VBI đ&atilde; vinh dự nhận được c&aacute;c giải thưởng v&agrave; danh hiệu uy t&iacute;n như &ldquo;Sao v&agrave;ng Đất Việt 2015&rdquo;, &ldquo;Top 20 nh&atilde;n hiệu nổi tiếng&rdquo;, &ldquo;Top 1000 Doanh nghiệp nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam&rdquo;. Những danh hiệu v&agrave; giải thưởng tr&ecirc;n như lời khẳng định cho chất lượng v&agrave; dịch vụ vượt trội của VBI.</p>\r\n<p><img src=\"../../../uploads/_MG_6010_fix.jpg\" alt=\"\" width=\"1366\" height=\"929\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VBI nhận C&uacute;p Thương hiệu mạnh Việt Nam 2015.)</em></p>\r\n<p>Với đ&agrave; tăng trưởng mạnh mẽ, dự kiến năm 2016, VBI sẽ nằm trong Top 10 doanh nghiệp bảo hiểm phi nh&acirc;n thọ lớn nhất thị trường Việt Nam v&agrave; nhanh ch&oacute;ng tiến gần mục ti&ecirc;u &ldquo;dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam&rdquo;.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 07:59:07', '2018-03-21 18:59:07', 'images/news/1520482029_MG_6010_f_635956454181364173_HasThumb_Thumb.jpg.png'),
(163, 2, 'VBI tổ chức đào tạo Quản trị rủi ro', 'vbi-to-chuc-dao-tao-quan-tri-rui-ro', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 06/04/2016, VBI phối hợp với VietinBank triển khai chương tr&igrave;nh đ&agrave;o tạo kiến thức v&agrave; chia sẻ kinh nghiệm trong Quản trị rủi ro cho c&aacute;c c&aacute;n bộ quản l&yacute; cấp cao v&agrave; trung tại VBI.&nbsp;</span></p>', '<p>Ng&agrave;y 06/04/2016, VBI phối hợp với VietinBank triển khai chương tr&igrave;nh đ&agrave;o tạo kiến thức v&agrave; chia sẻ kinh nghiệm trong Quản trị rủi ro cho c&aacute;c c&aacute;n bộ quản l&yacute; cấp cao v&agrave; trung tại VBI.<br /> <br />Chương tr&igrave;nh đ&agrave;o tạo tập trung v&agrave;o việc hệ thống h&oacute;a c&aacute;c vấn đề li&ecirc;n quan đến quản trị rủi ro tại doanh nghiệp từ c&aacute;ch thức nhận diện rủi ro, đ&aacute;nh gi&aacute; c&aacute;c rủi ro đến phương ph&aacute;p để x&acirc;y dựng chiến lược xử l&yacute; rủi ro hiệu quả.</p>\r\n<p>Trong khu&ocirc;n khổ kh&oacute;a học, L&atilde;nh đạo Khối Quản trị rủi ro VietinBank v&agrave; c&aacute;c c&aacute;n bộ quản l&yacute; tại VBI đ&atilde; c&ugrave;ng nhau trao đổi, chia sẻ v&agrave; thảo luận dựa tr&ecirc;n cơ sở l&yacute; thuyết c&aacute;c t&igrave;nh huống cụ thể nhằm n&acirc;ng cao năng lực quản l&yacute; rủi ro tại doanh nghiệp.<br />Kh&oacute;a học kết hợp giữa kiến thức l&yacute; thuyết v&agrave; thực tiễn, đi s&acirc;u v&agrave;o ph&acirc;n t&iacute;ch thực tế quản trị rủi ro tại VietinBank v&agrave; VBI. B&ecirc;n cạnh đ&oacute;, giảng vi&ecirc;n hướng dẫn c&aacute;c bước cụ thể, cung cấp t&agrave;i liệu, biểu mẫu để c&aacute;c c&aacute;n bộ c&oacute; thể dễ d&agrave;ng h&igrave;nh dung v&agrave; &aacute;p dụng triển khai quy tr&igrave;nh quản trị rủi ro tại VBI.</p>\r\n<p><img src=\"../../../uploads/AAA.jpg\" alt=\"\" width=\"1366\" height=\"476\" /></p>\r\n<p style=\"text-align: center;\"><em>C&aacute;c c&aacute;n bộ VBI chụp ảnh c&ugrave;ng c&aacute;c giảng vi&ecirc;n VietinBank</em></p>\r\n<p>Trong thời gian tới, hai b&ecirc;n sẽ tiếp tục tổ chức c&aacute;c buổi đ&agrave;o tạo n&acirc;ng cao kỹ năng quản trị rủi ro cho VBI. Trong buổi đ&agrave;o tạo, b&agrave; L&ecirc; Anh H&agrave; &ndash; Ph&oacute; gi&aacute;m đốc Khối Quản trị rủi ro chia sẻ: &ldquo;Chương tr&igrave;nh đ&agrave;o tạo l&agrave; một trong những hoạt động nằm trong đề &aacute;n Quản trị rủi ro của Tập đo&agrave;n VietinBank, hai b&ecirc;n chủ động trao đổi hai chiều theo chức năng v&agrave; nhiệm vụ được Ban L&atilde;nh đạo VietinBank giao ph&oacute;&rdquo;.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:00:01', '2018-03-21 19:00:01', 'images/news/1520482412_A.jpg'),
(164, 2, 'VBI bảo hiểm giải Hold-in-one trị giá 1,5 tỷ đồng', 'vbi-bao-hiem-giai-hold-in-one-tri-gia-1-5-ty-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 10/04/2016, tại s&acirc;n Golf Ho&agrave;ng Gia đ&atilde; diễn ra Giải Golf giao hữu VietinBank v&agrave; kh&aacute;ch h&agrave;ng doanh nghiệp H&agrave;n Quốc do VietinBank tổ chức. C&aacute;c golfer thi đấu tr&ecirc;n s&acirc;n golf 18 lỗ theo thể thức handicap, với cơ cấu giải: Nhất, Nh&igrave;, Ba (theo 3 bảng thi đấu), giải Kỹ thuật v&agrave; giải H...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 10/04/2016, tại s&acirc;n Golf Ho&agrave;ng Gia đ&atilde; diễn ra Giải Golf giao hữu VietinBank v&agrave; kh&aacute;ch h&agrave;ng doanh nghiệp H&agrave;n Quốc do VietinBank tổ chức. C&aacute;c golfer thi đấu tr&ecirc;n s&acirc;n golf 18 lỗ theo thể thức handicap, với cơ cấu giải: Nhất, Nh&igrave;, Ba (theo 3 bảng thi đấu), giải Kỹ thuật v&agrave; giải Hole in one.<br /> <br />VBI tự h&agrave;o l&agrave; nh&agrave; t&agrave;i trợ ch&iacute;nh giải thưởng Hole-in-one cho người chiến thắng với giải thưởng l&agrave; 01 xe &ocirc;t&ocirc; Mercedes c&oacute; gi&aacute; trị l&ecirc;n tới 1,5 tỷ đồng.</span></p>\r\n<p><img src=\"../../../uploads/anh VBI.jpg\" alt=\"\" width=\"1366\" height=\"909\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: TGĐ VBI trao 3 giải thưởng kỹ thuật cho c&aacute;c Golfer xuất sắc)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm h&agrave;ng đầu Việt Nam đứng ra nhận bảo hiểm giải thưởng Hole In One cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:00:41', '2018-03-21 19:00:41', 'images/news/1520482601_anh VBI.jpg'),
(165, 2, 'VBI bảo hiểm 2 giải Hold-in-one trị giá gần 4 tỷ đồng', 'vbi-bao-hiem-2-giai-hold-in-one-tri-gia-gan-4-ty-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 23/04/2016, VBI tham gia bảo hiểm 2 giải thưởng Hole-in-one c&oacute; gi&aacute; trị gần 4 tỷ đồng.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 23/04/2016, tại s&acirc;n Golf Ho&agrave;ng Gia đ&atilde; diễn ra Giải golf giao hữu với c&aacute;c đối t&aacute;c chiến lược do VietinBank tổ chức. C&aacute;c golfer thi đấu tr&ecirc;n s&acirc;n golf 18 lỗ theo thể thức handicap, với cơ cấu giải: Nhất, Nh&igrave;, Ba (theo 3 bảng thi đấu), giải Kỹ thuật v&agrave; giải Hole in one.<br /> <br />Tham gia Giải c&oacute; gần 100 golfer đến từ c&aacute;c tập đo&agrave;n, tổng c&ocirc;ng ty, doanh nghiệp&hellip; l&agrave; c&aacute;c kh&aacute;ch h&agrave;ng chiến lược của VietinBank. Ban L&atilde;nh đạo VietinBank c&oacute; &ocirc;ng Trần Minh B&igrave;nh - Ph&oacute; Tổng Gi&aacute;m đốc tham dự. Tại giải đấu, c&aacute;c golfer đ&atilde; cống hiến cho kh&aacute;n giả nhiều đường b&oacute;ng đẹp, thể hiện kỹ thuật đi&ecirc;u luyện, sự chuy&ecirc;n nghiệp, đẳng cấp.<br /> <br />VBI tham gia bảo hiểm giải thưởng Hole-in-one cho người chiến thắng với giải thưởng l&agrave; 01 xe &ocirc;t&ocirc; Mercedes E250 AMG c&oacute; gi&aacute; trị hơn 2,3 tỷ đồng.</span></p>\r\n<p><img src=\"../../../uploads/giao-huu-golf3.jpg\" alt=\"\" width=\"700\" height=\"372\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: c&aacute;c Golfer tham dự giải đấu)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Cũng trong ng&agrave;y, tại s&acirc;n Golf Geongsan, Đồng Nai, VBI đ&atilde; t&agrave;i trợ cho Giải Hole-in-one Giải golf hữu nghị - Vietinbank Nhơn Trạch. Giải thưởng l&agrave; 01 xe Toyota Camry 2.5Q 2016, trị gi&aacute; hơn 1,4 tỷ đồng.<br /> <br />VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm h&agrave;ng đầu Việt Nam đứng ra nhận bảo hiểm giải thưởng Hole-in-one cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:01:05', '2018-03-21 19:01:05', 'images/news/1520482817_giao-huu-_635972573753295286_HasThumb_Thumb.jpg.png'),
(166, 2, 'VBI tổ chức tặng quà cho 10 hộ nghèo tại Lâm Đồng', 'vbi-to-chuc-tang-qua-cho-10-ho-ngheo-tai-lam-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 2/5/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại L&acirc;m Đồng. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</span></p>', '<p>Ng&agrave;y 2/5/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại L&acirc;m Đồng. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại L&acirc;m Đồng như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/BT.png\" alt=\"\" width=\"351\" height=\"287\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại L&acirc;m Đồng)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:01:56', '2018-03-21 19:01:56', 'images/news/1520483102_pic-New-i_636177426240350110_HasThumb_Thumb.png'),
(167, 2, 'VBI tổ chức tặng quà cho 10 hộ nghèo tại Bình Thuận', 'vbi-to-chuc-tang-qua-cho-10-ho-ngheo-tai-binh-thuan', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 5/5/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại B&igrave;nh Thuận. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe ...&nbsp;</span></p>', '<p>Ng&agrave;y 5/5/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại B&igrave;nh Thuận. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại B&igrave;nh Thuận như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/BTh.jpg\" alt=\"\" width=\"1126\" height=\"634\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại B&igrave;nh Thuận)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:02:20', '2018-03-21 19:02:20', 'images/news/1520495735_pic-New-i_636177427581782115_HasThumb_Thumb.jpg.png'),
(168, 2, 'VBI tài trợ chương trình đào tạo bảo hiểm theo chuẩn quốc tế đầu tiên tại Việt Nam', 'vbi-tai-tro-chuong-trinh-dao-tao-bao-hiem-theo-chuan-quoc-te-dau-tien-tai-viet-nam', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 9/5/2016, tại trường Đại học Kinh tế Quốc d&acirc;n, H&agrave; Nội, đ&atilde; diễn ra &ldquo;Lễ trao chứng nhận đối t&aacute;c đ&agrave;o tạo ủy quyền của ANZIIF cho trường Đại học Kinh tế Quốc d&acirc;n\".&nbsp;</span></p>', '<p>Ng&agrave;y 9/5/2016, tại trường Đại học Kinh tế Quốc d&acirc;n, H&agrave; Nội, đ&atilde; diễn ra &ldquo;Lễ trao chứng nhận đối t&aacute;c đ&agrave;o tạo ủy quyền của ANZIIF cho trường Đại học Kinh tế Quốc d&acirc;n\". Buổi lễ diễn ra long trọng với sự tham dự của GS.TS Trần Thọ Đạt Hiệu trưởng, B&iacute; thư Đảng uỷ v&agrave; c&aacute;c đồng ch&iacute; trong Ban Gi&aacute;m hiệu, Đảng ủy Nh&agrave; trường; &Ocirc;ng Ph&ugrave;ng Đắc Lộc - Tổng Thư K&yacute; Hiệp hội bảo hiểm Việt Nam; L&atilde;nh đạo c&aacute;c doanh nghiệp bảo hiểm, c&aacute;c đơn vị c&oacute; đ&agrave;o tạo về bảo hiểm ở Việt Nam; c&aacute;c cơ quan th&ocirc;ng tấn b&aacute;o ch&iacute; c&ugrave;ng c&aacute;c thầy c&ocirc; gi&aacute;o v&agrave; sinh vi&ecirc;n ng&agrave;nh bảo hiểm. Về ph&iacute;a ANZIIF c&oacute; B&agrave; Prue Willsford - Tổng gi&aacute;m đốc điều h&agrave;nh của ANZIIF, &Ocirc;ng Anton Barnett - Harris - Gi&aacute;m đốc quản l&yacute; gi&aacute;o dục, c&aacute;c sự kiện v&agrave; quốc tế, B&agrave; L&ecirc; Hồng Dương - Gi&aacute;m đốc khu vực Đ&ocirc;ng Nam &Aacute; của ANZIIF.</p>\r\n<p>Sau buổi lễ, trường Đại học Kinh tế Quốc d&acirc;n (NEU) ch&iacute;nh thức trở th&agrave;nh trường Đại học đầu ti&ecirc;n tại Việt Nam tổ chức chương tr&igrave;nh đ&agrave;o tạo bảo hiểm theo chuẩn quốc tế nhằm đ&aacute;p ứng v&agrave; n&acirc;ng cao chất lượng nguồn nh&acirc;n lực cho thị trường Bảo hiểm Việt Nam.</p>\r\n<p><img src=\"../../../uploads/trao uy quyen.jpg\" alt=\"\" width=\"1366\" height=\"797\" /></p>\r\n<p style=\"text-align: center;\"><em>(Đại diện ANZIIF trao chứng nhận đối t&aacute;c ủy quyền cho Trường Đại học Kinh tế Quốc d&acirc;n)</em></p>\r\n<p>Chương tr&igrave;nh đ&agrave;o tạo ANZIIF uỷ quyền cho NEU cung cấp l&agrave; chương tr&igrave;nh DILOPMA về Bảo hiểm phi nh&acirc;n thọ, được đ&agrave;o tạo song ngữ (học vi&ecirc;n được cung cấp t&agrave;i liệu học bằng tiếng Anh v&agrave; Tiếng Việt), thi bằng tiếng Việt. Mỗi m&ocirc;n học, học vi&ecirc;n sẽ được c&aacute;c Hội vi&ecirc;n cao cấp của ANZIIF hướng dẫn v&agrave; giải đ&aacute;p thắc mắc 01 ng&agrave;y tại NEU; đến ng&agrave;y giờ thi theo lịch thi chung của ANZIIF trong to&agrave;n khu vực, học vi&ecirc;n tập trung thi tại NEU. Kết quả thi được ANZIIF đ&aacute;nh gi&aacute; v&agrave; cấp chứng chỉ.</p>\r\n<p>Khi học vi&ecirc;n ho&agrave;n th&agrave;nh 2 m&ocirc;n, được ANZIIF c&ocirc;ng nhận đủ điều kiện trở th&agrave;nh Hội vi&ecirc;n cao cấp của ANZIIF. C&aacute;c học vi&ecirc;n ho&agrave;n th&agrave;nh đủ 04 m&ocirc;n theo quy định sẽ được ANZIIF cấp bằng DIPLOMA về Bảo hiểm phi nh&acirc;n thọ.</p>\r\n<p>Đối tượng tham gia học chương tr&igrave;nh bao gồm: to&agrave;n bộ cử nh&acirc;n bảo hiểm của NEU v&agrave; của c&aacute;c trường đại học, học viện c&oacute; đ&agrave;o tạo về bảo hiểm. Ngo&agrave;i ra, c&aacute;c c&aacute;n bộ l&agrave;m trong ng&agrave;nh bảo hiểm từ 5 năm trở l&ecirc;n (sau khi ho&agrave;n th&agrave;nh 01 b&agrave;i thi trắc nghiệm) cũng l&agrave; đối tượng được tham gia chương tr&igrave;nh đ&agrave;o tạo n&agrave;y.</p>\r\n<p>Ngo&agrave;i ra, ANZIIF cũng c&ocirc;ng nhận chương tr&igrave;nh đ&agrave;o tạo cử nh&acirc;n bảo hiểm của trường ĐH KTQD đạt ti&ecirc;u chuẩn hội vi&ecirc;n Hội nghề nghiệp ANZIIF. C&oacute; nghĩa l&agrave; tất cả c&aacute;c cử nh&acirc;n bảo hiểm của trường ĐH KTQD từ trước đến nay đều được đăng k&yacute; để trở th&agrave;nh Hội vi&ecirc;n hội nghề nghiệp ANZIIF.</p>\r\n<p>Tại buổi lễ, GS.TS Trần Thọ Đạt - Hiệu trưởng trường Đại học Kinh tế Quốc d&acirc;n chia sẻ &ldquo;Với bề d&agrave;y lịch sử 60 năm của Nh&agrave; trường, ch&uacute;ng t&ocirc;i tin rằng, sự uỷ quyền của ANZIIF, sự hợp t&aacute;c v&agrave; hỗ trợ của c&aacute;c cơ quan quản l&yacute;, của Hiệp hội bảo hiểm Việt Nam v&agrave; c&aacute;c doanh nghiệp bảo hiểm, NEU sẽ l&agrave; địa chỉ uy t&iacute;n nhất cung cấp c&aacute;c chương tr&igrave;nh đ&agrave;o tạo theo chuẩn quốc tế về chuy&ecirc;n m&ocirc;n bảo hiểm tại Việt Nam, đ&aacute;p ứng y&ecirc;u cầu n&acirc;ng cao v&agrave; chuẩn ho&aacute; chất lượng nguồn nh&acirc;n lực theo chuẩn quốc tế cho thị trường bảo hiểm Việt Nam&rdquo; .</p>\r\n<p><img src=\"../../../uploads/DSC_0475 (Copy).jpg\" alt=\"\" width=\"1088\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\"><em>(Tổng Gi&aacute;m đốc VBI &ndash; đơn vị t&agrave;i trợ chương tr&igrave;nh ph&aacute;t biểu tại buổi lễ)</em></p>\r\n<p>Cũng tại buổi lễ, đại diện đơn vị t&agrave;i trợ chương tr&igrave;nh đ&agrave;o tạo, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI ph&aacute;t biểu về niềm tin tưởng v&agrave;o chương tr&igrave;nh đ&agrave;o tạo bảo hiểm ANZIIF tại Trường Đại học Kinh tế Quốc d&acirc;n sẽ l&agrave; c&aacute;nh cửa mở ra những cơ hội nghề nghiệp v&agrave; thăng tiến cho c&aacute;c bạn học vi&ecirc;n v&agrave; c&aacute;n bộ ng&agrave;nh bảo hiểm trong thời kỳ hội nhập.</p>\r\n<p><img src=\"../../../uploads/DSC_0486 (Copy).jpg\" alt=\"\" width=\"1174\" height=\"357\" /></p>\r\n<p><strong>Th&ocirc;ng tin chi tiết về chương tr&igrave;nh:</strong><br />Khoa Bảo hiểm, Ph&ograve;ng 102-103 Nh&agrave; 6B, trường Đại học Kinh tế Quốc d&acirc;n, 207 Đường Giải Ph&oacute;ng, Quận Hai B&agrave; Trưng, H&agrave; Nội.<br />Điện thoại: 04 36280280, m&aacute;y lẻ 5672/5681.<br />Hotline: 098 816 2036/ 093 591 8918<br />Website: http://khoabaohiem.neu.edu.vn/ViewThongBao.aspx?ID=574</p>', '2018-03-21 08:03:29', '2018-03-21 19:03:29', 'images/news/1520495875_trao-uy-q_635984072287614041_HasThumb_Thumb.jpg.png'),
(169, 2, 'VBI tổ chức khám bệnh và tặng quà cho 10 hộ nghèo tại Dak Lak', 'vbi-to-chuc-kham-benh-va-tang-qua-cho-10-ho-ngheo-tai-dak-lak', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 10/5/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại Dak Lak. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VB...&nbsp;</span></p>', '<p>Ng&agrave;y 10/5/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh kh&aacute;m bệnh v&agrave; trao qu&agrave; cho 10 hộ ngh&egrave;o tại Dak Lak. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Dak Lak như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/DL.jpg\" alt=\"\" width=\"1366\" height=\"1025\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại Dak Lak)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:04:08', '2018-03-21 19:04:08', 'images/news/1520496035_pic-New-i_636177429370410206_HasThumb_Thumb.jpg.png'),
(170, 2, 'VBI tổ chức tặng quà cho 10 hộ nghèo tại Gia Lai', 'vbi-to-chuc-tang-qua-cho-10-ho-ngheo-tai-gia-lai', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 12/6/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh trao qu&agrave; cho 10 hộ ngh&egrave;o tại Gia Lai. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổ...&nbsp;</span></p>', '<p>Ng&agrave;y 12/6/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh trao qu&agrave; cho 10 hộ ngh&egrave;o tại Gia Lai. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Gia Lai như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/GIA LAI.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại Gia Lai)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:06:02', '2018-03-21 19:06:02', 'images/news/1520496276_pic-New-i_636177411442093258_HasThumb_Thumb.jpg.png'),
(171, 2, 'VBI tổ chức tặng quà cho 10 hộ nghèo tại Thanh Hóa', 'vbi-to-chuc-tang-qua-cho-10-ho-ngheo-tai-thanh-hoa', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 14/6/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh trao qu&agrave; cho 10 hộ ngh&egrave;o tại Thanh H&oacute;a. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với ...&nbsp;</span></p>', '<p>Ng&agrave;y 14/6/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh trao qu&agrave; cho 10 hộ ngh&egrave;o tại Thanh H&oacute;a. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Thanh H&oacute;a như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/TH.jpg\" alt=\"\" width=\"1136\" height=\"852\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại Thanh H&oacute;a)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:06:28', '2018-03-21 19:06:28', 'images/news/1520496391_pic-New-i_636177422649812423_HasThumb_Thumb.jpg.png'),
(172, 2, 'Chương trình tình nguyện “Nồi cháo tình thương” 2016', 'chuong-trinh-tinh-nguyen-noi-chao-tinh-thuong-2016', '<p><span style=\"font-family: Arial;\">Với tinh thần chia sẻ kh&oacute; khăn c&ugrave;ng cộng đồng, mang đến cho c&aacute;c bệnh nh&acirc;n ngh&egrave;o đang điều trị tại Bệnh nhi h&agrave;ng trăm suất ch&aacute;o đủ dinh dưỡng, ấm &aacute;p t&igrave;nh người, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; l&agrave; một hoạt động thường ni&ecirc;n mang &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Trong suốt một tuần qua, c&aacute;c đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI lu&ocirc;n bắt đầu một ng&agrave;y mới đầy &yacute; nghĩa bằng chuyến h&agrave;nh tr&igrave;nh đến thăm c&aacute;c bệnh nh&acirc;n ở Bệnh viện Nhi Trung Ương. Với tinh thần chia sẻ kh&oacute; khăn c&ugrave;ng cộng đồng, mang đến cho c&aacute;c bệnh nh&acirc;n ngh&egrave;o đang điều trị tại Bệnh nhi h&agrave;ng trăm suất ch&aacute;o đủ dinh dưỡng, ấm &aacute;p t&igrave;nh người, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; l&agrave; một hoạt động thường ni&ecirc;n mang &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc. B&ecirc;n cạnh việc ph&aacute;t ch&aacute;o, đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI c&ograve;n thăm hỏi động vi&ecirc;n bệnh nh&acirc;n v&agrave; người nh&agrave; vượt qua kh&oacute; khăn, y&ecirc;n t&acirc;m điều trị.</span></p>\r\n<p><img src=\"../../../uploads/IMG20160608062245.jpg\" alt=\"\" width=\"861\" height=\"1200\" /></p>\r\n<p><img src=\"../../../uploads/4.jpg\" alt=\"\" width=\"1366\" height=\"906\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Niềm hạnh ph&uacute;c của c&aacute;c đo&agrave;n vi&ecirc;n VBI khi được đ&oacute;p g&oacute;p sức trẻ v&agrave;o c&ocirc;ng việc thiện nguyện)</span></em></p>\r\n<p>Từ đầu năm 2016 tới nay, VBI cũng đ&atilde; triển khai chương tr&igrave;nh từ thiện trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;,&hellip;</p>\r\n<p>Tổng Gi&aacute;m đốc VBI - &ocirc;ng L&ecirc; Tuấn Dũng chia sẻ: &ldquo;Phương ch&acirc;m hoạt đ&ocirc;ng của VBI l&agrave; &ldquo;Bảo to&agrave;n gi&aacute; trị cuộc sống&rdquo;. C&ugrave;ng với những cam kết về chất lượng sản phẩm, dịch vụ, hiệu quả hoạt động, ch&uacute;ng t&ocirc;i lu&ocirc;n ch&uacute; trọng v&agrave; sẵn s&agrave;ng trong c&ocirc;ng t&aacute;c chia sẻ, chung tay đ&oacute;ng g&oacute;p v&igrave; cộng đồng. Đ&acirc;y cũng ch&iacute;nh l&agrave; một trong những gi&aacute; trị cốt l&otilde;i m&agrave; VBI theo đuổi v&agrave; cũng l&agrave; niềm tự h&agrave;o của thương hiệu VBI&rdquo;.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:07:37', '2018-03-21 19:07:37', 'images/news/1520496508__636015143927824247_HasThumb_Thumb.jpg.png'),
(173, 2, 'VBI tổ chức tặng quà cho 10 hộ nghèo tại Khánh Hòa', 'vbi-to-chuc-tang-qua-cho-10-ho-ngheo-tai-khanh-hoa', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 20/6/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh trao qu&agrave; cho 10 hộ ngh&egrave;o tại Kh&aacute;nh H&ograve;a. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với ...&nbsp;</span></p>', '<p>Ng&agrave;y 20/6/2016, Bảo hiểm VietinBank (VBI) c&ugrave;ng với Nệm Kim Cương (NKC) đ&atilde; tổ chức chương tr&igrave;nh trao qu&agrave; cho 10 hộ ngh&egrave;o tại Kh&aacute;nh H&ograve;a. Chương tr&igrave;nh nằm trong dự &aacute;n trao tặng qu&agrave; cho 630 hộ ngh&egrave;o tại 63 tỉnh/th&agrave;nh tr&ecirc;n to&agrave;n quốc. Qu&agrave; tặng l&agrave; 630 g&oacute;i bảo hiểm sức khỏe VBI Care với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong chương tr&igrave;nh, VBI v&agrave; NKC đ&atilde; trao c&aacute;c g&oacute;i bảo hiểm sức khỏe VBI Care tới những người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại Kh&aacute;nh H&ograve;a như: Người gi&agrave; neo đơn, trẻ em mồ c&ocirc;i, c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;&hellip; Bảo hiểm sức khỏe VBI Care sẽ hỗ trợ c&aacute;c chi ph&iacute; kh&aacute;m, chữa bệnh khi người được bảo hiểm kh&ocirc;ng may gặp rủi ro, tai nạn, ốm đau bệnh tật với mức quyền lợi bảo hiểm l&ecirc;n tới 30 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/KHANH HOA.jpg\" alt=\"\" width=\"1366\" height=\"1020\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh VBI trao bảo hiểm sức khỏe cho người ngh&egrave;o tại Kh&aacute;nh H&ograve;a)</em></p>\r\n<p>Đặc biệt, tại H&agrave; Nội, TP. HCM, L&agrave;o Cai, Hải Ph&ograve;ng, Nghệ An, Đ&agrave; Nẵng, Bu&ocirc;n M&ecirc; Thuột, Cần Thơ, C&agrave; Mau, VBI v&agrave; NKC c&ograve;n tổ chức Chương tr&igrave;nh &ldquo;Kh&aacute;m bệnh miễn ph&iacute; cho người ngh&egrave;o&rdquo;. Tại c&aacute;c tỉnh tr&ecirc;n, c&ugrave;ng với việc trao qu&agrave; tặng l&agrave; bảo hiểm VBI Care, VBI v&agrave; NKC c&ograve;n bố tr&iacute; g&oacute;i kh&aacute;m sức khỏe tổng qu&aacute;t cho 10 người ngh&egrave;o.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện tặng qu&agrave; cho người ngh&egrave;o, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:08:06', '2018-03-21 19:08:06', 'images/news/1520496618_pic-New-i_636177435209741069_HasThumb_Thumb.JPG.png'),
(174, 2, 'VBI chi trả 300 triệu đồng tiền bồi thường bảo hiểm Con người VietinCare tại Gia Lai', 'vbi-chi-tra-300-trieu-dong-tien-boi-thuong-bao-hiem-con-nguoi-vietincare-tai-gia-lai', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 12/07/2016, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 300 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh anh Nguyễn Văn Th&agrave;nh, tr&uacute; tại phường An Ph&uacute;, thị x&atilde; An Kh&ecirc;, tỉnh Gia Lai.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 12/07/2016, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 300 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh anh Nguyễn Văn Th&agrave;nh, tr&uacute; tại phường An Ph&uacute;, thị x&atilde; An Kh&ecirc;, tỉnh Gia Lai.</span></p>\r\n<p><img src=\"../../../uploads/IMG_9254.png\" alt=\"\" width=\"700\" height=\"523\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p>Th&aacute;ng 7 năm 2015, anh Nguyễn Văn Th&agrave;nh được VietinBank CN Gia Lai cho vay 300 triệu đồng, đồng thời tham gia bảo hiểm con người VietinCare của VBI. Th&aacute;ng 12 năm 2015, anh Khoa gặp tai nạn rồi kh&ocirc;ng may qua đời.</p>\r\n<p>Sau khi nhận được th&ocirc;ng b&aacute;o từ gia đ&igrave;nh, VBI CN T&acirc;y Nguy&ecirc;n đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Gia Lai hướng dẫn gia đ&igrave;nh anh Khoa ho&agrave;n thiện hồ sơ nhận tiền bảo hiểm theo quy định. Ngay khi nhận được hồ sơ ho&agrave;n thiện, VBI đ&atilde; thanh to&aacute;n 300 triệu anh Th&agrave;nh c&ograve;n nợ cho Ng&acirc;n h&agrave;ng VietinBank Gia Lai, gi&uacute;p gia đ&igrave;nh anh giảm bớt g&aacute;nh nặng trả nợ.</p>\r\n<p>Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 6 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng v&agrave; chất lượng dịch vụ vượt trội, VBI hiện nằm Top 10 c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ uy t&iacute;n năm 2016 do C&ocirc;ng ty cổ phần B&aacute;o c&aacute;o đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report) c&ocirc;ng bố.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:08:37', '2018-03-21 19:08:37', 'images/news/1520496714_IMG_925_636039368662639012_HasThumb_Thumb.PNG'),
(175, 2, 'VBI chi trả 200 triệu đồng tiền bồi thường bảo hiểm sức khỏe VBI Care tại Thành phố Hồ Chí Minh', 'vbi-chi-tra-200-trieu-dong-tien-boi-thuong-bao-hiem-suc-khoe-vbi-care-tai-thanh-pho-ho-chi-minh', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 14/07/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Hồ Ch&iacute; Minh phối hợp với VietinBank CN Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 200 triệu đồng tiền bồi thường bảo hiểm sức khỏe cho gia đ&igrave;nh chị Lữ Thị Anh Th&uacute;y, tr&uacute; tại phường 10, quận G&ograve; Vấp, th&agrave;nh phố Hồ Ch&iacute; Minh.&nbsp;</span></p>', '<p>Ng&agrave;y 14/07/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Hồ Ch&iacute; Minh phối hợp với VietinBank CN Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 200 triệu đồng tiền bồi thường bảo hiểm sức khỏe cho gia đ&igrave;nh chị Lữ Thị Anh Th&uacute;y, tr&uacute; tại phường 10, quận G&ograve; Vấp, th&agrave;nh phố Hồ Ch&iacute; Minh.</p>\r\n<p>Năm 2014, chị Lữ Thị Anh Th&uacute;y mua bảo hiểm bảo hiểm sức khỏe VBI Care. Tới th&aacute;ng 12 năm 2015, chị Th&uacute;y l&acirc;m bệnh rồi kh&ocirc;ng may qua đời. Sau khi nhận được th&ocirc;ng b&aacute;o từ gia đ&igrave;nh, VBI Chi nh&aacute;nh Hồ Ch&iacute; Minh đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Th&agrave;nh phố Hồ Ch&iacute; Minh hướng dẫn gia đ&igrave;nh chị Th&uacute;y ho&agrave;n thiện hồ sơ nhận tiền bảo hiểm theo quy định.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 10 tỷ đồng bảo hiểm sức khỏe VBI Care cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng v&agrave; dịch vụ kh&aacute;ch h&agrave;ng vượt trội, VBI hiện nằm trong Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam do Vietnam Report c&ocirc;ng bố.</p>\r\n<p><img src=\"../../../uploads/qua tang bhsk kh uu tien.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Từ nay tới 31/12/2016, VBI triển khai chương tr&igrave;nh ưu đ&atilde;i d&agrave;nh ri&ecirc;ng cho kh&aacute;ch h&agrave;ng ưu ti&ecirc;n khi tham gia sản phẩm bảo hiểm Sức khỏe VBI Care.<br /> <br />Khi mua Bảo hiểm sức khoẻ VBI Care, c&aacute;c kh&aacute;ch h&agrave;ng ưu ti&ecirc;n tại H&agrave; Nội, Hồ Ch&iacute; Minh, B&igrave;nh Phước, B&igrave;nh Dương, T&acirc;y Ninh, Đồng Nai v&agrave; B&agrave; Rịa Vũng T&agrave;u sẽ nhận được h&agrave;ng ngh&igrave;n qu&agrave; tặng gi&aacute; trị từ VBI.</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><strong>Mức ph&iacute; bảo hiểm VBI Care</strong></p>\r\n<p>Từ 3 - 5 triệu đồng ph&iacute; bảo hiểm</p>\r\n<p>Tr&ecirc;n 5 triệu đồng ph&iacute; bảo hiểm</p>\r\n</td>\r\n<td>\r\n<p><strong>Qu&agrave; tặng</strong></p>\r\n<p>Tặng 01 nhiệt kế điện tử trị gi&aacute; 200.000 VND</p>\r\n<p>Tặng 01 m&aacute;y đo huyết &aacute;p trị gi&aacute; 1.000.000 VND</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, kh&aacute;ch h&agrave;ng c&oacute; thể tới ph&ograve;ng giao dịch/chi nh&aacute;nh VietinBank gần nhất, hoặc vui l&ograve;ng li&ecirc;n hệ Contact Center: 1900 558 868, email: <a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:09:47', '2018-03-21 19:09:47', 'images/news/1520496882_VBICARE-Q_636040928210046480_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(176, 2, 'Bảo hiểm VietinBank (VBI) nhận danh hiệu Top 10 công ty bảo hiểm uy tín nhất Việt Nam 2016', 'bao-hiem-vietinbank-vbi-nhan-danh-hieu-top-10-cong-ty-bao-hiem-uy-tin-nhat-viet-nam-2016', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 27/07/2016, trong Hội nghị thường ni&ecirc;n Vietnam CEO Summit 2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam năm 2016.&nbsp;</span></p>', '<p>Ng&agrave;y 27/07/2016, trong Hội nghị thường ni&ecirc;n Vietnam CEO Summit 2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam năm 2016. Đ&acirc;y l&agrave; kết quả nghi&ecirc;n cứu độc lập vừa được CTCP B&aacute;o c&aacute;o Đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report) phối hợp với B&aacute;o điện tử VietnamNet c&ocirc;ng bố dựa tr&ecirc;n 3 ti&ecirc;u ch&iacute; ch&iacute;nh l&agrave; năng lực t&agrave;i ch&iacute;nh, uy t&iacute;n truyền th&ocirc;ng v&agrave; mức độ h&agrave;i l&ograve;ng của người d&ugrave;ng đối với sản phẩm, dịch vụ bảo hiểm của c&aacute;c doanh nghiệp.</p>\r\n<p><br />Trong 6 th&aacute;ng đầu năm, VBI đ&atilde; được Vietnam Report vinh danh trong Top 1000 doanh nghiệp nộp thuế lớn nhất Việt Nam v&agrave; lần thứ 2 li&ecirc;n tiếp nằm trong Top 100 Thương hiệu mạnh Việt Nam do Thời b&aacute;o Kinh tế Việt Nam c&ocirc;ng bố.</p>\r\n<p><img src=\"../../../uploads/IMG_0824.jpg\" alt=\"\" width=\"1366\" height=\"790\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Đại diện VBI nhận danh hiệu TOP 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n 2016)</em></p>\r\n<p>Kết quả hoạt động 6 th&aacute;ng đầu năm 2016 của VBI cũng cho thấy những bước ph&aacute;t triển đột ph&aacute;, ghi dấu bằng kết quả hoạt động kinh doanh hiệu quả. Doanh thu ph&iacute; bảo hiểm gốc trong 6 th&aacute;ng đầu năm của VBI đạt 320 tỷ đồng, tăng trưởng 50,3% so với 6 th&aacute;ng đầu năm 2015. Doanh thu nhận t&aacute;i trong 6 th&aacute;ng đầu năm của VBI đạt 42,9 tỷ đồng, tăng trưởng 37% so với 6 th&aacute;ng đầu năm 2015. Lợi nhuận lũy kế 6 th&aacute;ng của VBI đạt 34 tỷ đồng. Năm 2016, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI tr&ecirc;n 800 tỷ đồng, tăng trưởng 50% so với 2015.</p>\r\n<p>Để đạt được kết quả kinh doanh ấn tượng tr&ecirc;n, ngay từ những th&aacute;ng đầu năm 2016, VBI đ&atilde; triển khai h&agrave;ng loạt c&aacute;c chương tr&igrave;nh ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng như: &ldquo;Chương tr&igrave;nh tặng qu&agrave; cho kh&aacute;ch h&agrave;ng tham gia bảo hiểm sức khỏe&rdquo;, &ldquo;Triệu lời tri &acirc;n&rdquo;, &ldquo;Ưu đ&atilde;i 25% ph&iacute; bảo hiểm nh&agrave; dự &aacute;n&rdquo;, &ldquo;Ưu đ&atilde;i 30% ph&iacute; bảo hiểm du học v&agrave; du lịch quốc tế&rdquo;, v.v&hellip;</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, những hoạt động hỗ trợ cộng đồng cũng được VBI đẩy mạnh triển khai trong 6 th&aacute;ng đầu năm như chương tr&igrave;nh kh&aacute;m chữa bệnh v&agrave; tặng bảo hiểm sức khỏe cho cộng đồng người ngh&egrave;o tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p>Trong năm 2016, nhằm n&acirc;ng cao năng lực t&agrave;i ch&iacute;nh, hiệu quả sử dụng vốn v&agrave; t&agrave;i sản, VBI sẽ chuyển đổi m&ocirc; h&igrave;nh tổ chức, mở rộng mạng lưới hoạt động th&ecirc;m 5 chi nh&aacute;nh mới, &aacute;p dụng c&ocirc;ng nghệ hiện đại v&agrave;o việc quản l&yacute;, triển khai chiến lược ph&aacute;t triển thương hiệu VBI. Những định hướng hoạt động tr&ecirc;n sẽ gi&uacute;p VBI đẩy nhanh tiến tr&igrave;nh cổ phần h&oacute;a, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:10:22', '2018-03-21 19:10:22', 'images/news/1520497000_IMG_0824.JPG'),
(177, 2, 'VBI chi trả 150 triệu đồng tiền bồi thường bảo hiểm Con người VietinCare tại Gia Lai', 'vbi-chi-tra-150-trieu-dong-tien-boi-thuong-bao-hiem-con-nguoi-vietincare-tai-gia-lai', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/07/2016, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 150 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh anh Nguyễn Duy Tuyến, tr&uacute; tại th&ocirc;n N&ocirc;ng Trường, x&atilde; Ia Glai, huyện Chư S&ecirc;, tỉnh Gia Lai.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/07/2016, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Gia Lai đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; trao 150 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh anh Nguyễn Duy Tuyến, tr&uacute; tại th&ocirc;n N&ocirc;ng Trường, x&atilde; Ia Glai, huyện Chư S&ecirc;, tỉnh Gia Lai.</span></p>\r\n<p><img src=\"../../../uploads/Boi thuong 29_07.jpg\" alt=\"\" width=\"915\" height=\"685\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p>Th&aacute;ng 2 năm 2015, anh Nguyễn Duy Tuyến được VietinBank CN Gia Lai cho vay 150 triệu đồng, đồng thời tham gia bảo hiểm con người VietinCare của VBI. Th&aacute;ng 2 năm 2016, anh Tuyến gặp tai nạn rồi kh&ocirc;ng may qua đời.</p>\r\n<p>Ngay sau khi nhận được th&ocirc;ng b&aacute;o từ gia đ&igrave;nh, VBI CN T&acirc;y Nguy&ecirc;n đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Gia Lai hướng dẫn gia đ&igrave;nh anh Tuyến ho&agrave;n thiện hồ sơ nhận tiền bảo hiểm theo quy định. Ngay khi nhận được hồ sơ ho&agrave;n thiện, VBI đ&atilde; thanh to&aacute;n 150 triệu anh Tuyến c&ograve;n nợ cho Ng&acirc;n h&agrave;ng VietinBank Gia Lai, gi&uacute;p gia đ&igrave;nh anh giảm bớt g&aacute;nh nặng trả nợ.</p>\r\n<p>Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 6 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng v&agrave; chất lượng dịch vụ vượt trội, VBI đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ uy t&iacute;n năm 2016 do Vietnam Report c&ocirc;ng bố.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:10:53', '2018-03-21 19:10:53', 'images/news/1520497121_Boi-thuon_636053820557865110_HasThumb_Thumb.jpg.png'),
(178, 2, 'VBI lần đầu tiên phát hành Giấy chứng nhận bảo hiểm Điện tử và Hóa đơn Điện tử', 'vbi-lan-dau-tien-phat-hanh-giay-chung-nhan-bao-hiem-dien-tu-va-hoa-don-dien-tu', '<p><span style=\"font-family: Arial;\">Bắt đầu từ ng&agrave;y 15/08/2016, VBI lần đầu ti&ecirc;n ph&aacute;t h&agrave;nh &ldquo;Giấy chứng nhận bảo hiểm điện tử v&agrave; H&oacute;a đơn điện tử&rdquo; với mong muốn mang đến cho Qu&yacute; kh&aacute;ch h&agrave;ng những trải nghiệm mới bằng c&ocirc;ng nghệ hiện đại v&agrave; chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.&nbsp;</span></p>', '<p>Bắt đầu từ ng&agrave;y 15/08/2016, VBI lần đầu ti&ecirc;n ph&aacute;t h&agrave;nh &ldquo;Giấy chứng nhận bảo hiểm điện tử v&agrave; H&oacute;a đơn điện tử&rdquo; với mong muốn mang đến cho Qu&yacute; kh&aacute;ch h&agrave;ng những trải nghiệm mới bằng c&ocirc;ng nghệ hiện đại v&agrave; chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.<br /> <br />Từ nay, Qu&yacute; kh&aacute;ch h&agrave;ng mua mới bảo hiểm VBI, c&oacute; đăng k&yacute; email v&agrave; số điện thoại, sẽ nhận ngay Giấy chứng nhận bảo hiểm c&oacute; chữ k&yacute; số của VBI qua Email v&agrave; SMS. Giấy chứng nhận được lưu trữ tại website tra cứ quyền lợi trực tuyến http://tracuu.baohiem-vbi.vn.</p>\r\n<p>Giấy chứng nhận bảo hiểm điện tử c&oacute; nhiều ưu điểm nổi trội, gi&uacute;p Kh&aacute;ch h&agrave;ng tiếp cận v&agrave; c&aacute;c quyền lợi bảo hiểm nhanh ch&oacute;ng hơn, an to&agrave;n hơn v&agrave; dễ d&agrave;ng hơn, theo h&igrave;nh thức hiện đại nhất tr&ecirc;n thị trường bảo hiểm.<br /> <br />Thời gian &aacute;p dụng việc cấp giấy chứng nhận điện tử c&aacute;c sản phẩm chi tiết như sau:</p>\r\n<ul>\r\n<li>Từ 15/08/2016: ph&aacute;t h&agrave;nh GCN điện tử bảo hiểm người vay vốn</li>\r\n<li>Từ 15/09/2016: ph&aacute;t h&agrave;nh GCN điện tử bảo hiểm sức khỏe, bảo hiểm &ocirc; t&ocirc;, bảo hiểm nh&agrave;, bảo hiểm h&agrave;ng h&oacute;a, bảo hiểm du lịch quốc tế.</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/banner-2-GCN.jpg\" alt=\"\" width=\"960\" height=\"303\" /></p>\r\n<p>B&ecirc;n cạnh việc ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử, VBI cũng lần đầu ph&aacute;t h&agrave;nh H&oacute;a đơn Điện tử cho c&aacute;c giao dịch thu ph&iacute; bảo hiểm tr&ecirc;n to&agrave;n quốc. Mẫu H&oacute;a đơn điện tử của VBI đ&atilde; được sự chấp thuận của Bộ T&agrave;i ch&iacute;nh v&agrave; c&oacute; gi&aacute; trị ph&aacute;p l&yacute; tương đương với H&oacute;a đơn gi&aacute; trị gia tăng in tr&ecirc;n giấy trước đ&acirc;y.<br /> <br />Qu&yacute; kh&aacute;ch sẽ nhận được h&oacute;a đơn điện tử qua email đ&atilde; đăng k&yacute; với VBI hoặc truy vấn dễ d&agrave;ng qua website tra cứu trực tuyến: http://tracuu.baohiem-vbi.vn<br /> <br />Việc sử dụng h&oacute;a đơn điện tử sẽ kh&ocirc;ng chỉ mang lại cho Qu&yacute; kh&aacute;ch h&agrave;ng sự thuận tiện v&agrave; nhanh ch&oacute;ng trong giao dịch, m&agrave; c&ograve;n đ&aacute;p ứng y&ecirc;u cầu bảo mật th&ocirc;ng tin, lưu trữ cũng như tra cứu h&oacute;a đơn của Qu&yacute; kh&aacute;ch được dễ d&agrave;ng hơn.</p>\r\n<p>Hiện nay, VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh giấy chứng nhận bảo hiểm điện tử v&agrave; h&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng.<br /> <br />Mọi thắc mắc li&ecirc;n quan đến Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ Hotline: 1900 55 8868 để được hỗ trợ.</p>\r\n<p>&nbsp;</p>', '2018-03-21 08:11:36', '2018-03-21 19:11:36', 'images/news/1520497210_thiet-ke-_636069385133991594_HasThumb_Thumb.jpg.png'),
(179, 2, 'VBI chi trả 800 triệu đồng tiền bồi thường bảo hiểm Con người VietinCare tại Đắk Nông', 'vbi-chi-tra-800-trieu-dong-tien-boi-thuong-bao-hiem-con-nguoi-vietincare-tai-dak-nong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 17/08/2016, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Đắk N&ocirc;ng đ&atilde; tổ chức chi trả 800 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh của 2 kh&aacute;ch h&agrave;ng l&agrave; &ocirc;ng Nguyễn Thanh Xu&acirc;n ở x&atilde; N&acirc;m N&rsquo;Jang (Đắk Song) v&agrave; &ocirc;ng Nguyễn Văn Hương ở x&atilde; Đức Mạ...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 17/08/2016, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n phối hợp với VietinBank CN Đắk N&ocirc;ng đ&atilde; tổ chức chi trả 800 triệu đồng tiền bồi thường bảo hiểm con người cho gia đ&igrave;nh của 2 kh&aacute;ch h&agrave;ng l&agrave; &ocirc;ng Nguyễn Thanh Xu&acirc;n ở x&atilde; N&acirc;m N&rsquo;Jang (Đắk Song) v&agrave; &ocirc;ng Nguyễn Văn Hương ở x&atilde; Đức Mạnh (Đắk Mil).<br /> <br />Theo đ&oacute;, trong năm 2015, gia đ&igrave;nh &ocirc;ng Xu&acirc;n v&agrave; &ocirc;ng Hương đ&atilde; được VietinBank Đắk N&ocirc;ng lần lượt giải quyết cho vay 200 triệu đồng v&agrave; 600 triệu đồng để đầu tư sản xuất, đồng thời, tham gia dịch vụ bảo hiểm t&iacute;n dụng d&agrave;nh cho người vay vốn. Tuy nhi&ecirc;n, do gặp rủi ro trong qu&aacute; tr&igrave;nh tham gia giao th&ocirc;ng n&ecirc;n hai &ocirc;ng đ&atilde; qua đời. Ngay sau khi nhận được th&ocirc;ng tin n&agrave;y, Bảo hiểm VietinBank CN T&acirc;y Nguy&ecirc;n đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank Đắk N&ocirc;ng hướng dẫn gia đ&igrave;nh ho&agrave;n thiện hồ sơ để được chi trả bồi thường bảo hiểm t&iacute;n dụng theo đ&uacute;ng quy định.</span></p>\r\n<p><img src=\"../../../uploads/trao boi thuong Dak Nong.jpg\" alt=\"\" width=\"1206\" height=\"797\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh của 2 kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p>Bảo hiểm con người VietinCare l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.</p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 6 tỷ đồng bảo hiểm con người VietinCare cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng v&agrave; chất lượng dịch vụ vượt trội, VBI đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ uy t&iacute;n năm 2016 do Vietnam Report c&ocirc;ng bố.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:13:11', '2018-03-21 19:13:11', 'images/news/1520497315_trao-boi-_636071093388449338_HasThumb_Thumb.jpg.png'),
(180, 2, 'VBI bổ nhiệm Giám đốc Chi nhánh Nghệ An', 'vbi-bo-nhiem-giam-doc-chi-nhanh-nghe-an', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 22/08/2016 tại Nghệ An, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ trao quyết định bổ nhiệm Gi&aacute;m đốc Chi nh&aacute;nh Nghệ An cho &ocirc;ng L&ecirc; Trung H&agrave;. Tham dự lễ trao quyết định c&oacute; đại diện Ban L&atilde;nh đạo VBI c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh Nghệ An.</span></p>', '<p>Ng&agrave;y 22/08/2016 tại Nghệ An, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ trao quyết định bổ nhiệm Gi&aacute;m đốc Chi nh&aacute;nh Nghệ An cho &ocirc;ng L&ecirc; Trung H&agrave;. Tham dự lễ trao quyết định c&oacute; đại diện Ban L&atilde;nh đạo VBI c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh Nghệ An.</p>\r\n<p>Tại buổi lễ, Tổng gi&aacute;m đốc VBI - &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm v&agrave; ch&uacute;c mừng &ocirc;ng L&ecirc; Trung H&agrave; nhận nhiệm vụ mới. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ mong muốn với 15 năm kinh nghiệm trong lĩnh vực bảo hiểm, &ocirc;ng L&ecirc; Trung H&agrave; sẽ c&ugrave;ng với VBI Nghệ An ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh. Trước khi gia nhập VBI, &ocirc;ng L&ecirc; Trung H&agrave; đ&atilde; đảm nhiệm nhiều vị tr&iacute; quản l&yacute; như: Ph&oacute; Gi&aacute;m đốc C&ocirc;ng ty Bảo hiểm BIDV Bắc Trung Bộ, Trưởng ph&ograve;ng kinh doanh H&agrave; Tĩnh - C&ocirc;ng ty Bảo hiểm BIDV Bắc Trung Bộ.</p>\r\n<p><img src=\"../../../uploads/GDCN Nghe An 2.jpg\" alt=\"\" width=\"1180\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><em>(H&igrave;nh ảnh tại Lễ trao quyết định bổ nhiệm Gi&aacute;m đốc Chi nh&aacute;nh Nghệ An)</em></p>\r\n<p>Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng L&ecirc; Trung H&agrave; thể hiện quyết t&acirc;m phấn đấu để VBI Nghệ An ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đồng thời cam kết sẽ tiếp tục cống hiến, đưa VBI Nghệ An ph&aacute;t triển mạnh mẽ.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:13:35', '2018-03-21 19:13:35', 'images/news/1520497404_GDCN-Nghe_636074713803785682_HasThumb_Thumb.JPG.png'),
(181, 2, 'VBI lần thứ 3 liên tiếp được Tổng cục Thuế tuyên dương', 'vbi-lan-thu-3-lien-tiep-duoc-tong-cuc-thue-tuyen-duong', '<p><span style=\"font-family: Arial;\">Tại Hội nghị tuy&ecirc;n dương người nộp thuế thực hiện tốt ch&iacute;nh s&aacute;ch ph&aacute;p luật thuế 2015 của Tổng cục Thuế ng&agrave;y 25/8/2016, Bảo hiểm VietinBank (VBI) được vinh danh &ldquo;Đơn vị nộp thuế thực hiện tốt ch&iacute;nh s&aacute;ch, ph&aacute;p luật thuế năm 2015&rdquo;.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Tại Hội nghị tuy&ecirc;n dương người nộp thuế thực hiện tốt ch&iacute;nh s&aacute;ch ph&aacute;p luật thuế 2015 của Tổng cục Thuế ng&agrave;y 25/8/2016, Bảo hiểm VietinBank (VBI) được vinh danh &ldquo;Đơn vị nộp thuế thực hiện tốt ch&iacute;nh s&aacute;ch, ph&aacute;p luật thuế năm 2015&rdquo;.</span></p>\r\n<p><img src=\"../../../uploads/IMG_6240.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Đại diện VBI (b&ecirc;n phải) nhận giấy khen của Tổng cục Thuế)</span></em></p>\r\n<p>Đ&acirc;y l&agrave; năm thứ 3 li&ecirc;n tiếp (2013, 2014, 2015) VBI vinh dự được Tổng cục Thuế trao Giấy khen &ldquo;Đơn vị nộp thuế thực hiện tốt ch&iacute;nh s&aacute;ch, ph&aacute;p luật thuế&rdquo;.</p>\r\n<p>Trong năm 2015, VBI đạt doanh thu 549 tỷ đồng, lợi nhuận trước thuế đạt 68 tỷ đồng, đ&oacute;ng g&oacute;p v&agrave;o Ng&acirc;n s&aacute;ch Nh&agrave; nước 35,8 tỷ đồng.</p>\r\n<p>Với VBI, thực hiện nghi&ecirc;m t&uacute;c nghĩa vụ nộp thuế, t&iacute;ch cực ho&agrave;n th&agrave;nh c&aacute;c nghĩa vụ đối với Nh&agrave; nước l&agrave; phương thức thể hiện tr&aacute;ch nhiệm đối với x&atilde; hội v&agrave; văn h&oacute;a doanh nghiệp.</p>\r\n<p>Ban L&atilde;nh đạo VBI lu&ocirc;n x&aacute;c định, ch&acirc;́p h&agrave;nh nghi&ecirc;m chỉnh Luật Thuế ch&iacute;nh l&agrave; nền tảng tạo dựng uy t&iacute;n cho thương hiệu VBI - Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n Việt Nam 2016.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:14:00', '2018-03-21 19:14:00', 'images/news/1520497513_IMG_624_636077400123604996_HasThumb_Thumb.JPG.png'),
(182, 2, 'VBI thông báo lịch nghỉ Lễ Quốc khánh 2/9', 'vbi-thong-bao-lich-nghi-le-quoc-khanh-2-9', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) sẽ nghỉ Lễ Quốc kh&aacute;nh ng&agrave;y 02 th&aacute;ng 09 năm 2016. Ng&agrave;y 05 th&aacute;ng 09 năm 2016, VBI sẽ trở lại hoạt động b&igrave;nh thường.&nbsp;</span></p>', '<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch h&agrave;ng,</strong></p>\r\n<p>Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.</p>\r\n<p>Bảo hiểm VietinBank (VBI) sẽ nghỉ Lễ Quốc kh&aacute;nh ng&agrave;y 02 th&aacute;ng 09 năm 2016. Ng&agrave;y 05 th&aacute;ng 09 năm 2016, VBI sẽ trở lại hoạt động b&igrave;nh thường.</p>\r\n<p>VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng một kỳ nghỉ tr&agrave;n ngập niềm vui v&agrave; hạnh ph&uacute;c.</p>\r\n<p><strong>Tr&acirc;n trọng cảm ơn!</strong></p>\r\n<p><img src=\"../../../uploads/Quoc khanh.jpg\" alt=\"\" width=\"727\" height=\"184\" /></p>', '2018-03-21 08:14:24', '2018-03-21 19:14:24', 'images/news/1520497639_Quoc-khan_636083377675352352_HasThumb_Thumb.jpg.png'),
(183, 2, 'VBI - Doanh nghiệp tiêu biểu vì quyền lợi người tiêu dùng 2016', 'vbi-doanh-nghiep-tieu-bieu-vi-quyen-loi-nguoi-tieu-dung-2016', '<p><span style=\"font-family: Arial;\">S&aacute;ng 04/09/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; vinh dự nhận danh hiệu &ldquo;Doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng 2016&rdquo; do Trung t&acirc;m bảo vệ người ti&ecirc;u d&ugrave;ng trao tặng, dưới sự bảo trợ của Ủy Ban Kinh tế Quốc hội.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng 04/09/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; vinh dự nhận danh hiệu &ldquo;Doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng 2016&rdquo; do Trung t&acirc;m bảo vệ người ti&ecirc;u d&ugrave;ng trao tặng, dưới sự bảo trợ của Ủy Ban Kinh tế Quốc hội.</span></p>\r\n<p><img src=\"../../../uploads/IMG_6305-.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Ảnh: &Ocirc;ng Nguyễn Hồng Phong - Ph&oacute; Tổng Gi&aacute;m đốc VBI vinh dự l&ecirc;n nhận giải thưởng</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Giải thưởng &ldquo;Doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng 2016&rdquo; nhằm t&ocirc;n vinh c&aacute;c doanh nghiệp c&oacute; th&agrave;nh t&iacute;ch v&agrave; c&aacute;c sản phẩm chất lượng ti&ecirc;u biểu, đ&aacute;p ứng được những ti&ecirc;u ch&iacute; của chương tr&igrave;nh. Giải thưởng được tổ chức nhằm th&uacute;c đẩy c&ocirc;ng t&aacute;c bảo vệ quyền lợi người ti&ecirc;u d&ugrave;ng, đồng thời hy vọng sẽ l&agrave;m cho mỗi người d&acirc;n cũng l&agrave; người ti&ecirc;u d&ugrave;ng hiểu v&agrave; bảo vệ được quyền lợi ch&iacute;nh đ&aacute;ng, hợp ph&aacute;p của m&igrave;nh; đồng thời n&acirc;ng cao nhận thức của mọi người d&acirc;n v&agrave; cộng đồng doanh nghiệp&rdquo;.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>\r\n<p>&nbsp;</p>', '2018-03-21 08:15:00', '2018-03-21 19:15:00', 'images/news/1520497750_IMG_6305_636086618307990884_HasThumb_Thumb.jpg.png'),
(184, 2, 'VBI chính thức phát hành Giấy chứng nhận bảo hiểm Điện tử và Hóa đơn Điện tử', 'vbi-chinh-thuc-phat-hanh-giay-chung-nhan-bao-hiem-dien-tu-va-hoa-don-dien-tu', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 15/09/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) tổ chức Lễ ra mắt &ldquo;Giấy chứng nhận bảo hiểm điện tử v&agrave; H&oacute;a đơn điện tử&rdquo;. Hiện nay, Bảo hiểm VietinBank (VBI) l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng.&nbsp;</span></p>', '<p>&nbsp;</p>\r\n<p>Ng&agrave;y 15/09/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) tổ chức Lễ ra mắt &ldquo;Giấy chứng nhận bảo hiểm điện tử v&agrave; H&oacute;a đơn điện tử&rdquo;. Hiện nay, Bảo hiểm VietinBank (VBI) l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng.<br /> <br />Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI ph&aacute;t biểu:&ldquo;Từ nay, khi kh&aacute;ch h&agrave;ng mua mới bảo hiểm VBI, c&oacute; đăng k&yacute; email v&agrave; số điện thoại, sẽ nhận ngay Giấy chứng nhận bảo hiểm c&oacute; chữ k&yacute; số của VBI qua Email v&agrave; SMS. Giấy chứng nhận được lưu trữ tại website tra cứ quyền lợi trực tuyến http://tracuu.baohiem-vbi.vn. Giấy chứng nhận điện tử c&oacute; nhiều ưu điểm nổi trội, gi&uacute;p kh&aacute;ch h&agrave;ng tiếp cận v&agrave; c&aacute;c quyền lợi bảo hiểm nhanh ch&oacute;ng hơn, an to&agrave;n hơn v&agrave; dễ d&agrave;ng hơn, theo h&igrave;nh thức hiện đại nhất tr&ecirc;n thị trường bảo hiểm.&rdquo;</p>\r\n<p>Giấy chứng nhận điện tử sẽ được cấp cho c&aacute;c sản phẩm chi tiết như sau:</p>\r\n<ul>\r\n<li>Bảo hiểm &ocirc; t&ocirc;</li>\r\n<li>Bảo hiểm nh&agrave;</li>\r\n<li>Bảo hiểm sức khỏe</li>\r\n<li>Bảo hiểm h&agrave;ng h&oacute;a</li>\r\n<li>Bảo hiểm người vay vốn</li>\r\n<li>Bảo hiểm du lịch quốc tế.</li>\r\n</ul>', '2018-03-08 20:30:30', '2018-03-08 20:30:30', 'images/news/1520497830_TGD-VBI-p_636095334189520571_HasThumb_Thumb.jpg.png'),
(185, 2, 'VBI ra mắt mô hình bán lẻ mới', 'vbi-ra-mat-mo-hinh-ban-le-moi', '<p><span style=\"font-family: Arial;\">Chiều 15/09 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) tổ chức Lễ ra mắt m&ocirc; h&igrave;nh b&aacute;n lẻ mới nhằm th&uacute;c đẩy hoạt động b&aacute;n lẻ theo hướng hiện đại, thỏa m&atilde;n nhu cầu đa dạng về bảo hiểm của kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n. Tham dự buổi lễ c&oacute; sự hiện diện của &Ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc VBI, &Ocirc;ng Ng...</span></p>', '<p>Chiều 15/09 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) tổ chức Lễ ra mắt m&ocirc; h&igrave;nh b&aacute;n lẻ mới nhằm th&uacute;c đẩy hoạt động b&aacute;n lẻ theo hướng hiện đại, thỏa m&atilde;n nhu cầu đa dạng về bảo hiểm của kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n. Tham dự buổi lễ c&oacute; sự hiện diện của &Ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc VBI, &Ocirc;ng Nguyễn Hồng Phong - Ph&oacute; Tổng Gi&aacute;m đốc c&ugrave;ng ban l&atilde;nh đạo v&agrave; c&aacute;n bộ kinh doanh c&aacute;c chi nh&aacute;nh chuyển đổi m&ocirc; h&igrave;nh th&iacute; điểm tr&ecirc;n địa b&agrave;n H&agrave; Nội.</p>\r\n<p>Để thực hiện v&agrave; triển khai được m&ocirc; h&igrave;nh b&aacute;n lẻ th&agrave;nh c&ocirc;ng, &Ocirc;ng L&ecirc; Tuấn Dũng - T&ocirc;ng Gi&aacute;m đốc VBI ph&aacute;t biểu: &ldquo;VBI định hướng r&otilde; mục ti&ecirc;u chiến lược trong năm 2016 v&agrave; giai đoạn tiếp theo: Đổi mới, bứt ph&aacute;, cung cấp c&aacute;c sản phẩm dịch vụ vượt trội đối với mọi ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng b&aacute;n lẻ, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam.&rdquo;</p>\r\n<p><img src=\"../../../uploads/IMG_6487.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em>&Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI ph&aacute;t biểu tại buổi lễ</em></p>\r\n<p><img src=\"../../../uploads/IMG_6532.jpg\" alt=\"\" width=\"1366\" height=\"1038\" /></p>\r\n<p style=\"text-align: center;\"><em>Đại diện Ban dự &aacute;n triển khai m&ocirc; h&igrave;nh b&aacute;n lẻ - B&agrave; L&ecirc; Quỳnh Hoa thuyết tr&igrave;nh về m&ocirc; h&igrave;nh mới.</em></p>\r\n<p>B&agrave; L&ecirc; Quỳnh Hoa - Đại diện Ban dự &aacute;n triển khai m&ocirc; h&igrave;nh b&aacute;n lẻ cho biết: Trong đợt chuyển đổi lần n&agrave;y, VBI sẽ tập trung v&agrave;o những thay đổi lớn li&ecirc;n quan đến mảng b&aacute;n lẻ. Trong đ&oacute; tập trung v&agrave;o c&aacute;c giải ph&aacute;p trọng t&acirc;m: Đột ph&aacute; về cơ cấu tổ chức, phương thức quản trị chiến lược b&aacute;n lẻ hiện đại; Đột ph&aacute; trong ứng dụng c&aacute;c giải ph&aacute;p c&ocirc;ng nghệ, giải ph&aacute;p tiện &iacute;ch sản phẩm dịch vụ th&acirc;n thiện, tiện lợi trong mọi ph&acirc;n kh&uacute;c B&aacute;n lẻ; Đổi mới phương thức quản trị rủi ro, quản trị t&iacute;nh to&aacute;n hiệu quả đến từng đơn vị, c&aacute; nh&acirc;n mảng b&aacute;n lẻ; Tối ưu h&oacute;a quy tr&igrave;nh nghiệp vụ (r&uacute;t ngắn thời gian giao dịch, giải quyết nhanh gọn, thuận tiện) đối với từng ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng;&hellip;</p>\r\n<p><img src=\"../../../uploads/IMG_6556.jpg\" alt=\"\" width=\"1366\" height=\"765\" /></p>\r\n<p style=\"text-align: center;\"><em>Ban l&atilde;nh đạo VBI c&ugrave;ng c&aacute;n bộ kinh doanh tại c&aacute;c chi nh&aacute;nh tại H&agrave; Nội quyết t&acirc;m triển khai th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh mới </em></p>\r\n<p>Ban l&atilde;nh đạo VBI tin tưởng c&aacute;c CN chuyển đổi m&ocirc; h&igrave;nh sẽ thực hiện chuyển đổi th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh b&aacute;n lẻ, đưa hoạt động b&aacute;n lẻ của VBI c&oacute; những bước tiến vượt bậc cả về m&ocirc; h&igrave;nh hoạt động, cơ cấu tổ chức theo hướng hiện đại, sản phẩm dịch vụ vượt trội, thỏa m&atilde;n tất cả nhu cầu bảo hiểm của kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:16:26', '2018-03-21 19:16:26', 'images/news/1520497968_IMG_655_636095594098412928_HasThumb_Thumb.jpg.png'),
(186, 2, 'VBI cung cấp bảo hiểm toàn diện Thẻ ATM E- Partner', 'vbi-cung-cap-bao-hiem-toan-dien-the-atm-e-partner', '<p><span style=\"font-family: Arial;\">Với mong muốn đảm bảo an to&agrave;n v&agrave; mang lại tiện &iacute;ch tốt nhất cho kh&aacute;ch h&agrave;ng khi sử dụng thẻ, Bảo hiểm VietinBank (VBI) ch&iacute;nh thức cung cấp dịch vụ Bảo hiểm to&agrave;n diện cho tất cả chủ thẻ ghi nợ nội địa E-Partner của VietinBank.&nbsp;</span></p>', '<p>Với mong muốn đảm bảo an to&agrave;n v&agrave; mang lại tiện &iacute;ch tốt nhất cho kh&aacute;ch h&agrave;ng khi sử dụng thẻ, Bảo hiểm VietinBank (VBI) ch&iacute;nh thức cung cấp dịch vụ Bảo hiểm to&agrave;n diện cho tất cả chủ thẻ ghi nợ nội địa E-Partner của VietinBank.</p>\r\n<p>Theo đ&oacute;, khi tham gia mua g&oacute;i bảo hiểm n&agrave;y chủ thẻ sẽ được bảo hiểm mất thẻ kết hợp với bảo hiểm gian lận thẻ như sau:</p>\r\n<p>Bảo hiểm chi ph&iacute; ph&aacute;t h&agrave;nh lại thẻ: Khi mất thẻ hay bị hư hỏng thẻ, kh&aacute;ch h&agrave;ng sẽ được bảo hiểm chi trả c&aacute;c chi ph&iacute; ph&aacute;t h&agrave;nh lại thẻ tối đa 3 lần/năm v&agrave; được hỗ trợ trả thẻ tại nh&agrave;. Chủ thẻ kh&ocirc;ng cần thực hiện bất kỳ thủ tục n&agrave;o khi nhận bồi thường từ bảo hiểm v&agrave; kh&ocirc;ng giới hạn phạm vi mất thẻ để được hưởng bảo hiểm.</p>\r\n<p>Bảo hiểm giao dịch gian lận thẻ: Trong qu&aacute; tr&igrave;nh sử dụng thẻ, nếu thẻ bị thất lạc, mất cắp hoặc bị lợi dụng để sử dụng m&agrave; kh&ocirc;ng được ph&eacute;p của chủ thẻ, kh&aacute;ch h&agrave;ng sẽ được bồi ho&agrave;n chi ph&iacute; ph&aacute;t sinh giao dịch tr&aacute;i ph&eacute;p. Số tiền đựợc bảo hiểm cho rủi ro gian lận tối đa 10.000.000 đồng/lần/vụ v&agrave; tối đa 50.000.000 đồng/năm/thẻ.</p>\r\n<p><img src=\"../../../uploads/final-2.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Với dịch vụ n&agrave;y kh&ocirc;ng chỉ gi&uacute;p kh&aacute;ch h&agrave;ng tiết kiệm thời gian, chi ph&iacute; khi ph&aacute;t h&agrave;nh lại thẻ m&agrave; c&ograve;n mang lại lợi &iacute;ch tối đa cho kh&aacute;ch h&agrave;ng trong qu&aacute; tr&igrave;nh sử dụng. Chỉ với mức ph&iacute; bảo hiểm 2.000 đồng/th&aacute;ng, chủ thẻ được sử dụng dịch vụ với những lợi &iacute;ch vượt trội.<br /> <br />Để biết th&ecirc;m th&ocirc;ng tin chi tiết, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ ph&ograve;ng giao dịch/chi nh&aacute;nh VietinBank gần nhất hoặc gọi số Contact Center: 1900 558 868; email: <a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:16:55', '2018-03-21 19:16:55', 'images/news/1520498053_Bao-hiem-_636100495949713207_HasThumb_Thumb.jpg.png'),
(187, 2, 'VBI chuyển đổi mô hình tổ chức tại Trụ sở chính', 'vbi-chuyen-doi-mo-hinh-to-chuc-tai-tru-so-chinh', '<p><span style=\"font-family: Arial;\">Để tiếp tục n&acirc;ng cao năng lực cạnh tranh v&agrave; năng lực điều h&agrave;nh, ng&agrave;y 03/10/2016, VBI ch&iacute;nh thức triển khai chuyển đổi m&ocirc; h&igrave;nh mới theo hướng cổ phần h&oacute;a nhằm đ&aacute;p ứng nhu cầu ng&agrave;y một ph&aacute;t triển của doanh nghiệp.&nbsp;</span></p>', '<p>Ng&agrave;y 03/10/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ ra mắt m&ocirc; h&igrave;nh tổ chức mới. Buổi lễ c&oacute; sự tham dự của &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI, &ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc c&ugrave;ng to&agrave;n thể l&atilde;nh đạo v&agrave; c&aacute;n bộ c&aacute;c ph&ograve;ng ban, chi nh&aacute;nh VBI tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI ph&aacute;t biểu: &ldquo;Để tiếp tục n&acirc;ng cao năng lực cạnh tranh v&agrave; năng lực điều h&agrave;nh, ng&agrave;y h&ocirc;m nay, VBI ch&iacute;nh thức triển khai chuyển đổi m&ocirc; h&igrave;nh mới theo hướng cổ phần h&oacute;a nhằm đ&aacute;p ứng nhu cầu ng&agrave;y một ph&aacute;t triển của doanh nghiệp. Trong thời gian tới, VBI sẽ tiếp tục đổi mới c&ocirc;ng t&aacute;c quản trị, ph&aacute;t triển c&aacute;c hoạt động dịch vụ v&agrave; hệ thống sản phẩm mới đa dạng, cải tiến mạnh mẽ hệ thống c&ocirc;ng nghệ hiện đại, hệ thống dịch vụ b&aacute;n h&agrave;ng v&agrave; sau b&aacute;n h&agrave;ng.&rdquo;</p>\r\n<p><img src=\"../../../uploads/Anh chuyen doi mo hinh moi.jpg\" alt=\"\" width=\"1366\" height=\"1190\" /></p>\r\n<p style=\"text-align: center;\"><strong><em>(Ảnh: &Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI đ&aacute;nh chi&ecirc;ng ra mắt m&ocirc; h&igrave;nh tổ chức mới)</em></strong></p>\r\n<p>Kết quả hoạt động 9 th&aacute;ng đầu năm 2016 của VBI cũng cho thấy những bước ph&aacute;t triển đột ph&aacute;, ghi dấu bằng kết quả hoạt động kinh doanh hiệu quả. Doanh thu ph&iacute; bảo hiểm gốc trong 9 th&aacute;ng đầu năm của VBI ước đạt 460 tỷ đồng, tăng trưởng 46% so với 9 th&aacute;ng đầu năm 2015. Lợi nhuận lũy kế 9 th&aacute;ng của VBI đạt 48 tỷ đồng. Năm 2016, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI tr&ecirc;n 800 tỷ đồng, tăng trưởng 50% so với 2015.</p>\r\n<p>Với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững, trong năm 2016, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u chiến lược chuyển đổi m&ocirc; h&igrave;nh nhằm n&acirc;ng cao hiệu quả kinh doanh, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:20:19', '2018-03-21 19:20:19', 'images/news/1520498155_Anh-chuye_636110910256334255_HasThumb_Thumb.jpg.png'),
(188, 2, 'VBI chi trả hơn 120 triệu đồng tiền bồi thường bảo hiểm sức khỏe tại Hội An', 'vbi-chi-tra-hon-120-trieu-dong-tien-boi-thuong-bao-hiem-suc-khoe-tai-hoi-an', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 06/10/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Đ&agrave; Nẵng phối hợp với VietinBank CN Hội An đ&atilde; tổ chức chi trả 127 triệu đồng tiền bồi thường bảo hiểm sức khỏe cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Trần Thị Nở (tr&uacute; tại Hội An).&nbsp;</span></p>', '<p>Ng&agrave;y 06/10/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Đ&agrave; Nẵng phối hợp với VietinBank CN Hội An đ&atilde; tổ chức chi trả 127 triệu đồng tiền bồi thường bảo hiểm sức khỏe cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Trần Thị Nở (tr&uacute; tại Hội An).<br /> <br />Năm 2015, b&agrave; Trần Thị Nở tham gia dịch vụ bảo hiểm sức khỏe VBI Care. Kh&ocirc;ng l&acirc;u sau đ&oacute;, do l&acirc;m bệnh nặng n&ecirc;n b&agrave; đ&atilde; kh&ocirc;ng may qua đời. Ngay sau khi nhận được th&ocirc;ng tin n&agrave;y, Bảo hiểm VietinBank CN Đ&agrave; Nẵng đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank Hội An hướng dẫn gia đ&igrave;nh ho&agrave;n thiện hồ sơ để được chi trả bồi thường bảo hiểm theo đ&uacute;ng quy định.</p>\r\n<p><br />Trong buổi nhận tiền bồi thường, đại diện gia đ&igrave;nh kh&aacute;ch h&agrave;ng Trần Thị Nở đ&atilde; gửi lời cảm ơn đến Bảo hiểm VietinBank Chi nh&aacute;nh Đ&agrave; Nẵng v&agrave; VietinBank Hội An đ&atilde; tạo mọi điều kiện hỗ trợ trong l&uacute;c gia đ&igrave;nh gặp kh&oacute; khăn.</p>\r\n<p><img src=\"../../../uploads/boi thuong hoi an.jpg\" alt=\"\" width=\"960\" height=\"728\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng)</em></p>\r\n<p>L&atilde;nh đạo VBI cho biết: Bảo hiểm sức khỏe VBI Care mang tới cho Qu&yacute; kh&aacute;ch v&agrave; gia đ&igrave;nh sự an t&acirc;m tuyệt đối về t&agrave;i ch&iacute;nh với những ưu đ&atilde;i v&agrave; quyền lợi đặc biệt.</p>\r\n<ul>\r\n<li>Quyền lợi bảo hiểm cho tai nạn l&ecirc;n đến 2 tỷ đồng</li>\r\n<li>Quyền lợi nội tr&uacute; l&ecirc;n đến 400 triệu đồng</li>\r\n<li>Quyền lợi thai sản l&ecirc;n đến 100 triệu đồng</li>\r\n<li>Quyền lợi bảo hiểm cho bệnh hiểm ngh&egrave;o l&ecirc;n đến 100 triệu đồng</li>\r\n<li>Kh&ocirc;ng y&ecirc;u cầu kh&aacute;m sức khỏe trước khi đăng k&yacute;</li>\r\n<li>Bảo l&atilde;nh viện ph&iacute; tại c&aacute;c bệnh viện, ph&ograve;ng kh&aacute;m tr&ecirc;n to&agrave;n quốc</li>\r\n<li>Bồi thường nhanh, ch&iacute;nh x&aacute;c</li>\r\n</ul>\r\n<p><br />Chi tiết về quyền lợi v&agrave; biểu ph&iacute; bảo hiểm, Qu&yacute; kh&aacute;ch vui l&ograve;ng xem tại đ&acirc;y<br /> <br />Để biết th&ecirc;m th&ocirc;ng tin chi tiết, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ ph&ograve;ng giao dịch/chi nh&aacute;nh VBI v&agrave; VietinBank gần nhất hoặc gọi số Contact Center: 1900 558 868.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:20:51', '2018-03-21 19:20:51', 'images/news/1520498295_pic-New-i_636113404212151138_HasThumb_Thumb.JPG.png'),
(189, 2, 'Bảo hiểm VietinBank (VBI) bổ nhiệm Phó Tổng Giám đốc', 'bao-hiem-vietinbank-vbi-bo-nhiem-pho-tong-giam-doc', '<p><span style=\"font-family: Arial;\">Được sự chấp thuận của Hội đồng quản trị VietinBank, ng&agrave;y 10/10/2016, VBI tổ chức lễ c&ocirc;ng bố v&agrave; trao Quyết định bổ nhiệm b&agrave; Trần Thị Hương Thủy &ndash; Gi&aacute;m đốc Ban Nh&acirc;n sự giữ chức vụ Ph&oacute; Tổng Gi&aacute;m đốc Bảo hiểm VietinBank.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Được sự chấp thuận của Hội đồng quản trị VietinBank, ng&agrave;y 10/10/2016, VBI tổ chức lễ c&ocirc;ng bố v&agrave; trao Quyết định bổ nhiệm b&agrave; Trần Thị Hương Thủy &ndash; Gi&aacute;m đốc Ban Nh&acirc;n sự giữ chức vụ Ph&oacute; Tổng Gi&aacute;m đốc Bảo hiểm VietinBank.</span></p>\r\n<p><img src=\"../../../uploads/IMG_6698.jpg\" alt=\"\" width=\"1366\" height=\"993\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Ủy vi&ecirc;n Hội đồng Quản trị VietinBank, Chủ tịch C&ocirc;ng ty &ndash; B&agrave; Nguyễn Hồng V&acirc;n trao Quyết định bổ nhiệm Ph&oacute; Tổng Gi&aacute;m đốc cho b&agrave; Trần Thị Hương Thủy</span></em></p>\r\n<p>Tham dự Lễ c&ocirc;ng bố v&agrave; trao quyết định c&oacute; B&agrave; Nguyễn Hồng V&acirc;n - Ủy vi&ecirc;n Hội đồng quản trị VietinBank, Chủ tịch C&ocirc;ng ty; &ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc VBI c&ugrave;ng Ban điều h&agrave;nh v&agrave; l&atilde;nh đạo Ban/Ph&ograve;ng Trụ sở ch&iacute;nh v&agrave; Chi nh&aacute;nh VBI tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>Tr&igrave;nh tự, thủ tục bổ nhiệm được thực hiện đ&uacute;ng quy tr&igrave;nh về c&ocirc;ng t&aacute;c c&aacute;n bộ với sự đồng thuận, nhất tr&iacute; của 100% th&agrave;nh vi&ecirc;n Hội nghị li&ecirc;n tịch C&ocirc;ng ty, c&aacute;c c&aacute;n bộ chủ chốt trong c&ocirc;ng ty v&agrave; được sự chấp thuận của Hội đồng Quản trị Ng&acirc;n h&agrave;ng VietinBank, bảo đảm tu&acirc;n thủ chặt chẽ c&aacute;c quy định của Ng&acirc;n h&agrave;ng VietinBank về quy tr&igrave;nh c&ocirc;ng t&aacute;c c&aacute;n bộ quản l&yacute; cấp cao.</p>\r\n<p>Qua 20 năm c&ocirc;ng t&aacute;c tại VietinBank, b&agrave; Trần Thị Hương Thủy được Ban l&atilde;nh đạo đ&aacute;nh gi&aacute; l&agrave; c&aacute;n bộ quản l&yacute; c&oacute; phẩm chất đạo đức tốt, c&oacute; tr&igrave;nh độ, năng lực quản trị điều h&agrave;nh; năng động, s&aacute;ng tạo, tr&aacute;ch nhiệm với c&ocirc;ng việc được giao. Tr&ecirc;n mọi cương vị c&ocirc;ng t&aacute;c, b&agrave; lu&ocirc;n được đ&aacute;nh gi&aacute; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ.</p>\r\n<p>Ph&aacute;t biểu tại buổi Lễ trang trọng n&agrave;y, Ủy vi&ecirc;n HĐQT VietinBank Nguyễn Hồng V&acirc;n ch&uacute;c mừng b&agrave; Trần Thị Hương Thủy được HĐQT tin tưởng bổ nhiệm chức vụ mới. B&agrave; V&acirc;n mong muốn, tr&ecirc;n cương vị Ph&oacute; Tổng Gi&aacute;m đốc, b&agrave; Trần Thị Hương Thủy sẽ tiếp tục ph&aacute;t huy năng lực, tr&iacute; tuệ trong c&ocirc;ng t&aacute;c, đồng sức đồng l&ograve;ng c&ugrave;ng với tập thể Ban L&atilde;nh đạo triển khai c&oacute; kết quả nhiệm vụ c&ocirc;ng t&aacute;c được ph&acirc;n c&ocirc;ng; thực hiện đầy đủ quyền hạn, nghĩa vụ, tr&aacute;ch nhiệm của Ph&oacute; Tổng Gi&aacute;m đốc; nghi&ecirc;n cứu, chỉ đạo s&acirc;u s&aacute;t hoạt động kinh doanh tr&ecirc;n từng mảng c&ocirc;ng việc được ph&acirc;n c&ocirc;ng phụ tr&aacute;ch, g&oacute;p phần đưa VBI ng&agrave;y c&agrave;ng ph&aacute;t triển vững mạnh, ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ m&agrave; Ban l&atilde;nh đạo giao.</p>\r\n<p><img src=\"../../../uploads/IMG_6701.jpg\" alt=\"\" width=\"1366\" height=\"719\" /></p>\r\n<p style=\"text-align: center;\"><em>Ban L&atilde;nh đạo VBI ch&uacute;c mừng t&acirc;n Ph&oacute; Tổng Gi&aacute;m đốc Trần Thị Hương Thủy</em></p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:21:35', '2018-03-21 19:21:35', 'images/news/1520498421_IMG_670_636117118917096931_HasThumb_Thumb.JPG.png'),
(190, 2, 'VBI 4 năm liên tiếp nằm trong Top 1000 doanh nghiệp nộp thuế thu nhập lớn nhất Việt Nam', 'vbi-4-nam-lien-tiep-nam-trong-top-1000-doanh-nghiep-nop-thue-thu-nhap-lon-nhat-viet-nam', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) được vinh danh trong Top 1000 doanh nghiệp nộp thuế lớn nhất năm 2016 do C&ocirc;ng ty Cổ phần B&aacute;o c&aacute;o đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report), B&aacute;o VietNamNet &ndash; Bộ Th&ocirc;ng tin Truyền th&ocirc;ng v&agrave; Tạp ch&iacute; Thuế - Tổng Cục Thuế vừa c&ocirc;ng bố. Đ&acirc;y l&agrave; năm thứ 4 li&ecirc;n tiếp, VBI đ...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) được vinh danh trong Top 1000 doanh nghiệp nộp thuế lớn nhất năm 2016 do C&ocirc;ng ty Cổ phần B&aacute;o c&aacute;o đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report), B&aacute;o VietNamNet &ndash; Bộ Th&ocirc;ng tin Truyền th&ocirc;ng v&agrave; Tạp ch&iacute; Thuế - Tổng Cục Thuế vừa c&ocirc;ng bố. Đ&acirc;y l&agrave; năm thứ 4 li&ecirc;n tiếp, VBI được vinh danh trong Bảng xếp hạng n&agrave;y. Trong khối doanh nghiệp bảo hiểm phi nh&acirc;n thọ, VBI nằm trong Top 10 C&ocirc;ng ty bảo hiểm nộp thuế lớn nhất Việt Nam.</span></p>\r\n<p><img src=\"../../../uploads/V1000-4.jpg\" alt=\"\" width=\"1366\" height=\"702\" /></p>\r\n<p><span style=\"font-family: Arial;\">Từ đầu năm 2016 tới nay, VBI li&ecirc;n tục được vinh danh &ldquo;Top 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam&rdquo;, &ldquo;Top 20 doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng&rdquo;, &ldquo;Top 100 thương hiệu mạnh Việt Nam&rdquo;, &ldquo;Top 1000 doanh nghiệp nộp thuế thu nhập lớn nhất Việt Nam&rdquo;. Những danh hiệu tr&ecirc;n đ&atilde; minh chứng cho những bước ph&aacute;t triển đột ph&aacute; của VBI. B&ecirc;n cạnh c&aacute;c giải thưởng uy t&iacute;n, VBI c&ograve;n đạt kết quả kinh doanh ấn tượng. Doanh thu ph&iacute; bảo hiểm gốc trong 9 th&aacute;ng đầu năm của VBI ước đạt 460 tỷ đồng, tăng trưởng 46% so với 9 th&aacute;ng đầu năm 2015. Lợi nhuận lũy kế 9 th&aacute;ng của VBI đạt 48 tỷ đồng. Năm 2016, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI tr&ecirc;n 800 tỷ đồng, tăng trưởng 50% so với 2015.<br /> <br />Hiện nay, VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng với nhiều ưu điểm nổi trội, gi&uacute;p kh&aacute;ch h&agrave;ng tiếp cận v&agrave; c&aacute;c quyền lợi bảo hiểm nhanh ch&oacute;ng hơn, an to&agrave;n hơn v&agrave; dễ d&agrave;ng hơn, theo h&igrave;nh thức hiện đại nhất tr&ecirc;n thị trường bảo hiểm.<br /> <br />Với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững, trong năm 2016, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 08:21:56', '2018-03-21 19:21:56', 'images/news/1520498498_V1000_636132710246380473_HasThumb_Thumb.jpg.png'),
(191, 2, 'VBI bảo hiểm giải thưởng Hole in one trị giá hơn 1,5 tỷ đồng', 'vbi-bao-hiem-giai-thuong-hole-in-one-tri-gia-hon-1-5-ty-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/10/2016 tại s&acirc;n golf Long Th&agrave;nh, tỉnh Đồng Nai đ&atilde; diễn ra Giải golf giao hữu với kh&aacute;ch h&agrave;ng doanh nghiệp (KHDN) FDI H&agrave;n Quốc khu vực ph&iacute;a Nam do VietinBank tổ chức. C&aacute;c golfer thi đấu tr&ecirc;n s&acirc;n golf 18 lỗ theo thể thức handicap, với cơ cấu giải: Nhất, Nh&igrave;, Ba (theo 3 bảng...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/10/2016 tại s&acirc;n golf Long Th&agrave;nh, tỉnh Đồng Nai đ&atilde; diễn ra Giải golf giao hữu với kh&aacute;ch h&agrave;ng doanh nghiệp (KHDN) FDI H&agrave;n Quốc khu vực ph&iacute;a Nam do VietinBank tổ chức. Tham dự Giải c&oacute; &ocirc;ng Trần C&ocirc;ng Quỳnh L&acirc;n - Ph&oacute; Tổng Gi&aacute;m đốc VietinBank; l&atilde;nh đạo c&aacute;c ph&ograve;ng, ban Trụ sở ch&iacute;nh, Văn ph&ograve;ng đại diện, c&aacute;c chi nh&aacute;nh. Đặc biệt, Giải c&oacute; sự tham dự của gần 150 golf thủ l&agrave; đối t&aacute;c, KHDN FDI H&agrave;n Quốc - kh&aacute;ch h&agrave;ng th&acirc;n thiết, truyền thống của VietinBank khu vực ph&iacute;a Nam.<br /> <br />C&aacute;c golfer thi đấu tr&ecirc;n s&acirc;n golf 18 lỗ theo thể thức handicap, với cơ cấu giải: Nhất, Nh&igrave;, Ba (theo 3 bảng thi đấu), giải Kỹ thuật v&agrave; giải Đặc biệt Hole in one.<br /> <br />VBI tham gia bảo hiểm giải thưởng Hole-in-one l&agrave; một chiếc xe &ocirc; t&ocirc; Mercedes C250 trị gi&aacute; hơn 1,5 tỷ đồng.<br /> <br />Kh&eacute;p lại ng&agrave;y thi đấu s&ocirc;i nổi, c&aacute;c golfer đ&atilde; cống hiến những đường b&oacute;ng đẹp, thể hiện những kỹ thuật đi&ecirc;u luyện, chuy&ecirc;n nghiệp, thi đấu với tinh thần fair-play. Kết th&uacute;c Giải, Ban Tổ chức trao giải cho c&aacute;c golfer như sau: 1 Giải Best Gross; 3 giải Nhất; 3 giải Nh&igrave;; 3 giải Ba v&agrave; 6 giải Kỹ thuật.</span></p>\r\n<p><img src=\"../../../uploads/giao-huu-golf-voi-dn-han-quoc-phia-nam.jpg\" alt=\"\" width=\"700\" height=\"397\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Tập thể c&aacute;c golfer chụp ảnh lưu niệm tại giải đấu</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong thời gian qua, Bảo hiểm VietinBank (VBI) lu&ocirc;n l&agrave; một trong những c&ocirc;ng ty h&agrave;ng đầu Việt Nam bảo hiểm cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 08:22:20', '2018-03-21 19:22:20', 'images/news/1520498742_giao-huu-_636135872790546187_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(192, 2, 'Chi bộ Trụ sở chính - Đảng bộ Công ty Bảo hiểm VietinBank kết nạp đảng viên', 'chi-bo-tru-so-chinh-dang-bo-cong-ty-bao-hiem-vietinbank-ket-nap-dang-vien', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 06/11/2016, tại Khu di t&iacute;ch T&acirc;n Tr&agrave;o, Tuy&ecirc;n Quang, Chi bộ Trụ sở ch&iacute;nh - Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức lễ kết nạp đảng cho 8 quần ch&uacute;ng ưu t&uacute;. Tham dự buổi lễ c&oacute; đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank, đồng ch&iacute; Trần Thị Hương Thủy &ndash; ...&nbsp;</span></p>', '<p>Ng&agrave;y 06/11/2016, tại Khu di t&iacute;ch T&acirc;n Tr&agrave;o, Tuy&ecirc;n Quang, Chi bộ Trụ sở ch&iacute;nh - Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank tổ chức lễ kết nạp đảng cho 8 quần ch&uacute;ng ưu t&uacute;. Tham dự buổi lễ c&oacute; đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank, đồng ch&iacute; Trần Thị Hương Thủy &ndash; Ph&oacute; B&iacute; thư Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank c&ugrave;ng c&aacute;c đồng ch&iacute; Đảng vi&ecirc;n trong Chi bộ Trụ sở ch&iacute;nh - Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank.</p>\r\n<p>Trải qua qu&aacute; tr&igrave;nh tu dưỡng v&agrave; r&egrave;n luyện, 8 quần ch&uacute;ng ưu t&uacute; Nguyễn Cao Bằng, L&ecirc; Đ&igrave;nh Quốc, Phạm Thị Hồng Thắm, Li&ecirc;n Văn Choang, Đinh Thi&ecirc;n Hải, Đỗ Qu&yacute; Chi, Qu&aacute;ch Văn H&agrave;, L&ecirc; Quang Vinh đ&atilde; được k&ecirc;́t nạp Đảng. Đ&acirc;y l&agrave; những c&aacute;n bộ ưu t&uacute;, c&oacute; phẩm chất đạo đức tốt, c&oacute; năng lực tr&igrave;nh độ chuy&ecirc;n m&ocirc;n, đ&atilde; trải qua qu&aacute; tr&igrave;nh phấn đấu v&agrave; r&egrave;n luyện tốt.</p>\r\n<p><img src=\"../../../uploads/IMG_7941.jpg\" alt=\"\" width=\"1366\" height=\"714\" /></p>\r\n<p>Ph&aacute;t biểu chỉ đạo tại buổi lễ, đồng ch&iacute; L&ecirc; Tuấn Dũng &ndash; B&iacute; thư Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank ch&uacute;c mừng c&aacute;c đảng vi&ecirc;n vừa được kết nạp v&agrave; nhấn mạnh: &ldquo;Phấn đấu trở th&agrave;nh đảng vi&ecirc;n l&agrave; niềm vinh dự, niềm tự h&agrave;o v&agrave; cũng l&agrave; đ&ograve;i hỏi, trọng tr&aacute;ch với c&aacute;c Đảng vi&ecirc;n mới. C&aacute;c Đảng vi&ecirc;n mới kết nạp cần học tập nắm vững chủ trương ch&iacute;nh s&aacute;ch của Đảng v&agrave; Nh&agrave; nước, tu&acirc;n thủ ph&aacute;p luật, quy chế nội quy lao động,&hellip;ra sức học tập n&acirc;ng cao nghiệp vụ, t&iacute;ch cực tham gia c&aacute;c hoạt động của đơn vị. Mỗi đồng ch&iacute; cần gương mẫu trong c&ocirc;ng việc, t&iacute;ch cực phấn đấu với tinh thần tr&aacute;ch nhiệm cao. Trong thời gian tới Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank sẽ đẩy mạnh ph&aacute;t triển lực lượng Đảng vi&ecirc;n v&agrave; quần ch&uacute;ng ưu t&uacute; đứng trong h&agrave;ng ngũ Đảng&rdquo;.<br /> <br />Thay mặt cho c&aacute;c đảng vi&ecirc;n mới được kết nạp, đồng ch&iacute; Nguyễn Cao Bằng ph&aacute;t biểu b&agrave;y tỏ niềm vinh dự được đứng trong h&agrave;ng ngũ của Đảng, cảm ơn sự quan t&acirc;m gi&aacute;o dục, bồi dưỡng của Đảng ủy C&ocirc;ng ty Bảo hiểm VietinBank, Ban l&atilde;nh đạo, c&aacute;c tổ chức Đảng cơ sở đ&atilde; tạo điều kiện cho c&aacute;c quần ch&uacute;ng ưu t&uacute; r&egrave;n luyện v&agrave; phấn đấu trở th&agrave;nh đảng vi&ecirc;n. C&aacute;c đảng vi&ecirc;n mới sẽ quyết t&acirc;m, nỗ lực nhiều hơn nữa để xứng đ&aacute;ng l&agrave; đảng vi&ecirc;n, thực hiện đầy đủ nhiệm vụ Đảng bộ giao, g&oacute;p phần x&acirc;y dựng Đảng bộ C&ocirc;ng ty Bảo hiểm VietinBank ng&agrave;y một vững mạnh.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:22:49', '2018-03-21 19:22:49', 'images/news/1520498854_IMG_794_636141138204248985_HasThumb_Thumb.JPG.png'),
(193, 2, 'VBI tổ chức chương trình từ thiện “Áo ấm trao em” 2016', 'vbi-to-chuc-chuong-trinh-tu-thien-ao-am-trao-em-2016', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 7/11/2016, Đo&agrave;n vi&ecirc;n thanh ni&ecirc;n Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh &ldquo;&Aacute;o ấm trao em&rdquo; tại Trường THCS x&atilde; Năng Khả, huyện Na Hang, tỉnh Tuy&ecirc;n Quang. Trong chương tr&igrave;nh, Đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; trao tận tay cho c&aacute;c em học sinh ngh&egrave;o 300 chiếc &aacute;o v&agrave; chăn ấm với mong ...&nbsp;</span></p>', '<p>Ng&agrave;y 7/11/2016, Đo&agrave;n vi&ecirc;n thanh ni&ecirc;n Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh &ldquo;&Aacute;o ấm trao em&rdquo; tại Trường THCS x&atilde; Năng Khả, huyện Na Hang, tỉnh Tuy&ecirc;n Quang. Trong chương tr&igrave;nh, Đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; trao tận tay cho c&aacute;c em học sinh ngh&egrave;o 300 chiếc &aacute;o v&agrave; chăn ấm c&ugrave;ng nhiều phần qu&agrave; như: đệm, chiếu, gối, ủng với mong muốn mang đến cho c&aacute;c em nhỏ một m&ugrave;a đ&ocirc;ng ấm &aacute;p.</p>\r\n<p>Đặc biệt, với những em nhỏ c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn, Đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; trao tặng 14 xe đạp l&agrave;m phương tiện cho c&aacute;c em tới trường. C&aacute;c em học sinh Trường THCS x&atilde; Năng Khả rất phấn khởi v&agrave; lu&ocirc;n nở nụ cười tr&ecirc;n m&ocirc;i khi nhận được xe đạp v&agrave; kho&aacute;c tr&ecirc;n m&igrave;nh chiếc &aacute;o mới ấm &aacute;p.</p>\r\n<p>H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng, x&atilde; hội. B&ecirc;n cạnh chương tr&igrave;nh từ thiện &ldquo;&Aacute;o ấm trao em&rdquo; được tổ chức thường ni&ecirc;n, VBI c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: Tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương; thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh; thăm v&agrave; tặng qu&agrave; cho đồng b&agrave;o lũ lụt H&agrave; Tĩnh; ủng hộ Trường sa th&acirc;n y&ecirc;u,v.v...</p>\r\n<p>Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh nổi bật của chương tr&igrave;nh từ thiện \"&Aacute;o ấm trao em\":</p>\r\n<p><img src=\"../../../uploads/14961394_1237314766361776_1904842373_n.jpg\" alt=\"\" width=\"959\" height=\"566\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Ph&oacute; Tổng Gi&aacute;m đốc VBI trao tặng 14 xe đạp cho c&aacute;c em nhỏ c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn)</em></p>\r\n<p><img src=\"../../../uploads/IMG_7520.jpg\" alt=\"\" width=\"1239\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Niềm vui của c&aacute;c em nhỏ khi nhận được &aacute;o ấm từ Đo&agrave;n thanh ni&ecirc;n VBI)</em></p>\r\n<p><img src=\"../../../uploads/IMG_7599.jpg\" alt=\"\" width=\"1366\" height=\"738\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Đo&agrave;n thanh ni&ecirc;n VBI chụp ảnh lưu niệm c&ugrave;ng c&aacute;c em học sinh d&acirc;n tộc Trường THCS x&atilde; Năng Khả, Tuy&ecirc;n Quang)</em></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:23:58', '2018-03-21 19:23:58', 'images/news/1520499009_IMG_759_636141377412429372_HasThumb_Thumb.JPG.png'),
(194, 2, 'Thông báo chuyển địa điểm nhận hồ sơ bồi thường bảo hiểm con người tại Hà Nội', 'thong-bao-chuyen-dia-diem-nhan-ho-so-boi-thuong-bao-hiem-con-nguoi-tai-ha-noi', '<p><span style=\"font-family: Arial;\">Bắt đầu từ ng&agrave;y 14/11/2016, địa chỉ nhận hồ sơ bồi thường bảo hiểm con người tại H&agrave; Nội của VBI như sau: Tầng 25 &ndash; T&ograve;a Nh&agrave; Ngọc Kh&aacute;nh Plaza &ndash; Số 1 Phạm Huy Th&ocirc;ng &ndash; Quận Ba Đ&igrave;nh &ndash; H&agrave; Nội.</span></p>', '<p>Bảo hiểm VietinBank (VBI) xin tr&acirc;n trọng th&ocirc;ng b&aacute;o!</p>\r\n<p>Bắt đầu từ ng&agrave;y 14/11/2016, địa chỉ nhận hồ sơ bồi thường bảo hiểm con người tại H&agrave; Nội của VBI như sau:<br /><strong>Tầng 25 &ndash; T&ograve;a Nh&agrave; Ngọc Kh&aacute;nh Plaza &ndash; Số 1 Phạm Huy Th&ocirc;ng &ndash; Quận Ba Đ&igrave;nh &ndash; H&agrave; Nội</strong></p>\r\n<p>Cảm ơn qu&yacute; kh&aacute;ch đ&atilde; v&agrave; sẽ tiếp tục đồng h&agrave;nh c&ugrave;ng Bảo hiểm VietinBank (VBI)!</p>', '2018-03-09 18:59:59', '2018-03-09 18:59:59', 'images/news/1520578799_multi-loc_636144853340506546_HasThumb_Thumb.jpg.png'),
(195, 2, 'Thông báo chuyển địa điểm nhận hồ sơ yêu cầu trả tiền bảo hiểm sức khỏe trên toàn quốc', 'thong-bao-chuyen-dia-diem-nhan-ho-so-yeu-cau-tra-tien-bao-hiem-suc-khoe-tren-toan-quoc', '<p><span style=\"font-family: Arial;\">Bắt đầu từ ng&agrave;y 01/12/2016, Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; hợp đồng bảo hiểm sức khỏe VBI Care hiệu lực kể từ ng&agrave;y 01/12/2016 vui l&ograve;ng gửi hồ sơ y&ecirc;u cầu trả tiền bảo hiểm về địa chỉ: Ph&ograve;ng con người - Ban Bồi thường - Bảo hiểm VietinBank (VBI) Tầng 7, T&ograve;a nh&agrave; VietinBank, 126 Đội Cấn, Quận B...&nbsp;</span></p>', '<p>Bảo hiểm VietinBank (VBI) xin tr&acirc;n trọng th&ocirc;ng b&aacute;o!</p>\r\n<p>Bắt đầu từ ng&agrave;y 01/12/2016, Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; hợp đồng bảo hiểm sức khỏe VBI Care hiệu lực kể từ ng&agrave;y 01/12/2016 vui l&ograve;ng gửi hồ sơ y&ecirc;u cầu trả tiền bảo hiểm về địa chỉ:</p>\r\n<p><strong>Ph&ograve;ng con người - Ban Bồi thường - Bảo hiểm VietinBank (VBI)</strong><br /><strong>Tầng 7, T&ograve;a nh&agrave; VietinBank, 126 Đội Cấn, Quận Ba Đ&igrave;nh, Tp. H&agrave; Nội</strong></p>\r\n<p><img src=\"../../../uploads/Change location-2.jpg\" alt=\"\" width=\"1142\" height=\"340\" /></p>\r\n<p>C&aacute;c kh&aacute;ch h&agrave;ng c&oacute; hợp đồng bảo hiểm sức khỏe VBI Care hiệu lực kết th&uacute;c v&agrave;o ng&agrave;y 30/11/2016, vui l&ograve;ng gửi hồ sơ y&ecirc;u cầu trả tiền bảo hiểm về địa chỉ:</p>\r\n<ul>\r\n<li>Tại H&agrave; Nội: Insmart H&agrave; Nội<br />Tầng 25, T&ograve;a Nh&agrave; Ngọc Kh&aacute;nh Plaza, Số 1 Phạm Huy Th&ocirc;ng, Quận Ba Đ&igrave;nh, Tp. H&agrave; Nội.</li>\r\n<li>Tại Tp. Hồ Ch&iacute; Minh: Insmart Hồ Ch&iacute; Minh<br />Tầng 3A, T&ograve;a nh&agrave; Nikko, số 374-374B V&otilde; Văn Tần, Phường 5, Quận 3, TP. Hồ Ch&iacute; Minh<br /> <br />Để biết th&ecirc;m th&ocirc;ng tin chi tiết, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ Hotline: 0934 596 339/ 0934 581 339 hoặc 04 3211 5140.</li>\r\n</ul>\r\n<p>Cảm ơn qu&yacute; kh&aacute;ch đ&atilde; v&agrave; sẽ tiếp tục đồng h&agrave;nh c&ugrave;ng Bảo hiểm VietinBank (VBI)!</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:25:02', '2018-03-21 19:25:02', 'images/news/1520578919_Change-lo_636153222893961853_HasThumb_Thumb.jpg.png'),
(196, 2, 'VBI tặng quà cho 140 hộ dân có hoàn cảnh khó khăn tại Hà Tĩnh', 'vbi-tang-qua-cho-140-ho-dan-co-hoan-canh-kho-khan-tai-ha-tinh', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/11/2016, tại UBND x&atilde; Kỳ Lạc, tỉnh H&agrave; Tĩnh, Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank (VBI) đ&atilde; phối hợp với Đo&agrave;n thanh ni&ecirc;n VietinBank Chi nh&aacute;nh H&agrave; Tĩnh tổ chức chương tr&igrave;nh tặng qu&agrave; cho 140 hộ d&acirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn trong địa b&agrave;n tỉnh.&nbsp;</span></p>', '<p>Ng&agrave;y 29/11/2016, tại UBND x&atilde; Kỳ Lạc, tỉnh H&agrave; Tĩnh, Đo&agrave;n thanh ni&ecirc;n Bảo hiểm VietinBank (VBI) đ&atilde; phối hợp với Đo&agrave;n thanh ni&ecirc;n VietinBank Chi nh&aacute;nh H&agrave; Tĩnh tổ chức chương tr&igrave;nh tặng qu&agrave; cho 140 hộ d&acirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn trong địa b&agrave;n tỉnh.</p>\r\n<p>Tại chương tr&igrave;nh, Đo&agrave;n thanh ni&ecirc;n VBI đ&atilde; trao tặng 140 phần qu&agrave; trị gi&aacute; 70 triệu đồng đ&ecirc;́n t&acirc;̣n tay 140 hộ d&acirc;n tại x&atilde; Kỳ Lạc, m&ocirc;̃i ph&acirc;̀n quà bao gồm: chăn ấm, m&igrave; g&oacute;i, dầu ăn, đường, sữa và 200.000 VNĐ ti&ecirc;̀n mặt.</p>\r\n<p>L&agrave; một đơn vị hoạt động trong lĩnh vực t&agrave;i ch&iacute;nh, VBI &yacute; thức r&otilde; tầm quan trọng v&agrave; sự cần thiết trong việc đ&oacute;ng g&oacute;p v&agrave;o sự ph&aacute;t triển bền vững v&agrave; l&acirc;u d&agrave;i của cộng đồng. VBI lu&ocirc;n coi tr&aacute;ch nhiệm với cộng đồng l&agrave; một trong những ưu ti&ecirc;n trong chiến lược ph&aacute;t triển của m&igrave;nh. Trong năm 2016, VBI đ&atilde; triển khai chương tr&igrave;nh từ thiện kh&aacute;m chữa bệnh v&agrave; tặng bảo hiểm sức khỏe cho cộng đồng người ngh&egrave;o tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng. Những chương tr&igrave;nh hỗ trợ cộng đồng của VBI kh&ocirc;ng chỉ thể hiện tr&aacute;ch nhiệm x&atilde; hội m&agrave; c&ograve;n minh chứng cho l&agrave; tấm l&ograve;ng thiện nguyện của tập thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n VBI.</p>\r\n<p>Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh nổi bật của chương tr&igrave;nh tại H&agrave; Tĩnh:</p>\r\n<p><img src=\"../../../uploads/IMG_8373.jpg\" alt=\"\" width=\"1366\" height=\"1112\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8293(1).jpg\" alt=\"\" width=\"941\" height=\"1200\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8330(1).jpg\" alt=\"\" width=\"875\" height=\"1200\" /></p>\r\n<p><img src=\"../../../uploads/IMG_8285.jpg\" alt=\"\" width=\"930\" height=\"1200\" /></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:36:46', '2018-03-21 19:36:46', 'images/news/1520579013_IMG_837_636160344708385469_HasThumb_Thumb.JPG.png'),
(197, 2, 'VBI bổ nhiệm Phó Giám đốc Chi nhánh Hà Nội', 'vbi-bo-nhiem-pho-giam-doc-chi-nhanh-ha-noi', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 2/12/2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Lễ c&ocirc;ng bố quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh H&agrave; Nội cho &ocirc;ng Phạm Duy Hợi. Lễ c&ocirc;ng bố c&oacute; sự tham gia của đại diện Ban L&atilde;nh đạo VBI c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh H&agrave; Nội.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 2/12/2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Lễ c&ocirc;ng bố quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh H&agrave; Nội cho &ocirc;ng Phạm Duy Hợi. Lễ c&ocirc;ng bố c&oacute; sự tham gia của đại diện Ban L&atilde;nh đạo VBI c&ugrave;ng to&agrave;n thể CBNV Chi nh&aacute;nh H&agrave; Nội.<br /> <br />Tại buổi lễ, Tổng gi&aacute;m đốc VBI - &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; trao quyết định bổ nhiệm v&agrave; ch&uacute;c mừng &ocirc;ng Phạm Duy Hợi nhận nhiệm vụ mới. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ mong muốn, với hơn 10 năm kinh nghiệm trong lĩnh vực bảo hiểm, &ocirc;ng Phạm Duy Hợi sẽ c&ugrave;ng với VBI H&agrave; Nội ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh.</span></p>\r\n<p><img src=\"../../../uploads/IMG_3114.jpg\" alt=\"\" width=\"1366\" height=\"966\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Tổng Gi&aacute;m đốc VBI trao quyết định bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh H&agrave; Nội)</span></em></p>\r\n<p><img src=\"../../../uploads/IMG_3117a.jpg\" alt=\"\" width=\"1366\" height=\"651\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh tại Lễ c&ocirc;ng bố bổ nhiệm Ph&oacute; Gi&aacute;m đốc Chi nh&aacute;nh H&agrave; Nội)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, &ocirc;ng Phạm Duy Hợi thể hiện quyết t&acirc;m phấn đấu, nỗ lực kh&ocirc;ng ngừng để c&ugrave;ng VBI H&agrave; Nội ho&agrave;n th&agrave;nh mục ti&ecirc;u kinh doanh m&agrave; Ban l&atilde;nh đạo C&ocirc;ng ty đ&atilde; giao, đồng thời cam kết sẽ tiếp tục cống hiến, đưa VBI H&agrave; Nội ph&aacute;t triển mạnh mẽ hơn nữa.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:37:36', '2018-03-21 19:37:36', 'images/news/1520579201_IMG_311_636165565600684013_HasThumb_Thumb.JPG.png'),
(198, 2, 'VBI bổ nhiệm Giám đốc Ban khách hàng', 'vbi-bo-nhiem-giam-doc-ban-khach-hang', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 8/12/2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Lễ c&ocirc;ng bố quyết định bổ nhiệm Gi&aacute;m đốc Ban kh&aacute;ch h&agrave;ng cho b&agrave; Kiều An. Lễ c&ocirc;ng bố c&oacute; sự tham gia của đại diện Ban L&atilde;nh đạo VBI c&ugrave;ng to&agrave;n thể CBNV Trụ sở ch&iacute;nh.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 8/12/2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức Lễ c&ocirc;ng bố quyết định bổ nhiệm Gi&aacute;m đốc Ban kh&aacute;ch h&agrave;ng cho b&agrave; Kiều An. Lễ c&ocirc;ng bố c&oacute; sự tham gia của đại diện Ban L&atilde;nh đạo VBI c&ugrave;ng to&agrave;n thể CBNV Trụ sở ch&iacute;nh.<br /> <br />Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng - Tổng gi&aacute;m đốc VBI đ&atilde; trao quyết định bổ nhiệm v&agrave; tặng hoa ch&uacute;c mừng b&agrave; Kiều An - Gi&aacute;m đốc Ban kh&aacute;ch h&agrave;ng. B&ecirc;n cạnh đ&oacute;, &ocirc;ng L&ecirc; Tuấn Dũng cũng chia sẻ niềm tin tưởng v&agrave; kỳ vọng, với 20 năm kinh nghiệm trong ng&agrave;nh bảo hiểm, b&agrave; Kiều An sẽ gi&uacute;p VBI tăng trưởng doanh thu mạnh mẽ trong thời gian tới.<br /> <br />Trước khi gia nhập VBI, B&agrave; Kiều An đ&atilde; đảm nhiệm c&aacute;c vị tr&iacute; quan trọng từ quản l&yacute; nghiệp vụ đến t&aacute;i bảo hiểm v&agrave; ph&aacute;t triển kinh doanh tại c&aacute;c doanh nghiệp bảo hiểm lớn tại Việt Nam như Tổng C&ocirc;ng ty Bảo hiểm Bảo Việt, Tổng C&ocirc;ng ty Bảo hiểm PJICO v&agrave; c&aacute;c c&ocirc;ng ty bảo hiểm lớn tr&ecirc;n thế giới như C&ocirc;ng ty m&ocirc;i giới bảo hiểm Gras Savoye, C&ocirc;ng ty Bảo hiểm QBE.<br /> <br />Đ&aacute;p lại sự tin tưởng của Ban l&atilde;nh đạo VBI, b&agrave; Kiều An - Gi&aacute;m đốc Ban kh&aacute;ch h&agrave;ng đ&atilde; thể hiện quyết t&acirc;m phấn đấu, nỗ lực kh&ocirc;ng ngừng để ho&agrave;n th&agrave;nh tốt nhiệm vụ được giao, cống hiến hết m&igrave;nh, x&acirc;y dựng VBI ng&agrave;y c&agrave;ng vững mạnh.</span></p>\r\n<p><img src=\"../../../uploads/IMG_8468 - Copy.jpg\" alt=\"\" width=\"1366\" height=\"989\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Tổng Gi&aacute;m đốc VBI trao quyết định bổ nhiệm Gi&aacute;m đốc Ban kh&aacute;ch h&agrave;ng)</span></em></p>\r\n<p><img src=\"../../../uploads/IMG_8470 - Copy.jpg\" alt=\"\" width=\"1024\" height=\"668\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(T&acirc;n Gi&aacute;m đốc Ban kh&aacute;ch h&agrave;ng chụp ảnh c&ugrave;ng Ban l&atilde;nh đạo VBI)</span></em></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:38:17', '2018-03-21 19:38:17', 'images/news/1520579344_IMG_8468-_636168141190634428_HasThumb_Thumb.JPG.png'),
(199, 2, 'VBI thông báo lịch nghỉ Tết dương lịch 2017', 'vbi-thong-bao-lich-nghi-tet-duong-lich-2017', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank sẽ nghỉ Tết dương lịch từ ng&agrave;y 01/01/2017 tới hết ng&agrave;y 02/01/2017. Ng&agrave;y 03/01/2017, VBI sẽ trở lại hoạt động b&igrave;nh thường.&nbsp;</span></p>', '<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch h&agrave;ng,</strong></p>\r\n<p>Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.<br /> <br />Nh&acirc;n dịp năm mới 2017, VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch h&agrave;ng một năm mới nhiều sức khỏe, an khang thịnh vượng, vạn sự như &yacute;.<br /> <br />Bảo hiểm VietinBank sẽ nghỉ Tết dương lịch từ ng&agrave;y 01/01/2017 tới hết ng&agrave;y 02/01/2017. Ng&agrave;y 03/01/2017, VBI sẽ trở lại hoạt động b&igrave;nh thường.<br /> <br /><strong>Tr&acirc;n trọng cảm ơn!</strong></p>\r\n<p><img src=\"../../../uploads/ecard_tieng viet.jpg\" alt=\"\" width=\"1141\" height=\"434\" /></p>', '2018-03-21 08:38:42', '2018-03-21 19:38:42', 'images/news/1520579652_ecard_tie_636185990149142681_HasThumb_Thumb.jpg.png'),
(200, 2, 'VBI chi trả 120 triệu đồng tiền bồi thường bảo hiểm người vay vốn tại Thanh Hóa', 'vbi-chi-tra-120-trieu-dong-tien-boi-thuong-bao-hiem-nguoi-vay-von-tai-thanh-hoa', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/12/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Thanh H&oacute;a đ&atilde; tổ chức chi trả 120 triệu đồng tiền bồi thường bảo hiểm người vay vốn cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Ho&agrave;ng Văn Hải, tr&uacute; tại Th&ocirc;n T&acirc;n Hưng &ndash; Hậu Lộc &ndash; Thanh H&oacute;a.&nbsp;</span></p>', '<p>Ng&agrave;y 29/12/2016, Bảo hiểm VietinBank Chi nh&aacute;nh Thanh H&oacute;a đ&atilde; tổ chức chi trả 120 triệu đồng tiền bồi thường bảo hiểm người vay vốn cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Ho&agrave;ng Văn Hải, tr&uacute; tại Th&ocirc;n T&acirc;n Hưng &ndash; Hậu Lộc &ndash; Thanh H&oacute;a.<br /> <br />Năm 2015, &ocirc;ng Ho&agrave;ng Văn Hải vay vốn 120 triệu đồng tại VietinBank CN Bỉm Sơn v&agrave; được c&aacute;n bộ ng&acirc;n h&agrave;ng tư vấn tham gia dịch vụ bảo hiểm người vay vốn. Trong một chuyến đi biển v&agrave;o th&aacute;ng 9 năm 2016, do s&oacute;ng to gi&oacute; lớn, biển động mạnh, &ocirc;ng Ho&agrave;ng Văn Hải đ&atilde; kh&ocirc;ng may qua đời. Ngay sau khi nhận được th&ocirc;ng tin, Bảo hiểm VietinBank CN Thanh H&oacute;a đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank CN Bỉm Sơn hướng dẫn gia đ&igrave;nh ho&agrave;n thiện hồ sơ để được chi trả bồi thường bảo hiểm theo đ&uacute;ng quy định.</p>\r\n<p>Trong buổi nhận tiền bồi thường, đại diện gia đ&igrave;nh kh&aacute;ch h&agrave;ng Ho&agrave;ng Văn Hải đ&atilde; gửi lời cảm ơn đến Bảo hiểm VietinBank Chi nh&aacute;nh Thanh H&oacute;a đ&atilde; tạo mọi điều kiện hỗ trợ trong l&uacute;c gia đ&igrave;nh gặp kh&oacute; khăn.</p>\r\n<p><img src=\"../../../uploads/anh trao boi thuong.jpg\" alt=\"\" width=\"1366\" height=\"937\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng)</em></p>\r\n<p>Bảo hiểm người vay vốn l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.<br /> <br />Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 8 tỷ đồng bảo hiểm người vay vốn cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng v&agrave; chất lượng dịch vụ vượt trội, VBI đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ uy t&iacute;n năm 2016 do Vietnam Report c&ocirc;ng bố.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:39:13', '2018-03-21 19:39:13', 'images/news/1520579731_anh-trao-_636187040341692543_HasThumb_Thumb.jpg.png'),
(201, 2, 'VBI đạt chứng nhận ISO 9001:2015', 'vbi-dat-chung-nhan-iso-9001-2015', '<p><span style=\"font-family: Arial;\">Tổng cục ti&ecirc;u chuẩn đo lường chất lượng - Trung t&acirc;m chứng nhận ph&ugrave; hợp (Quacert) vừa cấp Giấy chứng nhận ISO 9001:2015 về Hệ thống Quản l&yacute; Chất lượng cho Bảo hiểm VietinBank (VBI).&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Tổng cục ti&ecirc;u chuẩn đo lường chất lượng - Trung t&acirc;m chứng nhận ph&ugrave; hợp (Quacert) vừa cấp Giấy chứng nhận ISO 9001:2015 về Hệ thống Quản l&yacute; Chất lượng cho Bảo hiểm VietinBank (VBI).<br /> <br />Chứng nhận ISO 9001:2015 được cấp cho hệ thống quản l&yacute; chất lượng VBI dựa tr&ecirc;n c&aacute;c lĩnh vực cốt l&otilde;i: Quản l&yacute;, điều h&agrave;nh hoạt động ph&aacute;t triển v&agrave; cung cấp dịch vụ bảo hiểm phi nh&acirc;n thọ, t&aacute;i bảo hiểm v&agrave; đầu tư t&agrave;i ch&iacute;nh, bao gồm: Bảo hiểm sức khỏe v&agrave; bảo hiểm tai nạn con người; Bảo hiểm t&agrave;i sản v&agrave; bảo hiểm thiệt hại; Bảo hiểm h&agrave;ng h&oacute;a vận chuyển đường bộ, đường sắt, đường biển, đường s&ocirc;ng v&agrave; đường h&agrave;ng kh&ocirc;ng; Bảo hiểm xe cơ giới; Bảo hiểm ch&aacute;y, nổ; Bảo hiểm th&acirc;n t&agrave;u v&agrave; tr&aacute;ch nhiệm d&acirc;n sự chủ t&agrave;u; Bảo hiểm tr&aacute;ch nhiệm chung; Bảo hiểm thiệt hại kinh doanh.</span></p>\r\n<p><img src=\"../../../uploads/Giay chung nhan Quacert.jpg\" alt=\"\" width=\"1159\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(H&igrave;nh ảnh Giấy chứng nhận ISO 9001:2015 của Bảo hiểm VietinBank)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Hệ thống Quản l&yacute; chất lượng theo ti&ecirc;u chuẩn ISO 9001:2015 l&agrave; một trong những c&ocirc;ng cụ hữu hiệu, được c&aacute;c tổ chức, doanh nghiệp tr&ecirc;n khắp thế giới &aacute;p dụng hiệu quả để quản l&yacute; chất lượng hoạt động, n&acirc;ng cao chất lượng sản phẩm dịch vụ, g&oacute;p phần n&acirc;ng cao vị thế cạnh tranh của doanh nghiệp.<br /> <br />Việc &aacute;p dụng ti&ecirc;u chuẩn tr&ecirc;n đ&atilde; gi&uacute;p VBI tăng cường cải tiến hoạt động trong quản trị điều h&agrave;nh, quản l&yacute; chất lượng v&agrave; tăng hiệu quả l&agrave;m việc của c&aacute;n bộ nh&acirc;n vi&ecirc;n. B&ecirc;n cạnh đ&oacute;, VBI c&ograve;n tiết kiệm thời gian, chi ph&iacute;, nguồn lực trong c&aacute;c hoạt động, từ đ&oacute; gia tăng lợi nhuận v&agrave; mang lại c&aacute;c gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng.<br /> <br />Với định hướng ph&aacute;t triển bền vững, chiến lược kinh doanh d&agrave;i hạn, ch&uacute; trọng n&acirc;ng cao chất lượng dịch vụ kh&aacute;ch h&agrave;ng, trong năm 2017, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.<br /></span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 08:39:36', '2018-03-21 19:39:36', 'images/news/1520579858_Giay-chun_636191431298914607_HasThumb_Thumb.jpg.png'),
(202, 2, 'Bảo hiểm VietinBank: Vươn tầm cao mới', 'bao-hiem-vietinbank-vuon-tam-cao-moi', '<p><span style=\"font-family: Arial;\">Năm 2016 đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của Bảo hiểm VietinBank (VBI) với những chuyển biến tăng trưởng mạnh mẽ về mọi mặt. Kết th&uacute;c năm 2016, doanh thu của VBI đạt 781 tỷ đồng, tăng trưởng</span><span style=\"font-family: Arial;\"> 43%</span><span style=\"font-family: Arial;\"> so với năm 2015; lợi nhuận đạt 75,1 tỷ đồng, tăng trưởng 10% so với năm 2015.&nbsp;</span></p>', '<p><em><strong>Năm 2016 đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của Bảo hiểm VietinBank (VBI) với những chuyển biến tăng trưởng mạnh mẽ về mọi mặt. Kết th&uacute;c năm 2016, doanh thu của VBI đạt 781 tỷ đồng, tăng trưởng 43% so với năm 2015; lợi nhuận đạt 75,1 tỷ đồng, tăng trưởng 10% so với năm 2015.</strong></em></p>\r\n<p><strong>Những bước ph&aacute;t triển đột ph&aacute;</strong></p>\r\n<p>Chặng đường 8 năm x&acirc;y dựng v&agrave; ph&aacute;t triển, VBI đ&atilde; c&oacute; những bước tăng trưởng li&ecirc;n tục về quy m&ocirc; hoạt động v&agrave; số lượng kh&aacute;ch h&agrave;ng. Từ chỗ chỉ c&oacute; 1 trụ sở ch&iacute;nh v&agrave; 2 chi nh&aacute;nh, đến nay, VBI đ&atilde; c&oacute; hệ thống mạng lưới gồm 15 chi nh&aacute;nh v&agrave; 65 ph&ograve;ng kinh doanh khu vực tr&ecirc;n to&agrave;n quốc. Đặc biệt, VBI c&ograve;n c&oacute; hệ thống khai th&aacute;c bảo hiểm rộng lớn tại hơn 1000 chi nh&aacute;nh/ ph&ograve;ng giao dịch của VietinBank, GPBank v&agrave; OceanBank trải khắp c&aacute;c tỉnh/th&agrave;nh Việt Nam.<br /> <br />Năm 2016, VBI đ&atilde; c&oacute; nhiều &ldquo;dấu son&rdquo; th&agrave;nh c&ocirc;ng khi tăng trưởng mạnh mẽ cả về doanh thu, lợi nhuận lẫn cơ cấu bộ m&aacute;y tổ chức. Doanh thu ph&iacute; bảo hiểm đạt 781 tỷ, tăng trưởng 43% so với năm 2015; lợi nhuận đạt 75,1 tỷ đồng, tăng trưởng 10% so với năm 2015; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 42,9%.<br /> <br />Kh&ocirc;ng chỉ kinh doanh an to&agrave;n, hiệu quả, VBI cũng lu&ocirc;n l&agrave; đơn vị thực hiện đầy đủ v&agrave; kịp thời nghĩa vụ nộp ng&acirc;n s&aacute;ch. Trong 4 năm li&ecirc;n tiếp từ 2012 - 2015, VBI li&ecirc;n tục được vinh danh trong Top 1000 đơn vị nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam. Cũng trong năm 2016, VBI đ&atilde; vinh dự nhận nhiều danh hiệu như: &ldquo;Top 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam&rdquo;, &ldquo;Top 20 doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng&rdquo;, &ldquo;Top 100 thương hiệu mạnh Việt Nam&rdquo;. Gần đ&acirc;y nhất, ng&agrave;y 09/01/2017, VBI 4 năm li&ecirc;n tiếp được vinh danh &ldquo;Đơn vị ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh 2016\" tại Hội nghị Tổng kết hoạt động kinh doanh năm 2016 do VietinBank tổ chức. Những danh hiệu tr&ecirc;n đ&atilde; minh chứng phần n&agrave;o cho những bước ph&aacute;t triển đột ph&aacute; của VBI.</p>\r\n<p><img src=\"../../../uploads/VBI xuat sac 2016.jpg\" alt=\"\" width=\"1024\" height=\"579\" /></p>\r\n<p style=\"text-align: center;\"><em>Tổng Gi&aacute;m đốc VBI nhận bằng khen \"Đơn vị ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh 2016\"</em></p>\r\n<p><strong>Dấu ấn ri&ecirc;ng tr&ecirc;n thị trường bảo hiểm</strong></p>\r\n<p>Đến nay, VBI l&agrave; thương hiệu tin cậy đối với kh&aacute;ch h&agrave;ng ở c&aacute;c dịch vụ bảo hiểm, bao gồm: Bảo hiểm sức khỏe, bảo hiểm &ocirc; t&ocirc;, bảo hiểm t&agrave;i sản, bảo hiểm h&agrave;ng h&oacute;a, v.v&hellip;<br /> <br />Th&aacute;ng 10/2016, để tiếp tục n&acirc;ng cao năng lực cạnh tranh v&agrave; năng lực điều h&agrave;nh, VBI đ&atilde; triển khai chuyển đổi m&ocirc; h&igrave;nh mới theo hướng cổ phần h&oacute;a. &Ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc VBI nhấn mạnh: C&ocirc;ng ty triển khai chuyển đổi m&ocirc; h&igrave;nh mới nhằm đ&aacute;p ứng nhu cầu ng&agrave;y một ph&aacute;t triển của doanh nghiệp. Trong thời gian tới, VBI sẽ tiếp tục đổi mới c&ocirc;ng t&aacute;c quản trị, đồng thời cải tiến mạnh mẽ hệ thống c&ocirc;ng nghệ hiện đại, hệ thống dịch vụ b&aacute;n h&agrave;ng v&agrave; sau b&aacute;n h&agrave;ng&rdquo;.<br /> <br />Với c&aacute;ch thức quản l&yacute; hiện đại, quy tr&igrave;nh l&agrave;m việc chuy&ecirc;n nghiệp, đảm bảo an to&agrave;n, th&aacute;ng 12/2016, VBI đ&atilde; đạt chứng nhận ISO 9001:2015 về Hệ thống Quản l&yacute; Chất lượng do Tổng cục ti&ecirc;u chuẩn đo lường chất lượng - Trung t&acirc;m chứng nhận ph&ugrave; hợp (Quacert) cấp. Việc &aacute;p dụng ti&ecirc;u chuẩn ISO 9001:2015 đ&atilde; gi&uacute;p VBI tăng cường cải tiến hoạt động trong quản trị điều h&agrave;nh, quản l&yacute; chất lượng v&agrave; tăng hiệu quả l&agrave;m việc của c&aacute;n bộ, nh&acirc;n vi&ecirc;n.<br /> <br />Hiện nay, VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng với nhiều ưu điểm nổi trội. Điều n&agrave;y gi&uacute;p kh&aacute;ch h&agrave;ng của VBI tiếp cận v&agrave; c&oacute; được c&aacute;c quyền lợi bảo hiểm nhanh ch&oacute;ng v&agrave; an to&agrave;n hơn, theo h&igrave;nh thức hiện đại nhất tr&ecirc;n thị trường bảo hiểm.<br /> <br />Trong năm 2017, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp SPDV bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng. &ldquo;Ch&uacute;ng t&ocirc;i lu&ocirc;n phấn đấu v&igrave; mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam&rdquo; - &Ocirc;ng Dũng khẳng định.<br /> <br />Với định hướng ph&aacute;t triển bền vững, chiến lược kinh doanh d&agrave;i hạn, VBI kh&ocirc;ng ngừng ch&uacute; trọng n&acirc;ng cao chất lượng dịch vụ kh&aacute;ch h&agrave;ng, g&oacute;p phần đưa thương hiệu VietinBank n&oacute;i chung v&agrave; Bảo hiểm VietinBank n&oacute;i ri&ecirc;ng vươn cao, vươn xa tr&ecirc;n thị trường trong nước v&agrave; quốc tế.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:40:07', '2018-03-21 19:40:07', 'images/news/1520580482_VBI-xuat-_636196674299203809_HasThumb_Thumb.jpg.png'),
(203, 2, 'VBI bảo hiểm giải thưởng Hole in one trị giá hơn 1,5 tỷ đồng tại sân golf Hoàng Gia', 'vbi-bao-hiem-giai-thuong-hole-in-one-tri-gia-hon-1-5-ty-dong-tai-san-golf-hoang-gia', '<p><span style=\"font-family: Arial;\">VBI tham gia bảo hiểm giải thưởng Hole-in-one trị gi&aacute; hơn 1,5 tỷ đồng cho 02 Bộ sản phẩm thiết bị FAGOR trị gi&aacute; hơn 1 tỷ đồng v&agrave; 01 thẻ t&iacute;n dụng VietinBank trị gi&aacute; 500 triệu đồng tại \"Giải golf C&uacute;p M&ugrave;a Xu&acirc;n Ch&agrave;o Mừng Ng&agrave;y Khai Trương CLB Golf Ho&agrave;ng Gia Lần Thứ 7&rdquo;.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 07/01/2017 tại s&acirc;n golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh, đ&atilde; diễn ra &ldquo;Giải golf C&uacute;p M&ugrave;a Xu&acirc;n Ch&agrave;o Mừng Ng&agrave;y Khai Trương CLB Golf Ho&agrave;ng Gia Lần Thứ 7&rdquo;. Giải c&oacute; tham dự của nhiều Golfer đến từ c&aacute;c tập đo&agrave;n, doanh nghiệp, hiệp hội Golf từ khắp c&aacute;c tỉnh, th&agrave;nh tr&ecirc;n cả nước với h&igrave;nh thức Đấu gậy theo hệ thống t&iacute;nh điểm Handicap system 36.<br /> <br />VBI tham gia bảo hiểm giải thưởng Hole-in-one trị gi&aacute; hơn 1,5 tỷ đồng cho 02 Bộ sản phẩm thiết bị FAGOR trị gi&aacute; hơn 1 tỷ đồng v&agrave; 01 thẻ t&iacute;n dụng VietinBank trị gi&aacute; 500 triệu đồng.<br /> <br />Kh&eacute;p lại ng&agrave;y thi đấu s&ocirc;i nổi v&agrave; h&agrave;o hứng, c&aacute;c golfer đ&atilde; cống hiến những đường b&oacute;ng đẹp, thể hiện những kỹ thuật đi&ecirc;u luyện, chuy&ecirc;n nghiệp, thi đấu với tinh thần fair-play. Kết th&uacute;c Giải, Ban Tổ chức trao giải cho c&aacute;c golfer như sau: 1 Giải Best Gross; 4 giải Nhất; 4 giải Nh&igrave;; 4 giải Ba v&agrave; 6 giải Kỹ thuật.</span></p>\r\n<p><img src=\"../../../uploads/holeinone7.jpg\" alt=\"\" width=\"1366\" height=\"910\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: &Ocirc;ng L&ecirc; Thanh H&agrave; &ndash;Ph&oacute; Gi&aacute;m đốc trung t&acirc;m thẻ VietinBank trao giải Bảng C)</span></em></p>\r\n<p style=\"text-align: left;\"><span style=\"font-family: Arial;\">Trong thời gian qua, Bảo hiểm VietinBank (VBI) lu&ocirc;n l&agrave; một trong những c&ocirc;ng ty h&agrave;ng đầu Việt Nam bảo hiểm cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 08:41:15', '2018-03-21 19:41:15', 'images/news/1520580573_holeinone_636197395634090573_HasThumb_Thumb.jpg.png'),
(204, 2, 'VBI bảo hiểm giải thưởng Hole in one trị giá hơn 1 tỷ đồng', 'vbi-bao-hiem-giai-thuong-hole-in-one-tri-gia-hon-1-ty-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 15/01/2017 tại s&acirc;n golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh đ&atilde; diễn ra Giải Golf friends Nam Định 2017. VBI tham gia bảo hiểm giải thưởng Hole-in-one l&agrave; một chiếc xe &ocirc; t&ocirc; Camry 2.0 trị gi&aacute; hơn 1 tỷ đồng.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 15/01/2017 tại s&acirc;n golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh đ&atilde; diễn ra Giải Golf friends Nam Định 2017. Giải c&oacute; sự tham dự của 100 golf thủ đến từ c&aacute;c tập đo&agrave;n, doanh nghiệp, hiệp hội Golf tr&ecirc;n địa b&agrave;n tỉnh Nam Định.<br /> <br />C&aacute;c golfer thi đấu tr&ecirc;n s&acirc;n golf 18 lỗ theo thể thức handicap, với cơ cấu giải: Nhất, Nh&igrave;, Ba (theo 3 bảng thi đấu), giải Kỹ thuật v&agrave; giải Đặc biệt Hole in one. Kh&eacute;p lại ng&agrave;y thi đấu s&ocirc;i nổi, c&aacute;c golfer đ&atilde; cống hiến những đường b&oacute;ng đẹp, thể hiện những kỹ thuật đi&ecirc;u luyện, chuy&ecirc;n nghiệp.<br /> <br />VBI tham gia bảo hiểm giải thưởng Hole-in-one l&agrave; một chiếc xe &ocirc; t&ocirc; Camry 2.0 trị gi&aacute; hơn 1 tỷ đồng. Hiện nay, Bảo hiểm VietinBank (VBI) l&agrave; một trong những c&ocirc;ng ty h&agrave;ng đầu Việt Nam bảo hiểm cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</span></p>\r\n<p><img src=\"../../../uploads/Hole in one 15_01.jpg\" alt=\"\" width=\"570\" height=\"308\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Golfer thi đấu tại Giải Golf friends Nam Định 2017</span></em></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:41:45', '2018-03-21 19:41:45', 'images/news/1520580685_Hole-in-o_636202738042163764_HasThumb_Thumb.jpg.png'),
(205, 2, 'Bảo hiểm VietinBank thông báo lịch nghỉ Tết Đinh Dậu 2017', 'bao-hiem-vietinbank-thong-bao-lich-nghi-tet-dinh-dau-2017', '<p><span style=\"font-family: Arial;\">K&iacute;nh gửi Qu&yacute; kh&aacute;ch, Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.&nbsp;</span></p>', '<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch,</strong></p>\r\n<p>Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch đ&atilde; tin tưởng sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.</p>\r\n<p>Nh&acirc;n dịp năm mới Đinh Dậu, VBI k&iacute;nh ch&uacute;c Qu&yacute; kh&aacute;ch một năm mới nhiều sức khỏe, an khang thịnh vượng, vạn sự như &yacute;.</p>\r\n<p>Bảo hiểm VietinBank sẽ nghỉ Tết từ ng&agrave;y 26 th&aacute;ng 01 năm 2017 tới ng&agrave;y 01 th&aacute;ng 02 năm 2017. Ng&agrave;y 02 th&aacute;ng 02 năm 2017, VBI sẽ trở lại hoạt động b&igrave;nh thường.</p>\r\n<p><strong>Tr&acirc;n trọng cảm ơn!</strong></p>\r\n<p><img src=\"../../../uploads/lich-banner-website-.jpg\" alt=\"\" width=\"960\" height=\"303\" /></p>', '2018-03-21 08:42:13', '2018-03-21 19:42:13', 'images/news/1520580756_pic-New-i_636203543472973961_HasThumb_Thumb.jpg.png'),
(206, 2, 'Chương trình tình nguyện “Nồi cháo tình thương” 2017', 'chuong-trinh-tinh-nguyen-noi-chao-tinh-thuong-2017', '<p><span style=\"font-family: Arial;\">Với tinh thần chia sẻ kh&oacute; khăn c&ugrave;ng cộng đồng, mang đến cho c&aacute;c bệnh nh&acirc;n ngh&egrave;o đang điều trị tại Bệnh nhi h&agrave;ng trăm suất ch&aacute;o đủ dinh dưỡng, ấm &aacute;p t&igrave;nh người, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; l&agrave; một hoạt động thường ni&ecirc;n mang &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc của Đo&agrave;n thanh ni&ecirc;n VBI.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Trong suốt một tuần qua, c&aacute;c Đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI lu&ocirc;n bắt đầu một ng&agrave;y mới đầy &yacute; nghĩa bằng chuyến h&agrave;nh tr&igrave;nh đến thăm c&aacute;c bệnh nh&acirc;n ở Bệnh viện Nhi Trung Ương. Với tinh thần chia sẻ kh&oacute; khăn c&ugrave;ng cộng đồng, mang đến cho c&aacute;c bệnh nh&acirc;n ngh&egrave;o đang điều trị tại Bệnh nhi h&agrave;ng trăm suất ch&aacute;o đủ dinh dưỡng, ấm &aacute;p t&igrave;nh người, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; l&agrave; một hoạt động thường ni&ecirc;n mang &yacute; nghĩa nh&acirc;n văn s&acirc;u sắc của Đo&agrave;n thanh ni&ecirc;n VBI. B&ecirc;n cạnh việc ph&aacute;t ch&aacute;o, Đo&agrave;n vi&ecirc;n thanh ni&ecirc;n VBI c&ograve;n thăm hỏi động vi&ecirc;n bệnh nh&acirc;n v&agrave; người nh&agrave; vượt qua kh&oacute; khăn, y&ecirc;n t&acirc;m điều trị.</span></p>\r\n<p><img src=\"../../../uploads/IMG_4602.jpg\" alt=\"\" width=\"640\" height=\"480\" /></p>\r\n<p><img src=\"../../../uploads/2222.jpg\" alt=\"\" width=\"640\" height=\"480\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: c&aacute;c Đo&agrave;n vi&ecirc;n VBI ph&aacute;t ch&aacute;o tại Bệnh viện Nhi Trung Ương)</span></em></p>\r\n<p>C&ugrave;ng với những cam kết về chất lượng dịch vụ, VBI lu&ocirc;n ch&uacute; trọng v&agrave; sẵn s&agrave;ng chia sẻ, chung tay đ&oacute;ng g&oacute;p v&igrave; cộng đồng. Trong năm 2016 vừa qua, VBI đ&atilde; ghi dấu ấn với nhiều hoạt động cộng đồng &yacute; nghĩa như: Tặng qu&agrave; cho 630 người ngh&egrave;o tại 63 tỉnh tr&ecirc;n to&agrave;n quốc với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng; tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo; tổ chức chương tr&igrave;nh từ thiện &ldquo;&Aacute;o ấm trao em&rdquo; tại huyện Na Hang, tỉnh Tuy&ecirc;n Quang; tổ chức tặng qu&agrave; cho 140 hộ ngh&egrave;o tại H&agrave; Tĩnh,&hellip;</p>\r\n<p>Tổng Gi&aacute;m đốc VBI - &ocirc;ng L&ecirc; Tuấn Dũng chia sẻ: &ldquo;Phương ch&acirc;m hoạt động của VBI l&agrave; &ldquo;Bảo to&agrave;n gi&aacute; trị cuộc sống&rdquo;. C&ugrave;ng với những cam kết về chất lượng sản phẩm, dịch vụ, hiệu quả hoạt động, ch&uacute;ng t&ocirc;i lu&ocirc;n ch&uacute; trọng v&agrave; sẵn s&agrave;ng trong c&ocirc;ng t&aacute;c chia sẻ, chung tay đ&oacute;ng g&oacute;p v&igrave; cộng đồng. Đ&acirc;y cũng ch&iacute;nh l&agrave; một trong những gi&aacute; trị cốt l&otilde;i m&agrave; VBI theo đuổi v&agrave; cũng l&agrave; niềm tự h&agrave;o của thương hiệu VBI&rdquo;.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:43:38', '2018-03-21 19:43:38', 'images/news/1520580846_IMG_460_636223458907771226_HasThumb_Thumb.JPG.png'),
(207, 2, 'VBI ký kết hợp đồng hợp tác chiến lược với GINET', 'vbi-ky-ket-hop-dong-hop-tac-chien-luoc-voi-ginet', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 13/2/2017 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) v&agrave; GiNET đ&atilde; tiến h&agrave;nh lễ k&yacute; kết hợp đồng đối t&aacute;c chiến lược v&agrave; độc quyền ph&acirc;n phối sản phẩm bảo hiểm sức khỏe Goodhealth v&agrave; Mothercare của VBI.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 13/2/2017 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) v&agrave; GiNET đ&atilde; tiến h&agrave;nh lễ k&yacute; kết hợp đồng đối t&aacute;c chiến lược v&agrave; độc quyền ph&acirc;n phối sản phẩm bảo hiểm sức khỏe Goodhealth v&agrave; Mothercare của VBI.<br /> <br />Tại buổi lễ, đại diện ph&iacute;a VBI c&oacute; sự hiện diện của &ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n trong Ban l&atilde;nh đạo VBI. Về ph&iacute;a GiNET c&oacute; sự hiện diện của &ocirc;ng Tạ Chiến &ndash; Chủ tịch GiNET, &ocirc;ng Ng&ocirc; Thanh Nam &ndash; Tổng gi&aacute;m đốc GiNET c&ugrave;ng c&aacute;c th&agrave;nh vi&ecirc;n trong Ban l&atilde;nh đạo GiNET.<br /> <br />Hợp t&aacute;c to&agrave;n diện giữa VBI v&agrave; GiNET sẽ g&oacute;p phần n&acirc;ng cao chất lượng dịch vụ, mở rộng thị phần, đồng thời tăng nguồn lợi đầu tư cho c&aacute;c b&ecirc;n. B&ecirc;n cạnh thỏa thuận Hợp t&aacute;c to&agrave;n diện, GiNET v&agrave; VBI c&ograve;n k&yacute; kết Hợp đồng đại l&yacute; bảo hiểm, tr&ecirc;n cơ sở đ&oacute;, GiNET sẽ l&agrave; đại l&yacute; ph&acirc;n phối độc quyền sản phẩm bảo hiểm sức khỏe Goodhealth v&agrave; Mothercare của VBI.</span></p>\r\n<p><img src=\"../../../uploads/anh GiNET1.jpg\" alt=\"\" width=\"1366\" height=\"807\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\"> (Ảnh: Đại diện VBI v&agrave; GiNET k&yacute; kết hợp đồng đối t&aacute;c chiến lược v&agrave; độc quyền ph&acirc;n phối sản phẩm bảo hiểm sức khỏe Goodhealth v&agrave; Mothercare)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">&ldquo;Lễ k&yacute; kết hợp t&aacute;c chiến lược giữa VBI v&agrave; GiNET l&agrave; một bước tiến quan trọng gi&uacute;p ch&uacute;ng t&ocirc;i tối ưu h&oacute;a c&aacute;c dịch vụ cung cấp cho kh&aacute;ch h&agrave;ng. Với dịch vụ ưu việt của hai b&ecirc;n, ch&uacute;ng t&ocirc;i rất kỳ vọng sản phẩm bảo hiểm sức khỏe Goodhealth v&agrave; Mothercare sẽ mang tới cho kh&aacute;ch h&agrave;ng những quyền lợi ưu việt nhất&rdquo;, &ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m đốc VBI cho biết.<br /> <br />&ldquo;Được th&agrave;nh lập v&agrave; hoạt động từ năm 2006, GiNET l&agrave; đơn vị ti&ecirc;n phong &aacute;p dụng c&ocirc;ng nghệ trong lĩnh vực bảo hiểm v&agrave; c&oacute; kinh nghiệm v&agrave; am hiểu s&acirc;u sắc về nhu cầu cũng như c&aacute;c năng lực v&agrave; giải ph&aacute;p bảo hiểm tại Việt Nam cũng như c&oacute; hệ thống mạng lưới c&aacute;c đối t&aacute;c bảo hiểm trong nước v&agrave; tr&ecirc;n thế giới. Ch&uacute;ng t&ocirc;i rất vui mừng trở th&agrave;nh đối t&aacute;c chiến lược ph&acirc;n phối độc quyền sản phẩm bảo hiểm sức khỏe Goodhealth v&agrave; Mothercare của VBI. Việc thỏa thuận k&yacute; kết hợp t&aacute;c kh&ocirc;ng đơn thuần l&agrave; hợp t&aacute;c ph&acirc;n phối sản phẩm m&agrave; c&ograve;n l&agrave; c&aacute;i bắt tay chiến lược x&acirc;y dựng m&ocirc; h&igrave;nh kinh doanh bảo hiểm trực tuyến, gi&uacute;p mang đến kh&aacute;ch h&agrave;ng sự tiện lợi v&agrave; chất lượng dịch vụ vượt trội&rdquo;, &Ocirc;ng Tạ Chiến &ndash; Chủ tịch GiNET chia sẻ.</span></p>\r\n<p><img src=\"../../../uploads/anh GiNET.jpg\" alt=\"\" width=\"1024\" height=\"517\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Tổng Gi&aacute;m đốc VBI tặng hoa cho Chủ tịch GiNET)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Hiện nay, VBI l&agrave; một trong 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam 2016 do Vietnam Report c&ocirc;ng bố v&agrave; GiNET l&agrave; đại l&yacute; bảo hiểm chuy&ecirc;n nghiệp h&agrave;ng đầu tr&ecirc;n thị trường bảo hiểm Việt Nam. Sự kết hợp giữa VBI v&agrave; GiNET hứa hẹn sẽ mang lại nhiều gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng.<br /> <br />Thỏa thuận hợp t&aacute;c với GiNET đ&aacute;nh dấu bước ph&aacute;t triển của VBI trong c&aacute;c hoạt động hợp t&aacute;c song phương v&agrave; đa phương, n&acirc;ng cao hiệu quả hoạt động kinh doanh, g&oacute;p phần khẳng định gi&aacute; trị thương hiệu của VBI tr&ecirc;n thị trường bảo hiểm Việt Nam.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:44:33', '2018-03-21 19:44:33', 'images/news/1520580983_anh-GiNE_636225838491183303_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(208, 2, 'Bảo hiểm VietinBank triển khai nhiệm vụ kinh doanh năm 2017', 'bao-hiem-vietinbank-trien-khai-nhiem-vu-kinh-doanh-nam-2017', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 24/2/2017, tại Ph&uacute; Quốc, Bảo hiểm VietinBank (VBI) tổ chức hội nghị tổng kết hoạt động kinh doanh năm 2016 v&agrave; triển khai nhiệm vụ kinh doanh năm 2017.&nbsp;</span></p>', '<p><strong>Ng&agrave;y 24/2/2017, tại Ph&uacute; Quốc, Bảo hiểm VietinBank (VBI) tổ chức hội nghị tổng kết hoạt động kinh doanh năm 2016 v&agrave; triển khai nhiệm vụ kinh doanh năm 2017.</strong></p>\r\n<p>Tr&igrave;nh b&agrave;y tại Hội nghị, &ocirc;ng L&ecirc; Tuấn Dũng - Tổng gi&aacute;m đốc VBI đ&atilde; tổng kết hoạt động kinh doanh năm 2016 của VBI. Theo đ&oacute;, năm 2016, VBI đ&atilde; ho&agrave;n th&agrave;nh c&aacute;c chỉ ti&ecirc;u kinh doanh với doanh thu ph&iacute; bảo hiểm đạt 781 tỷ đồng, tăng trưởng 42% so với năm 2015 (trong đ&oacute;, doanh thu bảo hiểm gốc đạt 692 tỷ đồng, tăng trưởng 42,2% so với năm 2015; doanh thu nhận t&aacute;i bảo hiểm đạt 88,4 tỷ đồng, tăng trưởng 41,3% so với năm 2015); lợi nhuận đạt 75,1 tỷ đồng, tăng trưởng 10% so với năm 2015; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 42,9%.</p>\r\n<p><img src=\"../../../uploads/TGD VBI danh trong ra quan(1).jpg\" alt=\"\" width=\"1366\" height=\"860\" /></p>\r\n<p style=\"text-align: center;\"><em>(ảnh: &Ocirc;ng L&ecirc; Tuấn Dũng - Tổng Gi&aacute;m Đốc VBI đ&aacute;nh trống ra qu&acirc;n)</em></p>\r\n<p>Kết quả kinh doanh n&ecirc;u tr&ecirc;n đ&atilde; gi&uacute;p VBI vươn l&ecirc;n vị tr&iacute; l&agrave; một trong những c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ c&oacute; hiệu quả hoạt động h&agrave;ng đầu tại Việt Nam.<br /> <br />Đặc biệt, VBI đ&atilde; nhận nhiều danh hiệu uy t&iacute;n như &ldquo;Top 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam&rdquo; do Vietnam Report c&ocirc;ng bố, &ldquo;Top 20 doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng&rdquo;, &ldquo;Top 100 thương hiệu mạnh Việt Nam&rdquo;. Trong 4 năm li&ecirc;n tiếp từ 2012 - 2015, VBI li&ecirc;n tục được vinh danh trong &ldquo;Top 1000 đơn vị nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam&rdquo;.<br /> <br />Gần đ&acirc;y nhất, ng&agrave;y 9/1/2017, VBI 4 năm li&ecirc;n tiếp được vinh danh &ldquo;Đơn vị ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh 2016\" tại Hội nghị tổng kết hoạt động kinh doanh năm 2016 do VietinBank tổ chức. Những danh hiệu tr&ecirc;n đ&atilde; minh chứng phần n&agrave;o cho những bước ph&aacute;t triển đột ph&aacute; của VBI.<br /> <br />Trong năm 2016, VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n tại Việt Nam ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử v&agrave; h&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng.</p>\r\n<p><img src=\"../../../uploads/4 don vi hoan thanh dac biet sx nhiem vu kinh doanh 2016.jpg\" alt=\"\" width=\"1366\" height=\"1124\" /></p>\r\n<p style=\"text-align: center;\"><em>(ảnh: Chủ tịch c&ocirc;ng ty v&agrave; Tổng Gi&aacute;m đốc trao giấy khen cho 4 đơn vị ho&agrave;n th&agrave;nh đặc biệt xuất sắc nhiệm vụ năm 2016)</em></p>\r\n<p>B&ecirc;n cạnh đ&oacute;, VBI đ&atilde; &aacute;p dụng m&ocirc; h&igrave;nh quản l&yacute; bồi thường trực tuyến tập trung, v&agrave; cung cấp nhiều g&oacute;i sản phẩm mới, tiện &iacute;ch tới kh&aacute;ch h&agrave;ng.<br /> <br />Trong năm 2017, VBI sẽ tập trung x&acirc;y dựng v&agrave; n&acirc;ng cấp dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng trong c&aacute;c kh&acirc;u b&aacute;n h&agrave;ng, bồi thường v&agrave; t&aacute;i tục bảo hiểm, mang tới cho kh&aacute;ch h&agrave;ng trải nghiệm dịch vụ bảo hiểm chuy&ecirc;n nghiệp.<br /> <br />Kế hoạch kinh doanh năm 2017, VBI đề ra một số chỉ ti&ecirc;u kế hoạch t&agrave;i ch&iacute;nh: Tổng t&agrave;i sản tăng 10%, tổng doanh thu v&agrave; lợi nhuận tăng trưởng 10% so với năm 2016, mở rộng mạng lưới kinh doanh, th&agrave;nh lập th&ecirc;m 5 chi nh&aacute;nh mới.</p>\r\n<p><img src=\"../../../uploads/4 don vi hoan thanh sx nvkd 2016.jpg\" alt=\"\" width=\"1366\" height=\"731\" /></p>\r\n<p style=\"text-align: center;\"><em>(ảnh: Chủ tịch c&ocirc;ng ty v&agrave; Tổng Gi&aacute;m đốc trao giấy khen cho c&aacute;c đơn vị ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ năm 2016)</em></p>\r\n<p>Đ&aacute;ng ch&uacute; &yacute;, tại hội nghị, b&agrave; Nguyễn Hồng V&acirc;n - Chủ tịch C&ocirc;ng ty Bảo hiểm VietinBank đồng thời l&agrave; Ủy vi&ecirc;n Hội đồng Quản trị VietinBank ph&aacute;t biểu: &ldquo;Nhằm đ&aacute;p ứng nhu cầu ng&agrave;y một ph&aacute;t triển của doanh nghiệp, trong năm 2017, VBI sẽ ch&iacute;nh thức triển khai kế hoạch cổ phần h&oacute;a, lựa chọn nh&agrave; đầu tư chiến lược nước ngo&agrave;i&rdquo;.<br /> <br />Với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững, trong năm 2017, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u chiến lược chuyển đổi m&ocirc; h&igrave;nh nhằm n&acirc;ng cao hiệu quả kinh doanh, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:47:07', '2018-03-21 19:47:07', 'images/news/1520581131_4-don-vi-_636235364507060489_HasThumb_Thumb.JPG.png'),
(209, 2, 'Chương trình kết nối đồng nghiệp VBI năm 2017', 'chuong-trinh-ket-noi-dong-nghiep-vbi-nam-2017', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 25/02/2017, tại Ph&uacute; Quốc, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh kết nối đồng nghiệp 2017 với chủ đề: VBI VƯƠN TẦM CAO MỚI. Chương tr&igrave;nh l&agrave; cơ hội để c&aacute;c th&agrave;nh vi&ecirc;n VBI c&oacute; cơ hội gặp gỡ, giao lưu, tăng t&igrave;nh đo&agrave;n kết, x&acirc;y dựng văn h&oacute;a doanh nghiệp.&nbsp;</span></p>', '<p>Ng&agrave;y 25/02/2017, tại Ph&uacute; Quốc, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức chương tr&igrave;nh kết nối đồng nghiệp 2017 với chủ đề: VBI VƯƠN TẦM CAO MỚI. Chương tr&igrave;nh l&agrave; cơ hội để c&aacute;c th&agrave;nh vi&ecirc;n VBI c&oacute; cơ hội gặp gỡ, giao lưu, tăng t&igrave;nh đo&agrave;n kết, x&acirc;y dựng văn h&oacute;a doanh nghiệp.</p>\r\n<p>Chương tr&igrave;nh kết nối đồng nghiệp năm 2017 đ&atilde; để lại những kỷ niệm đẹp, ấn tượng, nhiều cảm x&uacute;c đặc biệt trong mỗi th&agrave;nh vi&ecirc;n VBI, khắc s&acirc;u trong tr&aacute;i tim của mỗi th&agrave;nh vi&ecirc;n v&agrave; tạo động lực cho c&aacute;c th&agrave;nh vi&ecirc;n VBI c&ugrave;ng thi đua, phấn đấu ho&agrave;n th&agrave;nh xuất sắc kế hoạch kinh doanh 2017 với những quyết t&acirc;m mạnh mẽ hơn bao giờ hết.</p>\r\n<p>Sau đ&acirc;y l&agrave; một v&agrave;i h&igrave;nh ảnh về chương tr&igrave;nh:</p>\r\n<p><img src=\"../../../uploads/1111.jpg\" alt=\"\" width=\"1024\" height=\"634\" /></p>\r\n<p><img src=\"../../../uploads/DJI_0049.jpg\" alt=\"\" width=\"1366\" height=\"998\" /></p>\r\n<p><img src=\"../../../uploads/DSC00021.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><img src=\"../../../uploads/DSC00032.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><img src=\"../../../uploads/DSC00048.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><img src=\"../../../uploads/DSC00060.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><img src=\"../../../uploads/DSC00010.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><img src=\"../../../uploads/2(1).jpg\" alt=\"\" width=\"1024\" height=\"512\" /></p>\r\n<p>&nbsp;</p>', '2018-03-21 08:50:27', '2018-03-21 19:50:27', 'images/news/1520581232__636237861273644651_HasThumb_Thumb.jpg.png'),
(210, 2, 'VBI triển khai Chương trình hỗ trợ cộng đồng “Biến bãi rác thành vườn hoa”', 'vbi-trien-khai-chuong-trinh-ho-tro-cong-dong-bien-bai-rac-thanh-vuon-hoa', '<p><span style=\"font-family: Arial;\">Bắt đầu từ năm 2017, Đo&agrave;n thanh ni&ecirc;n VBI c&ugrave;ng với nh&oacute;m &ldquo;Sen trong phố&rdquo; triển khai Chương tr&igrave;nh hỗ trợ cộng đồng &ldquo;Biến b&atilde;i r&aacute;c th&agrave;nh vườn hoa&rdquo; tại H&agrave; Nội. Trong chương tr&igrave;nh, Đo&agrave;n thanh ni&ecirc;n VBI v&agrave; nh&oacute;m Sen trong phố sẽ t&igrave;m kiếm v&agrave; khảo s&aacute;t c&aacute;c b&atilde;i r&aacute;c ph&aacute;t sinh tr&ecirc;n địa b&agrave;n H&agrave; Nộ...&nbsp;</span></p>', '<p>Bắt đầu từ năm 2017, Đo&agrave;n thanh ni&ecirc;n VBI c&ugrave;ng với nh&oacute;m &ldquo;Sen trong phố&rdquo; triển khai Chương tr&igrave;nh hỗ trợ cộng đồng &ldquo;Biến b&atilde;i r&aacute;c th&agrave;nh vườn hoa&rdquo; tại H&agrave; Nội.</p>\r\n<p>Trong chương tr&igrave;nh, Đo&agrave;n thanh ni&ecirc;n VBI v&agrave; nh&oacute;m \"Sen trong phố\" sẽ t&igrave;m kiếm v&agrave; khảo s&aacute;t c&aacute;c b&atilde;i r&aacute;c ph&aacute;t sinh tr&ecirc;n địa b&agrave;n H&agrave; Nội, sau đ&oacute; thu dọn r&aacute;c thải, x&acirc;y dựng vườn hoa tại ch&iacute;nh c&aacute;c b&atilde;i r&aacute;c ph&aacute;t sinh trước đ&oacute;. Tiếp đ&oacute;, tiến h&agrave;nh x&acirc;y dựng c&aacute;c đội tự quản, chăm s&oacute;c vườn hoa sau khi dự &aacute;n kết th&uacute;c.</p>\r\n<p><img src=\"../../../uploads/1(1).jpg\" alt=\"\" width=\"768\" height=\"1024\" /></p>\r\n<p><img src=\"../../../uploads/3333.jpg\" alt=\"\" width=\"768\" height=\"861\" /></p>\r\n<p><img src=\"../../../uploads/4444.jpg\" alt=\"\" width=\"985\" height=\"1200\" /></p>\r\n<p>(Ảnh: Đo&agrave;n thanh ni&ecirc;n VBI v&agrave; nh&oacute;m \"Sen trong phố\" thực hiện c&ocirc;ng tr&igrave;nh tại Ng&atilde; ba Bảo Kh&aacute;nh &ndash; H&agrave;ng Trống, Quận Ho&agrave;n Kiếm, TP. H&agrave; Nội)</p>\r\n<p>Với chương tr&igrave;nh n&agrave;y, Đo&agrave;n th&agrave;nh thi&ecirc;n VBI sẽ g&oacute;p sức m&igrave;nh trong c&ocirc;ng t&aacute;c hỗ trợ cộng đồng x&oacute;a bỏ c&aacute;c điểm r&aacute;c, b&atilde;i r&aacute;c ph&aacute;t sinh trong đ&ocirc; thị, qua đ&oacute; x&acirc;y dựng &yacute; thức vứt r&aacute;c đ&uacute;ng giờ, đ&uacute;ng nơi quy định của người d&acirc;n. B&ecirc;n cạnh đ&oacute; gi&uacute;p n&acirc;ng cao nhận thức cộng đồng về vấn đề xử l&yacute; chất thải v&agrave; x&acirc;y dựng kh&ocirc;ng gian xanh trong đ&ocirc; thị.</p>\r\n<p><img src=\"../../../uploads/Pho bi thudao duc toan tham khu vuc tuong hoa tren pho PHo Duc Chinh cua nhom STP.jpg\" alt=\"\" width=\"600\" height=\"400\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Ph&oacute; B&iacute; thư Th&agrave;nh ủy H&agrave; Nội thăm khu vực &ldquo;tường hoa&rdquo; tr&ecirc;n phố Ph&oacute; Đức Ch&iacute;nh &ndash; H&agrave; Nội của nh&oacute;m bạn &ldquo;Sen trong phố&rdquo;)</em></p>\r\n<p>M&ocirc; h&igrave;nh &ldquo;Biến b&atilde;i r&aacute;c th&agrave;nh vườn hoa&rdquo; của nh&oacute;m &ldquo;Sen trong phố&rdquo; được Ph&oacute; B&iacute; thư Th&agrave;nh ủy &ndash; &ocirc;ng Đ&agrave;o Đức To&agrave;n đ&aacute;nh gi&aacute; cao. Theo &ocirc;ng To&agrave;n, m&ocirc; h&igrave;nh kh&ocirc;ng tốn nhiều c&ocirc;ng sức, tiền bạc nhưng mang lại hiệu quả cao, biến những b&atilde;i r&aacute;c mất vệ sinh tồn tại từ l&acirc;u th&agrave;nh những vườn hoa sạch, đẹp.</p>\r\n<p><img src=\"../../../uploads/Hinh anh bai rac ban trong khu pho truoc khi co vuon hoa.jpg\" alt=\"\" width=\"386\" height=\"281\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: H&igrave;nh ảnh b&atilde;i r&aacute;c bẩn trong khu phố trước khi c&oacute; vườn hoa)</em></p>\r\n<p><img src=\"../../../uploads/khung canh vuon hoa thay the bai rac tren pho Tran Binh.jpg\" alt=\"\" width=\"660\" height=\"454\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: H&igrave;nh ảnh vườn hoa thay thế b&atilde;i r&aacute;c)</em></p>\r\n<p>Trong thời gian tới, Đo&agrave;n thanh ni&ecirc;n VBI sẽ c&ugrave;ng với nh&oacute;m &ldquo;Sen trong phố&rdquo; phối hợp nh&acirc;n rộng m&ocirc; h&igrave;nh n&agrave;y ra tại nhiều quận tr&ecirc;n địa b&agrave;n TP. H&agrave; Nội.</p>\r\n<p>B&ecirc;n cạnh những cam kết về chất lượng sản phẩm dịch vụ, hiệu quả hoạt động, VBI lu&ocirc;n ch&uacute; trọng v&agrave; sẵn s&agrave;ng trong c&ocirc;ng t&aacute;c chia sẻ, chung tay đ&oacute;ng g&oacute;p v&igrave; cộng đồng. H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng &yacute; nghĩa như: tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương c&ugrave;ng VietinBank, thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh, ủng hộ đồng b&agrave;o lũ lụt H&agrave; tĩnh, thăm v&agrave; tặng qu&agrave; cho b&agrave; con tỉnh H&agrave; Giang, Ủng hộ Trường sa th&acirc;n y&ecirc;u, v.v...</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:53:54', '2018-03-21 19:53:54', 'images/news/1520581419_khung-can_636238075036071742_HasThumb_Thumb.jpg.png'),
(211, 2, 'Chương trình hỗ trợ cộng đồng “Biến bãi rác thành vườn hoa” tháng 3/2017', 'chuong-trinh-ho-tro-cong-dong-bien-bai-rac-thanh-vuon-hoa-thang-3-2017', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 05/03/2017, Đo&agrave;n thanh ni&ecirc;n VBI c&ugrave;ng với nh&oacute;m &ldquo;Sen trong phố&rdquo; triển khai Chương tr&igrave;nh hỗ trợ cộng đồng &ldquo;Biến b&atilde;i r&aacute;c th&agrave;nh vườn hoa&rdquo; tại Ng&atilde; ba Nguyễn Hữu Hu&acirc;n &ndash; L&ograve; Sũ, Quận Ho&agrave;n Kiếm, TP. H&agrave; Nội.&nbsp;</span></p>', '<p>Ng&agrave;y 05/03/2017, Đo&agrave;n thanh ni&ecirc;n VBI c&ugrave;ng với nh&oacute;m &ldquo;Sen trong phố&rdquo; triển khai Chương tr&igrave;nh hỗ trợ cộng đồng &ldquo;Biến b&atilde;i r&aacute;c th&agrave;nh vườn hoa&rdquo; tại Ng&atilde; ba Nguyễn Hữu Hu&acirc;n &ndash; L&ograve; Sũ, Quận Ho&agrave;n Kiếm, TP. H&agrave; Nội.</p>\r\n<p>Trong chương tr&igrave;nh, Đo&agrave;n thanh ni&ecirc;n VBI v&agrave; nh&oacute;m \"Sen trong phố\" đ&atilde; thu dọn r&aacute;c thải, x&acirc;y dựng vườn hoa tại ch&iacute;nh b&atilde;i r&aacute;c ph&aacute;t sinh trước đ&oacute;. Tiếp đ&oacute;, tiến h&agrave;nh x&acirc;y dựng c&aacute;c đội tự quản, chăm s&oacute;c vườn hoa sau khi dự &aacute;n kết th&uacute;c.</p>\r\n<p><img src=\"../../../uploads/IMG_3132.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p><img src=\"../../../uploads/FullSizeRender.jpg\" alt=\"\" width=\"1127\" height=\"1200\" /></p>\r\n<p><img src=\"../../../uploads/IMG_3137.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Đo&agrave;n thanh ni&ecirc;n VBI v&agrave; nh&oacute;m \"Sen trong phố\" thực hiện c&ocirc;ng tr&igrave;nh tại Ng&atilde; ba Nguyễn Hữu Hu&acirc;n &ndash; L&ograve; Sũ, Quận Ho&agrave;n Kiếm, TP. H&agrave; Nội)</em></p>\r\n<p>Với chương tr&igrave;nh n&agrave;y, Đo&agrave;n th&agrave;nh thi&ecirc;n VBI sẽ g&oacute;p sức m&igrave;nh trong c&ocirc;ng t&aacute;c hỗ trợ cộng đồng x&oacute;a bỏ c&aacute;c điểm r&aacute;c, b&atilde;i r&aacute;c ph&aacute;t sinh trong đ&ocirc; thị, qua đ&oacute; x&acirc;y dựng &yacute; thức vứt r&aacute;c đ&uacute;ng giờ, đ&uacute;ng nơi quy định của người d&acirc;n. B&ecirc;n cạnh đ&oacute; gi&uacute;p n&acirc;ng cao nhận thức cộng đồng về vấn đề xử l&yacute; chất thải v&agrave; x&acirc;y dựng kh&ocirc;ng gian xanh trong đ&ocirc; thị.</p>\r\n<p>Trong năm 2017, Đo&agrave;n thanh ni&ecirc;n VBI sẽ c&ugrave;ng với nh&oacute;m &ldquo;Sen trong phố&rdquo; phối hợp nh&acirc;n rộng m&ocirc; h&igrave;nh n&agrave;y ra tại nhiều quận tr&ecirc;n địa b&agrave;n TP. H&agrave; Nội.</p>\r\n<p>B&ecirc;n cạnh những cam kết về chất lượng sản phẩm dịch vụ, hiệu quả hoạt động, VBI lu&ocirc;n ch&uacute; trọng v&agrave; sẵn s&agrave;ng trong c&ocirc;ng t&aacute;c chia sẻ, chung tay đ&oacute;ng g&oacute;p v&igrave; cộng đồng. H&agrave;ng năm, VBI đều d&agrave;nh ng&acirc;n s&aacute;ch lớn cho c&aacute;c hoạt động v&igrave; cộng đồng &yacute; nghĩa như: tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương c&ugrave;ng VietinBank, thăm v&agrave; tặng qu&agrave; tại Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh, ủng hộ đồng b&agrave;o lũ lụt H&agrave; tĩnh, thăm v&agrave; tặng qu&agrave; cho b&agrave; con tỉnh H&agrave; Giang, Ủng hộ Trường sa th&acirc;n y&ecirc;u, v.v...</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>\r\n<p>&nbsp;</p>', '2018-03-21 08:55:36', '2018-03-21 19:55:36', 'images/news/1520581542_khung-can_636245600604504475_HasThumb_Thumb.jpg.png'),
(212, 2, 'VBI tài trợ bảo hiểm giải thưởng Hole in one trị giá hơn 600 triệu tại sân golf Hoàng Gia', 'vbi-tai-tro-bao-hiem-giai-thuong-hole-in-one-tri-gia-hon-600-trieu-tai-san-golf-hoang-gia', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 05/03/2017 tại s&acirc;n golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh, đ&atilde; diễn ra &ldquo;Giải golf ra mắt CLB Golf VietinBank&rdquo;. VBI tham gia t&agrave;i trợ bảo hiểm cho 3 giải thưởng Hole-in-one trị gi&aacute; 300 triệu đồng v&agrave; bảo hiểm cho 01 thẻ t&iacute;n dụng VietinBank trị gi&aacute; 300 triệu đồng.&nbsp;</span></p>', '<p>Ng&agrave;y 05/03/2017 tại s&acirc;n golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh, đ&atilde; diễn ra &ldquo;Giải golf ra mắt CLB Golf VietinBank&rdquo;. Giải c&oacute; tham dự của hơn 120 Golfers đến từ trụ sở ch&iacute;nh v&agrave; c&aacute;c chi nh&aacute;nh VietinBank tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>VBI tham gia t&agrave;i trợ bảo hiểm cho 3 giải thưởng Hole-in-one trị gi&aacute; 300 triệu đồng v&agrave; bảo hiểm cho 01 thẻ t&iacute;n dụng VietinBank trị gi&aacute; 300 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/Phat bong Khai mac.jpg\" alt=\"\" width=\"1366\" height=\"909\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng Nguyễn Văn Thắng - Chủ tịch HĐQT VietinBank ph&aacute;t b&oacute;ng tại Lễ khai mạc giải)</em></p>\r\n<p>Trong thời gian qua, Bảo hiểm VietinBank (VBI) lu&ocirc;n l&agrave; một trong những c&ocirc;ng ty h&agrave;ng đầu Việt Nam bảo hiểm cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:56:12', '2018-03-21 19:56:12', 'images/news/1520581638_TAP-TH_636245617807178510_HasThumb_Thumb.JPG.png'),
(213, 2, 'VBI chi trả 400 triệu đồng tiền bồi thường bảo hiểm người vay vốn tại Kiên Giang', 'vbi-chi-tra-400-trieu-dong-tien-boi-thuong-bao-hiem-nguoi-vay-von-tai-kien-giang', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 8/3/2017, Bảo hiểm VietinBank - Chi nh&aacute;nh Cần Thơ đ&atilde; tổ chức chi trả 400 triệu đồng tiền bồi thường bảo hiểm người vay vốn cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Văn C&ocirc;ng, tr&uacute; tại x&atilde; B&igrave;nh Giang &ndash; Huyện H&ograve;n Đất &ndash; Tỉnh Ki&ecirc;n Giang.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 8/3/2017, Bảo hiểm VietinBank - Chi nh&aacute;nh Cần Thơ đ&atilde; tổ chức chi trả 400 triệu đồng tiền bồi thường bảo hiểm người vay vốn cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Văn C&ocirc;ng, tr&uacute; tại x&atilde; B&igrave;nh Giang &ndash; Huyện H&ograve;n Đất &ndash; Tỉnh Ki&ecirc;n Giang.<br /> <br />Năm 2016, &ocirc;ng Nguyễn Văn C&ocirc;ng vay vốn 400 triệu đồng tại VietinBank Chi nh&aacute;nh Ki&ecirc;n Giang v&agrave; được c&aacute;n bộ ng&acirc;n h&agrave;ng tư vấn tham gia dịch vụ bảo hiểm người vay vốn. Ng&agrave;y 11/1/2017, khi tham gia giao th&ocirc;ng bằng xe kh&aacute;ch, chiếc xe gặp sự cố dẫn tới tai nạn, &ocirc;ng C&ocirc;ng kh&ocirc;ng may đ&atilde; tử vong.<br /> <br />Ngay sau khi nhận được th&ocirc;ng tin, Bảo hiểm VietinBank Chi nh&aacute;nh Cần Thơ đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank Chi nh&aacute;nh Ki&ecirc;n Giang hướng dẫn gia đ&igrave;nh kh&aacute;ch h&agrave;ng ho&agrave;n thiện hồ sơ để được chi trả bồi thường bảo hiểm theo đ&uacute;ng quy định.<br /> <br />Trong buổi nhận tiền bồi thường, đại diện gia đ&igrave;nh kh&aacute;ch h&agrave;ng Nguyễn Văn C&ocirc;ng đ&atilde; gửi lời cảm ơn đến Bảo hiểm VietinBank Chi nh&aacute;nh Cần Thơ đ&atilde; tạo mọi điều kiện hỗ trợ trong l&uacute;c gia đ&igrave;nh gặp kh&oacute; khăn.</span></p>\r\n<p><img src=\"../../../uploads/VBI trao boi thuong 400 trieu tai can tho.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em> (Ảnh: Đại diện VBI v&agrave; VietinBank trao tiền bồi thường cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng)</em><br /></span></p>\r\n<p><span style=\"font-family: Arial;\">Bảo hiểm người vay vốn l&agrave; sản phẩm bảo hiểm rất c&oacute; &yacute; nghĩa với người vay vốn tại Ng&acirc;n h&agrave;ng VietinBank, vừa bảo đảm ng&acirc;n h&agrave;ng kh&ocirc;ng phải xử l&yacute; nợ, c&ograve;n gia đ&igrave;nh kh&aacute;ch h&agrave;ng kh&ocirc;ng phải chịu g&aacute;nh nặng trả nợ khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp rủi ro.<br /> <br />Trong thời gian qua, Bảo hiểm VietinBank (VBI) đ&atilde; chi trả hơn 8 tỷ đồng bảo hiểm người vay vốn cho c&aacute;c kh&aacute;ch h&agrave;ng VietinBank. Với cam kết bồi thường thỏa đ&aacute;ng cho kh&aacute;ch h&agrave;ng v&agrave; chất lượng dịch vụ vượt trội, VBI đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ uy t&iacute;n năm 2016 do Vietnam Report c&ocirc;ng bố.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:56:42', '2018-03-21 19:56:42', 'images/news/1520582016_VBI-trao-_636246748896772934_HasThumb_Thumb.JPG.png'),
(214, 2, 'Đại hội Chi đoàn Bảo hiểm VietinBank nhiệm kỳ 2017 - 2019', 'dai-hoi-chi-doan-bao-hiem-vietinbank-nhiem-ky-2017-2019', '<p><span style=\"font-family: Arial;\">Chiều ng&agrave;y 10/3/2017, tại H&agrave; Nội, Ban Chấp h&agrave;nh Chi đo&agrave;n Bảo hiểm VietinBank long trọng tổ chức Đại hội Chi đo&agrave;n nhiệm kỳ 2017 - 2019. Tham dự Đại hội c&oacute; đồng ch&iacute; Nguyễn Trung Hậu - Đại diện ban thường vụ Đo&agrave;n thanh ni&ecirc;n VietinBank, Ban l&atilde;nh đạo VBI v&agrave; Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n VBI...&nbsp;</span></p>', '<p>Chiều ng&agrave;y 10/3/2017, tại H&agrave; Nội, Ban Chấp h&agrave;nh Chi đo&agrave;n Bảo hiểm VietinBank long trọng tổ chức Đại hội Chi đo&agrave;n nhiệm kỳ 2017 - 2019. Tham dự Đại hội c&oacute; đồng ch&iacute; Nguyễn Trung Hậu - Đại diện ban thường vụ Đo&agrave;n thanh ni&ecirc;n VietinBank, Ban l&atilde;nh đạo VBI v&agrave; Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n VBI, c&ugrave;ng c&aacute;c đo&agrave;n vi&ecirc;n thanh ni&ecirc;n tại địa b&agrave;n H&agrave; Nội.</p>\r\n<p>Tại Đại hội, c&aacute;c đại biểu đ&atilde; nghe b&aacute;o c&aacute;o tổng kết c&aacute;c hoạt động Đo&agrave;n, b&aacute;o c&aacute;o kiểm điểm của BCH Chi đo&agrave;n trong nhiệm kỳ trước v&agrave; phương hướng, nhiệm vụ trong nhiệm kỳ 2017 - 2019. Nhiệm kỳ vừa qua, đa số đo&agrave;n vi&ecirc;n đều c&oacute; tư tưởng ch&iacute;nh trị vững v&agrave;ng, lối sống l&agrave;nh mạnh, tổ chức kỷ luật tốt, xung k&iacute;ch trong c&ocirc;ng việc, tham gia nhiệt t&igrave;nh c&aacute;c phong tr&agrave;o Đo&agrave;n. Đ&acirc;y l&agrave; cơ hội để Chi đo&agrave;n nh&igrave;n nhận ưu, khuyết điểm trong nhiệm kỳ qua, đ&aacute;nh gi&aacute; tiến độ thực hiện c&ocirc;ng việc, từ đ&oacute; r&uacute;t ra b&agrave;i học kinh nghiệm nhằm thực hiện tốt nhiệm vụ c&ocirc;ng t&aacute;c Đo&agrave;n trong nhiệm kỳ mới.</p>\r\n<p>Đại hội đ&atilde; bầu ra 7 đồng ch&iacute; (L&ecirc; Quang Vinh, Do&atilde;n Việt Anh, L&ecirc; Thị Huyền Trang, Đo&agrave;n Diệu Linh, Nguyễn Quốc Bảo v&agrave; Nguyễn Văn Toản) tham gia Ban Chấp h&agrave;nh Chi đo&agrave;n nhiệm kỳ 2017 &ndash; 2019 v&agrave; 3 đồng ch&iacute; tham dự Đại Hội đại biểu Đo&agrave;n VietinBank lần thứ 3, nhiệm k&yacute; 2017 - 2022. Đ&acirc;y l&agrave; những c&aacute;n bộ c&oacute; năng lực, nhiệt huyết với c&ocirc;ng t&aacute;c Đo&agrave;n.</p>\r\n<p><img src=\"../../../uploads/IMG_9153-low.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Ban Chấp h&agrave;nh Chi đo&agrave;n Bảo hiểm VietinBank nhiệm kỳ 2017 &ndash; 2019)</em></p>\r\n<p><img src=\"../../../uploads/bi thu VTB phat bieu.jpg\" alt=\"\" width=\"1023\" height=\"768\" /></p>\r\n<p style=\"text-align: center;\"><em>(&Ocirc;ng Nguyễn Trung Hậu - Ph&oacute; B&iacute; thư Thường trực Đo&agrave;n Thanh ni&ecirc;n VietinBank ph&aacute;t biểu tại Đại hội)</em></p>\r\n<p>Ph&aacute;t biểu chỉ đạo tại Đại hội, Ph&oacute; B&iacute; thư Thường trực Đo&agrave;n Thanh ni&ecirc;n VietinBank Nguyễn Trung Hậu đ&atilde; đ&aacute;nh gi&aacute; cao vai tr&ograve; của Chi đo&agrave;n thời gian qua đ&atilde; đ&oacute;ng g&oacute;p v&agrave;o th&agrave;nh t&iacute;ch chung của Đo&agrave;n Thanh ni&ecirc;n VietinBank. Đồng thời, Ph&oacute; B&iacute; thư Thường trực Đo&agrave;n Thanh ni&ecirc;n VietinBank động vi&ecirc;n tuổi trẻ VBI tiếp tục ph&aacute;t huy tinh thần xung k&iacute;ch, nỗ lực phấn đấu nhiều hơn nữa trong phong tr&agrave;o Đo&agrave;n cũng như c&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n, đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o kết quả kinh doanh của VBI.</p>\r\n<p>Thay mặt Đảng ủy, Ban L&atilde;nh đạo C&ocirc;ng ty, đồng ch&iacute; Trần Thị Hương Thủy - Ph&oacute; Tổng Gi&aacute;m đốc VBI ghi nhận những đ&oacute;ng g&oacute;p t&iacute;ch cực của Chi đo&agrave;n trong nhiệm kỳ 2014 - 2017. Đồng ch&iacute; cũng nhấn mạnh vai tr&ograve; của Đo&agrave;n trong việc bồi dưỡng, giới thiệu c&aacute;c đo&agrave;n vi&ecirc;n ưu t&uacute; nhằm tăng cường lực lượng cho Đảng. Ph&oacute; B&iacute; thư Đảng bộ cũng tin tưởng rằng bằng sức trẻ v&agrave; khả năng s&aacute;ng tạo, Chi đo&agrave;n sẽ tiếp tục c&oacute; những đ&oacute;ng g&oacute;p to lớn gi&uacute;p VBI ho&agrave;n th&agrave;nh xuất sắc c&aacute;c nhiệm vụ được giao; g&oacute;p phần đẩy mạnh họat động Đo&agrave;n tại đơn vị v&agrave; tại địa phương.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 08:57:40', '2018-03-21 19:57:40', 'images/news/1520582125_IMG_9153-_636249910219112722_HasThumb_Thumb.jpg.png'),
(215, 2, 'Bảo hiểm VietinBank thông báo lịch nghỉ lễ Giỗ tổ Hùng Vương, 30/4 và 1/5', 'bao-hiem-vietinbank-thong-bao-lich-nghi-le-gio-to-hung-vuong-30-4-va-1-5', '<p><span style=\"font-family: Arial;\">V&agrave;o dịp nghỉ lễ Giải ph&oacute;ng Miền Nam 30/4 v&agrave; Quốc tế Lao động 1/5, VBI xin th&ocirc;ng b&aacute;o lịch nghỉ lễ từ ng&agrave;y 29/04/2017 tới ng&agrave;y 02/05/2017. Ng&agrave;y 03/05/2017, VBI sẽ trở lại hoạt động b&igrave;nh thường.&nbsp;</span></p>', '<p>&nbsp;</p>\r\n<p><strong>K&iacute;nh gửi Qu&yacute; kh&aacute;ch,</strong></p>\r\n<p>Lời đầu ti&ecirc;n, Bảo hiểm VietinBank (VBI) tr&acirc;n trọng gửi lời cảm ơn đến Qu&yacute; kh&aacute;ch đ&atilde; tin tưởng, sử dụng c&aacute;c sản phẩm/ dịch vụ bảo hiểm của VBI trong thời gian qua.</p>\r\n<p>Nh&acirc;n dịp nghỉ lễ Giỗ tổ H&ugrave;ng Vương, VBI xin th&ocirc;ng b&aacute;o lịch nghỉ lễ v&agrave;o ng&agrave;y 06/04/2017. Ng&agrave;y 07/04/2017, VBI sẽ trở lại hoạt động b&igrave;nh thường.</p>\r\n<p>V&agrave;o dịp nghỉ lễ Giải ph&oacute;ng Miền Nam 30/4 v&agrave; Quốc tế Lao động 1/5, VBI xin th&ocirc;ng b&aacute;o lịch nghỉ lễ từ ng&agrave;y 29/04/2017 tới ng&agrave;y 02/05/2017. Ng&agrave;y 03/05/2017, VBI sẽ trở lại hoạt động b&igrave;nh thường.</p>\r\n<p>Trong thời gian nghỉ lễ, nếu ph&aacute;t sinh sự cố cần giải quyết, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ với VBI qua Hotline:<br />- Trung t&acirc;m bảo l&atilde;nh viện ph&iacute;: 09 3459 6339<br />- Trung t&acirc;m gi&aacute;m định bồi thường xe cơ giới: Tại Miền Bắc: 09 0489 7139; Tại Miền Trung: 09 3452 8139; Tại Miền Nam: 09 3622 0139</p>\r\n<p>Ch&uacute;ng t&ocirc;i sẽ tiếp nhận v&agrave; xử l&yacute; c&aacute;c y&ecirc;u cầu của Qu&yacute; kh&aacute;ch trong thời gian sớm nhất.</p>\r\n<p><strong>Tr&acirc;n trọng cảm ơn!</strong></p>', '2018-03-09 19:56:15', '2018-03-09 19:56:15', 'images/news/1520582175_lich-nghi_636268968035645694_HasThumb_Thumb.png'),
(216, 2, 'VBI ký kết thoả thuận hợp tác với Công đoàn ngân hàng Việt Nam', 'vbi-ky-ket-thoa-thuan-hop-tac-voi-cong-doan-ngan-hang-viet-nam', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 07/04/2017, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; k&yacute; kết thoả thuận hợp t&aacute;c với C&ocirc;ng đo&agrave;n Ng&acirc;n h&agrave;ng Việt Nam.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 07/04/2017, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; k&yacute; kết thoả thuận hợp t&aacute;c với C&ocirc;ng đo&agrave;n Ng&acirc;n h&agrave;ng Việt Nam (CĐNHVN). Lễ k&yacute; kết c&oacute; sự tham dự của b&agrave; Nguyễn Thị Thu Hồng - Ph&oacute; Chủ tịch TLĐLĐVN c&ugrave;ng đại diện l&atilde;nh đạo c&aacute;c Ban TLĐLĐVN, CĐNHVN; đại diện Đảng ủy, Đo&agrave;n Thanh ni&ecirc;n Ng&acirc;n h&agrave;ng Trung ương; Chủ tịch, Ph&oacute; chủ tịch c&aacute;c C&ocirc;ng đo&agrave;n cấp tr&ecirc;n cơ sở v&agrave; CĐCS trực thuộc CĐNHVN tại H&agrave; Nội. Về ph&iacute;a VBI c&oacute; sự tham dự của &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI, &ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VBI.<br /> <br />Theo thỏa thuận k&yacute; kết tại buổi lễ, VBI mang tới những ưu đ&atilde;i vượt trội d&agrave;nh ri&ecirc;ng cho c&aacute;c Đo&agrave;n vi&ecirc;n ng&agrave;nh ng&acirc;n h&agrave;ng khi tham gia bảo hiểm sức khỏe, bảo hiểm &ocirc; t&ocirc;, xe m&aacute;y với quyền lợi lớn, mức ph&iacute; ưu đ&atilde;i cao<br /> <br />Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI gửi lời cảm ơn tới Tổng li&ecirc;n đo&agrave;n lao động Việt Nam v&agrave; C&ocirc;ng đo&agrave;n Ng&acirc;n h&agrave;ng Việt Nam đ&atilde; lựa chọn VBI l&agrave;m đối t&aacute;c ch&iacute;nh cung cấp sản phẩm, dịch vụ bảo hiểm cho đo&agrave;n vi&ecirc;n ng&agrave;nh ng&acirc;n h&agrave;ng. B&ecirc;n cạnh đ&oacute;, &ocirc;ng Dũng b&agrave;y tỏ hy vọng buổi Lễ k&yacute; kết hợp t&aacute;c ng&agrave;y h&ocirc;m nay sẽ l&agrave; dấu mốc quan trọng trong sự hợp t&aacute;c hiệu quả giữa hai b&ecirc;n từ nay về sau. <br /> <br />Ph&aacute;t biểu tại buổi lễ, b&agrave; Nguyễn Thị Thu Hồng &ndash; Ph&oacute; Chủ tịch Tổng li&ecirc;n đo&agrave;n lao động Việt Nam gửi lời cảm ơn tới VBI v&agrave; c&aacute;c đối t&aacute;c đ&atilde; chung tay x&acirc;y dựng chương tr&igrave;nh với nhiều lợi &iacute;ch thiết thực, đặc biệt l&agrave; sự quan t&acirc;m của VBI tới sức khỏe của gia đ&igrave;nh c&aacute;c Đo&agrave;n vi&ecirc;n n&oacute;i chung v&agrave; c&aacute;c Đo&agrave;n vi&ecirc;n nữ n&oacute;i ri&ecirc;ng, th&ocirc;ng qua ưu đ&atilde;i về ph&iacute; v&agrave; quyền lợi của sản phẩm Bảo hiểm sức khỏe.</span></p>\r\n<p><img src=\"../../../uploads/VBI ky ket Cong doan NH_1.jpg\" alt=\"\" width=\"1024\" height=\"445\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Ảnh: TGĐ VBI (thứ 4 từ tr&aacute;i sang) nhận hoa v&agrave; kỷ niệm chương từ C&ocirc;ng đo&agrave;n Ng&acirc;n h&agrave;ng Việt Nam</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Lễ k&yacute; kết thoả thuận hợp t&aacute;c giữa VBI với C&ocirc;ng đo&agrave;n Ng&acirc;n h&agrave;ng Việt Nam l&agrave; hoạt động rất c&oacute; &yacute; nghĩa, thiết thực, th&uacute;c đẩy sự ph&aacute;t triển của cả hai b&ecirc;n, đ&oacute;ng g&oacute;p v&agrave;o sự ph&aacute;t triển chung của cộng đồng.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 08:58:22', '2018-03-21 19:58:22', 'images/news/1520582273_VBI-ky-ke_636271852858815158_HasThumb_Thumb.jpg.png'),
(217, 2, 'VBI năm thứ 3 liên tiếp nhận danh hiệu “Thương hiệu mạnh Việt Nam”', 'vbi-nam-thu-3-lien-tiep-nhan-danh-hieu-thuong-hieu-manh-viet-nam', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 8/4/2017 tại H&agrave; Nội, Thời b&aacute;o Kinh tế Việt Nam đ&atilde; tổ chức &ldquo;Li&ecirc;n hoan vinh danh c&aacute;c Doanh nghiệp Rồng V&agrave;ng v&agrave; Thương hiệu mạnh Việt Nam&rdquo;. Năm nay l&agrave; năm thứ 3 li&ecirc;n tiếp VBI được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo;.&nbsp;</span></p>', '<p>S&aacute;ng ng&agrave;y 8/4/2017 tại H&agrave; Nội, Thời b&aacute;o Kinh tế Việt Nam đ&atilde; tổ chức &ldquo;Li&ecirc;n hoan vinh danh c&aacute;c Doanh nghiệp Rồng V&agrave;ng v&agrave; Thương hiệu mạnh Việt Nam&rdquo;. Năm nay l&agrave; năm thứ 3 li&ecirc;n tiếp VBI được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo;.</p>\r\n<p>&ldquo;Li&ecirc;n hoan vinh danh c&aacute;c Doanh nghiệp Rồng V&agrave;ng v&agrave; Thương hiệu mạnh Việt Nam&rdquo; l&agrave; chương tr&igrave;nh do Thời b&aacute;o Kinh tế Việt Nam khởi xướng từ năm 2001. Chương tr&igrave;nh được tổ chức để ghi nhận, động vi&ecirc;n v&agrave; cổ vũ những doanh nghiệp c&oacute; th&agrave;nh t&iacute;ch xuất sắc trong hoạt động sản xuất, kinh doanh, cung ứng dịch vụ; &aacute;p dụng c&aacute;c giải ph&aacute;p, c&ocirc;ng nghệ bảo vệ m&ocirc;i trường; thực hiện đầy đủ c&aacute;c nghĩa vụ nộp thuế cũng như đảm bảo quyền lợi cho người lao động; t&iacute;ch cực tham gia c&aacute;c hoạt động x&atilde; hội, cộng đồng.</p>\r\n<p>VBI vinh dự nằm trong Top 100 doanh nghiệp c&oacute; thương hiệu mạnh nhất Việt Nam do Ban Bi&ecirc;n tập v&agrave; độc giả của Thời B&aacute;o Kinh tế Việt Nam b&igrave;nh chọn.</p>\r\n<p>Năm 2016, VBI đ&atilde; ho&agrave;n th&agrave;nh xuất sắc c&aacute;c chỉ ti&ecirc;u kinh doanh với doanh thu ph&iacute; bảo hiểm đạt 781 tỷ, tăng trưởng 42% so với năm 2015 (trong đ&oacute;, doanh thu bảo hiểm gốc đạt 692 tỷ đồng, tăng trưởng 42,2% so với năm 2015; doanh thu nhận t&aacute;i bảo hiểm đạt 89 tỷ đồng, tăng trưởng 41,3% so với năm 2015); lợi nhuận đạt 60,1 tỷ đồng, tăng trưởng 13,2% so với năm 2015; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 42,9%.<br /> <br />Đặc biệt, VBI đ&atilde; vinh dự nhận nhiều danh hiệu uy t&iacute;n như &ldquo;Top 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam&rdquo; do Vietnam Report c&ocirc;ng bố, &ldquo;Top 20 doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng&rdquo;. Trong 4 năm li&ecirc;n tiếp từ 2012 - 2015, VBI li&ecirc;n tục được vinh danh trong &ldquo;Top 1000 đơn vị nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam&rdquo;. Những danh hiệu v&agrave; giải thưởng tr&ecirc;n như lời khẳng định cho chất lượng dịch vụ vượt trội v&agrave; những nỗ lực trong c&ocirc;ng t&aacute;c x&acirc;y dựng v&agrave; ph&aacute;t triển thương hiệu của VBI.</p>\r\n<p>Kế hoạch kinh doanh năm 2017, VBI sẽ ch&iacute;nh thức triển khai kế hoạch cổ phần h&oacute;a, lựa chọn nh&agrave; đầu tư chiến lược nước ngo&agrave;i.</p>\r\n<p><img src=\"../../../uploads/VBI thuong hieu manh Viet Nam_web.jpg\" alt=\"\" width=\"1024\" height=\"586\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng Nguyễn Hồng Phong - Ph&oacute; Tổng Gi&aacute;m đốc VBI nhập C&uacute;p Thương hiệu mạnh Việt Nam 2016)</em></p>\r\n<p>Với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững, trong năm 2017, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u chiến lược chuyển đổi m&ocirc; h&igrave;nh nhằm n&acirc;ng cao hiệu quả kinh doanh, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:58:52', '2018-03-21 19:58:52', 'images/news/1520582359_IMG_931_636272545165405615_HasThumb_Thumb.JPG.png'),
(218, 2, 'Thông báo bổ sung địa điểm nhận hồ sơ yêu cầu trả tiền bảo hiểm sức khỏe tại Miền Nam', 'thong-bao-bo-sung-dia-diem-nhan-ho-so-yeu-cau-tra-tien-bao-hiem-suc-khoe-tai-mien-nam', '<p><span style=\"font-family: Arial;\">Bắt đầu từ ng&agrave;y 15/04/2017, VBI bổ sung th&ecirc;m địa điểm tiếp nhận v&agrave; giải quyết hồ sơ y&ecirc;u cầu trả tiền bảo hiểm sức khỏe VBI Care d&agrave;nh cho c&aacute;n bộ Vietinbank v&agrave; người th&acirc;n tại Miền Nam&nbsp;</span></p>', '<p>Bảo hiểm VietinBank (VBI) xin tr&acirc;n trọng th&ocirc;ng b&aacute;o!</p>\r\n<p>Bắt đầu từ ng&agrave;y <strong>15/04/2017</strong>, VBI bổ sung th&ecirc;m địa điểm tiếp nhận v&agrave; giải quyết hồ sơ y&ecirc;u cầu trả tiền bảo hiểm sức khỏe VBI Care d&agrave;nh cho c&aacute;n bộ Vietinbank v&agrave; người th&acirc;n tại:</p>\r\n<ul>\r\n<li><strong>Ph&ograve;ng bồi thường con người tại Miền Nam - VBI CN Hồ Ch&iacute; Minh</strong><br /><strong>​Tầng 7, 354-356 Nguyễn Thị Minh Khai, Quận 3, Tp. Hồ Ch&iacute; Minh</strong></li>\r\n</ul>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ Hotline: 0934 596 339</p>\r\n<p>Cảm ơn qu&yacute; kh&aacute;ch đ&atilde;, đang v&agrave; sẽ tiếp tục đồng h&agrave;nh c&ugrave;ng Bảo hiểm VietinBank (VBI)!</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-09 20:00:44', '2018-03-09 20:00:44', 'images/news/1520582444_thong-bao_636275938330900485_HasThumb_Thumb.jpg.png'),
(219, 2, 'VBI – Công ty bảo hiểm tăng trưởng nhanh nhất Việt Nam', 'vbi-cong-ty-bao-hiem-tang-truong-nhanh-nhat-viet-nam', '<p><span style=\"font-family: Arial;\">Tại Lễ c&ocirc;ng bố v&agrave; t&ocirc;n vinh 500 Doanh nghiệp tăng trưởng nhanh nhất Việt Nam năm 2017 được tổ chức ng&agrave;y 12/04/2017, Bảo hiểm VietinBank (VBI) vinh dự đứng vị tr&iacute; số 1 trong ng&agrave;nh Bảo hiểm v&agrave; xếp hạng 79 trong xếp hạng FAST500 - danh s&aacute;ch \"những ng&ocirc;i sao đang l&ecirc;n\" tr&ecirc;n bầu trời kin...&nbsp;</span></p>', '<p>Tại Lễ c&ocirc;ng bố v&agrave; t&ocirc;n vinh 500 Doanh nghiệp tăng trưởng nhanh nhất Việt Nam năm 2017 được tổ chức ng&agrave;y 12/04/2017, Bảo hiểm VietinBank (VBI) vinh dự đứng vị tr&iacute; số 1 trong ng&agrave;nh Bảo hiểm v&agrave; xếp hạng 79 trong xếp hạng FAST500 - danh s&aacute;ch \"những ng&ocirc;i sao đang l&ecirc;n\" tr&ecirc;n bầu trời kinh tế Việt Nam.</p>\r\n<p>Bảng xếp hạng FAST500 được x&acirc;y dựng dựa tr&ecirc;n nguy&ecirc;n tắc kh&aacute;ch quan v&agrave; độc lập, khoa học v&agrave; tu&acirc;n thủ theo c&aacute;c chuẩn mực quốc tế hiện h&agrave;nh. Thứ hạng của c&aacute;c doanh nghiệp trong Bảng xếp hạng được sắp xếp dựa tr&ecirc;n ti&ecirc;u ch&iacute; tăng trưởng k&eacute;p (CAGR) về doanh thu v&agrave; hiệu quả kinh doanh.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, c&aacute;c ti&ecirc;u ch&iacute; như tổng t&agrave;i sản, tổng lao động, lợi nhuận sau thuế v&agrave; uy t&iacute;n doanh nghiệp tr&ecirc;n truyền th&ocirc;ng,&hellip; được sử dụng như yếu tố bổ trợ để x&aacute;c định quy m&ocirc; cũng như vị thế của doanh nghiệp trong ng&agrave;nh hoạt động. Danh s&aacute;ch c&aacute;c doanh nghiệp ng&agrave;nh bảo hiểm tăng trưởng nhanh nhất Việt Nam được đăng tải đầy đủ tr&ecirc;n website <a href=\"http://www.fast500.vn/Nganh-nghe/Bao-hiem-Post/67.html\">http://www.fast500.vn/Nganh-nghe/Bao-hiem-Post/67.html</a></p>\r\n<p><img src=\"../../../uploads/VBI cong ty bao hiem tang truong nhanh nhat VN.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Ph&oacute; TGĐ VBI nhận Giấy chứng nhận Top 500 doanh nghiệp tăng trưởng nhanh nhất Việt Nam)</em></p>\r\n<p>Hiện nay, VBI l&agrave; c&ocirc;ng ty bảo hiểm c&oacute; tốc độ ph&aacute;t triển nhanh v&agrave; mạnh với mức tăng trưởng doanh thu ở mức tr&ecirc;n 55%/năm trong v&ograve;ng 5 năm, giai đoạn 2012 &ndash; 2016. Tốc độ tăng trưởng của VBI lu&ocirc;n đạt cao hơn mức tăng trưởng b&igrave;nh qu&acirc;n của thị trường bảo hiểm. Năm 2016, doanh thu ph&iacute; bảo hiểm của VBI đạt 781 tỷ, tăng trưởng 42% so với năm 2015 (trong đ&oacute; mức tăng trưởng doanh thu b&igrave;nh qu&acirc;n to&agrave;n thị trường l&agrave; 16%).</p>\r\n<p>Năm 2017, VBI đặt mục ti&ecirc;u doanh thu 1000 tỷ v&agrave; ch&iacute;nh thức triển khai kế hoạch cổ phần h&oacute;a, lựa chọn nh&agrave; đầu tư chiến lược nước ngo&agrave;i.</p>\r\n<p>B&ecirc;n cạnh FAST500, mới đ&acirc;y v&agrave;o ng&agrave;y 08/4/2017, VBI cũng được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo; do Ban Bi&ecirc;n tập v&agrave; độc giả của Thời B&aacute;o Kinh tế Việt Nam b&igrave;nh chọn. Đ&acirc;y l&agrave; năm thứ 3 li&ecirc;n tiếp VBI nhận danh hiệu n&agrave;y.</p>\r\n<p>Việc li&ecirc;n tục được vinh danh trong c&aacute;c bảng xếp hạng uy t&iacute;n đ&atilde; khẳng định nỗ lực kh&ocirc;ng ngừng trong hoạt động kinh doanh cũng như c&ocirc;ng t&aacute;c x&acirc;y dựng v&agrave; ph&aacute;t triển thương hiệu VBI. Đồng thời, đ&acirc;y cũng l&agrave; nguồn động lực để VBI tự tin đặt kỳ vọng v&agrave;o sự tăng trưởng đột ph&aacute;, mạnh mẽ hơn nữa trong thời gian tới.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 08:59:30', '2018-03-21 19:59:30', 'images/news/1520582537_VBI-cong-_636276760650698163_HasThumb_Thumb.jpg.png'),
(220, 2, 'VBI tài trợ bảo hiểm giải thưởng Hole-in-one trị giá gần 2 tỷ đồng', 'vbi-tai-tro-bao-hiem-giai-thuong-hole-in-one-tri-gia-gan-2-ty-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 22/4/2017, tại C&acirc;u lạc bộ Golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh, đ&atilde; diễn ra &ldquo;Giải Golf Hội thao to&agrave;n hệ thống VietinBank lần thứ 10 năm 2017\". VBI tham gia t&agrave;i trợ bảo hiểm cho giải thưởng Hole-in-one l&agrave; 01 chiếc xe Mercedes C300 trị gi&aacute; gần 2 tỷ đồng.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 22/4/2017, tại C&acirc;u lạc bộ Golf Ho&agrave;ng Gia, tỉnh Ninh B&igrave;nh, đ&atilde; diễn ra &ldquo;Giải Golf Hội thao to&agrave;n hệ thống VietinBank lần thứ 10 năm 2017\". Giải đấu c&oacute; sự tham gia của hơn 100 golfers được chia th&agrave;nh 4 bảng thi c&ugrave;ng nhau chinh phục 10 hố tại s&acirc;n C&acirc;u lạc bộ Golf Ho&agrave;ng Gia. Sau nửa ng&agrave;y thi đấu s&ocirc;i nổi c&ugrave;ng với tinh thần đo&agrave;n kết, trung thực v&agrave; cao thượng, Giải Golf thuộc Hội thao to&agrave;n hệ thống VietinBank lần thứ 10 năm 2017 đ&atilde; kết th&uacute;c tốt đẹp.</span></p>\r\n<p><img src=\"../../../uploads/giai golf VTB T4_2017.jpg\" alt=\"\" width=\"700\" height=\"385\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(C&aacute;c golfer đạt huy chương chụp ảnh lưu niệm c&ugrave;ng Ban L&atilde;nh đạo VietinBank v&agrave; Ban Tổ chức giải)</span></em></p>\r\n<p><img src=\"../../../uploads/giai golf VTB T4_2017_1(1).jpg\" alt=\"\" width=\"700\" height=\"444\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(C&aacute;c golfer đạt giải Nhất, Nh&igrave; tại 4 bảng thi đấu)</span></em></p>\r\n<p>VBI tham gia t&agrave;i trợ bảo hiểm cho giải thưởng Hole-in-one l&agrave; 01 chiếc xe Mercedes C300 trị gi&aacute; gần 2 tỷ đồng.</p>\r\n<p>Trong thời gian qua, VBI lu&ocirc;n l&agrave; một trong những c&ocirc;ng ty h&agrave;ng đầu Việt Nam bảo hiểm cho c&aacute;c giải Golf trong nước với ch&iacute;nh s&aacute;ch chi trả bồi thường ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 09:00:12', '2018-03-21 20:00:12', 'images/news/1520582630_giai-golf_636286512001630027_HasThumb_Thumb.jpg.png'),
(221, 2, 'Thông báo đổi mới quy trình bảo lãnh viện phí', 'thong-bao-doi-moi-quy-trinh-bao-lanh-vien-phi', '<p><span style=\"font-family: Arial;\">Với mong muốn n&acirc;ng cao chất lượng dịch vụ khi Qu&yacute; kh&aacute;ch đến thăm kh&aacute;m tại c&aacute;c Cơ sở y tế thuộc hệ thống bảo l&atilde;nh viện ph&iacute; của VBI, VBI th&ocirc;ng b&aacute;o một số đổi mới trong Quy tr&igrave;nh bảo l&atilde;nh viện ph&iacute;&nbsp;</span></p>', '<p><img src=\"../../../uploads/cover-news-max-width-700px (1).jpg\" alt=\"\" width=\"1366\" height=\"326\" /></p>\r\n<p><strong>Bảo hiểm VietinBank (VBI) xin tr&acirc;n trọng th&ocirc;ng b&aacute;o!</strong><br /> <br />Với mong muốn n&acirc;ng cao chất lượng dịch vụ khi Qu&yacute; kh&aacute;ch đến thăm kh&aacute;m tại c&aacute;c Cơ sở y tế thuộc hệ thống bảo l&atilde;nh viện ph&iacute; của VBI, VBI th&ocirc;ng b&aacute;o một số đổi mới trong Quy tr&igrave;nh bảo l&atilde;nh viện ph&iacute; như sau:</p>\r\n<ul>\r\n<li>Đối với Kh&aacute;ch h&agrave;ng từ 14 tuổi trở l&ecirc;n: Qu&yacute; kh&aacute;ch chỉ cần mang theo giấy tờ t&ugrave;y th&acirc;n c&oacute; ảnh (Chứng minh nh&acirc;n d&acirc;n/Hộ chiếu/Thẻ căn cước c&ocirc;ng d&acirc;n).</li>\r\n<li>Đối với Kh&aacute;ch h&agrave;ng dưới 14 tuổi:</li>\r\n</ul>\r\n<ul style=\"list-style-type: circle;\">\r\n<li style=\"padding-left: 30px;\">Trường hợp đi c&ugrave;ng bố/mẹ hoặc người gi&aacute;m hộ hợp ph&aacute;p: Xuất tr&igrave;nh Giấy khai sinh của em b&eacute; v&agrave; giấy tờ t&ugrave;y th&acirc;n c&oacute; ảnh của bố/mẹ/người gi&aacute;m hộ hợp ph&aacute;p.</li>\r\n<li style=\"padding-left: 30px;\">Trường hợp kh&ocirc;ng đi c&ugrave;ng bố/mẹ/người gi&aacute;m hộ hợp ph&aacute;p: Xuất tr&igrave;nh Giấy khai sinh của em b&eacute;, giấy tờ t&ugrave;y th&acirc;n c&oacute; ảnh của người đi c&ugrave;ng v&agrave; Số/Giấy chứng nhận điện tử.</li>\r\n</ul>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ <strong>Hotline: 0934 596 339</strong><br /> <br />Cảm ơn qu&yacute; kh&aacute;ch đ&atilde;, đang v&agrave; sẽ tiếp tục đồng h&agrave;nh c&ugrave;ng Bảo hiểm VietinBank!</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 09:00:40', '2018-03-21 20:00:40', 'images/news/1520582835_facebook-_636295927568966498_HasThumb_Thumb.jpg.png'),
(222, 2, 'VBI tài trợ bảo hiểm Hội thao VietinBank với mức trách nhiệm lên tới hơn 18 tỷ đồng', 'vbi-tai-tro-bao-hiem-hoi-thao-vietinbank-voi-muc-trach-nhiem-len-toi-hon-18-ty-dong', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 6/5/2017, tại H&agrave; Nội đ&atilde; diễn ra Lễ khai mạc hội thao VietinBank Khu vực I - năm 2017. Bảo hiểm VietinBank (VBI) t&agrave;i trợ bảo hiểm Hội thao với mức tr&aacute;ch nhiệm l&ecirc;n tới 18,3 tỷ đồng.&nbsp;</span></p>', '<p>Ng&agrave;y 6/5/2017, tại H&agrave; Nội đ&atilde; diễn ra Lễ khai mạc hội thao VietinBank Khu vực I - năm 2017. Bảo hiểm VietinBank (VBI) t&agrave;i trợ bảo hiểm Hội thao với mức tr&aacute;ch nhiệm l&ecirc;n tới 18,3 tỷ đồng.</p>\r\n<p>Hội thao c&oacute; sự tham dự của gần 400 VĐV đến từ c&aacute;c chi nh&aacute;nh VietinBank. C&aacute;c VĐV đ&atilde; đăng k&yacute; tham gia thi đấu tại c&aacute;c bộ m&ocirc;n như: b&oacute;ng đ&aacute;, cầu l&ocirc;ng, cờ tướng, quần vợt, b&oacute;ng b&agrave;n, v.v&hellip;</p>\r\n<p>Điểm đặc biệt của Hội thao VietinBank l&agrave; c&aacute;c VĐV đều l&agrave; những VĐV kh&ocirc;ng chuy&ecirc;n, những c&aacute;n bộ đang l&agrave;m việc tại VietinBank với tinh thần thi đấu, cống hiến hết m&igrave;nh để gi&agrave;nh huy chương về cho c&aacute; nh&acirc;n, đồng đội, cho chi nh&aacute;nh.</p>\r\n<p><img src=\"../../../uploads/1a26602755e011419c5f68cd08adfe10.jpg\" alt=\"\" width=\"700\" height=\"400\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: C&aacute;c đại biểu chụp ảnh lưu niệm với vận động vi&ecirc;n tham dự Hội thao)</em></p>\r\n<p style=\"text-align: left;\"><img src=\"../../../uploads/20170507_173345(0).jpg\" alt=\"\" width=\"1366\" height=\"959\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: VĐV V&otilde; Việt Cường -VBI đạt giải 3 Đơn Nam bộ m&ocirc;n Quần vợt)</em></p>\r\n<p style=\"text-align: left;\"><img src=\"../../../uploads/18278206_908908355915844_3562465942420109072_o(2).jpg\" alt=\"\" width=\"1366\" height=\"544\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Cổ động vi&ecirc;n VBI tham gia cổ vũ tại Hội thao)</em></p>\r\n<p>Đồng h&agrave;nh c&ugrave;ng c&aacute;c VĐV với sản phẩm Bảo hiểm sức khỏe VBIcare, VBI đ&atilde; g&oacute;p phần bảo vệ sức khỏe cho gần 400 VĐV tham gia Hội thao gi&uacute;p c&aacute;c VĐV y&ecirc;n t&acirc;m trong suốt qu&aacute; tr&igrave;nh thi đấu v&agrave; đạt nhiều th&agrave;nh t&iacute;ch cao.</p>\r\n<p>Th&ocirc;ng qua Hội thao, VBI c&oacute; cơ hội được giao lưu c&ugrave;ng c&aacute;c chi nh&aacute;nh VietinBank trong hệ thống, được cống hiến sức trẻ cũng như sự năng động của m&igrave;nh cho c&aacute;c hoạt động đo&agrave;n thể, đặc biệt l&agrave; được mang đến những dịch vụ bảo hiểm tiện &iacute;ch cho c&aacute;c c&aacute;n bộ VietinBank trong ch&iacute;nh những hoạt động văn h&oacute;a, thể thao.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 09:01:52', '2018-03-21 20:01:52', 'images/news/1520582965_e33f5552a_636298610884852707_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(223, 2, 'Cùng VBI chia sẻ yêu thương với Cuộc thi ảnh \"Gia đình là số 1\"', 'cung-vbi-chia-se-yeu-thuong-voi-cuoc-thi-anh-gia-dinh-la-so-1', '<p><span style=\"font-family: Arial;\">GIA Đ&Igrave;NH &ndash; đối với mỗi ch&uacute;ng ta &ndash; lu&ocirc;n l&agrave; điều tuyệt vời v&agrave; quan trọng nhất. Với mong muốn chia sẻ những kỉ niệm, khoảnh khắc &yacute; nghĩa của Qu&yacute; kh&aacute;ch v&agrave; người th&acirc;n, nh&acirc;n Ng&agrave;y Gia đ&igrave;nh Việt Nam 28/6, Bảo hiểm VietinBank &ndash; VBI tổ chức Cuộc thi ảnh &ldquo;Gia đ&igrave;nh l&agrave; số 1&rdquo; với nhiều giải th...</span></p>', '<p>&nbsp;GIA Đ&Igrave;NH &ndash; đối với mỗi ch&uacute;ng ta &ndash; lu&ocirc;n l&agrave; điều tuyệt vời v&agrave; quan trọng nhất. Với mong muốn chia sẻ những kỉ niệm, khoảnh khắc &yacute; nghĩa của Qu&yacute; kh&aacute;ch v&agrave; người th&acirc;n, nh&acirc;n Ng&agrave;y Gia đ&igrave;nh Việt Nam 28/6, Bảo hiểm VietinBank &ndash; VBI tổ chức Cuộc thi ảnh &ldquo;Gia đ&igrave;nh l&agrave; số 1&rdquo; với nhiều giải thưởng hấp dẫn:<br />&middot; Giải nhất: 01 Voucher nghỉ dưỡng 3 ng&agrave;y 2 đ&ecirc;m tại hệ thống kh&aacute;ch sạn 5 sao FLC Luxury Hotel tr&ecirc;n to&agrave;n quốc trị gi&aacute; l&ecirc;n tới 5 triệu đồng.<br />&middot; Giải nh&igrave;: 2 giải: Vali du lịch American Tourist trị gi&aacute; 3 triệu đồng.<br />&middot; Giải ba: 10 giải: thẻ Giftcard Vingroup trị gi&aacute; 100.000 đồng.</p>\r\n<p>C&aacute;ch thức tham gia<br />&middot; Bước 1: Th&iacute;ch (Like) trang Fanpage của Bảo hiểm VietinBank - VBI<br />&middot; Bước 2: Gửi tối đa 03 ảnh gia đ&igrave;nh k&egrave;m m&ocirc; tả (caption) qua inbox Facebook hoặc địa chỉ mail: Marketing.vbi@vietinbank.vn<br />&middot; Bước 3: Chia sẻ b&agrave;i dự thi l&ecirc;n trang facebook c&aacute; nh&acirc;n đi k&egrave;m 02 hashtag #VBI #Gia_đ&igrave;nh_l&agrave;_Số_1 v&agrave; mời bạn b&egrave;, người th&acirc;n v&agrave;o b&igrave;nh chọn cho b&agrave;i dự thi của m&igrave;nh bằng c&aacute;ch like hoặc share b&agrave;i dự thi tr&ecirc;n fanpage VBI.</p>\r\n<p>Thời gian gửi ảnh v&agrave; b&igrave;nh chọn: từ ng&agrave;y 29/05 tới 10h s&aacute;ng ng&agrave;y 19/06/2017. Kết quả sẽ được c&ocirc;ng bố v&agrave;o: ng&agrave;y 20/06/2017.</p>\r\n<p>Chi tiết thể lệ cuộc thi xem <a href=\"http://bit.ly/The-le-chuong-trinh-Gia-dinh-la-so-1\">tại đ&acirc;y</a></p>\r\n<p><img src=\"../../../uploads/Gia-dinh-la-so-1-1.jpg\" alt=\"\" width=\"940\" height=\"788\" /></p>\r\n<p>Qua cuộc thi n&agrave;y, VBI hy vọng được c&ugrave;ng Qu&yacute; Kh&aacute;ch chia sẻ những gi&acirc;y ph&uacute;t đ&aacute;ng nhớ b&ecirc;n gia đ&igrave;nh, những c&acirc;u chuyện thường nhật, mộc mạc nhưng đong đầy t&igrave;nh thương y&ecirc;u th&ocirc;ng qua những b&agrave;i viết v&agrave; h&igrave;nh ảnh tham gia cuộc thi &ldquo;Gia đ&igrave;nh l&agrave; số 1&rdquo;. VBI mong rằng cuộc thi sẽ nhận được sự ủng hộ, quan t&acirc;m v&agrave; đồng h&agrave;nh của Qu&yacute; Kh&aacute;ch.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 09:02:20', '2018-03-21 20:02:20', 'images/news/1520583064_pic-New-i_636316750917306304_HasThumb_Thumb.jpg.png'),
(224, 2, 'Thông báo Quy trình Bảo lãnh viện phí tại Bệnh viện Từ Dũ', 'thong-bao-quy-trinh-bao-lanh-vien-phi-tai-benh-vien-tu-du', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) xin tr&acirc;n trọng th&ocirc;ng b&aacute;o! Do đặc th&ugrave; n&ecirc;n Bệnh viện Từ Dũ chỉ chấp nhận bảo l&atilde;nh viện ph&iacute; cho những bệnh nh&acirc;n: sinh thường, sinh mổ hoặc phẫu thuật bệnh phụ khoa nội tr&uacute;, c&oacute; &ldquo;Giấy chứng nhận bảo l&atilde;nh chi ph&iacute; y tế&rdquo; do Bảo hiểm VietinBank (VBI) cấp v&agrave; nộ...&nbsp;</span></p>', '<p><strong>Bảo hiểm VietinBank (VBI) xin tr&acirc;n trọng th&ocirc;ng b&aacute;o!</strong></p>\r\n<p>Do đặc th&ugrave; n&ecirc;n Bệnh viện Từ Dũ chỉ chấp nhận bảo l&atilde;nh viện ph&iacute; cho những bệnh nh&acirc;n: sinh thường, sinh mổ hoặc phẫu thuật bệnh phụ khoa nội tr&uacute;, c&oacute; &ldquo;Giấy chứng nhận bảo l&atilde;nh chi ph&iacute; y tế&rdquo; do Bảo hiểm VietinBank (VBI) cấp v&agrave; nộp bản ch&iacute;nh cho Bệnh viện trước khi nhập viện.</p>\r\n<p>Trong trường hợp Qu&yacute; kh&aacute;ch c&oacute; nhu cầu bảo l&atilde;nh viện ph&iacute; nội tr&uacute; tại Bệnh viện Từ Dũ xin vui l&ograve;ng li&ecirc;n hệ với Trung t&acirc;m Bảo l&atilde;nh viện ph&iacute; VBI qua Hotline: 0934 596 339 để được tư vấn v&agrave; nhận &ldquo;Giấy chứng nhận bảo l&atilde;nh chi ph&iacute; y tế&rdquo; trước khi nhập viện.</p>\r\n<p>Cảm ơn qu&yacute; kh&aacute;ch đ&atilde;, đang v&agrave; sẽ tiếp tục đồng h&agrave;nh c&ugrave;ng Bảo hiểm VietinBank!</p>\r\n<p><img src=\"../../../uploads/quy-trinh-bao-lanh-tu-du-2.jpg\" alt=\"\" width=\"1366\" height=\"1145\" /></p>', '2018-03-21 09:03:08', '2018-03-21 20:03:08', 'images/news/1520583124_Quy-trinh_636332238880343398_HasThumb_Thumb.jpg.png'),
(225, 2, 'VBI và KPMG ký kết hợp tác nâng cấp khung quản trị rủi ro', 'vbi-va-kpmg-ky-ket-hop-tac-nang-cap-khung-quan-tri-rui-ro', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 06/06/2017, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) bắt đầu triển khai dự &aacute;n n&acirc;ng cấp hệ thống quản l&yacute; rủi ro, hướng tới c&aacute;c chuẩn quốc tế bằng việc k&yacute; kết hợp đồng với KPMG.&nbsp;</span></p>', '<p><em><strong>Ng&agrave;y 06/06/2017, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) bắt đầu triển khai dự &aacute;n n&acirc;ng cấp hệ thống quản l&yacute; rủi ro, hướng tới c&aacute;c chuẩn quốc tế bằng việc k&yacute; kết hợp đồng với KPMG.</strong></em></p>\r\n<p>Theo hợp đồng vừa k&yacute;, KPMG sẽ tư vấn x&acirc;y dựng khung Quản l&yacute; rủi ro v&agrave; triển khai Quản l&yacute; rủi ro chuy&ecirc;n s&acirc;u đến c&aacute;c Quy tr&igrave;nh Nghiệp vụ của VBI. Dự &aacute;n n&agrave;y cho ph&eacute;p VBI hướng tới thực hiện c&aacute;c th&ocirc;ng lệ tốt nhất theo chuẩn mực quốc tế, đồng thời, gi&uacute;p VBI đảm bảo tu&acirc;n thủ c&aacute;c quy định của Bộ t&agrave;i ch&iacute;nh về quản l&yacute; rủi ro.</p>\r\n<p>Việc triển khai dự &aacute;n tr&ecirc;n sẽ gi&uacute;p VBI x&acirc;y dựng một khung quản trị vững mạnh, bảo vệ VBI trước c&aacute;c yếu tố rủi ro bất ngờ, ổn định hiệu quả c&ocirc;ng việc, hỗ trợ Ban điều h&agrave;nh trong việc ra quyết định, xem x&eacute;t chi ph&iacute;, lợi &iacute;ch cũng như gi&uacute;p cải thiện việc ph&acirc;n bổ nguồn lực; từ kh&acirc;u chọn lọc kh&aacute;ch h&agrave;ng, đ&aacute;nh gi&aacute; rủi ro, đến quy tr&igrave;nh thẩm định, cấp bảo hiểm, quản l&yacute; v&agrave; theo d&otilde;i bồi thường.</p>\r\n<p>&Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI chia sẻ: &ldquo;Việc VBI tập trung ch&uacute; trọng v&agrave;o vấn đề quản l&yacute; rủi ro l&agrave; minh chứng cho tầm nh&igrave;n xa cũng như khả năng đi tắt đ&oacute;n đầu của VBI. Sự hợp t&aacute;c giữa VBI v&agrave; KPMG - một trong những c&ocirc;ng ty cung cấp dịch vụ tư vấn h&agrave;ng đầu thế giới sẽ mở ra những bước tiến mới, đưa VBI trở th&agrave;nh c&ocirc;ng ty bảo hiểm ti&ecirc;n phong trong c&ocirc;ng t&aacute;c Quản trị rủi ro tại thị trường Việt Nam&rdquo;.</p>\r\n<p><img src=\"../../../uploads/AIMG_9536.jpg\" alt=\"\" width=\"1366\" height=\"729\" /></p>\r\n<p>Thực tế, trong 3 năm vừa qua, VBI đ&atilde; v&agrave; đang c&oacute; sự chuyển đổi mạnh mẽ. VBI đ&atilde; nỗ lực thực thi c&aacute;c chủ đề chiến lược quan trọng với kỳ vọng tạo ra bước đột ph&aacute;, đưa VBI tiến gần tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt nam. Điều n&agrave;y đ&ograve;i hỏi phải c&oacute; khung quản trị rủi ro chặt chẽ, chuy&ecirc;n nghiệp để đảm bảo sự ph&aacute;t triển bền vững của VBI cũng như đem lại những gi&aacute; trị tốt nhất cho c&aacute;c kh&aacute;ch h&agrave;ng.</p>\r\n<p>Trong qu&aacute; tr&igrave;nh cổ phần h&oacute;a v&agrave; t&igrave;m kiếm đối t&aacute;c đầu tư chiến lược, VBI đ&atilde; hoạch định một c&aacute;ch b&agrave;i bản chiến lược ph&aacute;t triển giai đoạn 2017-2020. Đ&oacute; l&agrave; đẩy mạnh ph&aacute;t triển v&agrave;o thị trường b&aacute;n lẻ, doanh nghiệp vừa v&agrave; nhỏ tr&ecirc;n nền tảng một khung quản trị rủi ro ti&ecirc;n tiến; tiếp tục n&acirc;ng cao cấu tr&uacute;c quản l&yacute; rủi ro hiện c&oacute; của VBI, hướng đến tiếp cận với c&aacute;c chuẩn mực quốc tế.</p>\r\n<p>B&agrave; Đỗ Thị Thu H&agrave; &ndash; Ph&oacute; Tổng gi&aacute;m đốc KPMG khẳng định v&agrave; cam kết: &ldquo;Đội ngũ c&aacute;n bộ chuy&ecirc;n vi&ecirc;n của KPMG sẽ l&agrave;m việc hết m&igrave;nh với mục ti&ecirc;u ho&agrave;n th&agrave;nh đ&uacute;ng tiến độ, gi&uacute;p đưa hệ thống đi v&agrave;o hoạt động ổn định gi&uacute;p VBI nhanh ch&oacute;ng ho&agrave;n th&agrave;nh những mục ti&ecirc;u đề ra&rdquo;.</p>\r\n<p>Như vậy, th&ocirc;ng qua việc k&yacute; kết hợp t&aacute;c với KPMG n&acirc;ng cấp hệ thống quản trị rủi ro, VBI đ&atilde; thể hiện r&otilde; quyết t&acirc;m n&acirc;ng cao gi&aacute; trị đối với kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c, cũng như đ&oacute;ng g&oacute;p v&agrave;o qu&aacute; tr&igrave;nh ph&aacute;t triển bền vững, khẳng định gi&aacute; trị thương hiệu của VBI.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 09:03:37', '2018-03-21 20:03:37', 'images/news/1520583191_AIMG_953_636323584640426698_HasThumb_Thumb.jpg.png'),
(226, 2, 'Bảo hiểm VietinBank- Công ty bảo hiểm phi nhân thọ Việt Nam được yêu thích nhất trên Facebook', 'bao-hiem-vietinbank-cong-ty-bao-hiem-phi-nhan-tho-viet-nam-duoc-yeu-thich-nhat-tren-facebook', '<p><span style=\"font-family: Arial;\">Qua gần 1 năm hoạt động, Fanpage của VBI vượt l&ecirc;n dẫn đầu những fanpage c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ Việt Nam được y&ecirc;u th&iacute;ch nhất tr&ecirc;n Facebook với hơn 60.000 lượt th&iacute;ch trang.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Qua gần 1 năm hoạt động, Fanpage của VBI vượt l&ecirc;n dẫn đầu những fanpage c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ Việt Nam được y&ecirc;u th&iacute;ch nhất tr&ecirc;n Facebook với hơn 60.000 lượt th&iacute;ch trang.</span></p>\r\n<p><img src=\"../../../uploads/A (1).jpg\" alt=\"\" width=\"940\" height=\"788\" /></p>\r\n<p>&nbsp;</p>', '2018-03-21 09:04:02', '2018-03-21 20:04:02', 'images/news/1520583246_pic-New-i_636343413097169126_HasThumb_Thumb.jpg.png'),
(227, 2, 'Bảo hiểm Vietinbank lựa chọn đối tác quốc tế PwC tư vấn chiến lược về CNTT', 'bao-hiem-vietinbank-lua-chon-doi-tac-quoc-te-pwc-tu-van-chien-luoc-ve-cntt', '<p><span style=\"font-family: Arial;\">V&agrave;o ng&agrave;y 05/07/2017, Tại H&agrave; Nội, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) v&agrave; C&ocirc;ng ty PwC Việt Nam đ&atilde; k&yacute; kết hợp t&aacute;c tư vấn chiến lược C&ocirc;ng nghệ th&ocirc;ng tin. Theo đ&oacute; PwC sẽ triển khai g&oacute;i tư vấn cho VBI bao gồm: r&agrave; so&aacute;t v&agrave; đ&aacute;nh gi&aacute; hiện trạng CNTT tại VBI, x&acirc;y dựng m&ocirc; h&igrave;nh hệ thống CNTT...</span></p>', '<p>Ng&agrave;y 05/07/2017, tại H&agrave; Nội, C&ocirc;ng ty Bảo hiểm VietinBank (VBI) v&agrave; C&ocirc;ng ty PwC Việt Nam (PwC) đ&atilde; k&yacute; kết hợp t&aacute;c tư vấn chiến lược c&ocirc;ng nghệ th&ocirc;ng tin (CNTT). Theo đ&oacute; PwC sẽ triển khai g&oacute;i tư vấn cho VBI bao gồm: r&agrave; so&aacute;t v&agrave; đ&aacute;nh gi&aacute; hiện trạng CNTT tại VBI, x&acirc;y dựng m&ocirc; h&igrave;nh hệ thống CNTT tương lai; x&acirc;y dựng kế hoạch v&agrave; lộ tr&igrave;nh ph&aacute;t triển CNTT cho 5 năm tới; đ&aacute;nh gi&aacute; mức độ trưởng th&agrave;nh an to&agrave;n, bảo mật CNTT tại VBI. Th&ocirc;ng qua dự &aacute;n n&agrave;y, PwC sẽ hỗ trợ VBI x&acirc;y dựng một kế hoạch ph&aacute;t triển CNTT ph&ugrave; hợp, khả thi nhằm đ&aacute;p ứng nhu cầu hoạt động, chiến lược v&agrave; kế hoạch ph&aacute;t triển của VBI trong tương lai.</p>\r\n<p>&Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI chia sẻ: <em>&ldquo;VBI x&aacute;c định trở th&agrave;nh doanh nghiệp bảo hiểm ti&ecirc;n phong trong lĩnh vực CNTT ứng dụng v&agrave;o c&ocirc;ng t&aacute;c quản l&yacute; hoạt động v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng, nhằm n&acirc;ng cao vị thế cạnh tranh trong thị trường bảo hiểm hiện nay. Việc ứng dụng CNTT th&ocirc;ng qua c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến, đổi mới v&agrave; s&aacute;ng tạo kh&ocirc;ng ngừng sẽ gi&uacute;p n&acirc;ng cao chuẩn mực mới trong c&ocirc;ng t&aacute;c vận h&agrave;nh v&agrave; quản l&yacute; bảo hiểm, x&oacute;a nh&ograve;a khoảng c&aacute;ch giữa kh&aacute;ch h&agrave;ng v&agrave; doanh nghiệp để mang lại những dịch vụ nhanh ch&oacute;ng v&agrave; thuận tiện nhất. &rdquo;</em></p>\r\n<p>VBI hiện nay đang l&agrave; một trong số &iacute;t doanh nghiệp tr&ecirc;n thị trường ph&aacute;t triển giấy chứng nhận điện tử v&agrave; h&oacute;a đơn điện tử cho kh&aacute;ch h&agrave;ng. Khi kh&aacute;ch h&agrave;ng tham gia bảo hiểm, thay v&igrave; chờ ph&aacute;t h&agrave;nh hợp đồng v&agrave; h&oacute;a đơn in giấy v&agrave; thời gian chuyển ph&aacute;t, kh&aacute;ch h&agrave;ng sẽ nhận ngay hợp đồng v&agrave; h&oacute;a đơn dưới dạng điện tử qua h&ograve;m mail c&aacute; nh&acirc;n trong v&ograve;ng 30 ph&uacute;t.</p>\r\n<p>Thực tế, trong 3 năm vừa qua, VBI đ&atilde; v&agrave; đang c&oacute; sự chuyển đổi mạnh mẽ. VBI đ&atilde; nỗ lực thực thi c&aacute;c chủ đề chiến lược quan trọng với kỳ vọng tạo ra bước đột ph&aacute;, đưa VBI tiến gần tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam. Trong qu&aacute; tr&igrave;nh cổ phần h&oacute;a v&agrave; t&igrave;m kiếm đối t&aacute;c đầu tư chiến lược, VBI đ&atilde; hoạch định một c&aacute;ch b&agrave;i bản chiến lược ph&aacute;t triển giai đoạn 2017-2020. Đ&oacute; l&agrave; đẩy mạnh ph&aacute;t triển v&agrave;o thị trường b&aacute;n lẻ, doanh nghiệp vừa v&agrave; nhỏ tr&ecirc;n nền tảng CNTT hiện đại; hướng đến tiếp cận với c&aacute;c chuẩn mực quốc tế.</p>\r\n<p><img src=\"../../../uploads/IMG_9620(1).jpg\" alt=\"\" width=\"1366\" height=\"810\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: VBI v&agrave; PwC chụp h&igrave;nh lưu niệm sau khi k&yacute; kết)</em></p>\r\n<p>B&agrave; Đinh Thị Quỳnh V&acirc;n, Tổng Gi&aacute;m đốc PwC Việt Nam cho biết: <em>\"Ch&uacute;ng t&ocirc;i đ&aacute;nh gi&aacute; cao sự chủ động của VBI trong việc đầu tư v&agrave;o hệ thống CNTT v&agrave; hệ thống bảo mật trong hoạt động quản trị v&agrave; phục vụ kh&aacute;ch h&agrave;ng. Điều n&agrave;y c&oacute; &yacute; nghĩa đặc biệt trong bối cảnh c&aacute;c c&ocirc;ng ty Fintech đang nổi l&ecirc;n chiếm thị phần của ng&agrave;nh t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng, bảo hiểm; đồng thời việc giữ an to&agrave;n hệ thống đang trở n&ecirc;n một th&aacute;ch thức lớn, buộc c&aacute;c tổ chức phải thay đổi tư duy từ ứng ph&oacute; sang chủ động bảo vệ. Với sự hiểu biết chuy&ecirc;n s&acirc;u về c&ocirc;ng nghệ trong lĩnh vực t&agrave;i ch&iacute;nh - bảo hiểm, v&agrave; kinh nghiệm từ c&aacute;c dự &aacute;n tương tự, ch&uacute;ng t&ocirc;i cam kết mang đến cho VBI dịch vụ tư vấn to&agrave;n diện từ hoạch định chiến lược, lập kế hoạch cho tới triển khai giải ph&aacute;p, nhằm mang lại c&aacute;c gi&aacute; trị bền vững cho kh&aacute;ch h&agrave;ng.&rdquo;</em></p>\r\n<p>Việc chuẩn h&oacute;a c&aacute;c c&ocirc;ng t&aacute;c quản l&yacute; nghiệp vụ bảo hiểm, dịch vụ kh&aacute;ch h&agrave;ng tr&ecirc;n nền tảng CNTT kh&ocirc;ng những gi&uacute;p tối ưu h&oacute;a năng suất lao động, giảm thiểu rủi ro trong quy tr&igrave;nh hoạt động nội bộ, m&agrave; c&ograve;n gia tăng những tiện &iacute;ch cho kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm. Hơn thế nữa, g&oacute;p phần n&acirc;ng cao chuẩn mực quy tr&igrave;nh hoạt động cho c&aacute;c doanh nghiệp bảo hiểm tr&ecirc;n thị trường, x&acirc;y dựng m&ocirc;i trường bảo hiểm minh bạch h&oacute;a th&ocirc;ng tin, cạnh tranh l&agrave;nh mạnh v&agrave; ph&aacute;t triển bền vững.</p>\r\n<p>------------------Th&ocirc;ng tin th&ecirc;m------------------</p>\r\n<p><em><strong>C&ocirc;ng ty Bảo hiểm VietinBank (VBI):</strong> L&agrave; th&agrave;nh vi&ecirc;n của Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương (VietinBank), VBI đ&atilde; trải qua 9 năm x&acirc;y dựng v&agrave; ph&aacute;t triển với những bước tăng trưởng rực rỡ. VBI được xếp hạng Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n tr&ecirc;n thị trường v&agrave; l&agrave; c&ocirc;ng ty bảo hiểm c&oacute; tốc độ tăng trưởng nhanh nhất Việt Nam giai đoạn 2013-2016. Hiện nay, với chiến lược định vị l&agrave; \"người đi ti&ecirc;n phong về c&ocirc;ng nghệ\", VBI lu&ocirc;n mang tới cho kh&aacute;ch h&agrave;ng những trải nghiệm dịch vụ bảo hiểm mới v&agrave; hiện đại nhất tr&ecirc;n thị trường, mang tới nhiều tiện &iacute;ch cho kh&aacute;ch h&agrave;ng.</em><br /><em>Bắt đầu từ những chi nh&aacute;nh đầu ti&ecirc;n, đến nay, VBI đ&atilde; mở rộng mạng lưới l&ecirc;n 16 Chi nh&aacute;nh, 65 ph&ograve;ng kinh doanh khu vực, 4.000 đại l&yacute; v&agrave; hơn 10.000 điểm b&aacute;n bảo hiểm tại c&aacute;c ng&acirc;n h&agrave;ng hợp t&aacute;c với VBI tr&ecirc;n to&agrave;n quốc.</em><br /><a href=\"http://vbi.vietinbank.vn/\"><em>http://vbi.vietinbank.vn/</em></a></p>\r\n<p><em>PwC Việt Nam l&agrave; th&agrave;nh vi&ecirc;n của mạng lưới c&aacute;c c&ocirc;ng ty PwC hoạt động tr&ecirc;n 157 quốc gia với hơn 223.000 nh&acirc;n vi&ecirc;n, chuy&ecirc;n cung cấp c&aacute;c dịch vụ chuy&ecirc;n nghiệp h&agrave;ng đầu trong lĩnh vực kiểm to&aacute;n v&agrave; bảo đảm, tư vấn thuế v&agrave; ph&aacute;p l&yacute;, tư vấn quản trị doanh nghiệp v&agrave; tư vấn c&aacute;c thương vụ. PwC Việt Nam đ&atilde; c&oacute; 23 năm kinh nghiệm tại thị trường Việt Nam, đ&atilde; v&agrave; đang cung cấp dịch vụ cho h&agrave;ng ngh&igrave;n tổ chức, doanh nghiệp hoạt động tại Việt Nam, trong đ&oacute; c&oacute; h&agrave;ng trăm kh&aacute;ch h&agrave;ng trong ng&agrave;nh dịch vụ t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng, v&agrave; bảo hiểm. Trong lĩnh vực CNTT, đội ngũ chuy&ecirc;n gia d&agrave;y dặn kinh nghiệm của PwC đ&atilde; tư vấn cho c&aacute;c kh&aacute;ch h&agrave;ng những chiến lược v&agrave; giải ph&aacute;p thiết thực, gi&uacute;p doanh nghiệp giải quyết c&aacute;c vấn đề li&ecirc;n quan đến CNTT tr&ecirc;n phạm vi to&agrave;n cầu cũng như trong khu vực.</em><br /><em><a href=\"http://www.pwc.com/vn/vn.html\">http://www.pwc.com/vn/vn.html</a></em></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 09:04:29', '2018-03-21 20:04:29', 'images/news/1520583432_IMG_962_636348764236542120_HasThumb_Thumb.jpg.png'),
(228, 2, 'Bảo hiểm VietinBank lọt Top 10 công ty bảo hiểm phi nhân thọ uy tín nhất Việt Nam 2017', 'bao-hiem-vietinbank-lot-top-10-cong-ty-bao-hiem-phi-nhan-tho-uy-tin-nhat-viet-nam-2017', '<p><span style=\"font-family: Arial;\">H&agrave; Nội, ng&agrave;y 18 th&aacute;ng 07 năm 2017 &ndash; Bảo hiểm VietinBank (VBI) vừa được vinh danh trong bảng xếp hạng Top 10 doanh nghiệp bảo hiểm phi nh&acirc;n thọ uy t&iacute;n nhất Việt Nam 2017 do CTCP B&aacute;o c&aacute;o Đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report) phối hợp với B&aacute;o điện tử VietnamNet b&igrave;nh chọn nhằm t&ocirc;n vinh ...&nbsp;</span></p>', '<p><strong>H&agrave; Nội, ng&agrave;y 18 th&aacute;ng 07 năm 2017 &ndash; Bảo hiểm VietinBank (VBI) vừa được vinh danh trong bảng xếp hạng Top 10 doanh nghiệp bảo hiểm phi nh&acirc;n thọ uy t&iacute;n nhất Việt Nam 2017 do CTCP B&aacute;o c&aacute;o Đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report) phối hợp với B&aacute;o điện tử VietnamNet b&igrave;nh chọn nhằm t&ocirc;n vinh c&aacute;c doanh nghiệp trong khối ng&agrave;nh bảo hiểm.</strong></p>\r\n<p>Bảng xếp hạng n&agrave;y l&agrave; kết quả nghi&ecirc;n cứu độc lập của CTCP B&aacute;o c&aacute;o Đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report) phối hợp với B&aacute;o điện tử VietnamNet c&ocirc;ng bố, được x&acirc;y dựng dựa tr&ecirc;n c&aacute;c nguy&ecirc;n tắc khoa học, kh&aacute;ch quan v&agrave; sự tổng hợp c&aacute;c đ&aacute;nh gi&aacute; về uy t&iacute;n của c&aacute;c c&ocirc;ng ty bảo hiểm từ c&aacute;c phương tiện truyền th&ocirc;ng, từ g&oacute;c nh&igrave;n của kh&aacute;ch h&agrave;ng cũng như năng lực t&agrave;i ch&iacute;nh.</p>\r\n<p>VBI lọt top 10 doanh nghiệp bảo hiểm phi nh&acirc;n thọ uy t&iacute;n nhất Việt Nam 2017 l&agrave; cột mốc đầy &yacute; nghĩa đ&aacute;nh dấu th&ecirc;m một bước tiến bước chuyển dịch mạnh mẽ với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững.</p>\r\n<p>Vượt tr&ecirc;n bối cảnh kh&oacute; khăn chung của nền kinh tế trong nước v&agrave; thế giới, VBI đ&atilde; đạt được những con số ấn tượng về tăng trưởng doanh thu trong năm 2016. Doanh thu c&ocirc;ng ty đạt l&ecirc;n đến 781 tỷ đồng, tăng 42,25% so với năm 2015. Doanh thu nhận t&aacute;i trong năm 2016 của VBI đạt 89 tỷ, hơn 43,55% so với năm 2015, đạt v&agrave; vượt qua c&aacute;c chỉ ti&ecirc;u về kinh doanh đặt ra.</p>\r\n<p>X&eacute;t tr&ecirc;n 3 ti&ecirc;u ch&iacute; lớn nhất: tiềm lực t&agrave;i ch&iacute;nh, chỉ số truyền th&ocirc;ng v&agrave; chỉ số h&agrave;i l&ograve;ng của kh&aacute;ch h&agrave;ng, VBI đ&atilde; c&oacute; sự đột ph&aacute; về thứ hạng khi vượt l&ecirc;n vị tr&iacute; thứ 8 so với vị tr&iacute; thứ 10 năm 2016, thể hiện tầm v&oacute;c vượt trội về tiềm lực t&agrave;i ch&iacute;nh của VBI cũng như chất lượng dịch vụ kh&aacute;ch h&agrave;ng. Th&ecirc;m v&agrave;o đ&oacute; c&ugrave;ng với độ phủ s&oacute;ng tr&ecirc;n c&aacute;c phương tiện truyền th&ocirc;ng khẳng đinh uy t&iacute;n của VBI, fanpage của VBI vượt l&ecirc;n dẫn đầu những fanpage c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ Việt Nam được y&ecirc;u th&iacute;ch nhất tr&ecirc;n Facebook v&agrave; nhận được phản hồi t&iacute;ch cực từ ph&iacute;a khảo s&aacute;t của kh&aacute;ch h&agrave;ng về độ h&agrave;i l&ograve;ng.</p>\r\n<p><img src=\"../../../uploads/VBI cong ty bao hiem tang truong nhanh nhat VN.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng Nguyễn Hồng Phong - Ph&oacute; Tổng Gi&aacute;m đốc VBI nhận giải thưởng \"Top 10 C&ocirc;ng ty Bảo hiểm Uy t&iacute;n nhất Việt Nam 2017\")</em></p>\r\n<p>Để l&agrave;m được điều đ&oacute;, VBI đ&atilde; phải vượt qua nhiều đối thủ kh&aacute;c bằng sự ghi nhận lớn của qu&yacute; kh&aacute;ch h&agrave;ng đối với sự cố gắng, kh&ocirc;ng ngừng cải tiến vượt bậc về chất lượng dịch vụ. VBI trong năm qua khi ph&aacute;t h&agrave;nh giấy chứng nhận v&agrave; h&oacute;a đơn điện tử gi&uacute;p kh&aacute;ch h&agrave;ng tiết kiệm thời gian, tiện lợi tra cứu với h&igrave;nh thức hiện đại. Tổng đ&agrave;i contact center giải quyết c&aacute;c vấn đề của kh&aacute;ch h&agrave;ng một c&aacute;ch nhanh ch&oacute;ng, mạng lưới chi nh&aacute;nh rộng khắp nhằm hỗ trợ kh&aacute;ch h&agrave;ng một c&aacute;ch thuận tiện, mọi l&uacute;c mọi nơi.</p>\r\n<p>&Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc C&ocirc;ng ty Bảo hiểm Vietinbank cho biết: <em>&ldquo;Những nỗ lực cải tiến v&agrave; tối ưu ho&aacute; hệ thống quản trị đều nhằm gia tăng sự h&agrave;i l&ograve;ng của kh&aacute;ch h&agrave;ng. Mỗi chiến lược mới VBI đưa ra đều đặt c&acirc;u hỏi liệu kh&aacute;ch h&agrave;ng sẽ nhận th&ecirc;m được những gi&aacute; trị g&igrave;? Về l&acirc;u d&agrave;i chiến lược n&agrave;y sẽ đ&oacute;ng g&oacute;p như thế n&agrave;o để n&acirc;ng cao chất lượng ng&agrave;nh bảo hiểm n&oacute;i ri&ecirc;ng v&agrave; hỗ trợ x&acirc;y dựng cộng đồng n&oacute;i chung. Mang lại những điều tốt đẹp cho x&atilde; hội v&agrave; cộng đồng l&agrave; kim chỉ nam của VBI tr&ecirc;n con đường trở th&agrave;nh doanh nghiệp bảo hiểm c&oacute; dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng tốt nhất Việt Nam.&rdquo;</em></p>\r\n<p>Giải thưởng Top 10 C&ocirc;ng ty uy t&iacute;n nhất Việt nam đồng thời l&agrave; nguồn động lực kh&iacute;ch lệ để CBNV VBI tiếp tục nỗ lực kh&ocirc;ng ngừng nhằm mang lại những dịch vụ với chất lượng tốt nhất, đ&aacute;p ứng những y&ecirc;u cầu ng&agrave;y c&agrave;ng cao của qu&yacute; kh&aacute;ch h&agrave;ng.</p>\r\n<p>Đ&acirc;y l&agrave; năm thứ 2 li&ecirc;n tiếp VBI nằm trong Top 10 c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ uy t&iacute;n nhất việt nam theo đ&aacute;nh gi&aacute; của VNR. VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 09:05:29', '2018-03-21 20:05:29', 'images/news/1520583534_AATop1020_636360735588562898_HasThumb_Thumb.jpg.png'),
(229, 2, 'Quyết định bổ nhiệm Giám đốc Ban Tài chính ké toán', 'quyet-dinh-bo-nhiem-giam-doc-ban-tai-chinh-ke-toan', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 31/7/2017, Tại Hội sở ch&iacute;nh C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; diễn ra Lễ c&ocirc;ng bố Quyết định Bổ nhiệm Gi&aacute;m đốc Ban T&agrave;i ch&iacute;nh Kế to&aacute;n cho b&agrave; Lưu Thị Linh Nh&acirc;m, c&ugrave;ng với sự c&oacute; mặt của B&agrave; Nguyễn Hồng V&acirc;n, Chủ tịch C&ocirc;ng ty; B&agrave; Phạm Thị Ch&acirc;u Trưởng ph&ograve;ng Chế độ kế to&aacute;n Ng&acirc;n h&agrave;n...&nbsp;</span></p>', '<p>Ng&agrave;y<span style=\"font-family: Arial;\"> 31/7/2017, Tại Hội sở ch&iacute;nh C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; diễn ra Lễ c&ocirc;ng bố Quyết định Bổ nhiệm Gi&aacute;m đốc Ban T&agrave;i ch&iacute;nh Kế to&aacute;n cho b&agrave; Lưu Thị Linh Nh&acirc;m, c&ugrave;ng với sự c&oacute; mặt của B&agrave; Nguyễn Hồng V&acirc;n, Chủ tịch C&ocirc;ng ty; B&agrave; Phạm Thị Ch&acirc;u Trưởng ph&ograve;ng Chế độ kế to&aacute;n Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam; &Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m đốc, &Ocirc;ng Nguyễn Hồng Phong, Ph&oacute; Tổng Gi&aacute;m đốc v&agrave; B&agrave; Trần Thị Hương Thủy, Ph&oacute; Tổng Gi&aacute;m đốc C&ocirc;ng ty Bảo hiểm VietinBank.<br /> <br />B&agrave; Lưu Thị Linh Nh&acirc;m đ&atilde; trải qua qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c tại BIDV, ERNST &amp; YOUNG Việt Nam v&agrave; được học tập, đ&agrave;o luyện tại Ng&acirc;n h&agrave;ng VietinBank với những kinh nghiệm được đ&uacute;c kết trong c&aacute;c dự &aacute;n lớn của VietinBank. Gần đ&acirc;y nhất, b&agrave; Lưu Thị Linh Nh&acirc;m trực tiếp tham gia dự &aacute;n OGL v&agrave; module GL &ndash; dự &aacute;n thay thế core banking của VietinBank.<br /> <br />Với những kinh nghiệm thực tế c&ugrave;ng tr&igrave;nh độ học vấn s&acirc;u rộng trong l&atilde;nh vực t&agrave;i ch&iacute;nh &ndash; kế to&aacute;n, b&agrave; Linh Nh&acirc;m ch&iacute;nh thức được bổ nhiệm vị tr&iacute; Gi&aacute;m đốc Ban T&agrave;i ch&iacute;nh Kế to&aacute;n kể từ ng&agrave;y 2/8/2017.</span></p>\r\n<p><img src=\"../../../uploads/IMG_9917.jpg\" alt=\"\" width=\"1366\" height=\"740\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: T&acirc;n Gi&aacute;m đốc Ban T&agrave;i ch&iacute;nh Kế to&aacute;n - Lưu Thị Linh Nh&acirc;m chụp ảnh lưu niệm c&ugrave;ng Ban L&atilde;nh đạo C&ocirc;ng ty)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong Lễ Trao Quyết định bổ nhiệm, B&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch C&ocirc;ng ty nhận định: &ldquo;T&acirc;n Gi&aacute;m đốc Linh Nh&acirc;m, khi c&ograve;n đang l&agrave;m việc tại VietinBank đ&atilde; lu&ocirc;n nhiệt huyết với c&ocirc;ng việc, đ&oacute;ng g&oacute;p cho tập thể lớn. Trải qua qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c tại VietinBank, ngo&agrave;i tr&igrave;nh độ cao, Linh Nh&acirc;m c&ograve;n c&oacute; nhiều kinh nghiệm thực tế trong lĩnh vực kiểm to&aacute;n, t&agrave;i ch&iacute;nh &ndash; kế to&aacute;n qua việc tham gia c&aacute;c dự &aacute;n trọng điểm của VietinBank - điển h&igrave;nh như chuyển đổi phần mềm l&otilde;i. Với cương vị mới, T&acirc;n Gi&aacute;m đốc Ban T&agrave;i ch&iacute;nh Kế to&aacute;n sẽ tham mưu chiến lược đầu tư v&agrave; ph&acirc;n t&iacute;ch t&agrave;i ch&iacute;nh cho c&aacute;c hoạt động của C&ocirc;ng ty trong giai đoạn ph&aacute;t triển n&oacute;ng hiện nay.&rdquo;<br /> <br />Về ph&iacute;a Ban L&atilde;nh đạo VBI, &Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc C&ocirc;ng ty, ph&aacute;t biểu: &ldquo;Bảo hiểm VietinBank rất vui mừng v&agrave; đ&oacute;n ch&agrave;o một nh&acirc;n sự t&agrave;i năng về đảm nhận vị tr&iacute; quan trọng n&agrave;y, đồng thời cũng đề nghị b&agrave; Lưu Thị Linh Nh&acirc;m tiếp tục nỗ lực, học tập n&acirc;ng cao tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cũng như năng lực quản l&yacute; để ho&agrave;n th&agrave;nh tốt nhiệm vụ tr&ecirc;n cương vị c&ocirc;ng t&aacute;c mới.&rdquo;<br /> <br />Bảo hiểm VietinBank l&agrave; C&ocirc;ng ty Bảo hiểm Phi nh&acirc;n thọ được y&ecirc;u th&iacute;ch nhất tr&ecirc;n Facebook Việt Nam v&agrave; nằm trong TOP 10 C&ocirc;ng ty Bảo hiểm Uy t&iacute;n Việt Nam 2017 do VietnamReport b&igrave;nh chọn qua nhiều ti&ecirc;u ch&iacute;. Với sự thay đổi nh&acirc;n sự quan trọng n&agrave;y, VBI sẽ tiếp tục đẩy mạnh kinh doanh v&agrave; ph&aacute;t triển vượt bậc trong những năm tiếp theo.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 09:06:25', '2018-03-21 20:06:25', 'images/news/1520583654_IMG_991_636372916114138770_HasThumb_Thumb.JPG.png'),
(230, 2, 'Bảo hiểm VietinBank thông báo quy trình bảo lãnh Bệnh viện Nhi Trung Ương', 'bao-hiem-vietinbank-thong-bao-quy-trinh-bao-lanh-benh-vien-nhi-trung-uong', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank th&ocirc;ng b&aacute;o quy tr&igrave;nh bảo l&atilde;nh Bệnh viện Nhi Trung Ương tại khoa Quốc tế - Khoa điều trị tự nguyện S, kh&aacute;ch h&agrave;ng cần nộp đầy đủ hồ sơ của người được bảo hiểm ngay khi đăng k&yacute; kh&aacute;m chữa bệnh ngoại tr&uacute; trong v&ograve;ng 24 giờ đầu sau khi nhập viện điều trị nội tr&uacute;. Nếu...&nbsp;</span></p>', '<p><img src=\"../../../uploads/nhi-trung-uong-khoa-quoc-te.jpg\" alt=\"\" width=\"869\" height=\"1200\" /></p>\r\n<p>&nbsp;</p>', '2018-03-21 09:07:12', '2018-03-21 20:07:12', 'images/news/1520583726_Nhi-Trung_636373476868317638_HasThumb_Thumb.jpg.png'),
(231, 2, 'Giải pháp tăng sự trải nghiệm của khách hàng tham gia bảo hiểm', 'giai-phap-tang-su-trai-nghiem-cua-khach-hang-tham-gia-bao-hiem', '<p><span style=\"font-family: Arial;\">Cấp giấy chứng nhận bảo hiểm, h&oacute;a đơn điện tử l&agrave; một giải ph&aacute;p về c&ocirc;ng nghệ th&ocirc;ng tin mang t&iacute;nh đột ph&aacute; của C&ocirc;ng ty Bảo hiểm VietinBank (VBI). C&ocirc;ng nghệ n&agrave;y gi&uacute;p kh&aacute;ch h&agrave;ng nhận Giấy chứng nhận, h&oacute;a đơn ngay lập tức, đồng thời kiểm tra c&aacute;c quyền lợi nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c.&nbsp;</span></p>', '<p><strong>Cấp giấy chứng nhận bảo hiểm, h&oacute;a đơn điện tử l&agrave; một giải ph&aacute;p về c&ocirc;ng nghệ th&ocirc;ng tin mang t&iacute;nh đột ph&aacute; của C&ocirc;ng ty Bảo hiểm VietinBank (VBI). C&ocirc;ng nghệ n&agrave;y gi&uacute;p kh&aacute;ch h&agrave;ng nhận Giấy chứng nhận, h&oacute;a đơn ngay lập tức, đồng thời kiểm tra c&aacute;c quyền lợi nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c.</strong></p>\r\n<p><strong>Sự cam kết cố hữu của sản phẩm v&ocirc; h&igrave;nh</strong></p>\r\n<p>Thời đại của c&ocirc;ng nghệ 4.0 một lần nữa k&iacute;ch th&iacute;ch sự ph&aacute;t triển của c&aacute;c c&ocirc;ng nghệ kỹ thuật số tr&ecirc;n mọi lĩnh vực từ sản phẩm đến dịch vụ giải tr&iacute; v&agrave; dịch vụ t&agrave;i ch&iacute;nh. C&aacute;c doanh nghiệp kh&ocirc;ng chỉ nỗ lực n&acirc;ng cao chất lượng sản phẩm m&agrave; c&ograve;n ph&aacute;t triển ứng dụng c&ocirc;ng nghệ kỹ thuật số cho mọi hoạt động dịch vụ kh&aacute;ch h&agrave;ng.</p>\r\n<p>Tuy nhi&ecirc;n, một số c&aacute;c doanh nghiệp trong lĩnh vực bảo hiểm gặp kh&oacute; khăn trong việc &aacute;p dụng c&aacute;c c&ocirc;ng nghệ mới trong hoạt động quản l&yacute; bởi đặc th&ugrave; bảo hiểm l&agrave; một loại sản phẩm v&ocirc; h&igrave;nh, sự cam kết giữa doanh nghiệp v&agrave; kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm nằm ở Hợp đồng bảo hiểm hay c&ograve;n gọi l&agrave; Giấy chứng nhận bảo hiểm v&agrave; h&oacute;a đơn thanh to&aacute;n. V&igrave; vậy, để tạo sự an t&acirc;m cho kh&aacute;ch h&agrave;ng, từ bao l&acirc;u nay, c&aacute;c doanh nghiệp thường ph&aacute;t h&agrave;nh giấy chứng nhận bảo hiểm v&agrave; h&oacute;a đơn dưới dạng &ldquo;bản cứng&rdquo;. Mặc d&ugrave; điều n&agrave;y tạo được sự tin tưởng đối kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm, tuy nhi&ecirc;n lại dẫn đến sự phức tạp trong kh&acirc;u lưu trữ chứng từ từ ph&iacute;a doanh nghiệp doanh nghiệp; tạo ra những thủ tục h&agrave;nh ch&iacute;nh rườm ra khi kh&aacute;ch h&agrave;ng phải xuất tr&igrave;nh giấy tờ tại c&aacute;c cơ sở y tế hoặc c&aacute;c cơ sở bảo l&atilde;nh.<br /> <br /><strong>Giải ph&aacute;p th&ocirc;ng minh v&agrave; mang t&iacute;nh thời đại</strong></p>\r\n<p>Thấu hiểu được nhu cầu của kh&aacute;ch h&agrave;ng trong qu&aacute; tr&igrave;nh trước v&agrave; sau khi tham gia bảo hiểm, đồng thời thực hiện chỉ đạo của Ban L&atilde;nh đạo VietinBank trong việc nghi&ecirc;n cứu &aacute;p dụng c&ocirc;ng nghệ hiện đại v&agrave;o n&acirc;ng cao năng suất lao động, VBI đ&atilde; nghi&ecirc;n cứu, ph&aacute;t triển c&ocirc;ng nghệ &ldquo;Cấp giấy chứng nhận, h&oacute;a đơn điện tử&rdquo;. Việc ph&aacute;t h&agrave;nh Giấy chứng nhận v&agrave; h&oacute;a đơn dưới h&igrave;nh thức điện tử, gửi đến kh&aacute;ch h&agrave;ng bằng h&ograve;m thư c&aacute; nh&acirc;n tr&ecirc;n m&ocirc;i trường internet kh&ocirc;ng hề l&agrave;m giảm t&iacute;nh ch&iacute;nh thống v&agrave; hợp ph&aacute;p của &ldquo;Hợp đồng giữa b&ecirc;n cấp bảo hiểm v&agrave; b&ecirc;n mua bảo hiểm&rdquo;, m&agrave; c&ograve;n tạo sự nhanh ch&oacute;ng, tiện lợi cho c&aacute;c kh&aacute;ch h&agrave;ng trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p>C&ocirc;ng nghệ n&agrave;y được c&aacute;c nh&agrave; ph&acirc;n t&iacute;ch t&agrave;i ch&iacute;nh đ&aacute;nh gi&aacute; l&agrave; một bước đi đột ph&aacute; mới trong việc khai mở ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin trong quản l&yacute; bảo hiểm, tạo những gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng trong lĩnh vực bảo hiểm t&agrave;i ch&iacute;nh vốn được coi cần nhiều y&ecirc;u cầu trong thủ tục giấy tờ h&agrave;nh ch&iacute;nh.</p>\r\n<p>Đ&acirc;y l&agrave; c&ocirc;ng nghệ đầu ti&ecirc;n tr&ecirc;n thị trường bảo hiểm tại Việt Nam do ch&iacute;nh c&aacute;n bộ VBI nghi&ecirc;n cứu v&agrave; x&acirc;y dựng. Giấy chứng nhận bảo hiểm, h&oacute;a đơn điện tử được VBI c&ocirc;ng nhận c&oacute; gi&aacute; trị như giấy chứng nhận bảo hiểm v&agrave; h&oacute;a đơn th&ocirc;ng thường (bản cứng). Qua gần một năm hoạt động, c&ocirc;ng nghệ n&agrave;y kh&ocirc;ng ngừng ho&agrave;n thiện v&agrave; mang lại nhiều gi&aacute; trị to lớn cho doanh nghiệp.<br /> <br /><strong>Ưu điểm vượt trội</strong></p>\r\n<p>C&ocirc;ng nghệ cấp giấy chứng nhận bảo hiểm điện tử v&agrave; h&oacute;a đơn điện tử gi&uacute;p cho c&aacute;c kh&aacute;ch h&agrave;ng được bảo hiểm c&oacute; thể dễ d&agrave;ng tiếp cận với c&aacute;c quyền lợi bảo hiểm một c&aacute;ch nhanh ch&oacute;ng v&agrave; thuận tiện. Đi đ&ocirc;i với n&oacute; l&agrave; vấn đề an to&agrave;n v&agrave; bảo mật th&ocirc;ng tin của kh&aacute;ch h&agrave;ng lu&ocirc;n được VBI ch&uacute; trọng h&agrave;ng đầu. C&ocirc;ng nghệ đưa v&agrave;o sử dụng gi&uacute;p giảm thiểu mọi t&aacute;c động xấu, tr&aacute;nh t&igrave;nh trạng l&agrave;m giả h&oacute;a đơn, ấn chỉ&hellip; B&ecirc;n cạnh đ&oacute;, việc sử dụng Giấy chứng nhận bảo hiểm, h&oacute;a đơn điện tử c&ograve;n c&oacute; t&iacute;nh minh bạch, tiết kiệm&hellip; mang lại rất nhiều lợi &iacute;ch cho VBI.</p>\r\n<p><img src=\"../../../uploads/IMG_3034.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p style=\"text-align: center;\"><em>(ảnh c&aacute;n bộ cấp đơn bảo hiểm th&ocirc;ng qua h&igrave;nh thức Giấy chứng nhận điện tử)</em></p>\r\n<p><strong>Anh Nguyễn Th&agrave;nh C&ocirc;ng - Trưởng ph&ograve;ng Ph&aacute;t triển ứng dụng VBI cho biết:</strong> <em>&ldquo;Sau 1 năm kể từ ng&agrave;y ra mắt, c&ocirc;ng nghệ &ldquo;Giấy chứng nhận điện tử, h&oacute;a đơn điện tử&rdquo; đ&atilde; mang lại rất nhiều lợi &iacute;ch cho cả C&ocirc;ng ty v&agrave; kh&aacute;ch h&agrave;ng. Đối với VBI, nhờ c&oacute; Giấy chứng nhận điện tử đ&atilde; giảm thiểu được thời gian in ấn, ph&aacute;t h&agrave;nh v&agrave; lưu trữ, từ đ&oacute; n&acirc;ng cao hơn năng suất lao động&rdquo;.</em></p>\r\n<p><strong>C&ograve;n với chị Nguyễn Thị Mai Hương (Long Bi&ecirc;n, H&agrave; Nội) kh&aacute;ch h&agrave;ng sử dụng dịch vụ Bảo hiểm sức khỏe của VBI gần 2 năm nay chia sẻ</strong>: <em>&ldquo;Kể từ khi VBI ra mắt Giấy chứng nhận điện tử, t&ocirc;i thấy rất h&agrave;i l&ograve;ng với dịch vụ n&agrave;y. Bởi v&igrave;, mỗi khi t&ocirc;i đến bệnh viện c&oacute; li&ecirc;n kết với VBI để kh&aacute;m, t&ocirc;i chỉ cần cấp Giấy chứng nhận điện tử c&oacute; sẵn trong điện thoại của m&igrave;nh l&agrave; đ&atilde; c&oacute; thể được bảo l&atilde;nh viện ph&iacute;. Hoặc khi t&ocirc;i bị mất Giấy chứng nhận, t&ocirc;i đ&atilde; li&ecirc;n hệ l&ecirc;n tổng đ&agrave;i v&agrave; chỉ 10 ph&uacute;t sau l&agrave; VBI đ&atilde; gửi lại Giấy chứng nhận điện tử&rdquo;.</em></p>\r\n<p>Từ những lợi &iacute;ch tr&ecirc;n, c&ocirc;ng nghệ cấp &ldquo;Giấy chứng nhận, h&oacute;a đơn điện tử&rdquo; của tập thể c&aacute;n bộ, nh&acirc;n vi&ecirc;n VBI đ&atilde; vinh dự được c&ocirc;ng nhận l&agrave; <strong>C&ocirc;ng tr&igrave;nh Thanh ni&ecirc;n cấp Đo&agrave;n Khối Doanh nghiệp Trung ương</strong>. Đ&acirc;y l&agrave; phần thưởng xứng đ&aacute;ng thể hiện tr&iacute; tuệ, tinh thần của c&aacute;n bộ VBI n&oacute;i ri&ecirc;ng v&agrave; của VietinBank n&oacute;i chung.</p>\r\n<p><img src=\"../../../uploads/IMG_3038-.jpg\" alt=\"\" width=\"748\" height=\"1200\" /></p>\r\n<p><img src=\"../../../uploads/IMG_3041-.jpg\" alt=\"\" width=\"1366\" height=\"1024\" /></p>\r\n<p>Trong thời gian tới, VBI tiếp tục nghi&ecirc;n cứu n&acirc;ng cao chất lượng dịch vụ của c&ocirc;ng nghệ bằng việc tiếp tục cho ra mắt c&aacute;c ứng dụng c&ocirc;ng nghệ ti&ecirc;n tiến như: ra mắt website b&aacute;n bảo hiểm trực tuyến đa phương, tiện dụng cho b&ecirc;n cấp bảo hiểm v&agrave; b&ecirc;n tham gia bảo hiểm; ra mắt App chuy&ecirc;n dụng cho kh&aacute;ch h&agrave;ng truy vấn th&ocirc;ng tin về bảo hiểm, tương t&aacute;c tức th&igrave; với C&ocirc;ng ty bảo hiểm khi c&oacute; sự kiện bảo hiểm xảy ra. To&agrave;n bộ c&aacute;c c&ocirc;ng nghệ phần mềm của VBI đều được sử dụng tr&ecirc;n c&aacute;c hệ điều h&agrave;nh v&agrave; c&aacute;c phương tiện th&ocirc;ng minh như m&aacute;y t&iacute;nh bảng, điện thoại smartphone v&agrave; nhiều giao diện tiện dụng kh&aacute;c.</p>\r\n<p>Vừa qua VBI vừa k&yacute; kết hợp t&aacute;c chiến lược tư vấn về C&ocirc;ng nghệ th&ocirc;ng tin với C&ocirc;ng ty PwC Việt Nam - C&ocirc;ng ty to&agrave;n cầu trong trong lĩnh vực kiểm to&aacute;n v&agrave; tư vấn quản trị doanh nghiệp. Với sự đồng h&agrave;nh của PwC v&agrave; tầm nh&igrave;n chiến lược của Ban L&atilde;nh đạo C&ocirc;ng ty, VBI hướng tới trở th&agrave;nh doanh nghiệp Bảo hiểm ti&ecirc;n phong về C&ocirc;ng nghệ tại Việt Nam.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 10:16:44', '2018-03-21 21:16:44', 'images/news/1520583949_IMG_303_636426237331788312_HasThumb_Thumb.JPG.png'),
(232, 2, 'Bảo hiểm VietinBank tổ chức thành công Đại hội đại biểu công đoàn cơ sở', 'bao-hiem-vietinbank-to-chuc-thanh-cong-dai-hoi-dai-bieu-cong-doan-co-so', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/9/2017, tại nh&agrave; kh&aacute;ch La Th&agrave;nh, H&agrave; Nội, Bảo hiểm VietinBank (VBI) long trọng tổ chức Đại hội Đại biểu C&ocirc;ng đo&agrave;n Cơ sở C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam nhiệm kỳ 2017 &ndash; 2022. Đại hội được vinh dự đ&oacute;n tiếp B&agrave; Nguyễn Thị Y&ecirc;n &ndash; Trưởng Ban T&agrave;i ch&iacute;nh...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 29/9/2017, tại nh&agrave; kh&aacute;ch La Th&agrave;nh, H&agrave; Nội, Bảo hiểm VietinBank (VBI) long trọng tổ chức Đại hội Đại biểu C&ocirc;ng đo&agrave;n Cơ sở C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam nhiệm kỳ 2017 &ndash; 2022.<br /> <br />Đại hội được vinh dự đ&oacute;n tiếp B&agrave; Nguyễn Thị Y&ecirc;n &ndash; Trưởng Ban T&agrave;i ch&iacute;nh C&ocirc;ng Đo&agrave;n Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam tới dự, c&ugrave;ng với Ban L&atilde;nh đạo c&ocirc;ng ty: &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc; &ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc; b&agrave; Trần Thị Hương Thủy &ndash; Ph&oacute; Tổng Gi&aacute;m đốc, v&agrave; 106 Đại biểu đo&agrave;n vi&ecirc;n, người lao động của VBI.</span></p>\r\n<p><img src=\"../../../uploads/3(1).jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: &Ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m Đốc - Chủ tịch C&ocirc;ng Đo&agrave;n VBI đọc b&aacute;o c&aacute;o hoạt động C&ocirc;ng đo&agrave;n nhiệm kỳ 2012 &ndash; 2017)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Trong nhiệm kỳ 2012 &ndash; 2017, C&ocirc;ng đo&agrave;n Bảo hiểm VietinBank c&oacute; nhiều sự ph&aacute;t triển lớn mạnh về đo&agrave;n thể v&agrave; hoạt động. Nổi bật trong đ&oacute; l&agrave; số lượng đo&agrave;n vi&ecirc;n tăng 302 đo&agrave;n vi&ecirc;n so với năm 2012, trong đ&oacute; đo&agrave;n vi&ecirc;n l&agrave; lao động nữ chiếm 47%. Về mặt bằng tr&igrave;nh độ, học vấn cũng tăng 55% tr&igrave;nh độ thạc sỹ, tăng 212% tr&igrave;nh độ đại học.<br /> <br />Theo b&aacute;o c&aacute;o kết quả thực hiện Nghị quyết Đại hội Đại biểu lần thứ II (2012 -2017), C&ocirc;ng t&aacute;c Tuyền truyền gi&aacute;o dục ch&iacute;nh trị được C&ocirc;ng đo&agrave;n Bảo hiểm VietinBank đặt l&ecirc;n h&agrave;ng đầu. Đo&agrave;n vi&ecirc;n được l&atilde;nh hội v&agrave; c&oacute; bản lĩnh ch&iacute;nh trị vững v&agrave;ng. C&aacute;c hoạt động cổ động ho&agrave;n th&agrave;nh nhiệm vụ v&agrave; n&acirc;ng cao nghiệp vụ chuy&ecirc;n m&ocirc;n được đo&agrave;n vi&ecirc;n hưởng ứng, nhiệt t&igrave;nh tham gia ho&agrave;n th&agrave;nh.<br /> <br />C&ugrave;ng với đ&oacute;, được sự quan t&acirc;m của c&aacute;c cấp ủy Đảng, L&atilde;nh đạo chuy&ecirc;n m&ocirc;n. C&ocirc;ng đo&agrave;n Bảo hiểm VietinBank cũng d&agrave;nh thời gian chăm lo đời sống vật chất, tinh thần mỗi đo&agrave;n vi&ecirc;n, người lao động như khen thưởng kịp thời những đo&agrave;n vi&ecirc;n, người lao động, tập thể c&oacute; s&aacute;ng kiến, đ&oacute;ng g&oacute;p ho&agrave;n thiện hơn nữa c&ocirc;ng t&aacute;c nghiệp vụ. Tổ chức kh&aacute;m sức khỏe chất lượng cao định kỳ cho c&aacute;c đo&agrave;n vi&ecirc;n, người lao động. C&ocirc;ng t&aacute;c thăm hỏi, Hiếu, Hỷ cũng được C&ocirc;ng đo&agrave;n chăm lo tốt cho c&aacute;c đo&agrave;n vi&ecirc;n, người lao động trong C&ocirc;ng ty.<br /> <br />Hoạt động Văn h&oacute;a, Nghệ Thuật, Thể thao cũng được ch&uacute; trọng. Những năm qua, C&ocirc;ng đo&agrave;n Bảo hiểm VietinBank cử đo&agrave;n vi&ecirc;n, người lao động tham gia v&agrave;o c&aacute;c phong tr&agrave;o chung của Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam. Ngo&agrave;i ra, c&aacute;c c&ocirc;ng t&aacute;c về Quy chế d&acirc;n chủ, x&acirc;y dựng tổ chức v&agrave; x&acirc;y dựng Đảng, C&ocirc;ng đo&agrave;n cơ sở vững mạnh cũng được C&ocirc;ng đo&agrave;n VietinBank th&uacute;c đẩy mạnh mẽ v&agrave; đạt được hiệu quả cao.</span></p>\r\n<p><img src=\"../../../uploads/2(2).jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: B&agrave; Trần Thị Hương Thủy &ndash; Ph&oacute; Tổng Gi&aacute;m Đốc<br />tr&igrave;nh b&agrave;y Mục ti&ecirc;u, phương hướng hoạt động C&ocirc;ng đo&agrave;n nhiệm kỳ 2017 &ndash; 2022)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Ph&aacute;t biểu tại Đại hội C&ocirc;ng đo&agrave;n Cơ sở C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc C&ocirc;ng ty ghi nhận v&agrave; đ&aacute;nh gi&aacute; cao những th&agrave;nh quả của Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n cơ sở trong nhiệm kỳ vừa qua, đồng thời tin tưởng Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n cơ sở nhiệm kỳ mới với tinh thần đo&agrave;n kết, quyết t&acirc;m, năng động, s&aacute;ng tạo sẽ thực sự l&agrave; cầu nối gắn kết giữa c&aacute;c đo&agrave;n vi&ecirc;n, ph&aacute;t huy tối đa sức mạnh của tập thể, tr&iacute; tuệ của từng người, tất cả v&igrave; mục ti&ecirc;u x&acirc;y dựng C&ocirc;ng ty Bảo hiểm VietinBank ph&aacute;t triển, đo&agrave;n kết, nghĩa t&igrave;nh.</span></p>\r\n<p><img src=\"../../../uploads/1(2).jpg\" alt=\"\" width=\"1366\" height=\"875\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: &Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc chia sẻ trong Đại hội C&ocirc;ng đo&agrave;n Bảo hiểm VietinBank nhiệm kỳ 2017 - 2022)</span></em></p>\r\n<p><img src=\"../../../uploads/4(1).jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: &Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m Đốc v&agrave; B&agrave; Nguyễn Thị Y&ecirc;n &ndash; Trưởng Ban T&agrave;i ch&iacute;nh C&ocirc;ng Đo&agrave;n VietinBank trao hoa ch&uacute;c mừng T&acirc;n Ban chấp h&agrave;nh C&ocirc;ng đo&agrave;n Bảo hiểm VietinBank nhiệm kỳ 2017 - 2022)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Đại hội đ&atilde; th&ocirc;ng qua Nghị quyết với sự nhất tr&iacute; 100% của c&aacute;c đại biểu. Đại hội cũng đ&atilde; bầu Ban Chấp h&agrave;nh C&ocirc;ng đo&agrave;n cơ sở mới nhiệm kỳ 2017 - 2022 gồm 9 người; bầu 2 đại biểu ch&iacute;nh thức v&agrave; 1 đại biểu dự khuyết đi dự Đại hội C&ocirc;ng đo&agrave;n VietinBank lần thứ V.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-21 10:18:27', '2018-03-21 21:18:27', 'images/news/1520584339__636429083453330686_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(233, 2, 'Bảo hiểm VietinBank cập nhật chi trả bồi thường VietinCare', 'bao-hiem-vietinbank-cap-nhat-chi-tra-boi-thuong-vietincare', '<p><span style=\"font-family: Arial;\">&ldquo;Thay thế bạn đối mặt với mọi rủi ro trong cuộc sống&rdquo; Cuộc sống vốn v&ocirc; thường v&agrave; kh&ocirc;ng thể định trước những rủi ro c&oacute; thể ập đến bất ngờ cho bạn v&agrave; người th&acirc;n. Số tiền bảo hiểm sẽ b&ugrave; đắp những sự cố kh&ocirc;ng đ&aacute;ng c&oacute;.&nbsp;</span></p>', '<p><img src=\"../../../uploads/ban-tin-boi-thuong-thang7-2017.jpg\" alt=\"\" width=\"900\" height=\"1087\" /></p>\r\n<p><strong>Bảo hiểm VietinBank chi trả bồi thường VietinCare cho kh&aacute;ch h&agrave;ng L&ecirc; Văn Điền - Th&ocirc;n 2, X&atilde; Iapia, huyện Chư​Pr&ocirc;ng, Gia Lai</strong><br />Th&aacute;ng 8, Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n Phối hợp với Chi nh&aacute;nh ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương đ&atilde; tới thăm hỏi, động vi&ecirc;n v&agrave; tiến h&agrave;nh chi trả 500 triệu đồng tiền bồi thường bảo hiểm cho gia đ&igrave;nh &ocirc;ng L&ecirc; Văn Điền, ở địa chỉ th&ocirc;n 2, X&atilde; Iapia, Huyện ChưPr&ocirc;ng, Gia Lai.</p>\r\n<p>Trước đ&oacute;, &Ocirc;ng L&ecirc; Văn Điền c&oacute; khoản vay trị gi&aacute; 500 triệu đồng v&agrave; c&oacute; tham gia bảo hiểm cho khoản vay VietinCare. Đến ng&agrave;y 01/08/2016 &ocirc;ng Điền kh&ocirc;ng may gặp rủi ro qua đời. Ngay khi nhận được tin b&aacute;o gia đ&igrave;nh, c&aacute;n bộ chi nh&aacute;nh VBI T&acirc;y Nguy&ecirc;n v&agrave; c&aacute;n bộ VietinBank đ&atilde; nhanh ch&oacute;ng tới thăm viếng, đồng thời hướng dẫn gia đ&igrave;nh thu nhập hồ sơ để l&agrave;m thủ tục nhận bảo hiểm. Với mong muốn san sẻ rủi ro v&agrave; g&aacute;nh nặng về t&agrave;i ch&iacute;nh với gia đ&igrave;nh, VBI đ&atilde; cố gắng xử l&yacute; nhanh ch&oacute;ng, kịp thời việc chi trả quyền lợi bảo hiểm cho gia đ&igrave;nh &ocirc;ng L&ecirc; Văn Điền.<br /> <br /><strong>Bảo hiểm VietinBank chi trả bồi thường VietinCare cho kh&aacute;ch h&agrave;ng Ho&agrave;ng Xu&acirc;n Ng&agrave; - huyện Sa Thầy, Tỉnh Kon Tum</strong><br />Bảo hiểm VietinBank Chi nh&aacute;nh T&acirc;y Nguy&ecirc;n vừa thực hiện chi trả 250 triệu đồng tiền bồi thường Bảo hiểm người vay vốn cho Gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Ho&agrave;ng Xu&acirc;n Ng&agrave;, cư tr&uacute; tại Huyện Sa Thầy, Tỉnh Kon Tum.</p>\r\n<p>Năm 2015, &ocirc;ng Ho&agrave;ng Xu&acirc;n Ng&agrave; c&oacute; vay vốn tại Chi nh&aacute;nh Ng&acirc;n h&agrave;ng C&ocirc;ng thương Chi nh&aacute;nh Kon Tum với số tiền 250 triệu đồng v&agrave; tham gia mua Bảo hiểm người vay vốn với mức ph&iacute; l&agrave; 1,2 triệu đồng.</p>\r\n<p>Th&aacute;ng 4/2016, &ocirc;ng Ng&agrave; chẳng may gặp nạn do bị tai nạn v&agrave; tử vong tại Sa Thầy. Ngay sau khi nhận được th&ocirc;ng tin Bảo hiểm VietinBank chi nh&aacute;nh T&acirc;y Nguy&ecirc;n đ&atilde; phối hợp hướng dẫn gia đ&igrave;nh kh&aacute;ch h&agrave;ng ho&agrave;n thiện hồ sơ để được bồi thường 100% số tiền bảo hiểm l&agrave; 250 triệu đồng. Khoản vay của &ocirc;ng Ng&agrave; với Ng&acirc;n h&agrave;ng VietinBank được chi trả to&agrave;n bộ.</p>\r\n<p><strong>Bảo hiểm VietinBank chi trả bồi thường VietinCare cho Kh&aacute;ch h&agrave;ng Nguyễn Văn C&ocirc;ng - X&atilde; B&igrave;nh Giang, Huyện H&ograve;n đất, Ki&ecirc;n Giang</strong><br />Ng&agrave;y 8/3/2017, Bảo hiểm VietinBank chi nh&aacute;nh Cần thơ đ&atilde; tổ chức chi trả 400 triệu đồng tiền bồi thường bảo hiểm người vay vốn cho gia đ&igrave;nh của kh&aacute;ch h&agrave;ng Nguyễn Văn C&ocirc;ng, tr&uacute; tại x&atilde; B&igrave;nh Giang- Huyện H&ograve;n Đất &ndash; tỉnh Ki&ecirc;n Giang.</p>\r\n<p>Năm 2016, &ocirc;ng Nguyễn Văn C&ocirc;ng vay vốn 400 triệu đồng tại VietinBank chi nh&aacute;nh Ki&ecirc;n giang v&agrave; được c&aacute;n bộ ng&acirc;n h&agrave;ng tư vấn tham gia bảo hiểm người vay vốn. Ng&agrave;y 11/01/2017 khi tham gia giao th&ocirc;ng bằng xe kh&aacute;ch, chiếc xe gặp sự cố dẫn đến tai nạn, &ocirc;ng C&ocirc;ng kh&ocirc;ng may đ&atilde; tử vong. Ngay sau khi nhận được th&ocirc;ng tin, Bảo hiểm VietinBank chi nh&aacute;nh Cần thơ đ&atilde; nhanh ch&oacute;ng phối hợp với VietinBank Chi nh&aacute;nh Ki&ecirc;n Giang hướng dẫn gia đ&igrave;nh kh&aacute;ch h&agrave;ng ho&agrave;n thiện hồ sơ để được chi trả bồi thường bảo hiểm theo đ&uacute;ng quy định.</p>\r\n<p style=\"text-align: center;\"><br /><strong>BẢO HIỂM VIETINBANK CAM KẾT ĐỒNG H&Agrave;NH C&Ugrave;NG MỌI KH&Aacute;CH H&Agrave;NG</strong></p>', '2018-03-21 10:19:13', '2018-03-21 21:19:13', 'images/news/1520584442_Ban-tin-B_636440894593153418_HasThumb_Thumb.png'),
(234, 2, 'Bảo hiểm VietinBank (VBI) và ANIVA Việt Nam ký kết hợp tác toàn diện', 'bao-hiem-vietinbank-vbi-va-aniva-viet-nam-ky-ket-hop-tac-toan-dien', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 30/10/2017, tại Th&agrave;nh phố Hồ Ch&iacute; Minh, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam VietinBank (VBI) v&agrave; C&ocirc;ng ty TNHH Bảo hiểm Nh&acirc;n thọ Aviva Việt Nam (Aviva Việt Nam) triển khai k&yacute; kết hợp t&aacute;c to&agrave;n diện, đ&aacute;nh dấu một bước khởi đầu tốt đẹp trong việc ph&aacute;t triển mố...&nbsp;</span></p>', '<p><strong><span style=\"font-family: Arial;\">Ng&agrave;y 30/10/2017, tại Th&agrave;nh phố Hồ Ch&iacute; Minh, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam VietinBank (VBI) v&agrave; C&ocirc;ng ty TNHH Bảo hiểm Nh&acirc;n thọ Aviva Việt Nam (Aviva Việt Nam) triển khai k&yacute; kết hợp t&aacute;c to&agrave;n diện, đ&aacute;nh dấu một bước khởi đầu tốt đẹp trong việc ph&aacute;t triển mối quan hệ song phương giữa hai b&ecirc;n.</span></strong></p>\r\n<p><img src=\"../../../uploads/DSC_0391.jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: K&yacute; kết thỏa thuận hợp t&aacute;c giữa Bảo hiểm VietinBank v&agrave; Bảo hiểm AVIVA)</span></em></p>\r\n<p>Bảo hiểm VietinBank với chiến lược định vị l&agrave; &ldquo;người đi ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo; v&agrave; &ldquo;C&ocirc;ng ty Bảo hiểm dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ&rdquo;, lu&ocirc;n nhận thức được tầm quan trọng của việc kh&ocirc;ng ngừng mở rộng mạng lưới ph&acirc;n phối, n&acirc;ng cao vị thế cạnh tranh. Bảo hiểm VietinBank sẽ ch&iacute;nh thức triển khai kế hoạch cổ phần h&oacute;a, lựa chọn nh&agrave; đầu tư chiến lược nước ngo&agrave;i nhằm n&acirc;ng cao năng lực t&agrave;i ch&iacute;nh, cải tiến m&ocirc; h&igrave;nh tổ chức v&agrave; cập nhật phương thức quản trị hiện đại, đồng thời mở rộng hợp t&aacute;c với c&aacute;c tổ chức t&agrave;i ch&iacute;nh nước ngo&agrave;i để kh&ocirc;ng ngừng cải tiến c&ocirc;ng nghệ quản l&yacute; v&agrave; ph&acirc;n phối, chuy&ecirc;n nghiệp ho&aacute; đội ngũ b&aacute;n h&agrave;ng v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng.</p>\r\n<p>Theo hợp đồng vừa k&yacute;, Aviva Việt Nam sẽ ph&acirc;n phối c&aacute;c sản phẩm bảo hiểm phi nh&acirc;n thọ của VBI tới c&aacute;c kh&aacute;ch h&agrave;ng th&ocirc;ng qua 4.000 đại l&yacute; c&aacute; nh&acirc;n của Aviva Việt Nam. Đồng thời, hai b&ecirc;n cam kết đưa ra c&aacute;c g&oacute;i sản phẩm bảo hiểm hấp dẫn của mỗi b&ecirc;n d&agrave;nh cho c&aacute;n bộ nh&acirc;n vi&ecirc;n của Aviva Việt Nam v&agrave; Bảo hiểm VietinBank. Hơn thế nữa, VBI v&agrave; Aviva sẽ triển khai hợp t&aacute;c b&aacute;n h&agrave;ng tr&ecirc;n nền tảng c&ocirc;ng nghệ b&aacute;n h&agrave;ng ti&ecirc;n tiến nhất hiện nay. Giấy chứng nhận bảo hiểm được cấp trực tuyến th&ocirc;ng qua việc t&iacute;ch hợp hệ thống đồng thời tối ưu nhất c&aacute;c kh&acirc;u cho kh&aacute;ch h&agrave;ng trong đ&oacute; c&oacute; sử dụng c&aacute;c ứng dụng thanh to&aacute;n trực tuyến hiện đại.</p>\r\n<p>Tại lễ k&yacute; kết, &Ocirc;ng Nguyễn Hồng Phong, Ph&oacute; Tổng Gi&aacute;m Đốc Bảo Hiểm VietinBank, đại diện l&atilde;nh đạo, đ&atilde; chia sẻ: \"Sự hợp t&aacute;c n&agrave;y sẽ l&agrave; một h&igrave;nh mẫu tr&ecirc;n thị trường bảo hiểm Việt Nam, l&agrave; cơ hội để hai b&ecirc;n c&ugrave;ng chia sẻ thế mạnh của m&igrave;nh, c&ugrave;ng nhau ph&aacute;t triển trong thị trường bảo hiểm.\"</p>\r\n<p><img src=\"../../../uploads/DSC_0492.jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<p>&Ocirc;ng Randy Lianggara, Tổng Gi&aacute;m đốc Aviva Việt Nam cũng đ&atilde; nhấn mạnh: \"Th&ocirc;ng qua thỏa thuận hợp t&aacute;c chiến lược n&agrave;y, lực lượng đại l&yacute; c&aacute; nh&acirc;n của Aviva, đội ngũ MDRT xuất sắc, sẽ c&oacute; cơ hội được b&aacute;n ch&eacute;o th&ecirc;m sản phẩm chăm s&oacute;c sức khoẻ tốt nhất của C&ocirc;ng ty Bảo hiểm VietinBank nhằm cung cấp g&oacute;i sản phẩm đa dạng hơn cho kh&aacute;ch h&agrave;ng. Đồng thời, nh&oacute;m l&agrave;m việc của hai ph&iacute;a sẽ trao đổi c&ugrave;ng nhau trong việc thiết kế c&aacute;c sản phẩm tốt nhất của VBI cho kh&aacute;ch h&agrave;ng v&agrave; nh&acirc;n vi&ecirc;n của Aviva VN, đồng thời cung cấp c&aacute;c dịch vụ ho&agrave;n hảo với sự hỗ trợ kỹ thuật của VBI.&rdquo;</p>\r\n<p>Sự hợp t&aacute;c giữa một doanh nghiệp bảo hiểm phi nh&acirc;n thọ uy t&iacute;n h&agrave;ng đầu tại Việt Nam, c&oacute; c&aacute;c sản phẩm bảo hiểm hết sức cạnh tranh với một doanh nghiệp bảo hiểm nh&acirc;n thọ c&oacute; kinh nghiệm quốc tế, c&oacute; hệ thống rộng lớn tr&ecirc;n to&agrave;n quốc hứa hẹn sẽ mang lại những gi&aacute; trị thiết thực v&agrave; sự ph&aacute;t triển bền vững cho hai b&ecirc;n.</p>', '2018-03-21 10:19:52', '2018-03-21 21:19:52', 'images/news/1520584548_DSC_039_636451506619748080_HasThumb_Thumb.JPG.png'),
(235, 2, 'Bảo hiểm Vietinbank (VBI) và Công ty Cổ phần Tư vấn và Đầu tư BANKCA ký kết hợp tác chiến lược phân phối sản phẩm bảo hiểm', 'bao-hiem-vietinbank-vbi-va-cong-ty-co-phan-tu-van-va-dau-tu-bankca-ky-ket-hop-tac-chien-luoc-phan-phoi-san-pham-bao-hiem', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 3/11/2017, tại H&agrave; Nội, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam VietinBank (VBI) v&agrave; C&ocirc;ng ty cổ phần Tư Vấn v&agrave; Đầu Tư Bankca tiến h&agrave;nh k&yacute; kết hợp t&aacute;c chiến lược ph&acirc;n phối sản phẩm bảo hiểm, đ&aacute;nh dấu một bước khởi đầu tốt đẹp trong việc ph&aacute;t triển mối quan hệ...</span></p>', '<p><strong><span style=\"font-family: Arial;\">Ng&agrave;y 3/11/2017, tại H&agrave; Nội, C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam VietinBank (VBI) v&agrave; C&ocirc;ng ty cổ phần Tư Vấn v&agrave; Đầu Tư Bankca tiến h&agrave;nh k&yacute; kết hợp t&aacute;c chiến lược ph&acirc;n phối sản phẩm bảo hiểm, đ&aacute;nh dấu một bước khởi đầu tốt đẹp trong việc ph&aacute;t triển mối quan hệ song phương giữa hai b&ecirc;n.</span></strong></p>\r\n<p><img src=\"../../../uploads/IMG_1023.jpg\" alt=\"\" width=\"1366\" height=\"866\" /></p>\r\n<p>Với chiến lược ph&aacute;t triển to&agrave;n diện về sản phẩm v&agrave; thay đổi phương thức b&aacute;n h&agrave;ng hiệu quả, thời gian vừa qua, Bảo hiểm VietinBank đ&atilde; li&ecirc;n tục mở rộng ph&acirc;n phối sản phẩm qua c&aacute;c k&ecirc;nh VietinBank, GP Bank, Ocean Bank v&agrave; gần đ&acirc;y nhất l&agrave; việc k&yacute; kết hợp t&aacute;c to&agrave;n diện với C&ocirc;ng ty TNHH Bảo hiểm Nh&acirc;n thọ Aviva Việt Nam.</p>\r\n<p>Nhằm tiếp tục thực hiện chiến lược d&agrave;i hạn v&agrave; hướng đến mục ti&ecirc;u trở th&agrave;nh doanh nghiệp bảo hiểm phi nh&acirc;n thọ dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ, ng&agrave;y 3/11/2017, C&ocirc;ng ty Bảo hiểm VietinBank đ&atilde; thực hiện k&yacute; kết hợp t&aacute;c chiến lược ph&acirc;n phối sản phẩm bảo hiểm với C&ocirc;ng ty cổ phần Tư Vấn v&agrave; Đầu Tư Bankca.</p>\r\n<p><img src=\"../../../uploads/IMG_1032.jpg\" alt=\"\" width=\"1366\" height=\"739\" /></p>\r\n<p>Theo đ&oacute;, C&ocirc;ng ty Bankca sẽ thực hiện ph&acirc;n phối c&aacute;c sản phẩm bảo hiểm phi nh&acirc;n thọ của Bảo hiểm VietinBank th&ocirc;ng qua đội ngũ đại l&yacute; c&aacute; nh&acirc;n được đ&agrave;o tạo chuy&ecirc;n nghiệp v&agrave; b&agrave;i bản. Đặc biệt, Bảo hiểm VietinBank v&agrave; Bankca sẽ triển khai hợp t&aacute;c ph&acirc;n phối sản phẩm bảo hiểm tr&ecirc;n nền tảng c&ocirc;ng nghệ b&aacute;n h&agrave;ng ti&ecirc;n tiến nhất hiện nay. Giấy chứng nhận bảo hiểm được cấp trực tuyến th&ocirc;ng qua việc t&iacute;ch hợp hệ thống đồng thời tối ưu nhất c&aacute;c kh&acirc;u cho kh&aacute;ch h&agrave;ng trong đ&oacute; c&oacute; sử dụng c&aacute;c ứng dụng thanh to&aacute;n trực tuyến hiện đại.</p>\r\n<p><img src=\"../../../uploads/IMG_1011.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p>Tại lễ k&yacute; kết, B&agrave; Trần Thị Hương Thủy, Ph&oacute; Tổng Gi&aacute;m Đốc Bảo Hiểm VietinBank, đại diện l&atilde;nh đạo Bảo Hiểm VietinBank đ&atilde; chia sẻ: \"Hy vọng sự hợp t&aacute;c n&agrave;y sẽ l&agrave; một bước khởi đầu để ph&aacute;t triển mối quan hệ hợp t&aacute;c bền chặt v&agrave; vững mạnh giữa hai b&ecirc;n tr&ecirc;n thị trường bảo hiểm Việt Nam. Đ&acirc;y cũng l&agrave; cơ hội để hai b&ecirc;n c&ugrave;ng chia sẻ thế mạnh của m&igrave;nh, c&ugrave;ng nhau ph&aacute;t triển tr&ecirc;n thị trường bảo hiểm.\"</p>\r\n<p>&Ocirc;ng L&ecirc; Hải H&agrave;, Tổng Gi&aacute;m Đốc C&ocirc;ng ty cổ phần tư vấn v&agrave; đầu tư BankCa nhấn mạnh: \"Thỏa thuận hợp t&aacute;c chiến lược n&agrave;y sẽ đ&aacute;nh dấu sự khởi đầu tốt đẹp giữa VBI &amp; Bankca. Với những thế mạnh sẵn c&oacute; của đ&ocirc;i b&ecirc;n, đ&acirc;y l&agrave; c&aacute;nh cửa mở ra bước ph&aacute;t triển mới đầy tham vọng nhưng v&ocirc; c&ugrave;ng vững chắc. Bankca v&agrave; Bảo hiểm VietinBank cam kết hợp t&aacute;c l&acirc;u d&agrave;i, bền vững dựa tr&ecirc;n nguy&ecirc;n tắc b&igrave;nh đẳng, tin tưởng, c&ugrave;ng c&oacute; lợi với những chiến lược mang tầm vĩ m&ocirc;, b&aacute;m s&aacute;t những ch&iacute;nh s&aacute;ch của ch&iacute;nh phủ tiến tới phổ cập bảo hiểm cho 100% người d&acirc;n Việt Nam.&rdquo;</p>\r\n<p><img src=\"../../../uploads/IMG_1016.jpg\" alt=\"\" width=\"1366\" height=\"767\" /></p>\r\n<p>Sự hợp t&aacute;c giữa một doanh nghiệp bảo hiểm phi nh&acirc;n thọ uy t&iacute;n h&agrave;ng đầu Việt Nam, với c&aacute;c sản phẩm bảo hiểm hết sức cạnh tranh với một doanh nghiệp tư vấn v&agrave; đầu tư bảo hiểm c&oacute; hệ thống rộng lớn tr&ecirc;n cả nước hứa hẹn mang lại những gi&aacute; trị thiết thực cho kh&aacute;ch h&agrave;ng v&agrave; sự ph&aacute;t triển bền vững cho hai b&ecirc;n.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 10:23:31', '2018-03-21 21:23:31', 'images/news/1520584750_IMG_102_636457367949513504_HasThumb_Thumb.JPG.png'),
(236, 2, 'VBI trao vòng tay yêu thương tới trẻ em nghèo tại tỉnh Thanh Hóa', 'vbi-trao-vong-tay-yeu-thuong-toi-tre-em-ngheo-tai-tinh-thanh-hoa', '<p><span style=\"font-family: Arial;\">Tiếp nối truyền thống &ldquo;Uống nước nhớ nguồn&rdquo;,&ldquo;Tương th&acirc;n tương &aacute;i&rdquo;, Ng&agrave;y 24/11, tại địa b&agrave;n x&atilde; Giao Thiện, huyện Lang Ch&aacute;nh, tỉnh Thanh Ho&aacute;, Tổng C&ocirc;ng ty Bảo hiểm VietinBank, C&ocirc;ng ty Bảo hiểm VietinBank Thanh Ho&aacute; c&ugrave;ng UBND x&atilde; đ&atilde; tổ chức trao qu&agrave; trị gi&aacute; 60 triệu đồng cho trẻ em...</span></p>', '<p>Tiếp nối truyền thống <em>&ldquo;Uống nước nhớ nguồn&rdquo;,&ldquo;Tương th&acirc;n tương &aacute;i&rdquo;</em>, Ng&agrave;y 24/11, tại địa b&agrave;n x&atilde; Giao Thiện, huyện Lang Ch&aacute;nh, tỉnh Thanh Ho&aacute;, Tổng C&ocirc;ng ty Bảo hiểm VietinBank, C&ocirc;ng ty Bảo hiểm VietinBank Thanh Ho&aacute; c&ugrave;ng UBND x&atilde; đ&atilde; tổ chức trao qu&agrave; trị gi&aacute; 60 triệu đồng cho trẻ em ngh&egrave;o tại x&atilde;. Đ&acirc;y l&agrave; số tiền quy&ecirc;n g&oacute;p v&agrave; ủng hộ của to&agrave;n thể c&aacute;n bộ nh&acirc;n vi&ecirc;n Bảo hiểm VietinBank nhằm mang lại cho trẻ em ngh&egrave;o x&atilde; Giao Thiện c&oacute; them cơ hội đến trường v&agrave; khắc phục sự cố do ảnh hưởng của thi&ecirc;n tai b&atilde;o lũ.</p>\r\n<p>Chương tr&igrave;nh &ldquo;V&ograve;ng tay y&ecirc;u thương, c&ugrave;ng em tới trường&rdquo; được tổ chức cho hơn 360 trẻ em ngh&egrave;o, trẻ em c&oacute; ho&agrave;n cảnh kh&oacute; khăn tr&ecirc;n địa b&agrave;n. Chương tr&igrave;nh bao gồm c&aacute;c hoạt động: trao &aacute;o ấm, d&eacute;p cho học sinh cấp 1 x&atilde; Giao Thiện; vở, hộp b&uacute;t, balo cho học sinh cấp 2; 01 m&aacute;y lọc nước cho điểm trường mầm non v&agrave; ủng hộ từ thiện c&aacute;c hộ d&acirc;n c&oacute; nh&agrave; bị lũ cuốn tr&ocirc;i trong đợt b&atilde;o lũ th&aacute;ng 10 vừa qua, mỗi hộ d&acirc;n 2 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/AIMG_1502.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p>Sau gần 10 năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, bảo hiểm VietinBank đ&atilde; dần trở th&agrave;nh thương hiệu uy t&iacute;n tr&ecirc;n thị trường bảo hiểm phi nhận thọ Việt Nam, từng bước khẳng định được vị thế của m&igrave;nh với việc cung cấp c&aacute;c sản phẩm v&agrave; dịch vụ ti&ecirc;n tiến v&agrave; ưu việt nhất d&agrave;nh cho kh&aacute;ch h&agrave;ng.</p>\r\n<p><img src=\"../../../uploads/AIMG_1470.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p>&Ocirc;ng Ng&ocirc; Văn Quy&ecirc;n, Gi&aacute;m đốc C&ocirc;ng ty Bảo hiểm VietinBank Thanh Ho&aacute; cho biết: <em>&ldquo;C&ugrave;ng với c&aacute;c hoạt động kinh doanh v&agrave; dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng, C&ocirc;ng ty lu&ocirc;n &yacute; thức được tr&aacute;ch nhiệm của bản th&acirc;n với cộng đồng v&agrave; x&atilde; hội trong việc gi&uacute;p đỡ v&agrave; hỗ trợ những ho&agrave;n cảnh kh&oacute; khăn, những địa phương kh&ocirc;ng may phải chịu ảnh hưởng của thi&ecirc;n tai b&atilde;o lũ. C&aacute;c em sẽ l&agrave; tương lai của đất nước, đem kiến thức của m&igrave;nh dựng x&acirc;y tổ quốc ng&agrave;y một gi&agrave;u đẹp. Ch&iacute;nh v&igrave; thế, sứ mệnh C&ocirc;ng ty cần phải hỗ trợ v&agrave; gi&uacute;p đỡ thường xuy&ecirc;n tới c&aacute;c em, để c&aacute;c em c&oacute; them động lực v&agrave; niềm tin trong cuộc sống.&rdquo; </em></p>\r\n<p><img src=\"../../../uploads/AIMG_1538.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p>Bắc Trung Bộ lu&ocirc;n l&agrave; điểm n&oacute;ng hứng chịu rất nhiều thiệt hại do b&atilde;o lũ g&acirc;y ra. Năm nay, trước những diễn biến bất thường của thời tiết, c&aacute;c trận b&atilde;o lũ đ&atilde; tăng l&ecirc;n cả về số lượng cũng như mức độ ảnh hưởng v&agrave; đ&atilde; g&acirc;y ra thiệt hại lớn về t&agrave;i sản, t&iacute;nh mạng của người d&acirc;n. Trong đ&oacute; huyện Lang Ch&aacute;nh, tỉnh Thanh H&oacute;a l&agrave; một trong những địa phương bị thiệt hại nặng nề nhất bởi đợt mưa lũ lịch sử trong th&aacute;ng 10 vừa qua. T&iacute;nh ri&ecirc;ng tr&ecirc;n địa b&agrave;n huyện, đ&atilde; c&oacute; h&agrave;ng chục căn nh&agrave;, t&agrave;i sản bị đổ sập, hoa m&agrave;u bị cuốn tr&ocirc;i, c&aacute;c điểm trường bị c&ocirc; lập do sạt lở, ch&igrave;m trong lũ khiến cho cuộc sống vốn đ&atilde; kh&oacute; khăn của người d&acirc;n nay c&agrave;ng trở n&ecirc;n cơ cực hơn. Trận mưa lũ đ&atilde; đi qua nhưng hậu quả của n&oacute; g&acirc;y ra thật nặng nề đ&atilde; khiến cho nhiều gia đ&igrave;nh, em nhỏ l&acirc;m v&agrave;o cảnh kh&oacute; khăn, mất người th&acirc;n, thiếu s&aacute;ch vở học tập v&agrave; kh&ocirc;ng c&ograve;n m&aacute;i ấm để che mưa, che nắng.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 10:25:10', '2018-03-21 21:25:10', 'images/news/1520584909_pic-New-i_636473910114427482_HasThumb_Thumb.jpg.png'),
(237, 2, 'Bảo hiểm Vietinbank (VBI) chính thức chuyển đổi mô hình thành tổng công ty cổ phần', 'bao-hiem-vietinbank-vbi-chinh-thuc-chuyen-doi-mo-hinh-thanh-tong-cong-ty-co-phan', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 02/11/2017, Bộ T&agrave;i ch&iacute;nh đ&atilde; cấp Giấy ph&eacute;p điều chỉnh số 21/GPĐC23/KDBH cho ph&eacute;p C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam chuyển đổi h&igrave;nh thức v&agrave; đổi t&ecirc;n doanh nghiệp th&agrave;nh Tổng C&ocirc;ng ty Cổ phần Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam (Bảo hiểm Vietin...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Sau gần 10 năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, bảo hiểm VietinBank đ&atilde; dần trở th&agrave;nh thương hiệu uy t&iacute;n tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam, từng bước khẳng định được vị thế của m&igrave;nh với việc cung cấp c&aacute;c sản phẩm v&agrave; dịch vụ ưu việt tr&ecirc;n nền tảng c&ocirc;ng nghệ th&ocirc;ng tin hiện đại nhất d&agrave;nh cho kh&aacute;ch h&agrave;ng. Ng&agrave;y 02/11/2017, Bộ T&agrave;i ch&iacute;nh đ&atilde; cấp Giấy ph&eacute;p điều chỉnh số <strong>21/GPĐC23/KDBH</strong> cho ph&eacute;p C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam chuyển đổi h&igrave;nh thức v&agrave; đổi t&ecirc;n doanh nghiệp th&agrave;nh Tổng C&ocirc;ng ty Cổ phần Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam (Bảo hiểm VietinBank).</span></p>\r\n<p><img src=\"../../../uploads/AHVC8905.jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: B&agrave; Nguyễn Hồng V&acirc;n - Chủ tịch Hội đồng Quản trị, &Ocirc;ng L&ecirc; Tuấn Dũng - Th&agrave;nh vi&ecirc;n Hội Đồng Quản trị, Tổng Gi&aacute;m đốc VBI v&agrave; &Ocirc;ng Nguyễn L&acirc;m Dũng, Tổng gi&aacute;m đốc C&ocirc;ng ty VPBS thực hiện nghi thức ra mắt Tổng C&ocirc;ng ty Cổ Phần Bảo hiểm VietinBank)</span></em></p>\r\n<p><span style=\"font-family: Arial;\"> L&agrave; C&ocirc;ng ty 100% vốn của Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam, Tổng C&ocirc;ng ty CP Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam (Bảo hiểm VietinBank) c&oacute; được nhiều lợi thế thương hiệu ng&acirc;n h&agrave;ng mẹ như tiềm lực t&agrave;i ch&iacute;nh vững mạnh, chiến lược kinh doanh b&agrave;i bản, kinh nghiệm t&agrave;i ch&iacute;nh s&acirc;u rộng, nguồn kh&aacute;ch h&agrave;ng đa dạng, mạng lưới c&aacute;c chi nh&aacute;nh trải rộng khắp với 155 Chi nh&aacute;nh v&agrave; hơn 1.000 điểm giao dịch tr&ecirc;n to&agrave;n quốc. Đ&acirc;y l&agrave; một trong những lợi thế vượt trội của Bảo hiểm VietinBank so với c&aacute;c c&ocirc;ng ty bảo hiểm kh&aacute;c hoạt động tr&ecirc;n thị trường Việt Nam.<br /> <br />Việc chuyển đổi m&ocirc; h&igrave;nh hoạt động từ C&ocirc;ng ty tr&aacute;ch nhiệm hữu hạn MTV sang m&ocirc; h&igrave;nh Tổng C&ocirc;ng ty cổ phần sẽ tạo điều kiện cho C&ocirc;ng ty chủ động, minh bạch trong hoạt động kinh doanh theo c&aacute;c quyết định của C&ocirc;ng ty cổ phần, đẩy mạnh li&ecirc;n doanh li&ecirc;n kết với c&aacute;c đối t&aacute;c nhằm mở rộng mạng lưới hoạt động. L&agrave; bước tiền đề thu h&uacute;t c&aacute;c nh&agrave; đầu tư nước ngo&agrave;i như: tận dụng hệ thống quản l&yacute; hiện đại, hỗ trợ nguồn lực đầu tư lớn về c&ocirc;ng nghệ, c&aacute;c k&ecirc;nh ph&acirc;n phối, ph&aacute;t triển thương hiệu&hellip; nhằm chuẩn bị cho lộ tr&igrave;nh ni&ecirc;m yết tr&ecirc;n thị trường chứng kho&aacute;n.</span></p>\r\n<p><img src=\"../../../uploads/AHVC8932.jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Hội đồng Quản trị C&ocirc;ng ty v&agrave; c&aacute;c l&atilde;nh đạo c&ocirc;ng ty th&agrave;nh vi&ecirc;n n&acirc;ng ly ch&uacute;c mừng sự kiện ra mắt Tổng C&ocirc;ng ty cổ phần Bảo hiểm VietinBank)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Hơn thế nữa, khi chuyển đổi sang m&ocirc; h&igrave;nh n&agrave;y, người lao động trở th&agrave;nh cổ đ&ocirc;ng của C&ocirc;ng ty. Người lao động c&oacute; cơ hội gắn quyền lợi của m&igrave;nh với quyền lợi chung của C&ocirc;ng ty, từ đ&oacute; dẫn đến hiệu quả trong kinh doanh sẽ cao hơn. Về c&ocirc;ng t&aacute;c quản l&yacute;, sau khi chuyển đổi m&ocirc; h&igrave;nh sang m&ocirc; h&igrave;nh quản l&yacute; của C&ocirc;ng ty cổ phần dưới sự gi&aacute;m s&aacute;t chặt chẽ của c&aacute;c cổ đ&ocirc;ng, do vậy Hội đồng quản trị, Ban l&atilde;nh đạo v&agrave; người lao động sẽ l&agrave;m việc với tr&aacute;ch nhiệm cao hơn v&agrave; hiệu quả hơn để gia tăng lợi nhuận v&agrave; mang lại lợi &iacute;ch kinh tế thiết thực cho c&aacute;c cổ đ&ocirc;ng. <br /> <br />&Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc C&ocirc;ng ty Cổ phần Bảo hiểm VietinBank cho biết: &ldquo;Ra mắt m&ocirc; h&igrave;nh Tổng C&ocirc;ng ty sẽ mang lại cho Bảo hiểm VietinBank những cơ hội v&agrave; th&aacute;ch thức mới, đ&ograve;i hỏi c&oacute; một sự chuyển m&igrave;nh to&agrave;n diện về cả chất v&agrave; lượng để n&acirc;ng cao năng lực cạnh tranh tr&ecirc;n thị trường cũng như ho&agrave;n thiện chất lượng v&agrave; dịch vụ ng&agrave;y một tốt hơn. Việc chuyển đổi m&ocirc; h&igrave;nh Tổng C&ocirc;ng sẽ gia tăng quy m&ocirc; vốn chủ sở hữu, một mặt cũng l&agrave;m gia tăng &aacute;p lực tăng thị phần bảo hiểm tương ứng. Đồng thời Bảo hiểm VietinBank sẽ t&aacute;i cơ cấu tổ chức hoạt động của c&aacute;c khối, ph&ograve;ng ban nghiệp vụ theo hướng chuy&ecirc;n nghiệp, đ&ograve;i hỏi sự gi&aacute;m s&aacute;t, quản l&yacute; chặt chẽ của c&aacute;c cấp l&atilde;nh đạo v&agrave; đội ngũ nh&acirc;n sự phải ho&agrave;n th&agrave;nh tốt nhiệm vụ của m&igrave;nh trong bước đầu chuyển đổi m&ocirc; h&igrave;nh.&rdquo;</span></p>\r\n<p><img src=\"../../../uploads/A(1).jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ảnh: Hội đồng Quản trị v&agrave; Ban Kiểm so&aacute;t)</span></em></p>\r\n<p>Tr&ecirc;n cơ sở những dự b&aacute;o về t&igrave;nh h&igrave;nh thị trường bảo hiểm đến năm 2020 v&agrave; những năm tiếp theo, VBI đ&atilde; đề ra phương hướng v&agrave; chiến lược hoạt động cho c&aacute;c giai đoạn sau chuyển đổi m&ocirc; h&igrave;nh như sau:</p>\r\n<ul>\r\n<li><strong>Tầm nh&igrave;n</strong>: VBI hướng tới dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam.</li>\r\n<li><strong>Chiến lược</strong>: X&acirc;y dựng c&ocirc;ng ty ph&aacute;t triển bền vững, kinh doanh c&oacute; hiệu quả, c&oacute; tr&igrave;nh độ quản l&yacute; v&agrave; c&ocirc;ng nghệ hiện đại, c&oacute; khả năng cạnh tranh tr&ecirc;n thị trường, đảm bảo quyền lợi cho cổ đ&ocirc;ng v&agrave; người lao động, đ&oacute;ng g&oacute;p đầy đủ v&agrave; kịp thời nghĩa vụ đối với Nh&agrave; nước.</li>\r\n<li><strong>Mục ti&ecirc;u hoạt động</strong>: tối đa h&oacute;a lợi nhuận, tập trung v&agrave;o chiến lược kinh doanh dựa tr&ecirc;n c&aacute;c yếu tố chủ lực sau:<br /> - Tập trung mở rộng thị phần tại c&aacute;c thị trường mục ti&ecirc;u v&agrave; thị trường mới th&ocirc;ng qua ph&aacute;t triển sản phẩm, mở rộng k&ecirc;nh ph&acirc;n phối v&agrave; mạng lưới.<br /> - N&acirc;ng cao chất lượng dịch vụ bồi thường v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng.<br /> - X&acirc;y dựng, thực thi chiến lược ph&aacute;t triển nguồn nh&acirc;n lực th&ocirc;ng qua cơ chế tuyển dụng, đ&agrave;o tạo, ph&aacute;t triển nghề nghiệp, x&acirc;y dựng cơ chế đ&atilde;i ngộ, m&ocirc;i trường l&agrave;m việc tốt để thu h&uacute;t nguồn nh&acirc;n lực giỏi của thị trường.<br /> - X&acirc;y dựng v&agrave; chuẩn h&oacute;a hệ thống quản trị rủi ro.<br /> - Đầu tư x&acirc;y dựng hệ thống CNTT hiện đại, ti&ecirc;n tiến, coi đ&acirc;y l&agrave; lợi thế cạnh tranh của VBI tr&ecirc;n thị trường.</li>\r\n<li><strong>C&aacute;c chỉ ti&ecirc;u ch&iacute;nh</strong>:<br />- Trở th&agrave;nh một trong 08 doanh nghiệp bảo hiểm phi nh&acirc;n thọ lớn nhất Việt Nam về thị phần v&agrave;o năm 2020;<br />- Doanh thu ph&iacute; bảo hiểm: tăng trưởng từ 20% trở l&ecirc;n so với năm trước;<br />- Thu nhập b&igrave;nh qu&acirc;n của người lao động h&agrave;ng năm tăng từ 5% trở l&ecirc;n;<br />- Cổ tức h&agrave;ng năm từ 8% trở l&ecirc;n.</li>\r\n</ul>\r\n<p><br />Với sứ mệnh &ldquo;Bảo to&agrave;n gi&aacute; trị cuộc sống&rdquo;, Bảo hiểm VietinBank nỗ lực bảo vệ những gi&aacute; trị quan trọng trong cuộc sống, hướng đến r&uacute;t ngắn thời gian chi trả bồi thường cho kh&aacute;ch h&agrave;ng, kh&ocirc;ng ngừng n&acirc;ng cao chất lượng dịch vụ v&agrave; hỗ trợ bồi thường bảo hiểm kh&aacute;ch h&agrave;ng nhanh ch&oacute;ng, quay trở lại đ&oacute;ng g&oacute;p cho nền kinh tế đất nước ng&agrave;y c&agrave;ng phồn thịnh.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:26:16', '2018-03-21 21:26:16', 'images/news/1520585178_AHVC893_636475696351442710_HasThumb_Thumb.jpg.png'),
(238, 2, 'VBI tự hào là Công ty Bảo hiểm tốt nhất cho dịch vụ khách hàng Việt Nam 2017, trao tặng bởi tạp chí Global Banking & Finance Review', 'vbi-tu-hao-la-cong-ty-bao-hiem-tot-nhat-cho-dich-vu-khach-hang-viet-nam-2017-trao-tang-boi-tap-chi-global-banking-finance-review', '<p><span style=\"font-family: Arial;\">V&agrave;o th&aacute;ng 11 năm 2017 tại London, Tổng c&ocirc;ng ty bảo hiểm VietinBank vinh dự được tạp ch&iacute; t&agrave;i ch&iacute;nh Global Banking &amp; Finanace Review (GBAF) trao giải &ldquo;C&ocirc;ng ty Bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam năm 2017&rdquo;.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\"><strong>V&agrave;o th&aacute;ng 11 năm 2017 tại London, Tổng c&ocirc;ng ty bảo hiểm VietinBank vinh dự được tạp ch&iacute; t&agrave;i ch&iacute;nh Global Banking &amp; Finanace Review (GBAF) trao giải &ldquo;C&ocirc;ng ty Bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam năm 2017&rdquo;.</strong> &nbsp;</span></p>\r\n<p><img src=\"../../../uploads/VBI-Global Banking Finanace Review (GBAF).jpg\" alt=\"\" width=\"1024\" height=\"682\" /></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: Arial;\"><em> B&agrave; Nguyễn Hồng V&acirc;n &ndash; CTHĐQT VBI nhận giải từ đại diện GBAF</em><br /></span></p>\r\n<p>&ldquo;Global Banking &amp; Finance Review&rdquo; l&agrave; tạp ch&iacute; h&agrave;ng đầu thế giới của Vương Quốc Anh về lĩnh vực t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng. C&aacute;c giải thưởng được tạo ra để c&ocirc;ng nhận c&aacute;c doanh nghiệp c&oacute; những th&agrave;nh t&iacute;ch nổi bật trong c&aacute;c lĩnh vực chuy&ecirc;n m&ocirc;n cụ thể v&agrave; xuất sắc trong thế giới t&agrave;i ch&iacute;nh.</p>\r\n<p>Giải thưởng C&ocirc;ng ty bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam l&agrave; sự c&ocirc;ng nhận về hoạt động nổi trội trong lĩnh vực bảo hiểm bằng việc đ&aacute;nh gi&aacute; dựa tr&ecirc;n c&aacute;c ti&ecirc;u ch&iacute; về doanh thu, thị phần, dịch vụ kh&aacute;ch h&agrave;ng, năng lực cạnh tranh v&agrave; c&ocirc;ng nghệ ti&ecirc;n tiến. Global Banking &amp; Finance Review cũng xem x&eacute;t c&aacute;c &yacute; kiến của c&aacute;c chuy&ecirc;n gia ph&acirc;n t&iacute;ch c&aacute;c ng&agrave;nh, l&atilde;nh đạo c&aacute;c tập đo&agrave;n v&agrave; c&aacute;c chuy&ecirc;n gia c&ocirc;ng nghệ trong qu&aacute; tr&igrave;nh đ&aacute;nh gi&aacute;.</p>\r\n<p>Theo đ&aacute;nh gi&aacute; của Hội đồng tuyển chọn, những th&agrave;nh tựu nổi bật của VBI trong năm qua ch&iacute;nh l&agrave; việc nỗ lực n&acirc;ng cao chất lượng dịch vụ, đ&aacute;p dứng tốt nhất nhu cầu của kh&aacute;ch h&agrave;ng. VBI lu&ocirc;n cải tiến chất lượng dịch vụ, đưa ra những chương tr&igrave;nh, sản phẩm, đổi mới s&aacute;ng tạo, đa tiện &iacute;ch nhằm tăng hiệu quả trong sản xuất kinh doanh. Giải thưởng &ldquo;C&ocirc;ng ty Bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam năm 2017&rdquo; t&ocirc;n vinh những kết quả đạt được của VBI, đồng thời ghi nhận xứng đ&aacute;ng sự tăng trưởng mạnh mẽ v&agrave; hiệu quả của VBI.</p>\r\n<p>&Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc Tổng C&ocirc;ng ty Bảo hiểm VietinBank cho biết <em>&ldquo;Ch&uacute;ng t&ocirc;i lấy l&agrave;m vinh dự khi nhận được giải thưởng danh gi&aacute; của Global Banking &amp; Finance. Để xứng đ&aacute;ng với sự tin tưởng n&agrave;y của kh&aacute;ch h&agrave;ng, tập thể VBI sẽ nỗ lực hơn nữa cải tiến chất lượng dịch vụ, đặc biệt ch&uacute; trọng v&agrave;o việc đ&agrave;o tạo đội ngũ nh&acirc;n sự chuy&ecirc;n nghiệp, tận t&acirc;m với kh&aacute;ch h&agrave;ng theo đ&uacute;ng t&ocirc;n chỉ hoạt động của C&ocirc;ng ty thay kh&aacute;ch h&agrave;ng đối mặt mọi rủi ro trong cuộc sống&rdquo;.</em></p>\r\n<p><img src=\"../../../uploads/VBI-Global Banking  Finanace Review (GBAF)-2.jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<p style=\"text-align: center;\"><em>Ban l&atilde;nh đạo VBI nhận giải thưởng tại Vương Quốc Anh</em></p>\r\n<p>Bằng việc VBI &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, &aacute;p dụng c&ocirc;ng nghệ từ hệ thống k&ecirc;nh giao dịch với kh&aacute;ch h&agrave;ng, tối ưu h&oacute;a trong quy tr&igrave;nh, để dễ d&agrave;ng kết nối v&agrave; hỗ trợ kh&aacute;ch h&agrave;ng đ&atilde; được Global Banking &amp; Finanace đ&aacute;nh gi&aacute; rất cao. Năm 2016, VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng c&oacute;, gi&uacute;p kh&aacute;ch h&agrave;ng tiếp cận c&aacute;c quyền lợi. Trong năm 2017, VBI ra mắt ứng dụng My VBI với đầy đủ c&aacute;c t&iacute;nh năng như tra cứu tiện &iacute;ch, đăng k&yacute; mua sản phẩm trực tuyển v&agrave; tham khảo c&aacute;c ưu đ&atilde;i hiện c&oacute; tại VBI.</p>\r\n<p>Trải qua gần 10 năm x&acirc;y dựng v&agrave; ph&aacute;t triển, Tổng c&ocirc;ng ty bảo hiểm VietinBank (VBI) đ&atilde; vươn l&ecirc;n Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n tr&ecirc;n thị trường v&agrave; l&agrave; c&ocirc;ng ty bảo hiểm c&oacute; tốc độ tăng trưởng nhanh nhất Việt Nam giai đoạn 2013-2016 do Vietnam Report c&ocirc;ng bố. VBI đ&atilde; vinh dự nhận nhiều danh hiệu như: &ldquo;Top 20 doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng&rdquo;, &ldquo;Top 100 thương hiệu mạnh Việt Nam&rdquo;, &ldquo;Top 100 đơn vị nộp thuế thu nhập doanh nghiệp lớn nhất Việt Nam&rdquo;.</p>\r\n<p>Giải thưởng &ldquo;<strong>C&ocirc;ng ty Bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam năm 2017</strong>&rdquo; một lần nữa khẳng định cam kết của VBI lu&ocirc;n đặt kh&aacute;ch h&agrave;ng v&agrave;o trọng t&acirc;m mọi hoạt động ph&aacute;t triển kinh doanh. V&agrave; đ&acirc;y cũng l&agrave; một động lực rất lớn để VBI chuyển m&igrave;nh mạnh mẽ trong giai đoạn chuyển đổi m&ocirc; h&igrave;nh hoạt động từ tr&aacute;ch nhiệm hữu hạn một th&agrave;nh vi&ecirc;n sang c&ocirc;ng ty cổ phần để ho&agrave;n th&agrave;nh mục ti&ecirc;u đến năm 2020 trở th&agrave;nh top đầu mảng b&aacute;n lẻ bảo hiểm phi nh&acirc;n thọ.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 10:27:28', '2018-03-21 21:27:28', 'images/news/1520585438_VBI-Globa_636480815817045678_HasThumb_Thumb.jpg.png'),
(239, 2, 'Ứng dụng MyVBI - Một trải nghiệm bảo hiểm hoàn toàn mới', 'ung-dung-myvbi-mot-trai-nghiem-bao-hiem-hoan-toan-moi', '<p><span style=\"font-family: Arial;\">Th&aacute;ng 11/2017, Tổng C&ocirc;ng ty Bảo hiểm VietinBank ch&iacute;nh thức ra mắt Ứng dụng My VBI với đầy đủ c&aacute;c t&iacute;nh năng tiện &iacute;ch, hỗ trợ kh&aacute;ch h&agrave;ng một c&aacute;ch hiệu quả v&agrave; nhanh ch&oacute;ng. Đ&acirc;y l&agrave; ứng dụng d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tham gia bảo hiểm với c&aacute;c t&iacute;nh năng vượt trội...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Th&aacute;ng 11/2017, Tổng C&ocirc;ng ty Bảo hiểm VietinBank ch&iacute;nh thức ra mắt Ứng dụng My VBI với đầy đủ c&aacute;c t&iacute;nh năng tiện &iacute;ch, hỗ trợ kh&aacute;ch h&agrave;ng một c&aacute;ch hiệu quả v&agrave; nhanh ch&oacute;ng. Đ&acirc;y l&agrave; ứng dụng d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tham gia bảo hiểm với c&aacute;c t&iacute;nh năng vượt trội như: <em>Khai b&aacute;o bồi thường, chụp ảnh tổn thất xe cơ giới tại hiện trường, hướng dẫn bồi thường bảo hiểm, truy vấn th&ocirc;ng tin bồi thường bảo hiểm, tra cứu Giấy chứng nhận v&agrave; h&oacute;a đơn điện tử. Hơn thế nữa, ứng dụng My VBI c&ograve;n l&agrave; tiện &iacute;ch d&agrave;nh cho những kh&aacute;ch h&agrave;ng c&oacute; quan t&acirc;m đến bảo hiểm khi hỗ trợ kh&aacute;ch h&agrave;ng tham gia bảo hiểm trực tuyến, tra cứu mạng lưới hệ thống VBI, mạng lưới gara v&agrave; c&aacute;c cơ sở y tế trong trường hợp cần được trợ gi&uacute;p.</em></span></p>\r\n<p><img src=\"../../../uploads/VBI-Apps-171202-3.jpg\" alt=\"\" width=\"960\" height=\"306\" /></p>\r\n<p>Điểm nổi trội v&agrave; kh&aacute;c biệt của My VBI so với c&aacute;c ứng dụng bảo hiểm kh&aacute;c tr&ecirc;n thị trường nằm ở t&iacute;nh năng khai b&aacute;o tổn thất trực tiếp. Theo đ&oacute;, khi kh&aacute;ch h&agrave;ng kh&ocirc;ng may gặp sự cố tổn thất về xe cơ giới, chỉ bằng v&agrave;i thao t&aacute;c đơn giản tr&ecirc;n ứng dụng, kh&aacute;ch h&agrave;ng c&oacute; thể chủ động khai b&aacute;o c&aacute;c th&ocirc;ng tin cần thiết gi&uacute;p đẩy nhanh th&ocirc;ng tin đến C&ocirc;ng ty bảo hiểm so với việc đợi gi&aacute;m định vi&ecirc;n đến chụp ảnh.</p>\r\n<p>&Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc Bảo hiểm VietinBank cho biết: <em>&ldquo;Ứng dụng My VBI l&agrave; một trong những minh chứng cho nỗ lực kh&ocirc;ng ngừng của Bảo hiểm VietinBank nhằm n&acirc;ng cao hơn nữa chất lượng sản phẩm, dịch vụ, chất lượng bồi thường tổn thất cho kh&aacute;ch h&agrave;ng. Hiện nay, việc ứng dụng My VBI để khai b&aacute;o tổn thất v&agrave; bồi thường con người được xem như l&agrave; c&ocirc;ng nghệ ti&ecirc;n tiến nhất trong thời điểm hiện nay. Ch&uacute;ng t&ocirc;i hy vọng rằng My VBI sẽ được sử dụng rộng r&atilde;i trong cộng đồng để kh&aacute;ch h&agrave;ng c&oacute; cơ hội trải nghiệm c&aacute;c tiện &iacute;ch gia tăng, chủ động cho kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm trong kỉ nguy&ecirc;n c&ocirc;ng nghệ 4.0.&rdquo;</em></p>\r\n<p>D&agrave;nh cho những kh&aacute;ch h&agrave;ng mới khi quan t&acirc;m đến bảo hiểm hoặc mong muốn t&aacute;i tục c&aacute;c sản phẩm bảo hiểm, ứng dụng My VBI trở th&agrave;nh một người bạn đồng h&agrave;nh tiện dụng nhất d&agrave;nh cho kh&aacute;ch h&agrave;ng với c&aacute;c lựa chọn về sản phẩm ph&ugrave; hợp với từng nh&oacute;m kh&aacute;ch h&agrave;ng ri&ecirc;ng biệt. My VBI cũng sẽ hỗ trợ thanh to&aacute;n online v&agrave; tiện &iacute;ch n&agrave;y sẽ ch&iacute;nh thức được ra mắt v&agrave;o ng&agrave;y 1/1/2018 c&ugrave;ng với nhiều ưu đ&atilde;i hấp dẫn d&agrave;nh cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>Với giao diện tiện dụng, m&agrave;n h&igrave;nh tương t&aacute;c th&acirc;n thiện v&agrave; th&ocirc;ng minh, c&aacute;c thao t&aacute;c của người d&ugrave;ng tr&ecirc;n ứng dụng My VBI được đảm bảo dễ d&agrave;ng, nhanh ch&oacute;ng, hứa hẹn mang đến những trải nghiệm ho&agrave;n to&agrave;n mới cho c&aacute;c kh&aacute;ch h&agrave;ng tham gia bảo hiểm.<br /> <br />Th&ocirc;ng tin về sản phẩm li&ecirc;n hệ:<br />Tổng C&ocirc;ng ty CP Bảo hiểm VietinBank<br />Số 126 Đội Cấn, Ba Đ&igrave;nh, H&agrave; Nội<br />Điện thoại: 024 3211 5140</p>', '2018-03-21 10:29:22', '2018-03-21 21:29:22', 'images/news/1520585555_VBI-Apps-_636481551243705850_HasThumb_Thumb.jpg.png'),
(240, 2, 'Bảo hiểm VietinBank và Eroscare Việt Nam ký kết thỏa thuận hợp tác phân phối bảo hiểm du lịch', 'bao-hiem-vietinbank-va-eroscare-viet-nam-ky-ket-thoa-thuan-hop-tac-phan-phoi-bao-hiem-du-lich', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 8/12/2017, tại H&agrave; Nội, Tổng C&ocirc;ng Ty Bảo Hiểm VietinBank (VBI) v&agrave; C&ocirc;ng Ty Cổ Phần Chăm S&oacute;c Sức Khỏe Eroscare Việt Nam đ&atilde; tiến h&agrave;nh lễ k&yacute; kết hợp t&aacute;c chiến lược ph&acirc;n phối sản phẩm bảo hiểm du lịch, đ&aacute;nh dấu bước khởi đầu tốt đẹp trong việc ph&aacute;t triển mối quan hệ song phương...</span></p>', '<p><strong><span style=\"font-family: Arial;\">Ng&agrave;y 8/12/2017, tại H&agrave; Nội, Tổng C&ocirc;ng Ty Bảo Hiểm VietinBank (VBI) v&agrave; C&ocirc;ng Ty Cổ Phần Chăm S&oacute;c Sức Khỏe Eroscare Việt Nam đ&atilde; tiến h&agrave;nh lễ k&yacute; kết hợp t&aacute;c chiến lược ph&acirc;n phối sản phẩm bảo hiểm du lịch, đ&aacute;nh dấu bước khởi đầu tốt đẹp trong việc ph&aacute;t triển mối quan hệ song phương giữa hai b&ecirc;n.&nbsp;</span></strong></p>\r\n<p><img src=\"../../../uploads/AIMG_2047.jpg\" alt=\"\" width=\"1366\" height=\"860\" /></p>\r\n<p><span style=\"font-family: Arial;\">Theo đ&oacute;, Eroscare sẽ thực hiện ph&acirc;n phối b&aacute;n c&aacute;c sản phẩm bảo hiểm phi nh&acirc;n thọ của Bảo hiểm VietinBank th&ocirc;ng qua đội ngũ đại l&yacute; c&aacute; nh&acirc;n được đ&agrave;o tạo chuy&ecirc;n nghiệp v&agrave; b&agrave;i bản. Đặc biệt, Bảo hiểm VietinBank v&agrave; Eroscare sẽ triển khai hợp t&aacute;c ph&acirc;n phối sản phẩm bảo hiểm du lịch tr&ecirc;n với hệ thống đối t&aacute;c rộng lớn v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin quản trị ti&ecirc;n tiến nhất hiện nay. Giấy chứng nhận bảo hiểm được cấp trực tuyến th&ocirc;ng qua việc t&iacute;ch hợp hệ thống đồng thời tối ưu nhất c&aacute;c kh&acirc;u cho kh&aacute;ch h&agrave;ng trong đ&oacute; c&oacute; sử dụng c&aacute;c ứng dụng thanh to&aacute;n trực tuyến hiện đại. App My VBI gi&uacute;p kh&aacute;ch h&agrave;ng chủ động trong việc cập nhật th&ocirc;ng tin, khai b&aacute;o v&agrave; tra cứu bồi thường.</span></p>\r\n<p><img src=\"../../../uploads/AIMG_2060.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p><span style=\"font-family: Arial;\">B&agrave; Nguyễn Thu Liễu, Ph&oacute; Tổng Gi&aacute;m Đốc Eroscare Việt Nam nhấn mạnh: &ldquo; Việc hợp t&aacute;c chiến lược với Bảo Hiểm VietinBank trong việc ph&acirc;n phối c&aacute;c sản phẩm bảo hiểm du lịch l&agrave; bước tiến mới của Eroscare Việt Nam. Với đội ngũ nh&acirc;n vi&ecirc;n chuy&ecirc;n nghiệp, c&ocirc;ng nghệ th&ocirc;ng tin hiện đại v&agrave; đi đầu trong ng&agrave;nh bảo hiểm, Eroscare tự tin đem lại doanh thu lớn cho VBI trong 2 năm tới&rdquo;.</span></p>\r\n<p><img src=\"../../../uploads/AIMG_2028.jpg\" alt=\"\" width=\"1366\" height=\"767\" /></p>\r\n<p><span style=\"font-family: Arial;\">Tại lễ k&yacute; kết, &Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc Tổng C&ocirc;ng Ty Bảo Hiểm VietinBank đ&atilde; chia sẻ: \"Thỏa thuận hợp t&aacute;c chiến lược n&agrave;y sẽ l&agrave; tiền đề để Eroscare c&ugrave;ng VBI đồng h&agrave;nh chung tay vượt qua mọi th&aacute;ch thức c&ugrave;ng ph&aacute;t triển bền vững v&agrave; tăng cường mối quan hệ chặt chẽ hơn nữa trong c&aacute;c hoạt động kinh doanh cũng như đầu tư t&agrave;i ch&iacute;nh thời gian tới&rdquo;.</span></p>\r\n<p style=\"text-align: right;\"><strong><em><span style=\"font-family: Arial;\">VBI</span></em></strong></p>', '2018-03-21 10:30:46', '2018-03-21 21:30:46', 'images/news/1520585693_AIMG_204_636483557680390730_HasThumb_Thumb.jpg.png'),
(241, 2, 'Bảo hiểm VietinBank thông bảo lịch nghỉ bảo lãnh viện phí online trong dịp lễ tết dương lịch 2018', 'bao-hiem-vietinbank-thong-bao-lich-nghi-bao-lanh-vien-phi-online-trong-dip-le-tet-duong-lich-2018', '<p><span style=\"font-family: Arial;\">Trong thời gian nghỉ tết Dương lịch sẽ c&oacute; 1 số cơ sở y tế kh&ocirc;ng thực hiện bảo l&atilde;nh viện ph&iacute;. V&igrave; vậy, Qu&yacute; kh&aacute;ch vui l&ograve;ng thanh to&aacute;n trước v&agrave; thu thập hồ sơ, chứng từ gửi về VBI. Bảo hiểm VietinBank sẽ chi trả bồi thường sau.&nbsp;</span></p>', '<p><img src=\"../../../uploads/Artboard 1-100.jpg\" alt=\"\" width=\"900\" height=\"1118\" /></p>', '2018-03-21 10:32:10', '2018-03-21 21:32:10', 'images/news/1520585778_Artboard-_636501528911183614_HasThumb_Thumb.jpg.png'),
(242, 2, 'Ứng dụng My VBI – Đột phá công nghệ trong lĩnh vực bảo hiểm', 'ung-dung-my-vbi-dot-pha-cong-nghe-trong-linh-vuc-bao-hiem', '<p><span style=\"font-family: Arial;\">Với định vị &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, đi đầu xu thế c&ocirc;ng nghệ 4.0 trong lĩnh vực bảo hiểm, Bảo hiểm VietinBank (VBI) cho ra đời ứng dụng My VBI nhằm n&acirc;ng cao chất lượng dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng v&agrave; phục vụ cho hoạt động b&aacute;n h&agrave;ng.</span></p>', '<p><span style=\"font-family: Arial;\">Với định vị &ldquo;ti&ecirc;n phong về c&ocirc;ng nghệ&rdquo;, đi đầu xu thế c&ocirc;ng nghệ 4.0 trong lĩnh vực bảo hiểm, Bảo hiểm VietinBank (VBI) cho ra đời ứng dụng My VBI nhằm n&acirc;ng cao chất lượng dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng v&agrave; phục vụ cho hoạt động b&aacute;n h&agrave;ng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Ứng dụng My VBI được x&acirc;y dựng tr&ecirc;n nền tảng iOs v&agrave; Android, gi&uacute;p tối ưu h&oacute;a trong quy tr&igrave;nh để kh&aacute;ch h&agrave;ng v&agrave; Bảo hiểm VietinBank dễ d&agrave;ng kết nối, tương t&aacute;c với nhau qua thiết bị điện thoại th&ocirc;ng minh để từ đ&oacute;, quy tr&igrave;nh gi&aacute;m định, bồi thường hay mua h&agrave;ng của kh&aacute;ch h&agrave;ng được thực hiện một c&aacute;ch nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng nhất.</span></p>\r\n<p><span style=\"font-family: Arial;\">Sự ra đời của ứng dụng My VBI được Bảo Hiểm VietinBank ph&aacute;t triển tập trung cho ba sản phẩm ch&iacute;nh: Gi&aacute;m định, bồi thường tổn thất xe cơ giới, tra cứu bồi thường bảo hiểm sức khỏe v&agrave; mua h&agrave;ng trực tuyến.</span></p>\r\n<p><span style=\"font-family: Arial;\">T&iacute;nh năng gi&aacute;m định v&agrave; bồi thường bảo hiểm trực tuyến l&agrave; một trong những t&iacute;nh năng ưu việt, vượt trội nhất của ứng dụng My VBI. Thay v&igrave; quy tr&igrave;nh khai b&aacute;o bồi thường trước kia với đầy đủ c&aacute;c bước như tiếp nhận y&ecirc;u cầu xử l&yacute; bồi thường, gi&aacute;m định tổn thất, lựa chọn phương &aacute;n bồi thường, ho&agrave;n thiện hồ sơ bồi thường, để to&agrave;n tất thục tục bồi thường, kh&aacute;ch h&agrave;ng cần chờ đợi 15 ng&agrave;y l&agrave;m việc, thậm ch&iacute; h&agrave;ng th&aacute;ng trời. Tuy nhi&ecirc;n, khi kh&aacute;ch h&agrave;ng ph&aacute;t sinh nhu cầu bồi thường bảo hiểm về &ocirc; t&ocirc; hay sức khỏe, chỉ cần một v&agrave;i thao t&aacute;c tr&ecirc;n ứng dụng My VBI, chụp hồ sơ gốc ngay tại ứng dụng, My VBI sẽ tiếp nhận trục tiếp v&agrave; chuyển về bộ phận gi&aacute;m định v&agrave; bồi thường của Bảo hiểm VietinBank trong v&ograve;ng 2 gi&acirc;y. Tại đ&acirc;y, c&aacute;c th&ocirc;ng tin của kh&aacute;ch h&agrave;ng được xử l&yacute; ngay lập tức m&agrave; kh&ocirc;ng mất thời gian gửi nhận. Với ứng dụng My VBI thời gian xử l&yacute; được r&uacute;t gọn xuống c&ograve;n 5 ng&agrave;y l&agrave;m việc, chỉ bằng 1/3 tốc độ trung b&igrave;nh hiện tại tr&ecirc;n thị trường bảo hiểm.</span></p>\r\n<p><img src=\"../../../uploads/VBI-Apps-171202-1.jpg\" alt=\"\" width=\"803\" height=\"1200\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Ứng dụng My VBI &ndash; Gi&aacute;m định v&agrave; bồi thường bảo hiểm trực tuyến</span></em></p>\r\n<p><span style=\"font-family: Arial;\">ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m Đốc Bảo hiểm VietinBank cho biết:<em> &ldquo;Ứng dụng My VBI l&agrave; một trong những minh chứng cho nỗ lực kh&ocirc;ng ngừng của Bảo hiểm VietinBank nhằm n&acirc;ng cao hơn nữa chất lượng sản phẩm, dịch vụ, chất lượng bồi thường tổn thất cho kh&aacute;ch h&agrave;ng. Hiện nay, việc ứng dụng My VBI để khai b&aacute;o tổn thất v&agrave; bồi thường con người được xem như l&agrave; c&ocirc;ng nghệ ti&ecirc;n tiến nhất trong thời điểm hiện nay. Ch&uacute;ng t&ocirc;i hy vọng rằng My VBI sẽ được sử dụng rộng r&atilde;i trong cộng đồng để kh&aacute;ch h&agrave;ng c&oacute; cơ hội trải nghiệm c&aacute;c tiện &iacute;ch gia tăng, chủ động cho kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm trong kỉ nguy&ecirc;n c&ocirc;ng nghệ 4.0.&rdquo;</em><br />Ứng dụng My VBI l&agrave; sự t&acirc;m huyết, d&agrave;y c&ocirc;ng nghi&ecirc;n cứu của cả tập thể Ban L&atilde;nh đạo v&agrave; C&aacute;n bộ, nh&acirc;n vi&ecirc;n Bảo hiểm VietinBank để đem đến cho kh&aacute;ch h&agrave;ng những trải nghiệm ho&agrave;n to&agrave;n mới về việc sử dụng bảo hiểm, x&oacute;a nh&ograve;a những r&agrave;o cản của kh&aacute;ch h&agrave;ng khi tham gia bảo hiểm. My VBI cũng l&agrave; một h&agrave;nh trang vững v&agrave;ng trong năm 2018 gi&uacute;p Bảo hiểm VietinBank ho&agrave;n to&agrave;n tự tin với mục ti&ecirc;u trở th&agrave;nh C&ocirc;ng ty bảo hiểm top đầu về mảng b&aacute;n lẻ bảo hiểm phi nh&acirc;n thọ.<br />Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; thể tải App theo link sau:<br />Android: <a href=\"https://goo.gl/e2WsZA\">https://goo.gl/e2WsZA</a> IOS: <a href=\"https://goo.gl/wBU5aq\">https://goo.gl/wBU5aq</a></span></p>', '2018-03-21 10:32:48', '2018-03-21 21:32:48', 'images/news/1520585908_VBI-Apps-_636522310734934464_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(243, 3, 'Ưu đãi phí bảo hiểm lên tới 30% cho khách hàng mới', 'uu-dai-phi-bao-hiem-len-toi-30-cho-khach-hang-moi', '<p><span style=\"font-family: Arial;\">Với mong muốn mang lại quyền lợi ưu đ&atilde;i nhất cho kh&aacute;ch h&agrave;ng vay vốn tại VietinBank v&agrave; đồng h&agrave;nh c&ugrave;ng chương tr&igrave;nh &ldquo;Ưu đ&atilde;i kh&aacute;ch h&agrave;ng mới&rdquo;, Bảo hiểm VietinBank triển khai chương tr&igrave;nh khuyến mại giảm tới 30% ph&iacute; bảo hiểm cho c&aacute;c kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n vay vốn tại VietinBank&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Với mong muốn mang lại quyền lợi ưu đ&atilde;i nhất cho kh&aacute;ch h&agrave;ng vay vốn tại VietinBank v&agrave; đồng h&agrave;nh c&ugrave;ng chương tr&igrave;nh \"Ưu đ&atilde;i kh&aacute;ch h&agrave;ng mới\", Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh khuyến mại giảm tới 30% ph&iacute; bảo hiểm cho c&aacute;c kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n vay vốn tại VietinBank.</span></p>\r\n<p><img src=\"../../../uploads/Uu dai KH moi Banner.png\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p><strong>1. Thời gian khuyến mại:</strong> Từ 01/08/2014 đến 31/12/2014.</p>\r\n<p><strong>2. Sản phẩm &aacute;p dụng</strong></p>\r\n<p style=\"padding-left: 30px;\">- Bảo hiểm &Ocirc; t&ocirc; VietinCar<br /> - Bảo hiểm Nh&agrave; VietinHome<br /> - Bảo hiểm Con người VietinCare<br /> - Bảo hiểm T&agrave;i sản đảm bảo<br /> - Bảo hiểm H&agrave;ng h&oacute;a xuất nhập khẩu<br /> - Bảo hiểm Du học, Du lịch nước ngo&agrave;i</p>\r\n<p><strong>3. Nội dung khuyến mại</strong></p>\r\n<p>Ưu đ&atilde;i giảm tới 30% ph&iacute; bảo hiểm d&agrave;nh ri&ecirc;ng cho kh&aacute;ch h&agrave;ng của chương tr&igrave;nh khuyến m&atilde;i &ldquo;Ưu đ&atilde;i kh&aacute;ch h&agrave;ng mới&rdquo; của VietinBank.</p>\r\n<p><em>Vui l&ograve;ng li&ecirc;n hệ với c&aacute;c chi nh&aacute;nh Bảo hiểm VietinBank tr&ecirc;n to&agrave;n quốc hoặc li&ecirc;n hệ Email: info.vbi@vietinbank.vn để được tư vấn về chương tr&igrave;nh.</em></p>', '2018-03-21 10:33:16', '2018-03-21 21:33:16', 'images/news/1520590896_Uu-dai-KH_635448206238062484_HasThumb_Thumb.png'),
(244, 3, 'Ưu đãi bảo hiểm cho khách hàng tham gia gói tín dụng 3000 tỷ đồng vay nông sản của VietinBank', 'uu-dai-bao-hiem-cho-khach-hang-tham-gia-goi-tin-dung-3000-ty-dong-vay-nong-san-cua-vietinbank', '<p><span style=\"font-family: Arial;\">Kh&aacute;ch h&agrave;ng tham gia chương tr&igrave;nh sẽ đồng thời được tham gia bảo hiểm con người VietinCare với c&aacute;c g&oacute;i ưu đ&atilde;i linh hoạt v&agrave; gi&aacute; trị bảo hiểm l&ecirc;n tới 500 triệu đồng/Hợp đồng t&iacute;n dụng.&nbsp;</span></p>', '<p><img src=\"../../../uploads/VayNongSan_small.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Từ ng&agrave;y 10/06 đến 31/12/2014, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh ưu đ&atilde;i bảo hiểm cho kh&aacute;ch h&agrave;ng tham gia g&oacute;i t&iacute;n dụng trị gi&aacute; 3000 tỷ đồng của VietinBank. Chương tr&igrave;nh &aacute;p dụng cho kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n/hộ gia đ&igrave;nh v&agrave; doanh nghiệp si&ecirc;u vi m&ocirc; vay vốn sản xuất kinh doanh phục vụ mục đ&iacute;ch trồng trọt/ chăn nu&ocirc;i/ thu mua/ chế biến đối với c&aacute;c lĩnh vực n&ocirc;ng sản trọng yếu: cao su, ti&ecirc;u, c&agrave; ph&ecirc;/ch&egrave;, thủy sản, l&uacute;a gạo, hoa m&agrave;u.</p>\r\n<p>Kh&aacute;ch h&agrave;ng tham gia chương tr&igrave;nh sẽ đồng thời được tham gia bảo hiểm con người VietinCare với c&aacute;c g&oacute;i ưu đ&atilde;i linh hoạt v&agrave; gi&aacute; trị bảo hiểm l&ecirc;n tới 500 triệu đồng/Hợp đồng t&iacute;n dụng. Ngo&agrave;i ra, kh&aacute;ch h&agrave;ng c&oacute; nhu cầu c&oacute; thể tham gia c&aacute;c sản phẩm bảo hiểm t&agrave;i sản thế chấp của VietinBank như: Bảo hiểm kho h&agrave;ng n&ocirc;ng sản, bảo hiểm nh&agrave; xưởng, m&aacute;y m&oacute;c, bảo hiểm nh&agrave; tư nh&acirc;n, &ocirc;t&ocirc;,&hellip;với ưu đ&atilde;i giảm ph&iacute; bảo hiểm l&ecirc;n tới 30%.</p>\r\n<p>VietinBank d&agrave;nh 3000 tỷ với l&atilde;i suất ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng vay vốn ngắn hạn tr&ecirc;n c&aacute;c địa b&agrave;n khu vực miền Trung v&agrave; miền T&acirc;y Nam Bộ. Tham gia chương tr&igrave;nh, kh&aacute;ch h&agrave;ng ph&aacute;t sinh dư nợ lần đầu hoặc ph&aacute;t sinh dư nợ trở lại sau 12 th&aacute;ng kh&ocirc;ng c&oacute; số dư tiền vay tại VietinBank được hưởng l&atilde;i suất ưu đ&atilde;i từ 7%/năm. C&aacute;c đối tượng kh&aacute;ch h&agrave;ng c&ograve;n lại được &aacute;p dụng mức l&atilde;i suất ưu đ&atilde;i từ 7,5%/năm. L&atilde;i suất ưu đ&atilde;i được &aacute;p dụng trong &frac12; thời gian vay ban đầu.</p>\r\n<p>H&atilde;y tới c&aacute;c Chi nh&aacute;nh/Ph&ograve;ng giao dịch của VietinBank hoặc li&ecirc;n hệ Contact Center: 1900 55 8868; Email: contact@vietinbank.vn để được tư vấn v&agrave; tham gia chương tr&igrave;nh.</p>', '2018-03-21 10:33:42', '2018-03-21 21:33:42', 'images/news/1520590943_VayNongSa_635530241539193121_HasThumb_Thumb.jpg.png'),
(245, 3, 'Ưu đãi phí bảo hiểm cho khách hàng chương trình “Tiếp vốn nhanh - Vay ưu đãi”', 'uu-dai-phi-bao-hiem-cho-khach-hang-chuong-trinh-tiep-von-nhanh-vay-uu-dai', '<p><span style=\"font-family: Arial;\">Từ 22/07 &ndash; 31/12/2014, khi tham gia chương tr&igrave;nh &ldquo;Tiếp vốn nhanh &ndash; Vay ưu đ&atilde;i&rdquo;, kh&aacute;ch h&agrave;ng sẽ nhận được ưu đ&atilde;i giảm ph&iacute; bảo hiểm l&ecirc;n tới 30% cho c&aacute;c sản phẩm: Bảo hiểm con người VietinCare, Bảo hiểm &ocirc;t&ocirc; VietinCar, Bảo hiểm nh&agrave; VietinHome, Bảo hiểm du học, Bảo hiểm du lịch, Bảo hi...&nbsp;</span></p>', '<p><strong><span style=\"font-family: Arial;\">Từ 22/07 &ndash; 31/12/2014, khi tham gia chương tr&igrave;nh &ldquo;Tiếp vốn nhanh &ndash; Vay ưu đ&atilde;i&rdquo;, kh&aacute;ch h&agrave;ng sẽ nhận được ưu đ&atilde;i giảm ph&iacute; bảo hiểm l&ecirc;n tới 30% cho c&aacute;c sản phẩm: Bảo hiểm con người VietinCare, Bảo hiểm &ocirc;t&ocirc; VietinCar, Bảo hiểm nh&agrave; VietinHome, Bảo hiểm du học, Bảo hiểm du lịch, Bảo hiểm t&agrave;i sản thế chấp v&agrave; Bảo hiểm h&agrave;ng h&oacute;a xuất nhập khẩu.</span></strong></p>\r\n<p><img src=\"../../../uploads/tiepvonnhanh_banner.png\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Chương tr&igrave;nh cho vay ưu đ&atilde;i &ldquo;Tiếp vốn nhanh &ndash; Vay ưu đ&atilde;i&rdquo; được VietinBank triển khai từ nay đến hết năm 2014 với mức l&atilde;i suất cho vay đặc biệt ưu đ&atilde;i, chỉ từ 7,99%/năm.</p>\r\n<p>Cụ thể, kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n/ hộ gia đ&igrave;nh vay ti&ecirc;u d&ugrave;ng trong ngắn hạn được hưởng l&atilde;i suất cho vay ưu đ&atilde;i chỉ 8,5%/năm cho 1/2 thời gian khoản vay. Kh&aacute;ch h&agrave;ng vay ti&ecirc;u d&ugrave;ng trong trung d&agrave;i hạn c&oacute; thể chọn 2 phương &aacute;n l&atilde;i suất linh hoạt: L&atilde;i suất chỉ 7,99%/năm trong th&aacute;ng đầu ti&ecirc;n v&agrave; chỉ từ 9,99%/năm trong 5 th&aacute;ng tiếp theo; hoặc l&atilde;i suất cho vay thấp hơn 1% so với s&agrave;n l&atilde;i suất cho vay ti&ecirc;u d&ugrave;ng trung d&agrave;i hạn trong 12 th&aacute;ng đầu.<br /> <br />VietinBank cũng d&agrave;nh ưu đ&atilde;i đặc biệt cho kh&aacute;ch h&agrave;ng l&agrave; c&aacute; nh&acirc;n/hộ gia đ&igrave;nh/doanh nghiệp si&ecirc;u vi m&ocirc; vay SXKD. Trong đ&oacute;, c&aacute;c khoản vay SXKD ngắn hạn trong Chương tr&igrave;nh được hưởng mức l&atilde;i suất chỉ 8,5%/năm trong thời gian l&ecirc;n đến 6 th&aacute;ng. Đồng thời, VietinBank cũng d&agrave;nh cho kh&aacute;ch h&agrave;ng vay SXKD trong trung d&agrave;i hạn 2 phương &aacute;n ưu đ&atilde;i l&atilde;i suất, gồm: l&atilde;i suất 8,99%/năm cho th&aacute;ng đầu ti&ecirc;n v&agrave; 9,99%/năm cho 5 th&aacute;ng tiếp theo; hoặc hưởng l&atilde;i suất ưu đ&atilde;i thấp hơn s&agrave;n l&atilde;i suất cho vay SXKD trung d&agrave;i hạn 0,5%/năm trong 12 th&aacute;ng đầu.<br /> <br />Bạn c&oacute; nhu cầu mua nh&agrave;, mua xe &ocirc;t&ocirc; hoặc đầu tư kinh doanh&hellip; nhưng đang t&igrave;m kiếm nguồn hỗ trợ t&agrave;i ch&iacute;nh? H&atilde;y đến với chương tr&igrave;nh &ldquo;Tiếp vốn nhanh &ndash; Vay ưu đ&atilde;i&rdquo; của VietinBank để được hưởng mức l&atilde;i suất cho vay hấp dẫn nhất. VietinBank cam kết th&ocirc;ng tho&aacute;ng thủ tục, tinh gọn quy tr&igrave;nh, tạo điều kiện cho kh&aacute;ch h&agrave;ng dễ d&agrave;ng tiếp cận nguồn vốn gi&aacute; rẻ.</p>\r\n<p>H&atilde;y tới Chi nh&aacute;nh/Ph&ograve;ng giao dịch của VietinBank hoặc li&ecirc;n hệ Contact Center: 1900558868; Email: contact@vietinbank.vn để được tư vấn v&agrave; tham gia chương tr&igrave;nh.</p>', '2018-03-21 10:34:08', '2018-03-21 21:34:08', 'images/news/1520591007_tiepvonnh_635448385690198276_HasThumb_Thumb.png'),
(246, 3, 'Bảo hiểm VietinBank khuyến mãi, giao dịch nhận quà ngay', 'bao-hiem-vietinbank-khuyen-mai-giao-dich-nhan-qua-ngay', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 15/12/2014 đến 10/2/2015, Bảo hiểm VietinBank triển khai chương tr&igrave;nh ưu đ&atilde;i hấp dẫn nhất trong năm: &ldquo;Bảo hiểm VietinBank khuyến m&atilde;i, giao dịch nhận qu&agrave; ngay&rdquo;.&nbsp;</span></p>', '<p>Từ ng&agrave;y 15/12/2014 đến 10/2/2015, Bảo hiểm VietinBank triển khai chương tr&igrave;nh ưu đ&atilde;i hấp dẫn nhất trong năm: &ldquo;Bảo hiểm VietinBank khuyến m&atilde;i, giao dịch nhận qu&agrave; ngay&rdquo;.</p>\r\n<p>Chương tr&igrave;nh d&agrave;nh cho tất cả kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n v&agrave; c&aacute;c hộ kinh doanh nhỏ. Theo đ&oacute;, mỗi kh&aacute;ch h&agrave;ng khi tham gia đăng k&yacute; c&aacute;c sản phẩm bảo hiểm: <strong>Bảo hiểm con người VietinCare, bảo hiểm An to&agrave;n c&aacute; nh&acirc;n, bảo hiểm Nh&agrave; tư nh&acirc;n, bảo hiểm Du lịch Quốc tế</strong> sẽ sẽ được giảm ngay 25% tổng ph&iacute; mua bảo hiểm.</p>\r\n<p><img src=\"../../../uploads/CTKM2.jpg\" alt=\"\" width=\"923\" height=\"503\" /></p>\r\n<p>Chương tr&igrave;nh khuyến m&atilde;i n&agrave;y được &aacute;p dụng song song với c&aacute;c chương tr&igrave;nh khuyến m&atilde;i bảo hiểm kh&aacute;c đang triển khai.</p>\r\n<p>H&atilde;y tận hưởng v&agrave; trải nghiệm dịch vụ d&agrave;nh cho kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n từ Bảo hiểm VietinBank ngay h&ocirc;m nay để nhận được chương tr&igrave;nh khuyến m&atilde;i hấp dẫn đang triển khai cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>Th&ocirc;ng tin chi tiết qu&yacute; kh&aacute;ch h&agrave;ng li&ecirc;n hệ:</p>\r\n<p>C&aacute;c Chi nh&aacute;nh/Ph&ograve;ng giao dịch của VietinBank tr&ecirc;n to&agrave;n quốc, hoặc Tổng đ&agrave;i hỗ trợ kh&aacute;ch h&agrave;ng: 1900 55 8868; Email: contact@vietinbank.vn; Website: vbi.vietinbank.vn.</p>', '2018-03-21 10:34:31', '2018-03-21 21:34:31', 'images/news/1520591079_CTKM_635544303405151252_HasThumb_Thumb.jpg.png'),
(247, 3, 'Ưu đãi bảo hiểm ô tô “Ngàn xe sang vui Tết”', 'uu-dai-bao-hiem-o-to-ngan-xe-sang-vui-tet', '<p><span style=\"font-family: Arial;\">C&aacute;c kh&aacute;ch h&agrave;ng tham gia chương tr&igrave;nh &ldquo;Ng&agrave;n xe sang vui Tết&rdquo; v&agrave; mua sản phẩm bảo hiểm vật chất xe &ocirc; t&ocirc; của Bảo hiểm VietinBank (VBI) được miễn ph&iacute; to&agrave;n bộ ph&iacute; bảo hiểm năm đầu nếu tham gia bảo hiểm trong 4 năm li&ecirc;n tục trở l&ecirc;n; hoặc nhận ưu đ&atilde;i giảm 20% ph&iacute; bảo hiểm gốc&nbsp;</span></p>', '<p>Nhằm hỗ trợ kh&aacute;ch h&agrave;ng c&oacute; nhu cầu mua &ocirc; t&ocirc; nh&acirc;n dịp đ&oacute;n năm mới 2015, Bảo hiểm VietinBank (VBI) phối hợp c&ugrave;ng Ng&acirc;n h&agrave;ng VietinBank triển khai chương tr&igrave;nh ưu đ&atilde;i &ldquo;Ng&agrave;n xe sang vui Tết&rdquo; từ ng&agrave;y 08/01/2015 đến 31/03/2015.</p>\r\n<p>C&aacute;c kh&aacute;ch h&agrave;ng tham gia chương tr&igrave;nh &ldquo;Ng&agrave;n xe sang vui Tết&rdquo; v&agrave; mua sản phẩm bảo hiểm vật chất xe &ocirc; t&ocirc; của Bảo hiểm VietinBank (VBI) được miễn ph&iacute; to&agrave;n bộ ph&iacute; bảo hiểm năm đầu nếu tham gia bảo hiểm trong 4 năm li&ecirc;n tục trở l&ecirc;n; hoặc nhận ưu đ&atilde;i giảm 20% ph&iacute; bảo hiểm gốc cho c&aacute;c trường hợp c&ograve;n lại.</p>\r\n<p><img src=\"../../../uploads/banner nho CTKM o to.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>B&ecirc;n cạnh đ&oacute;, khi tham gia chương tr&igrave;nh, kh&aacute;ch h&agrave;ng c&ograve;n được ưu đ&atilde;i hấp dẫn nhất từ VietinBank. Cụ thể: VietinBank &aacute;p dụng mức l&atilde;i suất chỉ từ 7,3%/năm đối với khoản vay trung hạn, thời gian ưu đ&atilde;i tối đa l&ecirc;n đến 12 th&aacute;ng. Đối với c&aacute;c khoản vay ngắn hạn, VietinBank d&agrave;nh mức l&atilde;i suất ưu đ&atilde;i chỉ 7,5%/năm với thời gian ưu đ&atilde;i l&atilde;i suất trong tối đa 3 th&aacute;ng.</p>\r\n<p>Đặc biệt, kh&aacute;ch h&agrave;ng c&ograve;n được nhận ngay qu&agrave; tặng từ VietinBank. Trước ng&agrave;y 15/2/2015, kh&aacute;ch h&agrave;ng giải ng&acirc;n tr&ecirc;n 1 tỷ đồng được nhận g&oacute;i qu&agrave; tặng phụ kiện nội thất/ ngoại thất cho xe trị gi&aacute; l&ecirc;n đến 5 triệu đồng; kh&aacute;ch h&agrave;ng giải ng&acirc;n dưới 1 tỷ đồng được nhận g&oacute;i qu&agrave; tặng trị gi&aacute; 1 triệu đồng.</p>\r\n<p>&ldquo;Ng&agrave;n xe sang vui Tết&rdquo; l&agrave; chương tr&igrave;nh khuyến m&atilde;i bảo hiểm &ocirc; t&ocirc; triển khai nh&acirc;n dịp đầu năm mới 2015 của Bảo hiểm VietinBank (VBI) v&agrave; Ng&acirc;n h&agrave;ng VietinBank. Qua đ&oacute;, Bảo hiểm VietinBank (VBI) tri &acirc;n kh&aacute;ch h&agrave;ng, đồng thời cũng l&agrave; lời ch&uacute;c mừng năm mới của Bảo hiểm VietinBank (VBI) gửi tới kh&aacute;ch h&agrave;ng.</p>\r\n<p>Thể lệ chi tiết chương tr&igrave;nh xem tại đ&acirc;y.<br /> <br />Để biết th&ecirc;m th&ocirc;ng tin chi tiết, kh&aacute;ch h&agrave;ng c&oacute; thể tới ph&ograve;ng giao dịch/chi nh&aacute;nh VietinBank gần nhất, hoặc vui l&ograve;ng li&ecirc;n hệ Contact Center: 1900 558 868, email: <a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:35:02', '2018-03-21 21:35:02', 'images/news/1520591147_Banner-to_635566587588741497_HasThumb_Thumb.jpg.png'),
(248, 3, 'Ưu đãi phí bảo hiểm cho khách hàng tham gia chương trình “Lãi hè giảm nhiệt”', 'uu-dai-phi-bao-hiem-cho-khach-hang-tham-gia-chuong-trinh-lai-he-giam-nhiet', '<p><span style=\"font-family: Arial;\">Từ nay đến 30/06/2015, khi tham gia chương tr&igrave;nh &ldquo;L&atilde;i h&egrave; giảm nhiệt&rdquo;, kh&aacute;ch h&agrave;ng sẽ nhận được ưu đ&atilde;i giảm ph&iacute; bảo hiểm l&ecirc;n tới 25%&nbsp;</span></p>', '<p>Từ nay đến 30/06/2015, Bảo hiểm VietinBank (VBI) phối hợp với Ng&acirc;n h&agrave;ng VietinBank triển khai chương tr&igrave;nh ưu đ&atilde;i &ldquo;L&atilde;i h&egrave; giảm nhiệt&rdquo; cho c&aacute;c kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n/hộ gia đ&igrave;nh v&agrave; doanh nghiệp si&ecirc;u vi m&ocirc; c&oacute; nhu cầu vay vốn phục vụ mục đ&iacute;ch ti&ecirc;u d&ugrave;ng v&agrave; sản xuất kinh doanh với l&atilde;i suất chỉ từ 4,99%/năm. C&aacute;c sản phẩm phục vụ vay vốn ti&ecirc;u d&ugrave;ng gồm: cho vay mua &ocirc; t&ocirc;; cho vay mua nh&agrave; dự &aacute;n; cho vay mua, x&acirc;y dựng, sửa chữa nh&agrave; ở, nhận quyền sử dụng đất ở.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, với mục đ&iacute;ch cung cấp cho kh&aacute;ch h&agrave;ng g&oacute;i dịch vụ t&agrave;i ch&iacute;nh tốt nhất, khi tham gia chương tr&igrave;nh &ldquo;L&atilde;i h&egrave; giảm nhiệt&rdquo;, kh&aacute;ch h&agrave;ng sẽ được hưởng c&aacute;c ưu đ&atilde;i bảo hiểm:</p>\r\n<ul>\r\n<li>Ưu đ&atilde;i 25% ph&iacute; bảo hiểm VietinHome</li>\r\n<li>Ưu đ&atilde;i 25% ph&iacute; bảo hiểm VietinCare</li>\r\n<li>Tặng bảo hiểm &ldquo;Tai nạn l&aacute;i phụ xe v&agrave; người ngồi tr&ecirc;n xe&rdquo; cho kh&aacute;ch h&agrave;ng mua bảo hiểm vật chất xe &ocirc; t&ocirc; tr&ecirc;n 5 triệu đồng</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/Lai-he-giam-nhiet-2.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>C&ugrave;ng với chương tr&igrave;nh &ldquo;L&atilde;i h&egrave; giảm nhiệt&rdquo;, VietinBank cũng đưa ra g&oacute;i vay ưu đ&atilde;i &ldquo;Chung sức th&agrave;nh c&ocirc;ng&rdquo; với c&aacute;c mức l&atilde;i suất thả nổi, tương ứng với từng ph&acirc;n kh&uacute;c v&agrave; nhu cầu của kh&aacute;ch h&agrave;ng. Cụ thể, kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n/hộ gia đ&igrave;nh v&agrave; doanh nghiệp si&ecirc;u vi m&ocirc; được hưởng l&atilde;i suất ưu đ&atilde;i chỉ từ 6,5%/năm đối với khoản vay sản xuất kinh doanh v&agrave; từ 7,0%/năm đối với vay ti&ecirc;u d&ugrave;ng.</p>\r\n<p><strong>Th&ocirc;ng tin chi tiết qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ:</strong><br />C&aacute;c Chi nh&aacute;nh/Ph&ograve;ng giao dịch của VietinBank tr&ecirc;n to&agrave;n quốc, hoặc Tổng đ&agrave;i hỗ trợ kh&aacute;ch h&agrave;ng: 1900 55 8868; Email: contact@vietinbank.vn để được tư vấn v&agrave; tham gia chương tr&igrave;nh.</p>', '2018-03-21 10:35:30', '2018-03-21 21:35:30', 'images/news/1520591271_Lai-he-gi_635653855875815722_HasThumb_Thumb.jpg.png'),
(249, 3, 'Ưu đãi quà tặng khi mua bảo hiểm sức khỏe VBI Care', 'uu-dai-qua-tang-khi-mua-bao-hiem-suc-khoe-vbi-care', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 11/05 đến 31/12/2015, c&aacute;c kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care sẽ c&oacute; cơ hội nhận được h&agrave;ng trăm phần qu&agrave; chăm s&oacute;c sức khỏe hấp dẫn từ VBI.&nbsp;</span></p>', '<p>Từ ng&agrave;y 11/05 đến 31/12/2015, c&aacute;c kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care sẽ c&oacute; cơ hội nhận được h&agrave;ng trăm phần qu&agrave; chăm s&oacute;c sức khỏe hấp dẫn từ VBI.</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care với mức ph&iacute; từ 3 triệu đến 5 triệu đồng sẽ được tặng 01 nhiệt kế điện tử Omron.</li>\r\n<li>Kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care với mức ph&iacute; tr&ecirc;n 5 triệu đồng sẽ được tặng 01 m&aacute;y đo huyết &aacute;p Omron.</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/VBI-care-gift-.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Th&ocirc;ng tin về quyền lợi sản phẩm Bảo hiểm sức khỏe VBI Care, qu&yacute; kh&aacute;ch vui l&ograve;ng xem tại đ&acirc;y<br />Danh s&aacute;ch bệnh viện bảo l&atilde;nh, qu&yacute; kh&aacute;ch vui l&ograve;ng xem tại đ&acirc;y<br /> <br />Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ Chi nh&aacute;nh/ Ph&ograve;ng giao dịch VietinBank gần nhất hoặc tổng đ&agrave;i 1900 55 8868 để tham gia Bảo hiểm sức khỏe VBI Care ngay h&ocirc;m nay v&agrave; nhận qu&agrave; tặng hấp dẫn từ chương tr&igrave;nh.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:35:51', '2018-03-21 21:35:51', 'images/news/1520591347_VBI-care-_635756598044287587_HasThumb_Thumb.jpg.png'),
(250, 3, 'Ưu đãi đặc biệt với “Tận hưởng dịch vụ, khám phá năm châu”', 'uu-dai-dac-biet-voi-tan-huong-dich-vu-kham-pha-nam-chau', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 07/09/2015 đến hết ng&agrave;y 15/11/2015, VBI phối hợp c&ugrave;ng VietinBank triển khai chương tr&igrave;nh &ldquo;Tận hưởng dịch vụ, kh&aacute;m ph&aacute; năm ch&acirc;u&rdquo; d&agrave;nh những ưu đ&atilde;i th&uacute; vị cho kh&aacute;ch h&agrave;ng. Giao dịch tại VietinBank, Qu&yacute; kh&aacute;ch sẽ c&oacute; cơ hội quay số tr&uacute;ng thưởng c&aacute;c chuyến du lịch kh&aacute;m ph&aacute; Lục...</span></p>', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 07/09/2015 đến hết ng&agrave;y 15/11/2015, VBI phối hợp c&ugrave;ng VietinBank triển khai chương tr&igrave;nh &ldquo;Tận hưởng dịch vụ, kh&aacute;m ph&aacute; năm ch&acirc;u&rdquo; d&agrave;nh những ưu đ&atilde;i th&uacute; vị cho kh&aacute;ch h&agrave;ng. Giao dịch tại VietinBank, Qu&yacute; kh&aacute;ch sẽ c&oacute; cơ hội quay số tr&uacute;ng thưởng c&aacute;c chuyến du lịch kh&aacute;m ph&aacute; Lục địa đen, kh&aacute;m ph&aacute; xứ H&agrave;n thơ mộng khi sang thu c&ugrave;ng h&agrave;ng ngh&igrave;n giải thưởng bằng tiền mặt hấp dẫn. Chương tr&igrave;nh c&oacute; tổng gi&aacute; trị khuyến m&atilde;i l&ecirc;n đến gần 5 tỷ đồng.</span></p>\r\n<p><img src=\"../../../uploads/VBI2.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Đặc biệt, b&ecirc;n cạnh việc tham gia quay số tr&uacute;ng thưởng may mắn, Kh&aacute;ch h&agrave;ng của Vietinbank khi tham gia bảo hiểm (BH) của VBI sẽ được hưởng những ưu đ&atilde;i lớn về ph&iacute;:</p>\r\n<p>Ưu đ&atilde;i giảm 30% Ph&iacute; BH Du lịch quốc tế<br />Ưu đ&atilde;i giảm 25% Ph&iacute; BH Con người VietinCare .<br />Ưu đ&atilde;i giảm 25% Ph&iacute; BH vật chất nh&agrave; tư nh&acirc;n VietinHome.<br />Đối với KH tham gi&aacute; BH vật chất xe &ocirc; t&ocirc; ph&iacute; tr&ecirc;n 5 triệu đồng sẽ được tặng BH tai nạn l&aacute;i phụ xe v&agrave; người ngồi tr&ecirc;n xe ứng với chỗ ngồi tr&ecirc;n xe MTN 20tr/người/vụ. Sản phẩm gi&uacute;p qu&yacute; kh&aacute;ch bảo vệ bản th&acirc;n v&agrave; người bạn đồng h&agrave;nh tr&ecirc;n mỗi chặng đường.</p>\r\n<p><strong>Đối với KH tham gia BH sức khỏe VBIcare theo chương tr&igrave;nh VBIcare b&aacute;n lẻ:</strong> VBI gửi tới qu&yacute; kh&aacute;ch những phần qu&agrave; rất thiết thực trong việc chăm s&oacute;c sức khỏe gia đ&igrave;nh bạn mỗi ng&agrave;y:</p>\r\n<p>&middot; Kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care với mức ph&iacute; từ 3 triệu đến 5 triệu đồng sẽ được tặng 01 nhiệt kế điện tử Omron.</p>\r\n<p>&middot; Kh&aacute;ch h&agrave;ng mua bảo hiểm sức khỏe VBI Care với mức ph&iacute; tr&ecirc;n 5 triệu đồng sẽ được tặng 01 m&aacute;y đo huyết &aacute;p Omron.</p>\r\n<p>Th&ocirc;ng tin chi tiết về thể lệ chương tr&igrave;nh, Qu&yacute; kh&aacute;ch vui l&ograve;ng xem tại đ&acirc;y</p>\r\n<p>Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ Chi nh&aacute;nh/ Ph&ograve;ng giao dịch VietinBank gần nhất hoặc tổng đ&agrave;i 1900 55 8868 để tham gia c&aacute;c sản phẩm bảo hiểm của VBI ngay h&ocirc;m nay để nhận qu&agrave; tặng hấp dẫn từ chương tr&igrave;nh v&agrave; tham gia quay số tr&uacute;ng thưởng.</p>\r\n<p style=\"text-align: right;\"><em>VBI</em></p>', '2018-03-21 10:36:13', '2018-03-21 21:36:13', 'images/news/1520591466_VBI_635784427573110225_HasThumb_Thumb.jpg.png'),
(251, 3, 'Hiện thực giấc mơ du học cùng Bảo hiểm VietinBank', 'hien-thuc-giac-mo-du-hoc-cung-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Nhằm hiện thực ước mơ du học của c&aacute;c bạn trẻ v&agrave; cung cấp c&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh thiết yếu trong suốt qu&aacute; tr&igrave;nh học tập, VBI phối hợp với VietinBank triển khai g&oacute;i sản phẩm bảo hiểm du học với nhiều ưu đ&atilde;i vượt trội&nbsp;</span></p>', '<p><img src=\"../../../uploads/home-banner-2.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Ngo&agrave;i việc c&oacute; đủ kiến thức, ngoại ngữ&hellip;, một trong c&aacute;c bước quan trọng để bạn c&oacute; thể thực hiện giấc mơ du học đ&oacute; ch&iacute;nh l&agrave; một nền tảng t&agrave;i ch&iacute;nh vững chắc. Nhằm hiện thực ước mơ du học của c&aacute;c bạn trẻ v&agrave; cung cấp c&aacute;c c&ocirc;ng cụ t&agrave;i ch&iacute;nh thiết yếu trong suốt qu&aacute; tr&igrave;nh học tập, VBI phối hợp với VietinBank triển khai g&oacute;i sản phẩm bảo hiểm du học với nhiều ưu đ&atilde;i vượt trội, cụ thể:</p>\r\n<ul>\r\n<li>Giảm đến 30% ph&iacute; bảo hiểm du học - du lịch quốc tế</li>\r\n<li>Miễn ph&iacute; x&aacute;c nhận số dư t&agrave;i khoản tiền gửi chứng minh t&agrave;i ch&iacute;nh</li>\r\n<li>Giảm đến 50% ph&iacute; chuyển tiền ra nước ngo&agrave;i thanh to&aacute;n chi ph&iacute; du học</li>\r\n<li>Miễn ph&iacute; ph&aacute;t h&agrave;nh thẻ t&iacute;n dụng v&agrave; thẻ ghi nợ quốc tế VietinBank</li>\r\n<li>Miễn ph&iacute; SMS Banking/ VietinBank iPay</li>\r\n</ul>\r\n<p><br />Chi tiết điều kiện xem <a href=\"https://www.vietinbank.vn/web/export/sites/default/vn/news/15/09/images/du-hoc-the-le.pdf\">tại đ&acirc;y</a>.</p>\r\n<p>L&agrave; c&ocirc;ng ty bảo hiểm c&oacute; nhiều kinh nghiệm, tiềm lực t&agrave;i ch&iacute;nh, uy t&iacute;n trong việc triển khai c&aacute;c sản phẩm bảo hiểm du học, VBI tin tưởng rằng, với g&oacute;i ưu đ&atilde;i bảo hiểm du học tr&ecirc;n, ước mơ du học của nhiều bạn trẻ sớm trở th&agrave;nh hiện thực.</p>\r\n<p>Để c&oacute; được lộ tr&igrave;nh t&agrave;i ch&iacute;nh ph&ugrave; hợp nhất trong suốt thời gian học tập của m&igrave;nh, ngay khi được nh&agrave; trường chấp thuận c&aacute;c điều kiện, c&aacute;c bạn h&atilde;y li&ecirc;n hệ ngay tới Chi nh&aacute;nh v&agrave; Ph&ograve;ng Giao dịch VietinBank tr&ecirc;n to&agrave;n quốc hoặc Contact Center: 1900 558 868; Email: contact@vietinbank.vn để được tư vấn.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:36:41', '2018-03-21 21:36:41', 'images/news/1520591561_Bao-hiem-_635847278463334474_HasThumb_Thumb.jpg.png'),
(252, 3, 'Ưu đãi cho khách hàng tham gia bảo hiểm nhà dự án', 'uu-dai-cho-khach-hang-tham-gia-bao-hiem-nha-du-an', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) phối hợp với VietinBank triển khai g&oacute;i sản phẩm &ldquo;Cho vay mua nh&agrave; dự &aacute;n kết hợp bảo hiểm&rdquo;. Kh&aacute;ch h&agrave;ng khi tham gia g&oacute;i sản phẩm sẽ được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i về l&atilde;i suất. quyền lợi bảo hiểm v&agrave; ưu đ&atilde;i ph&iacute; bảo hiểm.&nbsp;</span></p>', '<p>Bảo hiểm VietinBank (VBI) phối hợp với VietinBank triển khai g&oacute;i sản phẩm &ldquo;Cho vay mua nh&agrave; dự &aacute;n kết hợp bảo hiểm&rdquo;. Kh&aacute;ch h&agrave;ng khi tham gia g&oacute;i sản phẩm sẽ được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i về l&atilde;i suất. quyền lợi bảo hiểm v&agrave; ưu đ&atilde;i ph&iacute; bảo hiểm.</p>\r\n<p>G&oacute;i sản phẩm ph&ugrave; hợp với kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n c&oacute; nhu cầu vay mua nh&agrave;/đ&atilde; vay mua nh&agrave; dự &aacute;n tại VietinBank phục vụ mục đ&iacute;ch ti&ecirc;u d&ugrave;ng đồng thời mua bảo hiểm ch&aacute;y nổ cho ch&iacute;nh ng&ocirc;i nh&agrave;.</p>\r\n<p><img src=\"../../../uploads/home-banner-2(1).jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Ưu đ&atilde;i cụ thể của g&oacute;i sản phẩm như sau:</p>\r\n<ul>\r\n<li><strong>Giảm đến 25% ph&iacute; bảo hiểm</strong>: Tất cả kh&aacute;ch h&agrave;ng tham gia g&oacute;i sản phẩm sẽ được giảm 20% ph&iacute; Bảo hiểm ch&aacute;y nổ bắt buộc. Trường hợp kh&aacute;ch h&agrave;ng mua cả 2 bảo hiểm sẽ được giảm 25% ph&iacute; Bảo hiểm ch&aacute;y nổ bắt buộc v&agrave; giảm đến 25% ph&iacute; Bảo hiểm con người VietinCare.</li>\r\n</ul>\r\n<p style=\"padding-left: 30px;\">Bảo hiểm ch&aacute;y nổ bắt buộc do VBI cung cấp l&agrave; bảo hiểm ch&aacute;y nổ với khung nh&agrave;, t&agrave;i sản trang thiết bị b&ecirc;n trong của nh&agrave; dự &aacute;n h&igrave;nh th&agrave;nh từ vốn vay đ&atilde; ho&agrave;n th&agrave;nh x&acirc;y dựng v&agrave; được chủ đầu tư b&agrave;n giao. VBI c&ograve;n tặng th&ecirc;m đến 50 triệu đồng kinh ph&iacute; hỗ trợ khắc phục tổn thất cho kh&aacute;ch h&agrave;ng.</p>\r\n<ul>\r\n<li><strong>Giảm th&ecirc;m đến 0,1% l&atilde;i suất cho vay:</strong> Tham gia g&oacute;i sản phẩm n&agrave;y, kh&aacute;ch h&agrave;ng được giảm tiếp 0,1% l&atilde;i suất cho vay so với l&atilde;i suất ưu đ&atilde;i/s&agrave;n l&atilde;i suất cho vay. Nhằm hỗ trợ tối đa cho kh&aacute;ch h&agrave;ng, VietinBank cho vay đến 80% nhu cầu vốn vay (&aacute;p dụng cho c&aacute;c khoản giải ng&acirc;n từ nay đến hết 31/12/2015).</li>\r\n<li><strong>Miễn ph&iacute; 3 th&aacute;ng dịch vụ SMS Banking</strong> cho kh&aacute;ch h&agrave;ng tham gia g&oacute;i sản phẩm v&agrave; đăng k&yacute; mới dịch vụ th&ocirc;ng b&aacute;o biến động số dư t&agrave;i khoản bằng tin nhắn (SMS Banking).</li>\r\n</ul>\r\n<p><br />H&atilde;y để ch&uacute;ng t&ocirc;i bảo vệ ng&ocirc;i nh&agrave; th&acirc;n y&ecirc;u của bạn th&ocirc;ng qua g&oacute;i sản phẩm &ldquo;Cho vay mua nh&agrave; dự &aacute;n kết hợp bảo hiểm&rdquo; của Bảo hiểm VietinBank.</p>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, vui l&ograve;ng xem <a href=\"http://www.vietinbank.vn/web/export/sites/default/vn/news/15/10/images/the-le-cho-vay-ket-hop-bh.pdf\">tại đ&acirc;y</a> hoặc li&ecirc;n hệ CN/PGD VietinBank to&agrave;n quốc, tổng đ&agrave;i hỗ trợ kh&aacute;ch h&agrave;ng: 1900 558 868; email: <a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a></p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:37:12', '2018-03-21 21:37:12', 'images/news/1520591702_home-bann_635847281140573376_HasThumb_Thumb.jpg.png'),
(253, 3, 'Ưu đãi cho khách hàng tham gia sản phẩm “Tiết kiệm Phúc Lộc”', 'uu-dai-cho-khach-hang-tham-gia-san-pham-tiet-kiem-phuc-loc', '<p><span style=\"font-family: Arial;\">Khi đăng k&yacute; sử dụng sản phẩm Tiết kiệm Ph&uacute;c Lộc, Qu&yacute; kh&aacute;ch sẽ đồng thời được tham gia dịch vụ bảo hiểm sức khỏe, được tư vấn c&aacute;c dịch vụ chăm s&oacute;c bản th&acirc;n v&agrave; cả gia đ&igrave;nh.&nbsp;</span></p>', '<p>Bước v&agrave;o độ tuổi trung ni&ecirc;n, khi bạn đ&atilde; c&oacute; c&ocirc;ng việc ổn định v&agrave; gia đ&igrave;nh của ri&ecirc;ng m&igrave;nh th&igrave; mong muốn chăm s&oacute;c, bảo vệ gia đ&igrave;nh ấy c&agrave;ng trở l&ecirc;n m&atilde;nh liệt hơn bao giờ hết. Đặc biệt, khi cuộc sống c&oacute; qu&aacute; nhiều rủi ro v&agrave; biến động như hiện nay, một giải ph&aacute;p chăm s&oacute;c sức khỏe to&agrave;n diện cũng như đảm bảo ổn định t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n sẽ lu&ocirc;n l&agrave; lựa chọn quan t&acirc;m h&agrave;ng đầu của tất cả c&aacute;c &ocirc;ng bố b&agrave; mẹ. Thấu hiểu nhu cầu ấy, VBI phối hợp với VietinBank ra mắt sản phẩm t&iacute;ch hợp với t&ecirc;n gọi &ldquo;Tiết kiệm Ph&uacute;c Lộc&rdquo;.</p>\r\n<p>Ưu việt hơn hẳn c&aacute;c sản phẩm tiết kiệm th&ocirc;ng thường, Tiết kiệm Ph&uacute;c Lộc mang lại cho kh&aacute;ch h&agrave;ng độ tuổi từ 45-64 sự an t&acirc;m trước những rủi ro bất trắc trong tương lai b&ecirc;n cạnh kế hoạch tiết kiệm t&iacute;ch lũy lu&ocirc;n được đảm bảo.</p>\r\n<p><img src=\"../../../uploads/tiet kiem phuc loc banner.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Tham gia gửi tiền tại VietinBank, Qu&yacute; kh&aacute;ch kh&ocirc;ng chỉ được hưởng l&atilde;i suất cố định hấp dẫn trong suốt kỳ hạn gửi 12 th&aacute;ng m&agrave; c&ograve;n được ưu ti&ecirc;n sử dụng cho mục đ&iacute;ch cầm cố vay vốn tại Ng&acirc;n h&agrave;ng c&ugrave;ng những qu&agrave; tặng hấp dẫn v&agrave;o ng&agrave;y sinh nhật. C&ocirc;ng sức lao động v&agrave; t&iacute;ch lũy của Qu&yacute; kh&aacute;ch sẽ được ch&uacute;ng t&ocirc;i lưu trữ v&agrave; đầu tư an to&agrave;n v&agrave; hiệu quả, mang đến cho bạn v&agrave; gia đ&igrave;nh sự an t&acirc;m tuyệt đối về t&agrave;i ch&iacute;nh.</p>\r\n<p>Khi đăng k&yacute; sử dụng sản phẩm Tiết kiệm Ph&uacute;c Lộc, Qu&yacute; kh&aacute;ch sẽ đồng thời được tham gia dịch vụ bảo hiểm sức khỏe, được tư vấn c&aacute;c dịch vụ chăm s&oacute;c bản th&acirc;n v&agrave; cả gia đ&igrave;nh.</p>\r\n<p>C&oacute; thể n&oacute;i, Tiết kiệm ph&uacute;c lộc l&agrave; sự kết hợp ho&agrave;n hảo giữa sản phẩm tiền gửi tiết kiệm th&ocirc;ng thường c&oacute; kỳ hạn 12 th&aacute;ng trả l&atilde;i sau v&agrave; sản phẩm bảo hiểm sức khỏe VBICare của Bảo hiểm VietinBank (VBI).</p>\r\n<p>Để được hướng dẫn chi tiết về thủ tục tham gia sản phẩm Tiết kiệm Ph&uacute;c Lộc v&agrave; tư vấn cụ thể về g&oacute;i bảo hiểm sức khỏe k&egrave;m theo, xin vui l&ograve;ng đến c&aacute;c điểm giao dịch gần nhất của VietinBank hoặc gọi 1900 55 8868.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:37:40', '2018-03-21 21:37:40', 'images/news/1520591775_tiet-kiem_635847285306122103_HasThumb_Thumb.jpg.png'),
(254, 3, 'Ưu đãi cho khách hàng tham gia gói bảo hiểm ô tô', 'uu-dai-cho-khach-hang-tham-gia-goi-bao-hiem-o-to', '<p><span style=\"font-family: Arial;\">H&atilde;y trải nghiệm g&oacute;i sản phẩm dịch vụ bảo hiểm &ocirc; t&ocirc; của Bảo hiểm VietinBank (VBI) - giải ph&aacute;p hiệu quả gi&uacute;p bạn hiện thực ước mơ của m&igrave;nh, loại bỏ những lo toan về mặt t&agrave;i ch&iacute;nh v&agrave; ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m bởi chiếc xe lu&ocirc;n được VBI bảo vệ.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Bạn mơ ước sở hữu chiếc &ocirc; t&ocirc; để cả gia đ&igrave;nh vi vu tr&ecirc;n những con đường du lịch v&agrave;o ng&agrave;y nghỉ; bạn muốn tr&aacute;nh xa c&aacute;i nắng n&oacute;ng, kh&oacute;i bụi của m&ugrave;a h&egrave; v&agrave; c&aacute;i gi&aacute; lạnh m&ugrave;a đ&ocirc;ng&hellip;H&atilde;y trải nghiệm g&oacute;i sản phẩm dịch vụ bảo hiểm &ocirc; t&ocirc; của Bảo hiểm VietinBank (VBI). Đ&acirc;y l&agrave; giải ph&aacute;p hiệu quả gi&uacute;p bạn hiện thực ước mơ của m&igrave;nh, loại bỏ những lo toan về mặt t&agrave;i ch&iacute;nh v&agrave; ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m bởi chiếc xe lu&ocirc;n được VBI bảo vệ.</span></p>\r\n<p><img src=\"../../../uploads/cho-vay-mua-o-to-ket-hop-BH--.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>G&oacute;i sản phẩm &aacute;p dụng cho kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n, hộ gia đ&igrave;nh v&agrave; doanh nghiệp si&ecirc;u vi m&ocirc; c&oacute; nhu cầu vay mua xe &ocirc; t&ocirc; v&agrave; vay chi ph&iacute; mua bảo hiểm vật chất VietinCar cho ch&iacute;nh chiếc xe trong suốt thời gian vay.</p>\r\n<p>Khi tham gia g&oacute;i sản phẩm, bạn kh&ocirc;ng những hiện thực h&oacute;a ước mơ sở hữu xe sang m&agrave; chiếc xe của bạn c&ograve;n được bảo vệ bởi sản phẩm bảo hiểm vật chất xe đi k&egrave;m của ch&uacute;ng t&ocirc;i. Kh&ocirc;ng những thế, ưu đ&atilde;i lớn nhất của g&oacute;i sản phẩm &ldquo;Cho vay mua &ocirc; t&ocirc; kết hợp bảo hiểm&rdquo; l&agrave; nhu cầu vốn của bạn được đ&aacute;p ứng tối đa 85% v&agrave; mức l&atilde;i suất vay giảm đến 0,2%/năm.</p>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, Qu&yacute; kh&aacute;ch vui l&ograve;ng xem thể lệ chi tiết tại đ&acirc;y hoặc li&ecirc;n hệ 1900 558 868.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-21 10:38:10', '2018-03-21 21:38:10', 'images/news/1520591855_cho-vay-m_635847283786092919_HasThumb_Thumb.jpg.png'),
(255, 3, 'Ưu đãi cho khách hàng tham gia Chương trình “Gửi tiền ngay - Quà liền tay”', 'uu-dai-cho-khach-hang-tham-gia-chuong-trinh-gui-tien-ngay-qua-lien-tay', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 6/10 đến hết 31/12/2015, khi kh&aacute;ch h&agrave;ng gửi tiền tiết kiệm tại VietinBank sẽ nhận ngay nhiều ưu đ&atilde;i hấp dẫn như: Giữ nguy&ecirc;n l&atilde;i suất được hưởng, tặng ngay 50.000 đồng v&agrave;o t&agrave;i khoản thanh to&aacute;n c&aacute; nh&acirc;n, tặng phiếu mua h&agrave;ng l&ecirc;n tới 500.000 đồng tại c&aacute;c hệ thống si&ecirc;u thị, tặng...&nbsp;</span></p>', '<p>Từ ng&agrave;y 6/10 đến hết 31/12/2015, khi kh&aacute;ch h&agrave;ng gửi tiền tiết kiệm tại VietinBank sẽ nhận ngay nhiều ưu đ&atilde;i hấp dẫn như: Giữ nguy&ecirc;n l&atilde;i suất được hưởng, tặng ngay 50.000 đồng v&agrave;o t&agrave;i khoản thanh to&aacute;n c&aacute; nh&acirc;n, tặng phiếu mua h&agrave;ng l&ecirc;n tới 500.000 đồng tại c&aacute;c hệ thống si&ecirc;u thị, tặng bảo hiểm sức khỏe trị gi&aacute; hơn 1.000.000 đồng.</p>\r\n<p>Ngo&agrave;i ra, kh&aacute;ch h&agrave;ng c&ograve;n được tặng ngay 50.000 đồng khi mở mới t&agrave;i khoản thanh to&aacute;n v&agrave; đăng k&yacute; dịch vụ SMS Banking hoặc VietinBank iPay. B&ecirc;n cạnh đ&oacute;, tặng 1 năm ph&iacute; bảo hiểm tr&aacute;ch nhiệm d&acirc;n sự xe m&aacute;y khi kh&aacute;ch h&agrave;ng gửi tiết kiệm ngoại tệ EUR trả l&atilde;i sau (từ 50.000 EUR trở l&ecirc;n).</p>\r\n<p><img src=\"../../../uploads/home-banner-2(2).jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Chương tr&igrave;nh khuyến m&atilde;i &aacute;p dụng với kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n l&agrave; người Việt Nam v&agrave; người nước ngo&agrave;i đang sinh sống v&agrave; hoạt động hợp ph&aacute;p tại Việt Nam.</p>\r\n<p>Đ&acirc;y l&agrave; chương tr&igrave;nh ưu đ&atilde;i hấp dẫn của VietinBank nhằm mang đến cho kh&aacute;ch h&agrave;ng nhiều gi&aacute; trị tiện &iacute;ch trong từng SPDV với phương ch&acirc;m &ldquo;N&acirc;ng gi&aacute; trị cuộc sống&rdquo;.</p>\r\n<p>Mọi th&ocirc;ng tin về chương tr&igrave;nh, vui l&ograve;ng li&ecirc;n hệ 1900 558 868 v&agrave; c&aacute;c Chi nh&aacute;nh/Ph&ograve;ng giao dịch của VietinBank tr&ecirc;n to&agrave;n quốc.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-21 10:38:36', '2018-03-21 21:38:36', 'images/news/1520591936_home-bann_635847285586127479_HasThumb_Thumb.jpg.png'),
(256, 3, 'Ưu đãi cho khách hàng tham gia Bảo hiểm tín dụng', 'uu-dai-cho-khach-hang-tham-gia-bao-hiem-tin-dung', '<p><span style=\"font-family: Arial;\">Với mong muốn chia sẻ g&aacute;nh nặng cho kh&aacute;ch h&agrave;ng, Bảo hiểm VietinBank (VBI) vừa cho ra mắt g&oacute;i sản phẩm &ldquo;Cho vay kết hợp bảo hiểm t&iacute;n dụng&rdquo;. Tham gia g&oacute;i sản phẩm, kh&aacute;ch h&agrave;ng kh&ocirc;ng những được hỗ trợ t&agrave;i ch&iacute;nh đến 90% nhu cầu vốn vay m&agrave; c&ograve;n được tặng ngay Bảo hiểm con người VietinCa...&nbsp;</span></p>', '<p>Với mong muốn chia sẻ g&aacute;nh nặng cho kh&aacute;ch h&agrave;ng, Bảo hiểm VietinBank (VBI) vừa cho ra mắt g&oacute;i sản phẩm &ldquo;Cho vay kết hợp bảo hiểm t&iacute;n dụng&rdquo;. Tham gia g&oacute;i sản phẩm, kh&aacute;ch h&agrave;ng kh&ocirc;ng những được hỗ trợ t&agrave;i ch&iacute;nh đến 90% nhu cầu vốn vay m&agrave; c&ograve;n được tặng ngay Bảo hiểm con người VietinCare của VBI.</p>\r\n<p>Kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n vay mua nh&agrave;/ đất, x&acirc;y sửa nh&agrave;, mua nh&agrave; dự &aacute;n, sản xuất kinh doanh nhỏ lẻ, mua &ocirc; t&ocirc;, ph&aacute;t triển n&ocirc;ng nghiệp n&ocirc;ng th&ocirc;n, du học đều c&oacute; cơ hội tham gia g&oacute;i sản phẩm &ldquo;Cho vay kết hợp bảo hiểm t&iacute;n dụng&rdquo; của VietinBank.<br />Theo đ&oacute;, kh&aacute;ch h&agrave;ng sẽ được tăng tỉ lệ cho vay/nhu cầu vay vốn từ 5% - 10% so với quy định hiện h&agrave;nh. Hạn mức cho vay theo g&oacute;i sản phẩm n&agrave;y c&oacute; thể đ&aacute;p ứng tới 90% nhu cầu vốn vay của kh&aacute;ch h&agrave;ng. B&ecirc;n cạnh ưu đ&atilde;i đặc biệt n&agrave;y, kh&aacute;ch h&agrave;ng c&ograve;n được tặng Bảo hiểm con người VietinCare.</p>\r\n<p>Với sản phẩm Bảo hiểm con người VietinCare của VBI, kh&aacute;ch h&agrave;ng sẽ được hưởng quyền lợi bảo hiểm chi trả nợ vay trong trường hợp tai nạn, ốm đau dẫn đến tử vong hoặc thương tật to&agrave;n bộ vĩnh viễn với số tiền chi trả l&ecirc;n tới 4 tỷ đồng/người/vụ. Khi xảy ra những rủi ro tr&ecirc;n, VBI sẽ ho&agrave;n th&agrave;nh nghĩa vụ trả nợ cho ng&acirc;n h&agrave;ng thay kh&aacute;ch h&agrave;ng. Ngo&agrave;i ra, kh&aacute;ch h&agrave;ng c&ograve;n được trợ cấp mất giảm thu nhập l&ecirc;n tới 50 triệu/năm trong qu&aacute; tr&igrave;nh điều trị thương tật vĩnh viễn do tai nạn v&agrave; chi trả thương tật bộ phận vĩnh viễn do tai nạn.</p>\r\n<p><img src=\"../../../uploads/home-banner-2(3).jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Đặc biệt, từ nay đến 15/1/2016, kh&aacute;ch h&agrave;ng tham gia g&oacute;i sản phẩm &ldquo;Cho vay kết hợp bảo hiểm t&iacute;n dụng&rdquo; c&ograve;n được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i l&atilde;i suất theo Chương tr&igrave;nh &ldquo;Gắn kết l&acirc;u - Ưu đ&atilde;i lớn&rdquo; v&agrave; Chương tr&igrave;nh &ldquo;10.000 tỷ ưu đ&atilde;i l&atilde;i vay&rdquo;.</p>\r\n<p>G&oacute;i sản phẩm &ldquo;Cho vay kết hợp bảo hiểm t&iacute;n dụng&rdquo; l&agrave; giải ph&aacute;p t&agrave;i ch&iacute;nh ho&agrave;n hảo gi&uacute;p bạn tiếp tục chăm s&oacute;c những người th&acirc;n y&ecirc;u bằng việc bảo to&agrave;n t&agrave;i sản thế chấp, kh&ocirc;ng phải lo khoản nợ ng&acirc;n h&agrave;ng v&agrave; đảm bảo cuộc sống y&ecirc;n b&igrave;nh cho gia đ&igrave;nh th&acirc;n y&ecirc;u của bạn.</p>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ ph&ograve;ng giao dịch/chi nh&aacute;nh VietinBank gần nhất hoặc gọi số Contact Center: 1900 558 868; email: contact@vietinbank.vn.</p>\r\n<p>Thể lệ chi tiết của chương tr&igrave;nh ưu đ&atilde;i xem tại đ&acirc;y.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:09:04', '2018-03-22 13:09:04', 'images/news/1520592000_home-bann_635847287660386054_HasThumb_Thumb.jpg.png'),
(257, 1, 'Nhận ngay bảo hiểm sức khỏe VBI Care khi mua Nệm Kim Cương', 'nhan-ngay-bao-hiem-suc-khoe-vbi-care-khi-mua-nem-kim-cuong', '<p><span style=\"font-family: Arial;\">Từ nay tới 01/04/2016, Bảo hiểm VietinBank (VBI) phối hợp với Nệm Kim Cương triển khai Chương tr&igrave;nh khuyến mại tri &acirc;n kh&aacute;ch h&agrave;ng&nbsp;</span></p>', '<p><img src=\"../../../uploads/Banner ngang.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Từ nay tới 01/04/2016, Bảo hiểm VietinBank (VBI) phối hợp với Nệm Kim Cương triển khai Chương tr&igrave;nh khuyến mại tri &acirc;n kh&aacute;ch h&agrave;ng với nội dung sau:</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng được tặng ngay Bảo hiểm sức khỏe VBI Care khi mua c&aacute;c sản phẩm của Nệm Kim Cương</li>\r\n</ul>\r\n<p style=\"padding-left: 30px;\">- Kh&aacute;ch h&agrave;ng mua Nệm Kim Cương tr&ecirc;n 5 triệu được tặng qu&agrave; l&agrave; bảo hiểm sức khỏe VBI Care với quyền lợi l&ecirc;n tới 80 triệu đồng<br />- Kh&aacute;ch h&agrave;ng mua Nệm Kim Cương tr&ecirc;n 10 triệu được tặng qu&agrave; l&agrave; bảo hiểm sức khỏe VBI Care với quyền lợi l&ecirc;n tới 130 triệu đồng<br />- Kh&aacute;ch h&agrave;ng mua Nệm Kim Cương tr&ecirc;n 15 triệu được tặng qu&agrave; l&agrave; bảo hiểm sức khỏe VBI Care với quyền lợi l&ecirc;n tới 250 triệu đồng</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng Vietinbank sẽ được giảm 25% gi&aacute; khi mua c&aacute;c sản phẩm của Nệm Kim Cương qua đường d&acirc;y n&oacute;ng 1900 5555 10.</li>\r\n</ul>\r\n<p style=\"padding-left: 30px;\">Chương tr&igrave;nh được &aacute;p dụng rộng r&atilde;i cho c&aacute;c chủ thẻ Master, Visa, JCB v&agrave; ATM của VietinBank. C&aacute;c chủ thẻ kh&ocirc;ng phải chi trả th&ecirc;m bất cứ chi ph&iacute; n&agrave;o kh&aacute;c ngo&agrave;i gi&aacute; trị đơn h&agrave;ng, kể cả ph&iacute; xử l&yacute; giao dịch v&agrave; chuyển tiền. Ngo&agrave;i ra, Nệm Kim Cương cam kết giao h&agrave;ng tận nơi cho chủ thẻ, trong thời hạn 7 ng&agrave;y l&agrave;m việc.</p>\r\n<p>Th&ocirc;ng tin về quyền lợi sản phẩm Bảo hiểm sức khỏe VBI Care, qu&yacute; kh&aacute;ch vui l&ograve;ng xem tại đ&acirc;y</p>\r\n<p>Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ tổng đ&agrave;i 1900 5555 10 để tham gia v&agrave; nhận ngay c&aacute;c qu&agrave; tặng hấp dẫn từ chương tr&igrave;nh.</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>C&ocirc;ng ty TNHH TM-SX Nệm Kim Cương l&agrave; đơn vị chuy&ecirc;n sản xuất v&agrave; cung cấp c&aacute;c loại nệm cao su, gối cao su, nệm l&ograve; xo... Sản phẩm của C&ocirc;ng ty được sản xuất với 100% cao su thi&ecirc;n nhi&ecirc;n, tr&ecirc;n d&acirc;y chuyền hiện đại của CHLB Đức, được kiểm so&aacute;t chặt chẽ bởi hệ thống quản l&yacute; chất lượng đạt ti&ecirc;u chuẩn ISO 9001:2008.<br /> Website:&nbsp;<a href=\"http://www.nemkimcuong.vn/\">www.nemkimcuong.vn</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:09:39', '2018-03-22 13:09:39', 'images/news/1520592122_Banner-we_635899421410003773_HasThumb_Thumb.jpg.png'),
(258, 3, 'Nhận ngay bảo hiểm sức khỏe VBI Care khi mua Nệm Kim Cương', 'nhan-ngay-bao-hiem-suc-khoe-vbi-care-khi-mua-nem-kim-cuong-1', '<p><span style=\"font-family: Arial;\">Từ nay tới 01/04/2016, Bảo hiểm VietinBank (VBI) phối hợp với Nệm Kim Cương triển khai Chương tr&igrave;nh khuyến mại tri &acirc;n kh&aacute;ch h&agrave;ng&nbsp;</span></p>', '<p><img src=\"../../../uploads/Banner ngang (1).jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Từ nay tới 01/04/2016, Bảo hiểm VietinBank (VBI) phối hợp với Nệm Kim Cương triển khai Chương tr&igrave;nh khuyến mại tri &acirc;n kh&aacute;ch h&agrave;ng với nội dung sau:</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng được tặng ngay Bảo hiểm sức khỏe VBI Care khi mua c&aacute;c sản phẩm của Nệm Kim Cương</li>\r\n</ul>\r\n<p style=\"padding-left: 30px;\">- Kh&aacute;ch h&agrave;ng mua Nệm Kim Cương tr&ecirc;n 5 triệu được tặng qu&agrave; l&agrave; bảo hiểm sức khỏe VBI Care với quyền lợi l&ecirc;n tới 80 triệu đồng<br />- Kh&aacute;ch h&agrave;ng mua Nệm Kim Cương tr&ecirc;n 10 triệu được tặng qu&agrave; l&agrave; bảo hiểm sức khỏe VBI Care với quyền lợi l&ecirc;n tới 130 triệu đồng<br />- Kh&aacute;ch h&agrave;ng mua Nệm Kim Cương tr&ecirc;n 15 triệu được tặng qu&agrave; l&agrave; bảo hiểm sức khỏe VBI Care với quyền lợi l&ecirc;n tới 250 triệu đồng</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng Vietinbank sẽ được giảm 25% gi&aacute; khi mua c&aacute;c sản phẩm của Nệm Kim Cương qua đường d&acirc;y n&oacute;ng 1900 5555 10.</li>\r\n</ul>\r\n<p style=\"padding-left: 30px;\">Chương tr&igrave;nh được &aacute;p dụng rộng r&atilde;i cho c&aacute;c chủ thẻ Master, Visa, JCB v&agrave; ATM của VietinBank. C&aacute;c chủ thẻ kh&ocirc;ng phải chi trả th&ecirc;m bất cứ chi ph&iacute; n&agrave;o kh&aacute;c ngo&agrave;i gi&aacute; trị đơn h&agrave;ng, kể cả ph&iacute; xử l&yacute; giao dịch v&agrave; chuyển tiền. Ngo&agrave;i ra, Nệm Kim Cương cam kết giao h&agrave;ng tận nơi cho chủ thẻ, trong thời hạn 7 ng&agrave;y l&agrave;m việc.</p>\r\n<p>Th&ocirc;ng tin về quyền lợi sản phẩm Bảo hiểm sức khỏe VBI Care, qu&yacute; kh&aacute;ch vui l&ograve;ng xem tại đ&acirc;y</p>\r\n<p>Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ tổng đ&agrave;i 1900 5555 10 để tham gia v&agrave; nhận ngay c&aacute;c qu&agrave; tặng hấp dẫn từ chương tr&igrave;nh.</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>C&ocirc;ng ty TNHH TM-SX Nệm Kim Cương l&agrave; đơn vị chuy&ecirc;n sản xuất v&agrave; cung cấp c&aacute;c loại nệm cao su, gối cao su, nệm l&ograve; xo... Sản phẩm của C&ocirc;ng ty được sản xuất với 100% cao su thi&ecirc;n nhi&ecirc;n, tr&ecirc;n d&acirc;y chuyền hiện đại của CHLB Đức, được kiểm so&aacute;t chặt chẽ bởi hệ thống quản l&yacute; chất lượng đạt ti&ecirc;u chuẩn ISO 9001:2008.<br />Website: www.nemkimcuong.vn</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:10:07', '2018-03-22 13:10:07', 'images/news/1520821211_Banner-we_635899421410003773_HasThumb_Thumb.jpg.png'),
(259, 3, 'Đón năm mới với cơn mưa quà tặng từ Bảo hiểm VietinBank', 'don-nam-moi-voi-con-mua-qua-tang-tu-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Nh&acirc;n dịp tết Dương lịch 2016, từ nay đến 5/2/2016, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh khuyến m&atilde;i đặc biệt cho kh&aacute;ch h&agrave;ng với nhiều qu&agrave; tặng gi&aacute; trị.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Nh&acirc;n dịp tết Dương lịch 2016, từ nay đến 5/2/2016, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh khuyến m&atilde;i đặc biệt cho kh&aacute;ch h&agrave;ng với nhiều qu&agrave; tặng gi&aacute; trị.</span></p>\r\n<p><img src=\"../../../uploads/222222.jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>VBI phối hợp với VietinBank tặng h&agrave;ng ngh&igrave;n giải tiền mặt trị gi&aacute; 100.000 đồng cho c&aacute;c kh&aacute;ch h&agrave;ng đầu ti&ecirc;n k&yacute; hợp đồng bảo hiểm VietinCar/VietinHome/VBI Care tại c&aacute;c chi nh&aacute;nh của VietinBank.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, Bảo hiểm VietinBank (VBI) ưu đ&atilde;i <strong>giảm 25% ph&iacute; bảo hiểm vật chất VietinHome.</strong></p>\r\n<p>Kh&aacute;ch h&agrave;ng tham gia bảo hiểm vật chất xe &ocirc; t&ocirc; VietinCar với mức ph&iacute; từ 5 triệu đồng trở l&ecirc;n sẽ được <strong>tặng bảo hiểm &ldquo;Tai nạn l&aacute;i phụ xe v&agrave; người ngồi tr&ecirc;n xe&rdquo; với trị gi&aacute; tối đa 20 triệu đồng.</strong></p>\r\n<p>Qu&yacute; kh&aacute;ch h&atilde;y tham gia để tận hưởng h&agrave;ng ngh&igrave;n ưu đ&atilde;i hấp dẫn v&agrave; đ&oacute;n Xu&acirc;n c&ugrave;ng Bảo hiểm VietinBank (VBI).</p>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ ph&ograve;ng giao dịch/chi nh&aacute;nh VietinBank gần nhất, gọi số Contact Center: <strong>1900 558 868</strong>; email: <em>contact@vietinbank.vn</em> để được tư vấn.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:10:42', '2018-03-22 13:10:42', 'images/news/1520821314__635865654777282418_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(260, 3, 'Ưu đãi bảo hiểm “Cùng VietinBank đến thiên đường nhiệt đới Hawaii”', 'uu-dai-bao-hiem-cung-vietinbank-den-thien-duong-nhiet-doi-hawaii', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 14/4-12/7/2016, VBI phối hợp với VietinBank triển khai chương tr&igrave;nh &ldquo;C&ugrave;ng VietinBank đến thi&ecirc;n đường nhiệt đới Hawaii&rdquo;.&nbsp;</span></p>', '<p><img src=\"../../../uploads/Poster_Hawaii_fix_CV-3(2).jpg\" alt=\"\" width=\"964\" height=\"303\" /></p>\r\n<p>Từ ng&agrave;y 14/4-12/7/2016, VBI phối hợp với VietinBank triển khai chương tr&igrave;nh &ldquo;C&ugrave;ng VietinBank đến thi&ecirc;n đường nhiệt đới Hawaii&rdquo;.</p>\r\n<p>Nội dung ưu đ&atilde;i:</p>\r\n<ul>\r\n<li>Ưu đ&atilde;i giảm 30% ph&iacute; khi tham gia sản phẩm bảo hiểm Du lịch quốc tế</li>\r\n<li>Ưu đ&atilde;i khi tham gia sản phẩm bảo hiểm Sức khỏe VBI Care</li>\r\n</ul>\r\n<p>Khi mua Bảo hiểm sức khoẻ VBI Care, c&aacute;c kh&aacute;ch h&agrave;ng tại H&agrave; Nội, Hồ Ch&iacute; Minh, B&igrave;nh Phước, B&igrave;nh Dương, T&acirc;y Ninh, Đồng Nai v&agrave; B&agrave; Rịa Vũng T&agrave;u sẽ nhận được h&agrave;ng ngh&igrave;n qu&agrave; tặng gi&aacute; trị từ VBI.</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td style=\"text-align: center;\"><strong>Mức ph&iacute; bảo hiểm VBI Care</strong></td>\r\n<td style=\"text-align: center;\"><strong>Qu&agrave; tặng</strong></td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\">Từ 3 - 5 triệu đồng ph&iacute; bảo hiểm</td>\r\n<td style=\"text-align: center;\">Tặng 01 <strong>nhiệt kế điện tử</strong> trị gi&aacute; 200.000 VND</td>\r\n</tr>\r\n<tr>\r\n<td style=\"text-align: center;\">Tr&ecirc;n 5 triệu đồng ph&iacute; bảo hiểm</td>\r\n<td style=\"text-align: center;\">Tặng 01 <strong>m&aacute;y đo huyết &aacute;p</strong> trị gi&aacute; 1.000.000 VND</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết, kh&aacute;ch h&agrave;ng c&oacute; thể tới ph&ograve;ng giao dịch/chi nh&aacute;nh VietinBank gần nhất, hoặc vui l&ograve;ng li&ecirc;n hệ Contact Center: <strong>1900 558 868</strong>, email: <em><a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</em></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:11:21', '2018-03-22 13:11:21', 'images/news/1520821499_Poster_Ha_635969408674298664_HasThumb_Thumb.jpg.png'),
(261, 3, 'Ưu đãi dành riêng cho khách hàng ưu tiên VietinBank', 'uu-dai-danh-rieng-cho-khach-hang-uu-tien-vietinbank', '<p><span style=\"font-family: Arial;\">Từ nay đến ng&agrave;y 31/12/2016, Bảo hiểm VietinBank (VBI) triển khai g&oacute;i bảo hiểm ưu đ&atilde;i An Thịnh Ph&aacute;t Lộc d&agrave;nh cho kh&aacute;ch h&agrave;ng ưu ti&ecirc;n. Với chương tr&igrave;nh n&agrave;y, kh&aacute;ch h&agrave;ng sẽ được trải nghiệm những đặc quyền ưu đ&atilde;i vượt trội về dịch vụ, ch&iacute;nh s&aacute;ch v&agrave; nhiều tiện &iacute;ch trong c&aacute;c g&oacute;i bảo hiểm của VBI.</span></p>', '<p><span style=\"font-family: Arial;\">Từ nay đến ng&agrave;y 31/12/2016, Bảo hiểm VietinBank (VBI) triển khai g&oacute;i bảo hiểm ưu đ&atilde;i An Thịnh Ph&aacute;t Lộc d&agrave;nh cho kh&aacute;ch h&agrave;ng ưu ti&ecirc;n. Với chương tr&igrave;nh n&agrave;y, kh&aacute;ch h&agrave;ng sẽ được trải nghiệm những đặc quyền ưu đ&atilde;i vượt trội về dịch vụ, ch&iacute;nh s&aacute;ch v&agrave; nhiều tiện &iacute;ch trong c&aacute;c g&oacute;i bảo hiểm của VBI.</span></p>\r\n<p><img src=\"../../../uploads/960x303px.jpg\" alt=\"\" width=\"960\" height=\"303\" /></p>\r\n<p>Cụ thể, c&aacute;c kh&aacute;ch h&agrave;ng ưu ti&ecirc;n tham gia Bảo hiểm sức khoẻ (VBI Care) sẽ được nhận ngay qu&agrave; tặng hấp dẫn (nhiệt kế điện tử, m&aacute;y đo huyết &aacute;p) v&agrave; được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i ph&iacute; l&ecirc;n tới 10%. B&ecirc;n cạnh đ&oacute;, qu&yacute; kh&aacute;ch c&ograve;n được tặng th&ecirc;m voucher kh&aacute;m chữa răng trị gi&aacute; 3 triệu đồng. Khi tham gia Bảo hiểm vật chất &ocirc; t&ocirc; (VietinCar) v&agrave; Bảo hiểm nh&agrave; tư nh&acirc;n (VietinHome), kh&aacute;ch h&agrave;ng sẽ nhận được ưu đ&atilde;i ph&iacute; l&ecirc;n tới 25%. Đặc biệt, với sản phẩm Bảo hiểm du lịch quốc tế, VBI d&agrave;nh mức ưu đ&atilde;i ph&iacute; l&ecirc;n tới 30% cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>Ngo&agrave;i c&aacute;c ưu đ&atilde;i được hưởng theo quy định của từng sản phẩm, kh&aacute;ch h&agrave;ng ưu ti&ecirc;n của VietinBank c&ograve;n được tặng th&ecirc;m voucher giảm 50% dịch vụ tẩy trắng răng khi tham gia c&aacute;c sản phẩm bảo hiểm của VBI.</p>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin, kh&aacute;ch h&agrave;ng vui l&ograve;ng li&ecirc;n hệ chi nh&aacute;nh/ph&ograve;ng giao dịch VietinBank to&agrave;n quốc, <strong>Hotline: 1900 558 868</strong>; Email: <em><a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</em></p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:11:54', '2018-03-22 13:11:54', 'images/news/1520821867_pic-New-i_636058986114557538_HasThumb_Thumb.jpg.png'),
(262, 1, 'Ưu đãi bảo hiểm “Vui xuân sang - nhận lộc vàng”', 'uu-dai-bao-hiem-vui-xuan-sang-nhan-loc-vang', '<p><span style=\"font-family: Arial;\">Từ nay tới 25/4/2017, Bảo hiểm VietinBank (VBI) phối hợp với VietinBank triển khai Chương tr&igrave;nh &ldquo;Vui xu&acirc;n sang - nhận lộc v&agrave;ng&rdquo; cho kh&aacute;ch h&agrave;ng mua bảo hiểm v&agrave; kh&aacute;ch h&agrave;ng gửi tiết kiệm tại VietinBank. Tổng giải thưởng của Chương tr&igrave;nh l&ecirc;n đến gần 8 tỷ đồng.&nbsp;</span></p>', '<p>&nbsp;Từ nay tới 25/4/2017, Bảo hiểm VietinBank (VBI) phối hợp với VietinBank triển khai Chương tr&igrave;nh &ldquo;Vui xu&acirc;n sang - nhận lộc v&agrave;ng&rdquo; cho kh&aacute;ch h&agrave;ng mua bảo hiểm v&agrave; kh&aacute;ch h&agrave;ng gửi tiết kiệm tại VietinBank. Tổng giải thưởng của Chương tr&igrave;nh l&ecirc;n đến gần 8 tỷ đồng.</p>\r\n<p>Khi tham gia Bảo hiểm người vay vốn v&agrave; Bảo hiểm Du lịch quốc tế, kh&aacute;ch h&agrave;ng sẽ được giảm 25% ph&iacute; bảo hiểm. B&ecirc;n cạnh đ&oacute;, Bảo hiểm VietinBank (VBI) c&ograve;n tặng c&aacute;p sạc điện thoại trị gi&aacute; 100.000 đồng cho kh&aacute;ch h&agrave;ng mua Bảo hiểm sức khỏe VBI Care, Bảo hiểm người vay vốn v&agrave; Bảo hiểm du lịch quốc tế với mức ph&iacute; từ 2 triệu đồng/hợp đồng bảo hiểm.</p>\r\n<p>Đặc biệt, chương tr&igrave;nh &ldquo;Vui xu&acirc;n sang - nhận lộc v&agrave;ng&rdquo; mang đến lộc t&agrave;i, may mắn cho kh&aacute;ch h&agrave;ng ngay từ những ng&agrave;y đầu năm mới 2017. Theo đ&oacute;, kh&aacute;ch h&agrave;ng gửi tiết kiệm bằng VND đ&aacute;p ứng điều kiện của Chương tr&igrave;nh qua c&aacute;c k&ecirc;nh gửi tiền của VietinBank: Tại quầy, qua Ng&acirc;n h&agrave;ng điện tử (Internet Banking v&agrave; Mobile Banking, hệ thống m&aacute;y ATM/Kiosk của VietinBank) tr&ecirc;n to&agrave;n quốc đều c&oacute; cơ hội nhận được nhiều giải thưởng, qu&agrave; tặng gi&aacute; trị.</p>\r\n<p>Từ nay tới 16/2/2017, VietinBank l&igrave; x&igrave; tới 200.000 đồng cho tất cả c&aacute;c kh&aacute;ch h&agrave;ng gửi tiền từ 200 triệu đồng trở l&ecirc;n (kh&ocirc;ng kể thời gian nghỉ Tết Nguy&ecirc;n đ&aacute;n từ 26/1/2017 đến 1/2/2017).</p>\r\n<p>Đặc biệt, tham dự Chương tr&igrave;nh, mỗi kh&aacute;ch h&agrave;ng được nhận 1 hoặc nhiều m&atilde; dự thưởng tham dự quay số may mắn. Trong thời gian từ 8/2/2017 - 25/4/2017, cứ 2 tuần 1 đợt, VietinBank sẽ tổ chức quay thưởng giải Tuần để chọn ra 10 kh&aacute;ch h&agrave;ng may mắn tr&uacute;ng thưởng. Giải thưởng Tuần l&agrave; sổ tiết kiệm trị gi&aacute; 10 triệu đồng/giải.</p>\r\n<p><img src=\"../../../uploads/BANNER-vietinbankINSURANCE2.jpg\" alt=\"\" width=\"964\" height=\"526\" /></p>\r\n<p>Cuối Chương tr&igrave;nh, VietinBank quay số t&igrave;m ra chủ nh&acirc;n c&aacute;c giải cao nhất: 1 giải Đặc biệt l&agrave; 10 lượng v&agrave;ng SJC; 2 giải Nhất mỗi giải l&agrave; 1 lượng v&agrave;ng SJC; 5 giải Nh&igrave; mỗi giải l&agrave; 5 chỉ v&agrave;ng; 10 giải Ba mỗi giải l&agrave; 2 chỉ v&agrave;ng; 100 giải Khuyến kh&iacute;ch mỗi giải l&agrave; 1 chỉ v&agrave;ng.</p>\r\n<p>H&atilde;y tham gia &ldquo;Vui xu&acirc;n sang - nhận lộc v&agrave;ng&rdquo; c&ugrave;ng Bảo hiểm VietinBank để đ&oacute;n thần T&agrave;i về nh&agrave;!</p>\r\n<p>Thể lệ chi tiết của Chương tr&igrave;nh, vui l&ograve;ng xem <a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/The-le-ch_636203547258984151.pdf\">tại đ&acirc;y</a>.</p>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ chi nh&aacute;nh/ph&ograve;ng giao dịch VietinBank to&agrave;n quốc,<br />số Contact Center: <strong>1900 558 868</strong>; email: <a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:12:31', '2018-03-22 13:12:31', 'images/news/1520821953_pic-New-i_636203548257909580_HasThumb_Thumb.jpg.png'),
(263, 3, 'Chương trình tri ân khách hàng đón xuân 2017', 'chuong-trinh-tri-an-khach-hang-don-xuan-2017', '<p><span style=\"font-family: Arial;\">Với mong muốn g&oacute;p phần chăm s&oacute;c v&agrave; bảo vệ sức khỏe của kh&aacute;ch h&agrave;ng, từ 02/02/2017 tới 31/12/2017, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh tặng qu&agrave; tri &acirc;n kh&aacute;ch h&agrave;ng đ&atilde; tham gia Bảo hiểm sức khỏe VBICare tại H&agrave; Nội.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Với mong muốn g&oacute;p phần chăm s&oacute;c v&agrave; bảo vệ sức khỏe của kh&aacute;ch h&agrave;ng, từ 02/02/2017 tới 31/12/2017, Bảo hiểm VietinBank (VBI) triển khai chương tr&igrave;nh tặng qu&agrave; tri &acirc;n kh&aacute;ch h&agrave;ng đ&atilde; tham gia Bảo hiểm sức khỏe VBICare tại H&agrave; Nội.<br /> <br />Theo đ&oacute;, tất c&aacute;c c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tham gia Bảo hiểm sức khỏe VBICare của VBI sẽ được giảm 10% cho tất cả c&aacute;c dịch vụ chỉnh nha tại Ph&ograve;ng kh&aacute;m nha khoa Tiến sĩ L&ecirc; Hưng v&agrave; Cộng sự, với gi&aacute; trị l&ecirc;n đến 10.000.000 đồng.</span></p>\r\n<p><img src=\"../../../uploads/964x526-05.jpg\" alt=\"\" width=\"1366\" height=\"746\" /></p>\r\n<p><span style=\"font-family: Arial;\">B&ecirc;n cạnh cạnh đ&oacute;, Voucher qu&agrave; tặng &aacute;p dụng cho c&aacute;c dịch vụ chăm s&oacute;c nha khoa thẩm mỹ sẽ được gửi tới email của Qu&yacute; kh&aacute;ch, chi tiết như sau:</span></p>\r\n<p style=\"padding-left: 30px;\"><span style=\"font-family: Arial;\">&middot; Kh&aacute;ch h&agrave;ng mua Bảo hiểm tr&ecirc;n 15 triệu đồng sẽ được nhận Voucher qu&agrave; tặng trị gi&aacute; 3.000.000 đồng.<br />&middot; Kh&aacute;ch h&agrave;ng mua Bảo hiểm tr&ecirc;n 5 triệu đồng sẽ được nhận Voucher qu&agrave; tặng trị gi&aacute; 1.000.000 đồng.<br />&middot; Kh&aacute;ch h&agrave;ng mua Bảo hiểm tr&ecirc;n 2 triệu đồng sẽ được nhận Voucher qu&agrave; tặng trị gi&aacute; 500.000 đồng.<br />&middot; Kh&aacute;ch h&agrave;ng mua Bảo hiểm đến 2 triệu đồng sẽ được nhận Voucher qu&agrave; tặng trị gi&aacute; 300.000 đồng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Để biết th&ecirc;m th&ocirc;ng tin, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ đường d&acirc;y n&oacute;ng: <strong>094.1083.888</strong></span></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>Ph&ograve;ng kh&aacute;m nha khoa Tiến sĩ L&ecirc; Hưng v&agrave; Cộng sự được th&agrave;nh lập tại H&agrave; Nội từ năm 1996. Ph&ograve;ng kh&aacute;m c&oacute; địa chỉ tại Số 3, ng&otilde; 45 H&agrave;o Nam, Q. Đống Đa, TP. H&agrave; Nội.<br /> <br />Điểm nổi trội của ph&ograve;ng kh&aacute;m l&agrave; đội ngũ b&aacute;c sĩ, nh&acirc;n viện tận t&igrave;nh, chuy&ecirc;n m&ocirc;n chuy&ecirc;n s&acirc;u v&agrave; chuy&ecirc;n nghiệp. Phụ tr&aacute;ch chuy&ecirc;n m&ocirc;n của ph&ograve;ng kh&aacute;m l&agrave; thầy thuốc ưu t&uacute;, tiến sĩ, b&aacute;c sĩ L&ecirc; Hưng, với hơn 25 năm kinh nghiệm chuy&ecirc;n s&acirc;u chuy&ecirc;n ng&agrave;nh răng h&agrave;m mặt c&ugrave;ng c&aacute;c cộng sự l&agrave; c&aacute;c thạc sĩ, b&aacute;c sĩ nội tr&uacute;, b&aacute;c sĩ chuy&ecirc;n ng&agrave;nh răng h&agrave;m mặt sẽ mang tới cho Qu&yacute; kh&aacute;ch sự dịch vụ chăm s&oacute;c răng miệng tốt nhất.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-22 02:13:02', '2018-03-22 13:13:02', 'images/news/1520822069_964x526-0_636255973884913257_HasThumb_Thumb.jpg.png'),
(264, 3, 'Ưu đãi đặc quyền dành tặng khách hàng VBI', 'uu-dai-dac-quyen-danh-tang-khach-hang-vbi', '<p><span style=\"font-family: Arial;\">Từ nay đến 30/04/2017, tất c&aacute;c c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tham gia Bảo hiểm sức khỏe VBICare tr&ecirc;n 10 triệu đồng hoặc Bảo hiểm &ocirc; t&ocirc; tr&ecirc;n 15 triệu đồng tại H&agrave; Nội, Đ&agrave; Nẵng v&agrave; TP. Hồ Ch&iacute; Minh trong năm 2016 sẽ được nhận voucher trải nghiệm miễn ph&iacute; 2 lần dịch vụ chăm s&oacute;c da mặt bằng c&ocirc;ng...&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Với mong muốn tạo n&ecirc;n một dịch vụ xứng tầm, mang lại cho kh&aacute;ch h&agrave;ng những gi&aacute; trị gia tăng để tận hưởng cuộc sống một c&aacute;ch to&agrave;n diện nhất, Bảo hiểm VietinBank (VBI) kết hợp c&ugrave;ng Tập đo&agrave;n chăm s&oacute;c da từ Singapore &ndash; PPP Laser Clinic mang đến cho kh&aacute;ch h&agrave;ng VBI chương tr&igrave;nh ưu đ&atilde;i cực kỳ hấp dẫn khi sử dụng dịch vụ chăm s&oacute;c da mặt tại c&aacute;c chi nh&aacute;nh PPP Laser Clinic tr&ecirc;n to&agrave;n quốc.<br /> <br />Theo đ&oacute;, từ nay đến 30/04/2017, tất c&aacute;c c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tham gia Bảo hiểm sức khỏe VBICare tr&ecirc;n 10 triệu đồng hoặc Bảo hiểm &ocirc; t&ocirc; tr&ecirc;n 15 triệu đồng tại H&agrave; Nội, Đ&agrave; Nẵng v&agrave; TP. Hồ Ch&iacute; Minh trong năm 2016 sẽ được nhận voucher trải nghiệm miễn ph&iacute; 2 lần dịch vụ chăm s&oacute;c da mặt bằng c&ocirc;ng nghệ Laser kết hợp &aacute;nh s&aacute;ng (LLS) trị gi&aacute; <strong>11.750.000 đồng</strong>.</span></p>\r\n<p><img src=\"../../../uploads/hinh anh chuong trinh(1).jpg\" alt=\"\" width=\"742\" height=\"526\" /></p>\r\n<p><span style=\"font-family: Arial;\">Nếu bạn đ&atilde; từng hoặc đang khao kh&aacute;t l&agrave;n da s&aacute;ng đẹp mịn m&agrave;ng v&agrave; tươi trẻ một c&aacute;ch an to&agrave;n tuyệt đối, th&igrave; đừng đắn đo nữa, h&atilde;y nhanh ch&oacute;ng đến chi nh&aacute;nh PPP gần nhất để tận hưởng đặc quyền d&agrave;nh ri&ecirc;ng kh&aacute;ch h&agrave;ng VBI.<br /> <br />Hotline: <strong>0967.070.070 &ndash; 0962.070.070</strong> hoạt động 24/7 để đảm bảo hỗ trợ kh&aacute;ch h&agrave;ng đặt hẹn v&agrave; nhận ưu đ&atilde;i khi c&oacute; y&ecirc;u cầu.</span></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>Đến Việt Nam từ năm 2012, PPP Laser Clinic &ndash; Tập đo&agrave;n y khoa chăm s&oacute;c da từ Singapore đ&atilde; trở th&agrave;nh điểm đến thường xuy&ecirc;n của những phụ nữ y&ecirc;u th&iacute;ch l&agrave;m đẹp an to&agrave;n. Kh&ocirc;ng những chinh phục kh&aacute;ch h&agrave;ng bằng c&aacute;c c&ocirc;ng nghệ chăm s&oacute;c da hiện đại nhất, tất cả quy tr&igrave;nh điều trị tại PPP Laser Clinic đều được thực hiện bởi đội ngũ B&aacute;c sĩ, chuy&ecirc;n gia được đ&agrave;o tạo chuy&ecirc;n nghiệp tại Singapore, đảm bảo mang đến kh&aacute;ch h&agrave;ng sự an to&agrave;n tuyệt đối v&agrave; hiệu quả tối ưu nhất.</p>\r\n<p>Website: http://ppp.com.vn</p>\r\n<p>Facebook: http://facebook.com/ppp.com.vn</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '2018-03-22 02:13:29', '2018-03-22 13:13:29', 'images/news/1520822181_PPP-Clini_636268296970359510_HasThumb_Thumb.jpg.png'),
(265, 3, 'Ưu đãi bảo hiểm “Hè rộn ràng ưu đãi thật sang”', 'uu-dai-bao-hiem-he-ron-rang-uu-dai-that-sang', '<p><span style=\"font-family: Arial;\">Từ 27/4/2017 đến 17/7/2017, Bảo hiểm VietinBank (VBI) phối hợp với VietinBank triển khai Chương tr&igrave;nh khuyến m&atilde;i &ldquo;H&egrave; rộn r&agrave;ng ưu đ&atilde;i thật sang&rdquo; với tổng gi&aacute; trị giải thưởng hơn 7 tỷ đồng. Giải Đặc biệt của Chương tr&igrave;nh l&agrave; 1 căn hộ nghỉ dưỡng Ecopark trị gi&aacute; 1 tỷ đồng.&nbsp;</span></p>', '<p>Từ 27/4/2017 đến 17/7/2017, Bảo hiểm VietinBank (VBI) phối hợp với VietinBank triển khai Chương tr&igrave;nh khuyến m&atilde;i &ldquo;H&egrave; rộn r&agrave;ng ưu đ&atilde;i thật sang&rdquo; với tổng gi&aacute; trị giải thưởng hơn 7 tỷ đồng. Giải Đặc biệt của Chương tr&igrave;nh l&agrave; 1 căn hộ nghỉ dưỡng Ecopark trị gi&aacute; 1 tỷ đồng.<br /> <br />Khi tham gia chương tr&igrave;nh, Qu&yacute; kh&aacute;ch sẽ được hưởng rất nhiều ưu đ&atilde;i hấp dẫn từ VBI như:</p>\r\n<ul>\r\n<li>Giảm 25% ph&iacute; Bảo hiểm VietinCare, Bảo hiểm ch&aacute;y nổ bắt buộc</li>\r\n<li>200 cặp nhiệt kế trị gi&aacute; 200.000 đồng cho 200 kh&aacute;ch h&agrave;ng đầu ti&ecirc;n tham gia Bảo hiểm sức khỏe VBI Care.</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/he ron rang uu dai that sang.jpg\" alt=\"\" width=\"900\" height=\"1032\" /></p>\r\n<p>Đặc biệt, cuối chương tr&igrave;nh, VietinBank sẽ tổ chức quay số để t&igrave;m ra chủ nh&acirc;n c&aacute;c giải thưởng. Giải Đặc biệt l&agrave; 1 căn hộ nghỉ dưỡng Ecopark trị gi&aacute; 1 tỷ đồng d&agrave;nh ri&ecirc;ng cho kh&aacute;ch h&agrave;ng gửi tiền tiết kiệm v&agrave; giải ng&acirc;n vay vốn tr&ecirc;n to&agrave;n hệ thống VietinBank.<br /> <br />C&aacute;c kh&aacute;ch h&agrave;ng gửi tiền tiết kiệm, giải ng&acirc;n vay vốn, chi ti&ecirc;u thẻ v&agrave; sử dụng VietinBank iPay c&oacute; cơ hội tr&uacute;ng tiếp c&aacute;c giải:</p>\r\n<ul>\r\n<li>1 giải Nhất - chuyến du lịch Ch&acirc;u &Acirc;u 9 ng&agrave;y cho 2 người</li>\r\n<li>3 giải Nh&igrave; - chuyến du lịch Maldives cho 2 người/giải</li>\r\n<li>100 giải Ba: Voucher nghỉ dưỡng tại Vinpearl Resort</li>\r\n<li>1000 giải Khuyến kh&iacute;ch: Tiền mặt 500.000 đồng/giải</li>\r\n</ul>\r\n<p>Du lịch h&egrave; sang trọng, nghỉ dưỡng miễn ph&iacute; chưa bao giờ dễ d&agrave;ng đến thế với khuyến m&atilde;i từ VietinBank.<br /> <br />Để biết th&ecirc;m th&ocirc;ng tin chi tiết, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ Chi nh&aacute;nh/ph&ograve;ng giao dịch VietinBank gần nhất hoặc gọi số Contact Center: 1900 558 868; email: <a href=\"mailto:contact@vietinbank.vn\">contact@vietinbank.vn</a>.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:14:00', '2018-03-22 13:14:00', 'images/news/1520822287_he-ron-ra_636289110562548099_HasThumb_Thumb.jpg.png'),
(266, 3, 'Da đẹp sáng khỏe cùng Bảo hiểm VietinBank', 'da-dep-sang-khoe-cung-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Nhằm tỏ l&ograve;ng tri &acirc;n tới c&aacute;c kh&aacute;ch h&agrave;ng, nh&acirc;n dịp quốc kh&aacute;ch 2/9 v&agrave; ng&agrave;y phụ nữ Việt Nam 20/10, C&ocirc;ng ty Bảo hiểm VietinBank phối hợp với Tập đo&agrave;n y khoa PPP Laser Clinic của Singapore tặng 2 buổi trị liệu laser LLCS cho 400 kh&aacute;ch h&agrave;ng Nữ đ&atilde; tham gia bảo hiểm VietinBank&nbsp;</span></p>', '<p><strong>Nhằm tỏ l&ograve;ng tri &acirc;n tới c&aacute;c kh&aacute;ch h&agrave;ng, nh&acirc;n dịp quốc kh&aacute;ch 2/9 v&agrave; ng&agrave;y phụ nữ Việt Nam 20/10, C&ocirc;ng ty Bảo hiểm VietinBank phối hợp với Tập đo&agrave;n y khoa PPP Laser Clinic của Singapore tặng 2 buổi trị liệu laser LLCS cho 400 kh&aacute;ch h&agrave;ng Nữ đ&atilde; tham gia bảo hiểm VietinBank từ ng&agrave;y 1/5/2017 đến ng&agrave;y 31/8/2017. Đ&acirc;y l&agrave; chương tr&igrave;nh tri &acirc;n kh&aacute;ch h&agrave;ng đ&atilde; ủng hộ v&agrave; tin tưởng Bảo hiểm VietinBank trong suốt thời gian vừa qua. 02 buổi trị liệu chăm s&oacute;c da bằng c&ocirc;ng nghệ laser ti&ecirc;n tiến nhất thế giới sẽ gi&uacute;p trẻ h&oacute;a v&agrave; ngăn ngừa c&aacute;c t&aacute;c nh&acirc;n g&acirc;y hại từ b&ecirc;n ngo&agrave;i.</strong></p>\r\n<p><img src=\"../../../uploads/Bo nhan dien VBI.png\" alt=\"\" width=\"1366\" height=\"1073\" /></p>\r\n<p>- C&ocirc;ng nghệ Laser v&agrave; &aacute;nh s&aacute;ng độc quyền của PPP gi&uacute;p l&agrave;m sạch da, giảm thiểu mụn v&agrave; dầu, tăng sinh collagen, gi&uacute;p da đều m&agrave;u, căng v&agrave; tươi s&aacute;ng</p>\r\n<p>- 03 loại laser được lựa chọn để sử dụng cho bặng: Laser kết hợp &aacute;nh s&aacute;ng, Micro Laser, Warm-Touch</p>\r\n<p>Chỉ với 02 lần trị liệu, kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; được diện mạo ưng &yacute; để tận hưởng dịp Nghỉ lễ. Phụ nữ lu&ocirc;n cần được chăm s&oacute;c, tr&acirc;n trọng v&agrave; y&ecirc;u thương.</p>\r\n<p><img src=\"../../../uploads/concept VBI.png\" alt=\"\" width=\"1366\" height=\"603\" /></p>\r\n<p>Bảo hiểm VietinBank mong muốn c&aacute;c Kh&aacute;ch h&agrave;ng lu&ocirc;n nhận được những gi&aacute; trị đặc biệt để ng&agrave;y một đẹp hơn, hạnh ph&uacute;c hơn.</p>\r\n<p>Mọi thắc mắc về voucher vui l&ograve;ng li&ecirc;n hệ:<strong> 0967.070.070</strong> hoặc <strong>0962.070.070</strong></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><strong>Th&ocirc;ng tin về PPP Laser Clinic</strong></p>\r\n<p>L&agrave; một tập đo&agrave;n y khoa, PPP Laser Clinic chuy&ecirc;n trị liệu c&aacute;c vấn đề về da bằng c&ocirc;ng nghệ laser. Tất cả quy tr&igrave;nh trị liệu tại PPP đều được thực hiện bằng nguy&ecirc;n tắc, liệu tr&igrave;nh an to&agrave;n do <strong>Dr. Goh Seng Heng</strong> ph&aacute;t minh, ho&agrave;n thiện từ 1992 v&agrave; được thực hiện dưới sự gi&aacute;m s&aacute;t, kiểm duyệt v&agrave; nghi&ecirc;m ngặt.</p>\r\n<p>Chi nh&aacute;nh của PPP Laser Clinic tại 03 miền Bắc, Trung, Nam. T&igrave;m hiểu th&ocirc;ng tin chi tiết tại website: <a href=\"https://ppp.com.vn/\">https://ppp.com.vn/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '2018-03-22 02:14:50', '2018-03-22 13:14:50', 'images/news/1520822440_concept-V_636396140606613381_HasThumb_Thumb.png'),
(267, 3, 'Nhận Oppo F1s khi tham gia Thử tài làm Hoạ sỹ cùng Bảo hiểm VietinBank', 'nhan-oppo-f1s-khi-tham-gia-thu-tai-lam-hoa-sy-cung-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Buổi tối qu&acirc;y quần b&ecirc;n gia đ&igrave;nh, c&ugrave;ng nhau vẽ những bức tranh &yacute; nghĩa v&agrave; nhận ngay Oppo F1s. BẠN C&Oacute; TH&Iacute;CH KH&Ocirc;NG? Bạn chỉ cần: - Vẽ 01 bức tranh khổ A4 với mọi đề t&agrave;i y&ecirc;u th&iacute;ch. - Bức tranh cần c&oacute; 2 yếu tố: D&ograve;ng chữ Bảo hiểm VietinBank v&agrave; số 19001566 - Bức tranh c&oacute; thể vẽ bằng...&nbsp;</span></p>', '<p><img src=\"../../../uploads/event-facebook-19001566-thive-2.jpg\" alt=\"\" width=\"1366\" height=\"1145\" /></p>\r\n<p><strong>THỂ LỆ CUỘC THI &ldquo;Thử t&agrave;i l&agrave;m Họa sỹ&rdquo;</strong><br />Thời gian: 10/9 &ndash; 7/10/2017<br /> <br /><strong>1. GIẢI THƯỞNG</strong><br />Những người chi&ecirc;́n thắng cu&ocirc;̣c thi sẽ được nhận c&aacute;c giải thưởng c&oacute; giá trị như sau:</p>\r\n<ul>\r\n<li>01 Giải nhất: Điện thoại OPPO F1S trị gi&aacute; 5.000.000đ</li>\r\n<li>01 Giải nh&igrave;: Xe đạp thời trang trị gi&aacute; 2.000.000 đ</li>\r\n<li>10 Giải ba: Tiền mặt trị gi&aacute; 200.000đ</li>\r\n<li>50 Giải tư: &Aacute;o mưa thời trang trị gi&aacute; 100.000đ</li>\r\n</ul>\r\n<p>Người chiến thắng c&oacute; tr&aacute;ch nhiệm đ&oacute;ng thuế thu nhập theo luật định.<br /> <br /><strong>2. NỘI DUNG THAM DỰ</strong></p>\r\n<ul>\r\n<li>Vẽ một bức tranh khổ A4 với mọi đề t&agrave;i y&ecirc;u th&iacute;ch.</li>\r\n<li>Bức tranh cần c&oacute; 2 yếu tố: D&ograve;ng chữ Bảo hiểm VietinBank v&agrave; số 19001566</li>\r\n<li>Bức tranh c&oacute; thể vẽ bằng mọi chất liệu, vẽ tay, vẽ bằng phần mềm</li>\r\n</ul>\r\n<p><br /><strong>3. C&Aacute;CH THỨC THAM GIA</strong></p>\r\n<ul>\r\n<li><strong>Bước 1</strong>: Th&iacute;ch (Like) trang Fanpage của Bảo hiểm VietinBank - VBI</li>\r\n<li><strong>Bước 2</strong>: Gửi tối đa 03 b&agrave;i dự thi k&egrave;m m&ocirc; tả (caption) qua inbox Facebook hoặc địa chỉ mail: <a href=\"mailto:marketing.vbi@vietinbank.vn\">marketing.vbi@vietinbank.vn</a></li>\r\n<li><strong>Bước 3</strong>: Chia sẻ b&agrave;i dự thi từ fanpage VBI l&ecirc;n trang facebook c&aacute; nh&acirc;n đi k&egrave;m 02 hashtag #19001566 v&agrave; #BảoHiểmVietinBank v&agrave; bạn b&egrave;, người th&acirc;n v&agrave;o b&igrave;nh chọn cho b&agrave;i dự thi của m&igrave;nh bằng c&aacute;ch like hoặc share b&agrave;i dự thi tr&ecirc;n fanpage VBI.</li>\r\n</ul>\r\n<p><br /><strong>4. ĐIỀU KIỆN CHO MỘT B&Agrave;I DỰ THI HỢP LỆ</strong></p>\r\n<ul>\r\n<li>B&agrave;i dự thi v&agrave; b&agrave;i viết được đăng tải tr&ecirc;n fanpage của VBI.</li>\r\n<li>B&agrave;i dự thi của người chơi tr&ecirc;n fanpage VBI được chia sẻ tr&ecirc;n trang c&aacute; nh&acirc;n của người chơi ở chế độ c&ocirc;ng khai v&agrave; c&oacute; đủ 02 hashtag #19001566 v&agrave; #BảoHiểmVietinBank</li>\r\n<li>BTC sẽ kh&ocirc;ng c&ocirc;ng nhận những h&igrave;nh ảnh hoặc c&acirc;u chuyện được sao ch&eacute;p từ tr&ecirc;n internet hoặc từ những c&aacute; nh&acirc;n kh&aacute;c.</li>\r\n</ul>\r\n<p><br /><strong>5. C&Aacute;CH THỨC T&Iacute;NH ĐIỂM</strong><br />Điểm số dự thi của mỗi b&agrave;i sẽ được t&iacute;nh như sau:</p>\r\n<ul>\r\n<li>80% Điểm từ cộng đồng b&igrave;nh chọn: dựa v&agrave;o tổng số lượt like + (share x2) nhiều nhất của b&agrave;i dự thi được đăng tải tr&ecirc;n fanpage VBI (BTC kh&ocirc;ng t&iacute;nh like/share tr&ecirc;n b&agrave;i share tại trang c&aacute; nh&acirc;n của người dự thi).</li>\r\n<li>20% Điểm do BTC chấm theo ti&ecirc;u ch&iacute;: bức tranh s&aacute;ng tạo nhất, bức tranh &yacute; nghĩa nhất.</li>\r\n</ul>\r\n<p><br />Đến hết ng&agrave;y 7/10/2017, BTC sẽ chọn ra c&aacute;c giải dựa tr&ecirc;n ti&ecirc;u ch&iacute;:</p>\r\n<ul>\r\n<li>01 giải nhất: b&agrave;i dự thi hợp lệ v&agrave; c&oacute; điểm số cao nhất</li>\r\n<li>01 giải nh&igrave;: b&agrave;i dự thi hợp lệ v&agrave; c&oacute; điểm số cao thứ nh&igrave;</li>\r\n<li>10 giải ba: b&agrave;i dự thi hợp lệ v&agrave; c&oacute; tổng lượt y&ecirc;u th&iacute;ch (Love) cao nhất. Kh&ocirc;ng t&iacute;nh c&aacute;c b&agrave;i dự thi đ&atilde; đạt giải nhất, nh&igrave;.</li>\r\n<li>50 giải tư : B&agrave;i dự thi hợp lệ v&agrave; c&oacute; tổng lượt y&ecirc;u th&iacute;ch theo thứ hạng từ cao đến thấp</li>\r\n</ul>\r\n<p><br /><strong>6. TH&Ocirc;NG B&Aacute;O KẾT QUẢ V&Agrave; NHẬN GIẢI</strong></p>\r\n<ul>\r\n<li>Giải thưởng được c&ocirc;ng bố v&agrave;o ng&agrave;y 10/10/2017.</li>\r\n<li>Danh s&aacute;ch tr&uacute;ng thưởng sẽ được c&ocirc;ng bố tr&ecirc;n trang Facebook Fanpage Bảo hiểm VietnBank &ndash; VBI. Những người đoạt giải sẽ được th&ocirc;ng b&aacute;o qua số điện thoại đăng k&yacute; v&agrave; địa chỉ Facebook.</li>\r\n</ul>\r\n<p><br /><strong>7. C&Aacute;C QUY ĐỊNH KH&Aacute;C</strong></p>\r\n<ul>\r\n<li>BTC c&oacute; quyền loại bỏ những ảnh dự thi kh&ocirc;ng hợp lệ hoặc kh&ocirc;ng ph&ugrave; hợp chủ đề cuộc thi.</li>\r\n<li>B&agrave;i dự thi của người chơi c&oacute; thể scan, chụp lại bằng m&aacute;y ảnh hoặc điện thoại di động, được chỉnh sửa về &aacute;nh s&aacute;ng. Sử dụng c&aacute;c ứng dụng scan tr&ecirc;n di động như tiny scanner, t&iacute;nh nắng scan của onedriver hoặc c&aacute;c ứng dụng tương tự.</li>\r\n<li>Th&iacute; sinh kh&ocirc;ng được sử dụng c&aacute;c phần mềm để tăng LIKE. Nếu BTC ph&aacute;t hiện ra bất kỳ vi phạm n&agrave;o th&igrave; sẽ hủy kết quả dự thi.</li>\r\n<li>BTC c&oacute; quyền sử dụng th&ocirc;ng tin, h&igrave;nh ảnh li&ecirc;n quan đến t&aacute;c giả, t&aacute;c phẩm dự thi để đăng tải tr&ecirc;n c&aacute;c phương tiện truyền th&ocirc;ng cũng như sử dụng cho c&aacute;c hoạt động kh&aacute;c của VBI m&agrave; kh&ocirc;ng cần phải trả th&ecirc;m bất kỳ chi ph&iacute; n&agrave;o.</li>\r\n<li>Tất cả c&aacute;c th&ocirc;ng tin của người dự thi như địa chỉ, số điện thoại,&hellip; sẽ được bảo mật.</li>\r\n</ul>\r\n<p><br />Mọi thắc mắc v&ecirc;̀ chương trình, xin vui l&ograve;ng li&ecirc;n hệ:<br /><strong>Bảo hiểm VietinBank &ndash; VBI</strong><br />Email: marketing.vbi@vietinbank.vn<br />Điện thoại: 024 3942 5650 (số lẻ: 688)</p>', '2018-03-22 02:15:22', '2018-03-22 13:15:22', 'images/news/1520822670_event-fac_636404815865654306_HasThumb_Thumb.jpg.png'),
(268, 3, 'Chương trình khuyến mại \"An tâm vay, quà liền tay\"', 'chuong-trinh-khuyen-mai-an-tam-vay-qua-lien-tay-1', '<p><span style=\"font-family: Arial;\">Từ ng&agrave;y 1/10/2017, nhằm mang đến nhiều quyền lợi cho kh&aacute;ch h&agrave;ng vay vốn tai Vietinbank, g&oacute;p phần tăng trưởng t&iacute;n dụng v&agrave; đảm bảo an to&agrave;n cho nguồn vốn vay của Ng&acirc;n h&agrave;ng. Đồng thời, nhằm đẩy mạnh doanh thu b&aacute;n ch&eacute;o bảo hiểm, bảo hiểm Vietinbank triển khai chương tr&igrave;nh khuyến mại &ldquo;...&nbsp;</span></p>', '<p>Cuộc sống lu&ocirc;n c&oacute; những biến cố kh&ocirc;ng lường trước được. Nếu kh&ocirc;ng may gia đ&igrave;nh của bạn mất đi người trụ cột của gia đ&igrave;nh th&igrave; g&aacute;nh nặng trả nợ cho ng&acirc;n h&agrave;ng sẽ chuyển sang cho ai? Vợ, con, cha, mẹ, anh chị em ruột&hellip; hay những người th&acirc;n y&ecirc;u nhất của bạn? Chỉ từ 3,000 VNĐ &ndash; 10,000 VNĐ một ng&agrave;y, bạn sẽ ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m khi chuyển giao rủi ro sang cho Bảo hiểm Vietinbank (VBI). Ch&uacute;ng t&ocirc;i sẽ gi&uacute;p bạn thanh to&aacute;n to&agrave;n bộ hoặc một phần nợ cho Ng&acirc;n h&agrave;ng khi kh&ocirc;ng may gặp rủi ro trong cuộc sống, với số tiền chi trả l&ecirc;n tới 1 tỷ đồng.</p>\r\n<p>Nhằm mang đến nhiều quyền lợi cho kh&aacute;ch h&agrave;ng vay vốn tai Vietinbank, g&oacute;p phần tăng trưởng t&iacute;n dụng v&agrave; đảm bảo an to&agrave;n cho nguồn vốn vay của Ng&acirc;n h&agrave;ng. Đồng thời, nhằm đẩy mạnh doanh thu b&aacute;n ch&eacute;o bảo hiểm, bảo hiểm Vietinbank triển khai chương tr&igrave;nh khuyến mại &ldquo;VietinCare sẻ chia hạnh ph&uacute;c&rdquo; đối với sản phẩm Người vay vốn VietinCare d&agrave;nh cho kh&aacute;ch h&agrave;ng. Từ ng&agrave;y 1/10/2017, C&ocirc;ng ty bảo hiểm Vietinbank tổ chức chương tr&igrave;nh khuyến mại khi kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n v&agrave; kh&aacute;ch h&agrave;ng si&ecirc;u vi m&ocirc; của Ng&acirc;n h&agrave;ng C&ocirc;ng thương tham gia sử dụng bảo hiểm Người vay vốn VietinCare của VBI với mức ph&iacute; từ 1.000.000 VNĐ/01 HĐBH trở l&ecirc;n sẽ được tặng 01 &aacute;o mưa in logo c&ocirc;ng ty.</p>\r\n<p>Đặc biệt b&ecirc;n ph&iacute;a c&ocirc;ng ty c&ograve;n c&oacute; những ch&iacute;nh s&aacute;ch mới nhằm đem lại nhiều quyền lợi bảo hiểm cho kh&aacute;ch h&agrave;ng hơn nữa:</p>\r\n<ul>\r\n<li>G&oacute;i bảo hiểm tối thiểu: B&ecirc;n cạnh điều kiện tử vong hoặc thương tật to&agrave;n bộ vĩnh viễn do tai nạn, mức chi trả tối đa 1 tỷ đồng. C&ograve;n bổ sung th&ecirc;m quyền lợi bảo hiểm thương tật bộ phận vĩnh viễn do tai nạn, sẽ chi trả theo bảng tỷ lệ trả tiền bảo hiểm quy định cho thương tật bộ phận vĩnh viễn của VBI.</li>\r\n<li>G&oacute;i bảo hiểm đầy đủ:</li>\r\n</ul>\r\n<ul style=\"list-style-type: circle;\">\r\n<li style=\"padding-left: 30px;\">Điều kiện A: Tử vong hoặc thương tật to&agrave;n bộ vĩnh viễn do tai nạn, mức chi trả tối đa 1 tỷ đồng.</li>\r\n<li style=\"padding-left: 30px;\">Điều kiện B: Tử vong hoặc t&agrave;n tật to&agrave;n bộ vĩnh viễn do ốm đau, bệnh (kh&ocirc;ng bao gồm bệnh đặc biệt v&agrave; bệnh c&oacute; sẵn), mức chi trả tối đa 500 triệu đồng.</li>\r\n<li style=\"padding-left: 30px;\">Bổ sung 01: Bảo hiểm thương tật bộ phận vĩnh viễn do tai nạn. Chi trả theo bảng tỷ lệ trả tiền bảo hiểm quy định cho thương tật bộ phận vĩnh viễn của VBI.</li>\r\n<li style=\"padding-left: 30px;\">Bổ sung 02: Bảo hiểm trợ cấp giảm thu nhập trong qu&aacute; tr&igrave;nh điều trị thương tật do tai nạn. Chi trả tiền bảo hiểm tối đa 05 triệu đồng/vụ tai nạn v&agrave; kh&ocirc;ng qu&aacute; 50 triệu đồng/năm</li>\r\n<li style=\"padding-left: 30px;\">Ngo&agrave;i ra bổ sung th&ecirc;m quyền lợi bảo hiểm bệnh đặc biệt, chi trả tối đa 50% số tiền bảo hiểm của điều kiện B nhưng kh&ocirc;ng vượt qu&aacute; 100 triệu đồng, quyền lợi bảo hiểm c&oacute; hiệu lực ngay sau (180) ng&agrave;y kể từ ng&agrave;y bắt đầu thời hạn bảo hiểm.</li>\r\n</ul>\r\n<p>Chương tr&igrave;nh diễn ra từ 01/10/2017 &ndash; 31/10/2017, c&aacute;n bộ ng&acirc;n h&agrave;ng sẽ trao qu&agrave; ngay khi kh&aacute;ch h&agrave;ng được cấp đơn v&agrave; thanh to&aacute;n ph&iacute; bảo hiểm. Trường hợp c&aacute;n bộ ng&acirc;n h&agrave;ng kh&ocirc;ng chuyển qu&agrave; trực tiếp được cho kh&aacute;ch h&agrave;ng, c&aacute;n bộ VBI sẽ thực hiện chuyển qu&agrave; cho kh&aacute;ch h&agrave;ng chậm nhất trong v&ograve;ng 07 ng&agrave;y kể từ ng&agrave;y kh&aacute;ch h&agrave;ng được cấp đơn. Kết th&uacute;c chương tr&igrave;nh Contact Center của C&ocirc;ng ty 1900 1566 sẽ li&ecirc;n hệ với kh&aacute;ch h&agrave;ng để x&aacute;c nhận giải thưởng.</p>\r\n<p><img src=\"../../../uploads/EMKT - VietinCare se chia hanh phuc - Email - Banner web - postFB - 1-3.jpg\" alt=\"\" width=\"1200\" height=\"379\" /></p>\r\n<p>Chương tr&igrave;nh khuyến mại <strong>&ldquo;VietinCare sẻ chia hạnh ph&uacute;c&rdquo;</strong> như l&agrave; một ưu đ&atilde;i, thể hiện sự quan t&acirc;m của VBI tới to&agrave;n bộ kh&aacute;ch h&agrave;ng nh&acirc;n dịp triển khai hoạt động hướng tới kỉ niệm 10 năm th&agrave;nh lập bảo hiểm Vietinbank <strong>&ldquo;10 năm trọn niềm tin&rdquo;</strong>. H&atilde;y tham gia chương tr&igrave;nh <strong>&ldquo;An t&acirc;m vay, qu&agrave; liền tay&rdquo;</strong> của VBI, qu&agrave; tặng hấp dẫn đang chờ đ&oacute;n bạn. Để biết th&ecirc;m th&ocirc;ng tin kh&aacute;ch h&agrave;ng vui l&ograve;ng gọi đến tổng đ&agrave;i chăm s&oacute;c kh&aacute;ch h&agrave;ng hoặc đến chi nh&aacute;nh bảo hiểm Vietinbank gần nhất để được tư vấn đầy đủ về chương tr&igrave;nh khuyến mại cũng như th&ocirc;ng tin về tham gia sản phẩm.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:16:21', '2018-03-22 13:16:21', 'images/news/1520822862_EMKT-Viet_636428118027100102_HasThumb_Thumb.jpg.png'),
(269, 3, 'Chương trình khuyến mại \"An tâm vay, quà liền tay\"', 'chuong-trinh-khuyen-mai-an-tam-vay-qua-lien-tay', '<p><span style=\"font-family: Arial;\">Tiếp nối sự hưởng ứng v&agrave; quan t&acirc;m của kh&aacute;ch h&agrave;ng với chương tr&igrave;nh khuyến mại th&aacute;ng 10 &ldquo;An t&acirc;m vay, qu&agrave; liền tay&rdquo; đối với sản phẩm Người vay vốn VietinCare. Từ ng&agrave;y 01/11/2017, bảo hiểm VietinBank tiếp tục triển khai chương tr&igrave;nh khuyến mại tặng h&agrave;ng ng&agrave;n phần qu&agrave; hấp dẫn l&ecirc;n đến...&nbsp;</span></p>', '<p>&nbsp;Tiếp nối sự hưởng ứng v&agrave; quan t&acirc;m của kh&aacute;ch h&agrave;ng với chương tr&igrave;nh khuyến mại th&aacute;ng 10 &ldquo;An t&acirc;m vay, qu&agrave; liền tay&rdquo; đối với sản phẩm Người vay vốn VietinCare. Từ ng&agrave;y 01/11/2017, bảo hiểm VietinBank tiếp tục triển khai chương tr&igrave;nh khuyến mại tặng h&agrave;ng ng&agrave;n phần qu&agrave; hấp dẫn l&ecirc;n đến hơn 1 tỷ đồng d&agrave;nh cho kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n v&agrave; Kh&aacute;ch h&agrave;ng doanh nghiệp Si&ecirc;u vi m&ocirc; của Ng&acirc;n h&agrave;ng C&ocirc;ng thương.<br /> <br />Sản phẩm Người vay vốn VietinCare d&agrave;nh cho c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; khoản vay tại VietinBank hoặc tại c&aacute;c Ng&acirc;n h&agrave;ng, Tổ chức t&iacute;n dụng kh&aacute;c, gi&uacute;p kh&aacute;ch h&agrave;ng giảm thiểu c&aacute;c g&aacute;nh nặng về t&agrave;i ch&iacute;nh v&agrave; an t&acirc;m trước những rủi ro trong cuộc sống, đảm bảo cho khả năng trả nợ của kh&aacute;ch h&agrave;ng trong trường hợp tai nạn, ốm đau dẫn đến tử vong hoặc thương tật to&agrave;n bộ vĩnh viễn với số tiền chi trả l&ecirc;n tới 1 tỷ đồng/người/vụ</p>\r\n<p>Từ <strong>01/11 &ndash; 15/11/2017</strong>:</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng tham gia bảo hiểm VietinCare của VBI với mức ph&iacute; từ <strong>1.000.000 VNĐ/01 HĐBH</strong> trở l&ecirc;n sẽ được tặng <strong>01 &aacute;o mưa thời trang</strong>.</li>\r\n</ul>\r\n<p><br />Từ <strong>16/11 &ndash; 31/12/2017</strong>:</p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng tham gia bảo hiểm VietinCare của VBI với mức ph&iacute; từ <strong>1.000.000 VNĐ &ndash; dưới 2.000.000 VNĐ/01 HĐBH</strong> sẽ được tặng <strong>01 t&uacute;i thời trang</strong></li>\r\n<li>Kh&aacute;ch h&agrave;ng tham gia bảo hiểm VietinCare của VBI với mức ph&iacute; từ <strong>2.000.000 VNĐ/01 HĐBH</strong> trở l&ecirc;n sẽ được tặng <strong>01 mũ bảo hiểm thời trang</strong></li>\r\n</ul>\r\n<p><img src=\"../../../uploads/chuong trinh khuyen mai than 1117-Web- 4.jpg\" alt=\"\" width=\"1366\" height=\"738\" /></p>\r\n<p>Chương tr&igrave;nh khuyến mại &ldquo;An t&acirc;m vay, qu&agrave; liền tay&rdquo; như l&agrave; một ưu đ&atilde;i, thể hiện sự quan t&acirc;m của VBI tới to&agrave;n bộ kh&aacute;ch h&agrave;ng nh&acirc;n dịp triển khai hoạt động hướng tới kỉ niệm 10 năm th&agrave;nh lập bảo hiểm VietinBank &ldquo;10 năm trọn niềm tin&rdquo;</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>Để biết th&ecirc;m th&ocirc;ng tin chi tiết kh&aacute;ch h&agrave;ng vui l&ograve;ng gọi đến tổng đ&agrave;i chăm s&oacute;c kh&aacute;ch h&agrave;ng <strong>1900 1566</strong> hoặc đến Chi nh&aacute;nh của Bảo hiểm VietinBank gần nhất để được tư vấn đầy đủ th&ocirc;ng tin tham gia sản phẩm cũng như chương tr&igrave;nh khuyến mại.</td>\r\n</tr>\r\n</tbody>\r\n</table>', '2018-03-22 02:17:19', '2018-03-22 13:17:19', 'images/news/1520822976_Chuong-tr_636446308039593002_HasThumb_Thumb.jpg.png'),
(270, 3, '“NĂM MỚI TẾT SANG – DU XUÂN AN TOÀN” CÙNG GÓI BẢO HIỂM 10K', 'nam-moi-tet-sang-du-xuan-an-toan-cung-goi-bao-hiem-10k', '<p><span style=\"font-family: Arial;\">M&ugrave;a xu&acirc;n l&agrave; m&ugrave;a \"dịch chuyển\" của nhiều người, bạn c&oacute; thể đi về qu&ecirc; ăn Tết, bạn c&oacute; thể đi lễ ch&ugrave;a cầu may hay tự thưởng một chuyến du lịch sau một năm miệt m&agrave;i cống hiến. Tuy nhi&ecirc;n, v&agrave;o thời điểm n&agrave;y, đường x&aacute; đ&ocirc;ng đ&uacute;c, người người đi lại nườm nượp v&agrave; rất dễ để xảy ra những điều kh&ocirc;ng may.</span></p>', '<p><img src=\"../../../uploads/dong-gia-03-03-03-03 (003).jpg\" alt=\"\" width=\"828\" height=\"1200\" /></p>\r\n<p><span style=\"font-family: Arial;\">M&ugrave;a xu&acirc;n l&agrave; m&ugrave;a \"dịch chuyển\" của nhiều người, bạn c&oacute; thể đi về qu&ecirc; ăn Tết, bạn c&oacute; thể đi lễ ch&ugrave;a cầu may hay tự thưởng một chuyến du lịch sau một năm miệt m&agrave;i cống hiến. Tuy nhi&ecirc;n, v&agrave;o thời điểm n&agrave;y, đường x&aacute; đ&ocirc;ng đ&uacute;c, người người đi lại nườm nượp v&agrave; rất dễ để xảy ra những điều kh&ocirc;ng may.<br />Để chia sẻ nỗi lo về những rủi ro c&oacute; thể xảy ra v&agrave; đảm bảo một chuyến du xu&acirc;n an t&acirc;m, Bảo hiểm VietinBank VBI triển khai chương tr&igrave;nh &ldquo;Năm mới Tết sang &ndash; Du xu&acirc;n an to&agrave;n&rdquo; d&agrave;nh tặng bạn g&oacute;i bảo hiểm du lịch nội địa v&agrave; du lịch nước ngo&agrave;i SI&Ecirc;U ƯU Đ&Atilde;I c&ugrave;ng nhiều quyền lợi thiết thực với ứng dụng bảo hiểm My VBI!<br /> <br />G&Oacute;I BẢO HIỂM DU LỊCH NỘI ĐỊA 10,000 VND<br />- Số tiền bảo hiểm l&ecirc;n tới 50.000.000VND<br />- &Aacute;p dụng với chuyến du lịch 5 ng&agrave;y trở xuống<br />- &Aacute;p dụng cho c&aacute;c chuyến đi du xu&acirc;n, về qu&ecirc;. Kh&ocirc;ng giới hạn điểm đến, miễn l&agrave; ở trong phạm vi l&atilde;nh thổ Việt Nam.<br /> <br />G&Oacute;I BẢO HIỂM DU LỊCH NƯỚC NGO&Agrave;I 100,000VND<br />- Số tiền bảo hiểm l&ecirc;n tới 20.000USD<br />- &Aacute;p dụng với chuyến du lịch 5 ng&agrave;y trở xuống<br /> <br />Điều kiện &aacute;p dụng: Mua bảo hiểm tr&ecirc;n ứng dụng My VBI của Bảo Hiểm VietinBank.<br /> <br />Với ứng dụng My VBI, bạn c&oacute; thể mua bảo hiểm du lịch với gi&aacute; 10.000VND cho chuyến du lịch nội địa hoặc 100.000VND cho chuyến du lịch nước ngo&agrave;i ngay tr&ecirc;n chiếc smartphone của m&igrave;nh!<br />- Bước 1: V&agrave;o thư viện ứng dụng AppStore hoặc PlayStore.<br />- Bước 2: T&igrave;m ứng dụng My VBI v&agrave; tải xuống.<br />- Bước 3: Chọn biểu tượng giỏ h&agrave;ng. Chọn phần Bảo hiểm du lịch.<br />- Bước 4: Điền c&aacute;c th&ocirc;ng tin cần thiết.<br />- Bước 5: Thanh to&aacute;n v&agrave; ho&agrave;n tất thủ tục.<br /> <br />C&agrave;i My VBI v&agrave; mua Bảo hiểm du lịch cho cả gia đ&igrave;nh ngay h&ocirc;m nay để c&oacute; một chuyến du xu&acirc;n tr&ograve;n vẹn v&agrave; an t&acirc;m!<br /> <br /><strong>Trụ sở ch&iacute;nh</strong>: Tầng 10-11 T&ograve;a nh&agrave; VietinBank, Số 126 Đội Cấn,Q Ba Đ&igrave;nh, H&agrave; Nội<br /><strong>Hotline</strong>: 1900 15 66 <br /><strong>Fanpage</strong>: <a href=\"https://www.facebook.com/BAOHIEM.VBI/\">https://www.facebook.com/BAOHIEM.VBI/</a><br /><strong>Email</strong>: <a href=\"mailto:marketing.vbi@vietinbank.vn%20\">marketing.vbi@vietinbank.vn</a> <br /><strong>Website</strong>: <a href=\"https://l.facebook.com/l.php?u=http%3A%2F%2Fvbi.vietinbank.vn%2F&amp;h=ATNCGVWAkQ6DCTej6_oRl78wn_E3sA9Qfc26MjkGBGHYfSDqDjMLegp_pOJRoYLwlV9vVPUPkRy86YnoCDf8GugNyZoJ7JyqdlleJN9PX80pSHFJJD4AcWR0AEOA0E8LRB7SM4F5aYW0pf9L74xIlhG5Yl5ukYBuvHqZsVdT6Zpz-s03rLeflamBa4I0TzmGwpfNawMoY4hq2xyx3vrqszq5B5kFA1s9Vkho0rf4oxoc6LdUNd9MatQJ_-h6tlNYj87r1gKQXY2GQDuI02dVqQNtTAZYsxFmwIzzJfI\">http://vbi.vietinbank.vn/</a></span></p>', '2018-03-22 02:17:55', '2018-03-22 13:17:55', 'images/news/1520823224_Dong-gia-_636541380444470244_HasThumb_Thumb.jpg.png'),
(271, 5, 'Nhiễu tin tăng phí hâm nóng thị trường ôtô', 'nhieu-tin-tang-phi-ham-nong-thi-truong-oto', '<p><span style=\"font-family: Arial;\">C&aacute;c kiến nghị như tăng lệ ph&iacute; trước bạ tại H&agrave; Nội v&agrave; TP.HCM l&ecirc;n 20%, tăng ph&iacute; cấp biển số l&ecirc;n 10 triệu đồng, tăng thuế ti&ecirc;u thụ đặc biệt với &ocirc;t&ocirc; được nhắc tới dồn dập những ng&agrave;y qua đ&atilde; khiến cho thị trường bắt đầu n&oacute;ng.&nbsp;</span></p>', '<p>C&aacute;c kiến nghị như tăng lệ ph&iacute; trước bạ tại H&agrave; Nội v&agrave; TP.HCM l&ecirc;n 20%, tăng ph&iacute; cấp biển số l&ecirc;n 10 triệu đồng, tăng thuế ti&ecirc;u thụ đặc biệt với &ocirc;t&ocirc; được nhắc tới dồn dập những ng&agrave;y qua đ&atilde; khiến cho thị trường bắt đầu n&oacute;ng.</p>\r\n<p>Ngay sau khi những th&ocirc;ng tin tr&ecirc;n được tung ra, thị trường &ocirc;t&ocirc; đ&atilde; c&oacute; phản ứng. Kh&ocirc;ng chỉ kh&aacute;ch h&agrave;ng đ&ocirc;n đ&aacute;o t&igrave;m mua xe m&agrave; c&aacute;c đại l&yacute;, salon &ocirc;t&ocirc; cũng chớp lấy cơ hội n&agrave;y để l&agrave;m gi&aacute;.</p>\r\n<p><strong>Kh&aacute;ch h&agrave;ng giật m&igrave;nh</strong></p>\r\n<p>Đến đại l&yacute; hay cửa h&agrave;ng &ocirc;t&ocirc; n&agrave;o trong thời điểm n&agrave;y, kh&aacute;ch h&agrave;ng chẳng cần hỏi th&igrave; cũng được c&aacute;c nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng nhanh nhảu cho biết thuế, ph&iacute; sắp tăng nhằm hạn chế xe c&aacute; nh&acirc;n, nếu kh&ocirc;ng tranh thủ mua ngay th&igrave; sau n&agrave;y chi ph&iacute; cho 1 chiếc xe c&oacute; thể tăng th&ecirc;m tới tr&ecirc;n 10%.</p>\r\n<p>C&aacute;c nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng sẵn s&agrave;ng t&iacute;nh cho kh&aacute;ch biết chiếc xe m&igrave;nh dự định mua sẽ tăng th&ecirc;m bao nhi&ecirc;u tiền nếu kh&ocirc;ng mua ngay l&uacute;c n&agrave;y m&agrave; chờ tới sau khi ph&iacute; đ&atilde; tăng.</p>\r\n<p>Tại đại l&yacute; xe Kia tr&ecirc;n đường Giải Ph&oacute;ng (H&agrave; Nội), nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng đ&atilde; t&iacute;nh cho kh&aacute;ch biết, chiếc Kia Forte SX số tự động do Trường Hải lắp r&aacute;p c&oacute; gi&aacute; b&aacute;n 594 triệu đồng, nếu ph&iacute; trước bạ tăng l&ecirc;n 20%, kh&aacute;ch h&agrave;ng phải trả th&ecirc;m 8 điểm phần trăm (hiện đang l&agrave; 12%) tr&ecirc;n gi&aacute; xe, như vậy sẽ tốn th&ecirc;m 47,52 triệu đồng. Cộng th&ecirc;m 8 triệu đồng tiền biển số (hiện l&agrave; 2 triệu) th&igrave; số tiền tăng th&ecirc;m sẽ l&agrave; 55,52 triệu đồng.</p>\r\n<p><img src=\"../../../uploads/635400833714320000.jpg\" alt=\"\" width=\"500\" height=\"300\" /></p>\r\n<p>Đấy l&agrave; với xe b&igrave;nh d&acirc;n, c&ograve;n xe cao cấp c&oacute; gi&aacute; khoảng 1 tỷ đồng như Hyundai Sonata, chi ph&iacute; tăng th&ecirc;m tr&ecirc;n 80 triệu đồng. Nếu thuế ti&ecirc;u thụ đặc biệt cũng tăng như đề xuất của c&aacute;c cơ quan, th&ecirc;m 10-15%, th&igrave; mỗi chiếc xe sẽ tăng thấp nhất cỡ từ 50 triệu đồng nữa.</p>\r\n<p>Vừa mới nghe tin thuế suất thuế nhập khẩu xe nguy&ecirc;n chiếc giảm khoảng 3-4% v&agrave; thuế linh kiện một số mẫu xe cũng giảm, nhiều kh&aacute;ch h&agrave;ng đ&atilde; mừng thầm, tạm dừng nhu cầu, ng&oacute;ng chờ ng&agrave;y &ocirc;t&ocirc; giảm gi&aacute;. Vậy m&agrave; th&ocirc;ng tin đề nghị tăng thuế, ph&iacute; l&agrave;m cho nhiều người giật m&igrave;nh. Nhẩm t&iacute;nh, gi&aacute; xe c&oacute; giảm cũng kh&ocirc;ng b&ugrave; được với số tiền tăng do ph&iacute; tăng tại hai th&agrave;nh phố lớn m&agrave; c&aacute;c cơ quan đề nghị.</p>\r\n<p><strong>Đại l&yacute;, cửa h&agrave;ng &ocirc;t&ocirc; \"mở cờ trong bụng\"</strong></p>\r\n<p>Thị trường &ocirc;t&ocirc; trong th&aacute;ng 10 vừa qua kh&aacute; ảm đạm. Hiệp hội c&aacute;c nh&agrave; sản xuất &ocirc;t&ocirc; Việt Nam (VAMA) cho biết, th&aacute;ng 10/2011, to&agrave;n bộ 17 th&agrave;nh vi&ecirc;n của VAMA chỉ b&aacute;n được 9.258 chiếc, giảm 8% so với th&aacute;ng 9/2011. So với th&aacute;ng 10 năm ngo&aacute;i, lượng xe b&aacute;n ra trong th&aacute;ng 10 năm nay giảm đến 11%, trong đ&oacute; xe đa dụng giảm 18% v&agrave; xe thương mại giảm 31%. Duy nhất chỉ c&oacute; xe từ 5 chỗ trở xuống l&agrave; tăng trưởng 17%.</p>\r\n<p>Đ&acirc;y l&agrave; mức giảm s&uacute;t bất ngờ, bởi nhu cầu về &ocirc;t&ocirc; tại Việt Nam thường tăng mạnh v&agrave;o cuối năm v&agrave; c&aacute;c DN thường c&oacute; c&aacute;c chương tr&igrave;nh khuyến m&atilde;i, k&iacute;ch cầu ti&ecirc;u d&ugrave;ng v&agrave;o thời điểm n&agrave;y. Tuy nhi&ecirc;n, năm nay lại ho&agrave;n to&agrave;n kh&aacute;c. C&aacute;c chương tr&igrave;nh khuyến m&atilde;i, k&iacute;ch cầu vẫn rầm rộ, nhưng kh&aacute;ch mua th&igrave; kh&ocirc;ng nhiều. Nhiều đại l&yacute;, cửa h&agrave;ng &ocirc;t&ocirc; đ&atilde; h&igrave;nh dung ra một c&aacute;i Tết buồn v&igrave; xe ế ẩm.</p>\r\n<p>Khi th&ocirc;ng tin về đề nghị tăng thuế, ph&iacute; được đăng tải, những người vui nhất kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; c&aacute;c đại l&yacute;, cửa h&agrave;ng b&aacute;n &ocirc;t&ocirc;. Đ&acirc;y l&agrave; th&ocirc;ng tin v&ocirc; c&ugrave;ng c&oacute; gi&aacute; trị với họ. Thị trường &ocirc;t&ocirc; đang ảm đạm sẽ được cứu v&atilde;n, xe đang ế ẩm, kh&aacute;ch khứa vắng vẻ bỗng dưng ra v&agrave;o tấp nập, ti&ecirc;u thụ xe tăng, gi&aacute; b&aacute;n tăng, niềm vui lại đến.</p>\r\n<p>Qua khảo s&aacute;t tr&ecirc;n thị trường H&agrave; Nội, một số mẫu xe đ&atilde; bắt đầu n&acirc;ng gi&aacute; b&aacute;n. Một v&agrave;i mẫu xe của Ford, Kia trước đ&acirc;y c&oacute; mức giảm dưới gi&aacute; c&ocirc;ng bố tới tr&ecirc;n 10 triệu đồng, nay đ&atilde; kh&ocirc;ng c&ograve;n giảm nữa. Tại c&aacute;c cửa h&agrave;ng b&aacute;n &ocirc;t&ocirc; tr&ecirc;n đường L&ecirc; Văn Lương, nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng vừa hướng dẫn, vừa thăm d&ograve; kh&aacute;ch, nếu kh&aacute;ch đổ tới nhiều v&agrave; cần mua xe ngay th&igrave; gi&aacute; sẽ dần dần được đẩy l&ecirc;n.</p>\r\n<p>C&aacute;c chi&ecirc;u c&acirc;u kh&aacute;ch cũng được triển khai nhanh ch&oacute;ng, xe sẽ lại khan hiếm, người ti&ecirc;u d&ugrave;ng muốn mua lại nộp tiền trước v&agrave; chờ đợi. Hiện kh&aacute;ch h&agrave;ng mua xe Altis, Fortuner của Toyota Việt Nam phải chờ sang th&aacute;ng 1/2012 (Toyota Việt Nam đang phải cắt giảm sản xuất do chịu ảnh hưởng từ t&igrave;nh trạng lũ lụt tại Th&aacute;i Lan).</p>\r\n<p>Nhưng nhiều kh&aacute;ch h&agrave;ng lại đang cố xoay sở để c&oacute; thể lấy xe v&agrave;o th&aacute;ng 12/2011 bởi sợ từ 1/1/2012 lệ ph&iacute; trước bạ, ph&iacute; biển số sẽ tăng. Từ nay đến cuối năm 2011, cảnh chen nhau mua xe, trả th&ecirc;m tiền để c&oacute; xe sớm c&oacute; thể lại diễn ra giống như nhiều năm trước đ&acirc;y.</p>\r\n<p>Đề xuất tăng ph&iacute; trước bạ, ph&iacute; cấp biển số, thuế ti&ecirc;u thụ đặc biệt l&uacute;c n&agrave;y đ&uacute;ng l&agrave; đ&atilde; gi&uacute;p k&iacute;ch cầu thị trường cho thị trường &ocirc;t&ocirc;. Tuy nhi&ecirc;n, ch&iacute;nh s&aacute;ch th&igrave; cứ thay đổi li&ecirc;n tục m&agrave; người ti&ecirc;u d&ugrave;ng chẳng được hưởng lợi g&igrave;.</p>\r\n<p style=\"text-align: right;\"><strong>(VEF.VN)</strong></p>', '2018-03-22 02:18:22', '2018-03-22 13:18:22', 'images/news/1520823405_pic-New-i_635400833717680000_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(272, 5, 'Môi giới bảo hiểm độc lập, chờ 10 năm nữa', 'moi-gioi-bao-hiem-doc-lap-cho-10-nam-nua', '<p><span style=\"font-family: Arial;\">(ĐTCK) Ở nhiều nước ph&aacute;t triển, c&aacute;c giao dịch bảo hiểm qua tổ chức m&ocirc;i giới v&agrave; m&ocirc;i giới độc lập chiếm tới 90% giao dịch tr&ecirc;n thị trường.&nbsp;</span></p>', '<p>(ĐTCK) Ở nhiều nước ph&aacute;t triển, c&aacute;c giao dịch bảo hiểm qua tổ chức m&ocirc;i giới v&agrave; m&ocirc;i giới độc lập chiếm tới 90% giao dịch tr&ecirc;n thị trường.</p>\r\n<p>C&ograve;n ở Việt Nam, giao dịch bảo hiểm qua m&ocirc;i giới mới chỉ chiếm khoảng 17%. Trong đ&oacute;, những giao dịch bảo hiểm qua m&ocirc;i giới độc lập vẫn l&agrave; con số 0.</p>\r\n<p>M&ocirc;i giới bảo hiểm l&agrave; người đại diện cho lợi &iacute;ch người được bảo hiểm, l&agrave; một tổ chức cung cấp dịch vụ tư vấn cho người y&ecirc;u cầu bảo hiểm về c&aacute;c mặt đề ph&ograve;ng, hạn chế tổn thất hoặc đ&aacute;nh gi&aacute; rủi ro, sắp xếp phương &aacute;n bảo hiểm, l&agrave;m thủ tục tham gia bảo hiểm, thay mặt người tham gia bảo hiểm hay người hưởng quyền lợi bảo hiểm để tiến h&agrave;nh gi&aacute;m định, đ&ograve;i bồi thường sau khi xảy ra rủi ro.</p>\r\n<p>Về mặt l&yacute; thuyết, một thị trường bảo hiểm c&oacute; sự g&oacute;p mặt của c&aacute;c th&agrave;nh phần m&ocirc;i giới sẽ g&oacute;p phần th&uacute;c đẩy giao dịch c&ocirc;ng bằng v&agrave; cạnh tranh c&oacute; trật tự; giảm bớt những tranh chấp về bảo hiểm, ngăn chặn những hoạt động kinh doanh vi phạm ph&aacute;p luật; th&uacute;c đẩy sự ph&aacute;t triển của thị trường bảo hiểm&hellip;</p>\r\n<p>C&oacute; hai loại m&ocirc;i giới. Một l&agrave; những m&ocirc;i giới hoạt động ri&ecirc;ng lẻ như đại l&yacute; của c&aacute;c c&ocirc;ng ty bảo hiểm hiện nay, nhưng kh&ocirc;ng phụ thuộc v&agrave;o c&ocirc;ng ty bảo hiểm, m&agrave; hoạt động độc lập (m&ocirc;i giới độc l&acirc;p), c&oacute; bằng cấp về c&aacute;c ng&agrave;nh kinh tế v&agrave; kiến thức luật kh&aacute; s&acirc;u rộng. Hai l&agrave; m&ocirc;i giới tổ chức (c&aacute;c c&ocirc;ng ty m&ocirc;i giới). Ở một số thị trường bảo hiểm ph&aacute;t triển quy định, c&oacute; những giao dịch bảo hiểm chỉ được th&ocirc;ng qua m&ocirc;i giới. Tại c&aacute;c thị trường n&agrave;y, nghề m&ocirc;i giới bảo hiểm l&agrave; nghề đầu ti&ecirc;n bắt buộc mua bảo hiểm tr&aacute;ch nhiệm nghề nghiệp, v&igrave; nếu tư vấn sai cho kh&aacute;ch h&agrave;ng th&igrave; c&aacute;c c&ocirc;ng ty m&ocirc;i giới phải c&oacute; tr&aacute;ch nhiệm bồi thường cho kh&aacute;ch h&agrave;ng v&agrave; rủi ro n&agrave;y được chuyển giao cho c&aacute;c c&ocirc;ng ty bảo hiểm, với mức ph&iacute; kh&aacute; cao.</p>\r\n<p>Đối với thị trường Việt Nam, tuy chưa c&oacute; m&ocirc;i giới độc lập, nhưng đ&atilde; c&oacute; sự tham gia của c&aacute;c c&ocirc;ng ty m&ocirc;i giới bảo hiểm. Hiện nay, AON l&agrave; c&ocirc;ng ty dẫn đầu về c&aacute;c thương vụ m&ocirc;i giới bảo hiểm chăm s&oacute;c sức khỏe, đang tập trung ph&aacute;t triển thị phần của m&igrave;nh tại thị trường Việt Nam.</p>\r\n<p>&Ocirc;ng Stephen J.Clack, Tổng gi&aacute;m đốc điều h&agrave;nh AIA Việt Nam cho rằng, với thị trường Việt Nam giai đoạn hiện nay, c&aacute;c c&ocirc;ng ty m&ocirc;i giới sẽ ph&aacute;t triển nhanh hơn, c&oacute; thể c&ograve;n c&oacute; sự tham gia nhiều hơn của c&aacute;c c&ocirc;ng ty m&ocirc;i giới đa quốc gia.</p>\r\n<p>Một số chuy&ecirc;n gia trong ng&agrave;nh nhận x&eacute;t, trong giai đoạn đầu khi thị trường mới ph&aacute;t triển, m&ocirc; h&igrave;nh đại l&yacute; b&aacute;n bảo hiểm dựa tr&ecirc;n mối quan hệ c&aacute; nh&acirc;n vẫn l&agrave; m&ocirc; h&igrave;nh phổ biến nhất. Nhưng khi thị trường đ&atilde; ph&aacute;t triển đến một mức độ nhất định, kh&aacute;ch h&agrave;ng c&oacute; hiểu biết nhiều hơn về bảo hiểm nh&acirc;n thọ (&yacute; thức về vai tr&ograve; của bảo hiểm, hiểu được đặc t&iacute;nh sản phẩm&hellip;), th&igrave; họ đ&ograve;i hỏi đại l&yacute; phục vụ phải chuy&ecirc;n nghiệp hơn, đồng thời muốn so s&aacute;nh sản phẩm bảo hiểm giữa c&aacute;c c&ocirc;ng ty. Đến l&uacute;c n&agrave;y, m&ocirc; h&igrave;nh m&ocirc;i giới bảo hiểm mới c&oacute; cơ hội ph&aacute;t triển. M&ocirc; h&igrave;nh m&ocirc;i giới bảo hiểm thường ph&aacute;t triển trước trong lĩnh vực bảo hiểm phi nh&acirc;n thọ (bảo hiểm sức khỏe), sau đ&oacute; mới đến lĩnh vực bảo hiểm nh&acirc;n thọ.</p>\r\n<p>Kh&aacute;c với những h&agrave;ng ho&aacute; cụ thể, sản phẩm bảo hiểm l&agrave; sự đảm bảo cho những rủi ro được dự đo&aacute;n về sau n&agrave;y, sản phẩm bảo hiểm c&oacute; đặc trưng l&agrave; kh&ocirc;ng thể cảm nhận được. Do đ&oacute;, người y&ecirc;u cầu bảo hiểm thường kh&ocirc;ng hiểu r&otilde; về lợi v&agrave; hại, c&aacute;i hay v&agrave; dở của sản phẩm bảo hiểm. Khi đ&oacute;, sự xuất hiện của những nh&agrave; m&ocirc;i giới bảo hiểm c&oacute; những t&aacute;c động t&iacute;ch cực trong hoạt động kinh doanh bảo hiểm, gi&uacute;p tr&aacute;nh xảy ra t&igrave;nh trạng bất lợi cho người tham gia bảo hiểm.</p>\r\n<p>Kh&oacute; c&oacute; thể định lượng phải mất bao l&acirc;u để x&acirc;y dựng được đội ngũ m&ocirc;i giới độc lập ở một thị trường bảo hiểm mới nổi, c&aacute;c chuy&ecirc;n gia trong ng&agrave;nh cho rằng, thời gian nhanh chậm c&ograve;n t&ugrave;y thuộc v&agrave;o tốc độ ph&aacute;t triển của thị trường. Với thị trường Việt Nam, dự đo&aacute;n m&ocirc; h&igrave;nh ph&acirc;n phối bảo hiểm qua đại l&yacute; tư vấn trực tiếp vẫn sẽ c&ograve;n thịnh h&agrave;nh trong những năm tới.</p>\r\n<p>&ldquo;M&ocirc;i giới bảo hiểm độc lập cần mất rất nhiều thời gian mới được phổ biến ở Việt Nam. Bởi ngay cả c&aacute;c thị trường bảo hiểm ph&aacute;t triển như Singapore, Hồng K&ocirc;ng, tỷ lệ m&ocirc;i giới độc lập cũng rất nhỏ. Thực tế, những kh&aacute;ch h&agrave;ng sử dụng m&ocirc;i giới l&agrave; những người c&oacute; nhu cầu tư vấn t&agrave;i ch&iacute;nh rất cao. Ở Việt Nam, để m&ocirc;i giới độc lập ph&aacute;t triển c&oacute; lẽ phải mất 10 năm nữa&rdquo;, &ocirc;ng Stephen J.Clack nhận định.</p>\r\n<p style=\"text-align: right;\"><strong>Ngọc Lan</strong><br /><strong>B&aacute;o Đầu tư &ndash; Cơ quan của Bộ Kế hoạch v&agrave; Đầu tư.</strong></p>', '2018-03-12 02:58:00', '2018-03-12 13:58:00', 'images/news/1520823469_tinmgbaoh_635400838003550000_HasThumb_Thumb.jpg.png'),
(273, 5, 'Từ 1/11/2012, tăng tiền bồi thường bảo hiểm xe cơ giới.', 'tu-1-11-2012-tang-tien-boi-thuong-bao-hiem-xe-co-gioi', '<p><span style=\"font-family: Arial;\">Đ&oacute; l&agrave; quy định trong Th&ocirc;ng tư 151/2012/TT-BTC sửa đổi, bổ sung Th&ocirc;ng tư số 126/2008/TT-BTC ng&agrave;y 22/12/2008 của Bộ T&agrave;i ch&iacute;nh.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 23/10, buổi tập huấn Th&ocirc;ng tư 151/2012/TT-BTC sửa đổi, bổ sung Th&ocirc;ng tư số 126/2008/TT-BTC ng&agrave;y 22/12/2008 của Bộ T&agrave;i ch&iacute;nh quy định Quy tắc, điều khoản, biểu ph&iacute; v&agrave; mức tr&aacute;ch nhiệm bảo hiểm bắt buộc tr&aacute;ch nhiệm d&acirc;n sự của chủ xe cơ giới v&agrave; Th&ocirc;ng tư số 103/2009/TT-BTC ng&agrave;y 25/5/2009 của Bộ T&agrave;i ch&iacute;nh quy định việc quản l&yacute;, sử dụng, thanh to&aacute;n v&agrave; quyết to&aacute;n Quỹ bảo hiểm xe cơ giới đ&atilde; thu h&uacute;t h&agrave;ng trăm người đến từ c&aacute;c DN bảo hiểm, Hiệp hội &ocirc; t&ocirc; vận tải Việt Nam. Th&ocirc;ng tư n&agrave;y ch&iacute;nh thức c&oacute; hiệu lực từ ng&agrave;y 1/11 tới.&nbsp;</span></p>\r\n<p><img src=\"../../../uploads/635400838655540000.jpg\" alt=\"\" width=\"620\" height=\"344\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Mức tr&aacute;ch nhiệm bảo hiểm đối với xe cơ giới sẽ tăng đến 40%</span></em></p>\r\n<p>&Ocirc;ng Trịnh Thanh Hoan, Cục trưởng Cục Quản l&yacute;, gi&aacute;m s&aacute;t bảo hiểm (Bộ T&agrave;i ch&iacute;nh) khẳng định, Th&ocirc;ng tư 151 được ban h&agrave;nh nhằm ph&ugrave; hợp với thực tiễn hoạt động của nghiệp vụ n&agrave;y, cũng như nhằm mang lại quyền lợi cao hơn cho chủ xe cơ giới v&agrave; người bị nạn.</p>\r\n<p>&nbsp;<strong>N&acirc;ng mức tr&aacute;ch nhiệm l&ecirc;n 70 triệu đồng/vụ</strong></p>\r\n<p>Mức tr&aacute;ch nhiệm bảo hiểm l&agrave; số tiền tối đa doanh nghiệp bảo hiểm c&oacute; thể phải trả đối với thiệt hại về th&acirc;n thể, t&iacute;nh mạng v&agrave; t&agrave;i sản của b&ecirc;n thứ ba v&agrave; h&agrave;nh kh&aacute;ch do xe cơ giới g&acirc;y ra trong mỗi vụ tai nạn xảy ra thuộc phạm vi tr&aacute;ch nhiệm bảo hiểm.</p>\r\n<p>Tại Th&ocirc;ng tư 151, mức tr&aacute;ch nhiệm bảo hiểm đ&atilde; được n&acirc;ng l&ecirc;n đối với cả 3 loại h&igrave;nh bảo hiểm so với trước đ&acirc;y. Cụ thể, mức tr&aacute;ch nhiệm bảo hiểm đối với thiệt hại về người do xe cơ giới g&acirc;y ra v&agrave; mức tr&aacute;ch nhiệm bảo hiểm đối với thiệt hại về t&agrave;i sản do xe &ocirc; t&ocirc;, m&aacute;y k&eacute;o&hellip; g&acirc;y ra đều tăng 40% so với trước đ&acirc;y, từ 50 triệu đồng/1 người/1 vụ tai nạn l&ecirc;n 70 triệu đồng/1 người/1 vụ tai nạn; mức tr&aacute;ch nhiệm bảo hiểm đối với thiệt hại về t&agrave;i sản do xe m&ocirc; t&ocirc; 2 b&aacute;nh, xe m&ocirc; t&ocirc; 3 b&aacute;nh, xe gắn m&aacute;y v&agrave; c&aacute;c loại xe cơ giới tương tự g&acirc;y ra l&agrave; 40 triệu đồng/1 vụ tai nạn (thay v&igrave; 30 triệu đồng).</p>\r\n<p>L&yacute; giải th&ecirc;m với ĐTCK, &ocirc;ng Phạm Đ&igrave;nh Trọng, Ph&oacute; Cục trưởng Cục Quản l&yacute;, gi&aacute;m s&aacute;t bảo hiểm cũng cho rằng, quy định mới n&agrave;y đ&atilde; một lần nữa cho thấy mục ti&ecirc;u an sinh x&atilde; hội của bảo hiểm bắt buộc xe cơ giới, trong khi mức ph&iacute; bảo hiểm phải đ&oacute;ng d&ugrave; tăng, nhưng tăng ở mức thấp hơn so với gi&aacute; trị bồi thường m&agrave; chủ xe cũng như người bị nạn được hưởng, thậm ch&iacute;, mức ph&iacute; với xe m&ocirc; t&ocirc; 2 b&aacute;nh c&ograve;n giữ nguy&ecirc;n.</p>\r\n<p>Ngo&agrave;i ra, theo &ocirc;ng Trọng, t&iacute;nh nh&acirc;n văn ở đ&acirc;y c&ograve;n thể hiện ở việc Th&ocirc;ng tư mới đ&atilde; n&acirc;ng mức chi hỗ trợ nh&acirc;n đạo l&ecirc;n tối đa 15% tổng số tiền đ&oacute;ng c&aacute;c quỹ h&agrave;ng năm, 20 triệu đồng/1 người/1 vụ đối với người chết cũng như bị thương tật to&agrave;n bộ vĩnh viễn (trong khi trước đ&acirc;y tương ứng l&agrave; 5 triệu đồng đối với người chết v&agrave; kh&ocirc;ng chi trả đối với thương tật to&agrave;n bộ vĩnh viễn).</p>\r\n<p>&nbsp;<strong>Bồi thường nhanh gọn hơn</strong></p>\r\n<p>Nhằm tạo thuận lợi cho c&aacute;c b&ecirc;n khi xảy ra tai nạn với thủ tục bồi thường nhanh gọn, linh hoạt, Th&ocirc;ng tư mới đ&atilde; đưa ra quy định mới bồi thường bảo hiểm cụ thể c&oacute; thể theo thỏa thuận giữa chủ xe cơ giới v&agrave; người bị thiệt hại, hoặc đại diện hợp ph&aacute;p của người bị thiệt hại (trong trường hợp người bị thiệt hại đ&atilde; chết) nhưng kh&ocirc;ng vượt qu&aacute; mức bồi thường quy định tại Th&ocirc;ng tư n&agrave;y. C&ograve;n trước đ&acirc;y, mức bồi thường cụ thể cho từng loại thương tật, thiệt hại về người chỉ được x&aacute;c định theo bảng quy định trả tiền bồi thường thiệt hại về người theo quy định tại Phụ lục 2 ban h&agrave;nh k&egrave;m theo Th&ocirc;ng tư. Ngo&agrave;i ra, Th&ocirc;ng tư mới cũng bổ sung trường hợp c&oacute; quyết định của to&agrave; &aacute;n th&igrave; căn cứ v&agrave;o quyết định của to&agrave; &aacute;n, nhưng kh&ocirc;ng vượt qu&aacute; mức bồi thường quy định tại Th&ocirc;ng tư n&agrave;y.</p>\r\n<p>&nbsp;<strong>Chủ xe được linh động nộp ph&iacute;</strong></p>\r\n<p>Theo quy định trước đ&acirc;y, DN chỉ cấp Giấy chứng nhận bảo hiểm cho chủ xe cơ giới khi chủ xe đ&atilde; đ&oacute;ng đủ ph&iacute; bảo hiểm. Việc đ&oacute;ng đủ ph&iacute; bảo hiểm phải được x&aacute;c nhận bằng chứng từ kế to&aacute;n của DN. Tuy nhi&ecirc;n, quy định mới cũng bổ sung quy định trong một số trường hợp, DN v&agrave; chủ xe cơ giới c&oacute; thể thỏa thuận (bằng văn bản) về thời hạn thanh to&aacute;n ph&iacute; bảo hiểm. Cụ thể, với chủ xe cơ giới l&agrave; c&aacute;c cơ quan h&agrave;nh ch&iacute;nh, đơn vị sự nghiệp của Nh&agrave; nước, ph&iacute; bảo hiểm phải được thanh to&aacute;n đủ trong thời hạn 10 ng&agrave;y kể từ khi giấy chứng nhận bảo hiểm c&oacute; hiệu lực. C&ograve;n nếu chủ xe kh&ocirc;ng thuộc diện tr&ecirc;n, nhưng c&oacute; nhiều xe cơ giới tham gia bảo hiểm tại c&ugrave;ng thời điểm v&agrave; c&oacute; tổng ph&iacute; bảo hiểm phải nộp từ 100 triệu đồng trở l&ecirc;n th&igrave; ph&iacute; bảo hiểm được thanh to&aacute;n th&agrave;nh 2 kỳ, mỗi kỳ nộp đủ 50% tổng ph&iacute; bảo hiểm thay v&igrave; đ&oacute;ng 1 lần như trước đ&acirc;y. Trường hợp chủ xe cơ giới kh&ocirc;ng đ&oacute;ng đủ ph&iacute; bảo hiểm theo thời hạn thỏa thuận n&ecirc;u tr&ecirc;n, hợp đồng bảo hiểm sẽ chấm dứt hiệu lực v&agrave;o ng&agrave;y kế tiếp ng&agrave;y chủ xe cơ giới phải đ&oacute;ng ph&iacute; bảo hiểm.</p>\r\n<p>Đại diện Hiệp hội &ocirc; t&ocirc; vận tải Việt Nam cũng cho rằng, những điểm mới tại Th&ocirc;ng tư 151 đang đến gần hơn với &ldquo;cuộc sống&rdquo;, ph&ugrave; hợp với thực tiễn để c&aacute;c b&ecirc;n li&ecirc;n quan hoạt động thuận tiện hơn.</p>\r\n<p>&Ocirc;ng Trọng cho biết, &ldquo;ban đầu, dự thảo Th&ocirc;ng tư ghi thời gian c&oacute; hiệu lực từ năm 2013, nhưng cuối c&ugrave;ng đ&atilde; đổi sang lấy mốc 1/11 tới do Ban soạn thảo cho rằng, với c&aacute;c điểm mới kể tr&ecirc;n, Th&ocirc;ng tư c&oacute; hiệu lực sớm ng&agrave;y n&agrave;o th&igrave; tốt ng&agrave;y đ&oacute;, quyền lợi của chủ xe v&agrave; c&aacute;c b&ecirc;n li&ecirc;n quan được đảm bảo sớm hơn&rdquo;.</p>\r\n<p style=\"text-align: right;\"><em><strong>(ĐTCK)</strong></em></p>', '2018-03-22 02:19:00', '2018-03-22 13:19:00', 'images/news/1520823607_pic-New-i_635400838657230000_HasThumb_Thumb.jpg.png'),
(274, 5, 'Cạnh tranh bằng dịch vụ, cuộc đua nước rút', 'canh-tranh-bang-dich-vu-cuoc-dua-nuoc-rut', '<p><span style=\"font-family: Arial;\">Trong một năm kinh tế vĩ m&ocirc; nhiều kh&oacute; khăn như 2012, với hầu hết c&aacute;c DN bảo hiểm, mục ti&ecirc;u tăng trưởng kinh doanh đ&atilde; được ĐHCĐ, ban l&atilde;nh đạo đưa ra hồi đầu năm vẫn c&ograve;n ở kh&aacute; xa.&nbsp;</span></p>', '<p><img src=\"../../../uploads/635400839327450000.jpg\" alt=\"\" width=\"620\" height=\"413\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Đầu tư n&acirc;ng cao chất lượng dịch vụ mới l&agrave; phương ph&aacute;p cạnh tranh bền vững</span></em></p>\r\n<p>Chỉ c&ograve;n gần hai th&aacute;ng l&agrave; kết th&uacute;c năm 2012, c&aacute;c DN đang v&agrave;o cuộc chạy đua nước r&uacute;t. Kh&ocirc;ng chỉ tung ra những biện ph&aacute;p k&iacute;ch cầu bảo hiểm, như gia tăng dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng khuyến mại v&agrave; thưởng hấp dẫn, nhiều DN bảo hiểm đ&atilde; đưa ra g&oacute;i sản phẩm mới ph&ugrave; hợp với từng đối tượng kh&aacute;ch h&agrave;ng&hellip;</p>\r\n<p>Năm nay, biện ph&aacute;p k&iacute;ch cầu bằng hạ ph&iacute;, từng được c&aacute;c DN bảo hiểm coi l&agrave; đ&ograve;n th&ocirc;ng dụng để lấy kh&aacute;ch h&agrave;ng của c&ocirc;ng ty bảo hiểm kh&aacute;c, nhưng cũng khiến kh&ocirc;ng &iacute;t DN phải nếm tr&aacute;i đắng thua lỗ, đ&atilde; dần vắng b&oacute;ng tr&ecirc;n thị trường. Đại diện một DN bảo hiểm phi nh&acirc;n thọ nh&igrave;n nhận, hiện nay, xu hướng cạnh tranh bằng h&igrave;nh thức giảm ph&iacute; đ&atilde; kh&ocirc;ng c&ograve;n được nhiều DN bảo hiểm lớn ch&uacute; trọng. Giờ đ&acirc;y, việc giảm ph&iacute; nếu c&oacute; cũng chỉ để giữ ch&acirc;n kh&aacute;ch h&agrave;ng nhằm tr&aacute;nh bị c&aacute;c DN bảo hiểm nhỏ ch&egrave;o k&eacute;o bằng c&aacute;ch ph&aacute; gi&aacute; ph&iacute;, c&ograve;n lại đều tập trung đầu tư cho chất lượng dịch vụ kh&aacute;ch h&agrave;ng.</p>\r\n<p>Hiện tại, c&aacute;c DN tập trung n&acirc;ng cấp dịch vụ hỗ trợ, tư vấn qua điện thoại, việc l&agrave;m kh&ocirc;ng thể thiếu của bất kỳ DN bảo hiểm n&agrave;o khi muốn đẩy mạnh dịch vụ v&agrave; tăng cường hợp t&aacute;c với c&aacute;c garage v&agrave; bệnh viện li&ecirc;n kết, nhằm tăng cường chất lượng phục vụ kh&aacute;ch h&agrave;ng. Việc hợp t&aacute;c n&agrave;y một mặt gi&uacute;p kh&aacute;ch h&agrave;ng được sử dụng c&aacute;c dịch vụ chất lượng cao, tạo lợi thế cho DN bảo hiểm trong việc giữ ch&acirc;n kh&aacute;ch h&agrave;ng; mặt kh&aacute;c c&aacute;c garage v&agrave; bệnh viện sẽ cam kết một mức gi&aacute; tốt nhất cho DN bảo hiểm, gi&uacute;p DN tiết kiệm được chi ph&iacute; cũng như hạn chế được c&aacute;c h&agrave;nh động trục lợi bảo hiểm.</p>\r\n<p>Kh&ocirc;ng chỉ n&acirc;ng cao chất lượng dịch vụ kh&aacute;ch h&agrave;ng, c&ocirc;ng t&aacute;c bồi thường, đẩy nhanh tiến độ giải quyết c&aacute;c đơn bảo hiểm cũng được c&aacute;c DN rốt r&aacute;o thực hiện. Nhiều DN đ&atilde; ứng dụng c&ocirc;ng nghệ v&agrave;o qu&aacute; tr&igrave;nh gi&aacute;m định, chứ kh&ocirc;ng thực hiện thủ c&ocirc;ng như trước đ&acirc;y, l&agrave;m tăng t&iacute;nh ch&iacute;nh x&aacute;c v&agrave; r&uacute;t ngắn thời gian gi&aacute;m định.</p>\r\n<p>Đại diện một DN bảo hiểm cho biết, thực tế, kh&acirc;u gi&aacute;m định bồi thường v&agrave; chi trả mới l&agrave; bộ phận quyết định sự h&agrave;i l&ograve;ng nhất của kh&aacute;ch h&agrave;ng. Ở một số DN bảo hiểm phi nh&acirc;n thọ, hiện vẫn c&ograve;n t&igrave;nh trạng kh&aacute;ch h&agrave;ng gọi đến để y&ecirc;u cầu bồi thường phải thăm d&ograve; th&aacute;i độ của nh&acirc;n vi&ecirc;n gi&aacute;m định. Thậm ch&iacute;, ở một số DN c&oacute; quy tr&igrave;nh bồi thường nhưng nếu kh&ocirc;ng kiểm tra, gi&aacute;m s&aacute;t chặt chẽ th&igrave; quy tr&igrave;nh vẫn c&oacute; thể tắc.</p>\r\n<p>X&eacute;t về quy tr&igrave;nh tiếp nhận v&agrave; xử l&yacute; bồi thường, c&aacute;c DN bảo hiểm phi nh&acirc;n thọ nước ngo&agrave;i đang c&oacute; ưu thế cạnh tranh, bởi họ đầu tư c&ocirc;ng nghệ hiện đại v&agrave; đồng bộ n&ecirc;n mọi quy tr&igrave;nh tiếp nhận v&agrave; xử l&yacute; bồi thường kh&aacute; nhanh ch&oacute;ng. Chẳng hạn, ở Liberty, đối với nghiệp vụ bảo hiểm xe cơ giới, khoảng 75% đơn bồi thường của kh&aacute;ch h&agrave;ng sẽ được xử l&yacute; trong v&ograve;ng 24 giờ, thậm ch&iacute; c&oacute; nhiều vụ chỉ trong 15 ph&uacute;t (đối với trường hợp tổn thất đơn giản kh&ocirc;ng c&oacute; thiệt hại đối với b&ecirc;n thứ ba&hellip;). Trong khi, đối với c&aacute;c DN bảo hiểm phi nh&acirc;n thọ trong nước, nếu nhanh th&igrave; quy tr&igrave;nh n&agrave;y cũng phải mất tới 48 giờ&hellip;</p>\r\n<p>C&oacute; lẽ, c&aacute;c DN bảo hiểm đ&atilde; hiểu quy tr&igrave;nh giải quyết bồi thường mới l&agrave; yếu tố mấu chốt quyết định việc kh&aacute;ch h&agrave;ng c&oacute; t&aacute;i tục hợp đồng hay l&agrave; &ldquo;một đi kh&ocirc;ng trở lại&rdquo;. V&igrave; vậy, c&aacute;c DN bảo hiểm phi nh&acirc;n thọ trong nước đang chạy đua với nhau v&agrave; chạy đua với c&aacute;c c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ nước ngo&agrave;i về chất lượng v&agrave; thời gian tiếp nhận v&agrave; giải quyết bồi thường cho kh&aacute;ch h&agrave;ng.</p>\r\n<p>Phụ tr&aacute;ch Maketing một c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ trong nước chia sẻ, c&aacute;c ph&ograve;ng ban trong c&ocirc;ng ty vừa nhận được chỉ thị từ l&atilde;nh đạo DN, từ nay đến cuối năm 2012, tập trung nh&acirc;n lực v&agrave; nguồn lực v&agrave;o thực hiện cam kết với kh&aacute;ch h&agrave;ng, bồi thường nhanh ch&oacute;ng, đ&uacute;ng, đủ theo tinh thần đ&atilde; n&ecirc;u tại c&aacute;c cuộc gặp gỡ kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c hồi giữa năm. &ldquo;Ch&iacute;nh v&igrave; thế, hiện nay cả c&ocirc;ng ty phải tập trung v&agrave;o hỗ trợ Ban Thẩm định giải quyết tồn đọng, bồi thường, c&ocirc;ng t&aacute;c maketing sẽ tạm ngưng...&rdquo;, vị phụ tr&aacute;ch tr&ecirc;n n&oacute;i.</p>\r\n<p style=\"text-align: right;\"><em><strong>(Nguồn ĐTCK)</strong></em></p>', '2018-03-22 02:19:27', '2018-03-22 13:19:27', 'images/news/1520823680_pic-New-i_635400839328010000_HasThumb_Thumb.jpg.png'),
(277, 5, 'Bảo hiểm 2013, triển khai thực hiện 6 nhóm giải pháp', 'bao-hiem-2013-trien-khai-thuc-hien-6-nhom-giai-phap', '<p><span style=\"font-family: Arial;\">Mu&ocirc;n v&agrave;n kh&oacute; khăn, đ&oacute; l&agrave; nhận x&eacute;t chung về thị trường bảo hiểm năm 2012 m&agrave; ĐTCK ghi nhận tại buổi lễ kỷ niệm 19 năm ng&agrave;y truyền thống ng&agrave;nh bảo hiểm diễn ra tối 17/12 tại H&agrave; Nội.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, b&ecirc;n cạnh kh&oacute; khăn, vẫn c&oacute; những điều đ&aacute;ng để ch&uacute;c mừng.</span></p>\r\n<p><img src=\"../../../uploads/635400841097420000.jpg\" alt=\"\" width=\"620\" height=\"410\" /></p>\r\n<p>Trao đổi với ĐTCK b&ecirc;n lề buổi lễ kỷ niệm, đại diện c&aacute;c DN bảo hiểm chia sẻ về mu&ocirc;n v&agrave;n kh&oacute; khăn, DN buộc phải &ldquo;gồng&rdquo; m&igrave;nh để c&oacute; thể đạt được kế hoạch kinh doanh đề ra trong năm 2012. L&atilde;nh đạo Cục Quản l&yacute; gi&aacute;m s&aacute;t bảo hiểm (Bộ T&agrave;i ch&iacute;nh) nhận định, năm 2013 sẽ c&ograve;n kh&oacute; hơn.</p>\r\n<p>Mặc d&ugrave; vậy, theo &ocirc;ng Trịnh Quang Tuyến, Chủ tịch Hiệp hội Bảo hiểm Việt Nam (AVI), với tr&aacute;ch nhiệm với x&atilde; hội, từng DN bảo hiểm đ&atilde; v&agrave; đang t&igrave;m c&aacute;c giải ph&aacute;p để vượt qua thử th&aacute;ch, nắm bắt cơ hội mới, khắc phục hạn chế, yếu k&eacute;m trong những năm qua, đầu tư an to&agrave;n hiệu quả, đem lại kết quả kinh doanh tốt hơn năm 2011.</p>\r\n<p>Cụ thể, năm 2012, tổng doanh thu ph&iacute; bảo hiểm to&agrave;n thị trường ước đạt 40.591 tỷ đồng, tăng 11% so với năm 2011; trong đ&oacute;, doanh thu ph&iacute; bảo hiểm phi nh&acirc;n thọ ước đạt 22.675 tỷ đồng, tăng 10,2% v&agrave; doanh thu ph&iacute; bảo hiểm nh&acirc;n thọ ước đạt 17.916, tăng 12%. To&agrave;n ng&agrave;nh bảo hiểm đ&atilde; đầu tư gần 91.000 tỷ đồng v&agrave;o nền kinh tế quốc d&acirc;n, g&oacute;p phần ph&aacute;t triển kinh tế đất nước, bảo đảm an sinh x&atilde; hội. C&ugrave;ng với đ&oacute;, c&aacute;c DN bảo hiểm tiến h&agrave;nh t&aacute;i cơ cấu, đảm bảo bi&ecirc;n khả năng thanh to&aacute;n, danh mục đầu tư an to&agrave;n, hiệu quả; xử l&yacute; nợ kh&oacute; đ&ograve;i (trong đ&oacute; c&oacute; nợ ph&iacute; bảo hiểm) v&agrave; t&aacute;i cơ cấu vốn chủ sở hữu, giảm dần vốn của c&aacute;c tập đo&agrave;n, tổng c&ocirc;ng ty nh&agrave; nước tại c&aacute;c DN bảo hiểm; lựa chọn đối t&aacute;c chiến lược tham gia g&oacute;p vốn.</p>\r\n<p>Trao đổi với ĐTCK, &ocirc;ng Trịnh Thanh Hoan, Cục trưởng Cục Quản l&yacute; gi&aacute;m s&aacute;t bảo hiểm (Bộ T&agrave;i ch&iacute;nh) cho rằng, về cơ bản, hoạt động t&aacute;i cấu tr&uacute;c c&aacute;c DN bảo hiểm được thực hiện thường xuy&ecirc;n, gắn liền với c&ocirc;ng t&aacute;c quản l&yacute;, gi&aacute;m s&aacute;t của cơ quan quản l&yacute; nh&agrave; nước về kinh doanh bảo hiểm. Tuy nhi&ecirc;n, để hoạt động n&agrave;y được thực hiện b&agrave;i bản v&agrave; đầy đủ, trong năm 2013, cần nghi&ecirc;n cứu x&acirc;y dựng v&agrave; ban h&agrave;nh Th&ocirc;ng tư quy định ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; to&agrave;n diện rủi ro hoạt động kinh doanh, mức độ an to&agrave;n t&agrave;i ch&iacute;nh v&agrave; c&ocirc;ng t&aacute;c quản trị điều h&agrave;nh của DN bảo hiểm. Tr&ecirc;n cơ sở c&aacute;c quy định tại Th&ocirc;ng tư, tiến h&agrave;nh đ&aacute;nh gi&aacute;, ph&acirc;n loại DN v&agrave; &aacute;p dụng c&aacute;c biện ph&aacute;p t&aacute;i cấu tr&uacute;c đối với từng nh&oacute;m DN.</p>\r\n<p>Về lĩnh vực bảo hiểm nh&acirc;n thọ, &ocirc;ng Takashi Fujii, Chủ tịch ki&ecirc;m Tổng gi&aacute;m đốc Dai-ichi Life Việt Nam nhấn mạnh: &ldquo;Với đặc th&ugrave; l&agrave; ng&agrave;nh kinh doanh trung v&agrave; d&agrave;i hạn, DN bảo hiểm nh&acirc;n thọ phải c&oacute; tầm nh&igrave;n v&agrave; chiến lược ph&aacute;t triển trong v&ograve;ng 10 - 20 năm tới, kh&ocirc;ng để kh&oacute; khăn che mất cơ hội. Kinh tế Việt Nam năm 2013 chắc chắn c&ograve;n nhiều kh&oacute; khăn, nhưng t&ocirc;i tin rằng, bảo hiểm nh&acirc;n thọ vẫn giữ được ưu thế l&agrave; một k&ecirc;nh đầu tư an to&agrave;n&rdquo;.</p>\r\n<p>Theo AVI, mặc d&ugrave; c&ograve;n nhiều kh&oacute; khăn, nhưng năm 2013, ng&agrave;nh bảo hiểm vẫn đặt ra chỉ ti&ecirc;u tăng trưởng ở mức hai con số: doanh thu ph&iacute; bảo hiểm phi nh&acirc;n thọ tăng 10 - 12%, doanh thu ph&iacute; bảo hiểm nh&acirc;n thọ tăng 12 - 14%.</p>\r\n<p>Về ph&iacute;a cơ quan quản l&yacute;, &ocirc;ng Trịnh Thanh Hoan cho biết, để tiếp tục khơi th&ocirc;ng tiềm lực của ng&agrave;nh bảo hiểm trong năm tới, mới đ&acirc;y, Bộ T&agrave;i ch&iacute;nh đ&atilde; k&yacute; Quyết định số 2330/QĐ-BTC về thực hiện c&aacute;c giải ph&aacute;p ph&aacute;t triển thị trường bảo hiểm giai đoạn 2011 - 2015. Theo đ&oacute;, năm 2013 l&agrave; năm đầu ti&ecirc;n triển khai thực hiện 6 nh&oacute;m giải ph&aacute;p chủ đạo đề ra trong Quyết định số 2330/QĐ-BTC l&agrave;: ho&agrave;n thiện hệ thống ph&aacute;p luật trong lĩnh vực kinh doanh bảo hiểm; n&acirc;ng cao t&iacute;nh an to&agrave;n hệ thống, hiệu quả hoạt động v&agrave; năng lực cạnh tranh của c&aacute;c DN bảo hiểm; khuyến kh&iacute;ch, hỗ trợ c&aacute;c DN ph&aacute;t triển v&agrave; đa dạng h&oacute;a sản phẩm bảo hiểm; đa dạng v&agrave; chuy&ecirc;n nghiệp h&oacute;a c&aacute;c k&ecirc;nh ph&acirc;n phối bảo hiểm; tăng cường hiệu quả quản l&yacute; của nh&agrave; nước về kinh doanh bảo hiểm; đẩy mạnh hợp t&aacute;c quốc tế v&agrave; hội nhập trong lĩnh vực bảo hiểm.</p>\r\n<p><strong>&Ocirc;ng Trịnh Quang Tuyến, Chủ tịch Hiệp hội Bảo hiểm Việt Nam (AVI)</strong></p>\r\n<p><img src=\"../../../uploads/635400841097660000.jpg\" alt=\"\" width=\"200\" height=\"258\" /></p>\r\n<p>Năm 2012, c&aacute;c tiểu ban b&aacute;n chuy&ecirc;n tr&aacute;ch của AVI đ&atilde; nghi&ecirc;n cứu x&acirc;y dựng ph&iacute; cơ bản v&agrave; th&ocirc;ng tin cảnh b&aacute;o rủi ro để b&aacute;o c&aacute;o Bộ T&agrave;i ch&iacute;nh c&oacute; giải ph&aacute;p hạn chế cạnh tranh kh&ocirc;ng l&agrave;nh mạnh bằng giảm ph&iacute;, mở rộng điều khoản bảo hiểm, ảnh hưởng tới khả năng thanh to&aacute;n của DN bảo hiểm; x&acirc;y dựng thuật ngữ cơ bản trong bảo hiểm nh&acirc;n thọ v&agrave; đang x&acirc;y dựng thuật ngữ trong bảo hiểm phi nh&acirc;n thọ; sử dụng cơ sở dữ liệu Lloyd\'s MIU phục vụ cho c&aacute;c DN bảo hiểm tra cứu thực trạng t&agrave;u v&agrave; h&agrave;ng được bảo hiểm; tổ tư vấn bảo hiểm n&ocirc;ng nghiệp đ&atilde; c&oacute; nhiều kiến nghị để Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n sửa đổi Th&ocirc;ng tư 47, Bộ T&agrave;i ch&iacute;nh ban h&agrave;nh Quyết định ph&ecirc; duyệt sản phẩm bảo hiểm với những nội dung ph&ugrave; hợp với thực tế sau 1 năm triển khai th&iacute; điểm.</p>\r\n<p>Hiện tại, AVI đang tiến h&agrave;nh x&acirc;y dựng cơ sở dữ liệu quản l&yacute; đại l&yacute; bảo hiểm nh&acirc;n thọ, phi nh&acirc;n thọ, ph&aacute;t h&agrave;nh cẩm nang tuy&ecirc;n truyền hướng dẫn bảo hiểm bắt buộc tr&aacute;ch nhiệm d&acirc;n sự của chủ xe cơ giới, ph&aacute;t h&agrave;nh kỷ yếu Hiệp hội Bảo hiểm Việt Nam, ph&aacute;t h&agrave;nh cuốn c&aacute;c văn bản ph&aacute;p quy về hoạt động kinh doanh bảo hiểm v&agrave; x&acirc;y dựng phần mềm thống k&ecirc; b&aacute;o c&aacute;o số liệu của c&aacute;c DN bảo hiểm.</p>', '2018-03-22 02:20:13', '2018-03-22 13:20:13', 'images/news/1520825415_pic-New-i_635400841098210000_HasThumb_Thumb.jpg.png'),
(278, 5, 'Năm 2013, kênh bán bảo hiểm qua ngân hàng sẽ khởi sắc', 'nam-2013-kenh-ban-bao-hiem-qua-ngan-hang-se-khoi-sac', '<p><span style=\"font-family: Arial;\">Ở ch&acirc;u &Aacute;, trong v&ograve;ng 5 - 6 năm trở lại đ&acirc;y, b&aacute;n bảo hiểm qua ng&acirc;n h&agrave;ng (bancassurance) đ&atilde; đạt được ph&aacute;t triển kh&aacute; nhanh.&nbsp;</span></p>', '<p><strong><span style=\"font-family: Arial;\">Ở ch&acirc;u &Aacute;, trong v&ograve;ng 5 - 6 năm trở lại đ&acirc;y, b&aacute;n bảo hiểm qua ng&acirc;n h&agrave;ng (bancassurance) đ&atilde; đạt được ph&aacute;t triển kh&aacute; nhanh. Ở một v&agrave;i thị trường, việc b&aacute;n sản phẩm bảo hiểm qua k&ecirc;nh n&agrave;y đ&atilde; chiếm tới 60% tổng doanh thu ph&iacute;. Nhận thấy tiềm năng to lớn như vậy, Tổng c&ocirc;ng ty Bảo Việt Nh&acirc;n thọ đang đẩy mạnh ph&aacute;t triển k&ecirc;nh n&agrave;y. B&ecirc;n cạnh đ&oacute;, việc Sumitomo Life trở th&agrave;nh đối t&aacute;c chiến lược của Tập đo&agrave;n Bảo Việt đang được kỳ vọng sẽ đưa lại nhiều điểm mới cho thị trường bảo hiểm Việt Nam cũng như ph&acirc;n kh&uacute;c mới mẻ n&agrave;y.</span></strong></p>\r\n<p><img src=\"../../../uploads/635400843078440000.jpg\" alt=\"\" width=\"596\" height=\"445\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Năm 2013, Tổng c&ocirc;ng ty Bảo Việt Nh&acirc;n thọ đẩy mạnh ph&aacute;t triển k&ecirc;nh bancassurance</span></em></p>\r\n<p>D&ugrave; được triển khai từ nhiều năm nay, nhưng k&ecirc;nh ph&acirc;n bancassurance vẫn đ&oacute;ng g&oacute;p doanh thu rất khi&ecirc;m tốn so với c&aacute;c k&ecirc;nh ph&acirc;n phối kh&aacute;c. Số liệu thống k&ecirc; cho thấy, nếu như bảo hiểm qua ng&acirc;n h&agrave;ng ở Trung Quốc đ&atilde; rất ph&aacute;t triển, chiếm khoảng 40% trong c&aacute;c k&ecirc;nh ph&acirc;n phối bảo hiểm th&igrave; tỷ lệ n&agrave;y ở Việt Nam mới chỉ ở mức 5%.</p>\r\n<p>Một số chuy&ecirc;n gia bảo hiểm cho biết, tại Việt Nam, ph&aacute;t triển k&ecirc;nh b&aacute;n h&agrave;ng mới mẻ n&agrave;y đang được kh&ocirc;ng chỉ c&aacute;c DN bảo hiểm nh&acirc;n thọ m&agrave; cả c&aacute;c DN phi nh&acirc;n thọ quan t&acirc;m đầu tư. C&aacute;c DN trong ng&agrave;nh bảo hiểm đều nhận thức được rằng, nếu kh&ocirc;ng tập trung ph&aacute;t triển k&ecirc;nh n&agrave;y th&igrave; sau n&agrave;y sẽ kh&oacute; cạnh tranh được.</p>\r\n<p>Tuy nhi&ecirc;n, một thực tế hiện nay l&agrave; dường như DN bảo hiểm phi nh&acirc;n thọ đang th&agrave;nh c&ocirc;ng hơn so với khối bảo hiểm nh&acirc;n thọ trong việc hợp t&aacute;c với c&aacute;c ng&acirc;n h&agrave;ng. Theo đ&oacute;, việc b&aacute;n sản phẩm bảo hiểm phi nh&acirc;n thọ qua k&ecirc;nh bancasurance kh&aacute; tốt do sản phẩm bảo hiểm phi nh&acirc;n thọ thường đơn giản, dễ b&aacute;n, mặt kh&aacute;c c&aacute;c ng&acirc;n h&agrave;ng cũng muốn b&aacute;n sản phẩm bảo hiểm phi nh&acirc;n thọ để bảo vệ vốn vay cho ch&iacute;nh ng&acirc;n h&agrave;ng. Thậm ch&iacute;, số liệu cho thấy sản phẩm y tế c&oacute; doanh thu b&aacute;n h&agrave;ng kh&aacute; cao, c&oacute; ng&acirc;n h&agrave;ng b&aacute;n được tới 700 tỷ đồng/năm.</p>\r\n<p>Tuy nhi&ecirc;n, k&ecirc;nh bancasurance đều ph&aacute;t triển chưa tương xứng với tiềm năng v&agrave; mức đầu tư của c&aacute;c DN. Chia sẻ về điều n&agrave;y, &ocirc;ng Nguyễn Đức Tuấn, Tổng gi&aacute;m đốc Tổng c&ocirc;ng ty Bảo Việt Nh&acirc;n thọ cho biết: &ldquo;10 năm trước, Bảo Việt Nh&acirc;n thọ đ&atilde; hợp t&aacute;c với Techcombank ph&acirc;n phối sản phẩm bảo hiểm. Nh&igrave;n lại qu&aacute; tr&igrave;nh hợp t&aacute;c, ch&uacute;ng t&ocirc;i nhận thấy kết quả chưa tương xứng với tiềm năng của hai b&ecirc;n. Điều n&agrave;y xuất ph&aacute;t từ nhiều l&yacute; do, cả do sản phẩm, c&aacute;ch b&aacute;n h&agrave;ng, chiến lược ph&aacute;t triển của hai b&ecirc;n chưa gặp nhau&hellip;&rdquo;</p>\r\n<p>Tr&ecirc;n một g&oacute;c nh&igrave;n kh&aacute;c, một số chuy&ecirc;n gia bảo hiểm cho rằng, hiện nhiều ng&acirc;n h&agrave;ng của Việt Nam theo m&ocirc; h&igrave;nh tập đo&agrave;n t&agrave;i ch&iacute;nh - bảo hiểm, do vậy mỗi ng&acirc;n h&agrave;ng đều c&oacute; DN bảo hiểm của ri&ecirc;ng m&igrave;nh, n&ecirc;n khi phối hợp với DN bảo hiểm kh&aacute;c th&igrave; c&oacute; vẻ như kh&ocirc;ng được l&acirc;u d&agrave;i. Hơn thế, lợi nhuận từ việc b&aacute;n sản phẩm bảo hiểm c&ograve;n thấp so với c&aacute;c sản phẩm ng&acirc;n h&agrave;ng, khiến một số ng&acirc;n h&agrave;ng kh&ocirc;ng qu&aacute; &ldquo;mặn m&agrave;&rdquo; với việc ph&acirc;n phối sản phẩm bảo hiểm.</p>\r\n<p>Tuy nhi&ecirc;n, &ocirc;ng Nguyễn Đức Tuấn cho rằng với c&aacute;c sản phẩm bảo hiểm nh&acirc;n thọ được đầu tư kỹ c&agrave;ng của Tổng c&ocirc;ng ty Bảo Việt Nh&acirc;n thọ, DN n&agrave;y kỳ vọng, trong thời gian tới, doanh thu b&aacute;n bảo hiểm qua k&ecirc;nh bancasurance sẽ được cải thiện. Điều n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể xảy ra khi mới đ&acirc;y, Tập đo&agrave;n bảo hiểm nh&acirc;n thọ Sumitomo Life (Nhật Bản) trở th&agrave;nh đối t&aacute;c chiến lược của Tập đo&agrave;n Bảo Việt, được kỳ vọng đem lại luồng gi&oacute; mới cho ph&acirc;n kh&uacute;c bancassurance n&oacute;i ri&ecirc;ng v&agrave; thị trường bảo hiểm Việt Nam n&oacute;i chung. Bởi với lịch sử tr&ecirc;n 100 năm ph&aacute;t triển, ngo&agrave;i k&ecirc;nh đại l&yacute; b&aacute;n h&agrave;ng truyền thống, Sumitomo Life c&ograve;n l&agrave; doanh nghiệp bảo hiểm nh&acirc;n thọ h&agrave;ng đầu về ph&aacute;t triển k&ecirc;nh bancassurance c&oacute; quan hệ hợp t&aacute;c với hơn 300 ng&acirc;n h&agrave;ng v&agrave; c&aacute;c tổ chức t&agrave;i ch&iacute;nh. &Ocirc;ng Yoshio Sato, Tổng Gi&aacute;m đốc Sumitomo Life cũng cho biết: &ldquo;Với vị thế l&agrave; doanh nghiệp bảo hiểm nh&acirc;n thọ h&agrave;ng đầu về ph&aacute;t triển k&ecirc;nh bancassurance với quan hệ hợp t&aacute;c với hơn 300 ng&acirc;n h&agrave;ng v&agrave; c&aacute;c tổ chức t&agrave;i ch&iacute;nh tr&ecirc;n thế giới, Sumitomo Life sẽ c&oacute; kế hoạch hỗ trợ cụ thể, đồng thời sẽ chia sẻ những b&iacute; quyết, kinh nghiệm với Bảo Việt đưa ra c&aacute;c sản phẩm ph&ugrave; hợp với nhu cầu của thị trường bảo hiểm Việt Nam&rdquo;.</p>\r\n<p style=\"text-align: right;\">Nguồn: <strong>tapchitaichinh.vn</strong></p>', '2018-03-22 02:20:43', '2018-03-22 13:20:43', 'images/news/1520825516_pic-New-i_635400843085270000_HasThumb_Thumb.jpg.png'),
(279, 5, 'Cháy nhiều vẫn quên… bảo hiểm', 'chay-nhieu-van-quen-bao-hiem', '<p><span style=\"font-family: Arial;\">Thời gian qua, c&aacute;c vụ ch&aacute;y nổ li&ecirc;n tiếp xảy ra, g&acirc;y thiệt hại kh&ocirc;ng nhỏ về người v&agrave; t&agrave;i sản. Tuy nhi&ecirc;n, người d&acirc;n chưa thực sự mặn m&agrave; với loại h&igrave;nh bảo hiểm ch&aacute;y nổ.&nbsp;</span></p>', '<p>Thời gian qua, c&aacute;c vụ ch&aacute;y nổ li&ecirc;n tiếp xảy ra, g&acirc;y thiệt hại kh&ocirc;ng nhỏ về người v&agrave; t&agrave;i sản. Tuy nhi&ecirc;n, người d&acirc;n chưa thực sự mặn m&agrave; với loại h&igrave;nh bảo hiểm ch&aacute;y nổ.<br /> <br />&ldquo;Lỗi&rdquo; do b&ecirc;n bảo hiểm<br />Bảo hiểm nh&agrave; tư nh&acirc;n l&agrave; sản phẩm trọn g&oacute;i, cung cấp hai giải ph&aacute;p bảo hiểm cho ng&ocirc;i nh&agrave; v&agrave; t&agrave;i sản b&ecirc;n trong ng&ocirc;i nh&agrave;, &aacute;p dụng cho tất cả đối tượng kh&aacute;ch h&agrave;ng (chủ nh&agrave; hoặc kh&aacute;ch thu&ecirc; nh&agrave;). Tham gia chương tr&igrave;nh, kh&aacute;ch h&agrave;ng sẽ được bồi thường c&aacute;c chi ph&iacute; thiệt hại cho ng&ocirc;i nh&agrave; v&agrave; t&agrave;i sản b&ecirc;n trong, trong trường hợp ch&aacute;y, nổ, s&eacute;t đ&aacute;nh v&agrave; c&aacute;c rủi ro mở rộng kh&aacute;c như gi&ocirc;ng, b&atilde;o, lũ lụt, vỡ/tr&agrave;n nước, trộm cướp&hellip; Ngo&agrave;i ra, kh&aacute;ch h&agrave;ng c&ograve;n được hỗ trợ một khoản chi ph&iacute; thu&ecirc; nh&agrave; sau tổn thất.</p>\r\n<p>Sản phẩm bảo hiểm nh&agrave; tư nh&acirc;n đang được hầu hết DN bảo hiểm phi nh&acirc;n thọ triển khai. Tuy nhi&ecirc;n, c&aacute;c DN mới chỉ tiếp cận được tới số &iacute;t kh&aacute;ch h&agrave;ng th&ocirc;ng qua đại l&yacute; hoặc ng&acirc;n h&agrave;ng cho vay vốn. Doanh thu ph&iacute; c&ograve;n rất thấp, chưa tương xứng với tiềm năng của thị trường.</p>\r\n<p><img src=\"../../../uploads/635400844110520000.jpg\" alt=\"\" width=\"738\" height=\"494\" /></p>\r\n<p style=\"text-align: center;\"><em>Rủi ro ch&aacute;y nổ lu&ocirc;n thường trực kh&ocirc;ng chỉ tại c&aacute;c DN m&agrave; c&ograve;n với tất cả c&aacute;c hộ d&acirc;n</em></p>\r\n<p>Nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; do sản phẩm bảo hiểm c&ograve;n kh&aacute; &ldquo;phức tạp&rdquo; đối với kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n, cũng như đội ngũ b&aacute;n h&agrave;ng: thủ tục k&ecirc; khai gi&aacute; trị t&agrave;i sản phức tạp, phạm vi bảo hiểm cố định chưa linh hoạt, quyền lợi mở rộng chưa đ&aacute;p ứng nhu cầu kh&aacute;ch h&agrave;ng, việc định gi&aacute; t&agrave;i sản khi ph&aacute;t sinh tổn thất kh&oacute; khăn theo cơ sở bảo hiểm dưới gi&aacute; trị v&agrave; t&iacute;nh khấu hao, dẫn đến thời gian giải quyết khiếu nại k&eacute;o d&agrave;i.<br />Hơn nữa, người d&acirc;n chưa c&oacute; th&oacute;i quen tham gia bảo hiểm nh&agrave; tư nh&acirc;n, cũng như chưa đ&aacute;nh gi&aacute; đ&uacute;ng nguy cơ rủi ro, chưa hiểu hết được quyền lợi v&agrave; sự cần thiết của loại h&igrave;nh bảo hiểm n&agrave;y để bảo vệ t&agrave;i sản c&aacute; nh&acirc;n v&agrave; gia đ&igrave;nh.<br /> <br /><strong>Thu h&uacute;t người d&acirc;n mua bảo hiểm, c&aacute;ch n&agrave;o?</strong><br />Nh&igrave;n nhận nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y ra sự thờ ơ của người d&acirc;n xuất ph&aacute;t từ l&yacute; do sản phẩm, một số DN bảo hiểm đ&atilde; nỗ lực cải tiến sản phẩm theo hướng tối đa h&oacute;a lợi &iacute;ch kh&aacute;ch h&agrave;ng: sản phẩm linh hoạt, thủ tục đơn giản với quyền lợi bồi thường thay thế mới hấp dẫn.</p>\r\n<p>Chẳng hạn, Tổng c&ocirc;ng ty Bảo hiểm Bảo Việt đ&atilde; ph&aacute;t triển sản phẩm bảo hiểm nh&agrave; tư nh&acirc;n mới, với 3 ưu thế nổi bật. Thứ nhất, thủ tục đăng k&yacute; tham gia bảo hiểm đơn giản, số tiền tham gia bảo hiểm theo giới hạn bồi thường lựa chọn tr&ecirc;n cơ sở ước t&iacute;nh của kh&aacute;ch h&agrave;ng, m&agrave; kh&ocirc;ng cần đ&aacute;nh gi&aacute; gi&aacute; trị thực tế của ng&ocirc;i nh&agrave; tại thời điểm mua bảo hiểm v&agrave; kh&ocirc;ng cần k&ecirc; khai danh mục t&agrave;i sản b&ecirc;n trong. Thứ hai, phạm vi bảo hiểm được thiết kế linh hoạt theo sự t&ugrave;y chọn của kh&aacute;ch h&agrave;ng: phạm vi bảo hiểm cơ bản v&agrave; c&aacute;c phạm vi bảo hiểm mở rộng hợp l&yacute;, dễ d&agrave;ng lựa chọn v&agrave; tiết kiệm chi ph&iacute; cho kh&aacute;ch h&agrave;ng. Thứ ba, cơ sở bồi thường l&agrave; gi&aacute; trị thay thế mới cho cả ng&ocirc;i nh&agrave; v&agrave; t&agrave;i sản b&ecirc;n trong. Đ&acirc;y ch&iacute;nh l&agrave; điểm đổi mới của sản phẩm, đồng bộ với việc lựa chọn giới hạn bồi thường tham gia bảo hiểm, nhằm gia tăng tối đa quyền lợi cho kh&aacute;ch h&agrave;ng, đơn giản h&oacute;a thủ tục khai th&aacute;c, cấp đơn v&agrave; giải quyết bồi thường.</p>\r\n<p>Sản phẩm Ph&uacute;c An Gia của Bảo hiểm PTI cũng được thiết kế với rất nhiều quyền lợi, tiện &iacute;ch hơn so với c&aacute;c sản phẩm th&ocirc;ng thường kh&aacute;c như: bồi thường đầy đủ gi&aacute; trị thay mới đối với t&agrave;i sản b&ecirc;n trong ng&ocirc;i nh&agrave;, chi trả tiền bảo hiểm trong v&ograve;ng 15 ng&agrave;y, t&iacute;ch hợp th&ecirc;m chương tr&igrave;nh bảo hiểm tai nạn cho c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh, thanh to&aacute;n to&agrave;n bộ chi ph&iacute; dọn dẹp hiện trường, chi ph&iacute; cứu hỏa v&agrave; chi ph&iacute; dập lửa kh&aacute;c&hellip;</p>\r\n<p>Theo l&atilde;nh đạo PTI, sự ra đời sản phẩm bảo hiểm nh&agrave; tư nh&acirc;n Ph&uacute;c An Gia l&agrave; nỗ lực cải tiến sản phẩm của PTI theo hướng tối đa h&oacute;a lợi &iacute;ch cho kh&aacute;ch h&agrave;ng. Dự kiến, trong thời gian tới, PTI sẽ tiếp tục triển khai nhiều sản phẩm kh&aacute;c nhằm đem lại cho kh&aacute;ch h&agrave;ng những sản phẩm thiết thực v&agrave; to&agrave;n diện.</p>\r\n<p style=\"text-align: right;\"><strong>Lam B&igrave;nh</strong><br />Nguồn: tinnhanhchungkhoan.vn</p>', '2018-03-22 02:21:10', '2018-03-22 13:21:10', 'images/news/1520825693_pic-New-i_635400844119680000_HasThumb_Thumb.jpg.png'),
(280, 5, 'Bảo hiểm phi nhân thọ nỗ lực vượt khó', 'bao-hiem-phi-nhan-tho-no-luc-vuot-kho', '<p><span style=\"font-family: Arial;\">X&aacute;c định năm 2013 tiếp tục l&agrave; một năm đầy kh&oacute; khăn đối với lĩnh vực bảo hiểm phi nh&acirc;n thọ, ngay từ những th&aacute;ng đầu năm, c&aacute;c doanh nghiệp bảo hiểm khối n&agrave;y đ&atilde; dồn sức cho c&aacute;c khối kinh doanh.&nbsp;</span></p>', '<p>X&aacute;c định năm 2013 tiếp tục l&agrave; một năm đầy kh&oacute; khăn đối với lĩnh vực bảo hiểm phi nh&acirc;n thọ, ngay từ những th&aacute;ng đầu năm, c&aacute;c doanh nghiệp bảo hiểm khối n&agrave;y đ&atilde; dồn sức cho c&aacute;c khối kinh doanh.<br /> <br />Kh&ocirc;ng chỉ đầu tư cho sản phẩm, hệ thống ph&acirc;n phối, một số doanh nghiệp đ&atilde; bắt tay hợp t&aacute;c, để tạo ra những cơ hội kinh doanh mới.</p>\r\n<p>Trao đổi với ĐTCK, đại diện một số doanh nghiệp bảo hiểm phi nh&acirc;n thọ cho biết, t&igrave;nh h&igrave;nh kinh doanh 2 th&aacute;ng đầu năm vẫn chưa c&oacute; nhiều t&iacute;n hiệu khả quan, d&ugrave; c&aacute;c doanh nghiệp đ&atilde; đưa ra nhiều chương tr&igrave;nh khuyến mại hấp dẫn đối với một số sản phẩm b&aacute;n lẻ chủ lực.</p>\r\n<p>Theo đại diện C&ocirc;ng ty Bảo hiểm Cathay, so với c&ugrave;ng kỳ năm 2012, C&ocirc;ng ty vẫn đạt mức tăng trưởng doanh thu 100%, nhưng nếu so với doanh thu mục ti&ecirc;u th&igrave; mới đạt khoảng 75 - 80%. &Aacute;p lực tăng trưởng doanh thu ph&iacute; bảo hiểm 240% so với năm 2012 đang đ&egrave; nặng l&ecirc;n C&ocirc;ng ty trong những qu&yacute; tiếp theo. Năm nay, Bảo hiểm Cathay đặt kế hoạch doanh thu ph&iacute; bảo hiểm 120 tỷ đồng, so với mức thực hiện năm 2012 l&agrave; 50 tỷ đồng.</p>\r\n<p><img src=\"../../../uploads/635400844541840000.jpg\" alt=\"\" width=\"737\" height=\"412\" /></p>\r\n<p style=\"text-align: center;\"><em>2 th&aacute;ng đầu năm, doanh thu ph&iacute; bảo hiểm của Liberty tăng 14% so với c&ugrave;ng kỳ</em></p>\r\n<p>Tr&aacute;i ngược với t&igrave;nh h&igrave;nh kinh doanh c&ograve;n kh&aacute; ảm đạm của c&aacute;c doanh nghiệp bảo hiểm tr&ecirc;n, đại diện Bảo hiểm Liberty lại tỏ ra kh&aacute; lạc quan. Được biết, tổng doanh thu ph&iacute; bảo hiểm 2 th&aacute;ng đầu năm của C&ocirc;ng ty tăng 14% so với c&ugrave;ng kỳ, nhờ th&agrave;nh c&ocirc;ng trong việc đẩy mạnh khai th&aacute;c c&aacute;c sản phẩm bảo hiểm sức khỏe v&agrave; bảo hiểm t&agrave;i sản. D&ugrave; sức mua của thị trường &ocirc; t&ocirc; tiếp tục suy giảm, nhưng Liberty vẫn đạt mức tăng trưởng doanh thu từ mảng bảo hiểm &ocirc; t&ocirc; l&agrave; 7% so với c&ugrave;ng kỳ 2012. Tỷ lệ bồi thường cũng giảm đ&aacute;ng kể do quản l&yacute; tốt rủi ro. Những t&iacute;n hiệu khả quan trong hoạt động kinh doanh đang cho ph&eacute;p Liberty hy vọng sẽ r&uacute;t ngắn 1 năm so với mục ti&ecirc;u đến cuối năm 2014 sẽ đạt điểm h&ograve;a vốn.</p>\r\n<p>C&ugrave;ng với việc tiếp tục c&oacute; những chương tr&igrave;nh khuyến mại hay chăm s&oacute;c kh&aacute;ch h&agrave;ng để k&iacute;ch cầu sức mua, c&aacute;c c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ tiếp tục thực hiện c&aacute;c kế hoạch n&acirc;ng cao chất lượng phục vụ kh&aacute;ch h&agrave;ng. Cuối th&aacute;ng 2/2013, CTCP Bảo hiểm Bưu điện (PTI) đ&atilde; ra mắt Trung t&acirc;m Bồi thường bảo hiểm chăm s&oacute;c sức khỏe. Trung t&acirc;m c&oacute; nhiệm vụ điều h&agrave;nh v&agrave; quản l&yacute; c&ocirc;ng t&aacute;c bồi thường sản phẩm bảo hiểm chăm s&oacute;c sức khỏe theo hướng tập trung đầu mối tại 3 khu vực Bắc - Trung - Nam. Đ&acirc;y l&agrave; bước đi tiếp theo trong kế hoạch n&acirc;ng cao chất lượng phục vụ kh&aacute;ch h&agrave;ng của PTI trong năm 2013.</p>\r\n<p>Trong khi đ&oacute;, BIC cũng vừa tổ chức th&agrave;nh c&ocirc;ng Hội nghị triển khai kế hoạch kinh doanh năm 2013, với mục ti&ecirc;u đạt 850 tỷ đồng doanh thu ph&iacute; bảo hiểm, lợi nhuận trước thuế 121 tỷ đồng, cổ tức năm 2012 ở mức 10%. Để đạt được những mục ti&ecirc;u kh&aacute; th&aacute;ch thức n&agrave;y, BIC sẽ cơ cấu c&aacute;c danh mục sản phẩm bảo hiểm, tăng cường c&ocirc;ng t&aacute;c quản trị rủi ro, giảm tỷ lệ bồi thường, cải thiện c&ocirc;ng t&aacute;c dịch vụ kh&aacute;ch h&agrave;ng, mở rộng ph&aacute;t triển thương hiệu&hellip;</p>\r\n<p>Động th&aacute;i mới nhất tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ l&agrave; ng&agrave;y 7/3/2013, CTCP Bảo Minh đ&atilde; k&yacute; kết thỏa thuận hợp t&aacute;c to&agrave;n diện với Bảo hiểm Ph&uacute; Hưng (PAC). Với việc trở th&agrave;nh đối t&aacute;c chiến lược của nhau, hai b&ecirc;n kỳ vọng sẽ nh&acirc;n l&ecirc;n lợi thế của nhau trong hoạt động kinh doanh bảo hiểm gốc (từ kh&acirc;u khai th&aacute;c, đ&aacute;nh gi&aacute; quản l&yacute; rủi ro, cho tới gi&aacute;m định tổn thất); t&aacute;i bảo hiểm (bao gồm nhận v&agrave; nhượng t&aacute;i bảo hiểm); đầu tư t&agrave;i ch&iacute;nh; đ&agrave;o tạo nghiệp vụ v&agrave; quản trị dự &aacute;n&hellip; Đại diện Bảo Minh cho biết, trước những kh&oacute; khăn, th&aacute;ch thức của nền kinh tế th&igrave; nhu cầu hợp t&aacute;c để c&ugrave;ng nhau ph&aacute;t triển sẽ l&agrave; xu hướng m&agrave; c&aacute;c doanh nghiệp n&oacute;i chung v&agrave; doanh nghiệp bảo hiểm phi nh&acirc;n thọ n&oacute;i ri&ecirc;ng đều &aacute;p dụng.</p>\r\n<p style=\"text-align: right;\"><strong> Gia Linh</strong><br />Nguồn: tinnhanhchungkhoan.vn</p>', '2018-03-22 02:21:42', '2018-03-22 13:21:42', 'images/news/1520825789_pic-New-i_635400844544480000_HasThumb_Thumb.jpg.png');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(281, 5, 'Bảo hiểm phi nhân thọ: CEO mới, chiến lược mới', 'bao-hiem-phi-nhan-tho-ceo-moi-chien-luoc-moi', '<p><span style=\"font-family: Arial;\">Kh&ocirc;ng chỉ Bảo hiểm AAA, cả PJICO hay Viễn Đ&ocirc;ng khi thay đổi CEO đều gắn với chiến lược t&aacute;i cơ cấu mạnh mẽ.</span></p>', '<p><span style=\"font-family: Arial;\">Kh&ocirc;ng chỉ Bảo hiểm AAA, cả PJICO hay Viễn Đ&ocirc;ng khi thay đổi CEO đều gắn với chiến lược t&aacute;i cơ cấu mạnh mẽ.</span></p>\r\n<p><img src=\"../../../uploads/635400845131080000.jpg\" alt=\"\" width=\"739\" height=\"537\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">PJICO đang lấy &yacute; kiến cổ đ&ocirc;ng về việc t&aacute;i cơ cấu v&agrave; n&acirc;ng cấp PJICO th&agrave;nh Tổng c&ocirc;ng ty</span></em></p>\r\n<p>Cuối tuần qua, Bảo hiểm AAA đ&atilde; ch&iacute;nh thức th&ocirc;ng b&aacute;o bổ nhiệm &ocirc;ng Jon Delalande l&agrave;m Tổng gi&aacute;m đốc mới sau khi b&agrave; Đỗ Thị Kim Li&ecirc;n, người s&aacute;ng lập Bảo hiểm AAA, quyết định về hưu. Vị CEO mới gia nhập Bảo hiểm AAA từ Tập đo&agrave;n Bảo hiểm &Uacute;c (IAG), một trong số c&aacute;c cổ đ&ocirc;ng của Bảo hiểm AAA, từng c&oacute; hơn 20 năm kinh nghiệm trong lĩnh vực bảo hiểm, trong đ&oacute; c&oacute; 6 năm l&agrave;m việc tại thị trường ch&acirc;u &Aacute;.</p>\r\n<p>Quyết định ch&iacute;nh thức thay Tổng gi&aacute;m đốc mới của Bảo hiểm AAA kh&ocirc;ng l&agrave;m thị trường qu&aacute; ngạc nhi&ecirc;n, bởi trước đ&oacute; đ&atilde; c&oacute; nhiều đồn đo&aacute;n về sự thay đổi n&agrave;y. Bảo hiểm AAA cũng kh&ocirc;ng phải c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ duy nhất tr&ecirc;n thị trường &ldquo;thay tướng&rdquo; ngay trong những th&aacute;ng đầu năm 2013. Trước đ&oacute;, Bảo hiểm PJICO th&ocirc;ng b&aacute;o &ocirc;ng Đ&agrave;o Nam Hải sẽ ch&iacute;nh thức giữ chức Tổng gi&aacute;m đốc PJICO nhiệm kỳ 5 năm, kể từ ng&agrave;y 1/3/2013.</p>\r\n<p>Bảo hiểm Viễn Đ&ocirc;ng cũng c&oacute; t&acirc;n Tổng gi&aacute;m đốc từ th&aacute;ng 2/2013. Quyền Tổng gi&aacute;m đốc Nguyễn Quang Vinh của Bảo hiểm Viễn Đ&ocirc;ng kh&ocirc;ng phải nh&acirc;n vật qu&aacute; xa lạ với thị trường bảo hiểm, bởi &ocirc;ng Vinh đ&atilde; từng giữ chức Ph&oacute; tổng gi&aacute;m đốc của Bảo hiểm AAA. L&agrave;n s&oacute;ng thay đổi CEO c&oacute; lẽ vẫn chưa dừng lại, bởi một c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ c&oacute; vốn nước ngo&agrave;i cũng đ&atilde; c&oacute; kế hoạch v&agrave; chỉ chờ Bộ T&agrave;i ch&iacute;nh ch&iacute;nh thức ph&ecirc; duyệt phương &aacute;n thay đổi CEO.</p>\r\n<p>Trao đổi với Đầu tư Chứng kho&aacute;n, đại diện một số c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ cho rằng, d&ugrave; c&ograve;n triển vọng nhưng kinh doanh bảo hiểm phi nh&acirc;n thọ những năm gần đ&acirc;y v&agrave; c&oacute; thể những năm tới sẽ kh&ocirc;ng c&ograve;n &ldquo;dễ thở&rdquo; như trước. C&ugrave;ng với những kh&oacute; khăn chung của thị trường l&agrave; cạnh tranh khốc liệt, trục lợi bảo hiểm gia tăng, kh&aacute;ch h&agrave;ng ng&agrave;y c&agrave;ng kh&oacute; t&iacute;nh hơn&hellip;, th&igrave; bản th&acirc;n c&aacute;c c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ, đặc biệt l&agrave; những c&ocirc;ng ty bảo hiểm ở nh&oacute;m dưới cũng c&oacute; những vấn đề của ri&ecirc;ng m&igrave;nh l&agrave; &aacute;p lực kh&ocirc;ng thể tiếp tục lỗ.</p>\r\n<p>Dự b&aacute;o về triển vọng ng&agrave;nh bảo hiểm năm 2013 được Ersnt&amp;Young đưa ra mới đ&acirc;y cũng nhận định rằng, rất nhiều c&ocirc;ng ty đang phải đối mặt với những quyết định kh&oacute; khăn, trong đ&oacute; c&oacute; việc t&aacute;i c&acirc;n bằng hệ sản phẩm dựa tr&ecirc;n sự ph&acirc;n bổ nguồn vốn, x&aacute;c định hoạt động kinh doanh v&agrave; d&ograve;ng sản phẩm n&agrave;o cần gỡ bỏ v&agrave; chiến lược t&aacute;i bảo hiểm n&agrave;o l&agrave; tối ưu&hellip; Một trong những th&aacute;ch thức nữa cho nhiều thị trường tại khu vực ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương l&agrave; khả năng thanh to&aacute;n bảo hiểm, do vậy chi ph&iacute; tăng th&ecirc;m cho t&aacute;i bảo hiểm thường kh&ocirc;ng phải l&agrave; lựa chọn được c&acirc;n nhắc.</p>\r\n<p>Đứng trước &aacute;p lực bắt buộc phải h&ograve;a vốn trong thời gian sớm nhất, c&aacute;c c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ phải ki&ecirc;n quyết thay đổi chiến lược. Trong buổi lễ chuyển giao vị tr&iacute; CEO, b&agrave; Đỗ Thị Kim Li&ecirc;n - người s&aacute;ng lập Bảo hiểm AAA n&oacute;i rằng, trong giai đoạn ph&aacute;t triển tiếp theo, để đạt được những mục ti&ecirc;u đ&atilde; đặt ra, trở th&agrave;nh c&ocirc;ng ty bảo hiểm được t&ocirc;n trọng nhất tại Việt Nam, Bảo hiểm AAA cần phải chuy&ecirc;n nghiệp hơn trong hoạt động của m&igrave;nh. V&agrave; c&aacute;ch duy nhất để C&ocirc;ng ty c&oacute; thể đạt được mục đ&iacute;ch l&agrave; phải thay đổi mạnh mẽ.</p>\r\n<p>&ldquo;Phải thay đổi để cải thiện những tồn tại c&ograve;n hiện hữu, cản bước ph&aacute;t triển của Bảo hiểm AAA&rdquo;, cựu Tổng gi&aacute;m đốc AAA gửi gắm đến thế hệ l&atilde;nh đạo sau của C&ocirc;ng ty.</p>\r\n<p>Kh&ocirc;ng chỉ Bảo hiểm AAA, cả PJICO hay Viễn Đ&ocirc;ng khi thay đổi CEO đều gắn với chiến lược t&aacute;i cơ cấu mạnh mẽ. Bảo hiểm Viễn Đ&ocirc;ng trước khi c&oacute; CEO mới cũng đ&atilde; c&oacute; một cuộc cải tổ với sự tham gia g&oacute;p vốn đầu tư của cổ đ&ocirc;ng mới l&agrave; Bamboo Capital - sự thay đổi n&agrave;y l&agrave; một bước chuẩn bị cho kế hoạch t&aacute;i cơ cấu mới.</p>\r\n<p>Trong khi đ&oacute;, c&ugrave;ng với quyết định thay đổi nh&acirc;n sự cấp cao, hiện PJICO cũng đang thực hiện lấy &yacute; kiến biểu quyết của cổ đ&ocirc;ng về việc đồng &yacute; thực hiện t&aacute;i cơ cấu v&agrave; n&acirc;ng cấp PJICO th&agrave;nh Tổng CTCP Bảo hiểm Petrolimex. C&ocirc;ng ty n&agrave;y do Petrolimex nắm giữ tr&ecirc;n 50% vốn điều lệ (C&ocirc;ng ty c&oacute; 709 tỷ đồng vốn điều lệ) v&agrave; l&agrave; doanh nghiệp th&agrave;nh vi&ecirc;n của tập đo&agrave;n n&agrave;y.</p>\r\n<p>Đại diện một doanh nghiệp bảo hiểm sắp c&oacute; CEO mới cũng tiết lộ, c&ocirc;ng ty sẽ c&oacute; nhiều thay đổi trong thời gian tới. Theo kế hoạch l&agrave; đến cuối năm sau, c&ocirc;ng ty mới h&ograve;a vốn, nhưng c&oacute; lẽ kế hoạch n&agrave;y sẽ ho&agrave;n th&agrave;nh sớm hơn v&igrave; t&igrave;nh h&igrave;nh doanh thu v&agrave; quản l&yacute; chi ph&iacute; những th&aacute;ng đầu năm đang tiến triển khả quan.</p>\r\n<p style=\"text-align: right;\"><strong>Ngọc Lan</strong></p>\r\n<p style=\"text-align: right;\">Nguồn: <em>tinnhanhchungkhoan.vn</em></p>', '2018-03-22 02:22:15', '2018-03-22 13:22:15', 'images/news/1520825881_pic-New-i_635400845132150000_HasThumb_Thumb.jpg.png'),
(282, 5, 'Giảm thiểu nỗi lo nhân viên “nhảy việc”', 'giam-thieu-noi-lo-nhan-vien-nhay-viec', '<p><span style=\"font-family: Arial;\">Mức lương cơ bản cao c&oacute; thể thu h&uacute;t nh&acirc;n nh&acirc;n vi&ecirc;n mới, nhưng sẽ kh&ocirc;ng thể giữ ch&acirc;n họ v&agrave; c&oacute; thể sẽ kh&ocirc;ng mang lại lợi &iacute;ch tốt nhất.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Mức lương cơ bản cao c&oacute; thể thu h&uacute;t nh&acirc;n nh&acirc;n vi&ecirc;n mới, nhưng sẽ kh&ocirc;ng thể giữ ch&acirc;n họ v&agrave; c&oacute; thể sẽ kh&ocirc;ng mang lại lợi &iacute;ch tốt nhất.</span></p>\r\n<p><img src=\"../../../uploads/635400846446000000.jpg\" alt=\"\" width=\"739\" height=\"489\" /></p>\r\n<p>C&oacute; rất nhiều l&yacute; do dẫn đến mức độ gắn b&oacute; với c&ocirc;ng ty thấp, nhưng một nguy&ecirc;n nh&acirc;n quan trọng l&agrave; sự kh&aacute;c biệt ng&agrave;y c&agrave;ng tăng giữa mong muốn của nh&acirc;n vi&ecirc;n v&agrave; của c&aacute;c c&ocirc;ng ty. Th&ocirc;ng tin tổng qu&aacute;t từ Cuộc khảo s&aacute;t Hiệu quả nguồn nh&acirc;n lực tại ch&acirc;u &Aacute;-Th&aacute;i B&igrave;nh Dương năm 2011 của Price waterhouse Coopers Saratoga (PwC) cho thấy, triết l&yacute; trả lương v&agrave; cơ cấu đ&atilde;i ngộ để giữ ch&acirc;n nh&acirc;n t&agrave;i đang ng&agrave;y c&agrave;ng thay đổi. Mức lương cơ bản cao c&oacute; thể thu h&uacute;t nh&acirc;n vi&ecirc;n mới, nhưng kh&ocirc;ng thể giữ ch&acirc;n họ v&agrave; c&oacute; thể sẽ kh&ocirc;ng mang lại lợi &iacute;ch tốt nhất. Mức lương cơ bản cao mới chỉ dừng lại ở việc thu h&uacute;t nh&acirc;n t&agrave;i. Yếu tố quyết định để nh&acirc;n t&agrave;i c&oacute; gắn b&oacute; l&acirc;u d&agrave;i với một doanh nghiệp hay kh&ocirc;ng phụ thuộc kh&aacute; nhiều v&agrave;o chế độ ph&uacute;c lợi tăng th&ecirc;m cho nh&acirc;n vi&ecirc;n.</p>\r\n<p>Theo một số khảo s&aacute;t về nguồn nh&acirc;n lực tại thị trường Việt Nam, c&aacute;c doanh nghiệp Việt Nam đang gặp phải vấn đề tương tự trong việc quản l&yacute; nguồn nh&acirc;n lực. Khảo s&aacute;t cho thấy, 75% c&ocirc;ng ty tại Việt Nam mong muốn ph&aacute;t triển đội ngũ lao động th&ecirc;m 20% trong những năm tới, nhưng lại phải đối mặt thường xuy&ecirc;n với việc nh&acirc;n sự thay đổi, đặc biệt l&agrave; lực lượng lao động thuộc thế hệ trẻ đang chiếm tr&ecirc;n 50% lực lượng lao động Việt Nam. Nhiều c&ocirc;ng ty c&ograve;n thiếu c&aacute;c chiến lược d&agrave;i hạn trong việc quản l&yacute; nguồn nh&acirc;n lực, trong đ&oacute; c&aacute;c ch&iacute;nh s&aacute;ch đ&atilde;i ngộ tăng th&ecirc;m ph&ugrave; hợp như bảo hiểm chăm s&oacute;c sức khỏe, tai nạn cho nh&acirc;n vi&ecirc;n v&agrave; người th&acirc;n trong gia đ&igrave;nh của họ c&oacute; vai tr&ograve; đặc biệt quan trọng.</p>\r\n<p>Thực tế, loại h&igrave;nh bảo hiểm ph&uacute;c lợi nh&acirc;n vi&ecirc;n đ&atilde; được &aacute;p dụng th&agrave;nh c&ocirc;ng tại ch&acirc;u &Acirc;u v&agrave; c&aacute;c nước c&oacute; nền kinh tế ph&aacute;t triển từ l&acirc;u, nhưng vẫn c&ograve;n kh&aacute; mới mẻ tại Việt Nam. Đến nay, mặc d&ugrave; chưa c&oacute; thống k&ecirc; về số lượng c&aacute;c doanh nghiệp đang hoạt động tại thị trường Việt Nam thường xuy&ecirc;n mua bảo hiểm ph&uacute;c lợi cho nh&acirc;n vi&ecirc;n, nhưng thị trường n&agrave;y đang c&oacute; những chuyển động t&iacute;ch cực. Th&ocirc;ng tin từ Generali Việt Nam, c&ocirc;ng ty bảo hiểm nh&acirc;n thọ đầu ti&ecirc;n tại thị trường Việt Nam được Bộ T&agrave;i ch&iacute;nh cấp ph&eacute;p cung cấp bảo hiểm ph&uacute;c lợi cho nh&acirc;n vi&ecirc;n v&agrave; người th&acirc;n trong gia đ&igrave;nh từ th&aacute;ng 10/2011 cho thấy, kết quả kh&aacute; khả quan. Hiện nay, số lượng th&agrave;nh vi&ecirc;n được bảo hiểm sức khỏe, bảo hiểm rủi ro tử vong v&agrave; tai nạn với Generali Việt Nam đ&atilde; l&ecirc;n đến hơn 7.700 người, bao gồm nh&acirc;n vi&ecirc;n đang l&agrave;m việc tại c&aacute;c doanh nghiệp uy t&iacute;n trong v&agrave; ngo&agrave;i nước v&agrave; những người phụ thuộc của họ.</p>\r\n<p>Kh&ocirc;ng chỉ c&aacute;c doanh nghiệp nước ngo&agrave;i, m&agrave; ch&iacute;nh c&aacute;c doanh nghiệp Việt Nam cũng đang ng&agrave;y c&agrave;ng thấu hiểu rằng, việc đầu tư chiến lược v&agrave;o nh&acirc;n t&agrave;i c&oacute; thể gi&uacute;p cải thiện r&otilde; rệt hiệu quả hoạt động của c&ocirc;ng ty. Khảo s&aacute;t của PwC cũng đưa ra con số kh&aacute; cụ thể về việc đầu tư v&agrave;o nh&acirc;n t&agrave;i sẽ l&agrave;m tỷ lệ lợi nhuận/nh&acirc;n vi&ecirc;n tăng th&ecirc;m hơn 20% mỗi năm; tỷ lệ tăng trưởng doanh thu c&oacute; thể gia tăng hơn 20% mỗi năm; tỷ lệ ứng cử vi&ecirc;n chọn nhận việc cao hơn v&agrave; tỷ lệ nh&acirc;n vi&ecirc;n nghỉ việc cũng thấp hơn&hellip;</p>\r\n<p>Theo &ocirc;ng Simon Lam, Tổng gi&aacute;m đốc Generali Việt Nam, với năng lực chuy&ecirc;n m&ocirc;n sẵn c&oacute; v&agrave; lợi thế từ Tập đo&agrave;n Generali đang dẫn đầu thị trường bảo hiểm nh&oacute;m tr&ecirc;n thế giới, mục ti&ecirc;u của Generali Việt Nam l&agrave; mang đến những lựa chọn tốt hơn cho kh&aacute;ch h&agrave;ng v&agrave; đưa ra những chuẩn mực tr&ecirc;n thị trường bảo hiểm nh&oacute;m tại Việt Nam. Để g&oacute;p phần ph&aacute;t triển ng&agrave;nh nh&acirc;n sự Việt Nam, th&ocirc;ng qua đ&oacute; tăng th&ecirc;m ph&uacute;c lợi mang đến cho người lao động, th&aacute;ng 10/2012, Generali Việt Nam đ&atilde; tổ chức Hội thảo \"Tối ưu h&oacute;a chương tr&igrave;nh g&igrave;n giữ nh&acirc;n t&agrave;i với Generali Việt Nam\", thu h&uacute;t sự tham gia của hơn 100 c&aacute;n bộ, chuy&ecirc;n vi&ecirc;n nh&acirc;n sự. Ng&agrave;y 6/3/2013, Generali Việt Nam phối hợp c&ugrave;ng C&acirc;u lạc bộ Nh&acirc;n sự Việt Nam (Vietnam Human Resources-VNHR) tổ chức Hội thảo tại Kh&aacute;ch sạn Rex với chủ đề &ldquo;Ph&uacute;c lợi gia tăng cho nh&acirc;n vi&ecirc;n&rdquo; (Flexible Employee Benefits), th&agrave;nh phần tham dự bao gồm c&aacute;c c&aacute;n bộ, chuy&ecirc;n vi&ecirc;n, người l&agrave;m c&ocirc;ng t&aacute;c nh&acirc;n sự tại c&aacute;c doanh nghiệp, c&ocirc;ng ty. Generali Việt Nam v&agrave; C&acirc;u lạc bộ Nh&acirc;n sự Việt Nam dự kiến sẽ tổ chức Hội thảo Nh&acirc;n sự tại H&agrave; Nội v&agrave;o th&aacute;ng 5/2013.</p>\r\n<p>Với những kết quả hoạt động đ&aacute;ng kh&iacute;ch lệ v&agrave; tiềm năng ph&aacute;t triển tại Việt Nam, mới đ&acirc;y, Generali Việt Nam đ&atilde; được Bộ T&agrave;i ch&iacute;nh chấp thuận tăng vốn điều lệ l&ecirc;n 800 tỷ đồng. Đ&acirc;y l&agrave; đợt tăng vốn điều lệ lần thứ ba của Generali Việt Nam kể từ khi C&ocirc;ng ty ch&iacute;nh thức hoạt động tại Việt Nam v&agrave;o th&aacute;ng 4/2011. Sự kiện n&agrave;y một lần nữa đ&atilde; chứng tỏ cam kết hoạt động l&acirc;u d&agrave;i, cũng như niềm tin v&agrave;o tiềm năng của thị trường bảo hiểm nh&acirc;n thọ Việt Nam của Tập đo&agrave;n Generali.<br />&Ocirc;ng Simon Lam cho biết, trong năm 2013, C&ocirc;ng ty sẽ tiếp tục thực hiện chiến lược k&ecirc;nh ph&acirc;n phối đa dạng. Đ&acirc;y l&agrave; đặc trưng rất ri&ecirc;ng của Generali Việt Nam hoạt động cả trong lĩnh vực bảo hiểm nh&acirc;n thọ c&aacute; nh&acirc;n v&agrave; bảo hiểm nh&oacute;m cho doanh nghiệp. Với chiến lược n&agrave;y, C&ocirc;ng ty tận dụng được kinh nghiệm chuy&ecirc;n m&ocirc;n đặc th&ugrave; của Tập đo&agrave;n Generali trong lĩnh vực bảo hiểm nh&oacute;m, đồng thời ph&aacute;t triển hoạt động bảo hiểm c&aacute; nh&acirc;n tại Việt Nam. Đối với bảo hiểm nh&oacute;m d&agrave;nh cho doanh nghiệp, Generali Việt Nam sẽ tiếp tục ch&uacute; trọng ph&aacute;t triển sản phẩm bảo hiểm ph&uacute;c lợi nh&acirc;n vi&ecirc;n, đ&aacute;p ứng nhu cầu của c&aacute;c doanh nghiệp trong v&agrave; ngo&agrave;i nước, đồng thời củng cố mối quan hệ gắn b&oacute; với kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c bảo l&atilde;nh viện ph&iacute;.</p>\r\n<p>Đối với bảo hiểm nh&acirc;n thọ d&agrave;nh cho kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n, C&ocirc;ng ty sẽ tăng cường đẩy mạnh m&ocirc; h&igrave;nh ch&uacute; trọng chất lượng hiện nay để hỗ trợ cho hoạt động của đội ngũ tư vấn bảo hiểm ở quy m&ocirc; lớn hơn, bao gồm việc n&acirc;ng cao chất lượng tuyển dụng, đ&agrave;o tạo v&agrave; ph&aacute;t triển, huấn luyện, quản trị hiệu quả để hỗ trợ c&aacute;c tư vấn bảo hiểm. C&aacute;ch tiếp cận n&agrave;y được hỗ trợ bởi sự đầu tư l&acirc;u d&agrave;i v&agrave;o c&ocirc;ng t&aacute;c n&acirc;ng cao năng lực của đội ngũ đại l&yacute; để Generali Việt Nam c&oacute; thể đạt được lợi thế cạnh tranh v&agrave; ph&aacute;t triển bền vững. C&ocirc;ng ty cũng đang triển khai mở rộng hoạt động ra thị trường H&agrave; Nội trong năm 2013.</p>\r\n<p style=\"text-align: right;\"><strong>Ngọc Lan</strong></p>\r\n<p style=\"text-align: right;\">Nguồn: <em>tinnhanhchungkhoan.vn</em></p>', '2018-03-22 02:22:46', '2018-03-22 13:22:46', 'images/news/1520826070_pic-New-i_635400846452440000_HasThumb_Thumb.jpg.png'),
(283, 5, 'Rửa tiền qua bảo hiểm: Không dễ', 'rua-tien-qua-bao-hiem-khong-de', '<p><span style=\"font-family: Arial;\">(ĐTCK) Trong năm 2012, Cục Quản l&yacute; gi&aacute;m s&aacute;t bảo hiểm đ&atilde; nhận được b&aacute;o c&aacute;o về 2 hợp đồng c&oacute; dấu hiệu rửa tiền.&nbsp;</span></p>', '<p><span style=\"font-family: Arial;\">Tại hội nghị tập huấn về ph&ograve;ng chống rửa tiền do một ng&acirc;n h&agrave;ng thương mại tổ chức vừa qua, đại diện Ng&acirc;n h&agrave;ng Nh&agrave; nước đ&atilde; cảnh b&aacute;o, tội phạm rửa tiền thường nhắm v&agrave;o hệ thống t&agrave;i ch&iacute;nh &ndash; ng&acirc;n h&agrave;ng để biến số tiền c&oacute; nguồn gốc bất minh th&agrave;nh tiền sạch. Rửa tiền qua c&aacute;c hợp đồng bảo hiểm, đặc biệt l&agrave; c&aacute;c hợp đồng bảo hiểm nh&acirc;n thọ tại Việt Nam đang c&oacute; nguy cơ cao.</span></p>\r\n<p><img src=\"../../../uploads/635400847130880000.jpg\" alt=\"\" width=\"620\" height=\"446\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Theo quy định, hợp đồng bảo hiểm c&oacute; số ph&iacute; từ 400 triệu đồng trở l&ecirc;n phải b&aacute;o c&aacute;o NHNN</span></em></p>\r\n<p><strong>Nhiều nguy cơ</strong></p>\r\n<p>Cục Quản l&yacute;, gi&aacute;m s&aacute;t bảo hiểm (Bộ T&agrave;i ch&iacute;nh) cho biết, trong năm 2012, Cục đ&atilde; nhận được b&aacute;o c&aacute;o 2 hợp đồng bảo hiểm l&agrave; giao dịch đ&aacute;ng ngờ v&agrave; đ&atilde; kịp thời chuyển đến Trung t&acirc;m Ph&ograve;ng chống rửa tiền để xử l&yacute; theo quy định của ph&aacute;p luật. D&ugrave; số hợp đồng bảo hiểm c&oacute; dấu hiệu rửa tiền bị ph&aacute;t hiện c&ograve;n rất &iacute;t, nhưng nguy cơ rửa tiền qua c&aacute;c hợp đồng bảo hiểm l&agrave; kh&ocirc;ng nhỏ.</p>\r\n<p>Theo một chuy&ecirc;n gia của Ernst&amp;Young, hợp đồng bảo hiểm nh&acirc;n thọ c&oacute; thể bị lợi dụng l&agrave;m phương tiện rửa tiền thường nằm trong c&aacute;c trường hợp: tiền ph&iacute; bảo hiểm được đem đi đầu tư; c&aacute;c đơn bảo hiểm nh&acirc;n thọ với một mức ph&iacute; duy nhất c&oacute; bản chất lưu giữ gi&aacute; trị tiền mặt; bảo hiểm ni&ecirc;n kim cố định hoặc thay đổi; hay đơn bảo hiểm chuyển nhượng được v&agrave; c&oacute; thể d&ugrave;ng để thế chấp ng&acirc;n h&agrave;ng&hellip; C&aacute;c c&ocirc;ng ty bảo hiểm cũng c&oacute; thể nghi ngờ một số loại dịch vụ bảo hiểm phi nh&acirc;n thọ đ&atilde; bị hoạt động rửa tiền lợi dụng nếu ph&aacute;t hiện việc đ&ograve;i bồi thường số tiền khổng lồ hoặc cho những vụ được d&agrave;n dựng. Chẳng hạn, cố &yacute; g&acirc;y ra hỏa hoạn hay c&aacute;c c&aacute;ch kh&aacute;c để đ&ograve;i bồi thường nhằm thu hồi một phần số tiền bất hợp ph&aacute;p đ&atilde; đầu tư; việc hủy hợp đồng bảo hiểm để lấy lại ph&iacute; bảo hiểm bằng s&eacute;c của c&ocirc;ng ty bảo hiểm hoặc như việc trả qu&aacute; số tiền ph&iacute; bảo hiểm, sau đ&oacute; y&ecirc;u cầu trả lại số tiền đ&oacute;ng qu&aacute;&hellip; l&agrave; những dấu hiệu đ&aacute;ng ngờ.</p>\r\n<p><strong>DN bảo hiểm tăng cường &ldquo;chốt chặn&rdquo;</strong></p>\r\n<p>Để ngăn chặn qu&aacute; tr&igrave;nh rửa tiền, theo c&aacute;c chuy&ecirc;n gia, c&aacute;c c&ocirc;ng ty bảo hiểm, đặc biệt với c&aacute;c c&ocirc;ng ty bảo hiểm nh&acirc;n thọ cần kiểm so&aacute;t nghi&ecirc;m ngặt &ldquo;đầu v&agrave;o&rdquo;, trước khi cấp hợp đồng bảo hiểm, đặc biệt với những hợp đồng bảo hiểm c&oacute; số ph&iacute; cao. Với những hợp đồng chỉ tồn tại một thời gian ngắn rồi bị hủy, cũng thường bị đưa v&agrave;o diện nghi vấn rửa tiền.</p>\r\n<p>Tuy nhi&ecirc;n, trao đổi với ĐTCK, đại diện một doanh nghiệp bảo hiểm nh&acirc;n thọ lớn cho biết, khả năng hợp đồng chỉ tồn tại trong thời gian ngắn l&agrave; thuộc đối tượng c&oacute; nghi vấn rửa tiền l&agrave; rất thấp. Th&ocirc;ng thường, kh&aacute;ch h&agrave;ng hủy hợp đồng l&agrave; do bị tăng ph&iacute; bảo hiểm sau khi c&oacute; kết quả về kh&aacute;m sức khỏe. &ldquo;Ở c&ocirc;ng ty, bất cứ hợp đồng bảo hiểm n&agrave;o c&oacute; số ph&iacute; tr&ecirc;n 200 triệu đồng đều được b&aacute;o c&aacute;o về c&ocirc;ng ty mẹ v&agrave; Bộ T&agrave;i ch&iacute;nh theo như quy định&rdquo;, vị đại diện tr&ecirc;n cho biết. Thực tế, ngo&agrave;i quy định ri&ecirc;ng của từng c&ocirc;ng ty bảo hiểm, c&aacute;c cơ quan chức năng cũng c&oacute; những quy định bắt buộc phải thực hiện đối với những hợp đồng bảo hiểm c&oacute; gi&aacute; trị lớn, c&oacute; số ph&iacute; đ&oacute;ng 400 triệu đồng trở l&ecirc;n l&agrave; phải b&aacute;o c&aacute;o l&ecirc;n Cục Ph&ograve;ng chống rửa tiền - Ng&acirc;n h&agrave;ng Nh&agrave; nước để thẩm định kiểm tra. Nếu cơ quan n&agrave;y nghi ngờ c&oacute; dấu hiệu rửa tiền sẽ y&ecirc;u cầu điều tra kỹ lưỡng. Ngo&agrave;i ra, c&aacute;c cơ quan chức năng cũng quy định việc kh&aacute;ch h&agrave;ng mua bảo hiểm nh&acirc;n thọ kh&ocirc;ng được đ&oacute;ng một lần ph&iacute; cao hơn 80% mệnh gi&aacute; hợp đồng....</p>\r\n<p>Đại diện một doanh nghiệp bảo hiểm nh&acirc;n thọ nước ngo&agrave;i cho biết, ngo&agrave;i những y&ecirc;u cầu bắt buộc phải thực hiện theo quy định của ph&aacute;p luật, C&ocirc;ng ty cũng quy định, với tất cả sản phẩm bảo hiểm, kh&aacute;ch h&agrave;ng kh&ocirc;ng được đ&oacute;ng qu&aacute; 5 lần ph&iacute; tối thiểu. Nếu hợp đồng c&oacute; số ph&iacute; đ&oacute;ng một lần khoảng 200 triệu đồng hoặc 10 ng&agrave;n USD th&igrave; đ&atilde; phải đưa v&agrave;o diện &ldquo;điều tra ngầm&rdquo;.</p>\r\n<p>&ldquo;Thực tế, ngo&agrave;i việc kiểm s&oacute;at vấn đề rửa tiền, c&aacute;c c&ocirc;ng ty bảo hiểm cũng rất lo ngại vấn đề trục lợi bảo hiểm với những hợp đồng c&oacute; mệnh gi&aacute; lớn&rdquo;, vị đại diện tr&ecirc;n cho biết.</p>\r\n<p style=\"text-align: right;\"><strong>Gia Linh</strong><br />(Theo ĐTCK)</p>', '2018-03-22 02:23:14', '2018-03-22 13:23:14', 'images/news/1520826162_pic-New-i_635400847133250000_HasThumb_Thumb.jpg.png'),
(284, 5, 'Kiểm soát nội bộ: Bắt buộc hay không bắt buộc?', 'kiem-soat-noi-bo-bat-buoc-hay-khong-bat-buoc', '<p><span style=\"font-family: Arial;\">(ĐTCK) Mang t&iacute;nh h&igrave;nh thức, chưa được quan t&acirc;m đ&uacute;ng mức, chưa thực hiện triệt để&hellip; l&agrave; nhận x&eacute;t chung d&agrave;nh cho hầu hết DN bảo hiểm trong hoạt động kiểm so&aacute;t nội bộ.&nbsp;</span></p>', '<p><img src=\"../../../uploads/635400847539600000.jpg\" alt=\"\" width=\"620\" height=\"455\" /></p>\r\n<p><span style=\"font-family: Arial;\">PVI l&agrave; một trong những DN đ&atilde; th&agrave;nh lập bộ phận kiểm tra, kiểm to&aacute;n nội bộ theo hướng dẫn của Bộ T&agrave;i ch&iacute;nh</span></p>\r\n<p><strong>Kh&ocirc;ng r&otilde; r&agrave;ng</strong></p>\r\n<p>Sau c&aacute;c văn bản hướng dẫn của Bộ T&agrave;i ch&iacute;nh, nhiều doanh nghiệp bảo hiểm đ&atilde; th&agrave;nh lập bộ phận kiểm tra, kiểm to&aacute;n nội bộ như Bảo Việt, Bảo Minh, Bảo hiểm PVI, Bảo hiểm Pjico, Bảo Long, Viễn Đ&ocirc;ng, AAA, Ph&uacute; Hưng, PTI, To&agrave;n Cầu.... Tuy nhi&ecirc;n, hoạt động của bộ m&aacute;y kiểm tra, kiểm to&aacute;n nội bộ tại từng doanh nghiệp c&oacute; sự kh&aacute;c nhau (m&ocirc; h&igrave;nh tổ chức, quy m&ocirc; hoạt động); hiệu quả của c&ocirc;ng t&aacute;c kiểm tra, kiểm to&aacute;n nội bộ tại từng doanh nghiệp cũng kh&aacute;c nhau.</p>\r\n<p>Kết quả điều tra sơ bộ về khung kiểm so&aacute;t nội bộ (KSNB) hiện nay tại 16 c&ocirc;ng ty bảo hiểm nh&acirc;n thọ v&agrave; phi nh&acirc;n thọ đang hoạt động tại Việt Nam, được tiến h&agrave;nh bởi Ernst &amp; Young Việt Nam mới đ&acirc;y cho thấy, b&ecirc;n cạnh một v&agrave;i dấu hiệu khả quan, vẫn c&ograve;n nhiều vấn đề cần cải thiện.</p>\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i nhận thấy, c&aacute;c c&ocirc;ng ty hầu hết đều c&oacute; những văn bản mi&ecirc;u tả chức năng v&agrave; tr&aacute;ch nhiệm của ph&ograve;ng quản trị rủi ro, ph&ograve;ng KSNB, ph&ograve;ng kiểm to&aacute;n nội bộ, nhưng nhiều văn bản n&agrave;y kh&ocirc;ng thực sự r&otilde; r&agrave;ng hoặc kh&ocirc;ng được th&ocirc;ng tin đến c&aacute;c cổ đ&ocirc;ng, dẫn đến việc thiếu t&iacute;nh r&otilde; r&agrave;ng v&agrave; minh bạch trong vai tr&ograve; v&agrave; tr&aacute;ch nhiệm của từng ph&ograve;ng cụ thể trong tổng thể khung KSNB của tổ chức, cũng như tr&aacute;ch nhiệm của từng ph&ograve;ng trong mối quan hệ v&agrave; hợp t&aacute;c với c&aacute;c ph&ograve;ng c&ograve;n lại nhằm hướng đến mục ti&ecirc;u chung của chức năng KSNB. Kh&ocirc;ng c&oacute; g&igrave; kh&oacute; hiểu khi trong một số trường hợp, c&aacute;c ph&ograve;ng, ban n&agrave;y kh&ocirc;ng hoạt động&rdquo;, đại diện Enrst &amp; Young Việt Nam n&oacute;i.</p>\r\n<p>C&ocirc;ng t&aacute;c kiểm tra, KSNB của c&aacute;c DN bảo hiểm cũng được &ocirc;ng Trịnh Thanh Hoan, Cục trưởng Cục Quản l&yacute; gi&aacute;m s&aacute;t bảo hiểm (Bộ T&agrave;i ch&iacute;nh) cho biết l&agrave; c&ograve;n yếu, chưa được quan t&acirc;m đ&uacute;ng mức v&agrave; chưa được thực hiện thường xuy&ecirc;n. C&aacute;c quy tr&igrave;nh nghiệp vụ tuy được x&acirc;y dựng, song vẫn mang t&iacute;nh h&igrave;nh thức, chưa được triển khai thực hiện triệt để trong thực tiễn, dẫn đến hoạt động kinh doanh bảo hiểm của một số DN trong nước chưa chuy&ecirc;n nghiệp, b&agrave;i bản.</p>\r\n<p>Theo Ernst &amp; Young Việt Nam, trong c&aacute;c DN bảo hiểm được điều tra, chỉ c&oacute; 31% c&ocirc;ng ty c&oacute; ủy ban kiểm to&aacute;n v&agrave; 19% c&ocirc;ng ty c&oacute; ban kiểm so&aacute;t (kiểm so&aacute;t vi&ecirc;n), với vai tr&ograve; v&agrave; tr&aacute;ch nhiệm bao gồm gi&aacute;m s&aacute;t kiểm to&aacute;n nội bộ n&oacute;i ri&ecirc;ng v&agrave; khung KSNB n&oacute;i chung. Th&ecirc;m v&agrave;o đ&oacute;, ở một số c&ocirc;ng ty, t&iacute;nh độc lập của ủy ban kiểm to&aacute;n v&agrave; kiểm to&aacute;n nội bộ kh&ocirc;ng được xem x&eacute;t đầy đủ, khiến cho ủy ban kiểm to&aacute;n v&agrave; kiểm to&aacute;n nội bộ vẫn b&aacute;o c&aacute;o l&ecirc;n Ban điều h&agrave;nh. Điều n&agrave;y cản trở t&iacute;nh hiệu quả của vai tr&ograve; v&agrave; hoạt động trong việc cung cấp sự đảm bảo kh&aacute;ch quan, trung thực của việc thực hiện KSNB của c&ocirc;ng ty.</p>\r\n<p>Điểm khả quan thuộc về một tr&aacute;ch nhiệm kh&aacute;c của HĐQT (HĐTV). C&oacute; 83% c&ocirc;ng ty bảo hiểm được điều tra, HĐQT (HĐTV) đ&atilde; thực hiện đ&aacute;nh gi&aacute; h&agrave;ng năm Ban điều h&agrave;nh v&agrave; c&aacute;c vị tr&iacute; quản l&yacute; chủ chốt. Tuy nhi&ecirc;n, đ&aacute;nh gi&aacute; n&agrave;y chủ yếu li&ecirc;n quan đến hoạt động t&agrave;i ch&iacute;nh, với &iacute;t bằng chứng thể hiện trong chương tr&igrave;nh l&agrave;m việc của HĐQT, bao gồm đ&aacute;nh gi&aacute; tr&aacute;ch nhiệm của ban quản l&yacute; trong hoạt động KSNB.</p>\r\n<p><strong>Chưa c&oacute; hệ thống cảnh b&aacute;o sớm</strong></p>\r\n<p>Điều đ&aacute;ng n&oacute;i trong kết quả điều tra của Ernst &amp; Young Việt Nam n&ecirc;u tr&ecirc;n l&agrave; kh&ocirc;ng c&oacute; một c&ocirc;ng ty bảo hiểm n&agrave;o c&oacute; hệ thống cảnh b&aacute;o sớm để nhận diện th&ocirc;ng tin cảnh b&aacute;o c&aacute;c rủi ro mới c&oacute; thể ảnh hưởng lớn đến c&ocirc;ng ty, hoặc theo d&otilde;i c&aacute;c thay đổi rủi ro v&agrave; b&aacute;o c&aacute;o kịp thời c&aacute;c thất bại kiểm so&aacute;t. C&aacute;c c&ocirc;ng ty cũng kh&ocirc;ng c&oacute; danh mục rủi ro, trong đ&oacute; liệt k&ecirc; tất cả rủi ro ở tầm DN, rủi ro ở quy tr&igrave;nh để đảm bảo kh&ocirc;ng c&oacute; rủi ro n&agrave;o bị bỏ s&oacute;t trong qu&aacute; tr&igrave;nh đ&aacute;nh gi&aacute;. Tương tự, ngo&agrave;i c&aacute;c thay đổi trong c&aacute;c chuẩn mực v&agrave; quy định kế to&aacute;n m&agrave; ph&ograve;ng t&agrave;i ch&iacute;nh - kế to&aacute;n c&oacute; tr&aacute;ch nhiệm phải x&aacute;c định v&agrave; &aacute;p dụng, kh&ocirc;ng nhiều c&ocirc;ng ty bảo hiểm c&oacute; sẵn cơ chế kiểm so&aacute;t như ủy ban quản l&yacute; t&agrave;i sản/c&ocirc;ng nợ (ALCO) để dự b&aacute;o, x&aacute;c định v&agrave; phản ứng với c&aacute;c thay đổi c&oacute; ảnh hưởng đ&aacute;ng kể v&agrave; rộng khắp đối với hoạt động của DN.</p>\r\n<p><strong>DN ngoại kiểm so&aacute;t nội bộ tốt hơn</strong></p>\r\n<p>Trong c&aacute;c c&ocirc;ng ty bảo hiểm được Ernst &amp; Young Việt Nam điều tra, x&eacute;t tr&ecirc;n kh&iacute;a cạnh về hệ thống KSNB, c&aacute;c c&ocirc;ng ty nước ngo&agrave;i phần lớn thực hiện tốt hơn c&aacute;c c&ocirc;ng ty kh&ocirc;ng c&oacute; sở hữu nước ngo&agrave;i. Điều n&agrave;y l&agrave; do nhận thức tốt hơn của c&aacute;c c&ocirc;ng ty về hệ thống KSNB quốc tế v&agrave; thực tiễn tốt nhất về quản trị DN; việc &aacute;p dụng phương ph&aacute;p đ&aacute;nh gi&aacute; rủi ro, c&aacute;c ch&iacute;nh s&aacute;ch KSNB v&agrave; c&aacute;c quy tr&igrave;nh đ&atilde; được thiết lập sẵn từ c&ocirc;ng ty mẹ; vai tr&ograve;, tr&aacute;ch nhiệm v&agrave; nguồn nh&acirc;n lực được x&aacute;c định r&otilde; r&agrave;ng hơn trong c&aacute;c c&ocirc;ng ty sở hữu nước ngo&agrave;i. Kết quả n&agrave;y ph&ugrave; hợp với b&aacute;o c&aacute;o thẻ điểm quản trị c&ocirc;ng ty do IFC thực hiện, trong đ&oacute; chỉ ra rằng, c&aacute;c c&ocirc;ng ty sở hữu nước ngo&agrave;i c&oacute; thực tiễn quản trị DN tốt hơn so với c&aacute;c c&ocirc;ng ty kh&ocirc;ng c&oacute; sở hữu nước ngo&agrave;i.</p>\r\n<p>Ngo&agrave;i ra, đại diện Ernst &amp; Young Việt Nam chia sẻ, trong c&aacute;c c&ocirc;ng ty được điều tra, Bảo Việt đang thực hiện một c&aacute;ch tương đối tốt hơn so với c&aacute;c c&ocirc;ng ty nội địa kh&aacute;c, điều n&agrave;y c&oacute; thể l&agrave; do c&oacute; yếu tố cổ đ&ocirc;ng chiến lược nước ngo&agrave;i tham gia quản trị v&agrave; điều h&agrave;nh.</p>\r\n<p><strong>&Ocirc;ng Nguyễn Sĩ H&ugrave;ng</strong> -Trưởng ban Kiểm tra v&agrave; Kiểm to&aacute;n nội bộ, Tổng CTCP Bảo Minh:</p>\r\n<p>Để ho&agrave;n thiện c&ocirc;ng t&aacute;c kiểm so&aacute;t nội bộ, cần ho&agrave;n thiện m&ocirc; h&igrave;nh tổ chức hoạt động kiểm so&aacute;t nội bộ, tuỳ thuộc v&agrave;o t&igrave;nh h&igrave;nh thực tế của doanh nghiệp v&agrave; mục ti&ecirc;u quản l&yacute; của l&atilde;nh đạo doanh nghiệp. Tuy nhi&ecirc;n, để hoạt động của bộ m&aacute;y kiểm so&aacute;t c&oacute; hiệu quả cao th&igrave; cần thống nhất m&ocirc; h&igrave;nh tổ chức hoạt động của bộ m&aacute;y kiểm so&aacute;t nội bộ.</p>\r\n<p>M&ocirc; h&igrave;nh chuẩn, l&yacute; tưởng c&oacute; thể l&agrave; bộ m&aacute;y kiểm so&aacute;t nội bộ chịu sự quản l&yacute; v&agrave; chỉ đạo của kiểm so&aacute;t doanh nghiệp, c&ograve;n m&ocirc; h&igrave;nh &aacute;p dụng đối với những doanh nghiệp lớn c&oacute; thể l&agrave; bộ m&aacute;y kiểm so&aacute;t nội bộ chịu sự quản l&yacute; v&agrave; chỉ đạo của HĐQT th&ocirc;ng qua ủy ban kiểm to&aacute;n thuộc HĐQT (m&ocirc; h&igrave;nh thực tế m&agrave; c&aacute;c doanh nghiệp đang triển khai l&agrave;: bộ m&aacute;y kiểm so&aacute;t nội bộ chịu sự quản l&yacute; v&agrave; chỉ đạo trực tiếp của tổng gi&aacute;m đốc doanh nghiệp).</p>\r\n<p><strong>B&agrave; Phạm Thị Ngọc Thanh</strong> - Ph&oacute; gi&aacute;m đốc Kiểm to&aacute;n nội bộ, C&ocirc;ng ty BHNT Dai-ichi Việt Nam:</p>\r\n<p>Để đ&aacute;p ứng y&ecirc;u cầu của Luật Kinh doanh bảo hiểm v&agrave; c&aacute;c văn bản ph&aacute;p luật li&ecirc;n quan, hầu hết DN bảo hiểm nh&acirc;n thọ đều th&agrave;nh lập hệ thống KSNB. Tuy nhi&ecirc;n, do chưa c&oacute; y&ecirc;u cầu bắt buộc cụ thể từ cơ quan quản l&yacute; nh&agrave; nước n&ecirc;n việc tổ chức, vận h&agrave;nh, gi&aacute;m s&aacute;t hệ thống n&agrave;y gần như l&agrave; t&ugrave;y thuộc v&agrave;o từng DN. T&ugrave;y theo văn h&oacute;a c&ocirc;ng ty, y&ecirc;u cầu của c&ocirc;ng ty mẹ hay nhu cầu thực tế ph&aacute;t sinh của DN m&agrave; hệ thống KSNB mỗi nơi mỗi kh&aacute;c, từ h&igrave;nh thức cho đến quy m&ocirc;.</p>\r\n<p>Để tu&acirc;n thủ Luật C&ocirc;ng ty được ban h&agrave;nh v&agrave; c&oacute; hiệu lực tại Nhật Bản, C&ocirc;ng ty Dai-ichi Life Nhật Bản đ&atilde; ban h&agrave;nh ch&iacute;nh s&aacute;ch KSNB, bao gồm c&aacute;c nguy&ecirc;n tắc v&agrave; phương ph&aacute;p hoạt động cơ bản trong hệ thống KSNB. C&aacute;c c&ocirc;ng ty con của Dai-ichi Life Nhật Bản đều tu&acirc;n thủ theo c&aacute;c điểm cơ bản của ch&iacute;nh s&aacute;ch KSNB tr&ecirc;n khi thiết kế c&aacute;c ch&iacute;nh s&aacute;ch KSNB cho ri&ecirc;ng m&igrave;nh.</p>\r\n<p><strong>&Ocirc;ng Nguyễn Hồng Sơn</strong> - Trưởng ban Kiểm tra, kiếm so&aacute;t nội bộ, Tổng CTCP Bảo hiểm Qu&acirc;n đội (MIC)</p>\r\n<p>Bộ T&agrave;i ch&iacute;nh n&ecirc;n hướng dẫn cụ thể hơn cho c&aacute;c DN bảo hiểm về hoạt động KSNB v&agrave; c&aacute;c quy định về bộ phận KSNB, bộ phận kiểm to&aacute;n nội bộ (cần thống nhất t&ecirc;n gọi). Bộ T&agrave;i ch&iacute;nh cũng n&ecirc;n ban h&agrave;nh quy định về chuẩn mực đạo đức nghề nghiệp, quy chuẩn cho kiểm to&aacute;n vi&ecirc;n nội bộ, những quy định ph&aacute;p l&yacute; về hoạt động kiểm to&aacute;n nội bộ.</p>\r\n<p>C&ograve;n DN bảo hiểm cần chủ động x&acirc;y dựng c&aacute;c quy tr&igrave;nh nghiệp vụ ph&ugrave; hợp với quy m&ocirc; hoạt động của DN; c&oacute; quy tr&igrave;nh đ&aacute;nh gi&aacute; rủi ro v&agrave; c&aacute;c thủ tục được thiết kế để ngăn ngừa rủi ro; tham gia c&aacute;c chương tr&igrave;nh quản l&yacute; quy chuẩn như ISO.</p>\r\n<p style=\"text-align: right;\"><strong>Diệu Minh</strong></p>\r\n<p>&nbsp;</p>', '2018-03-22 02:23:42', '2018-03-22 13:23:42', 'images/news/1520826298_pic-New-i_635400847540500000_HasThumb_Thumb.jpg.png'),
(285, 5, 'Chống trục lợi bảo hiểm, bắt đầu quyết liệt', 'chong-truc-loi-bao-hiem-bat-dau-quyet-liet', '<p><span style=\"font-family: Arial;\">(ĐTCK) Cơ sở dữ liệu về kh&aacute;ch h&agrave;ng như một tấm m&agrave;ng lọc, sẽ gi&uacute;p DN bảo hiểm ngăn chặn được rủi rủi bị trục lợi.&nbsp;</span></p>', '<p><img src=\"../../../uploads/635400848055410000.jpg\" alt=\"\" width=\"620\" height=\"322\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Bảo hiểm xe cơ giới đ&oacute;ng g&oacute;p 27% doanh thu ph&iacute; bảo hiểm to&agrave;n thị trường phi nh&acirc;n thọ</span></em></p>\r\n<p>Một trong những nhiệm vụ lớn ng&agrave;nh bảo hiểm đặt ra trong năm 2013 l&agrave; ph&ograve;ng chống trục lợi bảo hiểm, một vấn nạn đang diễn ra ng&agrave;y c&agrave;ng nghi&ecirc;m trọng, đe dọa đến lợi nhuận v&agrave; sự an to&agrave;n của DN trong ng&agrave;nh. Bảo hiểm xe cơ giới, nghiệp vụ đ&oacute;ng g&oacute;p khoảng 27% tổng doanh thu ph&iacute; bảo hiểm to&agrave;n thị trường bảo hiểm phi nh&acirc;n thọ đang l&agrave; t&acirc;m điểm của cuộc chiến chống trục lợi của ng&agrave;nh.</p>\r\n<p>Theo Cục Quản l&yacute; gi&aacute;m s&aacute;t bảo hiểm (Bộ T&agrave;i ch&iacute;nh), năm nay, Cục sẽ ho&agrave;n tất việc triển khai thực hiện cơ sở dữ liệu về bảo hiểm bắt buộc tr&aacute;ch nhiệm d&acirc;n sự của chủ xe cơ giới để &aacute;p dụng thống nhất trong to&agrave;n ng&agrave;nh. Đề &aacute;n x&acirc;y dựng cơ sở dữ liệu bảo hiểm bắt buộc tr&aacute;ch nhiệm d&acirc;n sự chủ xe cơ giới bắt đầu khởi động từ đầu năm 2012, tới nay, đ&atilde; qua giai đoạn kiểm thử v&agrave; đ&agrave;o tạo cho c&aacute;c DN trong ng&agrave;nh v&agrave; dự kiến b&agrave;n giao v&agrave;o đầu năm nay. Đ&acirc;y l&agrave; đề &aacute;n được c&aacute;c doanh nghiệp trong ng&agrave;nh cũng như cơ quan quản l&yacute; nh&agrave; nước, Hiệp hội Bảo hiểm Việt Nam kỳ vọng sẽ tạo ra sự thay đổi cả về chất v&agrave; lượng cho nghiệp vụ bảo hiểm xe cơ giới khi đi v&agrave;o thực thi. Theo đ&oacute;, Hiệp hội Bảo hiểm Việt Nam sẽ đứng ra x&acirc;y dựng một phần mềm chung cho c&aacute;c c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ nhập dữ liệu kh&aacute;ch h&agrave;ng của m&igrave;nh v&agrave; c&ugrave;ng khai th&aacute;c th&ocirc;ng tin lịch sử tổn thất của kh&aacute;ch h&agrave;ng.</p>\r\n<p>Cụ thể, nếu c&ocirc;ng ty bảo hiểm B c&oacute; kh&aacute;ch h&agrave;ng đăng k&yacute; mua bảo hiểm xe cơ giới, c&ocirc;ng ty n&agrave;y sẽ nhập th&ocirc;ng tin về kh&aacute;ch h&agrave;ng v&agrave;o cơ sở dữ liệu n&agrave;y. Nếu lịch sử tham gia bảo hiểm của kh&aacute;ch h&agrave;ng n&agrave;y tốt th&igrave; C&ocirc;ng ty sẽ chấp nhận bảo hiểm, c&ograve;n nếu đ&atilde; từng xảy ra tổn thất th&igrave; phải t&iacute;nh ph&iacute; c&aacute;ch kh&aacute;c, thậm ch&iacute; nếu kh&aacute;ch h&agrave;ng thuộc &ldquo;danh s&aacute;ch đen&rdquo; - trục lợi bảo hiểm th&igrave; c&ocirc;ng ty sẽ nhanh ch&oacute;ng loại bỏ kh&aacute;ch h&agrave;ng n&agrave;y. Cơ sở dữ liệu n&agrave;y được v&iacute; như một tấm &ldquo;m&agrave;ng lọc&rdquo; gi&uacute;p c&aacute;c doanh nghiệp bảo hiểm ngăn chặn được rủi ro.</p>\r\n<p>Thực tế, kế hoạch x&acirc;y dựng một danh s&aacute;ch dữ liệu kh&aacute;ch h&agrave;ng đ&atilde; được Hiệp hội v&agrave; c&aacute;c c&ocirc;ng ty bảo hiểm rậm rịch từ kh&aacute; l&acirc;u. Nhưng c&oacute; lẽ thời điểm đ&oacute;, c&aacute;c doanh nghiệp đều đang say sưa trong cuộc chiến gi&agrave;nh kh&aacute;ch để tăng thị phần n&ecirc;n kh&ocirc;ng quan t&acirc;m đến việc khai th&aacute;c dữ liệu chung của thị trường về kh&aacute;ch h&agrave;ng, bất chấp việc kh&aacute;ch h&agrave;ng đang nằm trong &ldquo;danh s&aacute;ch đen&rdquo; của c&ocirc;ng ty kh&aacute;c vẫn mời về l&agrave;m kh&aacute;ch của c&ocirc;ng ty m&igrave;nh.</p>\r\n<p>Trao đổi với ĐTCK, đại diện một doanh nghiệp bảo hiểm phi nh&acirc;n thọ cho biết, cũng c&ograve;n một số kh&oacute; khăn v&igrave; việc nhập th&ocirc;ng tin kh&aacute;ch h&agrave;ng đ&ograve;i hỏi c&aacute;c c&ocirc;ng ty bảo hiểm phải c&oacute; hệ thống c&ocirc;ng nghệ hiện đại đồng bộ. Trong khi đ&oacute;, ở một số doanh nghiệp bảo hiểm hiện nay, hệ thống dữ liệu kh&aacute;ch h&agrave;ng vẫn đang được nhập liệu kh&aacute; đơn giản&hellip; Tuy nhi&ecirc;n, trong bối cảnh t&igrave;nh h&igrave;nh trục lợi bảo hiểm xe cơ giới đang tăng l&ecirc;n ở mức b&aacute;o động như hiện nay, c&aacute;c c&ocirc;ng ty bảo hiểm đều hiểu rằng dữ liệu danh s&aacute;ch kh&aacute;ch h&agrave;ng tr&ecirc;n l&agrave; v&ocirc; c&ugrave;ng cần thiết để bảo vệ DN v&agrave; minh bạch h&oacute;a thị trường.</p>\r\n<p style=\"text-align: right;\"><strong>Ngọc Lan</strong></p>', '2018-03-22 02:24:08', '2018-03-22 13:24:08', 'images/news/1520826367_dich-vu-t_635406115514650000_HasThumb_Thumb.jpg.png'),
(286, 5, 'Bảo hiểm phi nhân thọ: Giã từ mục tiêu tăng trưởng tham vọng', 'bao-hiem-phi-nhan-tho-gia-tu-muc-tieu-tang-truong-tham-vong', '<p><span style=\"font-family: Arial;\">Sự suy giảm của nền kinh tế khiến c&aacute;c doanh nghiệp bảo hiểm phi nh&acirc;n thọ kh&ocirc;ng d&aacute;m đưa ra c&aacute;c mục ti&ecirc;u tăng trưởng tham vọng.&nbsp;</span></p>', '<p>Theo lộ tr&igrave;nh đ&atilde; b&aacute;o c&aacute;o Đại hội đồng cổ đ&ocirc;ng, năm 2012-2013, Tổng c&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng TMCP Đầu tư v&agrave; Ph&aacute;t triển Việt Nam (BIC) sẽ tăng vốn l&ecirc;n 850 tỷ đồng th&ocirc;ng qua việc ph&aacute;t h&agrave;nh th&ecirc;m vốn cho đối t&aacute;c chiến lược... Tuy nhi&ecirc;n, do t&igrave;nh h&igrave;nh kinh tế c&ograve;n nhiều kh&oacute; khăn, thị trường chứng kho&aacute;n kh&ocirc;ng ổn định, việc t&igrave;m kiếm đối t&aacute;c chiến lược kh&ocirc;ng thuận lợi, n&ecirc;n HĐQT Tổng c&ocirc;ng ty đ&atilde; tạm ho&atilde;n việc t&igrave;m kiếm đối t&aacute;c chiến lược, tập trung cho việc tăng v&agrave; nhận chuyển nhượng vốn g&oacute;p từ 2 li&ecirc;n doanh tại L&agrave;o v&agrave; Campuchia.</p>\r\n<p>Theo đ&oacute;, cuối năm 2012, HĐQT BIC đ&atilde; ph&ecirc; duyệt việc nhận chuyển nhượng khoản g&oacute;p vốn v&agrave;o CVI từ C&ocirc;ng ty Đầu tư v&agrave; Ph&aacute;t triển Campuchia (IDCC), với mệnh gi&aacute; chuyển nhượng l&agrave; 4,55 triệu USD, tương đương 65% vốn điều lệ của CVI. HĐQT BIC cũng ph&ecirc; duyệt việc nhận chuyển nhượng vốn g&oacute;p của Ng&acirc;n h&agrave;ng li&ecirc;n doanh L&agrave;o - Việt (LVB) tại LVI, với mệnh gi&aacute; chuyển nhượng l&agrave; 420.000 USD, tương đương 14% vốn điều lệ của LVI. Như vậy, tỷ lệ sở hữu của BIC tại LVI sau chuyển nhượng l&agrave; 65% vốn điều lệ.</p>\r\n<p>Hiện nay, sau khi Bộ T&agrave;i ch&iacute;nh c&oacute; văn bản chấp thuận chủ trương, Tổng c&ocirc;ng ty đang l&agrave;m thủ tục tr&igrave;nh Bộ Kế hoạch v&agrave; Đầu tư cấp giấy ph&eacute;p ph&ecirc; duyệt ch&iacute;nh thức việc nhận chuyển nhượng vốn v&agrave; ho&agrave;n thiện c&aacute;c thủ tục xin cấp ph&eacute;p đầu tư ra nước ngo&agrave;i đối với CVI.</p>\r\n<p>C&ugrave;ng với việc nhận chuyển nhượng vốn từ LVI v&agrave; CVI, HĐQT BIC cũng đ&atilde; chỉ đạo Ban điều h&agrave;nh ph&acirc;n c&ocirc;ng nh&acirc;n sự phối hợp với Ng&acirc;n h&agrave;ng TMCP Đầu tư v&agrave; Ph&aacute;t triển Việt Nam (BIDV) l&agrave;m việc với một số đối t&aacute;c tiềm năng để t&igrave;m kiếm cơ hội hợp t&aacute;c th&agrave;nh lập li&ecirc;n doanh bảo hiểm nh&acirc;n thọ. Về lộ tr&igrave;nh t&aacute;i cấu tr&uacute;c BIC, đến thời điểm hiện tại, c&ocirc;ng việc n&agrave;y vẫn đang được triển khai, nhưng tiến độ thực hiện c&oacute; thể chậm hơn lộ tr&igrave;nh đ&atilde; được Đại hội đồng cổ đ&ocirc;ng ph&ecirc; duyệt.</p>\r\n<p>Trước những dự b&aacute;o m&ocirc;i trường kinh doanh vẫn c&ograve;n nhiều kh&oacute; khăn, bất ổn v&agrave; kh&oacute; lường, mục ti&ecirc;u tăng trưởng nhanh v&agrave;o giai đoạn n&agrave;y l&agrave; kh&ocirc;ng ph&ugrave; hợp, BIC cũng định hướng năm 2013 chỉ phấn đấu tăng trưởng doanh thu cao hơn b&igrave;nh qu&acirc;n thị trường để giữ thị phần; lợi nhuận tăng trưởng khoảng 10% so với năm 2012.</p>\r\n<p>Theo dự b&aacute;o của Cục Quản l&yacute; gi&aacute;m s&aacute;t bảo hiểm, thị trường bảo hiểm phi nh&acirc;n thọ năm 2013 chỉ tăng trưởng khoảng 10%. Kh&ocirc;ng chỉ doanh nghiệp quy m&ocirc; nhỏ v&agrave; doanh nghiệp mới gia nhập thị trường phải chịu sức &eacute;p của kh&oacute; khăn thị trường v&agrave; cạnh tranh khốc liệt, c&aacute;c doanh nghiệp bảo hiểm lớn cũng kh&ocirc;ng ngoại lệ.</p>\r\n<p>Thống k&ecirc; sơ bộ của Bảo hiểm Bảo Việt cho thấy, 2 th&aacute;ng đầu năm, ph&iacute; bảo hiểm gốc chỉ tăng 4,5%, trong khi thị trường giảm 5,2% so với c&ugrave;ng kỳ. Với quan điểm ph&aacute;t triển thận trọng, năm 2013, Bảo hiểm Bảo Việt chỉ đặt mục ti&ecirc;u tổng doanh thu 6.739 tỷ đồng, tăng 5,3% so với năm 2012; lợi nhuận trước thuế 497 tỷ đồng, tăng 10,1% so với năm 2012.</p>\r\n<p>Trong khi đ&oacute;, vẫn ki&ecirc;n định mục ti&ecirc;u chiến lược đ&atilde; đề ra l&agrave; &ldquo;Hiệu quả v&agrave; ph&aacute;t triển bền vững&rdquo;, Bảo Minh tiếp tục tiến h&agrave;nh t&aacute;i cơ cấu v&agrave; triển khai c&aacute;c giải ph&aacute;p để đẩy mạnh hoạt động kinh doanh, tập trung v&agrave;o c&aacute;c địa b&agrave;n trọng t&acirc;m, trọng điểm, c&aacute;c nghiệp vụ kinh doanh c&oacute; hiệu quả&hellip; Mục ti&ecirc;u doanh thu bảo hiểm gốc năm 2013 được đưa ra cũng kh&aacute; khi&ecirc;m tốn, chỉ tăng trưởng khoảng 8-10% so với năm 2012.</p>\r\n<p>Theo đại diện bảo hiểm Liberty, thời kỳ tăng trưởng 60-70% đ&atilde; qua đi, kh&ocirc;ng doanh nghiệp n&agrave;o d&aacute;m đưa ra mục ti&ecirc;u tăng trưởng tham vọng v&agrave; Liberty cũng kh&ocirc;ng ngoại lệ. &ldquo;Kh&oacute; khăn buộc c&aacute;c doanh nghiệp bảo hiểm phải thực tế hơn trong việc thiết lập c&aacute;c chỉ ti&ecirc;u kinh doanh, chứ kh&ocirc;ng thể tiếp tục tuyển dụng đại l&yacute; ồ ạt, chi tiền l&agrave;m c&aacute;c chương tr&igrave;nh thu h&uacute;t kh&aacute;ch h&agrave;ng...&rdquo;, đại diện Liberty cho biết.</p>\r\n<p><strong>Ngọc Lan</strong></p>', '2018-03-12 14:47:03', '2018-03-12 14:47:03', 'images/news/1520826423_bhntsala_635406114043550000_HasThumb_Thumb.jpg.png'),
(287, 4, 'Tổng giám đốc VBI được vinh danh trong “Top 100 nhà quản lý tiêu biểu\"', 'tong-giam-doc-vbi-duoc-vinh-danh-trong-top-100-nha-quan-ly-tieu-bieu', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 30/6/2014, tại nh&agrave; h&aacute;t lớn H&agrave; Nội đ&atilde; diễn ra Lễ t&ocirc;n vinh Top 100 Nh&agrave; quản l&yacute; ti&ecirc;u biểu thời đại mới năm 2014. Đ&acirc;y l&agrave; chương tr&igrave;nh được thực hiện nhằm vinh danh c&aacute;c nh&agrave; quản l&yacute; ti&ecirc;u biểu tr&ecirc;n cả nước đ&atilde; đạt th&agrave;nh t&iacute;ch xuất sắc trong c&aacute;c phong tr&agrave;o thi đua lao động, phương ph&aacute;p quản l&yacute;, quản trị sản xuất kinh doanh.</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 30/6/2014, tại nh&agrave; h&aacute;t lớn H&agrave; Nội đ&atilde; diễn ra Lễ t&ocirc;n vinh Top 100 Nh&agrave; quản l&yacute; ti&ecirc;u biểu thời đại mới năm 2014. Đ&acirc;y l&agrave; chương tr&igrave;nh được thực hiện nhằm vinh danh c&aacute;c nh&agrave; quản l&yacute; ti&ecirc;u biểu tr&ecirc;n cả nước đ&atilde; đạt th&agrave;nh t&iacute;ch xuất sắc trong c&aacute;c phong tr&agrave;o thi đua lao động, phương ph&aacute;p quản l&yacute;, quản trị sản xuất kinh doanh.</span></p>\r\n<p><span style=\"font-family: Arial;\"><img src=\"../../../uploads/Le-Tuan-Dung-Award2.jpg\" alt=\"\" width=\"1366\" height=\"1012\" /><br /> <br />Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng - Tổng gi&aacute;m đốc C&ocirc;ng ty bảo hiểm VietinBank được vinh danh trong Top 100 Nh&agrave; quản l&yacute; ti&ecirc;u biểu thời đại mới. Tr&ecirc;n cương vị l&agrave; Tổng Gi&aacute;m đốc, &ocirc;ng L&ecirc; Tuấn Dũng đ&atilde; c&oacute; nhiều s&aacute;ng kiến v&agrave; th&agrave;nh t&iacute;ch nổi bật trong c&ocirc;ng t&aacute;c quản l&yacute; như t&aacute;i cơ cấu tổ chức th&ocirc;ng qua việc ph&aacute;t triển nguồn lực chất lượng, tăng cường hợp t&aacute;c với c&aacute;c đối t&aacute;c chiến lược, triển khai th&ecirc;m nhiều sản phẩm mới th&uacute;c đẩy hoạt động kinh doanh mang tới nhiều dịch vụ tiện &iacute;ch cho kh&aacute;ch h&agrave;ng.</span></p>\r\n<p><span style=\"font-family: Arial;\">Top 100 Nh&agrave; quản l&yacute; ti&ecirc;u biểu thời đại mới l&agrave; chương tr&igrave;nh nhằm vinh danh c&aacute;c nh&agrave; quản l&yacute; ti&ecirc;u biểu tr&ecirc;n cả nước đạt được những th&agrave;nh t&iacute;ch xuất sắc trong c&aacute;c phong tr&agrave;o thi đua lao động, phương ph&aacute;p quản l&yacute; v&agrave; quản trị kinh doanh. Qua đ&oacute; g&oacute;p phần cổ vũ cộng đồng doanh nghiệp, tổ chức phấn đấu x&acirc;y dựng những thương hiệu, nh&atilde;n hiệu h&agrave;ng h&oacute;a, sản phẩm dịch vụ c&oacute; uy t&iacute;n, chất lượng cao, được đ&ocirc;ng đảo người ti&ecirc;u d&ugrave;ng ưa chuộng cũng như x&acirc;y dựng m&ocirc;i trường văn h&oacute;a kinh doanh l&agrave;nh mạnh, thực hiện tốt hơn nữa tr&aacute;ch nhiệm x&atilde; hội đối với cộng đồng, đất nước.</span></p>', '2018-03-22 02:24:59', '2018-03-22 13:24:59', 'images/news/1521517393_Le-Tuan-Dung-Award.jpg'),
(288, 4, '6 tháng đầu năm: Bảo hiểm VietinBank tăng trưởng doanh thu phí bảo hiểm gần 90%', '6-thang-dau-nam-bao-hiem-vietinbank-tang-truong-doanh-thu-phi-bao-hiem-gan-90', '<p><span style=\"font-family: Arial;\">Tại Hội nghị sơ kết 6 th&aacute;ng đầu năm v&agrave; triển khai nhiệm vụ kinh doanh 6 th&aacute;ng cuối năm 2014 của Bảo hiểm VietinBank (VBI) ng&agrave;y 9/7/2014, VBI đ&atilde; cho biết c&aacute;c kết quả kinh doanh tăng trưởng khả quan trong 6 th&aacute;ng đầu năm 2014 của C&ocirc;ng ty.</span></p>', '<p><span style=\"font-family: Arial;\">Tại Hội nghị sơ kết 6 th&aacute;ng đầu năm v&agrave; triển khai nhiệm vụ kinh doanh 6 th&aacute;ng cuối năm 2014 của Bảo hiểm VietinBank (VBI) ng&agrave;y 9/7/2014, VBI đ&atilde; cho biết c&aacute;c kết quả kinh doanh tăng trưởng khả quan trong 6 th&aacute;ng đầu năm 2014 của C&ocirc;ng ty. Cụ thể, mặc d&ugrave; t&igrave;nh h&igrave;nh kinh tế c&ograve;n gặp nhiều kh&oacute; khăn, tổng doanh thu ph&iacute; bảo hiểm 6 th&aacute;ng đầu năm 2014 của VBI vẫn tăng trưởng 88,7% so với năm 2013. Trong đ&oacute;, doanh thu bảo hiểm gốc đạt 96,7 tỷ đồng, tăng trưởng 100% so với c&ugrave;ng kỳ năm 2013; doanh thu hoạt động nhận t&aacute;i bảo hiểm đạt 16,7 tỷ đồng, tăng trưởng 41,8% so với c&ugrave;ng kỳ năm 2013.</span></p>\r\n<p><img src=\"../../../uploads/Bao cao ket qua kinh doanh.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p>Tổng gi&aacute;m đốc VBI L&ecirc; Tuấn Dũng cho biết: \"Trong 6 th&aacute;ng cuối năm 2014, b&ecirc;n cạnh việc duy tr&igrave; thị trường kh&aacute;ch h&agrave;ng doanh nghiệp, kh&aacute;ch h&agrave;ng dự &aacute;n, C&ocirc;ng ty sẽ tiếp tục ph&aacute;t triển kinh doanh theo hướng mở rộng thị trường b&aacute;n lẻ, ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n, ho&agrave;n thiện m&ocirc; h&igrave;nh Bancassurance, tăng cường c&ocirc;ng t&aacute;c đ&agrave;o tạo c&aacute;n bộ, đại l&yacute; v&agrave; n&acirc;ng cao chất lượng bồi thường,...\"</p>\r\n<p>Trước đ&oacute;, th&aacute;ng 4/2014, tr&ecirc;n cơ sở đ&aacute;nh gi&aacute; từ C&ocirc;ng ty gi&aacute;m định độc lập Crawford, VBI đ&atilde; nhanh ch&oacute;ng tạm ứng số tiền bồi thường l&ecirc;n tới 5 tỷ đồng cho C&ocirc;ng ty xi măng C&ocirc;ng Thanh, đưa tổng số tiền đ&atilde; được VBI tạm ứng cho C&ocirc;ng ty khắc phục hậu quả cơn bảo số 10 l&ecirc;n tới 12 tỷ đồng. VBI cũng bồi thường tr&ecirc;n 4 tỷ đồng cho C&ocirc;ng ty H&ugrave;ng C&aacute; để C&ocirc;ng ty khắc phục ngay những tổn thất do mưa gi&ocirc;ng k&egrave;m theo gi&oacute; lốc, sấm s&eacute;t g&acirc;y ra.</p>\r\n<p>VBI l&agrave; C&ocirc;ng ty bảo hiểm trực thuộc VietinBank v&agrave; c&oacute; mạng lưới khai th&aacute;c bảo hiểm rộng lớn tại c&aacute;c Chi nh&aacute;nh VietinBank tr&ecirc;n to&agrave;n quốc. VBI c&oacute; tầm nh&igrave;n trở th&agrave;nh C&ocirc;ng ty Bảo hiểm Phi nh&acirc;n thọ chuy&ecirc;n nghiệp h&agrave;ng đầu Việt Nam tận t&acirc;m v&igrave; cộng đồng.</p>', '2018-03-22 02:25:26', '2018-03-22 13:25:26', 'images/news/1520827405_Bao cao ket qua kinh doanh.jpg');
INSERT INTO `news` (`id`, `news_categoryid`, `name`, `slug`, `excerpt`, `content`, `created_at`, `updated_at`, `thumbnail`) VALUES
(289, 4, 'Bảo hiểm VietinBank (VBI) ra mắt website mới - thân thiện và tiện dụng hơn', 'bao-hiem-vietinbank-vbi-ra-mat-website-moi-than-thien-va-tien-dung-hon', '<p><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) vừa ch&iacute;nh thức ra mắt website mới http://vbi.vietinbank.vn v&agrave;o ng&agrave;y 28/08/2014. VBI mong muốn mang đến cho kh&aacute;ch h&agrave;ng, nh&agrave; đầu tư v&agrave; đối t&aacute;c một website giao diện mới, hiện đại, gần gũi v&agrave; nhiều tiện &iacute;ch hơn với người sử dụng.</span></p>', '<p><strong><span style=\"font-family: Arial;\">Bảo hiểm VietinBank (VBI) vừa ch&iacute;nh thức ra mắt website mới http://vbi.vietinbank.vn v&agrave;o ng&agrave;y 28/08/2014. VBI mong muốn mang đến cho kh&aacute;ch h&agrave;ng, nh&agrave; đầu tư v&agrave; đối t&aacute;c một website giao diện mới, hiện đại, gần gũi v&agrave; nhiều tiện &iacute;ch hơn với người sử dụng.</span></strong></p>\r\n<p>Với giao diện được thiết kế hiện đại, th&acirc;n thiện, c&ugrave;ng c&aacute;c t&iacute;nh năng ưu việt dễ d&agrave;ng cho việc truy cập, website mới l&agrave; k&ecirc;nh truyền th&ocirc;ng ch&iacute;nh thức của VBI về c&aacute;c hoạt động kinh doanh, gi&uacute;p kh&aacute;ch h&agrave;ng dễ d&agrave;ng tra cứu, t&igrave;m hiểu th&ocirc;ng tin, sử dụng sản phẩm, dịch vụ v&agrave; c&aacute;c chương tr&igrave;nh khuyến m&atilde;i của VBI.</p>\r\n<p>Điểm ưu việt của website mới l&agrave; sự đơn giản v&agrave; tiện dụng đối với từng đối tượng kh&aacute;ch h&agrave;ng. Đồng thời t&iacute;nh năng hiển thị danh s&aacute;ch Chi nh&aacute;nh VBI, Bệnh viện v&agrave; Gara bảo l&atilde;nh gi&uacute;p kh&aacute;ch h&agrave;ng nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng t&igrave;m kiếm được c&aacute;c điạ điểm gần nhất. Website mới của VBI cũng được đầu tư kỹ lưỡng về giao diện v&agrave; thống nhất với c&aacute;c yếu tố nhận diện của Bộ nhận diện thương hiệu VBI 2014. Trong thời gian tới, VBI sẽ tiếp tục đầu tư v&agrave;o c&ocirc;ng nghệ để tăng cường khả năng tương t&aacute;c giữa VBI v&agrave; kh&aacute;ch h&agrave;ng như ch&agrave;o ph&iacute; bảo hiểm hay mua bảo hiểm trực tuyến,...</p>\r\n<p><img src=\"../../../uploads/Website VBI.png\" alt=\"\" width=\"966\" height=\"633\" /></p>\r\n<p style=\"text-align: center;\"><em>Giao diện trang chủ website VBI mới</em></p>\r\n<p>Việc ra mắt website mới v&agrave; thường xuy&ecirc;n cập nhật c&ocirc;ng nghệ ti&ecirc;n tiến v&agrave;o hoạt động kinh doanh một lần nữa khẳng định quyết t&acirc;m của VBI trong việc n&acirc;ng cao chất lượng dịch vụ nhằm đem đến cho kh&aacute;ch h&agrave;ng sự h&agrave;i l&ograve;ng trong từng giao dịch.</p>\r\n<p>Hiện nay, VBI đ&atilde; đ&atilde; x&acirc;y dựng th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh Bancassurance b&aacute;n bảo hiểm qua Ng&acirc;n h&agrave;ng với mạng lưới hơn 150 Chi nh&aacute;nh v&agrave; hơn 6000 c&aacute;n bộ b&aacute;n h&agrave;ng của VietinBank. Từ đ&oacute;, cung cấp cho kh&aacute;ch h&agrave;ng dịch vụ t&agrave;i ch&iacute;nh trọn g&oacute;i một cửa: ng&acirc;n h&agrave;ng &ndash; bảo hiểm. Tổng doanh thu ph&iacute; bảo hiểm 6 th&aacute;ng đầu năm 2014 của VBI tăng trưởng 88,7% so với năm 2013. Trong đ&oacute;, doanh thu bảo hiểm gốc đạt 96,7 tỷ đồng, tăng trưởng 100% so với c&ugrave;ng kỳ năm 2013; doanh thu hoạt động nhận t&aacute;i bảo hiểm đạt 16,7 tỷ đồng, tăng trưởng 41,8% so với c&ugrave;ng kỳ năm 2013. Th&aacute;ng 6/2014 vừa qua, VBI được Cục Thuế TP H&agrave; Nội khen thưởng v&igrave; đ&atilde; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ đ&oacute;ng g&oacute;p cho Ng&acirc;n s&aacute;ch Nh&agrave; nước.</p>\r\n<p>Với định hướng phấn đấu trở th&agrave;nh C&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ chuy&ecirc;n nghiệp h&agrave;ng đầu tại Việt Nam, Bảo hiểm VietinBank quyết t&acirc;m nỗ lực, s&aacute;ng tạo, g&oacute;p phần đưa thương hiệu VietinBank n&oacute;i chung v&agrave; Bảo hiểm VietinBank n&oacute;i ri&ecirc;ng vươn cao v&agrave; xa hơn nữa tr&ecirc;n thị trường trong nước v&agrave; quốc tế.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:25:49', '2018-03-22 13:25:49', 'images/news/1520827505_Website VBI.png'),
(290, 4, 'Bảo hiểm VietinBank tăng trưởng vượt bậc năm 2014', 'bao-hiem-vietinbank-tang-truong-vuot-bac-nam-2014', '<p><span style=\"font-family: Arial;\">C&ocirc;ng ty Bảo hiểm VietinBank (VBI) vừa ch&iacute;nh thức c&ocirc;ng bố kết quả kinh doanh trước kiểm to&aacute;n năm 2014. Năm vừa qua l&agrave; năm đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của VBI với sự chuyển biến tăng trưởng mạnh mẽ so với năm 2013 cả về doanh thu, lợi nhuận lẫn cơ cấu bộ m&aacute;y tổ chức.</span></p>', '<p>C&ocirc;ng ty Bảo hiểm VietinBank (VBI) vừa ch&iacute;nh thức c&ocirc;ng bố kết quả kinh doanh trước kiểm to&aacute;n năm 2014. Năm vừa qua l&agrave; năm đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của VBI với sự chuyển biến tăng trưởng mạnh mẽ so với năm 2013 cả về doanh thu, lợi nhuận lẫn cơ cấu bộ m&aacute;y tổ chức.</p>\r\n<p>Trong bối cảnh to&agrave;n thị trường gặp kh&oacute; khăn, VBI vẫn đạt những con số ấn tượng: doanh thu đạt 318 tỷ tăng trưởng 86%; lợi nhuận đạt 63,27 tỷ đồng tăng trưởng 30%; tỷ lệ ROE đạt 8,74% tăng trưởng 35% so với năm 2013; tỷ lệ ROA 7,12% tăng trưởng 18% so với năm 2013, l&atilde;i nghiệp vụ đạt 6,1 tỷ đồng; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 26,13%. Cũng trong năm, C&ocirc;ng ty đ&atilde; mở th&ecirc;m 1 Chi nh&aacute;nh mới, 15 ph&ograve;ng khu vực, tuyển th&ecirc;m 81 lao động v&agrave; tăng th&ecirc;m 1087 đại l&yacute;.</p>\r\n<p>Với th&agrave;nh t&iacute;ch vượt trội trong năm 2014, VBI đ&atilde; vinh dự nhận được c&aacute;c giải thưởng uy t&iacute;n như: &ldquo;Giải Thương hiệu mạnh 2014&rdquo; do Thời b&aacute;o kinh tế Kinh tế Việt Nam phối hợp c&ugrave;ng Cục X&uacute;c tiến thương mại tổ chức; &ldquo;Top 1000 đơn vị đ&oacute;ng thuế thu nhập doanh nghiệp lớn nhất VN&rdquo; do VietNam Report c&ocirc;ng bố.</p>\r\n<p><img src=\"../../../uploads/tong ket 2015.jpg\" alt=\"\" width=\"986\" height=\"772\" /></p>\r\n<p>B&ecirc;n cạnh đ&oacute;, L&atilde;nh đạo C&ocirc;ng ty cũng được nhận những giải thưởng vinh dự như: Được Hội doanh nh&acirc;n trẻ Việt Nam chứng nhận l&agrave; doanh nh&acirc;n trẻ xếp thứ 25 trong 100 doanh nh&acirc;n trẻ Việt Nam ti&ecirc;u biểu tại Giải thưởng Sao Đỏ năm 2014. Được chứng nhận Top 100 nh&agrave; quản l&yacute; ti&ecirc;u biểu thời đại mới năm 2014 do Viện Ch&iacute;nh s&aacute;ch Ph&aacute;p Luật v&agrave; Quản l&yacute; Nh&agrave; Nước tổ chức.</p>\r\n<p>Trong năm 2015, VBI sẽ tiếp tục mở rộng mạng lưới hoạt động; t&aacute;i cơ cấu theo m&ocirc; h&igrave;nh quản l&yacute; tập trung, ph&aacute;t triển mạnh k&ecirc;nh bancassurance v&agrave; tập trung v&agrave;o c&aacute;c sản phẩm b&aacute;n lẻ đ&oacute;ng g&oacute;i với c&aacute;c sản phẩm Ng&acirc;n h&agrave;ng như bảo hiểm con người, xe cơ giới; &aacute;p dụng c&ocirc;ng nghệ hiện đại v&agrave;o việc quản l&yacute;; tăng cường h&igrave;nh ảnh, thương hiệu của c&ocirc;ng ty.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:26:14', '2018-03-22 13:26:14', 'images/news/1520827569_tong ket 2015.jpg'),
(291, 4, 'Bảo hiểm VietinBank được vinh danh “Thương hiệu mạnh Việt Nam 2014”', 'bao-hiem-vietinbank-duoc-vinh-danh-thuong-hieu-manh-viet-nam-2014-1', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 21/3/2015, tại H&agrave; Nội đ&atilde; diễn ra Li&ecirc;n hoan Doanh nghiệp Rồng v&agrave;ng v&agrave; c&aacute;c Thương hiệu mạnh Việt Nam lần thứ XI do Thời b&aacute;o Kinh tế Việt Nam tổ chức. Với chủ đề &ldquo;Tạo dựng m&ocirc;i trường kinh doanh b&igrave;nh đẳng v&agrave; l&agrave;nh mạnh&rdquo;, li&ecirc;n hoan năm nay t&ocirc;n vinh 71 doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i (FDI) đạt danh hiệu Rồng V&agrave;ng v&agrave; 95 doanh nghiệp Việt đạt danh hiệu Thương hiệu mạnh Việt Nam.</span></p>', '<p>Ng&agrave;y 21/3/2015, tại H&agrave; Nội đ&atilde; diễn ra Li&ecirc;n hoan Doanh nghiệp Rồng v&agrave;ng v&agrave; c&aacute;c Thương hiệu mạnh Việt Nam lần thứ XI do Thời b&aacute;o Kinh tế Việt Nam tổ chức. Với chủ đề &ldquo;Tạo dựng m&ocirc;i trường kinh doanh b&igrave;nh đẳng v&agrave; l&agrave;nh mạnh&rdquo;, li&ecirc;n hoan năm nay t&ocirc;n vinh 71 doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i (FDI) đạt danh hiệu Rồng V&agrave;ng v&agrave; 95 doanh nghiệp Việt đạt danh hiệu Thương hiệu mạnh Việt Nam. Đ&acirc;y l&agrave; một trong những giải thưởng d&agrave;nh cho doanh nghiệp uy t&iacute;n tại Việt Nam, được tổ chức thường ni&ecirc;n nhằm vinh danh những doanh nghiệp c&oacute; th&agrave;nh t&iacute;ch trong sản xuất kinh doanh, cung ứng dịch vụ, thực hiện đầy đủ nghĩa vụ nộp thuế, đảm bảo lợi &iacute;ch người lao động, t&iacute;ch cực tham gia c&aacute;c hoạt động x&atilde; hội v&agrave; đ&oacute;ng g&oacute;p cho cộng đồng.</p>\r\n<p>L&agrave; một trong 95 doanh nghiệp được vinh danh &ldquo;Thương hiệu mạnh Việt Nam&rdquo; &ndash; C&ocirc;ng ty Bảo hiểm VietinBank (VBI) đ&atilde; khẳng định được t&ecirc;n tuổi v&agrave; tạo được vị thế tr&ecirc;n thị trường bảo hiểm Việt Nam. Năm 2014 vừa qua l&agrave; năm đ&aacute;nh dấu bước ngoặt lớn trong hoạt động của VBI với sự chuyển biến tăng trưởng mạnh mẽ cả về doanh thu, lợi nhuận lẫn cơ cấu bộ m&aacute;y tổ chức. Trong bối cảnh to&agrave;n thị trường gặp kh&oacute; khăn, VBI vẫn đạt những con số ấn tượng: doanh thu đạt 318 tỷ tăng trưởng 86% so với năm 2013; lợi nhuận đạt 63,27 tỷ đồng tăng trưởng 30%; tỷ lệ ROE đạt 8,74% tăng trưởng 35% so với năm 2013; tỷ lệ ROA 7,12% tăng trưởng 18% so với năm 2013; l&atilde;i nghiệp vụ đạt 6,1 tỷ đồng; tỷ lệ bồi thường giữ ở mức an to&agrave;n l&agrave; 26,13%. Cũng trong năm, VBI đ&atilde; mở th&ecirc;m 1 Chi nh&aacute;nh mới, 15 ph&ograve;ng kinh doanh khu vực, tuyển th&ecirc;m 81 lao động v&agrave; tăng th&ecirc;m 1896 đại l&yacute;.</p>\r\n<p><img src=\"../../../uploads/anh dang web.jpg\" alt=\"\" width=\"640\" height=\"462\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc VBI nhận C&uacute;p Thương hiệu mạnh Việt Nam 2014.)</em></p>\r\n<p>Theo Ban tổ chức, những doanh nghiệp được t&ocirc;n vinh năm nay đều c&oacute; kết quả tăng trưởng v&agrave; ph&aacute;t triển tốt, nỗ lực vượt qua kh&oacute; khăn trong bối cảnh khủng hoảng kinh tế khu vực v&agrave; thế giới k&eacute;o d&agrave;i; c&oacute; kế hoạch chiến lược n&acirc;ng cao năng lực cạnh tranh cả về nh&acirc;n lực, c&ocirc;ng nghệ, chất lượng, dịch vụ để c&oacute; thể chủ động đ&oacute;n những cơ hội v&agrave; th&aacute;ch thức từ hội nhập kinh tế quốc tế ng&agrave;y c&agrave;ng s&acirc;u rộng.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:26:39', '2018-03-22 13:26:39', 'images/news/1520827672_anh dang web.jpg'),
(292, 4, 'VBI tiếp tục được tôn vinh doanh nghiệp nộp thuế tiêu biểu năm 2014', 'vbi-tiep-tuc-duoc-ton-vinh-doanh-nghiep-nop-thue-tieu-bieu-nam-2014', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 11/06, Bảo hiểm VietinBank (VBI) được Cục Thuế th&agrave;nh phố H&agrave; Nội khen thưởng trong Hội nghị &ldquo;Tuy&ecirc;n dương tổ chức, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch kinh doanh hiệu quả v&agrave; thực hiện tốt nghĩa vụ thuế&rdquo;. Trong năm 2014, Bảo hiểm VietinBank (VBI) đạt doanh thu 310 tỷ đồng, lợi nhuận trước thuế 63 tỷ đồng, đ&oacute;ng g&oacute;p v&agrave;o ng&acirc;n s&aacute;ch Nh&agrave; nước 39,7 tỷ đồng.</span></p>', '<p><span style=\"font-family: Arial;\">S&aacute;ng ng&agrave;y 11/06, Bảo hiểm VietinBank (VBI) được Cục Thuế th&agrave;nh phố H&agrave; Nội khen thưởng trong Hội nghị &ldquo;Tuy&ecirc;n dương tổ chức, c&aacute; nh&acirc;n c&oacute; th&agrave;nh t&iacute;ch kinh doanh hiệu quả v&agrave; thực hiện tốt nghĩa vụ thuế&rdquo;. Trong năm 2014, Bảo hiểm VietinBank (VBI) đạt doanh thu 310 tỷ đồng, lợi nhuận trước thuế 63 tỷ đồng, đ&oacute;ng g&oacute;p v&agrave;o ng&acirc;n s&aacute;ch Nh&agrave; nước 39,7 tỷ đồng.<br /> <br />Theo đ&aacute;nh gi&aacute; của Cục thuế th&agrave;nh phố H&agrave; Nội, năm 2014 l&agrave; một năm kinh tế ph&aacute;t triển t&iacute;ch cực hơn những năm trước, tuy nhi&ecirc;n vẫn c&ograve;n những kh&oacute; khăn, hạn chế v&agrave; th&aacute;ch thức. Nhờ sự nhận định tốt xu hướng thị trường v&agrave; kh&ocirc;ng ngừng s&aacute;ng tạo, linh động trong hoạt động của c&ocirc;ng ty, Bảo hiểm VietinBank (VBI) vượt l&ecirc;n những kh&oacute; khăn chung, đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ đ&oacute;ng g&oacute;p cho Ng&acirc;n s&aacute;ch Nh&agrave; nước.</span></p>\r\n<p><img src=\"../../../uploads/IMG_3032.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(ảnh: Đại diện VBI nhận giấy khen từ &ocirc;ng H&agrave; Minh Hải &ndash; Cục trưởng cục thuế H&agrave; Nội)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Cục trưởng Cục Thuế th&agrave;nh phố H&agrave; Nội &ndash; H&agrave; Minh Hải cho biết: b&aacute;o c&aacute;o cập nhật đ&aacute;nh gi&aacute; về t&igrave;nh h&igrave;nh kinh tế Việt Nam năm 2014, ghi nhận những chuyển biến t&iacute;ch cực bước đầu của nền kinh tế, c&oacute; 423 đơn vị doanh nghiệp được nhận cờ, bằng khen của UBND th&agrave;nh phố H&agrave; Nội trong phong tr&agrave;o \"Thực hiện tốt ch&iacute;nh s&aacute;ch ph&aacute;p luật thuế năm 2014\". Trong bối cảnh thị trường hiện nay, uy t&iacute;n của doanh nghiệp trước đối t&aacute;c, kh&aacute;ch h&agrave;ng được bảo đảm kh&ocirc;ng chỉ từ việc doanh nghiệp ph&aacute;t triển lớn mạnh hơn, kinh doanh hiệu quả hơn, m&agrave; c&ograve;n từ việc t&iacute;ch cực ho&agrave;n th&agrave;nh c&aacute;c nghĩa vụ đối với Nh&agrave; nước, trong đ&oacute; c&oacute; nghĩa vụ thuế.</span></p>\r\n<p style=\"text-align: right;\"><em><strong><span style=\"font-family: Arial;\">VBI</span></strong></em></p>', '2018-03-22 02:27:04', '2018-03-22 13:27:04', 'images/news/1520827767_IMG_3032.JPG'),
(293, 4, 'VBI vinh dự nhận giải thưởng Sao Vàng đất Việt 2015', 'vbi-vinh-du-nhan-giai-thuong-sao-vang-dat-viet-2015', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 04/10/2015, Bảo hiểm Vietinbank (VBI) đ&atilde; vinh dự nhận giải thưởng Sao V&agrave;ng đất Việt 2015 sau những nỗ lực đổi mới v&agrave; kh&ocirc;ng ngừng s&aacute;ng tạo. Buổi lễ trao giải diễn ra long trọng tại Trung t&acirc;m h&ocirc;i nghị Quốc gia v&agrave; được truyền h&igrave;nh trực tiếp tr&ecirc;n s&oacute;ng VTV1 Đ&agrave;i truyền h&igrave;nh Việt Nam.</span></p>', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 04/10/2015, Bảo hiểm Vietinbank (VBI) đ&atilde; vinh dự nhận giải thưởng Sao V&agrave;ng đất Việt 2015 sau những nỗ lực đổi mới v&agrave; kh&ocirc;ng ngừng s&aacute;ng tạo. Buổi lễ trao giải diễn ra long trọng tại Trung t&acirc;m h&ocirc;i nghị Quốc gia v&agrave; được truyền h&igrave;nh trực tiếp tr&ecirc;n s&oacute;ng VTV1 Đ&agrave;i truyền h&igrave;nh Việt Nam. Tham dự chương tr&igrave;nh c&oacute; &ocirc;ng Nguyễn Xu&acirc;n Ph&uacute;c &ndash; Ủy vi&ecirc;n Bộ Ch&iacute;nh trị, Ph&oacute; Thủ tướng; đại diện l&atilde;nh đạo c&aacute;c bộ ng&agrave;nh Trung ương, địa phương c&ugrave;ng hơn 1000 doanh nh&acirc;n tr&ecirc;n khắp cả nước.</span></p>\r\n<p><img src=\"../../../uploads/IMG_3645.jpg\" alt=\"\" width=\"1366\" height=\"767\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">(Ph&oacute; thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c ph&aacute;t biểu tại lễ trao giải)</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Sao V&agrave;ng đất Việt l&agrave; giải thưởng uy t&iacute;n do Trung ương Hội li&ecirc;n hiệp Thanh ni&ecirc;n Việt Nam phối hợp với Trung ương Hội c&aacute;c nh&agrave; doanh nghiệp trẻ Việt Nam tổ chức từ năm 2003, nhằm t&ocirc;n vinh c&aacute;c thương hiệu v&agrave; sản phẩm ti&ecirc;u biểu của đất nước trong bối cảnh hội nhập quốc tế s&acirc;u rộng. Trong khối ng&agrave;nh bảo hiểm, với tốc độ tăng trưởng doanh thu tr&ecirc;n 80% trong năm 2014 cũng như 6 th&aacute;ng đầu năm 2015, VBI vinh dự nhận giải thưởng TOP thương hiệu Sao v&agrave;ng đất Việt c&oacute; sức cạnh tranh lớn tr&ecirc;n thị trường.</span></p>\r\n<p><img src=\"../../../uploads/IMG_3625.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Đại diện VBI l&ecirc;n nhận giải thưởng Sao v&agrave;ng đất Việt</span></em></p>\r\n<p><img src=\"../../../uploads/IMG_3651 (2).jpg\" alt=\"\" width=\"1366\" height=\"992\" /></p>\r\n<p style=\"text-align: center;\"><em><span style=\"font-family: Arial;\">Khoảnh khắc t&ocirc;n vinh c&aacute;c Doanh nghiệp ti&ecirc;u biểu tại Lễ trao Giải thưởng Sao V&agrave;ng đất Việt 2015</span></em></p>\r\n<p><span style=\"font-family: Arial;\">Giải thưởng Sao V&agrave;ng đất Việt 2015 đ&atilde; khẳng định sự cố gắng ph&aacute;t triển kh&ocirc;ng ngừng, sự vươn m&igrave;nh mạnh mẽ của VBI trong suốt thời gian qua v&agrave; một lần nữa thể hiện vị thế của c&ocirc;ng ty tr&ecirc;n thị trường trong nước v&agrave; quốc tế. C&ugrave;ng với niềm vinh dự n&agrave;y, VBI đ&atilde;, đang v&agrave; sẽ lu&ocirc;n tiếp tục đổi mới, s&aacute;ng tạo đầu tư ph&aacute;t triển c&ocirc;ng nghệ v&agrave; nguồn nh&acirc;n lực hướng đến mục ti&ecirc;u trở th&agrave;nh một trong những c&ocirc;ng ty bảo hiểm phi nh&acirc;n thọ h&agrave;ng đầu Việt Nam.</span></p>', '2018-03-22 02:28:16', '2018-03-22 13:28:16', 'images/news/1520827902_IMG_3651 (2).jpg'),
(294, 4, 'Bảo hiểm VietinBank (VBI) nhận danh hiệu Top 10 công ty bảo hiểm uy tín nhất Việt Nam 2016', 'bao-hiem-vietinbank-vbi-nhan-danh-hieu-top-10-cong-ty-bao-hiem-uy-tin-nhat-viet-nam-2016-1', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 27/07/2016, trong Hội nghị thường ni&ecirc;n Vietnam CEO Summit 2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam năm 2016.&nbsp;</span></p>', '<p>Ng&agrave;y 27/07/2016, trong Hội nghị thường ni&ecirc;n Vietnam CEO Summit 2016 tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; được vinh danh trong Top 10 c&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam năm 2016. Đ&acirc;y l&agrave; kết quả nghi&ecirc;n cứu độc lập vừa được CTCP B&aacute;o c&aacute;o Đ&aacute;nh gi&aacute; Việt Nam (Vietnam Report) phối hợp với B&aacute;o điện tử VietnamNet c&ocirc;ng bố dựa tr&ecirc;n 3 ti&ecirc;u ch&iacute; ch&iacute;nh l&agrave; năng lực t&agrave;i ch&iacute;nh, uy t&iacute;n truyền th&ocirc;ng v&agrave; mức độ h&agrave;i l&ograve;ng của người d&ugrave;ng đối với sản phẩm, dịch vụ bảo hiểm của c&aacute;c doanh nghiệp.</p>\r\n<p><br />Trong 6 th&aacute;ng đầu năm, VBI đ&atilde; được Vietnam Report vinh danh trong Top 1000 doanh nghiệp nộp thuế lớn nhất Việt Nam v&agrave; lần thứ 2 li&ecirc;n tiếp nằm trong Top 100 Thương hiệu mạnh Việt Nam do Thời b&aacute;o Kinh tế Việt Nam c&ocirc;ng bố.</p>\r\n<p><img src=\"../../../uploads/IMG_0824.jpg\" alt=\"\" width=\"1366\" height=\"790\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: Đại diện VBI nhận danh hiệu TOP 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n 2016)</em></p>\r\n<p>Kết quả hoạt động 6 th&aacute;ng đầu năm 2016 của VBI cũng cho thấy những bước ph&aacute;t triển đột ph&aacute;, ghi dấu bằng kết quả hoạt động kinh doanh hiệu quả. Doanh thu ph&iacute; bảo hiểm gốc trong 6 th&aacute;ng đầu năm của VBI đạt 320 tỷ đồng, tăng trưởng 50,3% so với 6 th&aacute;ng đầu năm 2015. Doanh thu nhận t&aacute;i trong 6 th&aacute;ng đầu năm của VBI đạt 42,9 tỷ đồng, tăng trưởng 37% so với 6 th&aacute;ng đầu năm 2015. Lợi nhuận lũy kế 6 th&aacute;ng của VBI đạt 34 tỷ đồng. Năm 2016, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI tr&ecirc;n 800 tỷ đồng, tăng trưởng 50% so với 2015.</p>\r\n<p><br />Để đạt được kết quả kinh doanh ấn tượng tr&ecirc;n, ngay từ những th&aacute;ng đầu năm 2016, VBI đ&atilde; triển khai h&agrave;ng loạt c&aacute;c chương tr&igrave;nh ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng như: &ldquo;Chương tr&igrave;nh tặng qu&agrave; cho kh&aacute;ch h&agrave;ng tham gia bảo hiểm sức khỏe&rdquo;, &ldquo;Triệu lời tri &acirc;n&rdquo;, &ldquo;Ưu đ&atilde;i 25% ph&iacute; bảo hiểm nh&agrave; dự &aacute;n&rdquo;, &ldquo;Ưu đ&atilde;i 30% ph&iacute; bảo hiểm du học v&agrave; du lịch quốc tế&rdquo;, v.v&hellip;</p>\r\n<p><br />B&ecirc;n cạnh đ&oacute;, những hoạt động hỗ trợ cộng đồng cũng được VBI đẩy mạnh triển khai trong 6 th&aacute;ng đầu năm như chương tr&igrave;nh kh&aacute;m chữa bệnh v&agrave; tặng bảo hiểm sức khỏe cho cộng đồng người ngh&egrave;o tại 63 tỉnh th&agrave;nh tr&ecirc;n to&agrave;n quốc với tổng gi&aacute; trị bảo hiểm l&ecirc;n tới 18,9 tỷ đồng.</p>\r\n<p><br />Trong năm 2016, nhằm n&acirc;ng cao năng lực t&agrave;i ch&iacute;nh, hiệu quả sử dụng vốn v&agrave; t&agrave;i sản, VBI sẽ chuyển đổi m&ocirc; h&igrave;nh tổ chức, mở rộng mạng lưới hoạt động th&ecirc;m 5 chi nh&aacute;nh mới, &aacute;p dụng c&ocirc;ng nghệ hiện đại v&agrave;o việc quản l&yacute;, triển khai chiến lược ph&aacute;t triển thương hiệu VBI. Những định hướng hoạt động tr&ecirc;n sẽ gi&uacute;p VBI đẩy nhanh tiến tr&igrave;nh cổ phần h&oacute;a, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:28:48', '2018-03-22 13:28:48', 'images/news/1520828008_IMG_0824.JPG'),
(295, 4, 'VBI lần đầu tiên phát hành Giấy chứng nhận bảo hiểm Điện tử và Hóa đơn Điện tử', 'vbi-lan-dau-tien-phat-hanh-giay-chung-nhan-bao-hiem-dien-tu-va-hoa-don-dien-tu-1', '<p><span style=\"font-family: Arial;\">Bắt đầu từ ng&agrave;y 15/08/2016, VBI lần đầu ti&ecirc;n ph&aacute;t h&agrave;nh &ldquo;Giấy chứng nhận bảo hiểm điện tử v&agrave; H&oacute;a đơn điện tử&rdquo; với mong muốn mang đến cho Qu&yacute; kh&aacute;ch h&agrave;ng những trải nghiệm mới bằng c&ocirc;ng nghệ hiện đại v&agrave; chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.</span></p>', '<p>Bắt đầu từ ng&agrave;y 15/08/2016, VBI lần đầu ti&ecirc;n ph&aacute;t h&agrave;nh &ldquo;Giấy chứng nhận bảo hiểm điện tử v&agrave; H&oacute;a đơn điện tử&rdquo; với mong muốn mang đến cho Qu&yacute; kh&aacute;ch h&agrave;ng những trải nghiệm mới bằng c&ocirc;ng nghệ hiện đại v&agrave; chất lượng dịch vụ kh&aacute;ch h&agrave;ng vượt trội.<br /> <br />Từ nay, Qu&yacute; kh&aacute;ch h&agrave;ng mua mới bảo hiểm VBI, c&oacute; đăng k&yacute; email v&agrave; số điện thoại, sẽ nhận ngay Giấy chứng nhận bảo hiểm c&oacute; chữ k&yacute; số của VBI qua Email v&agrave; SMS. Giấy chứng nhận được lưu trữ tại website tra cứ quyền lợi trực tuyến http://tracuu.baohiem-vbi.vn.</p>\r\n<p>Giấy chứng nhận bảo hiểm điện tử c&oacute; nhiều ưu điểm nổi trội, gi&uacute;p Kh&aacute;ch h&agrave;ng tiếp cận v&agrave; c&aacute;c quyền lợi bảo hiểm nhanh ch&oacute;ng hơn, an to&agrave;n hơn v&agrave; dễ d&agrave;ng hơn, theo h&igrave;nh thức hiện đại nhất tr&ecirc;n thị trường bảo hiểm.<br /> <br />Thời gian &aacute;p dụng việc cấp giấy chứng nhận điện tử c&aacute;c sản phẩm chi tiết như sau:</p>\r\n<ul>\r\n<li>Từ 15/08/2016: ph&aacute;t h&agrave;nh GCN điện tử bảo hiểm người vay vốn</li>\r\n<li>Từ 15/09/2016: ph&aacute;t h&agrave;nh GCN điện tử bảo hiểm sức khỏe, bảo hiểm &ocirc; t&ocirc;, bảo hiểm nh&agrave;, bảo hiểm h&agrave;ng h&oacute;a, bảo hiểm du lịch quốc tế.</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/banner-2-GCN.jpg\" alt=\"\" width=\"960\" height=\"303\" /></p>\r\n<p>B&ecirc;n cạnh việc ph&aacute;t h&agrave;nh Giấy chứng nhận điện tử, VBI cũng lần đầu ph&aacute;t h&agrave;nh H&oacute;a đơn Điện tử cho c&aacute;c giao dịch thu ph&iacute; bảo hiểm tr&ecirc;n to&agrave;n quốc. Mẫu H&oacute;a đơn điện tử của VBI đ&atilde; được sự chấp thuận của Bộ T&agrave;i ch&iacute;nh v&agrave; c&oacute; gi&aacute; trị ph&aacute;p l&yacute; tương đương với H&oacute;a đơn gi&aacute; trị gia tăng in tr&ecirc;n giấy trước đ&acirc;y.<br /> <br />Qu&yacute; kh&aacute;ch sẽ nhận được h&oacute;a đơn điện tử qua email đ&atilde; đăng k&yacute; với VBI hoặc truy vấn dễ d&agrave;ng qua website tra cứu trực tuyến: http://tracuu.baohiem-vbi.vn<br /> <br />Việc sử dụng h&oacute;a đơn điện tử sẽ kh&ocirc;ng chỉ mang lại cho Qu&yacute; kh&aacute;ch h&agrave;ng sự thuận tiện v&agrave; nhanh ch&oacute;ng trong giao dịch, m&agrave; c&ograve;n đ&aacute;p ứng y&ecirc;u cầu bảo mật th&ocirc;ng tin, lưu trữ cũng như tra cứu h&oacute;a đơn của Qu&yacute; kh&aacute;ch được dễ d&agrave;ng hơn.</p>\r\n<p>Hiện nay, VBI l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n ph&aacute;t h&agrave;nh giấy chứng nhận bảo hiểm điện tử v&agrave; h&oacute;a đơn điện tử tới kh&aacute;ch h&agrave;ng.<br /> <br />Mọi thắc mắc li&ecirc;n quan đến Giấy chứng nhận điện tử v&agrave; H&oacute;a đơn điện tử, Qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ <strong>Hotline: 1900 55 8868</strong> để được hỗ trợ.</p>', '2018-03-22 02:29:19', '2018-03-22 13:29:19', 'images/news/1520828087_banner-2-GCN.jpg'),
(296, 4, 'VBI chuyển đổi mô hình tổ chức mới', 'vbi-chuyen-doi-mo-hinh-to-chuc-moi', '<p><span style=\"font-family: Arial;\">Ng&agrave;y 03/10/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ ra mắt m&ocirc; h&igrave;nh tổ chức mới. Buổi lễ c&oacute; sự tham dự của &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI, &ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc c&ugrave;ng to&agrave;n thể l&atilde;nh đạo v&agrave; c&aacute;n bộ c&aacute;c ph&ograve;ng ban, chi nh&aacute;nh VBI tr&ecirc;n to&agrave;n quốc.</span></p>', '<p>Ng&agrave;y 03/10/2016, tại H&agrave; Nội, Bảo hiểm VietinBank (VBI) đ&atilde; tổ chức lễ ra mắt m&ocirc; h&igrave;nh tổ chức mới. Buổi lễ c&oacute; sự tham dự của &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI, &ocirc;ng Nguyễn Hồng Phong &ndash; Ph&oacute; Tổng Gi&aacute;m đốc c&ugrave;ng to&agrave;n thể l&atilde;nh đạo v&agrave; c&aacute;n bộ c&aacute;c ph&ograve;ng ban, chi nh&aacute;nh VBI tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>Tại buổi lễ, &ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI ph&aacute;t biểu: &ldquo;Để tiếp tục n&acirc;ng cao năng lực cạnh tranh v&agrave; năng lực điều h&agrave;nh, ng&agrave;y h&ocirc;m nay, VBI ch&iacute;nh thức triển khai chuyển đổi m&ocirc; h&igrave;nh mới theo hướng cổ phần h&oacute;a nhằm đ&aacute;p ứng nhu cầu ng&agrave;y một ph&aacute;t triển của doanh nghiệp. Trong thời gian tới, VBI sẽ tiếp tục đổi mới c&ocirc;ng t&aacute;c quản trị, ph&aacute;t triển c&aacute;c hoạt động dịch vụ v&agrave; hệ thống sản phẩm mới đa dạng, cải tiến mạnh mẽ hệ thống c&ocirc;ng nghệ hiện đại, hệ thống dịch vụ b&aacute;n h&agrave;ng v&agrave; sau b&aacute;n h&agrave;ng.&rdquo;</p>\r\n<p><img src=\"../../../uploads/Anh chuyen doi mo hinh moi.jpg\" alt=\"\" width=\"1366\" height=\"1190\" /></p>\r\n<p style=\"text-align: center;\"><em>(Ảnh: &Ocirc;ng L&ecirc; Tuấn Dũng &ndash; Tổng Gi&aacute;m đốc VBI đ&aacute;nh chi&ecirc;ng ra mắt m&ocirc; h&igrave;nh tổ chức mới)</em></p>\r\n<p>Kết quả hoạt động 9 th&aacute;ng đầu năm 2016 của VBI cũng cho thấy những bước ph&aacute;t triển đột ph&aacute;, ghi dấu bằng kết quả hoạt động kinh doanh hiệu quả. Doanh thu ph&iacute; bảo hiểm gốc trong 9 th&aacute;ng đầu năm của VBI ước đạt 460 tỷ đồng, tăng trưởng 46% so với 9 th&aacute;ng đầu năm 2015. Lợi nhuận lũy kế 9 th&aacute;ng của VBI đạt 48 tỷ đồng. Năm 2016, kế hoạch doanh thu ph&iacute; bảo hiểm của VBI tr&ecirc;n 800 tỷ đồng, tăng trưởng 50% so với 2015.<br /> <br /> <br />Với chiến lược x&acirc;y dựng doanh nghiệp ph&aacute;t triển nhanh v&agrave; bền vững, trong năm 2016, VBI sẽ tiếp tục tạo ra những bước đột ph&aacute; trong việc cung cấp sản phẩm dịch vụ bảo hiểm, t&agrave;i ch&iacute;nh trọn g&oacute;i, linh hoạt, ho&agrave;n th&agrave;nh mục ti&ecirc;u chiến lược chuyển đổi m&ocirc; h&igrave;nh nhằm n&acirc;ng cao hiệu quả kinh doanh, mang lại gi&aacute; trị gia tăng cho kh&aacute;ch h&agrave;ng, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</p>\r\n<p style=\"text-align: right;\"><strong><em>VBI</em></strong></p>', '2018-03-22 02:29:45', '2018-03-22 13:29:45', 'images/news/1520828171_Anh chuyen doi mo hinh moi.jpg'),
(297, 4, 'Bảo hiểm VietinBank ra mắt Tổng đài Chăm sóc khách hàng 19001566', 'bao-hiem-vietinbank-ra-mat-tong-dai-cham-soc-khach-hang-19001566', '<p><span style=\"font-family: Arial;\">H&agrave; Nội, từ ng&agrave;y 5 th&aacute;ng 9 năm 2017 &ndash; Bảo hiểm VietinBank (VBI) ch&iacute;nh thức ra mắt Tổng đ&agrave;i Chăm s&oacute;c kh&aacute;ch h&agrave;ng hoạt động 24/7 19001566 &nbsp;</span></p>', '<p><strong><span style=\"font-family: Arial;\">H&agrave; Nội, từ ng&agrave;y 5 th&aacute;ng 9 năm 2017 &ndash; Bảo hiểm VietinBank (VBI) ch&iacute;nh thức ra mắt Tổng đ&agrave;i Chăm s&oacute;c kh&aacute;ch h&agrave;ng hoạt động 24/7 19001566 &nbsp;</span></strong></p>\r\n<p><img src=\"../../../uploads/lang nghe tu trai tim-BT02-3(1).jpg\" alt=\"\" width=\"700\" height=\"700\" /></p>\r\n<p>Thấu hiểu t&acirc;m tư v&agrave; nhu cầu của kh&aacute;ch h&agrave;ng trong việc được li&ecirc;n tục chăm s&oacute;c v&agrave; tư vấn c&aacute;c th&ocirc;ng tin khi tham gia bảo hiểm, Tổng đ&agrave;i Chăm s&oacute;c kh&aacute;ch h&agrave;ng của Bảo hiểm Vietinbank ra mắt nhằm g&oacute;p phần n&acirc;ng cao t&iacute;nh chuy&ecirc;n nghiệp trong c&ocirc;ng t&aacute;c chăm s&oacute;c kh&aacute;ch h&agrave;ng, triển khai c&aacute;c chiến dịch khảo s&aacute;t thị hiếu v&agrave; ghi nhận &yacute; kiến đ&oacute;ng g&oacute;p của kh&aacute;ch h&agrave;ng, từ đ&oacute; nỗ lực cải tiến sản phẩm v&agrave; dịch vụ nhằm mang lại cho kh&aacute;ch h&agrave;ng sự h&agrave;i l&ograve;ng cao nhất.</p>\r\n<p>Để Tổng đ&agrave;i Chăm s&oacute;c kh&aacute;ch h&agrave;ng đi v&agrave;o hoạt động một c&aacute;ch hiệu quả, Bảo hiểm Vietinbank đ&atilde; ứng dụng c&aacute;c c&ocirc;ng nghệ hiện đại ti&ecirc;n tiến như:</p>\r\n<ul>\r\n<li><em><strong>Tốc độ đường truyền nhanh ch&oacute;ng, m&aacute;y m&oacute;c hỗ trợ hiện đại trang bị cho tổng đ&agrave;i vi&ecirc;n.</strong></em></li>\r\n<li><em><strong>Hệ thống CallCenter tối ưu h&oacute;a c&aacute;c ứng dụng kết nối đa k&ecirc;nh, đa chiều, hệ thống gọi ra, hỗ trợ quản l&yacute; thống k&ecirc; tần suất c&aacute;c cuộc gọi, t&iacute;ch hợp quản l&yacute; cơ sở dữ liệu kh&aacute;ch h&agrave;ng, cho ph&eacute;p tự động truy xuất th&ocirc;ng tin kh&aacute;ch h&agrave;ng khi c&oacute; cuộc gọi đến, c&oacute; khả năng mở rộng linh hoạt ph&ugrave; hợp với mọi m&ocirc; h&igrave;nh doanh nghiệp với dịch vụ hỗ trợ</strong></em></li>\r\n<li><em><strong>Tổng đ&agrave;i sử dụng hệ thống ACD/IVR, giải ph&aacute;p Customer Interaction Center (CIC) gi&aacute;m s&aacute;t, ph&ograve;ng chống gian lận c&ugrave;ng c&aacute;c thiết bị đảm bảo an ninh như Camera gi&aacute;m s&aacute;t, bảo mật th&ocirc;ng tin, ở cả dạng cứng v&agrave; mềm.</strong></em></li>\r\n<li><em><strong>Hệ thống vận h&agrave;nh dịch vụ của VBI tu&acirc;n thủ theo quy tr&igrave;nh vận h&agrave;nh bảo mật dữ liệu ISO 27001:2500.</strong></em></li>\r\n</ul>\r\n<p><br />B&ecirc;n cạnh đ&oacute;, đội ngũ Điện thoại vi&ecirc;n được đ&agrave;o tạo chuy&ecirc;n nghiệp v&agrave; b&agrave;i bản để tiếp nhận y&ecirc;u cầu của Kh&aacute;ch h&agrave;ng, đạt c&aacute;c ti&ecirc;u chuẩn KPI bao gồm:</p>\r\n<ul>\r\n<li><em><strong>Nhấc m&aacute;y trả lời trước 3 gi&acirc;y (&le; 3s)</strong></em></li>\r\n<li><em><strong>Đảm bảo chất lượng cuộc gọi của kh&aacute;ch h&agrave;ng đạt chỉ số h&agrave;i l&ograve;ng tr&ecirc;n 95%</strong></em></li>\r\n<li><em><strong>Tổng đ&agrave;i vi&ecirc;n hoạt động li&ecirc;n tục 24 giờ trong ng&agrave;y, 7 ng&agrave;y trong tuần (kể cả ng&agrave;y nghỉ, ng&agrave;y Lễ) với chức năng:Tiếp nhận tư vấn sản phẩm, hướng dẫn thủ tục lập hồ sơ bồi thường v&agrave; thanh to&aacute;n tiền bảo hiểm: Bảo hiểm xe cơ giới, Bảo hiểm Con người VBICare v&agrave; c&aacute;c loại h&igrave;nh bảo hiểm kh&aacute;c; Tiếp nhận th&ocirc;ng b&aacute;o tai nạn v&agrave; y&ecirc;u cầu bồi thường cho c&aacute;c nghiệp vụ bảo hiểm xe cơ giới; Tiếp nhận v&agrave; giải quyết mọi vướng mắc, khiếu nại của Kh&aacute;ch h&agrave;ng, ghi nhận những đ&oacute;ng g&oacute;p &yacute; kiến về c&aacute;c sản phẩm, dịch vụ bảo hiểm từ kh&aacute;ch h&agrave;ng; Hỗ trợ tiếp cận với c&aacute;c dịch vụ cứu hộ xe &ocirc; t&ocirc;; chỉ dẫn địa chỉ Gara li&ecirc;n kết; bệnh viện/ph&ograve;ng kh&aacute;m nằm trong hệ thống bảo l&atilde;nh viện ph&iacute;.</strong></em></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&Ocirc;ng L&ecirc; Tuấn Dũng, Tổng Gi&aacute;m đốc Bảo hiểm Vietinbank chia sẻ: <em>&ldquo;Kh&aacute;ch h&agrave;ng c&oacute; quyền được đ&ograve;i hỏi sự tư vấn thấu đ&aacute;o về sản phẩm v&agrave; dịch vụ bảo hiểm trước khi tham gia bảo hiểm v&agrave; c&aacute;c dịch vụ chăm s&oacute;c sau khi tham gia bảo hiểm. Hoạt động trong lĩnh vực bảo hiểm, ch&uacute;ng t&ocirc;i quan niệm rằng C&ocirc;ng ty Bảo hiểm kh&ocirc;ng chỉ đơn thuần mang lại g&oacute;i sản phẩm bảo vệ t&agrave;i ch&iacute;nh trước c&aacute;c rủi ro, m&agrave; c&ograve;n phải mang lại cả dịch vụ chăm s&oacute;c v&agrave; tư vấn xuy&ecirc;n suốt. Ch&iacute;nh v&igrave; vậy, Bảo hiểm Vietinbank đ&atilde; đầu tư nguồn lực mạnh mẽ để ra mắt Tổng đ&agrave;i Chăm s&oacute;c kh&aacute;ch h&agrave;ng c&oacute; chất lượng cao. Việc ra mắt Tổng đ&agrave;i Chăm s&oacute;c kh&aacute;ch h&agrave;ng 24/7 to&agrave;n diện đ&aacute;nh dấu một bước chuyển lớn trong việc chuẩn h&oacute;a v&agrave; tăng cường c&aacute;c dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng của Bảo hiểm Vietinbank trong thời gian tới.&rdquo;</em><br /> <br />Vừa qua, Bảo hiểm Vietinbank đ&atilde; vinh dự nhận được 02 giải thưởng danh gi&aacute; quốc tế: C&ocirc;ng ty Bảo hiểm phi nh&acirc;n thọ c&oacute; dịch vụ kh&aacute;ch h&agrave;ng tốt nhất Việt Nam 2017 do Tạp ch&iacute; Global Banking and Finance Vương quốc Anh b&igrave;nh chọn v&agrave; đạt chỉ số h&agrave;i l&ograve;ng kh&aacute;ch h&agrave;ng CSI do Tổ chức đ&aacute;nh gi&aacute; v&agrave; chứng nhận Quốc tế InterConformity (CHLB Đức, Ch&acirc;u &Acirc;u) gi&aacute;m s&aacute;t v&agrave; đo lường.</p>\r\n<p><img src=\"../../../uploads/v2.jpg\" alt=\"\" width=\"614\" height=\"614\" /></p>\r\n<p><img src=\"../../../uploads/v1.jpg\" alt=\"\" width=\"411\" height=\"584\" /></p>\r\n<p>Đ&acirc;y l&agrave; bằng chứng nỗ lực li&ecirc;n tục v&agrave; kh&ocirc;ng ngừng của VBI trong việc hướng tới lợi &iacute;ch của kh&aacute;ch h&agrave;ng.</p>\r\n<p>Tổng đ&agrave;i chăm s&oacute;c kh&aacute;ch h&agrave;ng của Bảo hiểm Vietinbank hứa hẹn đem đến một phong c&aacute;ch dịch vụ chuy&ecirc;n nghiệp, nhanh ch&oacute;ng v&agrave; th&ocirc;ng minh, đ&aacute;p ứng xu thế thời đại c&ocirc;ng nghệ 4.0 cũng như đặt quyền lợi kh&aacute;ch h&agrave;ng l&ecirc;n h&agrave;ng đầu. Bảo hiểm Vietinbank hướng đến trở th&agrave;nh Doanh nghiệp bảo hiểm c&oacute; chất lượng dịch vụ kh&aacute;ch h&agrave;ng tốt nhất Việt Nam.</p>\r\n<p style=\"text-align: right;\"><em><strong>VBI</strong></em></p>', '2018-03-22 02:31:00', '2018-03-22 13:31:00', 'images/news/1520828321_Lắng nghe từ trái tim-BT02-3(1).jpg'),
(298, 4, 'Thông báo mời họp Đại hội đồng cổ đông lần thứ nhất Tổng công ty Cổ phần Bảo hiểm VietinBank', 'thong-bao-moi-hop-dai-hoi-dong-co-dong-lan-thu-nhat-tong-cong-ty-co-phan-bao-hiem-vietinbank', '<p><span style=\"font-family: Arial;\">Ban tổ chức Đại hội đồng cổ đ&ocirc;ng Tổng C&ocirc;ng ty Cổ phần Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam (VBI) tr&acirc;n trọng k&iacute;nh mới Qu&yacute; vị Cổ đ&ocirc;ng tham gia cuộc họp Đại hội cổ đ&ocirc;ng lần thứ nhất của VBI</span></p>', '<p><img src=\"../../../uploads/1_ Thu moi DHDCD-1.jpg\" alt=\"\" width=\"831\" height=\"1200\" /></p>\r\n<p><strong>Tham khảo t&agrave;i liệu họp Đại hội Đồng Cổ đ&ocirc;ng Lần thứ Nhất VBI:</strong></p>\r\n<ul>\r\n<li><strong>Giấy Đăng k&yacute;, x&aacute;c nhận tham dự họp Đại hội Đồng Cổ đ&ocirc;ng VBI</strong></li>\r\n<li><strong>Chương tr&igrave;nh Đại hội</strong></li>\r\n<li><strong>Giấy Ủy quyền tham dự Đại hội Đồng Cổ đ&ocirc;ng VBI</strong></li>\r\n<li><strong>Quy chế tổ chức Đại hội Đồng Cổ đ&ocirc;ng VBI</strong></li>\r\n<li><strong>Quy chế bầu cử Hội đồng Quản trị v&agrave; Ban Kiểm so&aacute;t</strong></li>\r\n<li><strong>B&aacute;o c&aacute;o qu&aacute; tr&igrave;nh chuyển đổi m&ocirc; h&igrave;nh</strong></li>\r\n<li><strong>Dự thảo điều lệ</strong></li>\r\n<li><strong>Quy chế tổ chức v&agrave; hoạt động Hội đồng Quản trị</strong></li>\r\n<li><strong>Quy chế tổ chức v&agrave; hoạt động Ban Kiểm so&aacute;t</strong></li>\r\n<li><strong>B&aacute;o c&aacute;o kh&aacute;ch h&agrave;ng SXKD 03 năm 2018-2020</strong></li>\r\n<li><strong>Đơn ứng cử th&agrave;nh vi&ecirc;n Hội đồng Quản trị v&agrave; Ban Kiểm so&aacute;t</strong></li>\r\n<li><strong>Đơn đề cử th&agrave;nh vi&ecirc;n Hội đồng Quản trị v&agrave; Ban Kiểm so&aacute;t</strong></li>\r\n<li><strong>Mẫu SYLL ứng cử vi&ecirc;n Hội đồng Quản trị v&agrave; Ban Kiểm so&aacute;t</strong></li>\r\n<li><strong>Tờ tr&igrave;nh lựa chọn đơn vị kiểm to&aacute;n 2017</strong></li>\r\n<li><strong>Tờ tr&igrave;nh phương &aacute;n tăng vốn</strong></li>\r\n<li><strong>Tờ tr&igrave;nh số lượng th&agrave;nh vi&ecirc;n Hội đồng Quản trị v&agrave; Ban Kiểm so&aacute;t</strong></li>\r\n</ul>', '2018-03-22 02:31:23', '2018-03-22 13:31:23', 'images/news/1520828526_Untitled-1.jpg'),
(299, 4, 'Sơ yếu lý lịch thành viên Hội Đồng Quản trị và Ban Kiểm soát', 'so-yeu-ly-lich-thanh-vien-hoi-dong-quan-tri-va-ban-kiem-soat-1', '<p><span style=\"font-family: Arial;\">Danh s&aacute;ch th&agrave;nh vi&ecirc;n Hội Đồng Quản trị v&agrave; Ban Kiểm so&aacute;t</span></p>', '<p>&nbsp;</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"10\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\"><strong>TH&Agrave;NH VI&Ecirc;N HỘI ĐỒNG QUẢN TRỊ</strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>STT</strong></td>\r\n<td><strong>Th&agrave;nh vi&ecirc;n</strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>1</strong></td>\r\n<td><a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/13-Mau-SY_636474034854495746.pdf\"><strong>Nguyễn Hồng V&acirc;n</strong></a></td>\r\n</tr>\r\n<tr>\r\n<td><strong>2</strong></td>\r\n<td><a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/13-Mau-SY_636474034334638688.pdf\"><strong>L&ecirc; Tuấn Dũng</strong></a></td>\r\n</tr>\r\n<tr>\r\n<td><strong>3</strong></td>\r\n<td><a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/13-Mau-SY_636474035438885706.pdf\"><strong>Trần Thị Hương Thủy</strong></a></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\"><strong>TH&Agrave;NH VI&Ecirc;N&nbsp;BAN KIỂM SO&Aacute;T</strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>STT</strong></td>\r\n<td><strong>Th&agrave;nh vi&ecirc;n</strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>1</strong></td>\r\n<td><a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/13-Mau-SY_636474036239374948.pdf\"><strong>Nguyễn Th&aacute;i Dung</strong></a></td>\r\n</tr>\r\n<tr>\r\n<td><strong>2</strong></td>\r\n<td><a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/13-Mau-SY_636474036727356190.pdf\"><strong>L&ecirc; Thị Huyền Trang</strong></a></td>\r\n</tr>\r\n<tr>\r\n<td><strong>3</strong></td>\r\n<td><a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/13-Mau-SY_636474037221587592.pdf\"><strong>Nguyễn Ngọc Quỳnh</strong></a></td>\r\n</tr>\r\n</tbody>\r\n</table>', '2018-03-12 15:23:45', '2018-03-12 15:23:45', 'images/news/1520828625_Untitled-1.jpg'),
