INSERT INTO `pages` (`id`, `page_categoryid`, `title`, `slug`, `thumbnail`, `content`, `template`, `created_at`, `updated_at`) VALUES
(1, 1, 'Homepage', 'homepage', NULL, NULL, 'homepage', '2018-01-08 07:37:48', '2018-01-19 07:05:39'),
(2, 2, 'TỔNG CÔNG TY CỔ PHẦN BẢO HIỂM VIETINBANK', 'tong-the', 'image/page/1515549192_about-1.jpg', '<p><span style=\"font-weight: 400;\">Tổng c&ocirc;ng ty cổ phần Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam (Bảo hiểm VietinBank) tiền th&acirc;n l&agrave; C&ocirc;ng ty TNHH MTV Bảo hiểm Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng Thương Việt Nam.</span>&nbsp;</p>\r\n<p><span style=\"font-weight: 400;\">Được th&agrave;nh lập năm 2008 đến nay, qua gần 10 năm c&oacute; mặt tr&ecirc;n thị trường bảo hiểm Việt Nam,&nbsp;Bảo hiểm VietinBank (BẢO HIỂM VIETINBANK) đ&atilde; tăng trưởng, ph&aacute;t triển nhanh, mạnh mẽ, hướng tới mục ti&ecirc;u dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm Việt Nam.</span></p>\r\n<p><span style=\"font-weight: 400;\"><img src=\"../../../uploads/anh-tong-the-trang-gioi-thieu-2.jpg\" alt=\"\" width=\"1361\" height=\"796\" /></span></p>\r\n<p><span style=\"font-weight: 400;\">Minh chứng cho những th&agrave;nh c&ocirc;ng của Bảo hiểm VietinBank, năm 2017, Bảo hiểm VietinBank đ&atilde; ho&agrave;n th&agrave;nh xuất sắc c&aacute;c chỉ ti&ecirc;u kinh doanh với doanh thu ph&iacute; bảo hiểm đạt 1000 tỷ, ch&iacute;nh thức tham gia c&acirc;u lạc bộ doanh nghiệp ng&agrave;n tỷ trong thị trường Việt Nam, c&ugrave;ng với đ&oacute;, Bảo hiểm VietinBank tự h&agrave;o nhận giải thưởng danh gi&aacute; &ldquo;C&ocirc;ng ty Bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam năm 2017&rdquo; do tạp ch&iacute; t&agrave;i ch&iacute;nh Vương quốc Anh Global Banking &amp; Finanace Review (GBAF) trao tặng.</span></p>\r\n<p><span style=\"font-weight: 400;\">Trong năm 2017 ghi nhận những con số tăng trưởng tốt:</span></p>\r\n<p><span style=\"font-weight: 400;\">Doanh thu ph&iacute; bảo hiểm tăng trưởng 29.4% so với năm 2016 (trong đ&oacute;, doanh thu bảo hiểm gốc đạt 892 tỷ đồng, tăng trưởng 28.84% so với năm 2016; doanh thu nhận t&aacute;i bảo hiểm đạt 118.64 tỷ đồng, tăng trưởng 33.83% so với năm 2016); lợi nhuận trước thuế đạt 84.5 tỷ đồng, tăng trưởng 12.52% so với năm 2016. Trong năm 2018, với mục ti&ecirc;u kinh doanh 1.500 tỷ, Bảo hiểm VietinBank tự tin vươn l&ecirc;n TOP 5 c&ocirc;ng ty bảo hiểm tr&ecirc;n thị trường Việt Nam với c&aacute;c ti&ecirc;u ch&iacute; tốt nhất về sản phẩm, dịch vụ v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng. C&ugrave;ng với sự ph&aacute;t triển lớn mạnh trong năm 2017, Bảo hiểm VietinBank đ&atilde; vinh dự nhận nhiều danh hiệu uy t&iacute;n như:</span></p>\r\n<ul>\r\n<li><strong><span style=\"font-weight: 400;\">&ldquo;</span>C&ocirc;ng ty bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam 2017<span style=\"font-weight: 400;\">\", trao tặng bởi tạp ch&iacute; Global Banking &amp; Finance Review</span></strong></li>\r\n</ul>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">Xếp hạng 79 trong bảng xếp hạng&nbsp;\"</span><strong>500 Doanh nghiệp tăng trưởng nhanh nhất Việt Nam - FAST500</strong><span style=\"font-weight: 400;\">\" do Vietnam Report c&ocirc;ng bố</span></li>\r\n</ul>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">danh hiệu&nbsp;\"</span><strong>Thương Hiệu Mạnh&nbsp;2017</strong><span style=\"font-weight: 400;\">\"</span></li>\r\n</ul>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">danh hiệu&nbsp;\"</span><strong>TOP 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam&nbsp;2016</strong><span style=\"font-weight: 400;\"><strong>\"</strong></span></li>\r\n</ul>\r\n<p>&nbsp;<span style=\"font-weight: 400;\">Song song với hoạt động kinh doanh ph&aacute;t triển mạnh mẽ trong thị trường bảo hiểm những năm qua, Bảo hiểm VietinBank c&ograve;n ghi dấu ấn với nhiều hoạt động v&igrave; cộng đồng &yacute; nghĩa như: tham gia Ng&agrave;y hội hiến m&aacute;u nh&acirc;n đạo, chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; tại Bệnh viện Nhi Trung Ương c&ugrave;ng VietinBank, tổ chức chương tr&igrave;nh từ thiện &ldquo;&Aacute;o ấm trao em&rdquo;,v.v,&hellip;</span></p>\r\n<p><span style=\"font-weight: 400;\">Trong năm 2017, Bảo hiểm VietinBank l&agrave; một trong những c&ocirc;ng ty bảo hiểm đầu ti&ecirc;n tại Việt Nam mạng c&ocirc;ng nghệ v&agrave;o thị trường bảo hiểm bằng việc triển khai Giấy chứng nhận điện tử, H&oacute;a đơn điện tử. Đặc biệt, đ&aacute;nh dấu cho năm 2017, Bảo hiểm VietinBank l&agrave; đơn vị đầu ti&ecirc;n trong thị trường bảo hiểm Việt Nam triển khai th&agrave;nh c&ocirc;ng bảo l&atilde;nh viện ph&iacute; v&agrave; gi&aacute;m định bồi thường trực tuyến, mua sản phẩm bảo hiểm trực tuyến th&ocirc;ng qua </span><a href=\"http://vbi.vietinbank.vn/tin-tuc/trai-nghiem-boi-thuong-bao-hiem-chua-bao-gio-tien-loi-den-the.htm\"><span style=\"font-weight: 400;\">ứng dụng MyVBI</span></a><span style=\"font-weight: 400;\"> &ndash; thay đổi c&aacute;ch nh&igrave;n về bảo hiểm v&agrave; n&acirc;ng cao trải nghiệm dịch vụ cho kh&aacute;ch h&agrave;ng.</span></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm VietinBank lấy chiến lược ph&aacute;t triển tối ưu sản phẩm, n&acirc;ng cao trải nghiệm bảo hiểm cho kh&aacute;ch h&agrave;ng đồng thời t&iacute;ch ứng c&ocirc;ng nghệ hiện đại, ph&ugrave; hợp với nhu cầu v&agrave; xu hướng ph&aacute;t triển bảo hiểm tại Việt Nam l&agrave;m mũi nhọn trong kinh doanh v&agrave; ph&aacute;t triển bền vững trong những năm tiếp theo.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-weight: 400; font-size: 18pt;\">CH&Uacute;NG T&Ocirc;I L&Agrave; BẢO HIỂM VIETINBANK&nbsp;<br /> CH&Uacute;NG T&Ocirc;I SINH RA ĐỂ DẪN ĐẦU</span></p>', 'default', '2018-01-10 06:53:12', '2018-03-20 14:25:26'),
(3, 2, 'TẦM NHÌN – GIÁ TRỊ CỐT LÕI', 'tam-nhin-gia-tri-cot-loi', 'image/page/1521798949_gia tri cot loi.jpg', '<p><strong><span style=\"font-family: Arial;\"><img style=\"float: left;\" src=\"../../../uploads/tam-nhin.jpg\" width=\"343\" height=\"196\" /></span></strong></p>\r\n<p><strong>Tầm nh&igrave;n:</strong></p>\r\n<p><span style=\"font-weight: 400;\">BẢO HIỂM VIETINBANK hướng tới dẫn đầu ph&acirc;n kh&uacute;c b&aacute;n lẻ tr&ecirc;n thị trường bảo hiểm phi nh&acirc;n thọ Việt Nam.</span></p>\r\n<p><strong>Slogan:</strong><span style=\"font-weight: 400;\">&nbsp;</span><span style=\"font-weight: 400;\"><br /></span><span style=\"font-weight: 400;\">Bảo to&agrave;n gi&aacute; trị cuộc sống.</span></p>\r\n<p><strong>Gi&aacute; trị cốt l&otilde;i:</strong></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Hướng đến kh&aacute;ch h&agrave;ng:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Kh&aacute;ch h&agrave;ng l&agrave; trung t&acirc;m của mọi hoạt động của Bảo hiểm VietinBank, Bảo hiểm VietinBank cam kết mang đến những sản phẩm, dịch vụ đ&aacute;p ứng tốt nhất mọi nhu cầu của kh&aacute;ch h&agrave;ng</span></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Hướng đến sự ho&agrave;n hảo:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm VietinBank nỗ &nbsp;lực &nbsp;kh&ocirc;ng &nbsp;ngừng ho&agrave;n &nbsp;thiện &nbsp;c&aacute;c &nbsp;sản &nbsp;phẩm, &nbsp;dịch &nbsp;vụ th&ocirc;ng qua việc tiếp thu những &yacute; kiến, th&ocirc;ng tin phản hồi về chất lượng &nbsp;sản &nbsp;phẩm, &nbsp;dịch &nbsp;vụ từ &nbsp;kh&aacute;ch &nbsp;h&agrave;ng, đối &nbsp;t&aacute;c v&agrave; thị trường.</span></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Năng động, s&aacute;ng tạo, chuy&ecirc;n nghiệp, hiện đại:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm VietinBank lu&ocirc;n thể hiện sự năng động, s&aacute;ng tạo, chuy&ecirc;n nghiệp, tận t&acirc;m, minh bạch v&agrave; hiện đại trong mọi giao dịch, quan hệ với kh&aacute;ch h&agrave;ng, đối t&aacute;c v&agrave; đồng nghiệp.</span></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Trung thực, ch&iacute;nh trực, minh bạch, đạo đức nghề nghiệp:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm VietinBank cam kết h&agrave;nh động, kinh doanh, cạnh tranh c&ocirc;ng bằng, đạo đức, tr&aacute;ch nhiệm theo đ&uacute;ng quy định của ph&aacute;p luật.</span></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Sự t&ocirc;n trọng:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm VietinBank t&ocirc;n trọng, chia sẻ, quan t&acirc;m đối với kh&aacute;ch h&agrave;ng, đối t&aacute;c, l&atilde;nh đạo v&agrave; đồng nghiệp.</span></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Bảo vệ v&agrave; ph&aacute;t triển thương hiệu:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm VietinBank cung cấp c&aacute;c sản phẩm, dịch vụ c&oacute; uy t&iacute;n, chất lượng cao cho kh&aacute;ch h&agrave;ng để bảo vệ v&agrave; ph&aacute;t triển thương hiệu Bảo hiểm VietinBank.</span></p>\r\n<p><span style=\"text-decoration: underline;\"><span style=\"font-weight: 400;\">Ph&aacute;t triển bền vững v&agrave; tr&aacute;ch nhiệm với cộng đồng, x&atilde; hội:</span></span></p>\r\n<p><span style=\"font-weight: 400;\">Đổi mới, s&aacute;ng tạo l&agrave; động lực; tăng trưởng, ph&aacute;t triển, kinh doanh an to&agrave;n, hiệu quả, bền vững l&agrave; mục ti&ecirc;u. Thực hiện tốt tr&aacute;ch nhiệm với cộng đồng, x&atilde; hội nhằm mang lại cuộc sống chung tốt đẹp hơn.</span></p>', 'default', '2018-01-10 06:53:36', '2018-03-23 20:55:49'),
(4, 2, 'Cơ Cấu Quản Trị', 'co-cau-quan-tri', 'image/page/1521518812_trang-gioi-thieu-co-cau-quan-tri-2.jpg', '<p>&nbsp;</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../../../uploads/nguyen-hong-van.jpg\" alt=\"\" width=\"860\" height=\"1035\" /></td>\r\n<td>\r\n<p><span style=\"font-weight: 400;\"><strong>B&agrave; Nguyễn Hồng V&acirc;n &ndash; Chủ tịch Hội đồng quản trị - Chủ tịch C&ocirc;ng ty</strong>&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400;\">B&agrave; Nguyễn Hồng V&acirc;n tốt nghiệp Thạc sĩ T&agrave;i ch&iacute;nh Ng&acirc;n h&agrave;ng. B&agrave; bắt đầu c&ocirc;ng t&aacute;c tại Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam từ năm 1990. B&agrave; đ&atilde; từng giữ c&aacute;c chức vụ: Ph&oacute; ph&ograve;ng C&acirc;n đối Tổng hợp; Trưởng ph&ograve;ng Kế hoạch Tổng hợp v&agrave; Đầu tư; Trưởng ph&ograve;ng kế hoạch Tổng hợp v&agrave; Hỗ trợ ALCO. Hiện b&agrave; đang giữ chức Uỷ vi&ecirc;n Hội đồng quản trị Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;<strong>&Ocirc;ng L&ecirc; Tuấn Dũng - Ủy vi&ecirc;n Hội Đồng Quản trị - Tổng Gi&aacute;m đốc</strong></td>\r\n<td>\r\n<p><span style=\"font-weight: 400;\">&Ocirc;ng L&ecirc; Tuấn Dũng tốt nghiệp Cử nh&acirc;n Kinh tế chuy&ecirc;n ng&agrave;nh Bảo hiểm v&agrave; Thạc sỹ Quản trị kinh doanh, Đại học Kinh tế quốc d&acirc;n. &Ocirc;ng c&oacute; gần 20&nbsp;năm kinh nghiệm trong ng&agrave;nh bảo hiểm v&agrave; đ&atilde; đảm nhiệm c&aacute;c vị tr&iacute; quan trọng từ quản l&yacute; nghiệp vụ đến t&aacute;i bảo hiểm v&agrave; kinh doanh trực tiếp tại c&aacute;c doanh nghiệp bảo hiểm như Tổng C&ocirc;ng ty Bảo hiểm Qu&acirc;n đội, Tổng C&ocirc;ng ty Bảo hiểm Bưu điện v&agrave; Tổng C&ocirc;ng ty Bảo hiểm Dầu kh&iacute;.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;<strong>&Ocirc;ng Nguyễn Hồng Phong - Ph&oacute; Tổng Gi&aacute;m đốc</strong></td>\r\n<td>\r\n<p><span style=\"font-weight: 400;\">&Ocirc;ng Nguyễn Hồng Phong tốt nghiệp Cử nh&acirc;n Kinh tế tại Đại học Thương Mại v&agrave; Thạc sỹ Quản trị kinh doanh tại Đại học Kinh tế quốc d&acirc;n. Trước khi được bổ nhiệm Ph&oacute; Tổng gi&aacute;m đốc VBI v&agrave;o th&aacute;ng 7/2013, &ocirc;ng đ&atilde; trải qua c&aacute;c vị tr&iacute; phụ tr&aacute;ch kinh doanh v&agrave; quản l&yacute; nghiệp vụ tại Bảo hiểm VietinBank, C&ocirc;ng ty Bảo hiểm Ng&acirc;n h&agrave;ng N&ocirc;ng nghiệp v&agrave;&nbsp;Tổng C&ocirc;ng ty bảo hiểm Dầu kh&iacute;.</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;<strong>B&agrave; Trần Thị Hương Thủy &ndash; Ph&oacute; Tổng Gi&aacute;m đốc</strong></td>\r\n<td>\r\n<p><span style=\"font-weight: 400;\">B&agrave; Trần Thị Hương Thủy tốt nghiệp Cử nh&acirc;n&nbsp;Đại học Luật&nbsp;v&agrave; Thạc sỹ chuy&ecirc;n ng&agrave;nh Quản trị kinh doanh tại Đại học Latrobe. B&agrave; Thủy c&oacute; 20 năm kinh nghiệm c&ocirc;ng t&aacute;c tại Ng&acirc;n h&agrave;ng C&ocirc;ng thương Việt Nam. Trước khi được bổ nhiệm vị tr&iacute; Ph&oacute; tổng Gi&aacute;m đốc VBI v&agrave;o th&aacute;ng 10/2016, b&agrave; đ&atilde; giữ vị tr&iacute; Trưởng ph&ograve;ng Tổ chức H&agrave;nh ch&iacute;nh Bảo hiểm VietinBank.</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 'gioithieu', '2018-01-10 06:54:31', '2018-03-20 15:06:52'),
(5, 2, 'Giải thưởng và danh hiệu', 'giai-thuong-va-danh-hieu', 'image/page/1522140491_giai thuong4.jpg', '<p><strong>Năm 2017</strong></p>\r\n<ul>\r\n<li>VBI tự h&agrave;o l&agrave; \"<strong>C&ocirc;ng ty bảo hiểm tốt nhất cho dịch vụ kh&aacute;ch h&agrave;ng Việt Nam 2017</strong>\", trao tặng bởi tạp ch&iacute; Global Banking &amp; Finance Review</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/img.jpg\" alt=\"\" width=\"1366\" height=\"912\" /></p>\r\n<ul>\r\n<li>VBI vinh dự xếp hạng 79 trong bảng xếp hạng \"<strong>500 Doanh nghiệp tăng trưởng nhanh nhất Việt Nam - FAST500</strong>\" do Vietnam Report c&ocirc;ng bố</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/VBI cong ty bao hiem tang truong nhanh nhat VN.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<ul>\r\n<li>VBI nhận danh hiệu \"<strong>Thương Hiệu Mạnh 2017</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/VBI thuong hieu manh Viet Nam_web (1).jpg\" alt=\"\" width=\"1024\" height=\"586\" /></p>\r\n<p><strong>Năm 2016</strong></p>\r\n<ul>\r\n<li>VBI nhận danh hiệu \"<strong>Doanh nghiệp nộp thuế ti&ecirc;u biểu 2016</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMG_6240.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<ul>\r\n<li>VBI nhận danh hiệu \"<strong>Doanh nghiệp ti&ecirc;u biểu v&igrave; quyền lợi người ti&ecirc;u d&ugrave;ng 2016</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMG_6305-.jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<ul>\r\n<li>VBI nhận danh hiệu \"<strong>TOP 10 C&ocirc;ng ty bảo hiểm uy t&iacute;n nhất Việt Nam 2016</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMG_0824.jpg\" alt=\"\" width=\"1366\" height=\"790\" /></p>\r\n<ul>\r\n<li>VBI nhận danh hiệu \"<strong>Thương hiệu mạnh Việt Nam 2016</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/_MG_6010_fix.jpg\" alt=\"\" width=\"1366\" height=\"929\" /></p>\r\n<p><strong>Năm 2015</strong></p>\r\n<ul>\r\n<li>VBI nhận giải thưởng <strong>Đơn vị c&oacute; th&agrave;nh t&iacute;ch xuất sắc</strong> tại Hội nghị tổng kết hoạt động kinh doanh năm 2015 do VietinBank tổ chức</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/hoi nghi vietinbank 2016_web.jpg\" alt=\"\" width=\"800\" height=\"548\" /></p>\r\n<ul>\r\n<li>VBI tự h&agrave;o nhận danh hiệu \"<strong>Sao v&agrave;ng đất Việt</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/VBI nhan giai sao vang dat viet_web.jpg\" alt=\"\" width=\"640\" height=\"427\" /></p>\r\n<ul>\r\n<li>VBI nhận giải thưởng <strong>Top 20 Nh&atilde;n hiệu nổi tiếng</strong> &ndash; Nh&atilde;n hiệu cạnh tranh Việt Nam năm 2015</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/5.jpg\" alt=\"\" width=\"1366\" height=\"768\" /></p>\r\n<p><strong>Năm 2014</strong></p>\r\n<ul>\r\n<li>VBI nhận danh hiệu \"<strong>Thương hiệu mạnh Việt Nam 2014</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/anh dang web.jpg\" alt=\"\" width=\"640\" height=\"462\" /></p>\r\n<ul>\r\n<li>Tổng Gi&aacute;m đốc VBI vinh dự nhận Giải thưởng \"<strong>Top 100 Doanh nh&acirc;n trẻ ti&ecirc;u biểu</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/Award.jpg\" alt=\"\" width=\"1366\" height=\"1012\" /></p>\r\n<ul>\r\n<li>Tổng Gi&aacute;m đốc VBI được vinh danh trong \"<strong>Top 100 nh&agrave; quản l&yacute; ti&ecirc;u biểu</strong>\"</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/TGD_VBI.jpg\" alt=\"\" width=\"835\" height=\"768\" /></p>\r\n<ul>\r\n<li>VBI được t&ocirc;n vinh l&agrave; <strong>doanh nghiệp nộp thuế ti&ecirc;u biểu 2013</strong>. Năm 2013, Bảo hiểm VietinBank (VBI) đạt doanh thu hơn 174 tỷ đồng, lợi nhuận trước thuế 48 tỷ đồng, đ&oacute;ng g&oacute;p v&agrave;o ng&acirc;n s&aacute;ch Nh&agrave; nước 25 tỷ đồng.</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/nopthue.jpg\" alt=\"\" width=\"695\" height=\"467\" /></p>\r\n<p><strong>Năm 2013</strong></p>\r\n<ul>\r\n<li>VBI tự h&agrave;o nằm trong <strong>TOP 1000 doanh nghiệp nộp thuế cao nhất Việt Nam</strong></li>\r\n</ul>\r\n<p><img src=\"../../../uploads/V1000_2013390.jpg\" alt=\"\" width=\"1366\" height=\"727\" /></p>\r\n<ul>\r\n<li>VBI vinh dự được tổ chức FAST 500 xếp hạng thứ 135 trong <strong>500 Doanh nghiệp tăng trưởng nhanh nhất Việt Nam</strong>.</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/Fast500.jpg\" alt=\"\" width=\"420\" height=\"331\" /></p>\r\n<ul>\r\n<li>VBI nhận giấy chứng nhận <strong>ISO ti&ecirc;u chuẩn 9001:2008</strong></li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMG_9410 ed.jpg\" alt=\"\" width=\"1366\" height=\"1076\" /></p>', 'default', '2018-01-10 06:54:54', '2018-03-27 19:48:11'),
(6, 2, 'Hỗ trợ cộng đồng', 'ho-tro-cong-dong', 'image/page/1515549317_about-1.jpg', '<p>C&ocirc;ng ty Bảo hiểm VietinBank (VBI) lu&ocirc;n t&iacute;ch cực tham gia c&aacute;c hoạt động hỗ trợ cộng đồng với tinh thần &ldquo;Tương th&acirc;n tương &aacute;i&rdquo;, &ldquo;L&aacute; l&agrave;nh đ&ugrave;m l&aacute; r&aacute;ch&rdquo;. H&agrave;ng năm, to&agrave;n thể CBNV VBI tự nguyện đ&oacute;ng g&oacute;p ba ng&agrave;y lương để ủng hộ v&agrave;o quỹ từ thiện x&atilde; hội của C&ocirc;ng đo&agrave;n C&ocirc;ng ty.</p>\r\n<p>Một số chương tr&igrave;nh hỗ trợ cộng đồng ti&ecirc;u biểu m&agrave; VBI đ&atilde; triển khai:</p>\r\n<ul>\r\n<li>Đo&agrave;n thanh ni&ecirc;n VBI tổ chức tặng qu&agrave; cho 140 hộ d&acirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại H&agrave; Tĩnh</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMG_8373.jpg\" alt=\"\" width=\"1366\" height=\"1112\" /></p>\r\n<ul>\r\n<li>Đo&agrave;n thanh ni&ecirc;n VBI tổ chức chương tr&igrave;nh từ thiện &Aacute;o ấm cho em - 2016 tại Tuy&ecirc;n Quang</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/htcd2.jpg\" alt=\"\" width=\"1239\" height=\"1200\" /></p>\r\n<ul>\r\n<li>Đo&agrave;n thanh ni&ecirc;n VBI tổ chức chương tr&igrave;nh về nguồn tại Nghệ An</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/htcd3.jpg\" alt=\"\" width=\"632\" height=\"480\" /></p>\r\n<ul>\r\n<li>VBI đồng h&agrave;nh c&ugrave;ng chương tr&igrave;nh \"M&agrave;u Hoa Đỏ\" 2015</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/1 (3).jpg\" alt=\"\" width=\"1366\" height=\"911\" /></p>\r\n<ul>\r\n<li>Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; 2015</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/noi chao tinh thuong-htcd.jpg\" alt=\"\" width=\"1164\" height=\"1200\" /></p>\r\n<ul>\r\n<li>Tuổi trẻ VBI tham gia Ng&agrave;y hội hiến m&aacute;u t&igrave;nh nguyện mang t&ecirc;n &ldquo;H&agrave;nh tr&igrave;nh Đỏ - Kết nối d&ograve;ng m&aacute;u Việt&rdquo;</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/hien mau.jpg\" alt=\"\" width=\"1366\" height=\"1106\" /></p>\r\n<ul>\r\n<li>Chương tr&igrave;nh tặng qu&agrave; Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/2707_02.jpg\" alt=\"\" width=\"610\" height=\"480\" /></p>\r\n<ul>\r\n<li>Chương tr&igrave;nh tặng qu&agrave; Trung t&acirc;m điều dưỡng thương binh Thuận Th&agrave;nh</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMG_2657.jpg\" alt=\"\" width=\"640\" height=\"427\" /></p>\r\n<ul>\r\n<li>Chương tr&igrave;nh ủng hộ gia đ&igrave;nh c&aacute;c nh&acirc;n trong vụ lật xe ở Con Cu&ocirc;ng &ndash; tỉnh Nghệ An</li>\r\n<li>Chương tr&igrave;nh thăm v&agrave; tặng qu&agrave; cho b&agrave; con tỉnh H&agrave; Giang</li>\r\n</ul>\r\n<p><img src=\"../../../uploads/IMGL0420.jpg\" alt=\"\" width=\"640\" height=\"427\" /></p>\r\n<ul>\r\n<li>Chương tr&igrave;nh thăm v&agrave; tặng qu&agrave; c&aacute;c thương bệnh binh nặng tại Trung t&acirc;m điều dưỡng Duy Ti&ecirc;n &ndash; tỉnh H&agrave; Nam</li>\r\n<li>Chương tr&igrave;nh &ldquo;Nồi ch&aacute;o t&igrave;nh thương&rdquo; cho c&aacute;c bệnh nh&acirc;n tại Bệnh viện Nhi H&agrave; Nội </li>\r\n</ul>\r\n<p><img src=\"../../../uploads/htcd4.jpg\" alt=\"\" width=\"1000\" height=\"765\" /></p>\r\n<ul>\r\n<li>Ủng hộ Trường sa th&acirc;n y&ecirc;u</li>\r\n<li>Ủng hộ nạn nh&acirc;n Dioxin Đ&agrave; Nẵng</li>\r\n<li>Ủng hộ từ thiện tại tỉnh Tr&agrave; Vinh</li>\r\n<li>Tham gia ng&agrave;y hội hiến m&aacute;u t&igrave;nh nguyện</li>\r\n</ul>', 'default', '2018-01-10 06:55:17', '2018-03-21 21:03:53'),
