INSERT INTO `product` (`id`, `product_term_id`, `title`, `slug`, `mo_ta_san_pham`, `doi_tuong_bao_hiem`, `pham_vi_bao_hiem`, `doi_tuong_kh`, `ho_so`, `thong_tin`, `hotline`, `thumbnail`, `created_at`, `updated_at`, `quyen_loi_bao_hiem`, `product_category_id`, `to_roi`, `diem_ban_hang`, `url_tinh_phi`, `url_mua_ngay`) VALUES
(27, 12, 'Bảo hiểm du lịch nước ngoài', 'bao-hiem-du-lich-nuoc-ngoai', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bảo hiểm du lịch quốc tế của VBI sẽ l&agrave; người bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu của bạn trong mọi chuyến du lịch, c&ocirc;ng t&aacute;c, thăm người th&acirc;n, du học. Được thiết kế ph&ugrave; hợp với khả năng t&agrave;i ch&iacute;nh v&agrave; nhu cầu của kh&aacute;ch h&agrave;ng, với quyền lợi bảo hiểm đa dạng, phạm vi bảo vệ to&agrave;n diện, bạn v&agrave; gia đ&igrave;nh sẽ an t&acirc;m tuyệt đối cả về t&iacute;nh mạng v&agrave; t&agrave;i sản trước mọi rủi ro kh&ocirc;ng lường trước ở nước ngo&agrave;i.</span></p>', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Người Việt Nam hoặc nước ngo&agrave;i cư tr&uacute; tại Việt Nam, trong độ tuổi từ 6 tuần tuổi tới 80 tuổi</span></p>', NULL, NULL, '<p>&nbsp;</p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-du-lich/BM2501A-giay-yeu-cau-bao-hiem-du-lich-quoc-te-ca-nhan.docx\" rel=\"noopener\" download=\"\">BM 2501 A Giấy y&ecirc;u cầu bảo hiểm du lịch quốc tế</a></span></p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-du-lich/BM2501A-giay-yeu-cau-bao-hiem-du-lich-trong-nuoc-ca nhan.docx\" download=\"\">BM 2505 A Giấy y&ecirc;u cầu bảo hiểm du lịch trong nước c&aacute; nh&acirc;n</a></span></p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-du-lich/HUONG-DAN-HO-SO-YEU-CAU-TRA-TIEN-DU-LICH-QUOC-TE.doc\">Hướng dẫn hồ sơ y&ecirc;u cầu trả tiền du lịch quốc tế</a></span></p>\r\n<p>&nbsp;</p>', NULL, NULL, 'images/product/1521521168_bh du lich nc ngoai-1.jpg', '2018-03-28 07:52:37', '2018-03-28 18:52:37', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>VẬN CHUYỂN KHẨN CẤP</strong></span></p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Vận chuyển khẩn cấp người được bảo hiểm tới cơ sở y tế gần nhất c&oacute; khả năng cung cấp dịch vụ y tế th&iacute;ch hợp.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"2\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> HỒI HƯƠNG</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chi ph&iacute; đưa người được bảo hiểm về Việt Nam hoặc qu&ecirc; hương (bao gồm cả chi ph&iacute; cho thiết bị y tế di động v&agrave; nh&acirc;n vi&ecirc;n y tế đi k&egrave;m)</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><br /><br /></span></p>\r\n<ol start=\"3\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> HỖ TRỢ DU LỊCH</strong></span></li>\r\n</ol>\r\n<ul>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Th&ocirc;ng tin trước chuyến đi</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Th&ocirc;ng tin về đại sứ qu&aacute;n</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Những vấn đề dịch thuật</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Những vấn đề về ph&aacute;p luật</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Th&ocirc;ng tin về người cung cấp dịch vụ y tế</span></li>\r\n</ul>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"4\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> VẬN CHUYỂN H&Agrave;I CỐT / MAI T&Aacute;NG</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Vận chuyển h&agrave;i cốt của Người được bảo hiểm về Việt Nam hoặc qu&ecirc; hương hoặc mai t&aacute;ng ngay tại địa phương</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"5\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> CHI PH&Iacute; Y TẾ</strong></span></li>\r\n</ol>\r\n<ul>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Giới hạn ch&iacute;nh &aacute;p dụng đối với c&aacute;c chi ph&iacute; điều trị nội tr&uacute;, phẫu thuật, xe cứu thương v&agrave; nh&acirc;n vi&ecirc;n y tế đi k&egrave;m, x&eacute;t nghiệm,... Đ&acirc;y l&agrave; giới hạn cho tất cả c&aacute;c chi ph&iacute; ph&aacute;t sinh theo phần n&agrave;y.</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Chi ph&iacute; Điều trị ngoại tr&uacute;: chi ph&iacute; kh&aacute;m bệnh, thuốc k&ecirc; theo đơn của b&aacute;c sĩ điều trị, chụp X-quang, x&eacute;t nghiệm theo chỉ định&hellip;.</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Dụng cụ y tế cần thiết cho việc điều trị g&atilde;y chi do b&aacute;c sỹ chỉ định &nbsp;</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Việc trị liệu học bức xạ, liệu ph&aacute;p &aacute;nh s&aacute;ng v&agrave; c&aacute;c phương ph&aacute;p điều trị tương ứng kh&aacute;c do b&aacute;c sỹ chỉ định.</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Điều trị răng, nhưng chỉ để giảm đau răng cấp t&iacute;nh</span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Điều trị nội tr&uacute; tại một bệnh viện, được một b&aacute;c sỹ theo d&otilde;i chặt chẽ, c&oacute; đầy đủ c&aacute;c phương tiện chuẩn đo&aacute;n v&agrave; điều trị </span></li>\r\n<li><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\"> Phẫu thuật (bao gồm cả c&aacute;c chi ph&iacute; li&ecirc;n quan đến cuộc phẫu thuật) </span></li>\r\n</ul>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"6\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> TAI NẠN C&Aacute; NH&Acirc;N</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chết hoặc thương tật to&agrave;n bộ vĩnh viễn do tai nạn bao gồm mất một hoặc nhiều chi, mất khả năng nh&igrave;n của một hoặc hai mắt</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><br /><br /></span></p>\r\n<ol start=\"7\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> THĂM NGƯỜI TH&Acirc;N </strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Một v&eacute; khứ hồi loại thường cho một người th&acirc;n trong gia đ&igrave;nh sang thăm khi Người được bảo hiểm phải nằm viện tr&ecirc;n 10 ng&agrave;y hoặc chết ở nước ngo&agrave;i </span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"8\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> HỒI HƯƠNG TRẺ EM</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chi ph&iacute; ăn ở cần thiết ph&aacute;t sinh th&ecirc;m cho một trẻ em dưới 14 tuổi đi c&ugrave;ng v&agrave; một v&eacute; loại thường đưa trẻ em đ&oacute; về Việt Nam hoặc qu&ecirc; hương</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"9\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> CẮT NGẮN HAY HỦY BỎ CHUYẾN ĐI</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bồi thường tiền đặt cọc kh&ocirc;ng được ho&agrave;n lại cho chuyến đi v&agrave; chi ph&iacute; đi lại tăng l&ecirc;n v&igrave; cắt ngắn hay hủy chuyến do Người được bảo hiểm bị chết, ốm đau thương tật nghi&ecirc;m trọng, phải ra l&agrave;m chứng hay hầu to&agrave; hoặc được c&aacute;ch ly để kiểm dịch</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"10\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> BỔ SUNG CHI PH&Iacute; ĂN Ở ĐI LẠI</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Một v&eacute; m&aacute;y bay hạng thường cho một th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh hoặc Người đi k&egrave;m về qu&ecirc; hương, c&aacute;c chi ph&iacute; ăn ở đi lại cho Người được bảo hiểm hoặc một th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh hoặc Người đi k&egrave;m ph&aacute;t sinh th&ecirc;m do Người được bảo hiểm phải ở lại điều trị ốm đau bệnh tật. </span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"11\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> MẤT GIẤY TỜ TH&Ocirc;NG H&Agrave;NH</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chi ph&iacute; xin cấp lại hộ chiếu, visa đ&atilde; bị mất c&ugrave;ng chi ph&iacute; đi lại v&agrave; ăn ở ph&aacute;t sinh do việc xin cấp lại c&aacute;c giấy tờ đ&oacute;. Giới hạn bồi thường tối đa một ng&agrave;y l&agrave; 10% của mức giới hạn của quyền lợi n&agrave;y</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"12\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> NHẬN H&Agrave;NH L&Yacute; CHẬM</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chi ph&iacute; mua vật dụng vệ sinh thiết yếu v&agrave; quần &aacute;o tối đa 1.430.000 VNĐ một mục khi h&agrave;nh l&yacute; bị thất lạc &iacute;t nhất 12 tiếng li&ecirc;n tục kể từ khi tới đ&iacute;ch </span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"13\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> CHUYẾN ĐI BỊ TR&Igrave; HO&Atilde;N</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Tối đa &nbsp;2.750.000 VNĐ cho 12 giờ li&ecirc;n tục bị tr&igrave; ho&atilde;n hoặc tối đa 11.000.000 VNĐ nếu ph&aacute;t sinh chi ph&iacute; đi lại bằng phương tiện c&ocirc;ng cộng do hậu quả của thời tiết xấu, b&atilde;i c&ocirc;ng, cướp phương tiện giao th&ocirc;ng, lỗi m&aacute;y m&oacute;c hay kỹ thuật của m&aacute;y bay hay c&aacute;c phương tiện vận chuyển kh&aacute;c</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"14\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> TR&Aacute;CH NHIỆM C&Aacute; NH&Acirc;N</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bồi thường cho người thứ 3 bị thương t&iacute;ch hay t&agrave;i sản của họ bị thiệt hại do lỗi người được bảo hiểm g&acirc;y ra trong thời hạn ở nước ngo&agrave;i (kh&ocirc;ng &aacute;p dụng cho trường hợp thu&ecirc; xe)</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<ol start=\"15\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif;\"><strong> BẢO L&Atilde;NH VIỆN PH&Iacute;</strong></span></li>\r\n</ol>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><br /><span style=\"font-weight: 400;\">Th&ocirc;ng tin chi tiết vui l&ograve;ng li&ecirc;n hệ Hotline: </span><strong>1900 1566</strong></span></p>', 2, 'file/product/1520849341_canhan_bhdulich2.jpg', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Quyền lợi bảo hiểm tối đa l&ecirc;n tới 2 tỷ đồng /người/chuyến</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Thời hạn bảo hiểm tối đa 180 ng&agrave;y li&ecirc;n tục</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Phạm vi bảo hiểm tr&ecirc;n to&agrave;n thế giới (ngoại trừ Việt Nam)</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Dịch vụ bảo l&atilde;nh v&agrave; cứu trợ y tế khẩn cấp bằng tiếng Việt hoặc c&aacute;c ng&ocirc;n ngữ kh&aacute;c tr&ecirc;n phạm vi to&agrave;n cầu </span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">C&aacute;c dịch vụ hỗ trợ du lịch miễn ph&iacute; 24/7 như: thủ tục l&agrave;m thị thực, y&ecirc;u cầu ti&ecirc;m chủng, th&ocirc;ng tin về đại sứ qu&aacute;n, văn ph&ograve;ng luật, cơ sở y tế&hellip;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">&Aacute;p dụng biểu ph&iacute; ưu đ&atilde;i nếu cả gia đ&igrave;nh c&ugrave;ng tham gia một đơn bảo hiểm du lịch quốc tế</span></li>\r\n</ul>', NULL, NULL),
(28, 1, 'Bảo hiểm chi phí phát hành lại thẻ', 'bao-hiem-chi-phi-phat-hanh-lai-the-1', '<p>Sản phẩm bảo hiểm cho c&aacute;c rủi ro tai nạn, ốm đau bệnh tật v&agrave; thai sản ảnh hưởng đến t&iacute;nh mạng, sức khỏe v&agrave;&nbsp;c&aacute;c chi ph&iacute; y tế điều trị cho bạn v&agrave; gia đ&igrave;nh sự an t&acirc;m tuyệt đối về t&agrave;i ch&iacute;nh</p>', '<ul>\r\n<li>C&aacute;c c&aacute; nh&acirc;n l&agrave; c&ocirc;ng d&acirc;n VN, người nước ngo&agrave;i đang cư tr&uacute; hợp ph&aacute;p tại VN c&oacute; độ tuổi từ 12 th&aacute;ng tuổi đến 65 tuổi, VBI kh&ocirc;ng nhận bảo hiểm cho c&aacute;c đối tượng sau:\r\n<ul>\r\n<li>Những người bị bệnh thần kinh, t&acirc;m thần, phong;</li>\r\n<li>Những người bị t&agrave;n phế hoặc thương tật vĩnh viễn từ 50% trở l&ecirc;n.</li>\r\n<li>Những người đang trong thời gian điều trị bệnh tật, thương tật.</li>\r\n</ul>\r\n</li>\r\n</ul>', '<p><strong>I. Quyền lợi bảo hiểm ch&iacute;nh:</strong><br /><br /></p>\r\n<div>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm sinh mạng c&aacute; nh&acirc;n: Tử vong hoặc t&agrave;n tật to&agrave;n bộ vĩnh viễn do ốm đau, bệnh tật: VBI chi trả to&agrave;n bộ số tiền bảo hiểm.<br /><br />2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm tai nạn c&aacute; nh&acirc;n:</div>\r\n<ul>\r\n<li>Tử vong hoặc thương tật to&agrave;n bộ vĩnh viễn do tai nạn: VBI chi trả to&agrave;n bộ STBH.</li>\r\n<li>Thương tật bộ phận vĩnh viễn do tai nạn: VBI chi trả theo Bảng tỷ lệ.</li>\r\n<li>Thương tật tạm thời do tai nạn: VBI thanh to&aacute;n c&aacute;c chi ph&iacute; thực tế, hợp l&yacute;.</li>\r\n<li>Trợ cấp nghỉ việc do điều trị tai nạn (cụ thể trong Quy tắc bảo hiểm).</li>\r\n</ul>\r\n<div>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm chi ph&iacute; y tế nằm viện điều trị nội tr&uacute; do ốm đau, bệnh tật, thai sản:</div>\r\n<ul>\r\n<li>Nằm viện điều trị ốm đau, bệnh tật, thai sản: VBI sẽ thanh to&aacute;n c&aacute;c chi ph&iacute; cụ thể theo Quy tắc bảo hiểm.</li>\r\n<li>Phẫu thuật do ốm đau, bệnh tật, thai sản: VBI sẽ thanh to&aacute;n c&aacute;c chi ph&iacute; cụ thể theo Quy tắc bảo hiểm.</li>\r\n</ul>\r\n<div>4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c quyền lợi bảo hiểm kh&aacute;c.</div>\r\n<p><br /><strong>II.&nbsp;Quyền lợi bảo hiểm bổ sung:</strong><br /><br /></p>\r\n<div>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm điều trị ngoại tr&uacute;.<br /><br />2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm vận chuyển y tế cấp cứu.</div>', '<p>Kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n&nbsp;c&oacute; nhu cầu mua bảo hiểm cho bản th&acirc;n v&agrave; gia đ&igrave;nh.</p>', '<ul>\r\n<li>Giấy y&ecirc;u cầu bảo hiểm&nbsp;(<a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/BM-47-09-_636244797794650401.docx\">xem tại đ&acirc;y</a>).</li>\r\n</ul>', '<ul>\r\n<li>Quy tắc bảo hiểm sức khỏe VBI Care (<a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/QUY-TAC-B_636050426431780976.pdf\">xem tại đ&acirc;y</a>)</li>\r\n<li>Hướng dẫn bồi thường (<a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/huong-dan_636298522706284711.pdf\" target=\"_blank\" rel=\"noopener\">xem tại đ&acirc;y</a>)</li>\r\n<li>Danh s&aacute;ch bệnh viện bảo l&atilde;nh (<a href=\"http://vbi.vietinbank.vn/mang-luoi/benh-vien.htm\">xem tại đ&acirc;y</a>)</li>\r\n</ul>', '<ul>\r\n<li><strong>0934 596 339</strong>&nbsp;hoặc&nbsp;<strong>0934 581 339&nbsp;</strong>hoặc&nbsp;<strong>04 3211 5140</strong></li>\r\n</ul>', 'images/product/1515471533_bancassuara_the.jpg', '2018-02-06 02:35:46', '2018-02-06 07:35:46', '', 0, NULL, NULL, NULL, NULL),
