INSERT INTO `product` (`id`, `product_term_id`, `title`, `slug`, `mo_ta_san_pham`, `doi_tuong_bao_hiem`, `pham_vi_bao_hiem`, `doi_tuong_kh`, `ho_so`, `thong_tin`, `hotline`, `thumbnail`, `created_at`, `updated_at`, `quyen_loi_bao_hiem`, `product_category_id`, `to_roi`, `diem_ban_hang`, `url_tinh_phi`, `url_mua_ngay`) VALUES
(55, 1, 'Bảo hiểm ô tô Vietincar', 'bh-o-to-vietincar', '<p>Sản phẩm bảo hiểm cho c&aacute;c rủi ro tai nạn, ốm đau bệnh tật v&agrave; thai sản ảnh hưởng đến t&iacute;nh mạng, sức khỏe v&agrave;&nbsp;c&aacute;c chi ph&iacute; y tế điều trị cho bạn v&agrave; gia đ&igrave;nh sự an t&acirc;m tuyệt đối về t&agrave;i ch&iacute;nh</p>', '<ul>\r\n<li>C&aacute;c c&aacute; nh&acirc;n l&agrave; c&ocirc;ng d&acirc;n VN, người nước ngo&agrave;i đang cư tr&uacute; hợp ph&aacute;p tại VN c&oacute; độ tuổi từ 12 th&aacute;ng tuổi đến 65 tuổi, VBI kh&ocirc;ng nhận bảo hiểm cho c&aacute;c đối tượng sau:\r\n<ul>\r\n<li>Những người bị bệnh thần kinh, t&acirc;m thần, phong;</li>\r\n<li>Những người bị t&agrave;n phế hoặc thương tật vĩnh viễn từ 50% trở l&ecirc;n.</li>\r\n<li>Những người đang trong thời gian điều trị bệnh tật, thương tật.</li>\r\n</ul>\r\n</li>\r\n</ul>', '<p><strong>I. Quyền lợi bảo hiểm ch&iacute;nh:</strong><br /><br /></p>\r\n<div>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm sinh mạng c&aacute; nh&acirc;n: Tử vong hoặc t&agrave;n tật to&agrave;n bộ vĩnh viễn do ốm đau, bệnh tật: VBI chi trả to&agrave;n bộ số tiền bảo hiểm.<br /><br />2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm tai nạn c&aacute; nh&acirc;n:</div>\r\n<ul>\r\n<li>Tử vong hoặc thương tật to&agrave;n bộ vĩnh viễn do tai nạn: VBI chi trả to&agrave;n bộ STBH.</li>\r\n<li>Thương tật bộ phận vĩnh viễn do tai nạn: VBI chi trả theo Bảng tỷ lệ.</li>\r\n<li>Thương tật tạm thời do tai nạn: VBI thanh to&aacute;n c&aacute;c chi ph&iacute; thực tế, hợp l&yacute;.</li>\r\n<li>Trợ cấp nghỉ việc do điều trị tai nạn (cụ thể trong Quy tắc bảo hiểm).</li>\r\n</ul>\r\n<div>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm chi ph&iacute; y tế nằm viện điều trị nội tr&uacute; do ốm đau, bệnh tật, thai sản:</div>\r\n<ul>\r\n<li>Nằm viện điều trị ốm đau, bệnh tật, thai sản: VBI sẽ thanh to&aacute;n c&aacute;c chi ph&iacute; cụ thể theo Quy tắc bảo hiểm.</li>\r\n<li>Phẫu thuật do ốm đau, bệnh tật, thai sản: VBI sẽ thanh to&aacute;n c&aacute;c chi ph&iacute; cụ thể theo Quy tắc bảo hiểm.</li>\r\n</ul>\r\n<div>4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c quyền lợi bảo hiểm kh&aacute;c.</div>\r\n<p><br /><strong>II.&nbsp;Quyền lợi bảo hiểm bổ sung:</strong><br /><br /></p>\r\n<div>1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm điều trị ngoại tr&uacute;.<br /><br />2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bảo hiểm vận chuyển y tế cấp cứu.</div>', '<p>Kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n&nbsp;c&oacute; nhu cầu mua bảo hiểm cho bản th&acirc;n v&agrave; gia đ&igrave;nh.</p>', '<ul>\r\n<li>Giấy y&ecirc;u cầu bảo hiểm&nbsp;(<a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/BM-47-09-_636244797794650401.docx\">xem tại đ&acirc;y</a>).</li>\r\n</ul>', '<ul>\r\n<li>Quy tắc bảo hiểm sức khỏe VBI Care (<a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/QUY-TAC-B_636050426431780976.pdf\">xem tại đ&acirc;y</a>)</li>\r\n<li>Hướng dẫn bồi thường (<a href=\"http://vbi.vietinbank.vn/pic/FileLibrary2/huong-dan_636298522706284711.pdf\" target=\"_blank\" rel=\"noopener\">xem tại đ&acirc;y</a>)</li>\r\n<li>Danh s&aacute;ch bệnh viện bảo l&atilde;nh (<a href=\"http://vbi.vietinbank.vn/mang-luoi/benh-vien.htm\">xem tại đ&acirc;y</a>)</li>\r\n</ul>', '<ul>\r\n<li><strong>0934 596 339</strong>&nbsp;hoặc&nbsp;<strong>0934 581 339&nbsp;</strong>hoặc&nbsp;<strong>04 3211 5140</strong></li>\r\n</ul>', 'images/product/1515481238_doanhnghiep_bhoto.jpg', '2018-02-06 02:36:28', '2018-02-06 07:36:28', '', 0, NULL, NULL, NULL, NULL),
(58, 2, 'Bảo hiểm người vay vốn', 'bao-hiem-nguoi-vay-von', '<p><strong>Bạn lo lắng điều g&igrave; khi đi vay?</strong></p>\r\n<p><span style=\"font-weight: 400;\">Cuộc sống lu&ocirc;n c&oacute; những biến cố kh&ocirc;ng lường trước được. Nếu kh&ocirc;ng may gia đ&igrave;nh của bạn mất đi một người trụ cột th&igrave; g&aacute;nh nặng trả nợ ng&acirc;n h&agrave;ng sẽ chuyển sang cho ai? Vợ, con, cha, mẹ &hellip; hay những người th&acirc;n y&ecirc;u nhất của bạn?</span></p>\r\n<p><span style=\"font-weight: 400;\">Chỉ với khoảng 3000đ/ng&agrave;y, bạn sẽ nhận được:</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Sự bảo vệ trước những rủi ro ốm đau, bệnh, tai nạn</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">VBI thay bạn v&agrave; gia đ&igrave;nh trả nợ cho ng&acirc;n h&agrave;ng khi kh&ocirc;ng may gặp rủi ro trong cuộc sống </span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">An t&acirc;m tuyệt đối trước khoản vay của m&igrave;nh tại ng&acirc;n h&agrave;ng.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Tự tin đầu tư v&agrave; kinh doanh an to&agrave;n, hiệu quả</span></li>\r\n</ul>', '<p><span style=\"font-weight: 400;\">C&aacute;c </span><span style=\"font-weight: 400;\">c&aacute; nh&acirc;n, đại diện tổ chức </span><span style=\"font-weight: 400;\">từ 18 đến 65 tuổi l&agrave; c&ocirc;ng d&acirc;n Việt Nam, người nước ngo&agrave;i đang c&ocirc;ng t&aacute;c học tập tại Việt Nam vay vốn tại VietinBank hoặc c&aacute;c Ng&acirc;n h&agrave;ng/ Tổ chức t&iacute;n dụng kh&aacute;c.</span></p>', NULL, NULL, NULL, NULL, NULL, 'images/product/1522983776_bh ng vay von.jpg', '2018-04-06 03:02:56', '2018-04-06 14:02:56', '<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Phạm vi bảo hiểm to&agrave;n diện bao gồm bảo hiểm sinh mạng, tai nạn c&aacute; nh&acirc;n, điều trị nội tr&uacute; tại tất cả c&aacute;c bệnh viện.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Quyền lợi Điều trị nội tr&uacute; l&ecirc;n tới 400 triệu đồng/năm</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Quyền lợi Thai sản l&ecirc;n tới 100 triệu đồng/năm</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Quyền lợi Điều trị ngoại tr&uacute; l&ecirc;n tới 40 triệu đồng/năm</span></li>\r\n</ul>', NULL, NULL, '<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Số tiền chi trả l&ecirc;n tới 2 tỷ đồng với quyền lợi bảo hiểm tai nạn, tối đa 500 triệu đồng với quyền lợi sinh mạng do ốm đau, bệnh.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Giải quyết bồi thường nhanh ch&oacute;ng, thuận tiện</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Nhiều ưu đ&atilde;i hấp dẫn từ ng&acirc;n h&agrave;ng nếu tham gia bảo hiểm</span></li>\r\n</ul>', '#', '#'),
(59, 4, 'Bảo hiểm nhà tư nhân', 'bao-hiem-nha-tu-nhan', '<p><span style=\"font-family: Arial;\"><span style=\"font-weight: 400;\">BẠN C&Oacute; CHẮC CHẮN RẰNG M&Igrave;NH HO&Agrave;N TO&Agrave;N L&Agrave;M CHỦ CUỘC SỐNG, L&Agrave;M CHỦ CHO TẤT CẢ NHỮNG T&Igrave;NH HUỐNG BẤT NGỜ XẢY RA TRONG CUỘC SỐNG?</span></span></p>\r\n<p><span style=\"font-family: Arial;\"><strong><em>Nếu chẳng may nh&agrave; bạn xảy ra tổn thất, hỏa hoạn, chịu ảnh hưởng của thi&ecirc;n tai, rủi ro do trộm cướp,&hellip; sẽ ảnh hưởng đến cuộc sống của bạn như thế n&agrave;o?</em></strong></span></p>\r\n<ul>\r\n<li><strong><em><span style=\"font-weight: 400;\">Cả th&aacute;ng lương của bạn sẽ phải d&ugrave;ng cho việc sửa chữa tổn thất</span></em></strong></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Tiền chi ti&ecirc;u, sinh hoạt bị b&oacute; hẹp</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Chất lượng cuộc sống giảm dần</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Phải đi vay mượn tiền để sửa chữa tổn thất</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Kế hoạch T&agrave;i ch&iacute;nh của cả 1 năm sẽ ph&aacute; sản</span></li>\r\n</ul>\r\n<p><strong>Đừng để cuộc sống của bạn bị t&aacute;c động bởi ho&agrave;n cảnh. </strong></p>\r\n<p><strong>H&atilde;y tham gia Bảo hiểm VietinBank ngay từ b&acirc;y giờ !</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">VBI l&agrave; C&ocirc;ng ty Bảo hiểm c&oacute; tiềm lực T&agrave;i ch&iacute;nh lớn mạnh khi trực thuộc 100% vốn của Ng&acirc;n h&agrave;ng TMCP C&ocirc;ng thương Việt Nam.</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">C&ocirc;ng nghệ 4.0 đi đầu trong lĩnh vực Bảo hiểm </span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Hệ thống tổng đ&agrave;i CSKH hoạt động 24/7 lu&ocirc;n hỗ trợ KH mọi thời điểm</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Số ph&iacute; bỏ ra thấp nhất m&agrave; được bồi thường số tiền bảo hiểm cao nhất.</span></li>\r\n</ul>', '<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Ng&ocirc;i nh&agrave; (Gồm nh&agrave; chung cư, nh&agrave; liền kề, biệt thự)</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">T&agrave;i sản b&ecirc;n trong ng&ocirc;i nh&agrave;</span></li>\r\n</ul>', NULL, NULL, '<p>&nbsp;</p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-nha-tu-nhan/giay-yc-bh-nha-tu-nhan.pdf\" download=\"\">Giấy y&ecirc;u cầu bảo hiểm nh&agrave; tư nh&acirc;n (PDF)</a></span></p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-nha-tu-nhan/giay-yc-bh-nha-tu-nhan.docx\" download=\"\">Giấy y&ecirc;u cầu bảo hiểm nh&agrave; tư nh&acirc;n (doc)</a></span></p>', NULL, NULL, 'images/product/1522140749_bh nha.jpg', '2018-03-28 07:57:29', '2018-03-28 18:57:29', '<p>&nbsp;<strong>Phạm vi bảo hiểm</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Hỏa hoạn, s&eacute;t, nổ</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Gi&ocirc;ng, b&atilde;o, lũ lụt</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Nước tho&aacute;t ra từ c&aacute;c bể nước, thiết bị chứa nước hoặc đường ống dẫn nước</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Thiệt hại trực tiếp ph&aacute;t sinh từ việc đ&acirc;m va do va chạm với xe cơ giới hay động vật</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Rủi ro trộm cướp</span></li>\r\n</ul>\r\n<p><strong>Quyền lợi mở rộng: </strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Chi ph&iacute; dọn dẹp hiện trường đến 20 triệu đồng cho mỗi v&agrave; mọi vụ tổn thất</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Ph&iacute; thu&ecirc; nh&agrave; sau tổn thất đến 30 triệu đồng cho mỗi v&agrave; mọi vụ tổn thất, thời hạn thu&ecirc; l&ecirc;n tới 06 th&aacute;ng</span></li>\r\n</ul>', 2, NULL, '<ul>\r\n<li><span style=\"font-weight: 400;\">Số ph&iacute; tối thiểu: Chỉ với 0,05% số ph&iacute; cơ bản</span></li>\r\n<li><span style=\"font-weight: 400;\">Quyền lợi tối đa: 100% gi&aacute; trị nh&agrave; khi xảy ra tổn thất</span></li>\r\n</ul>', '#', '#'),
(60, 24, 'Bảo hiểm tài sản', 'bao-hiem-tai-san', '<p><strong>Bạn chuẩn bị g&igrave; cho sếp nhỉ? Kh&ocirc;ng c&ograve;n rủi ro, t&agrave;i ch&iacute;nh đảm bảo.</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Rủi ro ch&aacute;y v&agrave; thi&ecirc;n tai bất ngờ</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Tổn thất lớn v&agrave; kh&ocirc;ng lường trước được</span></li>\r\n</ul>\r\n<p><strong>Khi gặp phải vấn đề về rủi ro cho t&agrave;i sản doanh nghiệp hoặc c&aacute; nh&acirc;n bạn sẽ gặp phải c&aacute;c hệ lụy:</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Chi ph&iacute; khắc phục thiệt hại lớn cần thu xếp gấp</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Kế hoạch t&agrave;i ch&iacute;nh của c&ocirc;ng ty/gia đ&igrave;nh bị đảo lộn</span></li>\r\n</ul>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm t&agrave;i sản, chuyển giao rủi ro th&ocirc;ng minh nhất! Chuyển quỹ đề ph&ograve;ng rủi ro th&agrave;nh chi ph&iacute; gi&aacute; th&agrave;nh sản phẩm, c&oacute; thể quản l&yacute; được.</span></p>', '<p><span style=\"font-family: Arial;\"><span style=\"font-weight: 400;\">T&agrave;i sản (C&ocirc;ng tr&igrave;nh kiến tr&uacute;c, m&aacute;y m&oacute;c thiết bị, h&agrave;ng h&oacute;a) của tổ chức v&agrave; c&aacute; nh&acirc;n tr&ecirc;n l&atilde;nh thổ Việt nam.</span></span></p>', NULL, NULL, NULL, NULL, NULL, 'images/product/1519634264_product-2.jpg', '2018-02-26 13:37:44', '2018-02-26 13:37:44', '<p><strong>Phạm vi bảo hiểm:</strong></p>\r\n<p><span style=\"font-weight: 400;\">Bồi thường cho c&aacute;c tổn thất t&agrave;i ch&iacute;nh từ c&aacute;c rủi ro bất ngờ, kh&ocirc;ng lường trước được đối với t&agrave;i sản thuộc sở hữu, c&oacute; thể bao gồm cả việc mất thu nhập đ&aacute;ng ra được nhận nếu như kh&ocirc;ng c&oacute; tổn thất xảy ra.</span></p>\r\n<p><strong>Ưu điểm:</strong></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm to&agrave;n diện: Cả chi ph&iacute; kh&ocirc;i phục t&agrave;i sản lẫn nguồn vốn để tiếp tục kh&ocirc;i phục sxkd</span></p>\r\n<p><span style=\"font-weight: 400;\">Ph&ecirc; duyệt bảo hiểm nhanh ch&oacute;ng, sẵn s&agrave;ng phục vụ 24/24h</span></p>\r\n<p><span style=\"font-weight: 400;\">Thủ tục bồi thường: Kịp thời, đầy đủ v&agrave; ch&iacute;nh x&aacute;c; hỗ trợ tạm ứng bồi thường trước</span></p>', 2, NULL, '<ul>\r\n<li>Số ph&iacute; tối thiểu: Chỉ với 0,05% số ph&iacute; cơ bản</li>\r\n<li><span style=\"font-weight: 400;\">Quyền lợi tối đa: 100% gi&aacute; trị nh&agrave; khi xảy ra tổn thất</span></li>\r\n</ul>', '#', '#'),
(61, 24, 'Bảo hiểm công trình xây dựng dân dụng', 'bao-hiem-cong-trinh-xay-dung-dan-dung', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Bảo hiểm c&ocirc;ng tr&igrave;nh kỹ thuật d&acirc;n dụng đ&atilde; ho&agrave;n th&agrave;nh l&agrave; h&igrave;nh thức bảo hiểm vật chất cho c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng/ lắp đặt đ&atilde; ho&agrave;n th&agrave;nh v&agrave; đưa v&agrave;o&nbsp;sử dụng như: Đ&ecirc;, đập, k&egrave;, cầu cảng, s&acirc;n bay, hệ thống tho&aacute;t nước,&hellip;</span><span style=\"font-weight: 400;\"><br /></span></span></p>', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">C&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng đ&atilde; ho&agrave;n th&agrave;nh v&agrave; đi v&agrave;o sử dụng như: cầu; cảng; đường bộ; đường sắt; đ&ecirc;; k&egrave;; đập, tr&agrave;n, cửa lấy nước, cống xả của c&ocirc;ng tr&igrave;nh thủy điện&hellip;</span></span></p>', NULL, NULL, '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>T&agrave;i liệu cần thu thập:</strong></span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Danh mục t&agrave;i sản chi tiết</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Sơ đồ mặt bằng r&otilde; khoảng c&aacute;ch</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bản đ&aacute;nh gi&aacute; rủi ro k&egrave;m ảnh chụp minh họa</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bi&ecirc;n bản kiểm tra PCCC</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">T&agrave;i liệu về quy tr&igrave;nh vận h&agrave;nh, quy tr&igrave;nh bảo dưỡng m&aacute;y m&oacute;c, quy tr&igrave;nh đảm bảo an to&agrave;n vận h&agrave;nh, phương &aacute;n đề ph&ograve;ng hạn chế/đối ph&oacute; rủi ro,</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Giấy y&ecirc;u cầu bảo hiểm</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bản ch&agrave;o ph&iacute; dự thảo (dạng word)</span></li>\r\n</ul>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 'images/product/1520848420_doanhnghiep_bhhanghoa_1.jpg', '2018-03-12 20:53:40', '2018-03-12 20:53:40', '<p>&nbsp;</p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bảo hiểm cho c&aacute;c rủi ro cụ thể được n&ecirc;u trong đơn</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Hỏa hoạn, s&eacute;t nổ, va chạm của c&aacute;c phương tiện tr&ecirc;n bờ, dưới nước</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Va chạm của m&aacute;y bay hoặc c&aacute;c phương tiện h&agrave;ng kh&ocirc;ng hay c&aacute;c thiết bị tr&ecirc;n đ&oacute; </span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Động đất, n&uacute;i lửa, s&oacute;ng thần</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Gi&ocirc;ng b&atilde;o (sự di chuyển của gi&oacute; với tốc độ từ cấp 8 trở l&ecirc;n theo thang &nbsp;độ Beaufort)</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Lũ lụt, t&aacute;c động của s&oacute;ng hay nước</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Đất sụt, đ&aacute; lở, đất lở hay những di chuyển kh&aacute;c của đất </span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Sương gi&aacute;, tuyết lở, băng</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Sự ph&aacute; hoại kh&ocirc;ng c&oacute; tổ chức</span></li>\r\n</ul>\r\n<p>&nbsp;</p>', 3, NULL, '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', NULL, NULL),
(62, 24, 'Bảo hiểm thiết bị máy móc', 'bao-hiem-thiet-bi-may-moc', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Bảo hiểm m&aacute;y m&oacute;c v&agrave; thiết bị x&acirc;y dựng l&agrave; h&igrave;nh thức bảo hiểm vật chất cho c&aacute;c thiết bị v&agrave; m&aacute;y m&oacute;c thi c&ocirc;ng đang hoạt động tại c&ocirc;ng trường x&acirc;y dựng như cần cẩu, xe lu, m&aacute;y trộn xi măng, m&aacute;y x&uacute;c, m&aacute;y đ&oacute;ng cọc, m&aacute;y đầm.</span></span></p>\r\n<p>&nbsp;</p>', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">M&aacute;y m&oacute;c, thiết bị</span></span></p>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>T&agrave;i liệu cần thu thập:</strong></span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Th&ocirc;ng số kỹ thuật m&aacute;y m&oacute;c: số khung, số m&aacute;y, series, c&ocirc;ng dụng</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Địa điểm v&agrave; lĩnh vực l&agrave;m việc</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Đăng k&yacute;, đăng kiểm</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Lịch sử hoạt động/cạnh tranh</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Lịch sử tổn thất</span></li>\r\n</ul>', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 'images/product/1520848547_doanhnghiep_kythuat_1.jpg', '2018-03-12 20:55:47', '2018-03-12 20:55:47', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Thiệt hại vật chất bất ngờ ngoại trừ c&aacute;c rủi ro bị loại trừ được đề cập trong quy tắc (Kh&ocirc;ng bảo hiểm cho tr&aacute;ch nhiệm b&ecirc;n thứ 3)</span></p>', 3, NULL, '<p>&nbsp;</p>\r\n<p>&nbsp;</p>', NULL, NULL),
(63, 24, 'Bảo hiểm tài sản - Gián đoán kinh doanh', 'bao-hiem-tai-san-gian-doan-kinh-doanh', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bảo hiểm cho những mất m&aacute;t về lợi nhuận gộp do sự giảm s&uacute;t về doanh thu hoặc gia tăng về chi ph&iacute; kinh doanh, g&acirc;y ra do việc gi&aacute;n đoạn hoạt động kinh doanh của người được bảo hiểm do c&aacute;c rủi ro được bảo hiểm ở phần thiệt hại vật chất g&acirc;y ra.</span></p>', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Giảm lợi nhuận gộp do:</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Giảm doanh thu</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Tăng chi ph&iacute; hoạt động kinh doanh</span></li>\r\n</ul>', NULL, NULL, NULL, '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>T&agrave;i liệu cần thu thập:</strong></span></p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Ngo&agrave;i t&agrave;i liệu Đ&aacute;nh gi&aacute; rủi ro từ thiệt hại vật chất, kh&aacute;ch h&agrave;ng cần th&ecirc;m:</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">GYC bảo hiểm GĐKD</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Th&ocirc;ng tin về năng lực sản xuất dự ph&ograve;ng </span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Kế hoạch kh&ocirc;i phục hoạt động kinh doanh trong trường hợp c&oacute; tổn thất</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Khả năng thay thế nhanh nguy&ecirc;n vật liệu, m&aacute;y m&oacute;c đ&atilde; bị tổn thất, t&igrave;m địa điểm sản xuất mới sau tổn thất</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Lịch sử tổn thất THVC, GĐKD</span></li>\r\n</ul>', NULL, 'images/product/1520848681_doanhnghiep_taisan_3.jpg', '2018-03-12 20:58:01', '2018-03-12 20:58:01', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">C&ocirc;ng việc kinh doanh của Người được bảo hiểm tại địa điểm được bảo hiểm bị gi&aacute;n đoạn hay ngừng trệ do c&aacute;c nguy&ecirc;n nh&acirc;n thuộc phạm vi bảo hiểm của phần thiệt hại vật chất</span></span></p>', 3, NULL, NULL, NULL, NULL),
(64, 24, 'Bảo hiểm tài sản – Thiệt hại vật chất', 'bao-hiem-tai-san-thiet-hai-vat-chat', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Bảo hiểm t&agrave;i sản &ndash; Thiệt hại vật chất bảo vệ doanh nghiệp to&agrave;n diện trước nguy cơ thiệt hại v&agrave; mất m&aacute;t t&agrave;i sản c&ugrave;ng lợi nhuận do hỏa hoạn v&agrave; c&aacute;c rủi ro đặc biệt g&acirc;y ra để bạn c&oacute; thể y&ecirc;n t&acirc;m thực hiện c&aacute;c chiến lược ph&aacute;t triển kinh doanh.</span></span></p>', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Nh&agrave; cửa, c&ocirc;ng tr&igrave;nh kiến tr&uacute;c v&agrave; c&aacute;c trang thiết bị;</span></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">M&aacute;y m&oacute;c thiết bị;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">H&agrave;ng ho&aacute;, vật tư v&agrave; c&aacute;c t&agrave;i sản kh&aacute;c</span></li>\r\n</ul>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">M&agrave; gi&aacute; trị t&iacute;nh được th&agrave;nh tiền</span></p>', NULL, NULL, NULL, '<p><strong>T&agrave;i liệu cần thu thập:</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Danh mục t&agrave;i sản chi tiết</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Sơ đồ mặt bằng r&otilde; khoảng c&aacute;ch</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Bản đ&aacute;nh gi&aacute; rủi ro k&egrave;m ảnh chụp minh họa</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Bi&ecirc;n bản kiểm tra PCCC</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">T&agrave;i liệu về quy tr&igrave;nh sản xuất, quy tr&igrave;nh bảo dưỡng m&aacute;y m&oacute;c, quy tr&igrave;nh đảm bảo an to&agrave;n sản xuất, phương &aacute;n đề ph&ograve;ng hạn chế/đối ph&oacute; rủi ro, &hellip;</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Giấy y&ecirc;u cầu bảo hiểm</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Bản ch&agrave;o ph&iacute; dự thảo (dạng word)</span></li>\r\n</ul>', NULL, 'images/product/1520848959_doanhnghiep_taisan_5.jpg', '2018-03-22 09:16:25', '2018-03-22 20:16:25', '<p><strong>Bảo hiểm ch&aacute;y nổ bắt buộc:</strong></p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Phạm vi bảo hiểm A (Ch&aacute;y)</span></li>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Phạm vi bảo hiểm B (Nổ)</span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong>Bảo hiểm hoả hoạn v&agrave; c&aacute;c rủi ro đặc biệt:</strong></p>\r\n<p><span style=\"font-weight: 400;\">Bảo hiểm cho c&aacute;c rủi ro cụ thể được n&ecirc;u ra trong đơn (named risks) bao gồm 10 rủi ro:</span></p>\r\n<p><strong>(A)</strong><span style=\"font-weight: 400;\"> Hỏa hoạn , s&eacute;t nổ ( bắt buộc)</span></p>\r\n<p><strong>(B)</strong><span style=\"font-weight: 400;\"> Nổ; </span></p>\r\n<p><strong>(C)</strong><span style=\"font-weight: 400;\"> M&aacute;y bay v&agrave; c&aacute;c phương tiện h&agrave;ng kh&ocirc;ng kh&aacute;c hoặc c&aacute;c thiết bị tr&ecirc;n phương tiện đ&oacute; rơi v&agrave;o; </span></p>\r\n<p><strong>(D)</strong><span style=\"font-weight: 400;\"> G&acirc;y rối, đ&igrave;nh c&ocirc;ng, b&atilde;i c&ocirc;ng, sa thải;</span></p>\r\n<p><strong>(E)</strong><span style=\"font-weight: 400;\"> H&agrave;nh động &aacute;c &yacute;; </span></p>\r\n<p><strong>(F)</strong><span style=\"font-weight: 400;\"> Động đất, n&uacute;i lửa phun, bao gồm cả lụt v&agrave; nước biển tr&agrave;n do hậu quả của động đất, n&uacute;i lửa;&nbsp; </span></p>\r\n<p><strong>(G)</strong><span style=\"font-weight: 400;\"> Gi&ocirc;ng b&atilde;o; </span></p>\r\n<p><strong>(H) </strong><span style=\"font-weight: 400;\">Gi&ocirc;ng b&atilde;o, ngập lụt; </span></p>\r\n<p><strong>(I)</strong><span style=\"font-weight: 400;\"> Vỡ hay tr&agrave;n nước từ c&aacute;c bể chứa nước, thiết bị chứa nước hoặc đường ống dẫn nước; </span></p>\r\n<p><strong>(J)</strong><span style=\"font-weight: 400;\"> Đ&acirc;m va do xe cơ giới hay s&uacute;c vật</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Bảo hiểm t&agrave;i sản mọi rủi ro - Bảo hiểm mọi rủi ro C&ocirc;ng nghiệp:</strong></p>\r\n<p><span style=\"font-weight: 400;\">L&agrave; loại h&igrave;nh bảo hiểm cho mọi rủi ro ngoại trừ c&aacute;c rủi ro bị loại trừ được đề cập trong quy tắc (all risks)</span></p>', 3, NULL, NULL, NULL, NULL),
(65, 24, 'Bảo hiểm thẻ', 'bao-hiem-the', '<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Ng&agrave;y nay khi th&ocirc;ng tin dễ d&agrave;ng bị mất cắp th&igrave; g&oacute;i bảo hiểm thẻ của Bảo hiểm VietinBank sẽ l&agrave; giải ph&aacute;p an to&agrave;n, tiết kiệm chi ph&iacute; cho tất cả c&aacute;c chủ thẻ E-Partner.</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Để đảm bảo an to&agrave;n v&agrave; mang lại tiện &iacute;ch tối ưu cho kh&aacute;ch h&agrave;ng khi sử dụng thẻ, Bảo hiểm VietinBank cung cấp dịch vụ bảo hiểm giao dịch gian lận thẻ cho tất cả c&aacute;c chủ thẻ ghi quốc tế.</span></p>\r\n<p><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Mọi th&ocirc;ng tin chi tiết vui l&ograve;ng li&ecirc;n hệ Hotline: </span><strong>1900 1566</strong></span></p>', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Chủ thẻ hợp ph&aacute;p tại tất cả c&aacute;c ng&acirc;n h&agrave;ng tr&ecirc;n l&atilde;nh thổ Việt Nam</span></span></p>', NULL, NULL, NULL, NULL, NULL, 'images/product/1520849068_bancassuara_the.jpg', '2018-03-12 21:04:28', '2018-03-12 21:04:28', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Bảo hiểm chi ph&iacute; ph&aacute;t h&agrave;nh lại thẻ:</strong><span style=\"font-weight: 400;\"> Khi mất thẻ hay bị hư hỏng thẻ, kh&aacute;ch h&agrave;ng sẽ được VBI chi trả c&aacute;c chi ph&iacute; ph&aacute;t &nbsp;h&agrave;nh lại thẻ tối đa 3 lần/năm v&agrave; được hỗ trợ trả thẻ tại nh&agrave;. Chủ thẻ kh&ocirc;ng cần thực hiện bất kỳ thủ tục n&agrave;o khi nhận bồi thường từ bảo hiểm v&agrave; kh&ocirc;ng giới hạn phạm vi mất thẻ để được hưởng bảo hiểm.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Bảo hiểm giao dịch gian lận thẻ:</strong><span style=\"font-weight: 400;\"> Trong qu&aacute; tr&igrave;nh sử dụng thẻ, nếu thẻ bị thất lạc, mất cắp hoặc thẻ vẫn do chủ thẻ lưu giữ nhưng c&oacute; c&aacute;c khoản ph&iacute; tr&aacute;i ph&eacute;p được thực hiện bằng t&agrave;i khoản của chủ thẻ, chủ thẻ sẽ được bồi thường c&aacute;c chi ph&iacute; bị t&iacute;nh tr&aacute;i ph&eacute;p n&agrave;y. Số tiền đựợc bảo hiểm cho rủi ro gian lận tối đa 20.000.000 đồng/lần/vụ v&agrave; tối đa l&ecirc;n tới 100.000.000 đồng/năm/thẻ.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Phạm vi bảo hiểm:</strong></span></p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">VBI sẽ bồi thường cho những rủi ro sau đ&acirc;y, đến c&aacute;c hạn mức tr&aacute;ch nhiệm theo mỗi vụ v&agrave; trong thời gian hiệu lực của hợp đồng, được quy định tại Hợp đồng bảo hiểm:</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>1.1. Nếu thẻ thanh to&aacute;n của chủ thẻ bị thất lạc hoặc mất cắp</strong><span style=\"font-weight: 400;\">: VBI sẽ b&ocirc;̀i ho&agrave;n các Chi phí bị tính trái phép mà Chủ thẻ phải chịu trách nhi&ecirc;̣m đối với Thẻ thanh to&aacute;n bị Thất lạc hoặc bị Mất cắp t&ocirc;́i đa trong vòng 12 giờ trước khi Chủ thẻ th&ocirc;ng báo lần đ&acirc;̀u ti&ecirc;n v&ecirc;̀ sự vi&ecirc;̣c cho Ng&acirc;n h&agrave;ng.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>1.2. Nếu thẻ thanh to&aacute;n của chủ thẻ kh&ocirc;ng bị mất cắp, thất lạc, thẻ vẫn do chủ thẻ lưu giữ nhưng c&oacute; c&aacute;c khoản ph&iacute; tr&aacute;i ph&eacute;p được thực hiện bằng t&agrave;i khoản ng&acirc;n h&agrave;ng của chủ thẻ v&agrave;/hoặc t&agrave;i khoản t&iacute;n dụng, th&ocirc;ng qua:</strong><span style=\"font-weight: 400;\"> (i) cửa h&agrave;ng, (ii) điện thoại, (iii) m&aacute;y r&uacute;t tiền ATM, v&agrave; / hoặc (iv) mua sắm online, sử dụng th&ocirc;ng tin thẻ thanh to&aacute;n của chủ thẻ, VBI sẽ bồi ho&agrave;n cho c&aacute;c khoản ph&iacute; tr&aacute;i ph&eacute;p, m&agrave; chủ thẻ c&oacute; tr&aacute;ch nhiệm thanh to&aacute;n, ph&aacute;t sinh tối đa hai (2) th&aacute;ng trước khi sự việc được b&aacute;o c&aacute;o lần đầu ti&ecirc;n đến Ng&acirc;n h&agrave;ng.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><br /><span style=\"font-weight: 400;\">Th&ocirc;ng tin chi tiết li&ecirc;n hệ Hotline: </span><strong>1900 1566</strong></span></p>', 2, NULL, NULL, NULL, NULL),
(66, 12, 'Bảo hiểm du lịch trong nước', 'bao-hiem-du-lich-trong-nuoc', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Sản phẩm n&agrave;y hỗ trợ cho kh&aacute;ch du lịch kh&ocirc;ng may gặp phải c&aacute;c rủi ro trong suốt h&agrave;nh tr&igrave;nh du lịch như&nbsp; tai nạn trong qu&aacute; tr&igrave;nh đi lại, tham gia c&aacute;c hoạt động tại địa điểm du lịch g&acirc;y thương tật về th&acirc;n thể, thậm ch&iacute; c&oacute; thể ảnh hưởng tới t&iacute;nh mạng. H&atilde;y tận hưởng chuyến đi du lịch th&uacute; vị v&agrave; y&ecirc;n t&acirc;m với sản phẩm Bảo hiểm du lịch trong nước của ch&uacute;ng t&ocirc;i.</span></span></p>', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><span style=\"font-weight: 400;\">Những người đi tham quan, nghỉ m&aacute;t, tắm biển leo n&uacute;i,... hoặc nghỉ tại kh&aacute;ch sạn, nh&agrave; kh&aacute;ch trong phạm vi l&atilde;nh thổ việt Nam.</span></span></p>', NULL, NULL, '<p>&nbsp;</p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-du-lich/BM2501A-giay-yeu-cau-bao-hiem-du-lich-quoc-te-ca-nhan.docx\" rel=\"noopener\" download=\"\">BM 2501 A Giấy y&ecirc;u cầu bảo hiểm du lịch quốc tế</a></span></p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-du-lich/BM2501A-giay-yeu-cau-bao-hiem-du-lich-trong-nuoc-ca nhan.docx\" download=\"\">BM 2505 A Giấy y&ecirc;u cầu bảo hiểm du lịch trong nước c&aacute; nh&acirc;n</a></span></p>\r\n<p><span style=\"font-family: Arial;\"><a href=\"../../../files/san-pham/bh-du-lich/HUONG-DAN-HO-SO-YEU-CAU-TRA-TIEN-DU-LICH-QUOC-TE.doc\">Hướng dẫn hồ sơ y&ecirc;u cầu trả tiền du lịch quốc tế</a></span></p>\r\n<p>&nbsp;</p>', NULL, NULL, 'images/product/1521521220_bh du lich trong nuoc-1.jpg', '2018-03-28 07:52:21', '2018-03-28 18:52:21', '<p>&nbsp;</p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chỉ bao gồm những rủi ro xảy ra với người được bảo hiểm ph&aacute;t sinh ngo&agrave;i l&atilde;nh thổ Việt Nam v&agrave; trong thời hạn bảo hiểm. Cụ thể: &ldquo;Phạm vi bảo hiểm bắt đầu từ thời điểm ho&agrave;n tất thủ tục xuất cảnh từ Việt Nam v&agrave; kết th&uacute;c v&agrave;o ng&agrave;y trở về Việt Nam v&agrave;o l&uacute;c 24 giờ ng&agrave;y cuối c&ugrave;ng của thời hạn bảo hiểm t&ugrave;y theo thời điểm n&agrave;o sớm hơn sẽ được &aacute;p dụng&rdquo;. Như vậy, Người được bảo hiểm bị tai nạn hoặc ốm đau bệnh tật trong địa phận s&acirc;n bay của Việt Nam, nhưng chưa ho&agrave;n tất thủ tục xuất cảnh tại Việt Nam vẫn được bảo hiểm trong chương tr&igrave;nh n&agrave;y.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bao gồm c&aacute;c rủi ro sau:</span></p>\r\n<ul>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Chết, thương tật th&acirc;n thể do tai nạn.</span></li>\r\n</ul>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li style=\"font-weight: 400;\"><span style=\"font-family: arial, helvetica, sans-serif;\"><em><span style=\"font-weight: 400;\">Chết hoặc thương tật th&acirc;n thể do NĐBH c&oacute; h&agrave;nh động cứu người, cứu t&agrave;i sản của Nh&agrave; nước, của nh&acirc;n d&acirc;n v&agrave; tham gia chống c&aacute;c h&agrave;nh động phạm ph&aacute;p.</span></em></span></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Quyền lợi về chi ph&iacute; y tế:</strong></span></p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Bao gồm to&agrave;n bộ chi ph&iacute; y tế li&ecirc;n quan đến điều trị nội tr&uacute;, ngoại tr&uacute; v&agrave; phẫu thuật. C&aacute;c chi ph&iacute; n&agrave;y được chi trả theo chi ph&iacute; thực tế, hợp l&yacute; theo th&ocirc;ng lệ tại nước sở tại nhưng kh&ocirc;ng vượt qu&aacute; giới hạn tối đa quy định cho mỗi hạng bảo hiểm.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Lưu &yacute;:</strong><span style=\"font-weight: 400;\"> Kh&ocirc;ng &aacute;p dụng khấu trừ trong mỗi khiếu nại bồi thường như c&aacute;c đơn bảo hiểm du lịch kh&aacute;c.</span></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Quyền lợi bảo hiểm tai nạn con người:</strong></span></p>\r\n<p><span style=\"font-weight: 400; font-family: arial, helvetica, sans-serif;\">Quyền lợi bảo hiểm theo phần n&agrave;y chỉ bao gồm trường hợp chết hoặc thương tật vĩnh viễn như đ&atilde; được liệt k&ecirc; trong mục 3 phần 3 quy định trong Đơn bảo hiểm. Giới hạn tối đa cho quyền lợi n&agrave;y l&agrave; 100% số tiền bảo hiểm quy định theo mỗi hạng bảo hiểm. Sau khi người được bảo hiểm đ&atilde; được bồi thường theo mục n&agrave;y th&igrave; hiệu lực bảo hiểm sẽ theo quyền lợi n&agrave;y sẽ tự động kết th&uacute;c</span></p>', 2, NULL, NULL, NULL, NULL);
