INSERT INTO `testimonial` (`id`, `name`, `thumbnail`, `content`, `created_at`, `updated_at`, `job`) VALUES
(2, 'Ông Nguyễn Đức Văn', 'images/testimonial/1521452938_nguyen-van-duc.jpg', '<p>&ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; nhận được sự quan t&acirc;m, hỗ trợ rất nhiệt t&igrave;nh giải quyết hậu quả, cũng như sự hỗ trợ về thủ tục giấy tờ để ho&agrave;n thiện hồ sơ bảo hiểm. Trong suốt qu&aacute; tr&igrave;nh từ l&uacute;c sau khi xảy ra tổn th&acirc;t tới khi c&aacute;c bước bồi thường được ho&agrave;n th&agrave;nh, Đơn vị VBI thường xuy&ecirc;n gần gũi, chung tay với C&ocirc;ng ty như người th&acirc;n trong nh&agrave; để c&ugrave;ng khắc ph&uacute;c sớm nhất nhằm đưa hoạt động của c&ocirc;ng ty trở lại b&igrave;nh thường&hellip; Thực sự c&oacute; thể n&oacute;i l&agrave; Bảo hiểm VietinBank đ&atilde; sinh ra Bao b&igrave; Nhơn Trạch lần thứ hai&rdquo;</p>', '2018-03-29 02:34:47', '2018-03-29 13:34:47', 'TGĐ Công ty Cổ phần Bao bì Nhơn Trạch'),
(4, 'Bà Nguyễn Thị Nga', 'images/testimonial/1521452948_nguyen-thi-nga.jpg', '<p>&ldquo;T&ocirc;i rất cảm động khi nhận được chi trả bồi thường từ Bảo hiểm VietinBank. Ngay h&ocirc;m xảy ra sự kiện, c&aacute;c anh chị tại Bảo hiểm VietinBank T&acirc;y Nguy&ecirc;n đ&atilde; gọi điện thăm hỏi v&agrave; qua chia buồn c&ugrave;ng gia đ&igrave;nh. Đồng thời hướng dẫn chu đ&aacute;o v&agrave; hỗ trợ l&agrave;m c&aacute;c thủ tục bảo hiểm. Thực sự, t&ocirc;i rất tin tưởng ở Bảo hiểm VietinBank T&acirc;y Nguy&ecirc;n.&rdquo;</p>', '2018-03-29 02:35:16', '2018-03-29 13:35:16', 'Vợ khách hàng tại Chư-Prông, Gia Lai'),
(5, 'Con gái bà Nguyễn Thị Thanh Tỉnh', 'images/testimonial/1522291110_testimonial-3.png', '<p><span style=\"font-family: Arial;\">&ldquo;Ngay khi nhận được th&ocirc;ng b&aacute;o từ gia đ&igrave;nh về việc mẹ th&acirc;n sinh của t&ocirc;i qua đời, anh chị b&ecirc;n ph&iacute;a VBI đ&atilde; hỗ trợ nhanh ch&oacute;ng về hồ sơ v&agrave; giấy tờ li&ecirc;n quan. Kh&ocirc;ng chỉ hỗ trợ thủ tục l&agrave;m bảo hiểm, anh chị b&ecirc;n VBI cũng qua thăm hỏi như người th&acirc;n trong gia đ&igrave;nh, l&agrave;m nỗi đau mất m&aacute;t cũng được chia sẻ phần n&agrave;o. Gia đ&igrave;nh ch&uacute;ng t&ocirc;i đ&atilde; nhận được số tiền bồi thường 900 triệu đồng từ VBI. Ch&uacute;ng t&ocirc;i nhận thấy g&oacute;i bảo hiểm đ&atilde; đem lại rất nhiều lợi &iacute;ch. T&ocirc;i xin đại diện gia đ&igrave;nh cảm ơn anh chị Bảo hiểm VBI.&rdquo;</span></p>', '2018-03-29 13:38:30', '2018-03-29 13:38:30', 'Khách hàng tại Hải Phòng'),
(6, 'Mẹ chị Nguyễn Thị Hiền', 'images/testimonial/1522291245_testimonial-4.png', '<p>&ldquo;Con g&aacute;i t&ocirc;i c&oacute; tham gia g&oacute;i bảo hiểm của Bảo hiểm VietinBank nhưng kh&ocirc;ng may con t&ocirc;i gặp tai nạn qua đời đột ngột. C&ocirc;ng ty Bảo hiểm VietinBank Chi nh&aacute;nh Hải Ph&ograve;ng đ&atilde; chi trả số tiền bồi thường 100 triệu cho gia đ&igrave;nh. T&ocirc;i xin đại diện cho gia đ&igrave;nh cảm ơn C&ocirc;ng ty Bảo hiểm VietinBank.&rdquo;</p>', '2018-03-29 13:40:45', '2018-03-29 13:40:45', 'Khách hàng tại Hải Phòng');
